  INSERT INTO words (word, word_type, description) VALUES  (    'Concertante', 'n.', 'A concert for two or more principal instruments, with\n   orchestral accompaniment. Also adjectively; as, concertante parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concertation', 'n.', 'Strife; contention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concertative', 'a.', 'Contentious; quarrelsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concerted', 'a.', 'Mutually contrived or planned; agreed on; as, concerted\n   schemes, signals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concertina', 'n.', 'A small musical instrument on the principle of the\n   accordion. It is a small elastic box, or bellows, having free reeds on\n   the inside, and keys and handles on the outside of each of the two\n   hexagonal heads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concertino', 'n.', 'A piece for one or more solo instruments with\n   orchestra; -- more concise than the concerto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concertion', 'n.', 'Act of concerting; adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concertmeister', 'n.', 'The head violinist or leader of the strings in an\n   orchestra; the sub-leader of the orchestra; concert master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concertos', 'pl. ', 'of Concerto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concerto', 'n.', 'A composition (usually in symphonic form with three\n   movements) in which one instrument (or two or three) stands out in bold\n   relief against the orchestra, or accompaniment, so as to display its\n   qualities or the performers skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concession', 'n.', 'The act of conceding or yielding; usually implying a\n   demand, claim, or request, and thus distinguished from giving, which is\n   voluntary or spontaneous.');
