  INSERT INTO words (word, word_type, description) VALUES  (    'Conch', 'n.', 'A name applied to various marine univalve shells; esp. to\n   those of the genus Strombus, which are of large size. S. gigas is the\n   large pink West Indian conch. The large king, queen, and cameo conchs\n   are of the genus Cassis. See Cameo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conch', 'n.', 'In works of art, the shell used by Tritons as a trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conch', 'n.', 'One of the white natives of the Bahama Islands or one of\n   their descendants in the Florida Keys; -- so called from the commonness\n   of the conch there, or because they use it for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conch', 'n.', 'See Concha, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conch', 'n.', 'The external ear. See Concha, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concha', 'n.', 'The plain semidome of an apse; sometimes used for the\n   entire apse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concha', 'n.', 'The external ear; esp. the largest and deepest concavity of\n   the external ear, surrounding the entrance to the auditory canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchal', 'a.', 'Pertaining to the concha, or external ear; as, the conchal\n   cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchifer', 'n.', 'One of the Conchifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchifera', 'n. pl.', 'That class of Mollusca which includes the bivalve\n   shells; the Lamellibranchiata. See Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchiferous', 'a.', 'Producing or having shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchiform', 'a.', 'Shaped like one half of a bivalve shell; shell-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchinine', 'n.', 'See Quinidine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchite', 'n.', 'A fossil or petrified conch or shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchitic', 'a.', 'Composed of shells; containing many shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchoid', 'n.', 'A curve, of the fourth degree, first made use of by the\n   Greek geometer, Nicomedes, who invented it for the purpose of\n   trisecting an angle and duplicating the cube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchoidal', 'a.', 'Having elevations or depressions in form like one half\n   of a bivalve shell; -- applied principally to a surface produced by\n   fracture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchological', 'a.', 'Pertaining to, or connected with, conchology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchologist', 'n.', 'One who studies, or is versed in, conchology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchology', 'n.', 'The science of Mollusca, and of the shells which they\n   form; malacology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conchometer', 'n.', 'An instrument for measuring shells, or the angle of\n   their spire.');
