  INSERT INTO words (word, word_type, description) VALUES  (    'Concinnous', 'a.', 'Characterized by concinnity; neat; elegant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concionate', 'v. i.', 'To preach.');
