  INSERT INTO words (word, word_type, description) VALUES  (    'Concionator', 'n.', 'A common councilman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concionatory', 'a.', 'Of or pertaining to preaching or public addresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concise', 'a.', 'Expressing much in a few words; condensed; brief and\n   compacted; -- used of style in writing or speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concisely', 'adv.', 'In a concise manner; briefly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conciseness', 'n.', 'The quality of being concise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concision', 'n.', 'A cutting off; a division; a schism; a faction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concitation', 'n.', 'The act of stirring up, exciting, or agitating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concite', 'v. t.', 'To excite or stir up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclamation', 'n.', 'An outcry or shout of many together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclave', 'n.', 'The set of apartments within which the cardinals of the\n   Roman Catholic Church are continuously secluded while engaged in\n   choosing a pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclave', 'n.', 'The body of cardinals shut up in the conclave for the\n   election of a pope; hence, the body of cardinals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclave', 'n.', 'A private meeting; a close or secret assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclavist', 'n.', 'One of the two ecclesiastics allowed to attend a\n   cardinal in the conclave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concluded', 'imp. & p. p.', 'of Conclude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concluding', 'p. pr. & vb. n.', 'of Conclude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclude', 'v. t.', 'To shut up; to inclose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclude', 'v. t.', 'To include; to comprehend; to shut up together; to\n   embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclude', 'v. t.', 'To reach as an end of reasoning; to infer, as from\n   premises; to close, as an argument, by inferring; -- sometimes followed\n   by a dependent clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclude', 'v. t.', 'To make a final determination or judgment concerning;\n   to judge; to decide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclude', 'v. t.', 'To bring to an end; to close; to finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclude', 'v. t.', 'To bring about as a result; to effect; to make; as, to\n   conclude a bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclude', 'v. t.', 'To shut off; to restrain; to limit; to estop; to bar;\n   -- generally in the passive; as, the defendant is concluded by his own\n   plea; a judgment concludes the introduction of further evidence\n   argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclude', 'v. i.', 'To come to a termination; to make an end; to close; to\n   end; to terminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclude', 'v. i.', 'To form a final judgment; to reach a decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concludency', 'n.', 'Deduction from premises; inference; conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concludent', 'a.', 'Bringing to a close; decisive; conclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concluder', 'n.', 'One who concludes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concludingly', 'adv.', 'Conclusively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusible', 'a.', 'Demonstrable; determinable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusion', 'n.', 'The last part of anything; close; termination; end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusion', 'n.', 'Final decision; determination; result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusion', 'n.', 'Any inference or result of reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusion', 'n.', 'The inferred proposition of a syllogism; the necessary\n   consequence of the conditions asserted in two related propositions\n   called premises. See Syllogism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusion', 'n.', 'Drawing of inferences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusion', 'n.', 'An experiment, or something from which a conclusion may\n   be drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusion', 'n.', 'The end or close of a pleading, e.g., the formal ending\n   of an indictment, \"against the peace,\" etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusion', 'n.', 'An estoppel or bar by which a person is held to a\n   particular position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusive', 'a.', 'Belonging to a close or termination; decisive;\n   convincing; putting an end to debate or question; leading to, or\n   involving, a conclusion or decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusively', 'adv.', 'In the way of conclusion; decisively; positively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusiveness', 'n.', 'The quality of being conclusive; decisiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conclusory', 'a.', 'Conclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concocted', 'imp. & p. p.', 'of Concoct');
