  INSERT INTO words (word, word_type, description) VALUES  (    'Concoct', 'v. t.', 'To digest; to convert into nourishment by the organs of\n   nutrition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concoct', 'v. t.', 'To purify or refine chemically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concoct', 'v. t.', 'To prepare from crude materials, as food; to invent or\n   prepare by combining different ingredients; as, to concoct a new dish\n   or beverage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concoct', 'v. t.', 'To digest in the mind; to devise; to make up; to\n   contrive; to plan; to plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concoct', 'v. t.', 'To mature or perfect; to ripen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concocter', 'n.', 'One who concocts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concoction', 'n.', 'A change in food produced by the organs of nutrition;\n   digestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concoction', 'n.', 'The act of concocting or preparing by combining\n   different ingredients; also, the food or compound thus prepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concoction', 'n.', 'The act of digesting in the mind; planning or devising;\n   rumination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concoction', 'n.', 'Abatement of a morbid process, as a fever and return to\n   a normal condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concoction', 'n.', 'The act of perfecting or maturing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concoctive', 'a.', 'Having the power of digesting or ripening; digestive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concolor', 'a.', 'Of the same color; of uniform color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concolorous', 'a.', 'Of the same color throughout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concomitance', 'n.', 'Alt. of Concomitancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concomitancy', 'n.', 'The state of accompanying; accompaniment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concomitancy', 'n.', 'The doctrine of the existence of the entire body of\n   Christ in the eucharist, under each element, so that the body and blood\n   are both received by communicating in one kind only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concomitant', 'a.', 'Accompanying; conjoined; attending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concomitant', 'n.', 'One who, or that which, accompanies, or is\n   collaterally connected with another; a companion; an associate; an\n   accompaniment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concomitantly', 'adv.', 'In company with others; unitedly; concurrently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concord', 'n.', 'A state of agreement; harmony; union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concord', 'n.', 'Agreement by stipulation; compact; covenant; treaty or\n   league.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concord', 'n.', 'Agreement of words with one another, in gender, number,\n   person, or case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concord', 'n.', 'An agreement between the parties to a fine of land in\n   reference to the manner in which it should pass, being an\n   acknowledgment that the land in question belonged to the complainant.\n   See Fine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concord', 'n.', 'An agreeable combination of tones simultaneously heard; a\n   consonant chord; consonance; harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concord', 'n.', 'A variety of American grape, with large dark blue (almost\n   black) grapes in compact clusters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concord', 'v. i.', 'To agree; to act together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordable', 'a.', 'Capable of according; agreeing; harmonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordance', 'n.', 'Agreement; accordance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordance', 'n.', 'Concord; agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordance', 'n.', 'An alphabetical verbal index showing the places in the\n   text of a book where each principal word may be found, with its\n   immediate context in each place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordance', 'n.', 'A topical index or orderly analysis of the contents of\n   a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordancy', 'n.', 'Agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordant', 'a.', 'Agreeing; correspondent; harmonious; consonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordantly', 'adv.', 'In a concordant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordat', 'n.', 'A compact, covenant, or agreement concerning anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordat', 'n.', 'An agreement made between the pope and a sovereign or\n   government for the regulation of ecclesiastical matters with which both\n   are concerned; as, the concordat between Pope Pius VII and Bonaparte in\n   1801.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concordist', 'n.', 'The compiler of a concordance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concorporate', 'v. t. & i.', 'To unite in one mass or body; to incorporate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concorporate', 'a.', 'United in one body; incorporated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concorporation', 'n.', 'Union of things in one mass or body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concourse', 'n.', 'A moving, flowing, or running together; confluence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concourse', 'n.', 'An assembly; a gathering formed by a voluntary or\n   spontaneous moving and meeting in one place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concourse', 'n.', 'The place or point of meeting or junction of two bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concourse', 'n.', 'An open space where several roads or paths meet; esp. an\n   open space in a park where several roads meet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concourse', 'n.', 'Concurrence; cooperation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concreate', 'v. t.', 'To create at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concremation', 'n.', 'The act of burning different things together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrement', 'n.', 'A growing together; the collection or mass formed by\n   concretion, or natural union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrescence', 'n.', 'Coalescence of particles; growth; increase by the\n   addition of particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrescible', 'a.', 'Capable of being changed from a liquid to a solid\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrescive', 'a.', 'Growing together, or into union; uniting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrete', 'a.', 'United in growth; hence, formed by coalition of separate\n   particles into one mass; united in a solid form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrete', 'a.', 'Standing for an object as it exists in nature, invested\n   with all its qualities, as distinguished from standing for an attribute\n   of an object; -- opposed to abstract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrete', 'a.', 'Applied to a specific object; special; particular; --\n   opposed to general. See Abstract, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrete', 'n.', 'A compound or mass formed by concretion, spontaneous\n   union, or coalescence of separate particles of matter in one body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrete', 'n.', 'A mixture of gravel, pebbles, or broken stone with cement\n   or with tar, etc., used for sidewalks, roadways, foundations, etc., and\n   esp. for submarine structures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrete', 'n.', 'A term designating both a quality and the subject in\n   which it exists; a concrete term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrete', 'n.', 'Sugar boiled down from cane juice to a solid mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concreted', 'imp. & p. p.', 'of Concrete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concreting', 'p. pr & vb. n.', 'of Concrete');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrete', 'v. i.', 'To unite or coalesce, as separate particles, into a\n   mass or solid body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrete', 'v. t.', 'To form into a mass, as by the cohesion or coalescence\n   of separate particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrete', 'v. t.', 'To cover with, or form of, concrete, as a pavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concretely', 'adv.', 'In a concrete manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concreteness', 'n.', 'The quality of being concrete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concretion', 'n.', 'The process of concreting; the process of uniting or of\n   becoming united, as particles of matter into a mass; solidification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concretion', 'n.', 'A mass or nodule of solid matter formed by growing\n   together, by congelation, condensation, coagulation, induration, etc.;\n   a clot; a lump; a calculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concretion', 'n.', 'A rounded mass or nodule produced by an aggregation of\n   the material around a center; as, the calcareous concretions common in\n   beds of clay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concretional', 'a.', 'Concretionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concretionary', 'a.', 'Pertaining to, or formed by, concretion or\n   aggregation; producing or containing concretions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concretive', 'a.', 'Promoting concretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concretively', 'adv.', 'In a concrete manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concreture', 'n.', 'A mass formed by concretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrew', 'a.', 'To grow together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concrimination', 'n.', 'A joint accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubinacy', 'n.', 'The practice of concubinage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubinage', 'n.', 'The cohabiting of a man and a woman who are not\n   legally married; the state of being a concubine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubinage', 'n.', 'A plea, in which it is alleged that the woman suing\n   for dower was not lawfully married to the man in whose lands she seeks\n   to be endowed, but that she was his concubine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubinal', 'a.', 'Of or pertaining to concubinage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubinarian', 'a. & n.', 'Concubinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubinary', 'a.', 'Relating to concubinage; living in concubinage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubinaries', 'pl. ', 'of Concubinary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubinary', 'n.', 'One who lives in concubinage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubinate', 'n.', 'Concubinage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubine', 'n.', 'A woman who cohabits with a man without being his wife;\n   a paramour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concubine', 'n.', 'A wife of inferior condition; a lawful wife, but not\n   united to the man by the usual ceremonies, and of inferior condition.\n   Such were Hagar and Keturah, the concubines of Abraham; and such\n   concubines were allowed by the Roman laws. Their children were not\n   heirs of their father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concultated', 'imp. & p. p.', 'of Conculcate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conculcating', 'p. pr. & vb. n.', 'of Conculcate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conculcate', 'v. t.', 'To tread or trample under foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concupiscence', 'n.', 'Sexual lust; morbid carnal passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concupiscent', 'a.', 'Having sexual lust; libidinous; lustful; lecherous;\n   salacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concupiscential', 'a.', 'Relating to concupiscence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concupiscentious', 'a.', 'Concupiscent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concupiscible', 'a.', 'Exciting to, or liable to be affected by,\n   concupiscence; provoking lustful desires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concupiscible', 'a.', 'Exciting desire, good or evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concupiscibleness', 'n.', 'The state of being concupiscible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concupy', 'n.', 'Concupiscence. [Used only in \"Troilus and Cressida\"]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurred', 'imp. & p. p.', 'of Concur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurring', 'p. pr. & vb. n.', 'of Concur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concur', 'v. i.', 'To run together; to meet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concur', 'v. i.', 'To meet in the same point; to combine or conjoin; to\n   contribute or help toward a common object or effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concur', 'v. i.', 'To unite or agree (in action or opinion); to join; to\n   act jointly; to agree; to coincide; to correspond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concur', 'v. i.', 'To assent; to consent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrence', 'n.', 'The act of concurring; a meeting or coming together;\n   union; conjunction; combination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrence', 'n.', 'A meeting of minds; agreement in opinion; union in\n   design or act; -- implying joint approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrence', 'n.', 'Agreement or consent, implying aid or contribution of\n   power or influence; cooperation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrence', 'n.', 'A common right; coincidence of equal powers; as, a\n   concurrence of jurisdiction in two different courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrency', 'n.', 'Concurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrent', 'a.', 'Acting in conjunction; agreeing in the same act or\n   opinion; contributing to the same event or effect; cooperating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrent', 'a.', 'Conjoined; associate; concomitant; existing or\n   happening at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrent', 'a.', 'Joint and equal in authority; taking cognizance of\n   similar questions; operating on the same objects; as, the concurrent\n   jurisdiction of courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrent', 'a.', 'Meeting in one point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrent', 'n.', 'One who, or that which, concurs; a joint or\n   contributory cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrent', 'n.', 'One pursuing the same course, or seeking the same\n   objects; hence, a rival; an opponent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrent', 'n.', 'One of the supernumerary days of the year over\n   fifty-two complete weeks; -- so called because they concur with the\n   solar cycle, the course of which they follow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrently', 'adv.', 'With concurrence; unitedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurrentness', 'n.', 'The state or quality of being concurrent;\n   concurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concurring', 'a.', 'Agreeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concuss', 'v. t.', 'To shake or agitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concuss', 'v. t.', 'To force (a person) to do something, or give up\n   something, by intimidation; to coerce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concussation', 'n.', 'A violent shock or agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concussion', 'n.', 'A shaking or agitation; a shock; caused by the\n   collision of two bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concussion', 'n.', 'A condition of lowered functional activity, without\n   visible structural change, produced in an organ by a shock, as by fall\n   or blow; as, a concussion of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concussion', 'n.', 'The unlawful forcing of another by threats of violence\n   to yield up something of value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Concussive', 'a.', 'Having the power or quality of shaking or agitating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cond', 'v. t.', 'To con, as a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemned', 'imp. & p. p.', 'of Condemn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemning', 'p. pr. & vb. n.', 'of Condemn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemn', 'v. t.', 'To pronounce to be wrong; to disapprove of; to censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemn', 'v. t.', 'To declare the guilt of; to make manifest the faults or\n   unworthiness of; to convict of guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemn', 'v. t.', 'To pronounce a judicial sentence against; to sentence\n   to punishment, suffering, or loss; to doom; -- with to before the\n   penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemn', 'v. t.', 'To amerce or fine; -- with in before the penalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemn', 'v. t.', 'To adjudge or pronounce to be unfit for use or service;\n   to adjudge or pronounce to be forfeited; as, the ship and her cargo\n   were condemned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemn', 'v. t.', 'To doom to be taken for public use, under the right of\n   eminent domain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemnable', '', 'Worthy of condemnation; blamable; culpable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemnation', 'n.', 'The act of condemning or pronouncing to be wrong;\n   censure; blame; disapprobation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemnation', 'n.', 'The act of judicially condemning, or adjudging\n   guilty, unfit for use, or forfeited; the act of dooming to punishment\n   or forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemnation', 'n.', 'The state of being condemned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemnation', 'n.', 'The ground or reason of condemning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemnatory', 'a.', 'Condemning; containing or imposing condemnation or\n   censure; as, a condemnatory sentence or decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemned', 'a.', 'Pronounced to be wrong, guilty, worthless, or forfeited;\n   adjudged or sentenced to punishment, destruction, or confiscation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemned', 'a.', 'Used for condemned persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condemner', 'n.', 'One who condemns or censures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensability', 'n.', 'Capability of being condensed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensable', 'a.', 'Capable of being condensed; as, vapor is condensable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensate', 'v. t.', 'Made dense; condensed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensated', 'imp. & p. p.', 'of Condensate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensating', 'p. pr. & vb. n.', 'of Condensate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensate', 'v. t.', 'To condense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensation', 'n.', 'The act or process of condensing or of being\n   condensed; the state of being condensed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensation', 'n.', 'The act or process of reducing, by depression of\n   temperature or increase of pressure, etc., to another and denser form,\n   as gas to the condition of a liquid or steam to water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensation', 'n.', 'A rearrangement or concentration of the different\n   constituents of one or more substances into a distinct and definite\n   compound of greater complexity and molecular weight, often resulting in\n   an increase of density, as the condensation of oxygen into ozone, or of\n   acetone into mesitylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensative', 'a.', 'Having the property of condensing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensed', 'imp. & p. p.', 'of Condense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensing', 'p. pr. & vb. n.', 'of Condense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condense', 'v. t.', 'To make more close, compact, or dense; to compress or\n   concentrate into a smaller compass; to consolidate; to abridge; to\n   epitomize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condense', 'v. t.', 'To reduce into another and denser form, as by cold or\n   pressure; as, to condense gas into a liquid form, or steam into water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condense', 'v. i.', 'To become more compact; to be reduced into a denser\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condense', 'v. i.', 'To combine or unite (as two chemical substances) with\n   or without separation of some unimportant side products.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condense', 'v. i.', 'To undergo polymerization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condense', 'a.', 'Condensed; compact; dense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condenser', 'n.', 'One who, or that which, condenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condenser', 'n.', 'An instrument for condensing air or other elastic\n   fluids, consisting of a cylinder having a movable piston to force the\n   air into a receiver, and a valve to prevent its escape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condenser', 'n.', 'An instrument for concentrating electricity by the\n   effect of induction between conducting plates separated by a\n   nonconducting plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condenser', 'n.', 'A lens or mirror, usually of short focal distance, used\n   to concentrate light upon an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condenser', 'n.', 'An apparatus for receiving and condensing the volatile\n   products of distillation to a liquid or solid form, by cooling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condenser', 'n.', 'An apparatus, separate from the cylinder, in which the\n   exhaust steam is condensed by the action of cold water or air. See\n   Illust. of Steam engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condensible', 'a.', 'Capable of being condensed; as, a gas condensible to a\n   liquid by cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conder', 'n.', 'One who watches shoals of fish; a balker. See Balker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condescended', 'imp. & p. p.', 'of Condescend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condescending', 'p. pr. & vb. n.', 'of Condescend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condescend', 'v. i.', 'To stoop or descend; to let ones self down; to\n   submit; to waive the privilege of rank or dignity; to accommodate ones\n   self to an inferior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condescend', 'v. i.', 'To consent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condescendence', 'n.', 'Alt. of Condescendency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condescendency', 'n.', 'Condescension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condescendingly', 'adv.', 'In a condescending manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condescension', 'n.', 'The act of condescending; voluntary descent from\n   ones rank or dignity in intercourse with an inferior; courtesy toward\n   inferiors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condescent', 'n.', 'An act of condescension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condign', 'a.', 'Worthy; suitable; deserving; fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condign', 'a.', 'Deserved; adequate; suitable to the fault or crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condignity', 'n.', 'Merit, acquired by works, which can claim reward on the\n   score of general benevolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condignly', 'adv.', 'According to merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condignness', 'n.', 'Agreeableness to deserts; suitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condiment', 'n.', 'Something used to give relish to food, and to gratify\n   the taste; a pungment and appetizing substance, as pepper or mustard;\n   seasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condisciple', 'n.', 'A schoolfellow; a fellow-student.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condite', 'a.', 'Preserved; pickled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condite', 'v. t.', 'To pickle; to preserve; as, to condite pears, quinces,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'n.', 'Mode or state of being; state or situation with regard\n   to external circumstances or influences, or to physical or mental\n   integrity, health, strength, etc.; predicament; rank; position, estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'n.', 'Essential quality; property; attribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'n.', 'Temperament; disposition; character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'n.', 'That which must exist as the occasion or concomitant of\n   something else; that which is requisite in order that something else\n   should take effect; an essential qualification; stipulation; terms\n   specified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'n.', 'A clause in a contract, or agreement, which has for its\n   object to suspend, to defeat, or in some way to modify, the principal\n   obligation; or, in case of a will, to suspend, revoke, or modify a\n   devise or bequest. It is also the case of a future uncertain event,\n   which may or may not happen, and on the occurrence or non-occurrence of\n   which, the accomplishment, recission, or modification of an obligation\n   or testamentary disposition is made to depend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditioned', 'imp. & p. p.', 'of Condition');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditioning', 'p. pr. & vb. n.', 'of Condition');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'v. i.', 'To make terms; to stipulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'v. i.', 'To impose upon an object those relations or\n   conditions without which knowledge and thought are alleged to be\n   impossible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'n.', 'To invest with, or limit by, conditions; to burden or\n   qualify by a condition; to impose or be imposed as the condition of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'n.', 'To contract; to stipulate; to agree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'n.', 'To put under conditions; to require to pass a new\n   examination or to make up a specified study, as a condition of\n   remaining in ones class or in college; as, to condition a student who\n   has failed in some branch of study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'n.', 'To test or assay, as silk (to ascertain the proportion\n   of moisture it contains).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condition', 'n.', 'train; acclimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditional', 'a.', 'Containing, implying, or depending on, a condition or\n   conditions; not absolute; made or granted on certain terms; as, a\n   conditional promise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditional', 'a.', 'Expressing a condition or supposition; as, a\n   conditional word, mode, or tense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditional', 'n.', 'A limitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditional', 'n.', 'A conditional word, mode, or proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditionality', 'n.', 'The quality of being conditional, or limited;\n   limitation by certain terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditionally', 'adv.', 'In a conditional manner; subject to a condition or\n   conditions; not absolutely or positively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditionate', 'v. t.', 'Conditional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditionate', 'v. t.', 'To qualify by conditions; to regulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditionate', 'v. t.', 'To put under conditions; to render conditional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditioned', 'a.', 'Surrounded; circumstanced; in a certain state or\n   condition, as of property or health; as, a well conditioned man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditioned', 'a.', 'Having, or known under or by, conditions or relations;\n   not independent; not absolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditionly', 'adv.', 'Conditionally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditories', 'pl. ', 'of Conditory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conditory', 'n.', 'A repository for holding things; a hinding place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condog', 'v. i.', 'To concur; to agree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condolatory', 'a.', 'Expressing condolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condoled', 'imp. & p. p.', 'of Condole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condoling', 'p. pr. & vb. n.', 'of Condole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condole', 'v. i.', 'To express sympathetic sorrow; to grieve in sympathy;\n   -- followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condole', 'v. t.', 'To lament or grieve over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condolement', 'n.', 'Condolence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condolement', 'n.', 'Sorrow; mourning; lamentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condolence', 'n.', 'Expression of sympathy with another in sorrow or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condoler', 'n.', 'One who condoles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condonation', 'n.', 'The act of condoning or pardoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condonation', 'n.', 'Forgiveness, either express or implied, by a husband\n   of his wife or by a wife of her husband, for a breach of marital duty,\n   as adultery, with an implied condition that the offense shall not be\n   repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condoned', 'imp. & p. p.', 'of Condone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condoning', 'p. pr. & vb. n.', 'of Condone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condone', 'v. t.', 'To pardon; to forgive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condone', 'v. t.', 'To pardon; to overlook the offense of; esp., to forgive\n   for a violation of the marriage law; -- said of either the husband or\n   the wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condor', 'n.', 'A very large bird of the Vulture family (Sarcorhamphus\n   gryphus); found in the most elevated parts of the Andes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condottieri', 'pl. ', 'of Condottiere');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condottiere', 'n.', 'A military adventurer of the fourteenth and fifteenth\n   centuries, who sold his services, and those of his followers, to any\n   party in any contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduced', 'imp. & p. p.', 'of Conduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conducing', 'p. pr. & vb. n.', 'of Conduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduce', 'n.', 'To lead or tend, esp. with reference to a favorable or\n   desirable result; to contribute; -- usually followed by to or toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduce', 'v. t.', 'To conduct; to lead; to guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conducent', 'a.', 'Conducive; tending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conducibility', 'n.', 'The state or quality of being conducible;\n   conducibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conducible', 'a.', 'Conducive; tending; contributing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conducibleness', 'n.', 'Quality of being conducible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conducibly', 'adv.', 'In a manner to promote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conducive', 'a.', 'Loading or tending; helpful; contributive; tending to\n   promote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduciveness', 'n.', 'The quality of conducing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'The act or method of conducting; guidance; management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'Skillful guidance or management; generalship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'Convoy; escort; guard; guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'That which carries or conveys anything; a channel; a\n   conduit; an instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'The manner of guiding or carrying ones self; personal\n   deportment; mode of action; behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'Plot; action; construction; manner of development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conducted', 'imp. & p. p.', 'of Conduct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conducting', 'p. pr. & vb. n.', 'of Conduct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'To lead, or guide; to escort; to attend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'To lead, as a commander; to direct; to manage; to carry\n   on; as, to conduct the affairs of a kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'To behave; -- with the reflexive; as, he conducted himself\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'To serve as a medium for conveying; to transmit, as heat,\n   light, electricity, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'n.', 'To direct, as the leader in the performance of a musical\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'v. i.', 'To act as a conductor (as of heat, electricity, etc.);\n   to carry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduct', 'v. i.', 'To conduct ones self; to behave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductibility', 'n.', 'Capability of being conducted; as, the\n   conductibility of heat or electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductibility', 'n.', 'Conductivity; capacity for receiving and\n   transmitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductible', 'a.', 'Capable of being conducted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduction', 'n.', 'The act of leading or guiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduction', 'n.', 'The act of training up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduction', 'n.', 'Transmission through, or by means of, a conductor;\n   also, conductivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductive', 'a.', 'Having the quality or power of conducting; as, the\n   conductive tissue of a pistil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductivity', 'n.', 'The quality or power of conducting, or of receiving\n   and transmitting, as heat, electricity, etc.; as, the conductivity of a\n   nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductor', 'n.', 'One who, or that which, conducts; a leader; a commander;\n   a guide; a manager; a director.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductor', 'n.', 'One in charge of a public conveyance, as of a railroad\n   train or a street car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductor', 'n.', 'The leader or director of an orchestra or chorus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductor', 'n.', 'A substance or body capable of being a medium for the\n   transmission of certain forces, esp. heat or electricity; specifically,\n   a lightning rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductor', 'n.', 'A grooved sound or staff used for directing instruments,\n   as lithontriptic forceps, etc.; a director.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductor', 'n.', 'Same as Leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductory', 'a.', 'Having the property of conducting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conductress', 'n.', 'A woman who leads or directs; a directress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduit', 'n.', 'A pipe, canal, channel, or passage for conveying water or\n   fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduit', 'n.', 'A structure forming a reservoir for water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduit', 'n.', 'A narrow passage for private communication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduplicate', 'a.', 'Folded lengthwise along the midrib, the upper face\n   being within; -- said of leaves or petals in vernation or aestivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conduplication', 'n.', 'A doubling together or folding; a duplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condurango', 'n.', 'See Cundurango.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condurrite', 'n.', 'A variety of the mineral domeykite, or copper arsenide,\n   from the Condurra mine in Cornwall, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condylar', 'a.', 'Of or pertaining to a condyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condyle', 'n.', 'A bony prominence; particularly, an eminence at the end of\n   a bone bearing a rounded articular surface; -- sometimes applied also\n   to a concave articular surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condyloid', 'a.', 'Shaped like or pertaining to a condyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condylomata', 'pl. ', 'of Condylome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condylomes', 'pl. ', 'of Condylome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condyloma', 'n.', 'Alt. of Condylome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condylome', 'n.', 'A wartlike new growth on the outer skin or adjoining\n   mucous membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Condylopod', 'n.', 'An arthropod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cone', 'n.', 'A solid of the form described by the revolution of a\n   right-angled triangle about one of the sides adjacent to the right\n   angle; -- called also a right cone. More generally, any solid having a\n   vertical point and bounded by a surface which is described by a\n   straight line always passing through that vertical point; a solid\n   having a circle for its base and tapering to a point or vertex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cone', 'n.', 'Anything shaped more or less like a mathematical cone; as, a\n   volcanic cone, a collection of scoriae around the crater of a volcano,\n   usually heaped up in a conical form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cone', 'n.', 'The fruit or strobile of the Coniferae, as of the pine, fir,\n   cedar, and cypress. It is composed of woody scales, each one of which\n   has one or two seeds at its base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cone', 'n.', 'A shell of the genus Conus, having a conical form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cone', 'v. t.', 'To render cone-shaped; to bevel like the circular segment\n   of a cone; as, to cone the tires of car wheels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cone-in-cone', 'a.', 'Consisting of a series of parallel cones, each made\n   up of many concentric cones closely packed together; -- said of a kind\n   of structure sometimes observed in sedimentary rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coneine', 'n.', 'See Conine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conepate', 'n.', 'Alt. of Conepatl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conepatl', 'n.', 'The skunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cone pulley', '', 'A pulley for driving machines, etc., having two or more\n   parts or steps of different diameters; a pulley having a conical shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coney', 'n.', 'A rabbit. See Cony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coney', 'n.', 'A fish. See Cony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confab', 'n.', 'Familiar talk or conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confabulated', 'imp. & p. p.', 'of Confabulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confabulating', 'p. pr. & vb. n.', 'of Confabulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confabulate', 'v. i.', 'To talk familiarly together; to chat; to prattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confabulation', 'n.', 'Familiar talk; easy, unrestrained, unceremonious\n   conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confabulatory', 'a.', 'Of the nature of familiar talk; in the form of a\n   dialogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confalon', 'n.', 'One of a fraternity of seculars, also called Penitents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confarreation', 'n.', 'A form of marriage among the Romans, in which an\n   offering of bread was made, in presence of the high priest and at least\n   ten witnesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confated', 'p.a.', 'Fated or decreed with something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confected', 'imp. & p. p.', 'of Confect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confecting', 'p. pr. & vb. n.', 'of Confect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confect', 'v. t.', 'To prepare, as sweetmeats; to make a confection of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confect', 'v. t.', 'To construct; to form; to mingle or mix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confect', 'n.', 'A comfit; a confection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confection', 'n.', 'A composition of different materials.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confection', 'n.', 'A preparation of fruits or roots, etc., with sugar; a\n   sweetmeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confection', 'n.', 'A composition of drugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confection', 'n.', 'A soft solid made by incorporating a medicinal\n   substance or substances with sugar, sirup, or honey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confectionary', 'n.', 'A confectioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confectionary', 'a.', 'Prepared as a confection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confectioner', 'n.', 'A compounder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confectioner', 'n.', 'One whose occupation it is to make or sell\n   confections, candies, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confectionery', 'n.', 'Sweetmeats, in general; things prepared and sold by\n   a confectioner; confections; candies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confectionery', 'n.', 'A place where candies, sweetmeats, and similar\n   things are made or sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confectory', 'a.', 'Pertaining to the art of making sweetmeats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confecture', 'n.', 'Same as Confiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confeder', 'v. i.', 'To confederate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederacies', 'pl. ', 'of Confederacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederacy', 'n.', 'A league or compact between two or more persons,\n   bodies of men, or states, for mutual support or common action;\n   alliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederacy', 'n.', 'The persons, bodies, states, or nations united by a\n   league; a confederation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederacy', 'n.', 'A combination of two or more persons to commit an\n   unlawful act, or to do a lawful act by unlawful means. See Conspiracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederate', 'a.', 'United in a league; allied by treaty; engaged in a\n   confederacy; banded together; allied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederate', 'a.', 'Of or pertaining to the government of the eleven\n   Southern States of the United States which (1860-1865) attempted to\n   establish an independent nation styled the Confederate States of\n   America; as, the Confederate congress; Confederate money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederate', 'n.', 'One who is united with others in a league; a person or\n   a nation engaged in a confederacy; an ally; also, an accomplice in a\n   bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederate', 'n.', 'A name designating an adherent to the cause of the\n   States which attempted to withdraw from the Union (1860-1865).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederated', 'imp. & p. p.', 'of Confederate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederating', 'p. pr. & vb. n.', 'of Confederate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederate', 'v. t.', 'To unite in a league or confederacy; to ally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederate', 'v. i.', 'To unite in a league; to join in a mutual contract\n   or covenant; to band together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederater', 'n.', 'A confederate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederation', 'n.', 'The act of confederating; a league; a compact for\n   mutual support; alliance, particularly of princes, nations, or states.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederation', 'n.', 'The parties that are confederated, considered as a\n   unit; a confederacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederative', 'a.', 'Of or pertaining to a confederation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confederator', 'n.', 'A confederate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferred', 'imp. & p. p.', 'of Confer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferring', 'p. pr. & vb. n.', 'of Confer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confer', 'v. t.', 'To bring together for comparison; to compare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confer', 'v. t.', 'To grant as a possession; to bestow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confer', 'v. t.', 'To contribute; to conduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confer', 'v. i.', 'To have discourse; to consult; to compare views; to\n   deliberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferee', 'n.', 'One who is conferred with, or who takes part in a\n   conference; as, the conferees on the part of the Senate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferee', 'n.', 'One upon whom something is conferred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conference', 'n.', 'The act of comparing two or more things together;\n   comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conference', 'n.', 'The act of consulting together formally; serious\n   conversation or discussion; interchange of views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conference', 'n.', 'A meeting for consultation, discussion, or an\n   interchange of opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conference', 'n.', 'A meeting of the two branches of a legislature, by\n   their committees, to adjust between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conference', 'n.', 'A stated meeting of preachers and others, invested with\n   authority to take cognizance of ecclesiastical matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conference', 'n.', 'A voluntary association of Congregational churches of a\n   district; the district in which such churches are.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferential', 'a.', 'Relating to conference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferrable', 'a.', 'Capable of being conferred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferree', 'n.', 'Same as Conferee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferrer', 'n.', 'One who confers; one who converses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferrer', 'n.', 'One who bestows; a giver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferruminate', 'a.', 'Alt. of Conferruminated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferruminated', 'a.', 'Closely united by the coalescence, or sticking\n   together, of contiguous faces, as in the case of the cotyledons of the\n   live-oak acorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confervae', 'pl. ', 'of Conferva');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conferva', 'n.', 'Any unbranched, slender, green plant of the fresh-water\n   algae. The word is frequently used in a wider sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confervaceous', 'a.', 'Belonging to the confervae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confervoid', 'a.', 'Like, or related to, the confervae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confervous', 'a.', 'Pertaining to confervae; consisting of, or resembling,\n   the confervae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessed', 'imp. & p. p.', 'of Confess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessing', 'p. pr. & vb. n.', 'of Confess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confess', 'v. t.', 'To make acknowledgment or avowal in a matter pertaining\n   to ones self; to acknowledge, own, or admit, as a crime, a fault, a\n   debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confess', 'v. t.', 'To acknowledge faith in; to profess belief in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confess', 'v. t.', 'To admit as true; to assent to; to acknowledge, as\n   after a previous doubt, denial, or concealment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confess', 'v. t.', 'To make known or acknowledge, as ones sins to a\n   priest, in order to receive absolution; -- sometimes followed by the\n   reflexive pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confess', 'v. t.', 'To hear or receive such confession; -- said of a\n   priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confess', 'v. t.', 'To disclose or reveal, as an effect discloses its\n   cause; to prove; to attest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confess', 'v. i.', 'To make confession; to disclose sins or faults, or the\n   state of the conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confess', 'v. i.', 'To acknowledge; to admit; to concede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessant', 'n.', 'One who confesses to a priest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessary', 'n.', 'One who makes a confession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessedly', 'adv.', 'By confession; without denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confesser', 'n.', 'One who makes a confession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confession', 'n.', 'Acknowledgment; avowal, especially in a matter\n   pertaining to ones self; the admission of a debt, obligation, or\n   crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confession', 'n.', 'Acknowledgment of belief; profession of ones faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confession', 'n.', 'The act of disclosing sins or faults to a priest in\n   order to obtain sacramental absolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confession', 'n.', 'A formulary in which the articles of faith are\n   comprised; a creed to be assented to or signed, as a preliminary to\n   admission to membership of a church; a confession of faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confession', 'n.', 'An admission by a party to whom an act is imputed, in\n   relation to such act. A judicial confession settles the issue to which\n   it applies; an extrajudical confession may be explained or rebutted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessional', 'n.', 'The recess, seat, or inclosed place, where a priest\n   sits to hear confessions; often a small structure furnished with a seat\n   for the priest and with a window or aperture so that the penitent who\n   is outside may whisper into the priests ear without being seen by him\n   or heard by others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessional', 'a.', 'Pertaining to a confession of faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessionalism', 'n.', 'An exaggerated estimate of the importance of\n   giving full assent to any particular formula of the Christian faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessionalist', 'n.', 'A priest hearing, or sitting to hear, confession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessionary', 'n.', 'A confessional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessionary', 'a.', 'Pertaining to auricular confession; as, a\n   confessionary litany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessionist', 'n.', 'One professing a certain faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessor', 'n.', 'One who confesses; one who acknowledges a fault, or the\n   truth of a charge, at the risk of suffering; specifically, one who\n   confesses himself a follower of Christ and endures persecution for his\n   faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessor', 'n.', 'A priest who hears the confessions of others and is\n   authorized to grant them absolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confessorship', 'n.', 'The act or state of suffering persecution for\n   religious faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confestly', 'adv.', 'See Cofessedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidant', 'n. fem.', 'Alt. of Confidante');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidante', 'n. fem.', 'One to whom secrets, especially those relating to\n   affairs of love, are confided or intrusted; a confidential or bosom\n   friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confided', 'imp. & p. p.', 'of Confide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiding', 'p. pr. & vb. n.', 'of Confide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confide', 'v. i.', 'To put faith (in); to repose confidence; to trust; --\n   usually followed by in; as, the prince confides in his ministers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confide', 'v. t.', 'To intrust; to give in charge; to commit to ones\n   keeping; -- followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidence', 'n.', 'The act of confiding, trusting, or putting faith in;\n   trust; reliance; belief; -- formerly followed by of, now commonly by\n   in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidence', 'n.', 'That in which faith is put or reliance had.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidence', 'n.', 'The state of mind characterized by ones reliance on\n   himself, or his circumstances; a feeling of self-sufficiency; such\n   assurance as leads to a feeling of security; self-reliance; -- often\n   with self prefixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidence', 'n.', 'Private conversation; (pl.) secrets shared; as, there\n   were confidences between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidence', 'n.', 'Trustful; without fear or suspicion; frank; unreserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidence', 'n.', 'Having self-reliance; bold; undaunted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidence', 'n.', 'Having an excess of assurance; bold to a fault;\n   dogmatical; impudent; presumptuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidence', 'n.', 'Giving occasion for confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confident', 'n.', 'See Confidant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidential', 'a.', 'Enjoying, or treated with, confidence; trusted in;\n   trustworthy; as, a confidential servant or clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidential', 'a.', 'Communicated in confidence; secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidentially', 'adv.', 'In confidence; in reliance on secrecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidently', 'adv.', 'With confidence; with strong assurance; positively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confidentness', 'n.', 'The quality of being confident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confider', 'n.', 'One who confides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiding', 'a.', 'That confides; trustful; unsuspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Configurate', 'v. i.', 'To take form or position, as the parts of a complex\n   structure; to agree with a pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Configuration', 'n.', 'Form, as depending on the relative disposition of\n   the parts of a thing shape; figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Configuration', 'n.', 'Relative position or aspect of the planets; the face\n   of the horoscope, according to the relative positions of the planets at\n   any time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Configured', 'imp. & p. p.', 'of Configure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Configuring', 'p. pr. & vb. n.', 'of Configure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Configure', 'v. t.', 'To arrange or dispose in a certain form, figure, or\n   shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confinable', 'a.', 'Capable of being confined, restricted, or limited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confined', 'imp. & p. p.', 'of Confine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confining', 'p. pr. & vb. n.', 'of Confine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confine', 'v. t.', 'To restrain within limits; to restrict; to limit; to\n   bound; to shut up; to inclose; to keep close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confine', 'v. i.', 'To have a common boundary; to border; to lie\n   contiguous; to touch; -- followed by on or with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confine', 'n.', 'Common boundary; border; limit; -- used chiefly in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confine', 'n.', 'Apartment; place of restraint; prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confineless', 'a.', 'Without limitation or end; boundless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confinement', 'n.', 'Restraint within limits; imprisonment; any restraint\n   of liberty; seclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confinement', 'n.', 'Restraint within doors by sickness, esp. that caused\n   by childbirth; lying-in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiner', 'n.', 'One who, or that which, limits or restrains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiner', 'n.', 'One who lives on confines, or near the border of a\n   country; a borderer; a near neighbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confinity', 'n.', 'Community of limits; contiguity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confrmed', 'imp. & p. p.', 'of Confirm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirming', 'p. pr. & vb. n.', 'of Confirm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirm', 'v. t.', 'To make firm or firmer; to add strength to; to\n   establish; as, health is confirmed by exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirm', 'v. t.', 'To strengthen in judgment or purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirm', 'v. t.', 'To give new assurance of the truth of; to render\n   certain; to verify; to corroborate; as, to confirm a rumor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirm', 'v. t.', 'To render valid by formal assent; to complete by a\n   necessary sanction; to ratify; as, to confirm the appoinment of an\n   official; the Senate confirms a treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirm', 'v. t.', 'To administer the rite of confirmation to. See\n   Confirmation, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmable', 'a.', 'That may be confirmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmance', 'n.', 'Confirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmation', 'n.', 'The act of confirming or strengthening; the act of\n   establishing, ratifying, or sanctioning; as, the confirmation of an\n   appointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmation', 'n.', 'That which confirms; that which gives new strength or\n   assurance; as to a statement or belief; additional evidence; proof;\n   convincing testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmation', 'n.', 'A rite supplemental to baptism, by which a person is\n   admitted, through the laying on of the hands of a bishop, to the full\n   privileges of the church, as in the Roman Catholic, the Episcopal\n   Church, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmation', 'n.', 'A conveyance by which a voidable estate is made sure\n   and not voidable, or by which a particular estate is increased; a\n   contract, express or implied, by which a person makes that firm and\n   binding which was before voidable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmative', 'a.', 'Tending to confirm or establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmator', 'n.', 'One who, or that which, confirms; a confirmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmatory', 'a. .', 'Serving to confirm; corroborative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmatory', 'a. .', 'Pertaining to the rite of confirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmedly', 'adv.', 'With confirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmedness', 'n.', 'A fixed state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmee', 'n.', 'One to whom anything is confirmed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmer', 'n.', 'One who, or that which, confirms, establishes, or\n   ratifies; one who corroborates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confirmingly', 'adv.', 'In a confirming manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiscable', 'a.', 'Capable of being confiscated; liable to forfeiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiscate', 'a.', 'Seized and appropriated by the government to the public\n   use; forfeited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiscated', 'imp. & p. p.', 'of Confiscate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiscating', 'p. pr. & vb. n.', 'of Confiscate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiscate', 'v. t. ', 'To seize as forfeited to the public treasury; to\n   appropriate to the public use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiscation', 'n.', 'The act or process of taking property or condemning\n   it to be taken, as forfeited to the public use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiscator', 'n.', 'One who confiscates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiscatory', 'a.', 'Effecting confiscation; characterized by\n   confiscations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confit', 'n.', 'Same as Comfit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confitent', 'n.', 'One who confesses his sins and faults.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiteor', 'n.', 'A form of prayer in which public confession of sins is\n   made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confiture', 'n.', 'Composition; preparation, as of a drug, or confection; a\n   sweetmeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confixed', 'imp. & p. p.', 'of Confix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confix', 'v. t.', 'To fix; to fasten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confixure', 'n.', 'Act of fastening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conflagrant', 'a.', 'Burning together in a common flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conflagration', 'n.', 'A fire extending to many objects, or over a large\n   space; a general burning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conflated', 'imp. & p. p.', 'of Conflate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conflating', 'p. pr. & vb. n.', 'of Conflate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conflate', 'v. t.', 'To blow together; to bring together; to collect; to\n   fuse together; to join or weld; to consolidate.');
