  INSERT INTO words (word, word_type, description) VALUES  (    'Conformist', 'n.', 'One who conforms or complies; esp., one who conforms to\n   the Church of England, or to the Established Church, as distinguished\n   from a dissenter or nonconformist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conformities', 'pl. ', 'of Conformity');
