  INSERT INTO words (word, word_type, description) VALUES  (    'Confusedness', 'n.', 'A state of confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confusely', 'adv.', 'Confusedly; obscurely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Confusion', 'n.', 'The state of being mixed or blended so as to produce\n   indistinctness or error; indistinct combination; disorder; tumult.');
