  INSERT INTO words (word, word_type, description) VALUES  (    'Conglutinated', 'imp. & p. p.', 'of Conglutinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conglutinate', 'v. t.', 'To glue together; to unite by some glutinous or\n   tenacious substance; to cause to adhere or to grow together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conglutinate', 'v. i.', 'To unite by the intervention of some glutinous\n   substance; to coalesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conglutination', 'n.', 'A gluing together; a joining by means of some\n   tenacious substance; junction; union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conglutinative', 'a.', 'Conglutinant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congou', 'n.', 'Alt. of Congo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congo', 'n.', 'Black tea, of higher grade (finer leaf and less dusty) than\n   the present bohea. See Tea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congo snake', '', 'An amphibian (Amphiuma means) of the order Urodela,\n   found in the southern United States. See Amphiuma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congratulant', 'a.', 'Rejoicing together; congratulatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congratulated', 'imp. & p. p.', 'of Congratulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congratulating', 'p. pr. & vb. n.', 'of Congratulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congratulate', 'v. t.', 'To address with expressions of sympathetic\n   pleasure on account of some happy event affecting the person addressed;\n   to wish joy to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congratulate', 'v. i.', 'To express of feel sympathetic joy; as, to\n   congratulate with ones country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congratulation', 'n.', 'The act of congratulating; an expression of\n   sympathetic pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congratulator', 'n.', 'One who offers congratulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congratulatory', 'a.', 'Expressive of sympathetic joy; as, a congratulatory\n   letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congree', 'v. i.', 'To agree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congreet', 'v. t.', 'To salute mutually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregate', 'a.', 'Collected; compact; close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregated', 'imp. & p. p.', 'of Congregate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregating', 'p. pr. & vb. n.', 'of Congregate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregate', 'v. t.', 'To collect into an assembly or assemblage; to\n   assemble; to bring into one place, or into a united body; to gather\n   together; to mass; to compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregate', 'v. i.', 'To come together; to assemble; to meet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregation', 'n.', 'The act of congregating, or bringing together, or of\n   collecting into one aggregate or mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregation', 'n.', 'A collection or mass of separate things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregation', 'n.', 'An assembly of persons; a gathering; esp. an assembly\n   of persons met for the worship of God, and for religious instruction; a\n   body of people who habitually so meet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregation', 'n.', 'The whole body of the Jewish people; -- called also\n   Congregation of the Lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregation', 'n.', 'A body of cardinals or other ecclesiastics to whom as\n   intrusted some department of the church business; as, the Congregation\n   of the Propaganda, which has charge of the missions of the Roman\n   Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregation', 'n.', 'A company of religious persons forming a subdivision\n   of a monastic order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregation', 'n.', 'The assemblage of Masters and Doctors at Oxford or\n   Cambrige University, mainly for the granting of degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregation', 'n.', 'the name assumed by the Protestant party under John\n   Knox. The leaders called themselves (1557) Lords of the Congregation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregational', 'a.', 'Of or pertaining to a congregation; conducted, or\n   participated in, by a congregation; as, congregational singing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregational', 'a.', 'Belonging to the system of Congregationalism, or to\n   Congregationalist; holding to the faith and polity of\n   Congregationalism; as, a Congregational church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregationalism', 'n.', 'That system of church organization which vests\n   all ecclesiastical power in the assembled brotherhood of each local\n   church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregationalism', 'n.', 'The faith and polity of the Congregational\n   churches, taken collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congregationalist', 'n.', 'One who belongs to a Congregational church or\n   society; one who holds to Congregationalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congresses', 'pl. ', 'of Congress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congress', 'n.', 'A meeting of individuals, whether friendly or hostile; an\n   encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congress', 'n.', 'A sudden encounter; a collision; a shock; -- said of\n   things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congress', 'n.', 'The coming together of a male and female in sexual\n   commerce; the act of coition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congress', 'n.', 'A gathering or assembly; a conference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congress', 'n.', 'A formal assembly, as of princes, deputies,\n   representatives, envoys, or commissioners; esp., a meeting of the\n   representatives of several governments or societies to consider and\n   determine matters of common interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congress', 'n.', 'The collective body of senators and representatives of\n   the people of a nation, esp. of a republic, constituting the chief\n   legislative body of the nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congress', 'n.', 'The lower house of the Spanish Cortes, the members of\n   which are elected for three years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congression', 'n.', 'A coming or bringing together, as in a public meeting,\n   in a dispute, in the act of comparing, or in sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congressional', 'a.', 'Of or pertaining to a congress, especially, to the\n   Congress of the United States; as, congressional debates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congressive', 'a.', 'Encountering, or coming together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congressmen', 'pl. ', 'of Congressman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congressman', 'n.', 'A member of the Congress of the United States, esp. of\n   the House of Representatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congreve rocket', '', 'See under Rocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congrue', 'v. i.', 'To agree; to be suitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congruence', 'n.', 'Suitableness of one thing to another; agreement;\n   consistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congruency', 'n.', 'Congruence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congruent', 'a.', 'Possessing congruity; suitable; agreeing; corresponding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congruism', 'n.', 'See Congruity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congruities', 'pl. ', 'of Congruity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congruity', 'n.', 'The state or quality of being congruous; the relation or\n   agreement between things; fitness; harmony; correspondence;\n   consistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congruity', 'n.', 'Coincidence, as that of lines or figures laid over one\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congruity', 'n.', 'That, in an imperfectly good persons, which renders it\n   suitable for God to bestow on him gifts of grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congruous', 'a.', 'Suitable or concordant; accordant; fit; harmonious;\n   correspondent; consistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Congruously', 'adv.', 'In a congruous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conhydrine', 'n.', 'A vegetable alkaloid found with conine in the poison\n   hemlock (Conium maculatum). It is a white crystalline substance,\n   C8H17NO, easily convertible into conine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conia', 'n.', 'Same as Conine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conic', 'a.', 'Alt. of Conical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conical', 'a.', 'Having the form of, or resembling, a geometrical cone;\n   round and tapering to a point, or gradually lessening in circumference;\n   as, a conic or conical figure; a conical vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conical', 'a.', 'Of or pertaining to a cone; as, conic sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conic', 'n.', 'A conic section.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conicality', 'n.', 'Conicalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conically', 'adv.', 'In the form of a cone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conicalness', 'n.', 'State or quality of being conical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conico-', 'a.', 'A combining form, meaning somewhat resembling a cone; as,\n   conico-cylindrical, resembling a cone and a cylinder;\n   conico-hemispherical; conico-subulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conicoid', 'a.', 'Same as Conoidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conics', 'n.', 'That branch of geometry which treats of the cone and the\n   curves which arise from its sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conics', 'n.', 'Conic sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conida', 'pl. ', 'of Conidium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conidium', 'n.', 'A peculiar kind of reproductive cell found in certain\n   fungi, and often containing zoospores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conifer', 'n.', 'A tree or shrub bearing cones; one of the order Coniferae,\n   which includes the pine, cypress, and (according to some) the yew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coniferin', 'n.', 'A glucoside extracted from the cambium layer of\n   coniferous trees as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coniferous', 'a.', 'Bearing cones, as the pine and cypress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coniferous', 'a.', 'Pertaining to the order Coniferae, of which the pine\n   tree is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coniform', 'a.', 'Cone-shaped; conical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coniine', 'n.', 'See Conine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conimene', 'n.', 'Same as Olibene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conine', 'n.', 'A powerful and very poisonous vegetable alkaloid found in\n   the hemlock (Conium maculatum) and extracted as a colorless oil,\n   C8H17N, of strong repulsive odor and acrid taste. It is regarded as a\n   derivative of piperidine and likewise of one of the collidines. It\n   occasions a gradual paralysis of the motor nerves. Called also coniine,\n   coneine, conia, etc. See Conium, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coniroster', 'n.', 'One of the Conirostres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conirostral', 'a.', 'Belonging to the Conirostres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conirostres', 'n. pl.', 'A tribe of perching birds, including those which\n   have a strong conical bill, as the finches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conisor', 'n.', 'See Cognizor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conistra', 'n.', 'Originally, a part of the palestra, or gymnasium among\n   the Greeks; either the place where sand was stored for use in\n   sprinkling the wrestlers, or the wrestling ground itself. Hence, a part\n   of the orchestra of the Greek theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conite', 'n.', 'A magnesian variety of dolomite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conium', 'n.', 'A genus of biennial, poisonous, white-flowered,\n   umbelliferous plants, bearing ribbed fruit (\"seeds\") and decompound\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conium', 'n.', 'The common hemlock (Conium maculatum, poison hemlock,\n   spotted hemlock, poison parsley); a roadside weed of Europe, Asia, and\n   America, cultivated in the United States for medicinal purpose. It is\n   an active poison. The leaves and fruit are used in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conject', 'n.', 'To throw together, or to throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conject', 'v. t.', 'To conjecture; also, to plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjector', 'n.', 'One who guesses or conjectures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjecturable', 'a.', 'Capable of being conjectured or guessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjectural', 'a.', 'Dependent on conjecture; fancied; imagined; guessed\n   at; undetermined; doubtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjecturalist', 'n.', 'A conjecturer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjecturally', 'n.', 'That which depends upon guess; guesswork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjecturally', 'adv.', 'In a conjectural manner; by way of conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjecture', 'n.', 'An opinion, or judgment, formed on defective or\n   presumptive evidence; probable inference; surmise; guess; suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjectured', 'imp. & p. p.', 'of Conjecture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjecturing', 'p. pr. & vb. n.', 'of Conjecture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjecture', 'v. t.', 'To arrive at by conjecture; to infer on slight\n   evidence; to surmise; to guess; to form, at random, opinions\n   concerning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjecture', 'v. i.', 'To make conjectures; to surmise; to guess; to infer;\n   to form an opinion; to imagine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjecturer', 'n.', 'One who conjectures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjoined', 'imp. & p. p.', 'of Conjoin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjoining', 'p. pr. & vb. n.', 'of Conjoin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjoin', 'v. t.', 'To join together; to unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjoin', 'v. i.', 'To unite; to join; to league.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjoined', 'a.', 'Joined together or touching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjoint', 'a.', 'United; connected; associated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjointly', 'adv.', 'In a conjoint manner; untitedly; jointly; together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjointness', 'n.', 'The quality of being conjoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjubilant', 'a.', 'Shouting together for joy; rejoicing together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugal', 'a.', 'Belonging to marriage; suitable or appropriate to the\n   marriage state or to married persons; matrimonial; connubial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugality', 'n.', 'The conjugal state; sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugally', 'adv.', 'In a conjugal manner; matrimonially; connubially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugate', 'a.', 'United in pairs; yoked together; coupled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugate', 'a.', 'In single pairs; coupled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugate', 'a.', 'Containing two or more radicals supposed to act the part\n   of a single one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugate', 'a.', 'Agreeing in derivation and radical signification; --\n   said of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugate', 'a.', 'Presenting themselves simultaneously and having\n   reciprocal properties; -- frequently used in pure and applied\n   mathematics with reference to two quantities, points, lines, axes,\n   curves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugate', 'n.', 'A word agreeing in derivation with another word, and\n   therefore generally resembling it in signification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugate', 'n.', 'A complex radical supposed to act the part of a single\n   radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugated', 'imp. & p. p.', 'of Conjugate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugating', 'p. pr. & vb. n.', 'of Conjugate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugate', 'v. t.', 'To unite in marriage; to join.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugate', 'v. t.', 'To inflect (a verb); or give in order the forms which\n   it assumed in its several voices, moods, tenses, numbers, and persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugate', 'v. i.', 'To unite in a kind of sexual union, as two or more\n   cells or individuals among the more simple plants and animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugation', 'n.', 'the act of uniting or combining; union; assemblage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugation', 'n.', 'Two things conjoined; a pair; a couple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugation', 'n.', 'The act of conjugating a verb or giving in order its\n   various parts and inflections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugation', 'n.', 'A scheme in which are arranged all the parts of a\n   verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugation', 'n.', 'A class of verbs conjugated in the same manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugation', 'n.', 'A kind of sexual union; -- applied to a blending of\n   the contents of two or more cells or individuals in some plants and\n   lower animals, by which new spores or germs are developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugational', 'a.', 'relating to conjugation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugial', 'a.', 'Conjugal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjugium', 'n.', 'The marriage tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunct', 'a.', 'United; conjoined; concurrent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunct', 'a.', 'Same as Conjoined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunction', 'n.', 'The act of conjoining, or the state of being\n   conjoined, united, or associated; union; association; league.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunction', 'n.', 'The meeting of two or more stars or planets in the\n   same degree of the zodiac; as, the conjunction of the moon with the\n   sun, or of Jupiter and Saturn. See the Note under Aspect, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunction', 'n.', 'A connective or connecting word; an indeclinable word\n   which serves to join together sentences, clauses of a sentence, or\n   words; as, and, but, if.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunctional', 'a.', 'Relating to a conjunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunctiva', 'n.', 'The mucous membrane which covers the external surface\n   of the ball of the eye and the inner surface of the lids; the\n   conjunctival membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunctival', 'a.', 'Joining; connecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunctival', 'a.', 'Of or pertaining to the conjunctiva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunctive', 'a.', 'Serving to unite; connecting together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunctive', 'a.', 'Closely united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunctively', 'adv.', 'In conjunction or union; together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunctiveness', 'n.', 'The state or quality of being conjunctive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunctivitis', 'n.', 'Inflammation of the conjunctiva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjunctly', 'adv.', 'In union; conjointly; unitedly; together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjuncture', 'n.', 'The act of joining, or state of being joined; union;\n   connection; combination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjuncture', 'n.', 'A crisis produced by a combination of circumstances;\n   complication or combination of events or circumstances; plight\n   resulting from various conditions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjuration', 'n.', 'The act of calling or summoning by a sacred name, or\n   in solemn manner; the act of binding by an oath; an earnest entreaty;\n   adjuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjuration', 'n.', 'The act or process of invoking supernatural aid by the\n   use of a magical form of words; the practice of magic arts;\n   incantation; enchantment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjuration', 'n.', 'A league for a criminal purpose; conspiracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjurator', 'n.', 'One who swears or is sworn with others; one bound by\n   oath with others; a compurgator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjured', 'imp. & p. p.', 'of Conjure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjuring', 'p. pr. & vb. n.', 'of Conjure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjure', 'v. t.', 'To call on or summon by a sacred name or in solemn\n   manner; to implore earnestly; to adjure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjure', 'v. i.', 'To combine together by an oath; to conspire; to\n   confederate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjure', 'v. t.', 'To affect or effect by conjuration; to call forth or\n   send away by magic arts; to excite or alter, as if by magic or by the\n   aid of supernatural powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjure', 'v. i.', 'To practice magical arts; to use the tricks of a\n   conjurer; to juggle; to charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjurement', 'n.', 'Serious injunction; solemn demand or entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjurer', 'n.', 'One who conjures; one who calls, entreats, or charges in\n   a solemn manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjurer', 'n.', 'One who practices magic arts; one who pretends to act by\n   the aid super natural power; also, one who performs feats of\n   legerdemain or sleight of hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjurer', 'n.', 'One who conjectures shrewdly or judges wisely; a man of\n   sagacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjuror', 'n.', 'One bound by a common oath with others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conjury', 'n.', 'The practice of magic; enchantment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conn', 'v. t.', 'See Con, to direct a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connascence', 'n.', 'Alt. of Connascency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connascency', 'n.', 'The common birth of two or more at the same tome;\n   production of two or more together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connascency', 'n.', 'That which is born or produced with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connascency', 'n.', 'The act of growing together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connascent', 'a.', 'Born together; produced at the same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connate', 'a.', 'Born with another; being of the same birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connate', 'a.', 'Congenital; existing from birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connate', 'a.', 'Congenitally united; growing from one base, or united at\n   their bases; united into one body; as, connate leaves or athers. See\n   Illust. of Connate-perfoliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connate-perfoliate', 'a.', 'Connate or coalescent at the base so as to\n   produce a broad foliaceous body through the center of which the stem\n   passes; -- applied to leaves, as the leaves of the boneset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connation', 'n.', 'Connection by birth; natural union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connatural', 'a. ', 'Connected by nature; united in nature; inborn;\n   inherent; natural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connatural', 'a. ', 'Partaking of the same nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connaturality', 'n.', 'Participation of the same nature; natural union or\n   connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connaturalize', 'v. t.', 'To bring to the same nature as something else; to\n   adapt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connaturally', 'adv.', 'By the act of nature; originally; from birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connaturalness', 'n.', 'Participation of the same nature; natural union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connature', 'n.', 'Participation in a common nature or character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connected', 'imp. & p. p.', 'of Connect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connecting', 'p. pr. & vb. n.', 'of Connect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connect', 'v. t.', 'To join, or fasten together, as by something\n   intervening; to associate; to combine; to unite or link together; to\n   establish a bond or relation between.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connect', 'v. t.', 'To associate (a person or thing, or ones self) with\n   another person, thing, business, or affair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connect', 'v. i.', 'To join, unite, or cohere; to have a close relation;\n   as, one line of railroad connects with another; one argument connect\n   with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connectedly', 'adv.', 'In a connected manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connection', 'n.', 'The act of connecting, or the state of being connected;\n   junction; union; alliance; relationship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connection', 'n.', 'That which connects or joins together; bond; tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connection', 'n.', 'A relation; esp. a person connected with another by\n   marriage rather than by blood; -- used in a loose and indefinite, and\n   sometimes a comprehensive, sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connection', 'n.', 'The persons or things that are connected; as, a\n   business connection; the Methodist connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connective', 'a.', 'Connecting, or adapted to connect; involving\n   connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connective', 'n.', 'That which connects');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connective', 'n.', 'A word that connect words or sentences; a conjunction\n   or preposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connective', 'n.', 'That part of an anther which connects its thecae,\n   lobes, or cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connectively', 'adv.', 'In connjunction; jointly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connector', 'n.', 'One who, or that which, connects');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connector', 'n.', 'A flexible tube for connecting the ends of glass tubes\n   in pneumatic experiments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connector', 'n.', 'A device for holding two parts of an electrical\n   conductor in contact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conner', 'n.', 'A marine European fish (Crenilabrus melops); also, the\n   related American cunner. See Cunner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connex', 'v. t.', 'To connect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connexion', 'n.', 'Connection. See Connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connexive', 'a.', 'See Connective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conning tower', 'n.', 'The shot-proof pilot house of a war vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connivance', 'n.', 'Intentional failure or forbearance to discover a fault\n   or wrongdoing; voluntary oversight; passive consent or cooperation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connivance', 'n.', 'Corrupt or guilty assent to wrongdoing, not involving\n   actual participation in, but knowledge of, and failure to prevent or\n   oppose it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connived', 'imp. & p. p.', 'of Connive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conniving', 'p. pr. & vb. n.', 'of Connive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connive', 'v. i.', 'To open and close the eyes rapidly; to wink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connive', 'v. i.', 'To close the eyes upon a fault; to wink (at); to fail\n   or forbear by intention to discover an act; to permit a proceeding, as\n   if not aware of it; -- usually followed by at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connive', 'v. t.', 'To shut the eyes to; to overlook; to pretend not to\n   see.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connivency', 'n.', 'Connivance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connivent', 'a.', 'Forbearing to see; designedly inattentive; as, connivent\n   justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connivent', 'a.', 'Brought close together; arched inward so that the points\n   meet; converging; in close contact; as, the connivent petals of a\n   flower, wings of an insect, or folds of membrane in the human system,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conniver', 'n.', 'One who connives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connoisseur', 'n.', 'One well versed in any subject; a skillful or knowing\n   person; a critical judge of any art, particulary of one of the fine\n   arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connoisseurship', 'n.', 'State of being a connoisseur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connotate', 'v. t.', 'To connote; to suggest or designate (something) as\n   additional; to include; to imply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connotation', 'n.', 'The act of connoting; a making known or designating\n   something additional; implication of something more than is asserted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connotative', 'a.', 'Implying something additional; illative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connotative', 'a.', 'Implying an attribute. See Connote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connotatively', 'adv.', 'In a connotative manner; expressing connotation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connoted', 'imp. & p. p.', 'of Connote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connoting', 'p. pr. & vb. n.', 'of Connote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connote', 'v. t.', 'To mark along with; to suggest or indicate as\n   additional; to designate by implication; to include in the meaning; to\n   imply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connote', 'v. t.', 'To imply as an attribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connubial', 'a.', 'Of or pertaining to marriage, or the marriage state;\n   conjugal; nuptial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connubiality', 'n.', 'The quality of being connubial; something\n   characteristics of the conjugal state; an expression of connubial\n   tenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connumeration', 'n.', 'A reckoning together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connusance', 'n.', 'See Cognizance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connusant', 'a.', 'See Cognizant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connusor', 'n.', 'See Cognizor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Connutritious', 'a.', 'Nutritious by force of habit; -- said of certain\n   kinds of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conny', 'a.', 'Brave; fine; canny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conodont', 'n.', 'A peculiar toothlike fossil of many forms, found\n   especially in carboniferous rocks. Such fossils are supposed by some to\n   be the teeth of marsipobranch fishes, but they are probably the jaws of\n   annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conoid', 'n.', 'Anything that has a form resembling that of a cone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conoid', 'n.', 'A solid formed by the revolution of a conic section about\n   its axis; as, a parabolic conoid, elliptic conoid, etc.; -- more\n   commonly called paraboloid, ellipsoid, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conoid', 'n.', 'A surface which may be generated by a straight line moving\n   in such a manner as always to meet a given straight line and a given\n   curve, and continue parallel to a given plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conoid', 'a.', 'Resembling a cone; conoidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conoidal', 'a.', 'Nearly, but not exactly, conical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conoidic', 'a.', 'Alt. of Conoidical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conoidical', 'a.', 'Pertaining to a conoid; having the form of a conoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conominee', 'n.', 'One nominated in conjunction with another; a joint\n   nominee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquadrate', 'v. t.', 'To bring into a square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquassate', 'v. t.', 'To shake; to agitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquered', 'imp. & p. p.', 'of Conquer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquering', 'p. pr. & vb. n.', 'of Conquer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquer', 'v. t.', 'To gain or acquire by force; to take possession of by\n   violent means; to gain dominion over; to subdue by physical means; to\n   reduce; to overcome by force of arms; to cause to yield; to vanquish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquer', 'v. t.', 'To subdue or overcome by mental or moral power; to\n   surmount; as, to conquer difficulties, temptation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquer', 'v. t.', 'To gain or obtain, overcoming obstacles in the way; to\n   win; as, to conquer freedom; to conquer a peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquer', 'v. i.', 'To gain the victory; to overcome; to prevail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquerable', 'a.', 'Capable of being conquered or subdued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conqueress', 'n.', 'A woman who conquers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conqueror', 'n.', 'One who conquers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquest', 'n.', 'The act or process of conquering, or acquiring by force;\n   the act of overcoming or subduing opposition by force, whether physical\n   or moral; subjection; subjugation; victory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquest', 'n.', 'That which is conquered; possession gained by force,\n   physical or moral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquest', 'n.', 'The acquiring of property by other means than by\n   inheritance; acquisition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conquest', 'n.', 'The act of gaining or regaining by successful struggle;\n   as, the conquest of liberty or peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consanguineal', 'a.', 'Of the same blood; related by birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consanguined', 'a.', 'Of kin blood; related.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consanguineous', 'a.', 'Of the same blood; related by birth; descended from\n   the same parent or ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consanguinity', 'n.', 'The relation of persons by blood, in distinction\n   from affinity or relation by marriage; blood relationship; as, lineal\n   consanguinity; collateral consanguinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consarcination', 'n.', 'A patching together; patchwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscience', 'n.', 'Knowledge of ones own thoughts or actions;\n   consciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscience', 'n.', 'The faculty, power, or inward principle which decides\n   as to the character of ones own actions, purposes, and affections,\n   warning against and condemning that which is wrong, and approving and\n   prompting to that which is right; the moral faculty passing judgment on\n   ones self; the moral sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscience', 'n.', 'The estimate or determination of conscience; conviction\n   or right or duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscience', 'n.', 'Tenderness of feeling; pity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscienced', 'a.', 'Having a conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscienceless', 'a.', 'Without conscience; indifferent to conscience;\n   unscrupulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscient', 'a.', 'Conscious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscientious', 'a.', 'Influenced by conscience; governed by a strict\n   regard to the dictates of conscience, or by the known or supposed rules\n   of right and wrong; -- said of a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscientious', 'a.', 'Characterized by a regard to conscience; conformed\n   to the dictates of conscience; -- said of actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscientiously', 'adv.', 'In a conscientious manner; as a matter of\n   conscience; hence; faithfully; accurately; completely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscientiousness', 'n.', 'The quality of being conscientious; a scrupulous\n   regard to the dictates of conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscionable', 'a.', 'Governed by, or according to, conscience; reasonable;\n   just.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscionableness', 'n.', 'The quality of being conscionable;\n   reasonableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscionably', 'adv.', 'Reasonably; justly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscious', 'a.', 'Possessing the faculty of knowing ones own thoughts or\n   mental operations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscious', 'a.', 'Possessing knowledge, whether by internal, conscious\n   experience or by external observation; cognizant; aware; sensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscious', 'a.', 'Made the object of consciousness; known to ones self;\n   as, conscious guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consciously', 'adv.', 'In a conscious manner; with knowledge of ones own\n   mental operations or actions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consciousness', 'n.', 'The state of being conscious; knowledge of ones own\n   existence, condition, sensations, mental operations, acts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consciousness', 'n.', 'Immediate knowledge or perception of the presence of\n   any object, state, or sensation. See the Note under Attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consciousness', 'n.', 'Feeling, persuasion, or expectation; esp., inward\n   sense of guilt or innocence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscribe', 'v. t.', 'To enroll; to enlist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscript', 'a.', 'Enrolled; written; registered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscript', 'n.', 'One taken by lot, or compulsorily enrolled, to serve as\n   a soldier or sailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscript', 'v. t.', 'To enroll, by compulsion, for military service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscription', 'n.', 'An enrolling or registering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscription', 'n.', 'A compulsory enrollment of men for military or naval\n   service; a draft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conscription', 'a.', 'Belonging to, or of the nature of, a conspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecrate', 'a.', 'Consecrated; devoted; dedicated; sacred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecrated', 'imp. & p. p.', 'of Consecrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecrating', 'p. pr. & vb. n.', 'of Consecrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecrate', 'v. t.', 'To make, or declare to be, sacred; to appropriate to\n   sacred uses; to set apart, dedicate, or devote, to the service or\n   worship of God; as, to consecrate a church; to give (ones self)\n   unreservedly, as to the service of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecrate', 'v. t.', 'To set apart to a sacred office; as, to consecrate a\n   bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecrate', 'v. t.', 'To canonize; to exalt to the rank of a saint; to\n   enroll among the gods, as a Roman emperor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecrate', 'v. t.', 'To render venerable or revered; to hallow; to\n   dignify; as, rules or principles consecrated by time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecrater', 'n.', 'Consecrator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecration', 'n.', 'The act or ceremony of consecrating; the state of\n   being consecrated; dedication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecrator', 'n.', 'One who consecrates; one who performs the rites by\n   which a person or thing is devoted or dedicated to sacred purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecratory', 'a.', 'Of or pertaining to the act of consecration;\n   dedicatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consectaneous', 'a.', 'Following as a matter of course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consectary', 'a.', 'Following by consequence; consequent; deducible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consectary', 'n.', 'That which follows by consequence or is logically\n   deducible; deduction from premises; corollary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecute', 'v. t.', 'To follow closely; to endeavor to overtake; to\n   pursue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecution', 'n.', 'A following, or sequel; actual or logical dependence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecution', 'n.', 'A succession or series of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecutive', 'a.', 'Following in a train; succeeding one another in a\n   regular order; successive; uninterrupted in course or succession; with\n   no interval or break; as, fifty consecutive years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecutive', 'a.', 'Following as a consequence or result; actually or\n   logically dependent; consequential; succeeding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecutive', 'a.', 'Having similarity of sequence; -- said of certain\n   parallel progressions of two parts in a piece of harmony; as,\n   consecutive fifths, or consecutive octaves, which are forbidden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecutively', 'adv.', 'In a consecutive manner; by way of sequence;\n   successively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consecutiveness', 'n.', 'The state or quality of being consecutive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consension', 'n.', 'Agreement; accord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consensual', 'v. i.', 'Existing, or made, by the mutual consent of two or\n   more parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consensual', 'v. i.', 'Excited or caused by sensation, sympathy, or reflex\n   action, and not by conscious volition; as, consensual motions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consensus', 'n.', 'Agreement; accord; consent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consented', 'imp. & p. p.', 'of Consent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consenting', 'p. pr. & vb. n', 'of Consent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consent', 'v. i.', 'To agree in opinion or sentiment; to be of the same\n   mind; to accord; to concur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consent', 'v. i.', 'To indicate or express a willingness; to yield to\n   guidance, persuasion, or necessity; to give assent or approval; to\n   comply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consent', 'v. t.', 'To grant; to allow; to assent to; to admit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consent', 'n.', 'Agreement in opinion or sentiment; the being of one mind;\n   accord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consent', 'n.', 'Correspondence in parts, qualities, or operations;\n   agreement; harmony; coherence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consent', 'n.', 'Voluntary accordance with, or concurrence in, what is done\n   or proposed by another; acquiescence; compliance; approval; permission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consent', 'n.', 'Capable, deliberate, and voluntary assent or agreement to,\n   or concurrence in, some act or purpose, implying physical and mental\n   power and free action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consent', 'n.', 'Sympathy. See Sympathy, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consentaneity', 'n.', 'Mutual agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consentaneous', 'a.', 'Consistent; agreeable; suitable; accordant to;\n   harmonious; concurrent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consentant', 'a.', 'Consenting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consenter', 'a.', 'One who consents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consentient', 'a.', 'Agreeing in mind; accordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consentingly', 'adv.', 'With consent; in a compliant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequence', 'n.', 'That which follows something on which it depends; that\n   which is produced by a cause; a result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequence', 'n.', 'A proposition collected from the agreement of other\n   previous propositions; any conclusion which results from reason or\n   argument; inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequence', 'n.', 'Chain of causes and effects; consecution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequence', 'n.', 'Importance with respect to what comes after; power to\n   influence or produce an effect; value; moment; rank; distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequencing', 'n.', 'Drawing inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequent', 'a.', 'Following as a result, inference, or natural effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequent', 'a.', 'Following by necessary inference or rational deduction;\n   as, a proposition consequent to other propositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequent', 'n.', 'That which follows, or results from, a cause; a result\n   or natural effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequent', 'n.', 'That which follows from propositions by rational\n   deduction; that which is deduced from reasoning or argumentation; a\n   conclusion, or inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequent', 'n.', 'The second term of a ratio, as the term b in the ratio\n   a:b, the first a, being the antecedent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequential', 'a.', 'Following as a consequence, result, or logical\n   inference; consequent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequential', 'a.', 'Assuming or exhibiting an air of consequence;\n   pretending to importance; pompous; self-important; as, a consequential\n   man. See Consequence, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequentially', 'adv.', 'With just deduction of consequence; with right\n   connection of ideas; logically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequentially', 'adv.', 'By remote consequence; not immediately;\n   eventually; as, to do a thing consequentially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequentially', 'adv.', 'In a regular series; in the order of cause and\n   effect; with logical concatenation; consecutively; continuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequentially', 'adv.', 'With assumed importance; pompously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequentialness', 'n.', 'The quality of being consequential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consequently', 'adv.', 'By consequence; by natural or logical sequence or\n   connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consertion', 'n.', 'Junction; adaptation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservable', 'a.', 'Capable of being preserved from decay or injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservancy', 'n.', 'Conservation, as from injury, defilement, or irregular\n   use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservant', 'a.', 'Having the power or quality of conservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservation', 'n.', 'The act of preserving, guarding, or protecting; the\n   keeping (of a thing) in a safe or entire state; preservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservational', 'a.', 'Tending to conserve; preservative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservatism', 'n.', 'The disposition and tendency to preserve what is\n   established; opposition to change; the habit of mind; or conduct, of a\n   conservative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservative', 'a.', 'Having power to preserve in a safe of entire state,\n   or from loss, waste, or injury; preservative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservative', 'a.', 'Tending or disposed to maintain existing\n   institutions; opposed to change or innovation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservative', 'a.', 'Of or pertaining to a political party which favors\n   the conservation of existing institutions and forms of government, as\n   the Conservative party in England; -- contradistinguished from Liberal\n   and Radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservative', 'n.', 'One who, or that which, preserves from ruin, injury,\n   innovation, or radical change; a preserver; a conserver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservative', 'n.', 'One who desires to maintain existing institutions and\n   customs; also, one who holds moderate opinions in politics; -- opposed\n   to revolutionary or radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservative', 'n.', 'A member of the Conservative party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservativeness', 'a.', 'The quality of being conservative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservatoire', 'n.', 'A public place of instruction in any special branch,\n   esp. music and the arts. [See Conservatory, 3].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservator', 'n.', 'One who preserves from injury or violation; a\n   protector; a preserver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservator', 'n.', 'An officer who has charge of preserving the public\n   peace, as a justice or sheriff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservator', 'n.', 'One who has an official charge of preserving the\n   rights and privileges of a city, corporation, community, or estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservatory', 'a.', 'Having the quality of preserving from loss, decay, or\n   injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservatory', 'n.', 'That which preserves from injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservatory', 'n.', 'A place for preserving anything from loss, decay,\n   waste, or injury; particulary, a greenhouse for preserving exotic or\n   tender plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservatory', 'n.', 'A public place of instruction, designed to preserve\n   and perfect the knowledge of some branch of science or art, esp. music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conservatrix', 'n.', 'A woman who preserves from loss, injury, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conserved', 'imp. & p. p.', 'of Conserve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conserving', 'p. pr. & vb. n.', 'of Conserve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conserve', 'v. t.', 'To keep in a safe or sound state; to save; to\n   preserve; to protect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conserve', 'v. t.', 'To prepare with sugar, etc., for the purpose of\n   preservation, as fruits, etc.; to make a conserve of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conserve', 'n.', 'Anything which is conserved; especially, a sweetmeat\n   prepared with sugar; a confection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conserve', 'n.', 'A medicinal confection made of freshly gathered vegetable\n   substances mixed with finely powdered refined sugar. See Confection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conserve', 'n.', 'A conservatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conserver', 'n.', 'One who conserves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considered', 'imp. & p. p.', 'of Consider');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considering', 'p. pr. & vb. n.', 'of Consider');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consider', 'v. t.', 'To fix the mind on, with a view to a careful\n   examination; to think on with care; to ponder; to study; to meditate\n   on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consider', 'v. t.', 'To look at attentively; to observe; to examine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consider', 'v. t.', 'To have regard to; to take into view or account; to\n   pay due attention to; to respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consider', 'v. t.', 'To estimate; to think; to regard; to view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consider', 'v. i.', 'To think seriously; to make examination; to reflect;\n   to deliberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consider', 'v. i.', 'To hesitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerable', 'a.', 'Worthy of consideration, borne in mind, or attended\n   to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerable', 'a.', 'Of some distinction; noteworthy; influential;\n   respectable; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerable', 'a.', 'Of importance or value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerableness', 'n.', 'Worthiness of consideration; dignity; value;\n   size; amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerably', 'adv.', 'In a manner or to a degree not trifling or\n   unimportant; greatly; much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerance', 'n.', 'Act of considering; consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerate', 'a.', 'Given to consideration or to sober reflection;\n   regardful of consequences or circumstances; circumspect; careful; esp.\n   careful of the rights, claims, and feelings of other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerate', 'a.', 'Having respect to; regardful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consideration', 'n.', 'The act or process of considering; continuous\n   careful thought; examination; contemplation; deliberation; attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consideration', 'n.', 'Attentive respect; appreciative regard; -- used\n   especially in diplomatic or stately correspondence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consideration', 'n.', 'Thoughtful or sympathetic regard or notice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consideration', 'n.', 'Claim to notice or regard; some degree of importance\n   or consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consideration', 'n.', 'The result of delibration, or of attention and\n   examonation; matured opinion; a reflection; as, considerations on the\n   choice of a profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consideration', 'n.', 'That which is, or should be, taken into account as a\n   ground of opinion or action; motive; reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consideration', 'n.', 'The cause which moves a contracting party to enter\n   into an agreement; the material cause of a contract; the price of a\n   stripulation; compensation; equivalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerative', 'a.', 'Considerate; careful; thoughtful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerator', 'n.', 'One who considers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Considerer', 'n.', 'One who considers; a man of reflection; a thinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consideringly', 'adv.', 'With consideration or deliberation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consigned', 'imp. & p. p.', 'of Consign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consigning', 'p. pr. & vb. n.', 'of Consign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consign', 'v. t.', 'To give, transfer, or deliver, in a formal manner, as\n   if by signing over into the possession of another, or into a different\n   state, with the sense of fixedness in that state, or permanence of\n   possession; as, to consign the body to the grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consign', 'v. t.', 'To give in charge; to commit; to intrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consign', 'v. t.', 'To send or address (by bill of lading or otherwise) to\n   an agent or correspondent in another place, to be cared for or sold, or\n   for the use of such correspondent; as, to consign a cargo or a ship; to\n   consign goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consign', 'v. t.', 'To assign; to devote; to set apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consign', 'v. t.', 'To stamp or impress; to affect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consign', 'v. i.', 'To submit; to surrender or yield ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consign', 'v. i.', 'To yield consent; to agree; to acquiesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignatary', 'n.', 'A consignee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignation', 'n.', 'The act of consigning; the act of delivering or\n   committing to another person, place, or state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignation', 'n.', 'The act of ratifying or establishing, as if by\n   signing; confirmation; ratification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignation', 'n.', 'A stamp; an indication; a sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignatory', 'n.', 'One of several that jointly sign a written\n   instrument, as a treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignature', 'n.', 'Joint signature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consigne', 'n.', 'A countersign; a watchword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consigne', 'n.', 'One who is orders to keep within certain limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignee', 'n.', 'The person to whom goods or other things are consigned;\n   a factor; -- correlative to consignor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consigner', 'n.', 'One who consigns. See Consignor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignificant', 'a.', 'Having joint or equal signification; synonymous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignification', 'n.', 'Joint signification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignificative', 'a.', 'Consignificant; jointly significate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignify', 'v. t.', 'To signify or denote in combination with something\n   else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignment', 'n.', 'The act of consigning; consignation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignment', 'n.', 'The act of consigning or sending property to an agent\n   or correspondent in another place, as for care, sale, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignment', 'n.', 'That which is consigned; the goods or commodities sent\n   or addressed to a consignee at one time or by one conveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignment', 'n.', 'The writing by which anything is consigned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consignor', 'n.', 'One who consigns something to another; -- opposed to\n   consignee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consilience', 'n.', 'Act of concurring; coincidence; concurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consimilitude', 'n.', 'Alt. of Consimility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consimility', 'n.', 'Common resemblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consisted', 'imp. & p. p.', 'of Consist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consisting', 'p. pr. & vb. n.', 'of Consist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consist', 'v. i.', 'To stand firm; to be in a fixed or permanent state, as\n   a body composed of parts in union or connection; to hold together; to\n   be; to exist; to subsist; to be supported and maintained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consist', 'v. i.', 'To be composed or made up; -- followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consist', 'v. i.', 'To have as its substance or character, or as its\n   foundation; to be; -- followed by in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consist', 'v. i.', 'To be consistent or harmonious; to be in accordance; --\n   formerly used absolutely, now followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consist', 'v. i.', 'To insist; -- followed by on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistence', 'n.', 'Alt. of Consistency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistency', 'n.', 'The condition of standing or adhering together, or\n   being fixed in union, as the parts of a body; existence; firmness;\n   coherence; solidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistency', 'n.', 'A degree of firmness, density, or spissitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistency', 'n.', 'That which stands together as a united whole; a\n   combination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistency', 'n.', 'Firmness of constitution or character; substantiality;\n   durability; persistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistency', 'n.', 'Agreement or harmony of all parts of a complex thing\n   among themselves, or of the same thing with itself at different times;\n   the harmony of conduct with profession; congruity; correspondence; as,\n   the consistency of laws, regulations, or judicial decisions;\n   consistency of opinions; consistency of conduct or of character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistent', 'a.', 'Possessing firmness or fixedness; firm; hard; solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistent', 'a.', 'Having agreement with itself or with something else;\n   having harmony among its parts; possesing unity; accordant; harmonious;\n   congruous; compatible; uniform; not contradictory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistent', 'a.', 'Living or acting in conformity with ones belief or\n   professions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistently', 'adv.', 'In a consistent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistorial', 'a.', 'Of or pertaining to a consistory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistorian', 'a.', 'Pertaining to a Presbyterian consistory; -- a\n   contemptuous term of 17th century controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistories', 'pl. ', 'of Consistory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistory', 'n.', 'Primarily, a place of standing or staying together;\n   hence, any solemn assembly or council.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistory', 'n.', 'The spiritual court of a diocesan bishop held before\n   his chancellor or commissioner in his cathedral church or elsewhere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistory', 'n.', 'An assembly of prelates; a session of the college of\n   cardinals at Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistory', 'n.', 'A church tribunal or governing body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistory', 'n.', 'A civil court of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consistory', 'a.', 'Of the nature of, or pertaining to, a consistory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consociate', 'n.', 'An associate; an accomplice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consociated', 'imp. & p. p.', 'of Consociate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consociating', 'p. pr. & vb. n.', 'of Consociate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consociate', 'v. t.', 'To bring into alliance, confederacy, or\n   relationship; to bring together; to join; to unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consociate', 'v. t.', 'To unite in an ecclesiastical consociation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consociate', 'v. i.', 'To be allied, confederated, or associated; to\n   coalescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consociate', 'v. i.', 'To form an ecclesiastical consociation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consociation', 'n.', 'Intimate union; fellowship; alliance; companionship;\n   confederation; association; intimacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consociation', 'n.', 'A voluntary and permanent council or union of\n   neighboring Congregational churches, for mutual advice and cooperation\n   in ecclesiastical matters; a meeting of pastors and delegates from\n   churches thus united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consociational', 'a.', 'Of or pertaining to a consociation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolable', 'a.', 'Capable of receiving consolation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolate', 'v. t.', 'To console; to comfort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolation', 'n.', 'The act of consoling; the state of being consoled;\n   allevation of misery or distress of mind; refreshment of spirit;\n   comfort; that which consoles or comforts the spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolato del mare', '', 'A collection of maritime laws of disputed origin,\n   supposed to have been first published at Barcelona early in the 14th\n   century. It has formed the basis of most of the subsequent collections\n   of maritime laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolator', 'n.', 'One who consoles or comforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolatory', 'a.', 'Of a consoling or comforting nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolatory', 'n.', 'That which consoles; a speech or writing intended for\n   consolation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consoled', 'imp. & p. p.', 'of Console');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consoling', 'p. pr. & vb. n.', 'of Console');
  INSERT INTO words (word, word_type, description) VALUES  (    'Console', 'v. t.', 'To cheer in distress or depression; to alleviate the\n   grief and raise the spirits of; to relieve; to comfort; to soothe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Console', 'n.', 'A bracket whose projection is not more than half its\n   height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Console', 'n.', 'Any small bracket; also, a console table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consoler', 'n.', 'One who gives consolation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidant', 'a.', 'Serving to unite or consolidate; having the quality of\n   consolidating or making firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidate', 'a.', 'Formed into a solid mass; made firm; consolidated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidated', 'imp. & p. p.', 'of Consolidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidating', 'p. pr. & vb. n.', 'of Consolidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidate', 'v. t.', 'To make solid; to unite or press together into a\n   compact mass; to harden or make dense and firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidate', 'v. t.', 'To unite, as various particulars, into one mass or\n   body; to bring together in close union; to combine; as, to consolidate\n   the armies of the republic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidate', 'v. t.', 'To unite by means of applications, as the parts of\n   a broken bone, or the lips of a wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidate', 'v. i.', 'To grow firm and hard; to unite and become solid;\n   as, moist clay consolidates by drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidated', 'p. p. & a.', 'Made solid, hard, or compact; united; joined;\n   solidified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidated', 'p. p. & a.', 'Having a small surface in proportion to bulk,\n   as in the cactus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidation', 'n.', 'The act or process of consolidating, making firm, or\n   uniting; the state of being consolidated; solidification; combination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidation', 'n.', 'To organic cohesion of different circled in a\n   flower; adnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidation', 'n.', 'The combination of several actions into one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consolidative', 'a.', 'Tending or having power to consolidate; healing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consoling', 'a.', 'Adapted to console or comfort; cheering; as, this is\n   consoling news.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consols', 'n. pl. ', 'The leading British funded government security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consomme', 'n.', 'A clear soup or bouillion boiled down so as to be very\n   rich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonance', 'n.', 'Alt. of Consonancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonancy', 'n.', 'Accord or agreement of sounds produced simultaneously,\n   as a note with its third, fifth, and eighth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonancy', 'n.', 'Agreement or congruity; harmony; accord; consistency;\n   suitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonancy', 'n.', 'Friendship; concord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonant', 'a.', 'Having agreement; congruous; consistent; according; --\n   usually followed by with or to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonant', 'a.', 'Having like sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonant', 'a.', 'harmonizing together; accordant; as, consonant tones,\n   consonant chords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonant', 'a.', 'Of or pertaining to consonants; made up of, or\n   containing many, consonants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonant', 'n.', 'An articulate sound which in utterance is usually\n   combined and sounded with an open sound called a vowel; a member of the\n   spoken alphabet other than a vowel; also, a letter or character\n   representing such a sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonantal', 'a.', 'Of the nature of a consonant; pertaining to\n   consonants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonantize', 'v. t.', 'To change into, or use as, a consonant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonantly', 'adv.', 'In a consonant, consistent, or congruous manner;\n   agreeably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonantness', 'n.', 'The quality or condition of being consonant,\n   agreeable, or consistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consonous', 'a.', 'Agreeing in sound; symphonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consopiation', 'n.', 'The act of sleeping, or of lulling, to sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consopite', 'a.', 'Lulled to sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consopite', 'v. t.', 'To lull to sleep; to quiet; to compose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consort', 'n.', 'One who shares the lot of another; a companion; a partner;\n   especially, a wife or husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consort', 'n.', 'A ship keeping company with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consort', 'n.', 'Concurrence; conjunction; combination; association; union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consort', 'n.', 'An assembly or association of persons; a company; a group;\n   a combination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consort', 'n.', 'Harmony of sounds; concert, as of musical instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consorted', 'imp. & p. p.', 'of Consort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consorting', 'p. pr. & vb. n.', 'of Consort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consort', 'v. i.', 'To unite or to keep company; to associate; -- used with\n   with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consort', 'v. t.', 'To unite or join, as in affection, harmony, company,\n   marriage, etc.; to associate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consort', 'v. t.', 'To attend; to accompany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consortable', 'a.', 'Suitable for association or companionship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consortion', 'n.', 'Fellowship; association; companionship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consortship', 'n.', 'The condition of a consort; fellowship; partnership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consound', 'n.', 'A name applied loosely to several plants of different\n   genera, esp. the comfrey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspecific', 'a.', 'Of the same species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspectuities', 'pl. ', 'of Conspectuity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspectuity', 'n.', 'The faculty of seeing; sight; eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspectus', 'n.', 'A general sketch or outline of a subject; a synopsis;\n   an epitome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspersion', 'n.', 'The act of sprinkling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspicuity', 'n.', 'The state or quality of being clear or bright;\n   brightness; conspicuousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspicuous', 'a.', 'Open to the view; obvious to the eye; easy to be seen;\n   plainly visible; manifest; attracting the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspicuous', 'a.', 'Obvious to the mental eye; easily recognized; clearly\n   defined; notable; prominent; eminent; distinguished; as, a conspicuous\n   excellence, or fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspiracies', 'pl. ', 'of Conspiracy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspiracy', 'n.', 'A combination of men for an evil purpose; an agreement,\n   between two or more persons, to commit a crime in concert, as treason;\n   a plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspiracy', 'n.', 'A concurence or general tendency, as of circumstances,\n   to one event, as if by agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspiracy', 'n.', 'An agreement, manifesting itself in words or deeds, by\n   which two or more persons confederate to do an unlawful act, or to use\n   unlawful to do an act which is lawful; confederacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspirant', 'a.', 'Engaging in a plot to commit a crime; conspiring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspiration', 'n.', 'Agreement or concurrence for some end or purpose;\n   conspiracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspirator', 'n.', 'One who engages in a conspiracy; a plotter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspired', 'imp. & p. p.', 'of Conspire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspiring', 'p. pr. & vb. n.', 'of Conspire');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspire', 'v. i.', 'To make an agreement, esp. a secret agreement, to do\n   some act, as to commit treason or a crime, or to do some unlawful deed;\n   to plot together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspire', 'v. i.', 'To concur to one end; to agree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspire', 'v. t.', 'To plot; to plan; to combine for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspirer', 'n.', 'One who conspires; a conspirator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspiringly', 'adv.', 'In the manner of a conspirator; by conspiracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspissation', 'n.', 'A making thick or viscous; thickness; inspissation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspurcate', 'v. t.', 'To pollute; to defile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conspurcation', 'n.', 'The act of defiling; defilement; pollution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constable', 'n.', 'A high officer in the monarchical establishments of the\n   Middle Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constable', 'n.', 'An officer of the peace having power as a conservator of\n   the public peace, and bound to execute the warrants of judicial\n   officers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constablery', 'n.', 'The constabulary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constablery', 'n.', 'The district or jurisdiction of a constable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constableship', 'n.', 'The office or functions of a constable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constabless', 'n.', 'The wife of a constable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constablewick', 'n.', 'The district to which a constables power is\n   limited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constabulary', 'a.', 'Of or pertaining to constables; consisting of\n   constables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constabulary', 'n.', 'The collective body of constables in any town,\n   district, or country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constabulatory', 'n.', 'A constabulary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constancy', 'n.', 'The state or quality of being constant or steadfast;\n   freedom from change; stability; fixedness; immutability; as, the\n   constancy of God in his nature and attributes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constancy', 'n.', 'Fixedness or firmness of mind; persevering resolution;\n   especially, firmness of mind under sufferings, steadiness in\n   attachments, or perseverance in enterprise; stability; fidelity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constant', 'v. t.', 'Firm; solid; fixed; immovable; -- opposed to fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constant', 'v. t.', 'Not liable, or given, to change; permanent; regular;\n   continuous; continually recurring; steadfast; faithful; not fickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constant', 'v. t.', 'Remaining unchanged or invariable, as a quantity,\n   force, law, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constant', 'v. t.', 'Consistent; logical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constant', 'n.', 'That which is not subject to change; that which is\n   invariable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constant', 'n.', 'A quantity that does not change its value; -- used in\n   countradistinction to variable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constantia', 'n.', 'A superior wine, white and red, from Constantia, in\n   Cape Colony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constantly', 'adv.', 'With constancy; steadily; continually; perseveringly;\n   without cessation; uniformly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constat', 'n.', 'A certificate showing what appears upon record touching a\n   matter in question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constate', 'v. t.', 'To ascertain; to verify; to establish; to prove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constellate', 'v. i. ', 'To join luster; to shine with united radiance, or\n   one general light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constellate', 'v. t.', 'To unite in one luster or radiance, as stars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constellate', 'v. t.', 'To set or adorn with stars or constellations; as,\n   constellated heavens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constellation', 'n.', 'A cluster or group of fixed stars, or dvision of the\n   heavens, designated in most cases by the name of some animal, or of\n   some mythologial personage, within whose imaginary outline, as traced\n   upon the heavens, the group is included.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constellation', 'n.', 'An assemblage of splendors or excellences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constellation', 'n.', 'Fortune; fate; destiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Consternation', 'n.', 'Amazement or horror that confounds the faculties,\n   and incapacitates for reflection; terror, combined with amazement;\n   dismay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constipated', 'imp. & p. p.', 'of Constipate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constipating', 'p. pr. & vb. n.', 'of Constipate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constipate', 'v. t.', 'To crowd or cram into a narrow compass; to press\n   together or condense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constipate', 'v. t.', 'To stop (a channel) by filling it, and preventing\n   passage through it; as, to constipate the capillary vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constipate', 'v. t.', 'To render costive; to cause constipation in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constipation', 'n.', 'Act of crowding anything into a less compass, or the\n   state of being crowded or pressed together; condensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constipation', 'n.', 'A state of the bowels in which the evacuations are\n   infrequent and difficult, or the intestines become filled with hardened\n   faeces; costiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituencies', 'pl. ', 'of Constituency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituency', 'n.', 'A body of constituents, as the body of citizens or\n   voters in a representative district.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituent', 'a.', 'Serving to form, compose, or make up; elemental;\n   component.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituent', 'a.', 'Having the power of electing or appointing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituent', 'n.', 'The person or thing which constitutes, determines, or\n   constructs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituent', 'n.', 'That which constitutes or composes, as a part, or an\n   essential part; a component; an element.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituent', 'n.', 'One for whom another acts; especially, one who is\n   represented by another in a legislative assembly; -- correlative to\n   representative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituent', 'n.', 'A person who appoints another to act for him as\n   attorney in fact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituted', 'imp. & p. p.', 'of Constitute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituting', 'p. pr. & vb. n.', 'of Constitute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitute', 'v. t.', 'To cause to stand; to establish; to enact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitute', 'v. t.', 'To make up; to compose; to form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitute', 'v. t.', 'To appoint, depute, or elect to an office; to make\n   and empower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitute', 'n.', 'An established law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constituter', 'n.', 'One who constitutes or appoints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitution', 'n.', 'The act or process of constituting; the action of\n   enacting, establishing, or appointing; enactment; establishment;\n   formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitution', 'n.', 'The state of being; that form of being, or structure\n   and connection of parts, which constitutes and characterizes a system\n   or body; natural condition; structure; texture; conformation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitution', 'n.', 'The aggregate of all ones inherited physical\n   qualities; the aggregate of the vital powers of an individual, with\n   reference to ability to endure hardship, resist disease, etc.; as, a\n   robust constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitution', 'n.', 'The aggregate of mental qualities; temperament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitution', 'n.', 'The fundamental, organic law or principles of\n   government of men, embodied in written documents, or implied in the\n   institutions and usages of the country or society; also, a written\n   instrument embodying such organic law, and laying down fundamental\n   rules and principles for the conduct of affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitution', 'n.', 'An authoritative ordinance, regulation or enactment;\n   especially, one made by a Roman emperor, or one affecting\n   ecclesiastical doctrine or discipline; as, the constitutions of\n   Justinian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutional', 'a.', 'Belonging to, or inherent in, the constitution, or\n   in the structure of body or mind; as, a constitutional infirmity;\n   constitutional ardor or dullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutional', 'a.', 'In accordance with, or authorized by, the\n   constitution of a state or a society; as, constitutional reforms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutional', 'a.', 'Regulated by, dependent on, or secured by, a\n   constitution; as, constitutional government; constitutional rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutional', 'a.', 'Relating to a constitution, or establishment form\n   of government; as, a constitutional risis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutional', 'a.', 'For the benefit or ones constitution or health;\n   as, a constitutional walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutional', 'n.', 'A walk or other exercise taken for ones health or\n   constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutionalism', 'n.', 'The theory, principles, or authority of\n   constitutional government; attachment or adherence to a constitution or\n   constitutional government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutionalist', 'n.', 'One who advocates a constitutional form of\n   government; a constitutionalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'ties', 'pl. ', 'of Constitutionality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutionality', 'n.', 'The quality or state of being constitutional, or\n   inherent in the natural frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutionality', 'n.', 'The state of being consistent with the\n   constitution or frame of government, or of being authorized by its\n   provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutionally', 'adv.', 'In accordance with the constitution or natural\n   disposition of the mind or body; naturally; as, he was constitutionally\n   timid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutionally', 'adv.', 'In accordance with the constitution or\n   fundamental law; legally; as, he was not constitutionally appointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutionist', 'n.', 'One who adheres to the constitution of the\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutive', 'a.', 'Tending or assisting to constitute or compose;\n   elemental; essential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutive', 'a.', 'Having power to enact, establish, or create;\n   instituting; determining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constitutively', 'adv.', 'In a constitutive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrained', 'imp. & p. p.', 'of Constrain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constraining', 'p. pr. & vb. n.', 'of Constrain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrain', 'v. t.', 'To secure by bonds; to chain; to bond or confine; to\n   hold tightly; to constringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrain', 'v. t.', 'To bring into a narrow compass; to compress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrain', 'v. t.', 'To hold back by force; to restrain; to repress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrain', 'v. t.', 'To compel; to force; to necessitate; to oblige.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrain', 'v. t.', 'To violate; to ravish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrain', 'v. t.', 'To produce in such a manner as to give an unnatural\n   effect; as, a constrained voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrainable', 'a.', 'Capable of being constrained; liable to constraint,\n   or to restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrained', 'a.', 'Marked by constraint; not free; not voluntary;\n   embarrassed; as, a constrained manner; a constrained tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrainedly', 'adv.', 'By constraint or compulsion; in a constrained\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrainer', 'n.', 'One who constrains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constraint', 'n.', 'The act of constraining, or the state of being\n   constrained; that which compels to, or restrains from, action;\n   compulsion; restraint; necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constraintive', 'a.', 'Constraining; compulsory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constricted', 'imp. & p. p.', 'of Constrict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constricting', 'p. pr. & vb. n.', 'of Constrict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrict', 'v. t.', 'To draw together; to render narrower or smaller; to\n   bind; to cramp; to contract or cause to shrink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constricted', 'a.', 'Drawn together; bound; contracted; cramped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constricted', 'a.', 'Contracted or compressed so as to be smaller in\n   certain places or parts than in others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constriction', 'n.', 'The act of constricting by means of some inherent\n   power or by movement or change in the thing itself, as distinguished\n   from compression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constriction', 'n.', 'The state of being constricted; the point where a\n   thing is constricted; a narrowing or binding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrictive', 'a.', 'Serving or tending to bind or constrict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrictor', 'n.', 'That which constricts, draws together, or contracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrictor', 'n.', 'A muscle which contracts or closes an orifice, or\n   which compresses an organ; a sphincter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constrictor', 'n.', 'A serpent that kills its prey by inclosing and\n   crushing it with its folds; as, the boa constrictor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constringed', 'imp. & p. p.', 'of Constringe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constringing', 'p. pr. & vb. n.', 'of Constringe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constringe', 'v. t.', 'To dawn together; to contract; to force to contract\n   itself; to constrict; to cause to shrink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constringent', 'a.', 'Having the quality of contracting, binding, or\n   compressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constructed', 'imp. & p. p.', 'of Construct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constructing', 'p. pr. & vb. n.', 'of Construct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Construct', 'v. t.', 'To put together the constituent parts of (something)\n   in their proper place and order; to build; to form; to make; as, to\n   construct an edifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Construct', 'v. t.', 'To devise; to invent; to set in order; to arrange;\n   as, to construct a theory of ethics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Construct', 'a.', 'Formed by, or relating to, construction, interpretation,\n   or inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constructer', 'n.', 'One who, or that which, constructs or frames.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Construction', 'n.', 'The process or art of constructing; the act of\n   building; erection; the act of devising and forming; fabrication;\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Construction', 'n.', 'The form or manner of building or putting together\n   the parts of anything; structure; arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Construction', 'n.', 'The arrangement and connection of words in a\n   sentence; syntactical arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Construction', 'n.', 'The method of construing, interpreting, or explaining\n   a declaration or fact; an attributed sense or meaning; understanding;\n   explanation; interpretation; sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constructional', 'a.', 'Pertaining to, or deduced from, construction or\n   interpretation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constructionist', 'n.', 'One who puts a certain construction upon some\n   writing or instrument, as the Constitutions of the United States; as, a\n   strict constructionist; a broad constructionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constructive', 'a.', 'Having ability to construct or form; employed in\n   construction; as, to exhibit constructive power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constructive', 'a.', 'Derived from, or depending on, construction or\n   interpretation; not directly expressed, but inferred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Constructively', 'adv.', 'In a constructive manner; by construction or\n   inference.');
