  INSERT INTO words (word, word_type, description) VALUES  (    'Contender', 'n.', 'One who contends; a contestant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contendress', 'n.', 'A female contestant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contenement', 'n.', 'That which is held together with another thing; that\n   which is connected with a tenement, or thing holden, as a certain\n   quantity of land adjacent to a dwelling, and necessary to the reputable\n   enjoyment of the dwelling; appurtenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Content', 'a.', 'Contained within limits; hence, having the desires limited\n   by that which one has; not disposed to repine or grumble; satisfied;\n   contented; at rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contents', 'pl. ', 'of Content');
  INSERT INTO words (word, word_type, description) VALUES  (    'Content', 'n.', 'That which is contained; the thing or things held by a\n   receptacle or included within specified limits; as, the contents of a\n   cask or bale or of a room; the contents of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Content', 'n.', 'Power of containing; capacity; extent; size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Content', 'n.', 'Area or quantity of space or matter contained within\n   certain limits; as, solid contents; superficial contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Content', 'a.', 'To satisfy the desires of; to make easy in any situation;\n   to appease or quiet; to gratify; to please.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Content', 'a.', 'To satisfy the expectations of; to pay; to requite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Content', 'n.', 'Rest or quietness of the mind in ones present condition;\n   freedom from discontent; satisfaction; contentment; moderate happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Content', 'n.', 'Acquiescence without examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Content', 'n.', 'That which contents or satisfies; that which if attained\n   would make one happy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Content', 'n.', 'An expression of assent to a bill or motion; an\n   affirmative vote; also, a member who votes \"Content.\".');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contentation', 'n.', 'Content; satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contented', 'a.', 'Content; easy in mind; satisfied; quiet; willing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contentful', 'a.', 'Full of content.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contention', 'n.', 'A violent effort or struggle to obtain, or to resist,\n   something; contest; strife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contention', 'n.', 'Strife in words; controversy; altercation; quarrel;\n   dispute; as, a bone of contention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contention', 'n.', 'Vehemence of endeavor; eagerness; ardor; zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contention', 'n.', 'A point maintained in an argument, or a line of\n   argument taken in its support; the subject matter of discussion or\n   strife; a position taken or contended for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contentious', 'a.', 'Fond of contention; given to angry debate; provoking\n   dispute or contention; quarrelsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contentious', 'a.', 'Relating to contention or strife; involving or\n   characterized by contention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contentious', 'a.', 'Contested; litigated; litigious; having power to\n   decide controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contentless', 'a.', 'Discontented; dissatisfied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contently', 'adv.', 'In a contented manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contentment', 'v. t.', 'The state of being contented or satisfied; content.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contentment', 'v. t.', 'The act or process of contenting or satisfying; as,\n   the contentment of avarice is impossible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contentment', 'v. t.', 'Gratification; pleasure; satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contents', 'n. pl.', 'See Content, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conterminable', 'a.', 'Having the same bounds; terminating at the same time\n   or place; conterminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conterminal', 'a.', 'Conterminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conterminant', 'a.', 'Having the same limits; ending at the same time;\n   conterminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conterminate', 'a.', 'Having the same bounds; conterminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conterminous', 'a.', 'Having the same bounds, or limits; bordering upon;\n   contiguous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conterranean', 'a.', 'Alt. of Conterraneous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conterraneous', 'a.', 'Of or belonging to the same country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contesseration', 'n.', 'An assemblage; a collection; harmonious union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contested', 'imp. & p. p.', 'of Contest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contesting', 'p. pr. & vb. n.', 'of Contest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contest', 'v. t.', 'To make a subject of dispute, contention, litigation,\n   or emulation; to contend for; to call in question; to controvert; to\n   oppose; to dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contest', 'v. t.', 'To strive earnestly to hold or maintain; to struggle to\n   defend; as, the troops contested every inch of ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contest', 'v. t.', 'To make a subject of litigation; to defend, as a suit;\n   to dispute or resist; as a claim, by course of law; to controvert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contest', 'v. i.', 'To engage in contention, or emulation; to contend; to\n   strive; to vie; to emulate; -- followed usually by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contest', 'n.', 'Earnest dispute; strife in argument; controversy; debate;\n   altercation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contest', 'n.', 'Earnest struggle for superiority, victory, defense, etc.;\n   competition; emulation; strife in arms; conflict; combat; encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contestable', 'a.', 'Capable of being contested; debatable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contestant', 'n.', 'One who contests; an opponent; a litigant; a disputant;\n   one who claims that which has been awarded to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contestation', 'n.', 'The act of contesting; emulation; rivalry; strife;\n   dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contestation', 'n.', 'Proof by witness; attestation; testimony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contestingly', 'adv.', 'In a contending manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contex', 'v. t.', 'To context.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Context', 'a.', 'Knit or woven together; close; firm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Context', 'n.', 'The part or parts of something written or printed, as of\n   Scripture, which precede or follow a text or quoted sentence, or are so\n   intimately associated with it as to throw light upon its meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Context', 'v. t.', 'To knit or bind together; to unite closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contextural', 'a.', 'Pertaining to contexture or arrangement of parts;\n   producing contexture; interwoven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contexture', 'n.', 'The arrangement and union of the constituent parts of a\n   thing; a weaving together of parts; structural character of a thing;\n   system; constitution; texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contextured', 'a.', 'Formed into texture; woven together; arranged;\n   composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conticent', 'a.', 'Silent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contignation', 'n.', 'The act or process of framing together, or uniting,\n   as beams in a fabric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contignation', 'n.', 'A framework or fabric, as of beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contiguate', 'a.', 'Contiguous; touching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contiguity', 'n.', 'The state of being contiguous; intimate association;\n   nearness; proximity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contiguous', 'a.', 'In actual contact; touching; also, adjacent; near;\n   neighboring; adjoining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continence', 'n.', 'Alt. of Continency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continency', 'n.', 'Self-restraint; self-command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continency', 'n.', 'The restraint which a person imposes upon his desires\n   and passions; the act or power of refraining from indulgence of the\n   sexual appetite, esp. from unlawful indulgence; sometimes, moderation\n   in sexual indulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continency', 'n.', 'Uninterrupted course; continuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continent', 'a.', 'Serving to restrain or limit; restraining; opposing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continent', 'a.', 'Exercising restraint as to the indulgence of desires or\n   passions; temperate; moderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continent', 'a.', 'Abstaining from sexual intercourse; exercising restraint\n   upon the sexual appetite; esp., abstaining from illicit sexual\n   intercourse; chaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continent', 'a.', 'Not interrupted; connected; continuous; as, a continent\n   fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continent', 'a.', 'That which contains anything; a receptacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continent', 'a.', 'One of the grand divisions of land on the globe; the\n   main land; specifically (Phys. Geog.); a large body of land differing\n   from an island, not merely in its size, but in its structure, which is\n   that of a large basin bordered by mountain chains; as, the continent of\n   North America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continental', 'a.', 'Of or pertaining to a continent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continental', 'a.', 'Of or pertaining to the main land of Europe, in\n   distinction from the adjacent islands, especially England; as, a\n   continental tour; a continental coalition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continental', 'a.', 'Of or pertaining to the confederated colonies\n   collectively, in the time of the Revolutionary War; as, Continental\n   money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continental', 'n.', 'A soldier in the Continental army, or a piece of the\n   Continental currency. See Continental, a., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continently', 'adv.', 'In a continent manner; chastely; moderately;\n   temperately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingence', 'n.', 'See Contingency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingencies', 'pl. ', 'of Contingency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingency', 'n.', 'Union or connection; the state of touching or contact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingency', 'n.', 'The quality or state of being contingent or casual;\n   the possibility of coming to pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingency', 'n.', 'An event which may or may not occur; that which is\n   possible or probable; a fortuitous event; a chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingency', 'n.', 'An adjunct or accessory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingency', 'n.', 'A certain possible event that may or may not happen,\n   by which, when happening, some particular title may be affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingent', 'a.', 'Possible, or liable, but not certain, to occur;\n   incidental; casual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingent', 'a.', 'Dependent on that which is undetermined or unknown; as,\n   the success of his undertaking is contingent upon events which he can\n   not control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingent', 'a.', 'Dependent for effect on something that may or may not\n   occur; as, a contingent estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingent', 'n.', 'An event which may or may not happen; that which is\n   unforeseen, undetermined, or dependent on something future; a\n   contingency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingent', 'n.', 'That which falls to one in a division or apportionment\n   among a number; a suitable share; proportion; esp., a quota of troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingently', 'adv.', 'In a contingent manner; without design or\n   foresight; accidentally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contingentness', 'n.', 'The state of being contingent; fortuitousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuable', 'a.', 'Capable of being continued');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continual', 'a.', 'Proceeding without interruption or cesstaion;\n   continuous; unceasing; lasting; abiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continual', 'a.', 'Occuring in steady and rapid succession; very frequent;\n   often repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continually', 'adv.', 'Without cessation; unceasingly; continuously; as,\n   the current flows continually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continually', 'adv.', 'In regular or repeated succession; very often.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuance', 'n.', 'A holding on, or remaining in a particular state;\n   permanence, as of condition, habits, abode, etc.; perseverance;\n   constancy; duration; stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuance', 'n.', 'Uninterrupted succession; continuation; constant\n   renewal; perpetuation; propagation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuance', 'n.', 'A holding together; continuity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuance', 'n.', 'The adjournment of the proceedings in a cause from one\n   day, or from one stated term of a court, to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuance', 'n.', 'The entry of such adjournment and the grounds thereof\n   on the record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuant', 'a.', 'Continuing; prolonged; sustained; as, a continuant\n   sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuant', 'n.', 'A continuant sound; a letter whose sound may be\n   prolonged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuate', 'a.', 'Immediately united together; intimately connected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuate', 'a.', 'Uninterrupted; unbroken; continual; continued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuation', 'n.', 'That act or state of continuing; the state of being\n   continued; uninterrupted extension or succession; prolongation;\n   propagation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuation', 'n.', 'That which extends, increases, supplements, or\n   carries on; as, the continuation of a story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuative', 'n.', 'A term or expression denoting continuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuative', 'n.', 'A word that continues the connection of sentences or\n   subjects; a connective; a conjunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuator', 'n.', 'One who, or that which, continues; esp., one who\n   continues a series or a work; a continuer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continued', 'imp. & p. p.', 'of Continue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuing', 'p. pr. & vb. n.', 'of Continue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continue', 'v. i.', 'To remain in a given place or condition; to remain in\n   connection with; to abide; to stay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continue', 'v. i.', 'To be permanent or durable; to endure; to last.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continue', 'v. i.', 'To be steadfast or constant in any course; to\n   persevere; to abide; to endure; to persist; to keep up or maintain a\n   particular condition, course, or series of actions; as, the army\n   continued to advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continue', 'v. t.', 'To unite; to connect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continue', 'v. t.', 'To protract or extend in duration; to preserve or\n   persist in; to cease not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continue', 'v. t.', 'To carry onward or extend; to prolong or produce; to\n   add to or draw out in length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continue', 'v. t.', 'To retain; to suffer or cause to remain; as, the\n   trustees were continued; also, to suffer to live.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continued', 'p. p. & a.', 'Having extension of time, space, order of\n   events, exertion of energy, etc.; extended; protracted; uninterrupted;\n   also, resumed after interruption; extending through a succession of\n   issues, session, etc.; as, a continued story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuedly', 'adv.', 'Continuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuer', 'n.', 'One who continues; one who has the power of perseverance\n   or persistence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuities', 'pl. ', 'of Continuity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuity', 'n.', 'the state of being continuous; uninterupted connection\n   or succession; close union of parts; cohesion; as, the continuity of\n   fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuo', 'n.', 'Basso continuo, or continued bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuous', 'a.', 'Without break, cessation, or interruption; without\n   intervening space or time; uninterrupted; unbroken; continual;\n   unceasing; constant; continued; protracted; extended; as, a continuous\n   line of railroad; a continuous current of electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuous', 'a.', 'Not deviating or varying from uninformity; not\n   interrupted; not joined or articulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Continuously', 'adv.', 'In a continuous maner; without interruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contline', 'n.', 'The space between the strands on the outside of a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contline', 'n.', 'The space between the bilges of two casks stowed side by\n   side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contorniate', 'n.', 'Alt. of Contorniate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contorniate', 'n.', 'A species of medal or medallion of bronze, having a\n   deep furrow on the contour or edge; -- supposed to have been struck in\n   the days of Constantine and his successors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contorsion', 'n.', 'See Contortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contort', 'v. t.', 'To twist, or twist together; to turn awry; to bend; to\n   distort; to wrest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contorted', 'a.', 'Twisted, or twisted together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contorted', 'a.', 'Twisted back upon itself, as some parts of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contorted', 'a.', 'Arranged so as to overlap each other; as, petals in\n   contorted or convolute aestivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contortion', 'n.', 'A twisting; a writhing; wry motion; a twist; as, the\n   contortion of the muscles of the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contertionist', 'n.', 'One who makes or practices contortions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contortive', 'a.', 'Expressing contortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contortuplicate', 'a.', 'Plaited lengthwise and twisted in addition, as the\n   bud of the morning-glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contour', 'n.', 'The outline of a figure or body, or the line or lines\n   representing such an outline; the line that bounds; periphery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contour', 'n.', 'The outline of a horizontal section of the ground, or of\n   works of fortification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contourne', 'a.', 'Turned in a direction which is not the usual one; --\n   said of an animal turned to the sinister which is usually turned to the\n   dexter, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contourniated', 'a.', 'Having furrowed edges, as if turned in a lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contra', '', 'A Latin adverb and preposition, signifying against, contrary,\n   in opposition, etc., entering as a prefix into the composition of many\n   English words. Cf. Counter, adv. & pref.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraband', 'n.', 'Illegal or prohibited traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraband', 'n.', 'Goods or merchandise the importation or exportation of\n   which is forbidden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraband', 'n.', 'A negro slave, during the Civil War, escaped to, or was\n   brought within, the Union lines. Such slave was considered contraband\n   of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraband', 'a.', 'Prohibited or excluded by law or treaty; forbidden; as,\n   contraband goods, or trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraband', 'v. t.', 'To import illegally, as prohibited goods; to\n   smuggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraband', 'v. t.', 'To declare prohibited; to forbid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrabandism', 'n.', 'Traffic in contraband goods; smuggling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrabandist', 'n.', 'One who traffics illegally; a smuggler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrabass', 'n.', 'Double bass; -- applied to any instrument of the same\n   deep range as the stringed double bass; as, the contrabass ophicleide;\n   the contrabass tuba or bombardon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrabasso', 'n.', 'The largest kind of bass viol. See Violone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contracted', 'imp. & p. p.', 'of Contract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contracting', 'p. pr. & vb. n.', 'of Contract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'n.', 'To draw together or nearer; to reduce to a less compass;\n   to shorten, narrow, or lessen; as, to contract ones sphere of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'n.', 'To draw together so as to wrinkle; to knit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'n.', 'To bring on; to incur; to acquire; as, to contract a\n   habit; to contract a debt; to contract a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'n.', 'To enter into, with mutual obligations; to make a bargain\n   or covenant for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'n.', 'To betroth; to affiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'n.', 'To shorten by omitting a letter or letters or by reducing\n   two or more vowels or syllables to one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'v. i.', 'To be drawn together so as to be diminished in size or\n   extent; to shrink; to be reduced in compass or in duration; as, iron\n   contracts in cooling; a rope contracts when wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'v. i.', 'To make an agreement; to covenant; to agree; to\n   bargain; as, to contract for carrying the mail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'a.', 'Contracted; as, a contract verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'a.', 'Contracted; affianced; betrothed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'n.', 'The agreement of two or more persons, upon a sufficient\n   consideration or cause, to do, or to abstain from doing, some act; an\n   agreement in which a party undertakes to do, or not to do, a particular\n   thing; a formal bargain; a compact; an interchange of legal rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'n.', 'A formal writing which contains the agreement of parties,\n   with the terms and conditions, and which serves as a proof of the\n   obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contract', 'n.', 'The act of formally betrothing a man and woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contracted', 'a.', 'Drawn together; shrunken; wrinkled; narrow; as, a\n   contracted brow; a contracted noun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contracted', 'a.', 'Narrow; illiberal; selfish; as, a contracted mind;\n   contracted views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contracted', 'a.', 'Bargained for; betrothed; as, a contracted peace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contractedness', 'n.', 'The state of being contracted; narrowness;\n   meanness; selfishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contractibility', 'n.', 'Capability of being contracted; quality of being\n   contractible; as, the contractibility and dilatability of air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contractible', 'a.', 'Capable of contraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contractibleness', 'n.', 'Contractibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contractile', 'a.', 'tending to contract; having the power or property of\n   contracting, or of shrinking into shorter or smaller dimensions; as,\n   the contractile tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contractility', 'n.', 'The quality or property by which bodies shrink or\n   contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contractility', 'n.', 'The power possessed by the fibers of living muscle\n   of contracting or shortening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraction', 'n.', 'The act or process of contracting, shortening, or\n   shrinking; the state of being contracted; as, contraction of the heart,\n   of the pupil of the eye, or of a tendion; the contraction produced by\n   cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraction', 'n.', 'The process of shortening an operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraction', 'n.', 'The act of incurring or becoming subject to, as\n   liabilities, obligation, debts, etc.; the process of becoming subject\n   to; as, the contraction of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraction', 'n.', 'Something contracted or abbreviated, as a word or\n   phrase; -- as, plenipo for plenipotentiary; crim. con. for criminal\n   conversation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraction', 'n.', 'The shortening of a word, or of two words, by the\n   omission of a letter or letters, or by reducing two or more vowels or\n   syllables to one; as, neer for never; cant for can not; dont for do\n   not; its for it is.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraction', 'n.', 'A marriage contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contractive', 'a.', 'Tending to contract; having the property or power or\n   power of contracting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contractor', 'n.', 'One who contracts; one of the parties to a bargain; one\n   who covenants to do anything for another; specifically, one who\n   contracts to perform work on a rather large scale, at a certain price\n   or rate, as in building houses or making a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contracture', 'n.', 'A state of permanent rigidity or contraction of the\n   muscles, generally of the flexor muscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradance', 'n.', 'A dance in which the partners are arranged face to\n   face, or in opposite lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradicted', 'imp. & p. p.', 'of Contradict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradicting', 'p. pr. & vb. n.', 'of Contradict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradict', 'v. t.', 'To assert the contrary of; to oppose in words; to\n   take issue with; to gainsay; to deny the truth of, as of a statement or\n   a speaker; to impugn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradict', 'v. t.', 'To be contrary to; to oppose; to resist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradict', 'v. i.', 'To oppose in words; to gainsay; to deny, or assert\n   the contrary of, something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictable', 'a.', 'Capable of being contradicting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradicter', 'n.', 'one who contradicts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradiction', 'n.', 'An assertion of the contrary to what has been said\n   or affirmed; denial of the truth of a statement or assertion; contrary\n   declaration; gainsaying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradiction', 'n.', 'Direct opposition or repugnancy; inconsistency;\n   incongruity or contrariety; one who, or that which, is inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictional', 'a.', 'Contradictory; inconsistent; opposing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictions', 'a.', 'Filled with contradictions; inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictions', 'a.', 'Inclined to contradict or cavil');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictive', 'a.', 'Contradictory; inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictor', 'n.', 'A contradicter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictorily', 'adv.', 'In a contradictory manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictoriness', 'n.', 'The quality of being contradictory; opposition;\n   inconsistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictory', 'a.', 'Affirming the contrary; implying a denial of what\n   has been asserted; also, mutually contradicting; inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictory', 'a.', 'Opposing or opposed; repugnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictories', 'pl. ', 'of Contradictory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictory', 'n.', 'A proposition or thing which denies or opposes\n   another; contrariety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradictory', 'n.', 'propositions with the same terms, but opposed to\n   each other both in quality and quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradistinct', 'a.', 'Distinguished by opposite qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradistinction', 'n.', 'Distinction by contrast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradistinctive', 'a.', 'having the quality of contradistinction;\n   distinguishing by contrast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradistinguished', 'imp. & p. p.', 'of Contradistinguish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradistinguishing', 'p. pr. & vb. n.', 'of Contradistinguish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contradistinguish', 'v. t. ', 'To distinguish by a contrast of opposite\n   qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrafagetto', 'n.', 'The double bassoon, an octave deeper than the\n   bassoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrafissure', 'n.', 'A fissure or fracture on the side opposite to that\n   which received the blow, or at some distance from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrahent', 'a.', 'Entering into covenant; contracting; as, contrahent\n   parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraindicant', 'n.', 'Something, as a symptom, indicating that the usual\n   mode of treatment is not to be followed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraindicated', 'imp. & p. p.', 'of Contraindicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraindicating', 'p. pr. & vb. n.', 'of Contraindicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraindicate', 'v. t.', 'To indicate, as by a symptom, some method of\n   treatment contrary to that which the general tenor of the case would\n   seem to require.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraindication', 'n.', 'An indication or symptom which forbids the method\n   of treatment usual in such cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contralto', 'n.', 'The part sung by the highest male or lowest female\n   voices; the alto or counter tenor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contralto', 'n.', 'the voice or singer performing this part; as, her voice\n   is a contralto; she is a contralto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contralto', 'a.', 'Of or pertaining to a contralto, or to the part in music\n   called contralto; as, a contralto voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contramure', 'n.', 'An outer wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contranatural', 'a.', 'Opposed to or against nature; unnatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraposition', 'n.', 'A placing over against; opposite position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraposition', 'n.', 'A so-called immediate inference which consists in\n   denying the original subject of the contradictory predicate; e.g.:\n   Every S is P; therefore, no Not-P is S.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrapuntal', 'a.', 'Pertaining to, or according to the rules of,\n   counterpoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrapuntist', 'n.', 'One skilled in counterpoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraremonstrant', 'n.', 'One who remonstrates in opposition or answer to\n   a remonstrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrariant', 'a.', 'Contrary; opposed; antagonistic; inconsistent;\n   contradictory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrariantly', 'adv.', 'Contrarily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraries', 'n.', 'Propositions which directly and destructively\n   contradict each other, but of which the falsehood of one does not\n   establish the truth of the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrarieties', 'pl. ', 'of Contrariety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrariety', 'n.', 'The state or quality of being contrary; opposition;\n   repugnance; disagreement; antagonism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrariety', 'n.', 'Something which is contrary to, or inconsistent with,\n   something else; an inconsistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrarily', 'adv.', 'In a contrary manner; in opposition; on the other\n   side; in opposite ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrariness', 'n.', 'state or quality of being contrary; opposition;\n   inconsistency; contrariety; perverseness; obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrarious', 'a.', 'Showing contrariety; repugnant; perverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrariously', 'adv.', 'Contrarily; oppositely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrariwise', 'adv.', 'On the contrary; oppositely; on the other hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrariwise', 'adv.', 'In a contrary order; conversely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrarotation', 'n.', 'Circular motion in a direction contrary to some\n   other circular motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrary', 'a.', 'Opposite; in an opposite direction; in opposition;\n   adverse; as, contrary winds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrary', 'a.', 'Opposed; contradictory; repugnant; inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrary', 'a.', 'Given to opposition; perverse; forward; wayward; as, a\n   contrary disposition; a contrary child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrary', 'a.', 'Affirming the opposite; so opposed as to destroy each\n   other; as, contrary propositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraries', 'pl. ', 'of Contrary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrary', 'n.', 'A thing that is of contrary or opposite qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrary', 'n.', 'An opponent; an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrary', 'n.', 'the opposite; a proposition, fact, or condition\n   incompatible with another; as, slender proofs which rather show the\n   contrary. See Converse, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrary', 'n.', 'See Contraries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrarry', 'a.', 'To contradict or oppose; to thwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrasted', 'imp. & p. p.', 'of Contrast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrasting', 'p. pr. & vb. n.', 'of Contrast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrast', 'v. i.', 'To stand in opposition; to exhibit difference,\n   unlikeness, or opposition of qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrast', 'v. t.', 'To set in opposition, or over against, in order to\n   show the differences between, or the comparative excellences and\n   defects of; to compare by difference or contrariety of qualities; as,\n   to contrast the present with the past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrast', 'v. t.', 'To give greater effect to, as to a figure or other\n   object, by putting it in some relation of opposition to another figure\n   or object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrast', 'n.', 'The act of contrasting, or the state of being contrasted;\n   comparison by contrariety of qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrast', 'n.', 'Opposition or dissimilitude of things or qualities;\n   unlikeness, esp. as shown by juxtaposition or comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrast', 'n.', 'The opposition of varied forms, colors, etc., which by\n   such juxtaposition more vividly express each others peculiarities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrastimulant', 'a.', 'Counteracting the effects of stimulants; relating\n   to a course of medical treatment based on a theory of contrastimulants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrastimulant', 'n.', 'An agent which counteracts the effect of a\n   stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrate', 'a.', 'Having cogs or teeth projecting parallel to the axis,\n   instead of radiating from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contratenor', 'n.', 'Counter tenor; contralto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contravallation', 'n.', 'A trench guarded with a parapet, constructed by\n   besiegers, to secure themselves and check sallies of the besieged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contravened', 'imp. & p. p.', 'of Contravene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contravening', 'p. pr. & vb. n.', 'of Contravene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contravene', 'v. t.', 'To meet in the way of opposition; to come into\n   conflict with; to oppose; to contradict; to obstruct the operation of;\n   to defeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contravene', 'v. t.', 'To violate; to nullify; to be inconsistent with; as,\n   to contravene a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contravener', 'n.', 'One who contravenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contravention', 'n.', 'The act of contravening; opposition; obstruction;\n   transgression; violation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contraversion', 'n.', 'A turning to the opposite side; antistrophe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrayerva', 'n.', 'A species of Dorstenia (D. Contrayerva); a South\n   American plant, the aromatic root of which is sometimes used in\n   medicine as a gentle stimulant and tonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrecoup', 'n.', 'A concussion or shock produced by a blow or other\n   injury, in a part or region opposite to that at which the blow is\n   received, often causing rupture or disorganisation of the parts\n   affected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contretemps', 'n.', 'An unexpected and untoward accident; something\n   inopportune or embarrassing; a hitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributable', 'a.', 'Capable of being contributed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributary', 'a.', 'Contributory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributary', 'a.', 'Tributary; contributing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributed', 'imp. & p. p.', 'of Contribute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributing', 'p. pr. & vb. n.', 'of Contribute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contribute', 'v. t.', 'To give or grant i common with others; to give to a\n   common stock or for a common purpose; to furnish or suply in part; to\n   give (money or other aid) for a specified object; as, to contribute\n   food or fuel for the poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contribute', 'v. i.', 'To give a part to a common stock; to lend assistance\n   or aid, or give something, to a common purpose; to have a share in any\n   act or effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contribute', 'v. i.', 'To give or use ones power or influence for any\n   object; to assist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contribution', 'n.', 'The act of contributing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contribution', 'n.', 'That which is contributed; -- either the portion\n   which an individual furnishes to the common stock, or the whole which\n   is formed by the gifts of individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contribution', 'n.', 'An irregular and arbitrary imposition or tax leved on\n   the people of a town or country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contribution', 'n.', 'Payment, by each of several jointly liable, of a\n   share in a loss suffered or an amount paid by one of their number for\n   the common benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributional', 'a.', 'Pertaining to, or furnishing, a contribution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributive', 'a.', 'Contributing, or tending to contribute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributer', 'n.', 'One who, or that which, contributes; specifically, one\n   who writes articles for a newspaper or magazine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributory', 'a.', 'Contributing to the same stock or purpose; promoting\n   the same end; bringing assistance to some joint design, or increase to\n   some common stock; contributive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributories', 'pl. ', 'of Contributory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contributory', 'n.', 'One who contributes, or is liable to be called upon\n   to contribute, as toward the discharge of a common indebtedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrist', 'v. t.', 'To make sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contristate', 'v. t. & i.', 'To make sorrowful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrite', 'a.', 'Thoroughly bruised or broken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrite', 'a.', 'Broken down with grief and penitence; deeply sorrowful\n   for sin because it is displeasing to God; humbly and thoroughly\n   penitent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrite', 'n.', 'A contrite person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrite', 'v.', 'In a contrite manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contriteness', 'n.', 'Deep sorrow and penitence for sin; contrition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrition', 'n.', 'The act of grinding or ribbing to powder; attrition;\n   friction; rubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrition', 'n.', 'The state of being contrite; deep sorrow and repentance\n   for sin, because sin is displeasing to God; humble penitence; through\n   repentance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contriturate', 'v. t.', 'To triturate; to pulverize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrivble', 'a.', 'Capable of being contrived, planned, invented, or\n   devised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrivance', 'n.', 'The act or faculty of contriving, inventing, devising,\n   or planning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrivance', 'n.', 'The thing contrived, invented, or planned; disposition\n   of parts or causes by design; a scheme; plan; atrifice; arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrived', 'imp. & p. p.', 'of Contrive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contriving', 'p. pr. & vb. n.', 'of Contrive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrive', 'v. t.', 'To form by an exercise of ingenuity; to devise; to\n   invent; to design; to plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrive', 'v. i.', 'To make devices; to form designs; to plan; to scheme;\n   to plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contrivement', 'n.', 'Contrivance; invention; arrangement; design; plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contriver', 'n.', 'One who contrives, devises, plans, or schemas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Control', 'n.', 'A duplicate book, register, or account, kept to correct or\n   check another account or register; a counter register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Control', 'n.', 'That which serves to check, restrain, or hinder;\n   restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Control', 'n.', 'Power or authority to check or restrain; restraining or\n   regulating influence; superintendence; government; as, children should\n   be under parental control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controlled', 'imp. & p. p.', 'of Control');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controlling', 'p. pr. & vb. n.', 'of Control');
  INSERT INTO words (word, word_type, description) VALUES  (    'Control', 'v. t.', 'To check by a counter register or duplicate account; to\n   prove by counter statements; to confute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Control', 'v. t.', 'To exercise restraining or governing influence over; to\n   check; to counteract; to restrain; to regulate; to govern; to\n   overpower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controllability', 'n.', 'Capability of being controlled; controllableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controllable', 'a.', 'Capable of being controlled, checked, or restrained;\n   amenable to command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controllableness', 'n.', 'Capability of being controlled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controller', 'n.', 'One who, or that which, controls or restraines; one who\n   has power or authority to regulate or control; one who governs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controller', 'n.', 'An officer appointed to keep a counter register of\n   accounts, or to examine, rectify, or verify accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controller', 'n.', 'An iron block, usually bolted to a ships deck, for\n   controlling the running out of a chain cable. The links of the cable\n   tend to drop into hollows in the block, and thus hold fast until\n   disengaged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controllership', 'n.', 'The office of a controller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controlment', 'n.', 'The power or act of controlling; the state of being\n   restrained; control; restraint; regulation; superintendence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controlment', 'n.', 'Opposition; resistance; hostility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversal', 'a.', 'Turning or looking opposite ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversal', 'a.', 'Controversial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversary', 'a.', 'Controversial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controverse', 'n.', 'Controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controverse', 'v. t.', 'To dispute; to controvert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controverser', 'n.', 'A disputant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversial', 'a.', 'Relating to, or consisting of, controversy;\n   disputatious; polemical; as, controversial divinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversialist', 'n.', 'One who carries on a controversy; a disputant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversially', 'adv.', 'In a controversial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversion', 'n.', 'Act of controverting; controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversor', 'n.', 'A controverser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversies', 'pl. ', 'of Controversy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversy', 'n.', 'Contention; dispute; debate; discussion; agitation of\n   contrary opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversy', 'n.', 'Quarrel; strife; cause of variance; difference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controversy', 'n.', 'A suit in law or equity; a question of right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controverted', 'imp. & p. p.', 'of Controvert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controverting', 'p. pr. & vb. n.', 'of Controvert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controvert', 'v. t.', 'To make matter of controversy; to dispute or oppose\n   by reasoning; to contend against in words or writings; to contest; to\n   debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controverter', 'n.', 'One who controverts; a controversial writer; a\n   controversialist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controvertible', 'a.', 'Capable of being controverted; disputable;\n   admitting of question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Controvertist', 'n.', 'One skilled in or given to controversy; a\n   controversialist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contubernal', 'a.', 'Alt. of Contubernial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contubernial', 'a.', 'Living or messing together; familiar; in\n   companionship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contumacious', 'a.', 'Exhibiting contumacy; contemning authority;\n   obstinate; perverse; stubborn; disobedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contumacious', 'a.', 'Willfully disobedient to the summous or prders of a\n   court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contumacies', 'pl. ', 'of Contumacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contumacy', 'n.', 'Stubborn perverseness; pertinacious resistance to\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contumacy', 'n.', 'A willful contempt of, and disobedience to, any lawful\n   summons, or to the rules and orders of court, as a refusal to appear in\n   court when legally summoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contumelious', 'a.', 'Exhibiting contumely; rudely contemptuous; insolent;\n   disdainful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contumelious', 'a.', 'Shameful; disgraceful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contumely', 'n.', 'Rudeness compounded of haughtiness and contempt;\n   scornful insolence; despiteful treatment; disdain; contemptuousness in\n   act or speech; disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contused', 'imp. & p. p.', 'of Contuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contusing', 'p. pr. & vb. n.', 'of Contuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contuse', 'v. t.', 'To beat, pound, or together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contuse', 'v. t.', 'To bruise; to injure or disorganize a part without\n   breaking the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contusion', 'n.', 'The act or process of beating, bruising, or pounding;\n   the state of being beaten or bruised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Contusion', 'n.', 'A bruise; an injury attended with more or less\n   disorganization of the subcutaneous tissue and effusion of blood\n   beneath the skin, but without apparent wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conundrum', 'n.', 'A kind of riddle based upon some fanciful or fantastic\n   resemblance between things quite unlike; a puzzling question, of which\n   the answer is or involves a pun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conundrum', 'n.', 'A question to which only a conjectural answer can be\n   made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conure', 'n.', 'An American parrakeet of the genus Conurus. Many species\n   are known. See Parrakeet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conus', 'n.', 'A cone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conus', 'n.', 'A Linnean genus of mollusks having a conical shell. See\n   Cone, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conusable', 'a.', 'Cognizable; liable to be tried or judged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conusant', 'a.', 'See Cognizant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conusor', 'n.', 'See Cognizor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convalesced', 'imp. & p. p.', 'of Convalesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convalescing', 'p. pr. & vb. n.', 'of Convalesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convalesce', 'v. i.', 'To recover health and strength gradually, after\n   sickness or weakness; as, a patient begins to convalesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convalesced', 'a.', 'Convalescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convalescence', 'n.', 'Alt. of Convalescency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convalescency', 'n.', 'The recovery of heath and strength after disease;\n   the state of a body renewing its vigor after sickness or weakness; the\n   time between the subsidence of a disease and complete restoration to\n   health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convalescent', 'a.', 'Recovering from sickness or debility; partially\n   restored to health or strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convalescent', 'a.', 'Of or pertaining to convalescence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convalescent', 'n.', 'One recovering from sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convalescently', 'adv.', 'In the manner of a convalescent; with increasing\n   strength or vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convallamarin', 'n.', 'A white, crystalline, poisonous substance, regarded\n   as a glucoside, extracted from the lily of the valley (Convallaria\n   Majalis). Its taste is first bitter, then sweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convallaria', 'n.', 'The lily of the valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convallarin', 'n.', 'A white, crystalline glucoside, of an irritating\n   taste, extracted from the convallaria or lily of the valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convection', 'n.', 'The act or process of conveying or transmitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convection', 'n.', 'A process of transfer or transmission, as of heat or\n   electricity, by means of currents in liquids or gases, resulting from\n   changes of temperature and other causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convective', 'a.', 'Caused or accomplished by convection; as, a convective\n   discharge of electricity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convectively', 'adv.', 'In a convective manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convellent', 'a.', 'Tending to tear or pull up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convenable', 'a.', 'Capable of being convened or assembled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convenable', 'a.', 'Consistent; accordant; suitable; proper; as, convenable\n   remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convenance', 'n.', 'That which is suitable, agreeable, or convenient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convened', 'imp. & p. p.', 'of Convene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convenong', 'p. pr. & vb. n.', 'of Convene');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convene', 'v. i.', 'To come together; to meet; to unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convene', 'v. i.', 'To come together, as in one body or for a public\n   purpose; to meet; to assemble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convene', 'v. t.', 'To cause to assemble; to call together; to convoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convene', 'v. t.', 'To summon judicially to meet or appear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convener', 'n.', 'One who convenes or meets with others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convener', 'n.', 'One who calls an assembly together or convenes a meeting;\n   hence, the chairman of a committee or other organized body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convenience', 'n.', 'Alt. of Conveniency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveniency', 'n.', 'The state or quality of being convenient; fitness or\n   suitableness, as of place, time, etc.; propriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveniency', 'n.', 'Freedom from discomfort, difficulty, or trouble;\n   commodiousness; ease; accommodation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveniency', 'n.', 'That which is convenient; that which promotes comfort\n   or advantage; that which is suited to ones wants; an accommodation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveniency', 'n.', 'A convenient or fit time; opportunity; as, to do\n   something at ones convenience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convenient', 'v. i.', 'Fit or adapted; suitable; proper; becoming;\n   appropriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convenient', 'v. i.', 'Affording accommodation or advantage; well adapted\n   to use; handly; as, a convenient house; convenient implements or tools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convenient', 'v. i.', 'Seasonable; timely; opportune; as, a convenient\n   occasion; a convenient season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convenient', 'v. i.', 'Near at hand; easy of access.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveniently', 'adv.', 'In a convenient manner, form, or situation; without\n   difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convent', 'v. i.', 'A coming together; a meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convent', 'v. i.', 'An association or community of recluses devoted to a\n   religious life; a body of monks or nuns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convent', 'v. i.', 'A house occupied by a community of religious recluses;\n   a monastery or nunnery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convent', 'v. i.', 'To meet together; to concur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convent', 'v. i.', 'To be convenient; to serve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convent', 'v. t.', 'To call before a judge or judicature; to summon; to\n   convene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventical', 'a.', 'Of or from, or pertaining to, a convent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventicle', 'n.', 'A small assembly or gathering; esp., a secret\n   assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventicle', 'n.', 'An assembly for religious worship; esp., such an\n   assembly held privately, as in times of persecution, by Nonconformists\n   or Dissenters in England, or by Covenanters in Scotland; -- often used\n   opprobriously, as if those assembled were heretics or schismatics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventicler', 'n.', 'One who supports or frequents conventicles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventicling', 'a.', 'Belonging or going to, or resembling, a conventicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convention', 'v. i.', 'The act of coming together; the state of being\n   together; union; coalition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convention', 'v. i.', 'General agreement or concurrence; arbitrary custom;\n   usage; conventionality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convention', 'v. i.', 'A meeting or an assembly of persons, esp. of\n   delegates or representatives, to accomplish some specific object, --\n   civil, social, political, or ecclesiastical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convention', 'v. i.', 'An extraordinary assembly of the parkiament or\n   estates of the realm, held without the kings writ, -- as the assembly\n   which restored Charles II. to the throne, and that which declared the\n   throne to be abdicated by James II.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convention', 'v. i.', 'An agreement or contract less formal than, or\n   preliminary to, a treaty; an informal compact, as between commanders of\n   armies in respect to suspension of hostilities, or between states;\n   also, a formal agreement between governments or sovereign powers; as, a\n   postal convention between two governments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventional', 'a.', 'Formed by agreement or compact; stipulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventional', 'a.', 'Growing out of, or depending on, custom or tacit\n   agreement; sanctioned by general concurrence or usage; formal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventional', 'a.', 'Based upon tradition, whether religious and\n   historical or of artistic rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventional', 'a.', 'Abstracted; removed from close representation of\n   nature by the deliberate selection of what is to be represented and\n   what is to be rejected; as, a conventional flower; a conventional\n   shell. Cf. Conventionalize, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalism', 'n.', 'That which is received or established by\n   convention or arbitrary agreement; that which is in accordance with the\n   fashion, tradition, or usage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalism', 'n.', 'The principles or practice of conventionalizing.\n   See Conventionalize, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalist', 'n.', 'One who adheres to a convention or treaty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalist', 'n.', 'One who is governed by conventionalism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalities', 'pl. ', 'of Conventionality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionality', 'n.', 'The state of being conventional; adherence to\n   social formalities or usages; that which is established by conventional\n   use; one of the customary usages of social life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalization', 'n.', 'The act of making conventional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalization', 'n.', 'The state of being conventional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalized', 'imp. & p. p.', 'of Conventionalizw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalizing', 'p. pr. & vb. n.', 'of Conventionalizw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalizw', 'v. t.', 'To make conventional; to bring under the\n   influence of, or cause to conform to, conventional rules; to establish\n   by usage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalizw', 'v. t.', 'To represent by selecting the important\n   features and those which are expressible in the medium employed, and\n   omitting the others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalizw', 'v. t.', 'To represent according to an established\n   principle, whether religious or traditional, or based upon certain\n   artistic rules of supposed importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalize', 'v. i.', 'To make designs in art, according to\n   conventional principles. Cf. Conventionalize, v. t., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionalily', 'adv.', 'In a conventional manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionary', 'a.', 'Acting under contract; settled by express agreement;\n   as, conventionary tenants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventioner', 'n.', 'One who belongs to a convention or assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventionist', 'n.', 'One who enters into a convention, covenant, or\n   contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventual', 'a.', 'Of or pertaining to a convent; monastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conventual', 'n.', 'One who lives in a convent; a monk or nun; a recluse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converged', 'imp. & p. p.', 'of Converge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converging', 'p. pr. & vb. n.', 'of Converge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converge', 'v. i.', 'To tend to one point; to incline and approach nearer\n   together; as, lines converge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converge', 'v. t.', 'To cause to tend to one point; to cause to incline and\n   approach nearer together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convergence', 'n.', 'Alt. of Convergency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convergency', 'n.', 'The condition or quality of converging; tendency to\n   one point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convergent', 'a.', 'tending to one point of focus; tending to approach each\n   other; converging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converging', 'a.', 'Tending to one point; approaching each other;\n   convergent; as, converging lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversable', 'a.', 'Qualified for conversation; disposed to converse;\n   sociable; free in discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversableness', 'n.', 'The quality of being conversable; disposition to\n   converse; sociability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversably', 'adv.', 'In a conversable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversance', 'n.', 'The state or quality of being conversant; habit of\n   familiarity; familiar acquaintance; intimacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversancy', 'n.', 'Conversance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversant', 'a.', 'Having frequent or customary intercourse; familiary\n   associated; intimately acquainted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversant', 'a.', 'Familiar or acquainted by use or study; well-informed;\n   versed; -- generally used with with, sometimes with in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversant', 'a.', 'Concerned; occupied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversant', 'n.', 'One who converses with another; a convenser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversantly', 'adv.', 'In a familiar manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversation', 'n.', 'General course of conduct; behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversation', 'n.', 'Familiar intercourse; intimate fellowship or\n   association; close acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversation', 'n.', 'Commerce; intercourse; traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversation', 'n.', 'Colloquial discourse; oral interchange of sentiments\n   and observations; informal dialogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversation', 'n.', 'Sexual intercourse; as, criminal conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversational', 'a.', 'Pertaining to conversation; in the manner of one\n   conversing; as, a conversational style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversationalist', 'n.', 'A conversationist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversationed', 'a.', 'Acquainted with manners and deportment; behaved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversationism', 'n.', 'A word or phrase used in conversation; a\n   colloquialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversationist', 'n.', 'One who converses much, or who excels in\n   conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversative', 'a.', 'Relating to intercourse with men; social; -- opposed\n   to contemplative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversazioni', 'pl. ', 'of Conversazi-one');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversazi-one', 'n.', 'A meeting or assembly for conversation,\n   particularly on literary or scientific subjects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversed', 'imp. & p. p.', 'of Converse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversing', 'p. pr. & vb. n.', 'of Converse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converse', 'v. i.', 'To keep company; to hold intimate intercourse; to\n   commune; -- followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converse', 'v. i.', 'To engage in familiar colloquy; to interchange\n   thoughts and opinions in a free, informal manner; to chat; -- followed\n   by with before a person; by on, about, concerning, etc., before a\n   thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converse', 'v. i.', 'To have knowledge of, from long intercourse or study;\n   -- said of things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converse', 'n.', 'Frequent intercourse; familiar communion; intimate\n   association.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converse', 'n.', 'Familiar discourse; free interchange of thoughts or\n   views; conversation; chat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converse', 'a.', 'Turned about; reversed in order or relation; reciprocal;\n   as, a converse proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converse', 'n.', 'A proposition which arises from interchanging the terms\n   of another, as by putting the predicate for the subject, and the\n   subject for the predicate; as, no virtue is vice, no vice is virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converse', 'n.', 'A proposition in which, after a conclusion from something\n   supposed has been drawn, the order is inverted, making the conclusion\n   the supposition or premises, what was first supposed becoming now the\n   conclusion or inference. Thus, if two sides of a sides of a triangle\n   are equal, the angles opposite the sides are equal; and the converse is\n   true, i.e., if these angles are equal, the two sides are equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversely', 'adv.', 'In a converse manner; with change of order or\n   relation; reciprocally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converser', 'n.', 'One who engages in conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversible', 'a.', 'Capable of being converted or reversed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversion', 'n.', 'The act of turning or changing from one state or\n   condition to another, or the state of being changed; transmutation;\n   change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversion', 'n.', 'The act of changing ones views or course, as in\n   passing from one side, party, or from of religion to another; also, the\n   state of being so changed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversion', 'n.', 'An appropriation of, and dealing with the property of\n   another as if it were ones own, without right; as, the conversion of a\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversion', 'n.', 'The act of interchanging the terms of a proposition, as\n   by putting the subject in the place of the predicate, or the contrary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversion', 'n.', 'A change or reduction of the form or value of a\n   proposition; as, the conversion of equations; the conversion of\n   proportions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversion', 'n.', 'A change of front, as a body of troops attacked in the\n   flank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversion', 'n.', 'A change of character or use, as of smoothbore guns\n   into rifles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversion', 'n.', 'A spiritual and moral change attending a change of\n   belief with conviction; a change of heart; a change from the service of\n   the world to the service of God; a change of the ruling disposition of\n   the soul, involving a transformation of the outward life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversive', 'a.', 'Capable of being converted or changed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conversive', 'a.', 'Ready to converse; social.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converted', 'imp. & p. p.', 'of Convert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converting', 'p. pr. & vb. n.', 'of Convert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'v. t.', 'To cause to turn; to turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'v. t.', 'To change or turn from one state or condition to\n   another; to alter in form, substance, or quality; to transform; to\n   transmute; as, to convert water into ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'v. t.', 'To change or turn from one belief or course to another,\n   as from one religion to another or from one party or sect to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'v. t.', 'To produce the spiritual change called conversion in\n   (any one); to turn from a bad life to a good one; to change the heart\n   and moral character of (any one) from the controlling power of sin to\n   that of holiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'v. t.', 'To apply to any use by a diversion from the proper or\n   intended use; to appropriate dishonestly or illegally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'v. t.', 'To exchange for some specified equivalent; as, to\n   convert goods into money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'v. t.', 'To change (one proposition) into another, so that what\n   was the subject of the first becomes the predicate of the second.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'v. t.', 'To turn into another language; to translate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'v. i.', 'To be turned or changed in character or direction; to\n   undergo a change, physically or morally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'n.', 'A person who is converted from one opinion or practice to\n   another; a person who is won over to, or heartily embraces, a creed,\n   religious system, or party, in which he has not previously believed;\n   especially, one who turns from the controlling power of sin to that of\n   holiness, or from unbelief to Christianity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convert', 'n.', 'A lay friar or brother, permitted to enter a monastery for\n   the service of the house, but without orders, and not allowed to sing\n   in the choir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convertend', 'n.', 'Any proposition which is subject to the process of\n   conversion; -- so called in its relation to itself as converted, after\n   which process it is termed the converse. See Converse, n. (Logic).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converter', 'n.', 'One who converts; one who makes converts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Converter', 'n.', 'A retort, used in the Bessemer process, in which molten\n   cast iron is decarburized and converted into steel by a blast of air\n   forced through the liquid metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convertibility', 'n.', 'The condition or quality of being convertible;\n   capability of being exchanged; convertibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convertible', 'a.', 'Capable of being converted; susceptible of change;\n   transmutable; transformable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convertible', 'a.', 'Capable of being exchanged or interchanged;\n   reciprocal; interchangeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convertibleness', 'n.', 'The state of being convertible; convertibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convertibly', 'adv.', 'In a convertible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convertite', 'n.', 'A convert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convex', 'a.', 'Rising or swelling into a spherical or rounded form;\n   regularly protuberant or bulging; -- said of a spherical surface or\n   curved line when viewed from without, in opposition to concave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convex', 'n.', 'A convex body or surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convexed', 'a.', 'Made convex; protuberant in a spherical form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convexedly', 'dv.', 'In a convex form; convexly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convexedness', 'n.', 'Convexity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convexities', 'pl. ', 'of Convexity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convexity', 'n.', 'The state of being convex; the exterior surface of a\n   convex body; roundness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convexly', 'adv.', 'In a convex form; as, a body convexly shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convexness', 'n.', 'The state of being convex; convexity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convexo-concave', 'a.', 'Convex on one side, and concave on the other. The\n   curves of the convex and concave sides may be alike or may be\n   different. See Meniscus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convexo-convex', 'a.', 'Convex on both sides; double convex. See under\n   Convex, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convexo-plane', 'a.', 'Convex on one side, and flat on the other;\n   plano-convex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyed', 'imp. & p. p.', 'of Convey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveying', 'p. pr. & vb. n.', 'of Convey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convey', 'v. t.', 'To carry from one place to another; to bear or\n   transport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convey', 'v. t.', 'To cause to pass from one place or person to another; to\n   serve as a medium in carrying (anything) from one place or person to\n   another; to transmit; as, air conveys sound; words convey ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convey', 'v. t.', 'To transfer or deliver to another; to make over, as\n   property; more strictly (Law); to transfer (real estate) or pass (a\n   title to real estate) by a sealed writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convey', 'v. t.', 'To impart or communicate; as, to convey an impression;\n   to convey information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convey', 'v. t.', 'To manage with privacy; to carry out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convey', 'v. t.', 'To carry or take away secretly; to steal; to thieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convey', 'v. t.', 'To accompany; to convoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convey', 'v. i.', 'To play the thief; to steal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyable', 'a.', 'Capable of being conveyed or transferred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyance', 'n.', 'The act of conveying, carrying, or transporting;\n   carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyance', 'n.', 'The instrument or means of carrying or transporting\n   anything from place to place; the vehicle in which, or means by which,\n   anything is carried from one place to another; as, stagecoaches,\n   omnibuses, etc., are conveyances; a canal or aqueduct is a conveyance\n   for water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyance', 'n.', 'The act or process of transferring, transmitting,\n   handing down, or communicating; transmission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyance', 'n.', 'The act by which the title to property, esp. real\n   estate, is transferred; transfer of ownership; an instrument in writing\n   (as a deed or mortgage); by which the title to property is conveyed\n   from one person to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyance', 'n.', 'Dishonest management, or artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyancer', 'n.', 'One whose business is to draw up conveyances of\n   property, as deeds, mortgages, leases, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyancing', 'n.', 'The business of a conveyancer; the act or business of\n   drawing deeds, leases, or other writings, for transferring the title to\n   property from one person to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyer', 'n.', 'One who, or that which, conveys or carries, transmits or\n   transfers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyer', 'n.', 'One given to artifices or secret practices; a juggler; a\n   cheat; a thief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conveyor', 'n.', 'A contrivance for carrying objects from place to place;\n   esp., one for conveying grain, coal, etc., -- as a spiral or screw\n   turning in a pipe or trough, an endless belt with buckets, or a truck\n   running along a rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conviciate', 'v. i.', 'To utter reproaches; to raise a clamor; to rail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convicinities', 'pl. ', 'of Convicinity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convicinity', 'n.', 'Immediate vicinity; neighborhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convicious', 'a.', 'Expressing reproach; abusive; railing; taunting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convict', 'p.a.', 'Proved or found guilty; convicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convict', 'n.', 'A person proved guilty of a crime alleged against him; one\n   legally convicted or sentenced to punishment for some crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convict', 'n.', 'A criminal sentenced to penal servitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convicted', 'imp. & p. p.', 'of Convict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convicting', 'p. pr. & vb. n.', 'of Convict');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convict', 'v. t.', 'To prove or find guilty of an offense or crime charged;\n   to pronounce guilty, as by legal decision, or by ones conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convict', 'v. t.', 'To prove or show to be false; to confute; to refute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convict', 'v. t.', 'To demonstrate by proof or evidence; to prove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convict', 'v. t.', 'To defeat; to doom to destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convict1ible', 'a.', 'Capable of being convicted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conviction', 'n.', 'The act of convicting; the act of proving, finding, or\n   adjudging, guilty of an offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conviction', 'n.', 'A judgment of condemnation entered by a court having\n   jurisdiction; the act or process of finding guilty, or the state of\n   being found guilty of any crime by a legal tribunal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conviction', 'n.', 'The act of convincing of error, or of compelling the\n   admission of a truth; confutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conviction', 'n.', 'The state of being convinced or convicted; strong\n   persuasion or belief; especially, the state of being convicted of sin,\n   or by ones conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convictism', 'n.', 'The policy or practice of transporting convicts to\n   penal settlements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convictive', 'a.', 'Convincing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convinced', 'imp. & p. p.', 'of Convince');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convincing', 'p. pr. & vb. n.', 'of Convince');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convince', 'v. t.', 'To overpower; to overcome; to subdue or master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convince', 'v. t.', 'To overcome by argument; to force to yield assent to\n   truth; to satisfy by proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convince', 'v. t.', 'To confute; to prove the fallacy of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convince', 'v. t.', 'To prove guilty; to convict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convincement', 'n.', 'Act of convincing, or state of being convinced;\n   conviction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convincer', 'n.', 'One who, or that which, convinces; one who wins over by\n   proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convincible', 'a.', 'Capable of being convinced or won over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convincible', 'a.', 'Capable of being confuted and disproved by argument;\n   refutable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convincingly', 'adv.', 'in a convincing manner; in a manner to compel\n   assent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convincingness', 'n.', 'The power of convincing, or the quality of being\n   convincing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convival', 'a.', 'pertaining to a feast or to festivity; convivial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convive', 'v. i.', 'To feast together; to be convivial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convive', 'n.', 'A quest at a banquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convivial', 'a.', 'Of or relating to a feast or entertainment, or to eating\n   and drinking, with accompanying festivity; festive; social; gay;\n   jovial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convivialist', 'n.', 'A person of convivial habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convivialities', 'pl. ', 'of Conviviality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conviviality', 'n.', 'The good humor or mirth indulged in upon festive\n   occasions; a convivial spirit or humor; festivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convivially', 'adv.', 'In a convivial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convocated', 'imp. & p. p.', 'of Convocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convocating', 'p. pr. & vb. n.', 'of Convocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convocate', 'v. t.', 'To convoke; to call together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convocation', 'n.', 'The act of calling or assembling by summons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convocation', 'n.', 'An assembly or meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convocation', 'n.', 'An assembly of the clergy, by their representatives,\n   to consult on ecclesiastical affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convocation', 'n.', 'An academical assembly, in which the business of the\n   university is transacted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convocational', 'a.', 'Of or pertaining to a convocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convocationist', 'n.', 'An advocate or defender of convocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoked', 'imp. & p. p.', 'of Convoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoking', 'p. pr. & vb. n.', 'of Convoke');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoke', 'v. t.', 'To call together; to summon to meet; to assemble by\n   summons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolute', 'a.', 'Rolled or wound together, one part upon another; -- said\n   of the leaves of plants in aestivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoluted', 'a.', 'Having convolutions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoluted', 'a.', 'Folded in tortuous windings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolution', 'n.', 'The act of rolling anything upon itself, or one thing\n   upon another; a winding motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolution', 'n.', 'The state of being rolled upon itself, or rolled or\n   doubled together; a tortuous or sinuous winding or fold, as of\n   something rolled or folded upon itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolution', 'n.', 'An irregular, tortuous folding of an organ or part;\n   as, the convolutions of the intestines; the cerebral convolutions. See\n   Brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolved', 'imp. & p. p.', 'of Convolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolving', 'p. pr. & vb. n.', 'of Convolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolve', 'v. t.', 'To roll or wind together; to roll or twist one part on\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolvulaceous', 'a.', 'Of, pertaining to, or resembling, the family of\n   plants of which the bindweed and the morning-glory are common examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolvulin', 'n.', 'A glucoside occurring in jalap (the root of a\n   convolvulaceous plant); and extracted as a colorless, tasteless, gummy\n   mass of powerful purgative properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolvuli', 'pl. ', 'of Convolvulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoluluses', 'pl. ', 'of Convolvulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convolvulus', 'n.', 'A large genus of plants having monopetalous flowers,\n   including the common bindweed (C. arwensis); and formerly the\n   morning-glory, but this is now transferred to the genus Ipomaea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoyed', 'imp. & p. p.', 'of Convoy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoying', 'p. pr. & vb. n.', 'of Convoy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoy', 'v. t.', 'To accompany for protection, either by sea or land; to\n   attend for protection; to escort; as, a frigate convoys a merchantman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoy', 'n.', 'The act of attending for defense; the state of being so\n   attended; protection; escort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoy', 'n.', 'A vessel or fleet, or a train or trains of wagons, employed\n   in the transportation of munitions of war, money, subsistence,\n   clothing, etc., and having an armed escort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoy', 'n.', 'A protection force accompanying ships, etc., on their way\n   from place to place, by sea or land; an escort, for protection or\n   guidance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoy', 'n.', 'Conveyance; means of transportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convoy', 'n.', 'A drag or brake applied to the wheels of a carriage, to\n   check their velocity in going down a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulsed', 'imp. & p. p.', 'of Convulse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulsing', 'p. pr. & vb. n.', 'of Convulse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulse', 'v. t.', 'To contract violently and irregulary, as the muscular\n   parts of an animal body; to shake with irregular spasms, as in\n   excessive laughter, or in agony from grief or pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulse', 'v. t.', 'To agitate greatly; to shake violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulsion', 'n.', 'An unnatural, violent, and unvoluntary contraction of\n   the muscular parts of an animal body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulsion', 'n.', 'Any violent and irregular motion or agitation; a\n   violent shaking; a tumult; a commotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulsional', 'a.', 'Pertaining to, or having, convulsions; convulsionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulsionary', 'a.', 'Pertaining to convulsion; convulsive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulsionary', 'n.', 'A convulsionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulsionist', 'n.', 'One who has convulsions; esp., one of a body of\n   fanatics in France, early in the eighteenth century, who went into\n   convulsions under the influence of religious emotion; as, the\n   Convulsionists of St. Medard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulsive', 'a.', 'Producing, or attended with, convulsions or spasms;\n   characterized by convulsions; convulsionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Convulsively', 'adv.', 'in a convulsive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cony', 'n.', 'A rabbit, esp., the European rabbit (Lepus cuniculus)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cony', 'n.', 'The chief hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cony', 'n.', 'A simpleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cony', 'n.', 'An important edible West Indian fish (Epinephelus apua); the\n   hind of Bermuda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cony', 'n.', 'A local name of the burbot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cony-catch', 'v. t.', 'To deceive; to cheat; to trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cony-catcher', 'n.', 'A cheat; a sharper; a deceiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conylene', 'n.', 'An oily substance, C8H14, obtained from several\n   derivatives of conine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Conyrine', 'n.', 'A blue, fluorescent, oily base (regarded as a derivative\n   of pyridine); obtained from conine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooed', 'imp. & p. p.', 'of Coo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooing', 'p. pr. & vb. n.', 'of Coo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coo', 'v. i.', 'To make a low repeated cry or sound, like the\n   characteristic note of pigeons or doves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coo', 'v. i.', 'To show affection; to act in a loving way. See under Bill,\n   v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooey', 'n.', 'Alt. of Cooee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooee', 'n.', 'A peculiar whistling sound made by the Australian aborigenes\n   as a call or signal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cook', 'v. i.', 'To make the noise of the cuckoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cook', 'v. t.', 'To throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cook', 'n.', 'One whose occupation is to prepare food for the table; one\n   who dresses or cooks meat or vegetables for eating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cook', 'n.', 'A fish, the European striped wrasse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooked', 'imp. & p. p.', 'of Cook');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooking', 'p. pr & vb. n.', 'of Cook');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cook', 'v. t.', 'To prepare, as food, by boiling, roasting, baking,\n   broiling, etc.; to make suitable for eating, by the agency of fire or\n   heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cook', 'v. t.', 'To concoct or prepare; hence, to tamper with or alter; to\n   garble; -- often with up; as, to cook up a story; to cook an account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cook', 'v. i.', 'To prepare food for the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cookbook', 'n.', 'A book of directions and receipts for cooking; a cookery\n   book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cookee', 'n.', 'A female cook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cookery', 'n.', 'The art or process of preparing food for the table, by\n   dressing, compounding, and the application of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cookery', 'n.', 'A delicacy; a dainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cookey', 'n.', 'Alt. of Cookie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cookie', 'n.', 'See Cooky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cookmaid', 'n.', 'A female servant or maid who dresses provisions and\n   assists the cook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cookroom', 'n.', 'A room for cookery; a kitchen; the galley or caboose of a\n   ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cookshop', 'n.', 'An eating house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cookies', 'pl. ', 'of Cooky');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooky', 'n.', 'A small, flat, sweetened cake of various kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'superl.', 'Moderately cold; between warm and cold; lacking in\n   warmth; producing or promoting coolness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'superl.', 'Not ardent, warm, fond, or passionate; not hasty;\n   deliberate; exercising self-control; self-possessed; dispassionate;\n   indifferent; as, a cool lover; a cool debater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'superl.', 'Not retaining heat; light; as, a cool dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'superl.', 'Manifesting coldness or dislike; chilling; apathetic;\n   as, a cool manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'superl.', 'Quietly impudent; negligent of propriety in matters of\n   minor importance, either ignorantly or willfully; presuming and\n   selfish; audacious; as, cool behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'superl.', 'Applied facetiously, in a vague sense, to a sum of\n   money, commonly as if to give emphasis to the largeness of the amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'n.', 'A moderate state of cold; coolness; -- said of the\n   temperature of the air between hot and cold; as, the cool of the day;\n   the cool of the morning or evening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooled', 'imp. & p. p.', 'of Cool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooling', 'p. pr. & vb. n.', 'of Cool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'v. t.', 'To make cool or cold; to reduce the temperature of; as,\n   ice cools water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'v. t.', 'To moderate the heat or excitement of; to allay, as\n   passion of any kind; to calm; to moderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'v. i.', 'To become less hot; to lose heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool', 'v. i.', 'To lose the heat of excitement or passion; to become more\n   moderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooler', 'n.', 'That which cools, or abates heat or excitement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooler', 'n.', 'Anything in or by which liquids or other things are cooled,\n   as an ice chest, a vessel for ice water, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cool-headed', 'a.', 'Having a temper not easily excited; free from passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coolie', 'n.', 'Same as Cooly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooling', 'p.a.', 'Adapted to cool and refresh; allaying heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coolish', 'a.', 'Somewhat cool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coolly', 'a.', 'Coolish; cool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coolly', 'adv.', 'In a cool manner; without heat or excessive cold; without\n   passion or ardor; calmly; deliberately; with indifference; impudently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coolness', 'n.', 'The state of being cool; a moderate degree of cold; a\n   moderate degree, or a want, of passion; want of ardor, zeal, or\n   affection; calmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coolness', 'n.', 'Calm impudence; self-possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coolung', 'n.', 'The great gray crane of India (Grus cinerea).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coolies', 'pl. ', 'of Coolie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooly', 'n.', 'Alt. of Coolie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coolie', 'n.', 'An East Indian porter or carrier; a laborer transported\n   from the East Indies, China, or Japan, for service in some other\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coom', 'n.', 'Soot; coal dust; refuse matter, as the dirty grease which\n   comes from axle boxes, or the refuse at the mouth of an oven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coomb', 'n.', 'A dry measure of four bushels, or half a quarter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coomb', 'n.', 'Alt. of Coombe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coombe', 'n.', 'A hollow in a hillside. [Prov. Eng.] See Comb, Combe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coon', 'n.', 'A raccoon. See Raccoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coontie', 'n.', 'A cycadaceous plant of Florida and the West Indies, the\n   Zamia integrifolia, from the stems of which a kind of sago is prepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coop', 'n.', 'A barrel or cask for liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coop', 'n.', 'An inclosure for keeping small animals; a pen; especially, a\n   grated box for confining poultry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coop', 'n.', 'A cart made close with boards; a tumbrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooped', 'imp. & p. p.', 'of Coop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooping', 'p. pr. & vb. n.', 'of Coop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coop', 'v. t.', 'To confine in a coop; hence, to shut up or confine in a\n   narrow compass; to cramp; -- usually followed by up, sometimes by in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coop', 'v. t.', 'To work upon in the manner of a cooper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coopee', 'n.', 'See Coupe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooper', 'n.', 'One who makes barrels, hogsheads, casks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coopered', 'imp. & p. p.', 'of Cooper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coopering', 'p. pr. & vb. n.', 'of Cooper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooper', 'v. t.', 'To do the work of a cooper upon; as, to cooper a cask or\n   barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperage', 'n.', 'Work done by a cooper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperage', 'n.', 'The price paid for coopers; work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperage', 'n.', 'A place where coopers work is done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperant', 'a.', 'Operating together; as, cooperant forces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperated', 'imp. & p. p.', 'of Cooperate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperating', 'p. pr. & vb. n.', 'of Cooperate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperate', 'v. i.', 'To act or operate jointly with another or others; to\n   concur in action, effort, or effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperation', 'n.', 'The act of cooperating, or of operating together to\n   one end; joint operation; concurrent effort or labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperation', 'n.', 'The association of a number of persons for their\n   benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperative', 'a.', 'Operating jointly to the same end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooperator', 'n.', 'One who labors jointly with others to promote the same\n   end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooper', 'n.', 'Work done by a cooper in making or repairing barrels,\n   casks, etc.; the business of a cooper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coopery', 'a.', 'Relating to a cooper; coopered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coopery', 'n.', 'The occupation of a cooper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coopt', 'v. t.', 'To choose or elect in concert with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooptate', 'v. t.', 'To choose; to elect; to coopt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooptation', 'n.', 'The act of choosing; selection; choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordain', 'v. t.', 'To ordain or appoint for some purpose along with\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinance', 'n.', 'Joint ordinance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinate', 'a.', 'Equal in rank or order; not subordinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinated', 'imp. & p. p.', 'of Coordinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinating', 'p. pr. & vb. n.', 'of Coordinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinate', 'v. t.', 'To make coordinate; to put in the same order or\n   rank; as, to coordinate ideas in classification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinate', 'v. t.', 'To give a common action, movement, or condition to;\n   to regulate and combine so as to produce harmonious action; to adjust;\n   to harmonize; as, to coordinate muscular movements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinate', 'n.', 'A thing of the same rank with another thing; one two or\n   more persons or things of equal rank, authority, or importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinate', 'n.', 'Lines, or other elements of reference, by means of\n   which the position of any point, as of a curve, is defined with respect\n   to certain fixed lines, or planes, called coordinate axes and\n   coordinate planes. See Abscissa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinately', 'adv.', 'In a coordinate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinateness', 'n.', 'The state of being coordinate; equality of rank or\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordination', 'n.', 'The act of coordinating; the act of putting in the\n   same order, class, rank, dignity, etc.; as, the coordination of the\n   executive, the legislative, and the judicial authority in forming a\n   government; the act of regulating and combining so as to produce\n   harmonious results; harmonious adjustment; as, a coordination of\n   functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordination', 'n.', 'The state of being coordinate, or of equal rank,\n   dignity, power, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coordinative', 'a.', 'Expressing coordination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coot', 'n.', 'A wading bird with lobate toes, of the genus Fulica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coot', 'n.', 'The surf duck or scoter. In the United States all the species\n   of (/demia are called coots. See Scoter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coot', 'n.', 'A stupid fellow; a simpleton; as, a silly coot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooter', 'n.', 'A fresh-water tortoise (Pseudemus concinna) of Florida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cooter', 'n.', 'The box tortoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cootfoot', 'n.', 'The phalarope; -- so called because its toes are like the\n   coots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coothay', 'n.', 'A striped satin made in India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cop', 'n.', 'The top of a thing; the head; a crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cop', 'n.', 'A conical or conical-ended mass of coiled thread, yarn, or\n   roving, wound upon a spindle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cop', 'n.', 'A tube or quill upon which silk is wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cop', 'n.', 'Same as Merlon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cop', 'n.', 'A policeman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copaiba', 'n.', 'Alt. of Copaiva');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copaiva', 'n.', 'A more or less viscid, yellowish liquid, the bitter\n   oleoresin of several species of Copaifera, a genus of trees growing in\n   South America and the West Indies. It is stimulant and diuretic, and is\n   much used in affections of the mucous membranes; -- called also balsam\n   of copaiba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copal', '', 'A resinous substance flowing spontaneously from trees of\n   Zanzibar, Madagascar, and South America (Trachylobium Hornemannianum,\n   T. verrucosum, and Hymenaea Courbaril); and dug from earth where\n   forests have stood in Africa; -- used chiefly in making varnishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coparcenaries', 'pl. ', 'of Coparcenary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coparcenary', 'n.', 'Partnership in inheritance; joint heirship; joint\n   right of succession to an inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coparcener', 'n.', 'One who has an equal portion with others of an\n   inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coparceny', 'n.', 'An equal share of an inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copart', 'v. t.', 'To share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copartment', 'n.', 'A compartment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copartner', 'n.', 'One who is jointly concerned with one or more persons in\n   business, etc.; a partner; an associate; a partaker; a sharer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copartnership', 'n.', 'The state of being a copartner or of having a joint\n   interest in any matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copartnership', 'n.', 'A partnership or firm; as, A. and B. have this day\n   formed a copartnership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copartneries', 'pl. ', 'of Copartnery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copartnery', 'n.', 'the state of being copartners in any undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copatain', 'a.', 'Having a high crown, or a point or peak at top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copatriot', 'n.', 'A joint patriot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'n.', 'A covering for the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'n.', 'Anything regarded as extended over the head, as the arch or\n   concave of the sky, the roof of a house, the arch over a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'n.', 'An ecclesiastical vestment or cloak, semicircular in form,\n   reaching from the shoulders nearly to the feet, and open in front\n   except at the top, where it is united by a band or clasp. It is worn in\n   processions and on some other occasions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'n.', 'An ancient tribute due to the lord of the soil, out of the\n   lead mines in Derbyshire, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'n.', 'The top part of a flask or mold; the outer part of a loam\n   mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'v. i.', 'To form a cope or arch; to bend or arch; to bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'v. t.', 'To pare the beak or talons of (a hawk).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coped', 'imp. & p. p.', 'of Cope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coping', 'p. pr. & vb. n.', 'of Cope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'v. i.', 'To exchange or barter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'v. i.', 'To encounter; to meet; to have to do with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'v. i.', 'To enter into or maintain a hostile contest; to struggle;\n   to combat; especially, to strive or contend on equal terms or with\n   success; to match; to equal; -- usually followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'v. t.', 'To bargain for; to buy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'v. t.', 'To make return for; to requite; to repay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope', 'v. t.', 'To match ones self against; to meet; to encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cope-chisel', 'n.', 'A narrow chisel adapted for cutting a groove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copeck', 'n.', 'A Russian copper coin. See Kopeck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coped', 'a.', 'Clad in a cope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copelata', 'n. pl.', 'See Larvalla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copeman', 'v. i.', 'A chapman; a dealer; a merchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copepod', 'a.', 'Of or pertaining to the Copepoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copepod', 'n.', 'One of the Copepoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copepoda', 'n. pl.', 'An order of Entomostraca, including many minute\n   Crustacea, both fresh-water and marine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copernican', 'a.', 'Pertaining to Copernicus, a Prussian by birth (b. 1473,\n   d. 1543); who taught the world the solar system now received, called\n   the Copernican system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copesmate', 'n.', 'An associate or companion; a friend; a partner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copestone', 'n.', 'A stone for coping. See Coping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copier', 'n.', 'One who copies; one who writes or transcribes from an\n   original; a transcriber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copier', 'n.', 'An imitator; one who imitates an example; hence, a\n   plagiarist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coping', 'n.', 'The highest or covering course of masonry in a wall, often\n   with sloping edges to carry off water; -- sometimes called capping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copious', 'a.', 'Large in quantity or amount; plentiful; abundant;\n   fruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copiously', 'adv.', 'In a copious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copiousness', 'n.', 'The state or quality of being copious; abudance;\n   plenty; also, diffuseness in style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copist', 'n.', 'A copier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coplaner', 'a.', 'Situated in one plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copland', 'n.', 'A piece of ground terminating in a point or acute angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coportion', 'n.', 'Equal share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copped', 'a.', 'Rising to a point or head; conical; pointed; crested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coppel', 'n. & v.', 'See Cupel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper', 'n.', 'A common metal of a reddish color, both ductile and\n   malleable, and very tenacious. It is one of the best conductors of heat\n   and electricity. Symbol Cu. Atomic weight 63.3. It is one of the most\n   useful metals in itself, and also in its alloys, brass and bronze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper', 'n.', 'A coin made of copper; a penny, cent, or other minor coin\n   of copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper', 'n.', 'A vessel, especially a large boiler, made of copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper', 'n.', 'the boilers in the galley for cooking; as, a ships\n   coppers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coppered', 'imp. & p. p.', 'of Copper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coppering', 'p. pr. & vb. n.', 'of Copper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper', 'v. t.', 'To cover or coat with copper; to sheathe with sheets of\n   copper; as, to copper a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copperas', 'n.', 'Green vitriol, or sulphate of iron; a green crystalline\n   substance, of an astringent taste, used in making ink, in dyeing black,\n   as a tonic in medicine, etc. It is made on a large scale by the\n   oxidation of iron pyrites. Called also ferrous sulphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper-bottomed', 'a.', 'Having a bottom made of copper, as a tin boiler or\n   other vessel, or sheathed with copper, as a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper-faced', 'a.', 'Faced or covered with copper; as, copper-faced type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper-fastened', 'a.', 'Fastened with copper bolts, as the planks of\n   ships, etc.; as, a copper-fastened ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copperhead', 'n.', 'A poisonous American serpent (Ancistrodon conotortrix);\n   closely allied to the rattlesnake, but without rattles; -- called also\n   copper-belly, and red viper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copperhead', 'n.', 'A nickname applied to a person in the Northern States\n   who sympathized with the South during the Civil War.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coppering', 'n.', 'The act of covering with copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coppering', 'n.', 'An envelope or covering of copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copperish', 'a.', 'Containing, or partaking of the nature of, copper; like\n   copper; as, a copperish taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper-nickel', 'n.', 'Niccolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper-nose', 'n.', 'A red nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copperplate', 'n.', 'A plate of polished copper on which a design or\n   writing is engraved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copperplate', 'n.', 'An impression on paper taken from such a plate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coppersmith', 'n.', 'One whose occupation is to manufacture copper\n   utensils; a worker in copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copper works', '', 'A place where copper is wrought or manufactured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copperworm', 'n.', 'The teredo; -- so called because it injures the bottoms\n   of vessels, where not protected by copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copperworm', 'n.', 'The ringworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coppery', 'a.', 'Mixed with copper; containing copper, or made of copper;\n   like copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coppice', 'n.', 'A grove of small growth; a thicket of brushwood; a wood\n   cut at certain times for fuel or other purposes. See Copse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coppin', 'n.', 'A cop of thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copple', 'n.', 'Something rising in a conical shape; specifically, a hill\n   rising to a point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copple-crown', 'n.', 'A created or high-topped crown or head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coppled', 'a.', 'Rising to a point; conical; copped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copple dust', '', 'Cupel dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copplestone', 'n.', 'A cobblestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copps', 'n.', 'See Copse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copra', 'n.', 'The dried meat of the cocoanut, from which cocoanut oil is\n   expressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coprolite', 'n.', 'A piece of petrified dung; a fossil excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coprolitic', 'a.', 'Containing, pertaining to, or of the nature of,\n   coprolites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coprophagan', 'n.', 'A kind of beetle which feeds upon dung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coprophagous', 'a.', 'Feeding upon dung, as certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cop-rose', 'n.', 'The red, or corn, poppy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cops', 'n.', 'The connecting crook of a harrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copse', 'n.', 'A wood of small growth; a thicket of brushwood. See Coppice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copse', 'v. t.', 'To trim or cut; -- said of small trees, brushwood, tufts\n   of grass, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copse', 'v. t.', 'To plant and preserve, as a copse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copsewood', 'n.', 'Brushwood; coppice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copsy', 'a.', 'Characterized by copses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coptic', 'a.', 'Of or pertaining to the Copts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coptic', 'n.', 'The language of the Copts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copts', 'n. pl.', 'An Egyptian race thought to be descendants of the\n   ancient Egyptians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copts', 'n. pl.', 'The principal sect of Christians in Egypt and the valley\n   of the Nile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copula', 'n.', 'The word which unites the subject and predicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copula', 'n.', 'The stop which connects the manuals, or the manuals with\n   the pedals; -- called also coupler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulate', 'a.', 'Joined; associated; coupled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulate', 'a.', 'Joining subject and predicate; copulative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulated', 'imp. & p. p.', 'of Copulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulating', 'p. pr. & vb. n.', 'of Copulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulate', 'v. i.', 'To unite in sexual intercourse; to come together in\n   the act of generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulation', 'n.', 'The act of coupling or joining; union; conjunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulation', 'n.', 'The coming together of male and female in the act of\n   generation; sexual union; coition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulative', 'a.', 'Serving to couple, unite, or connect; as, a copulative\n   conjunction like \"and\".');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulative', 'n.', 'Connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulative', 'n.', 'A copulative conjunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copulatively', 'adv.', 'In a copulative manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coplatry', 'a.', 'Pertaining to copulation; tending or serving to unite;\n   copulative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coplatry', 'a.', 'Used in sexual union; as, the copulatory organs of\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copies', 'pl. ', 'of Copy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'n.', 'An abundance or plenty of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'n.', 'An imitation, transcript, or reproduction of an original\n   work; as, a copy of a letter, an engraving, a painting, or a statue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'n.', 'An individual book, or a single set of books containing the\n   works of an author; as, a copy of the Bible; a copy of the works of\n   Addison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'n.', 'That which is to be imitated, transcribed, or reproduced; a\n   pattern, model, or example; as, his virtues are an excellent copy for\n   imitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'n.', 'Manuscript or printed matter to be set up in type; as, the\n   printers are calling for more copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'n.', 'A writing paper of a particular size. Same as Bastard. See\n   under Paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'n.', 'Copyhold; tenure; lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copied', 'imp. & p. p.', 'of Copy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copying', 'p. pr. & vb. n.', 'of Copy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'n.', 'To make a copy or copies of; to write; print, engrave, or\n   paint after an original; to duplicate; to reproduce; to transcribe; as,\n   to copy a manuscript, inscription, design, painting, etc.; -- often\n   with out, sometimes with off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'n.', 'To imitate; to attempt to resemble, as in manners or course\n   of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'v. i.', 'To make a copy or copies; to imitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copy', 'v. i.', 'To yield a duplicate or transcript; as, the letter did not\n   copy well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copyer', 'n.', 'See Copier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copygraph', 'n.', 'A contrivance for producing manifold copies of a writing\n   or drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copyhold', 'n.', 'A tenure of estate by copy of court roll; or a tenure for\n   which the tenant has nothing to show, except the rolls made by the\n   steward of the lords court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copyhold', 'n.', 'Land held in copyhold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copyholder', 'n.', 'One possessed of land in copyhold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copyholder', 'n.', 'A device for holding copy for a compositor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copyholder', 'n.', 'One who reads copy to a proof reader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copying', 'a. & n.', 'From Copy, v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copyist', 'n.', 'A copier; a transcriber; an imitator; a plagiarist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copyright', 'n.', 'The right of an author or his assignee, under statute,\n   to print and publish his literary or artistic work, exclusively of all\n   other persons. This right may be had in maps, charts, engravings,\n   plays, and musical compositions, as well as in books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Copyright', 'v. t.', 'To secure a copyright on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquelicot', 'n.', 'The wild poppy, or red corn rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquelicot', 'n.', 'The color of the wild poppy; a color nearly red, like\n   orange mixed with scarlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquetted', 'imp. & p. p.', 'of Coquet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquetting', 'p. pr. & vb. n.', 'of Coquet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquet', 'v. t.', 'To attempt to attract the notice, admiration, or love\n   of; to treat with a show of tenderness or regard, with a view to\n   deceive and disappoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquet', 'v. i.', 'To trifle in love; to stimulate affection or interest;\n   to play the coquette; to deal playfully instead of seriously; to play\n   (with); as, we have coquetted with political crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquetries', 'pl. ', 'of Coquetry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquetry', 'n.', 'Attempts to attract admiration, notice, or love, for the\n   mere gratification of vanity; trifling in love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquette', 'n.', 'A vain, trifling woman, who endeavors to attract\n   admiration from a desire to gratify vanity; a flirt; -- formerly\n   sometimes applied also to men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquette', 'n.', 'A tropical humming bird of the genus Lophornis, with very\n   elegant neck plumes. Several species are known. See Illustration under\n   Spangle, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquettish', 'a.', 'Practicing or exhibiting coquetry; alluring; enticing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquettishly', 'adv.', 'In a coquettish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquilla nut', '', 'The fruit of a Brazilian tree (Attalea funifera of\n   Martius.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquimbite', 'n.', 'A mineral consisting principally of sulphate of iron;\n   white copperas; -- so called because found in the province of Coquimbo,\n   Chili.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coquina', 'n.', 'A soft, whitish, coral-like stone, formed of broken shells\n   and corals, found in the southern United States, and used for roadbeds\n   and for building material, as in the fort at St. Augustine, Florida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cor-', '', 'A prefix signifying with, together, etc. See Com-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cor', 'n.', 'A Hebrew measure of capacity; a homer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cora', 'n.', 'The Arabian gazelle (Gazella Arabica); found from persia to\n   North Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coracle', 'n.', 'A boat made by covering a wicker frame with leather or\n   oilcloth. It was used by the ancient Britons, and is still used by\n   fisherman in Wales and some parts of Ireland. Also, a similar boat used\n   in Thibet and in Egypt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coracoid', 'a.', 'Shaped like a crows beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coracoid', 'a.', 'Pertaining to a bone of the shoulder girdle in most\n   birds, reptiles, and amphibians, which is reduced to a process of the\n   scapula in most mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coracoid', 'n.', 'The coracoid bone or process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corage', 'n.', 'See Courage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coral', 'n.', 'The hard parts or skeleton of various Anthozoa, and of a few\n   Hydrozoa. Similar structures are also formed by some Bryozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coral', 'n.', 'The ovaries of a cooked lobster; -- so called from their\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coral', 'n.', 'A piece of coral, usually fitted with small bells and other\n   appurtenances, used by children as a plaything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coraled', 'a.', 'Having coral; covered with coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coral fish', '', 'Any bright-colored fish of the genera Chaetodon,\n   Pomacentrus, Apogon, and related genera, which live among reef corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corallaceous', 'a.', 'Like coral, or partaking of its qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corallian', 'n.', 'A deposit of coralliferous limestone forming a portion\n   of the middle division of the oolite; -- called also coral-rag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralliferous', 'a.', 'Containing or producing coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralliform', 'a.', 'resembling coral in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralligena', 'n. pl.', 'Same as Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralligenous', 'a.', 'producing coral; coralligerous; coralliferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralligerous', 'a.', 'Producing coral; coralliferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corallin', 'n.', 'A yellow coal-tar dyestuff which probably consists\n   chiefly of rosolic acid. See Aurin, and Rosolic acid under Rosolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralline', 'a.', 'Composed of corallines; as, coralline limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralline', 'n.', 'A submarine, semicalcareous or calcareous plant,\n   consisting of many jointed branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralline', 'n.', 'Formerly any slender coral-like animal; -- sometimes\n   applied more particulary to bryozoan corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corallinite', 'n.', 'A fossil coralline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corallite', 'n.', 'A mineral substance or petrifaction, in the form of\n   coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corallite', 'n.', 'One of the individual members of a compound coral; or\n   that part formed by a single coral animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralloid', 'a.', 'Having the form of coral; branching like coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralloidal', 'a.', 'resembling coral; coralloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corallum', 'n.', 'The coral or skeleton of a zoophyte, whether calcareous\n   of horny, simple or compound. See Coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coral-rag', 'n.', 'Same as Corallian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coralwort', 'n.', 'A cruciferous herb of certain species of Dentaria; --\n   called also toothwort, tooth violet, or pepper root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coranach', 'n.', 'A lamentation for the dead; a dirge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corant', 'n.', 'Alt. of Coranto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coranto', 'n.', 'A sprightly but somewhat stately dance, now out of\n   fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corb', 'n.', 'A basket used in coal mines, etc. see Corf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corb', 'n.', 'An ornament in a building; a corbel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corban', 'n.', 'An offering of any kind, devoted to God and therefore not\n   to be appropriated to any other use; esp., an offering in fulfillment\n   of a vow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corban', 'n.', 'An alms basket; a vessel to receive gifts of charity; a\n   treasury of the church, where offerings are deposited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corbe', 'a.', 'Crooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corbell', 'n.', 'A sculptured basket of flowers; a corbel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corbell', 'n.', 'Small gabions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corbel', 'n.', 'A bracket supporting a superincumbent object, or receiving\n   the spring of an arch. Corbels were employed largely in Gothic\n   architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corbel', 'v. t.', 'To furnish with a corbel or corbels; to support by a\n   corbel; to make in the form of a corbel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corbel-table', 'n.', 'A horizontal row of corbels, with the panels or\n   filling between them; also, less properly used to include the\n   stringcourse on them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corbies', 'pl. ', 'of Corby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corbie', 'n.', 'Alt. of Corby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corby', 'n.', 'The raven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corby', 'n.', 'A raven, crow, or chough, used as a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corbiestep', 'n.', 'One of the steps in which a gable wall is often\n   finished in place of a continuous slope; -- also called crowstep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corchorus', 'n.', 'The common name of the Kerria Japonica or Japan\n   globeflower, a yellow-flowered, perennial, rosaceous plant, seen in\n   old-fashioned gardens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corcle', 'n.', 'Alt. of Corcule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corcule', 'n.', 'The heart of the seed; the embryo or germ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cord', 'n.', 'A string, or small rope, composed of several strands twisted\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cord', 'n.', 'A solid measure, equivalent to 128 cubic feet; a pile of\n   wood, or other coarse material, eight feet long, four feet high, and\n   four feet broad; -- originally measured with a cord or line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cord', 'n.', 'Fig.: Any moral influence by which persons are caught, held,\n   or drawn, as if by a cord; an enticement; as, the cords of the wicked;\n   the cords of sin; the cords of vanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cord', 'n.', 'Any structure having the appearance of a cord, esp. a tendon\n   or a nerve. See under Spermatic, Spinal, Umbilical, Vocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cord', 'n.', 'See Chord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corded', 'imp. & p. p.', 'of Cord');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cording', 'p. pr. & vb. n.', 'of Cord');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cord', 'v. t.', 'To bind with a cord; to fasten with cords; to connect with\n   cords; to ornament or finish with a cord or cords, as a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cord', 'v. t.', 'To arrange (wood, etc.) in a pile for measurement by the\n   cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordage', 'n.', 'Ropes or cords, collectively; hence, anything made of rope\n   or cord, as those parts of the rigging of a ship which consist of\n   ropes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordal', 'n.', 'Same as Cordelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordate', 'a.', 'Heart-shaped; as, a cordate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordately', 'adv.', 'In a cordate form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corded', 'a.', 'Bound or fastened with cords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corded', 'a.', 'Piled in a form for measurement by the cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corded', 'a.', 'Made of cords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corded', 'a.', 'Striped or ribbed with cords; as, cloth with a corded\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corded', 'a.', 'Bound about, or wound, with cords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordelier', 'n.', 'A Franciscan; -- so called in France from the girdle of\n   knotted cord worn by all Franciscans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordelier', 'n.', 'A member of a French political club of the time of the\n   first Revolution, of which Danton and Marat were members, and which met\n   in an old Cordelier convent in Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordeling', 'a.', 'Twisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordelle', 'n.', 'A twisted cord; a tassel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordial', 'a.', 'Proceeding from the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordial', 'a.', 'Hearty; sincere; warm; affectionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordial', 'a.', 'Tending to revive, cheer, or invigorate; giving strength\n   or spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordial', 'n.', 'Anything that comforts, gladdens, and exhilarates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordial', 'n.', 'Any invigorating and stimulating preparation; as, a\n   peppermint cordial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordial', 'n.', 'Aromatized and sweetened spirit, used as a beverage; a\n   liqueur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordialities', 'pl. ', 'of Cordiality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordiality', 'n.', 'Relation to the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordiality', 'n.', 'Sincere affection and kindness; warmth of regard;\n   heartiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordialize', 'v. t.', 'To make into a cordial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordialize', 'v. t.', 'To render cordial; to reconcile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordialize', 'v. i.', 'To grow cordial; to feel or express cordiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordially', 'adv.', 'In a cordial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordialness', 'n.', 'Cordiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordierite', 'n.', 'See Iolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordoform', 'a.', 'Heart-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordillera', 'n.', 'A mountain ridge or chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordiner', 'n.', 'A cordwainer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordon', 'n.', 'A cord or ribbon bestowed or borne as a badge of honor; a\n   broad ribbon, usually worn after the manner of a baldric, constituting\n   a mark of a very high grade in an honorary order. Cf. Grand cordon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordon', 'n.', 'The cord worn by a Franciscan friar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordon', 'n.', 'The coping of the scarp wall, which projects beyong the\n   face of the wall a few inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordon', 'n.', 'A line or series of sentinels, or of military posts,\n   inclosing or guarding any place or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordon', 'n.', 'A rich and ornamental lace or string, used to secure a\n   mantle in some costumes of state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordonnet', 'n.', 'Doubled and twisted thread, made of coarse silk, and\n   used for tassels, fringes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordovan', 'n.', 'Same as Cordwain. In England the name is applied to\n   leather made from horsehide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corduroy', 'n.', 'A sort of cotton velveteen, having the surface raised in\n   ridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corduroy', 'n.', 'Trousers or breeches of corduroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corduroy', 'v. t.', 'To form of logs laid side by side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordwain', 'n.', 'A term used in the Middle Ages for Spanish leather\n   (goatskin tanned and dressed); and hence, any leather handsomely\n   finished, colored, gilded, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cordwainer', 'n.', 'A worker in cordwain, or cordovan leather; a shoemaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'n.', 'A body of individuals; an assemblage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'n.', 'A miners underground working time or shift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'n.', 'A Hebrew dry measure; a cor or homer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'n.', 'The heart or inner part of a thing, as of a column, wall,\n   rope, of a boil, etc.; especially, the central part of fruit,\n   containing the kernels or seeds; as, the core of an apple or quince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'n.', 'The center or inner part, as of an open space; as, the core\n   of a square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'n.', 'The most important part of a thing; the essence; as, the core\n   of a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'n.', 'The prtion of a mold which shapes the interior of a cylinder,\n   tube, or other hollow casting, or which makes a hole in or through a\n   casting; a part of the mold, made separate from and inserted in it, for\n   shaping some part of the casting, the form of which is not determined\n   by that of the pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'n.', 'A disorder of sheep occasioned by worms in the liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'n.', 'The bony process which forms the central axis of the horns in\n   many animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cord', 'imp. & p. p.', 'of Core');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coring', 'p. pr. & vb. n.', 'of Core');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'v. t.', 'To take out the core or inward parts of; as, to core an\n   apple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Core', 'v. t.', 'To form by means of a core, as a hole in a casting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-regent', 'n.', 'A joint regent or ruler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-relation', 'n.', 'Corresponding relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-religionist', 'n.', 'One of the same religion with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coreopsis', 'n.', 'A genus of herbaceous composite plants, having the\n   achenes two-horned and remotely resembling some insect; tickseed. C.\n   tinctoria, of the Western plains, the commonest plant of the genus, has\n   been used in dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corer', 'n.', 'That which cores; an instrument for coring fruit; as, an\n   apple corer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-respondent', 'n.', 'One who is called upon to answer a summons or other\n   proceeding jointly with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corves', 'pl. ', 'of Corf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corf', 'n.', 'A basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corf', 'n.', 'A large basket used in carrying or hoisting coal or ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corf', 'n.', 'A wooden frame, sled, or low-wheeled wagon, to convey coal or\n   ore in the mines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corfiote', 'n.', 'Alt. of Corfute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corfute', 'n.', 'A native or inhabitant of Corfu, an island in the\n   Mediterranean Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coriaceous', 'a.', 'Consisting of or resembling, leather; leatherlike;\n   tough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coriaceous', 'a.', 'Stiff, like leather or parchment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coriander', 'n.', 'An umbelliferous plant, the Coriandrum sativum, the\n   fruit or seeds of which have a strong smell and a spicy taste, and in\n   medicine are considered as stomachic and carminative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coridine', 'n.', 'A colorless or yellowish oil, C10H15N, of a leathery\n   odor, occuring in coal tar, Dippels oil, tobacco smoke, etc., regarded\n   as an organic base, homologous with pyridine. Also, one of a series of\n   metameric compounds of which coridine is a type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corindon', 'n.', 'See Corrundum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corinne', 'n.', 'The common gazelle (Gazella dorcas). See Gazelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corinth', 'n.', 'A city of Greece, famed for its luxury and extravagance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corinth', 'n.', 'A small fruit; a currant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corinthiac', 'a.', 'Pertaining to Corinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corinthian', 'a.', 'Of or relating to Corinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corinthian', 'a.', 'Of or pertaining to the Corinthian order of\n   architecture, invented by the Greeks, but more commonly used by the\n   Romans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corinthian', 'a.', 'Debauched in character or practice; impure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corinthian', 'a.', 'Of or pertaining to an amateur sailor or yachtsman; as,\n   a corinthian race (one in which the contesting yachts must be manned by\n   amateurs.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corinthian', 'n.', 'A native or inhabitant of Corinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corinthian', 'n.', 'A gay, licentious person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corium', 'n.', 'Armor made of leather, particularly that used by the\n   Romans; used also by Enlish soldiers till the reign of Edward I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corium', 'n.', 'Same as Dermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corium', 'n.', 'The deep layer of mucous membranes beneath the epithelium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corival', 'n.', 'A rival; a corrival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corival', 'v. t.', 'To rival; to pretend to equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corivalry', 'n.', 'Alt. of Corivalship');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corivalship', 'n.', 'Joint rivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cork', 'n.', 'The outer layer of the bark of the cork tree (Quercus Suber);\n   of which stoppers for bottles and casks are made. See Cutose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cork', 'n.', 'A stopper for a bottle or cask, cut out of cork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cork', 'n.', 'A mass of tabular cells formed in any kind of bark, in\n   greater or less abundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corked', 'imp. & p. p.', 'of Cork');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corking', 'p. pr. & vb. n.', 'of Cork');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cork', 'v. t.', 'To stop with a cork, as a bottle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cork', 'v. t.', 'To furnish or fit with cork; to raise on cork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corkage', 'n.', 'The charge made by innkeepers for drawing the cork and\n   taking care of bottles of wine bought elsewhere by a guest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corked', 'a.', 'having acquired an unpleasant taste from the cork; as, a\n   bottle of wine is corked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cork fossil', '', 'A variety of amianthus which is very light, like cork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corkiness', 'n.', 'The quality of being corky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corking pin', '', 'A pin of a large size, formerly used attaching a womans\n   headdress to a cork mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corkscrew', 'n.', 'An instrument with a screw or a steel spiral for drawing\n   corks from bottles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corkscrew', 'v. t.', 'To press forward in a winding way; as, to corkscrew\n   ones way through a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corkwing', 'n.', 'A fish; the goldsinny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corky', 'a.', 'Consisting of, or like, cork; dry shriveled up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corky', 'a.', 'Tasting of cork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corm', 'n.', 'A solid bulb-shaped root, as of the crocus. See Bulb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corm', 'n.', 'Same as Cormus, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cormogeny', 'n.', 'The embryological history of groups or families of\n   individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cormophylogeny', 'n.', 'The phylogeny of groups or families of individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cormophytes', 'n. pl.', 'Alt. of Cormophyta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cormophyta', 'n. pl.', 'A term proposed by Endlicher to include all plants\n   with an axis containing vascular tissue and with foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cormorant', 'n.', 'Any species of Phalacrocorax, a genus of sea birds\n   having a sac under the beak; the shag. Cormorants devour fish\n   voraciously, and have become the emblem of gluttony. They are generally\n   black, and hence are called sea ravens, and coalgeese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cormorant', 'n.', 'A voracious eater; a glutton, or gluttonous servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cormoraut', 'a.', 'Ravenous; voracious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cormus', 'n.', 'See Corm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cormus', 'n.', 'A vegetable or animal made up of a number of individuals,\n   such as, for example, would be formed by a process of budding from a\n   parent stalk wherre the buds remain attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corn', 'n.', 'A thickening of the epidermis at some point, esp. on the\n   toes, by friction or pressure. It is usually painful and troublesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corn', 'n.', 'A single seed of certain plants, as wheat, rye, barley, and\n   maize; a grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corn', 'n.', 'The various farinaceous grains of the cereal grasses used for\n   food, as wheat, rye, barley, maize, oats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corn', 'n.', 'The plants which produce corn, when growing in the field; the\n   stalks and ears, or the stalks, ears, and seeds, after reaping and\n   before thrashing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corn', 'n.', 'A small, hard particle; a grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corned', 'imp. & p. p.', 'of Corn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corning', 'p. pr. & vb. n.', 'of Corn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corn', 'v. t.', 'To preserve and season with salt in grains; to sprinkle\n   with salt; to cure by salting; now, specifically, to salt slightly in\n   brine or otherwise; as, to corn beef; to corn a tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corn', 'v. t.', 'To form into small grains; to granulate; as, to corn\n   gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corn', 'v. t.', 'To feed with corn or (in Sctland) oats; as, to corn\n   horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corn', 'v. t.', 'To render intoxicated; as, ale strong enough to corn one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornage', 'n.', 'Anancient tenure of land, which obliged the tenant to give\n   notice of an invasion by blowing a horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornamute', 'n.', 'A cornemuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornbind', 'n.', 'A weed that binds stalks of corn, as Convolvulus\n   arvensis, Polygonum Convolvulus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corncob', 'n.', 'The cob or axis on which the kernels of Indian corn grow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corncrake', 'n.', 'A bird (Crex crex or C. pratensis) which frequents grain\n   fields; the European crake or land rail; -- called also corn bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corncrib', 'n.', 'A crib for storing corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corncutter', 'n.', 'A machine for cutting up stalks of corn for food of\n   cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corncutter', 'n.', 'An implement consisting of a long blade, attached to a\n   handle at nearly a right angle, used for cutting down the stalks of\n   Indian corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corndodger', 'n.', 'A cake made of the meal of Indian corn, wrapped in a\n   covering of husks or paper, and baked under the embers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corneas', 'pl. ', 'of Cornea');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornea', 'n.', 'The transparent part of the coat of the eyeball which\n   covers the iris and pupil and admits light to the interior. See Eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corneal', 'a.', 'Pertaining to the cornea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornel', 'n.', 'The cornelian cherry (Cornus Mas); a European shrub with\n   clusters of small, greenish flowers, followed by very acid but edible\n   drupes resembling cherries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornel', 'n.', 'Any species of the genus Cornus, as C. florida, the\n   flowering cornel; C. stolonifera, the osier cornel; C. Canadensis, the\n   dwarf cornel, or bunchberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornelian', 'n.', 'Same as Carnelian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornemuse', 'n.', 'A wind instrument nearly identical with the bagpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corneocalcareous', 'a.', 'Formed of a mixture of horny and calcareous\n   materials, as some shells and corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corneocalcareous', 'a.', 'Horny on one side and calcareous on the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corneouss', 'a.', 'Of a texture resembling horn; horny; hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corner', 'n.', 'The point where two converging lines meet; an angle, either\n   external or internal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corner', 'n.', 'The space in the angle between converging lines or walls\n   which meet in a point; as, the chimney corner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corner', 'n.', 'An edge or extremity; the part farthest from the center;\n   hence, any quarter or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corner', 'n.', 'A secret or secluded place; a remote or out of the way\n   place; a nook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corner', 'n.', 'Direction; quarter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corner', 'n.', 'The state of things produced by a combination of persons,\n   who buy up the whole or the available part of any stock or species of\n   property, which compels those who need such stock or property to buy of\n   them at their own price; as, a corner in a railway stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornered', 'imp. & p. p.', 'of Corner');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornering', 'p. pr. & vb. n.', 'of Corner');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corner', 'v. t.', 'To drive into a corner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corner', 'v. t.', 'To drive into a position of great difficulty or hopeless\n   embarrassment; as, to corner a person in argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corner', 'v. t.', 'To get command of (a stock, commodity, etc.); so as to\n   be able to put ones own price on it; as, to corner the shares of a\n   railroad stock; to corner petroleum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornercap', 'n.', 'The chief ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornered', 'p. a.', '1 Having corners or angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornered', 'p. a.', 'In a possition of great difficulty; brought to bay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornerwise', 'adv.', 'With the corner in front; diagonally; not square.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'An obsolete rude reed instrument (Ger. Zinken); of the oboe\n   family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'A brass instrument, with cupped mouthpiece, and furnished\n   with valves or pistons, now used in bands, and, in place of the\n   trumpet, in orchestras. See Cornet-a-piston.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'A certain organ stop or register.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'A cap of paper twisted at the end, used by retailers to\n   inclose small wares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'A troop of cavalry; -- so called from its being accompanied\n   by a cornet player.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'The standard of such a troop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'The lowest grade of commissioned officer in a British\n   cavalry troop, who carried the standard. The office was abolished in\n   1871.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'A headdress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'A square cap anciently worn as a mark of certain\n   professions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'A part of a womans headdress, in the 16th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet', 'n.', 'See Coronet, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornets-a-piston', 'pl. ', 'of Cornet-a-piston');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornet-a-piston', 'n.', 'A brass wind instrument, like the trumpet,\n   furnished with valves moved by small pistons or sliding rods; a\n   cornopean; a cornet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornetcy', 'n.', 'The commission or rank of a cornet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corneter', 'n.', 'One who blows a cornet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corneule', 'n.', 'One of the corneas of a compound eye in the\n   invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornfield', 'n.', 'A field where corn is or has been growing; -- in\n   England, a field of wheat, rye, barley, or oats; in America, a field of\n   Indian corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornfloor', 'n.', 'A thrashing floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornflower', 'n.', 'A conspicuous wild flower (Centaurea Cyanus); growing\n   in grainfields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornic', 'a.', 'Pertaining to, derived from, or resembling, the dogwood\n   (Cornus florida).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornice', 'n.', 'Any horizontal, molded or otherwise decorated projection\n   which crowns or finishes the part to which it is affixed; as, the\n   cornice of an order, pedestal, door, window, or house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corniced', 'a.', 'Having a cornice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornicle', 'n.', 'A little horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornicular', 'n.', 'A secretary or clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cor/niculate', 'a.', 'Horned; having horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cor/niculate', 'a.', 'Having processes resembling small horns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornicula', 'pl. ', 'of Corniculum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corniculum', 'n.', 'A small hornlike part or process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corniferous', 'a.', 'Of or pertaining to the lowest period of the Devonian\n   age. (See the Diagram, under Geology.) The Corniferous period has been\n   so called from the numerous seams of hornstone which characterize the\n   later part of the period, as developed in the State of New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornific', 'a.', 'Producing horns; forming horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornification', 'n.', 'Conversion into, or formation of, horn; a becoming\n   like horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornified', 'a.', 'Converted into horn; horny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corniform', 'a.', 'Having the shape of a horn; horn-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornigerous', 'a.', 'Horned; having horns; as, cornigerous animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornin', 'n.', 'A bitter principle obtained from dogwood (Cornus florida);\n   as a white crystalline substance; -- called also cornic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornin', 'n.', 'An extract from dogwood used as a febrifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corniplume', 'n.', 'A hornlike tuft of feathers on the head of some birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornish', 'a.', 'Of or pertaining to Cornwall, in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornish', 'n.', 'The dialect, or the people, of Cornwall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornist', 'n.', 'A performer on the cornet or horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornloft', 'n.', 'A loft for corn; a granary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornmuse', 'n.', 'A cornemuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corni', '-n/', 'di basseto (pl. ) of Corno di bassetto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corno di bassetto', '', 'A tenor clarinet; -- called also basset horn, and\n   sometimes confounded with the English horn, which is a tenor oboe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corni Inglesi', 'pl. ', 'of Corno Inglese');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corno Inglese', '', 'A reed instrument, related to the oboe, but deeper in\n   pitch; the English horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornopean', 'n.', 'An obsolete name for the cornet-a-piston.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornsheller', 'n.', 'A machine that separates the kernels of corn from the\n   cob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornshuck', 'n.', 'The husk covering an ear of Indian corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornstalk', 'n.', 'A stalk of Indian corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornstarch', 'n.', 'Starch made from Indian corn, esp. a fine white flour\n   used for puddings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornua', 'pl. ', 'of Cornu');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornu', 'n.', 'A horn, or anything shaped like or resembling a horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornua Ammonis', 'pl. ', 'of Cornu Ammonis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornu Ammonis', '', 'A fossil shell, curved like a rams horn; an obsolete\n   name for an ammonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornucopias', 'pl. ', 'of Cornucopia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornucopia', 'n.', 'The horn of plenty, from which fruits and flowers are\n   represented as issuing. It is an emblem of abundance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornucopia', 'n.', 'A genus of grasses bearing spikes of flowers resembling\n   the cornucopia in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornute', 'a.', 'Alt. of Cornuted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornuted', 'a.', 'Bearing horns; horned; horn-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornuted', 'a.', 'Cuckolded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornute', 'v. t.', 'To bestow horns upon; to make a cuckold of; to cuckold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornuto', 'n.', 'A man that wears the horns; a cuckold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cornutor', 'n.', 'A cuckold maker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corny', 'a.', 'Strong, stiff, or hard, like a horn; resembling horn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corny', 'a.', 'Producing corn or grain; furnished with grains of corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corny', 'a.', 'Containing corn; tasting well of malt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corny', 'a.', 'Tipsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corocore', 'n.', 'A kind of boat of various forms, used in the Indian\n   Archipelago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corody', 'n.', 'An allowance of meat, drink, or clothing due from an abbey\n   or other religious house for the sustenance of such of the kings\n   servants as he may designate to receive it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corol', 'n.', 'A corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corolla', 'n.', 'The inner envelope of a flower; the part which surrounds\n   the organs of fructification, consisting of one or more leaves, called\n   petals. It is usually distinguished from the calyx by the fineness of\n   its texture and the gayness of its colors. See the Note under Blossom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corollaceous', 'a.', 'Pertaining to, or resembling, a corolla; having the\n   form or texture of a corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corollaries', 'pl. ', 'of Corollary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corollary', 'n.', 'That which is given beyond what is actually due, as a\n   garland of flowers in addition to wages; surplus; something added or\n   superfluous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corollary', 'n.', 'Something which follows from the demonstration of a\n   proposition; an additional inference or deduction from a demonstrated\n   proposition; a consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corollate', 'a.', 'Alt. of Corollated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corollated', 'a.', 'Having a corolla or corollas; like a corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corollet', 'n.', 'A floret in an aggregate flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corollifloral', 'a.', 'Alt. of Corolliflorous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corolliflorous', 'a.', 'Having the stamens borne on the petals, and the\n   latter free from the calyx. Compare Calycifloral and Thalamifloral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corolline', 'a.', 'Of or pertaining to a corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coromandel', 'n.', 'The west coast, or a portion of the west coast, of the\n   Bay of Bengal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronae', 'pl. ', 'of Corona');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronas', 'pl. ', 'of Corona');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'A crown or garland bestowed among the Romans as a reward\n   for distinguished services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'The projecting part of a Classic cornice, the under side of\n   which is cut with a recess or channel so as to form a drip. See Illust.\n   of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'The upper surface of some part, as of a tooth or the skull;\n   a crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'The shelly skeleton of a sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'A peculiar luminous appearance, or aureola, which surrounds\n   the sun, and which is seen only when the sun is totally eclipsed by the\n   moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'An inner appendage to a petal or a corolla, often forming a\n   special cup, as in the daffodil and jonquil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'Any crownlike appendage at the top of an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'A circle, usually colored, seen in peculiar states of the\n   atmosphere around and close to a luminous body, as the sun or moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'A peculiar phase of the aurora borealis, formed by the\n   concentration or convergence of luminous beams around the point in the\n   heavens indicated by the direction of the dipping needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'A crown or circlet suspended from the roof or vaulting of\n   churches, to hold tapers lighted on solemn occasions. It is sometimes\n   formed of double or triple circlets, arranged pyramidically. Called\n   also corona lucis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corona', 'n.', 'A character [/] called the pause or hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronach', 'n.', 'See Coranach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronal', 'a.', 'Of or pertaining to a corona (in any of the senses).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronal', 'a.', 'Of or pertaining to a kings crown, or coronation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronal', 'a.', 'Of or pertaining to the top of the head or skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronal', 'a.', 'Of or pertaining to the shell of a sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronal', 'n.', 'A crown; wreath; garland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronal', 'n.', 'The frontal bone, over which the ancients wore their\n   coronae or garlands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronamen', 'n.', 'The upper margin of a hoof; a coronet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronary', 'a.', 'Of or pertaining to a crown; forming, or adapted to form,\n   a crown or garland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronary', 'a.', 'Resembling, or situated like, a crown or circlet; as, the\n   coronary arteries and veins of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronary', 'n.', 'A small bone in the foot of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronary', 'n.', 'Informal shortening of coronary thrombosis, also used\n   generally to mean heart attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronate', 'a.', 'Alt. of Coronated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronated', 'a.', 'Having or wearing a crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronated', 'a.', 'Having the coronal feathers lengthened or otherwise\n   distinguished; -- said of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronated', 'a.', 'Girt about the spire with a row of tubercles or spines;\n   -- said of spiral shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronated', 'a.', 'Having a crest or a crownlike appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronation', 'n.', 'The act or solemnity of crowning a sovereign; the act\n   of investing a prince with the insignia of royalty, on his succeeding\n   to the sovereignty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronation', 'n.', 'The pomp or assembly at a coronation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronel', 'n.', 'A colonel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronel', 'n.', 'The iron head of a tilting spear, divided into two, three,\n   or four blunt points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coroner', 'n.', 'An officer of the peace whose principal duty is to\n   inquire, with the help of a jury, into the cause of any violent, sudden\n   or mysterious death, or death in prison, usually on sight of the body\n   and at the place where the death occurred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronet', 'n.', 'An ornamental or honorary headdress, having the shape and\n   character of a crown; particularly, a crown worn as the mark of high\n   rank lower than sovereignty. The word is used by Shakespeare to denote\n   also a kingly crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronet', 'n.', 'The upper part of a horses hoof, where the horn\n   terminates in skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronet', 'n.', 'The iron head of a tilting spear; a coronel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coroneted', 'a.', 'Wearing, or entitled to wear, a coronet; of noble birth\n   or rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coroniform', 'a.', 'Having the form of a crown or coronet; resembling a\n   crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronilla', 'n.', 'A genus of plants related to the clover, having their\n   flowers arranged in little heads or tufts resembling coronets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronis', 'n.', 'In Greek grammar, a sign [] sometimes placed over a\n   contracted syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronis', 'n.', 'The curved line or flourish at the end of a book or\n   chapter; hence, the end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronoid', 'a.', 'Resembling the beak of a crow; as, the coronoid process\n   of the jaw, or of the ulna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coronule', 'n.', 'A coronet or little crown of a seed; the downy tuft on\n   seeds. See Pappus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coroun', 'v. & n.', 'Crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corozo', 'n.', 'Alt. of Corosso');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corosso', 'n.', 'The name in Central America for the seed of a true palm;\n   also, a commercial name for the true ivory nut. See Ivory nut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporace', 'n.', 'See Corporas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporal', 'n.', 'A noncommissioned officer, next below a sergeant. In the\n   United States army he is the lowest noncommissioned officer in a\n   company of infantry. He places and relieves sentinels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporal', 'a.', 'Belonging or relating to the body; bodily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporal', 'a.', 'Having a body or substance; not spiritual; material. In\n   this sense now usually written corporeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporal', 'a.', 'Alt. of Corporale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporale', 'a.', 'A fine linen cloth, on which the sacred elements are\n   consecrated in the eucharist, or with which they are covered; a\n   communion cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporalities', 'pl. ', 'of Corporality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporality', 'n.', 'The state of being or having a body; bodily existence;\n   corporeality; -- opposed to spirituality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporality', 'n.', 'A confraternity; a guild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporally', 'adv.', 'In or with the body; bodily; as, to be corporally\n   present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporalship', 'n.', 'A corporals office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporas', 'n.', 'The corporal, or communion cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporate', 'a.', 'Formed into a body by legal enactment; united in an\n   association, and endowed by law with the rights and liabilities of an\n   individual; incorporated; as, a corporate town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporate', 'a.', 'Belonging to a corporation or incorporated body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporate', 'a.', 'United; general; collectively one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporate', 'v. t.', 'To incorporate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporate', 'v. i.', 'To become incorporated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporately', 'adv.', 'In a corporate capacity; acting as a corporate body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporately', 'adv.', 'In, or as regarda, the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporation', 'n.', 'A body politic or corporate, formed and authorized by\n   law to act as a single person, and endowed by law with the capacity of\n   succession; a society having the capacity of transacting business as an\n   individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporator', 'n.', 'A member of a corporation, esp. one of the original\n   members.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporature', 'n.', 'The state of being embodied; bodily existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporeal', 'a.', 'Having a body; consisting of, or pertaining to, a\n   material body or substance; material; -- opposed to spiritual or\n   immaterial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporealism', 'n.', 'Materialism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporealist', 'n.', 'One who denies the reality of spiritual existences; a\n   materialist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporealities', 'pl. ', 'of Corporeality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporeality', 'n.', 'The state of being corporeal; corporeal existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporeally', 'adv.', 'In the body; in a bodily form or manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporealness', 'n.', 'Corporeality; corporeity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporeity', 'n.', 'The state of having a body; the state of being\n   corporeal; materiality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corporify', 'v. t.', 'To embody; to form into a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corposant', 'n.', 'St. Elmos fire. See under Saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corps', 'n. sing. & pl.', 'The human body, whether living or dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corps', 'n. sing. & pl.', 'A body of men; esp., an organized division of\n   the military establishment; as, the marine corps; the corps of\n   topographical engineers; specifically, an army corps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corps', 'n. sing. & pl.', 'A body or code of laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corps', 'n. sing. & pl.', 'The land with which a prebend or other\n   ecclesiastical office is endowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpse', 'n.', 'A human body in general, whether living or dead; --\n   sometimes contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpse', 'n.', 'The dead body of a human being; -- used also Fig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpulence', 'n.', 'Alt. of Corpulency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpulency', 'n.', 'Excessive fatness; fleshiness; obesity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpulency', 'n.', 'Thickness; density; compactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpulent', 'a.', 'Very fat; obese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpulent', 'a.', 'Solid; gross; opaque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpulently', 'adv.', 'In a corpulent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpora', 'pl. ', 'of Corpus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpus', 'n.', 'A body, living or dead; the corporeal substance of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpora callosa', 'pl. ', 'of Corpus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpora lutea', 'pl. ', 'of Corpus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpora striata', 'pl. ', 'of Corpus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpuscle', 'n.', 'A minute particle; an atom; a molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpuscle', 'n.', 'A protoplasmic animal cell; esp., such as float free,\n   like blood, lymph, and pus corpuscles; or such as are imbedded in an\n   intercellular matrix, like connective tissue and cartilage corpuscles.\n   See Blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpuscular', 'a.', 'Pertaining to, or composed of, corpuscles, or small\n   particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpuscularian', 'a.', 'Corpuscular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpuscularian', 'n.', 'An adherent of the corpuscular philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpuscule', 'n.', 'A corpuscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corpusculous', 'a.', 'Corpuscular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrade', 'v. t.', 'To gnaw into; to wear away; to fret; to consume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrade', 'v. t.', 'To erode, as the bed of a stream. See Corrosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corradial', 'a.', 'Radiating to or from the same point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corradiate', 'v. t.', 'To converge to one point or focus, as light or rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corradiation', 'n.', 'A conjunction or concentration of rays in one point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corral', 'n.', 'A pen for animals; esp., an inclosure made with wagons, by\n   emigrants in the vicinity of hostile Indians, as a place of security\n   for horses, cattle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corraled', 'imp. & p. p.', 'of Corral');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corralling', 'p. pr. & vb. n.', 'of Corral');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corral', 'v. t.', 'To surround and inclose; to coop up; to put into an\n   inclosed space; -- primarily used with reference to securing horses and\n   cattle in an inclosure of wagons while traversing the plains, but in\n   the Southwestern United States now colloquially applied to the\n   capturing, securing, or penning of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrasion', 'n.', 'The erosion of the bed of a stream by running water,\n   principally by attrition of the detritus carried along by the stream,\n   but also by the solvent action of the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrasive', 'a.', 'Corrosive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correct', 'a.', 'Set right, or made straight; hence, conformable to truth,\n   rectitude, or propriety, or to a just standard; not faulty or\n   imperfect; free from error; as, correct behavior; correct views.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrected', 'imp. & p. p.', 'of Correct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correcting', 'p. pr. & vb. n.', 'of Correct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correct', 'v. t.', 'To make right; to bring to the standard of truth,\n   justice, or propriety; to rectify; as, to correct manners or\n   principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correct', 'v. t.', 'To remove or retrench the faults or errors of; to\n   amend; to set right; as, to correct the proof (that is, to mark upon\n   the margin the changes to be made, or to make in the type the changes\n   so marked).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correct', 'v. t.', 'To bring back, or attempt to bring back, to propriety\n   in morals; to reprove or punish for faults or deviations from moral\n   rectitude; to chastise; to discipline; as, a child should be corrected\n   for lying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correct', 'v. t.', 'To counteract the qualities of one thing by those of\n   another; -- said of whatever is wrong or injurious; as, to correct the\n   acidity of the stomach by alkaline preparations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correctible', 'a.', 'Alt. of Correctable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correctable', 'a.', 'Capable of being corrected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correctify', 'v. t.', 'To correct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correction', 'n.', 'The act of correcting, or making that right which was\n   wrong; change for the better; amendment; rectification, as of an\n   erroneous statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correction', 'n.', 'The act of reproving or punishing, or that which is\n   intended to rectify or to cure faults; punishment; discipline;\n   chastisement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correction', 'n.', 'That which is substituted in the place of what is\n   wrong; an emendation; as, the corrections on a proof sheet should be\n   set in the margin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correction', 'n.', 'Abatement of noxious qualities; the counteraction of\n   what is inconvenient or hurtful in its effects; as, the correction of\n   acidity in the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correction', 'n.', 'An allowance made for inaccuracy in an instrument; as,\n   chronometer correction; compass correction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correctional', 'a.', 'Tending to, or intended for, correction; used for\n   correction; as, a correctional institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correctioner', 'n.', 'One who is, or who has been, in the house of\n   correction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrective', 'a.', 'Having the power to correct; tending to rectify; as,\n   corrective penalties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrective', 'a.', 'Qualifying; limiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrective', 'n.', 'That which has the power of correcting, altering, or\n   counteracting what is wrong or injurious; as, alkalies are correctives\n   of acids; penalties are correctives of immoral conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrective', 'n.', 'Limitation; restriction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correctly', 'adv.', 'In a correct manner; exactly; acurately; without fault\n   or error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correctness', 'n.', 'The state or quality of being correct; as, the\n   correctness of opinions or of manners; correctness of taste;\n   correctness in writing or speaking; the correctness of a text or copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrector', 'n.', 'One who, or that which, corrects; as, a corrector of\n   abuses; a corrector of the press; an alkali is a corrector of acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correctory', 'a.', 'Containing or making correction; corrective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correctress', 'n.', 'A woman who corrects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corregidor', 'n.', 'The chief magistrate of a Spanish town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correi', 'n.', 'A hollow in the side of a hill, where game usually lies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlatable', 'a.', 'Such as can be correlated; as, correlatable\n   phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlated', 'imp. & p. p.', 'of Correlate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlating', 'p. pr. & vb. n.', 'of Correlate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlate', 'v. i.', 'To have reciprocal or mutual relations; to be\n   mutually related.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlate', 'v. t.', 'To put in relation with each other; to connect\n   together by the disclosure of a mutual relation; as, to correlate\n   natural phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlate', 'n.', 'One who, or that which, stands in a reciprocal relation\n   to something else, as father to son; a correlative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlation', 'n.', 'Reciprocal relation; corresponding similarity or\n   parallelism of relation or law; capacity of being converted into, or of\n   giving place to, one another, under certain conditions; as, the\n   correlation of forces, or of zymotic diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlative', 'a.', 'Having or indicating a reciprocal relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlative', 'n.', 'One who, or that which, stands in a reciprocal\n   relation, or is correlated, to some other person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlative', 'n.', 'The antecedent of a pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlatively', 'adv.', 'In a correlative relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correlativeness', 'n.', 'Quality of being correlative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correligionist', 'n.', 'A co-religion/ist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correption', 'n.', 'Chiding; reproof; reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corresponded', 'imp. & p. p.', 'of Correspond');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corresponding', 'p. pr. & vb. n.', 'of Correspond');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspond', 'v. i.', 'To be like something else in the dimensions and\n   arrangement of its parts; -- followed by with or to; as, concurring\n   figures correspond with each other throughout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspond', 'v. i.', 'To be adapted; to be congruous; to suit; to agree;\n   to fit; to answer; -- followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspond', 'v. i.', 'To have intercourse or communion; especially, to\n   hold intercourse or to communicate by sending and receiving letters; --\n   followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondence', 'n.', 'Friendly intercourse; reciprocal exchange of\n   civilities; especially, intercourse between persons by means of\n   letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondence', 'n.', 'The letters which pass between correspondents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondence', 'n.', 'Mutual adaptation, relation, or agreement, of one\n   thing to another; agreement; congruity; fitness; relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondencies', 'pl. ', 'of Correspondency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondency', 'n.', 'Same as Correspondence, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondent', 'a.', 'Suitable; adapted; fit; corresponding; congruous;\n   conformable; in accord or agreement; obedient; willing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondent', 'n.', 'One with whom intercourse is carried on by letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondent', 'n.', 'One who communicates information, etc., by letter or\n   telegram to a newspaper or periodical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondent', 'n.', 'One who carries on commercial intercourse by letter\n   or telegram with a person or firm at a distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondently', 'adv.', 'In a a corresponding manner; conformably;\n   suitably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corresponding', 'a.', 'Answering; conformable; agreeing; suiting; as,\n   corresponding numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corresponding', 'a.', 'Carrying on intercourse by letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Correspondingly', 'adv.', 'In a corresponding manner; conformably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corresponsive', 'a.', 'Corresponding; conformable; adapted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corridor', 'n.', 'A gallery or passageway leading to several apartments of\n   a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corridor', 'n.', 'The covered way lying round the whole compass of the\n   fortifications of a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrie', 'n.', 'Same as Correi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrigenda', 'pl. ', 'of Corrigendum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrigendum', 'n.', 'A fault or error to be corrected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrigent', 'n.', 'A substance added to a medicine to mollify or modify its\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrigibility', 'n.', 'Quality of being corrigible; capability of being\n   corrected; corrigibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrigible', 'a.', 'Capable of being set right, amended, or reformed; as, a\n   corrigible fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrigible', 'a.', 'Submissive to correction; docile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrigible', 'a.', 'Deserving chastisement; punishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrigible', 'a.', 'Having power to correct; corrective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrigibleness', 'n.', 'The state or quality of being corrigible;\n   corrigibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrival', 'n.', 'A fellow rival; a competitor; a rival; also, a companion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrival', 'a.', 'Having rivaling claims; emulous; in rivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrival', 'v. i. & t.', 'To compete with; to rival.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrivalry', 'n.', 'Corivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrivalship', 'n.', 'Corivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrivate', 'v. t.', 'To cause to flow together, as water drawn from\n   several streams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrivation', 'n.', 'The flowing of different streams into one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroborant', 'a.', 'Strengthening; supporting; corroborating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroborant', 'n.', 'Anything which gives strength or support; a tonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroborated', 'imp. & p. p.', 'of Corroborate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroborating', 'p. pr. & vb. n.', 'of Corroborate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroborate', 'v. t.', 'To make strong, or to give additional strength to;\n   to strengthen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroborate', 'v. t.', 'To make more certain; to confirm; to establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroborate', 'a.', 'Corroborated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroboration', 'n.', 'The act of corroborating, strengthening, or\n   confirming; addition of strength; confirmation; as, the corroboration\n   of an argument, or of information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroboration', 'n.', 'That which corroborates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroborative', 'a.', 'Tending to strengthen of confirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroborative', 'n.', 'A medicine that strengthens; a corroborant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroboratory', 'a.', 'Tending to strengthen; corroborative; as,\n   corroboratory facts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroded', 'imp. & p. p.', 'of Corrode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroding', 'p. pr. & vb. n.', 'of Corrode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrode', 'v. t.', 'To eat away by degrees; to wear away or diminish by\n   gradually separating or destroying small particles of, as by action of\n   a strong acid or a caustic alkali.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrode', 'v. t.', 'To consume; to wear away; to prey upon; to impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrode', 'v. i.', 'To have corrosive action; to be subject to corrosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrodent', 'a.', 'Corrosive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrodent', 'n.', 'Anything that corrodes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrodiate', 'v. t.', 'To eat away by degrees; to corrode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrodibility', 'n.', 'The quality of being corrodible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrodible', 'a.', 'Capable of being corroded; corrosible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrosibility', 'n.', 'Corrodibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrosible', 'a.', 'Corrodible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrosibleness', 'n.', 'The quality or state of being corrosible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrosion', 'n.', 'The action or effect of corrosive agents, or the process\n   of corrosive change; as, the rusting of iron is a variety of corrosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrosive', 'a.', 'Eating away; having the power of gradually wearing,\n   changing, or destroying the texture or substance of a body; as, the\n   corrosive action of an acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrosive', 'a.', 'Having the quality of fretting or vexing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrosive', 'n.', 'That which has the quality of eating or wearing away\n   gradually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrosive', 'n.', 'That which has the power of fretting or irritating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corroval', 'n.', 'A dark brown substance of vegetable origin, allied to\n   curare, and used by the natives of New Granada as an arrow poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrovaline', 'n.', 'A poisonous alkaloid extracted from corroval, and\n   characterized by its immediate action in paralyzing the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrugant', 'a.', 'Having the power of contracting into wrinkles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrugate', 'a.', 'Wrinkled; crumpled; furrowed; contracted into ridges and\n   furrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrugated', 'imp. & p. p.', 'of Corrugate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrugating', 'p. pr. & vb. n.', 'of Corrugate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrugate', 'v. t.', 'To form or shape into wrinkles or folds, or alternate\n   ridges and grooves, as by drawing, contraction, pressure, bending, or\n   otherwise; to wrinkle; to purse up; as, to corrugate plates of iron; to\n   corrugate the forehead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrugation', 'n.', 'The act corrugating; contraction into wrinkles or\n   alternate ridges and grooves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrugator', 'n.', 'A muscle which contracts the skin of the forehead into\n   wrinkles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrugent', 'a.', 'Drawing together; contracting; -- said of the\n   corrugator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrump', 'v. t.', 'To corrupt. See Corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrumpable', 'a.', 'Corruptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupt', 'a.', 'Changed from a sound to a putrid state; spoiled; tainted;\n   vitiated; unsound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupt', 'a.', 'Changed from a state of uprightness, correctness, truth,\n   etc., to a worse state; vitiated; depraved; debased; perverted; as,\n   corrupt language; corrupt judges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupt', 'a.', 'Abounding in errors; not genuine or correct; as, the text\n   of the manuscript is corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupted', 'imp. & p. p.', 'of Corrupt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupting', 'p. pr. & vb. n.', 'of Corrupt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupt', 'v. t.', 'To change from a sound to a putrid or putrescent state;\n   to make putrid; to putrefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupt', 'v. t.', 'To change from good to bad; to vitiate; to deprave; to\n   pervert; to debase; to defile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupt', 'v. t.', 'To draw aside from the path of rectitude and duty; as,\n   to corrupt a judge by a bribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupt', 'v. t.', 'To debase or render impure by alterations or\n   innovations; to falsify; as, to corrupt language; to corrupt the sacred\n   text.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupt', 'v. t.', 'To waste, spoil, or consume; to make worthless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupt', 'v. i.', 'To become putrid or tainted; to putrefy; to rot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupt', 'v. i.', 'To become vitiated; to lose putity or goodness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corrupter', 'n.', 'One who corrupts; one who vitiates or taints; as, a\n   corrupter of morals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptful', 'a.', 'Tending to corrupt; full of corruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptibility', 'n.', 'The quality of being corruptible; the possibility\n   or liability of being corrupted; corruptibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptible', 'a.', 'Capable of being made corrupt; subject to decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptible', 'a.', 'Capable of being corrupted, or morally vitiated;\n   susceptible of depravation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptible', 'n.', 'That which may decay and perish; the human body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptingly', 'adv.', 'In a manner that corrupts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruption', 'n.', 'The act of corrupting or making putrid, or state of\n   being corrupt or putrid; decomposition or disorganization, in the\n   process of putrefaction; putrefaction; deterioration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruption', 'n.', 'The product of corruption; putrid matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruption', 'n.', 'The act of corrupting or of impairing integrity,\n   virtue, or moral principle; the state of being corrupted or debased;\n   loss of purity or integrity; depravity; wickedness; impurity; bribery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruption', 'n.', 'The act of changing, or of being changed, for the\n   worse; departure from what is pure, simple, or correct; as, a\n   corruption of style; corruption in language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptionist', 'n.', 'One who corrupts, or who upholds corruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptive', 'a.', 'Having the quality of taining or vitiating; tending to\n   produce corruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptless', 'a.', 'Not susceptible of corruption or decay; incorruptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptly', 'adv.', 'In a corrupt manner; by means of corruption or\n   corrupting influences; wrongfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptness', 'n.', 'The quality of being corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corruptress', 'n.', 'A woman who corrupts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corsac', 'n.', 'The corsak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corsage', 'n.', 'The waist or bodice of a ladys dress; as, a low corsage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corsage', 'n.', 'a flower or small arrangement of flowers worn by a person\n   as a personal ornament. Typically worn by women on special occasions\n   (as, at a ball or an anniversary celebration); a corsage may be worn\n   pinned to the chest, or tied to the wrist. It is usually larger or more\n   elaborate than a boutonniere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corsair', 'n.', 'A pirate; one who cruises about without authorization from\n   any government, to seize booty on sea or land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corsair', 'n.', 'A piratical vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corsak', 'n.', 'A small foxlike mammal (Cynalopex corsac); found in Central\n   Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corse', 'n.', 'A living body or its bulk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corse', 'n.', 'A corpse; the dead body of a human being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corselet', 'n.', 'Armor for the body, as, the body breastplate and\n   backpiece taken together; -- also, used for the entire suit of the day,\n   including breastplate and backpiece, tasset and headpiece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corselet', 'n.', 'The thorax of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corsepresent', 'n.', 'An offering made to the church at the interment of a\n   dead body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corset', 'n.', 'In the Middle Ages, a gown or basque of which the body was\n   close fitting, worn by both men and women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corset', 'n.', 'An article of dress inclosing the chest and waist worn\n   (chiefly by women) to support the body or to modify its shape; stays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corseted', 'imp. & p. p.', 'of Corset');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corseting', 'p. pr. & vb. n.', 'of Corset');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corset', 'v. t.', 'To inclose in corsets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corslet', 'n.', 'A corselet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corsned', 'n.', 'The morsel of execration; a species of ordeal consisting\n   in the eating of a piece of bread consecrated by imprecation. If the\n   suspected person ate it freely, he was pronounced innocent; but if it\n   stuck in his throat, it was considered as a proof of his guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cortege', 'n.', 'A train of attendants; a procession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cortes', 'n. pl.', 'The legislative assembly, composed of nobility, clergy,\n   and representatives of cities, which in Spain and in Portugal answers,\n   in some measure, to the Parliament of Great Britain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cortices', 'pl. ', 'of Cortex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cortex', 'n.', 'Bark, as of a tree; hence, an outer covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cortex', 'n.', 'Bark; rind; specifically, cinchona bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cortex', 'n.', 'The outer or superficial part of an organ; as, the cortex\n   or gray exterior substance of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cortical', 'a.', 'Belonging to, or consisting of, bark or rind; resembling\n   bark or rind; external; outer; superficial; as, the cortical substance\n   of the kidney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corticate', 'a.', 'Alt. of Corticated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corticated', 'a.', 'Having a special outer covering of a nature unlike the\n   interior part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corticifer', 'n.', 'One of the Gorgoniacea; -- so called because the fleshy\n   part surrounds a solid axis, like a bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corticiferous', 'a.', 'Producing bark or something that resembling that\n   resembles bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corticiferous', 'a.', 'Having a barklike c/nenchyms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corticiform', 'a.', 'Resembling, or having the form of, bark or rind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corticine', 'n.', 'A material for carpeting or floor covering, made of\n   ground cork and caoutchouc or India rubber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corticose', 'a.', 'Abounding in bark; resembling bark; barky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corticous', 'a.', 'Relating to, or resembling, bark; corticose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cortile', 'n.', 'An open internal courtyard inclosed by the walls of a\n   large dwelling house or other large and stately building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corundums', 'pl. ', 'of Corundum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corundum', 'n.', 'The earth alumina, as found native in a crystalline\n   state, including sapphire, which is the fine blue variety; the oriental\n   ruby, or red sapphire; the oriental amethyst, or purple sapphire; and\n   adamantine spar, the hair-brown variety. It is the hardest substance\n   found native, next to the diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coruscant', 'a.', 'Glittering in flashes; flashing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coruscate', 'v. i.', 'To glitter in flashes; to flash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coruscation', 'n.', 'A sudden flash or play of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coruscation', 'n.', 'A flash of intellectual brilliancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corve', 'n.', 'See Corf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corvee', 'n.', 'An obligation to perform certain services, as the repair of\n   roads, for the lord or sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corven', '', 'p. p. of Carve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corvet', 'n.', 'Alt. of Corvette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corvette', 'n.', 'A war vessel, ranking next below a frigate, and having\n   usually only one tier of guns; -- called in the United States navy a\n   sloop of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corvetto', 'n.', 'A curvet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corvine', 'a.', 'Of or pertaining to the crow; crowlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corvorant', 'n.', 'See Cormorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corybants', 'pl. ', 'of Corybant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corybantes', 'pl. ', 'of Corybant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corybant', 'n.', 'One of the priests of Cybele in Phrygia. The rites of the\n   Corybants were accompanied by wild music, dancing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corybantiasm', 'n.', 'A kind of frenzy in which the patient is tormented by\n   fantastic visions and want of sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corybantic', 'a.', 'Of, pertaining to, or resembling, the Corybantes or\n   their rites; frantic; frenzied; as, a corybantic dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corymb', 'n.', 'A flat-topped or convex cluster of flowers, each on its own\n   footstalk, and arising from different points of a common axis, the\n   outermost blossoms expanding first, as in the hawthorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corymb', 'n.', 'Any flattish flower cluster, whatever be the order of\n   blooming, or a similar shaped cluster of fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corymbed', 'a.', 'Corymbose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corymbiferous', 'a.', 'Bearing corymbs of flowers or fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corymbose', 'a.', 'Consisting of corymbs, or resembling them in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corymbosely', 'adv.', 'In corymbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coryphaenoid', 'a.', 'Belonging to, or like, the genus Coryphaena. See\n   Dolphin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coryphee', 'n.', 'A ballet dancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coryphene', 'n.', 'A fish of the genus Coryphaena. See Dolphin. (2)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corypheuses', 'pl. ', 'of Corypheus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coryphei', 'pl. ', 'of Corypheus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Corypheus', 'n.', 'The conductor, chief, or leader of the dramatic chorus;\n   hence, the chief or leader of a party or interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coryphodon', 'n.', 'A genus of extinct mammals from the eocene tertiary of\n   Europe and America. Its species varied in size between the tapir and\n   rhinoceros, and were allied to those animals, but had short,\n   plantigrade, five-toed feet, like the elephant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coryphodont', 'a.', 'Pertaining to, or resembling, the genus Coryphodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coryza', 'n.', 'Nasal catarrh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coscinomancy', 'n.', 'Divination by means of a suspended sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coscoroba', 'n.', 'A large, white, South American duck, of the genus\n   Cascoroba, resembling a swan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosecant', 'n.', 'The secant of the complement of an arc or angle. See\n   Illust. of Functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosen', 'v. t.', 'See Cozen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosenage', 'n.', 'See Cozenage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosening', 'n.', 'Anything done deceitfully, and which could not be\n   properly designated by any special name, whether belonging to contracts\n   or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosentient', 'a.', 'Perceiving together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosey', 'a.', 'See Cozy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosher', 'v. t.', 'To levy certain exactions or tribute upon; to lodge and\n   eat at the expense of. See Coshering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosher', 'v. t.', 'To treat with hospitality; to pet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosherer', 'n.', 'One who coshers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coshering', 'n.', 'A feudal prerogative of the lord of the soil entitling\n   him to lodging and food at his tenants house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosier', 'n.', 'A tailor who botches his work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosignificative', 'a.', 'Having the same signification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosignitary', 'a.', 'Signing some important public document with another or\n   with others; as, a treaty violated by one of the cosignitary powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosignitaries', 'pl. ', 'of Cosignitary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosignitary', 'n.', 'One who signs a treaty or public document along with\n   others or another; as, the cosignitaries of the treaty of Berlin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosily', 'adv.', 'See Cozily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosinage', 'n.', 'Collateral relationship or kindred by blood;\n   consanguinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosinage', 'n.', 'A writ to recover possession of an estate in lands, when\n   a stranger has entered, after the death of the grandfathers\n   grandfather, or other distant collateral relation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosine', 'n.', 'The sine of the complement of an arc or angle. See Illust.\n   of Functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmetic', 'a.', 'Alt. of Cosmetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmetical', 'a.', 'Imparting or improving beauty, particularly the beauty\n   of the complexion; as, a cosmetical preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmetic', 'n.', 'Any external application intended to beautify and improve\n   the complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmic', 'a.', 'Alt. of Cosmical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmical', 'a.', 'Pertaining to the universe, and having special reference\n   to universal law or order, or to the one grand harmonious system of\n   things; hence; harmonious; orderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmical', 'a.', 'Pertaining to the solar system as a whole, and not to the\n   earth alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmical', 'a.', 'Characteristic of the cosmos or universe; inconceivably\n   great; vast; as, cosmic speed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmical', 'a.', 'Rising or setting with the sun; -- the opposite of\n   acronycal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmically', 'adv.', 'With the sun at rising or setting; as, a star is said\n   to rise or set cosmically when it rises or sets with the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmically', 'adv.', 'Universally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmogonal', 'a.', 'Alt. of Cosmogonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmogonic', 'a.', 'Alt. of Cosmogonical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmogonical', 'a.', 'Belonging to cosmogony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmogonist', 'n.', 'One who treats of the origin of the universe; one\n   versed in cosmogony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmogonies', 'pl. ', 'of Cosmogony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmogony', 'n.', 'The creation of the world or universe; a theory or\n   account of such creation; as, the poetical cosmogony of Hesoid; the\n   cosmogonies of Thales, Anaxagoras, and Plato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmographer', 'n.', 'One who describes the world or universe, including\n   the heavens and the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmographic', 'a.', 'Alt. of Cosmographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmographical', 'a.', 'Of or pertaining to cosmography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmographically', 'adv.', 'In a cosmographic manner; in accordance with\n   cosmography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmographies', 'pl. ', 'of Cosmography');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmography', 'n.', 'A description of the world or of the universe; or the\n   science which teaches the constitution of the whole system of worlds,\n   or the figure, disposition, and relation of all its parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmolabe', 'n.', 'An instrument resembling the astrolabe, formerly used\n   for measuring the angles between heavenly bodies; -- called also\n   pantacosm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmolatry', 'n.', 'Worship paid to the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmoline', 'n.', 'A substance obtained from the residues of the\n   distillation of petroleum, essentially the same as vaseline, but of\n   somewhat stiffer consistency, and consisting of a mixture of the higher\n   paraffines; a kind of petroleum jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmological', 'a.', 'Of or pertaining to cosmology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmologist', 'n.', 'One who describes the universe; one skilled in\n   cosmology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmology', 'n.', 'The science of the world or universe; or a treatise\n   relating to the structure and parts of the system of creation, the\n   elements of bodies, the modifications of material things, the laws of\n   motion, and the order and course of nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmometry', 'n.', 'The art of measuring the world or the universe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmoplastic', 'a.', 'Pertaining to a plastic force as operative in the\n   formation of the world independently of God; world-forming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmopolitan', 'n.', 'Alt. of Cosmopolite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmopolite', 'n.', 'One who has no fixed residence, or who is at home in\n   every place; a citizen of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmopolitan', 'a.', 'Alt. of Cosmopolite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmopolite', 'a.', 'Having no fixed residence; at home in any place; free\n   from local attachments or prejudices; not provincial; liberal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmopolite', 'a.', 'Common everywhere; widely spread; found in all parts\n   of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmopolitanism', 'n.', 'The quality of being cosmopolitan; cosmopolitism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmopolite', 'a. & n.', 'See Cosmopolitan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmopolitical', 'a.', 'Having the character of a cosmopolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmopolitism', 'n.', 'The condition or character of a cosmopolite;\n   disregard of national or local peculiarities and prejudices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmorama', 'n.', 'An exhibition in which a series of views in various\n   parts of the world is seen reflected by mirrors through a series of\n   lenses, with such illumination, etc., as will make the views most\n   closely represent reality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmoramic', 'a.', 'Of or pertaining to a cosmorama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmos', 'n.', 'The universe or universality of created things; -- so\n   called from the order and harmony displayed in it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmos', 'n.', 'The theory or description of the universe, as a system\n   displaying order and harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmosphere', 'n.', 'An apparatus for showing the position of the earth, at\n   any given time, with respect to the fixed stars. It consist of a hollow\n   glass globe, on which are depicted the stars and constellations, and\n   within which is a terrestrial globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmotheism', 'n.', 'Same as Pantheism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosmothetic', 'a.', 'Assuming or positing the actual existence or reality\n   of the physical or external world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosovereign', 'n.', 'A joint sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coss', 'n.', 'A Hindoo measure of distance, varying from one and a half to\n   two English miles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coss', 'n.', 'A thing (only in phrase below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cossack', 'n.', 'One of a warlike, pastoral people, skillful as horsemen,\n   inhabiting different parts of the Russian empire and furnishing\n   valuable contingents of irregular cavalry to its armies, those of\n   Little Russia and those of the Don forming the principal divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cossas', 'n.', 'Plain India muslin, of various qualities and widths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosset', 'n.', 'A lamb reared without the aid of the dam. Hence: A pet, in\n   general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosset', 'v. t.', 'To treat as a pet; to fondle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cossic', 'a.', 'Alt. of Cossical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cossical', 'a.', 'Of or relating to algebra; as, cossic numbers, or the\n   cossic art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cost', 'n.', 'A rib; a side; a region or coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cost', 'n.', 'See Cottise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cost', 'imp. & p. p.', 'of Cost');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costing', 'p. pr. & vb. n.', 'of Cost');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cost', 'v. t.', 'To require to be given, expended, or laid out therefor, as\n   in barter, purchase, acquisition, etc.; to cause the cost, expenditure,\n   relinquishment, or loss of; as, the ticket cost a dollar; the effort\n   cost his life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cost', 'v. t.', 'To require to be borne or suffered; to cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cost', 'v. t.', 'The amount paid, charged, or engaged to be paid, for\n   anything bought or taken in barter; charge; expense; hence, whatever,\n   as labor, self-denial, suffering, etc., is requisite to secure benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cost', 'v. t.', 'Loss of any kind; detriment; pain; suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cost', 'v. t.', 'Expenses incurred in litigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costa', 'n.', 'A rib of an animal or a human being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costa', 'n.', 'A rib or vein of a leaf, especially the midrib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costa', 'n.', 'The anterior rib in the wing of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costa', 'n.', 'One of the riblike longitudinal ridges on the exterior of\n   many corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costage', 'n.', 'Expense; cost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costal', 'a.', 'Pertaining to the ribs or the sides of the body; as, costal\n   nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costal', 'a.', 'Relating to a costa, or rib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costal-nerved', 'a.', 'Having the nerves spring from the midrib.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costard', 'n.', 'An apple, large and round like the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costard', 'n.', 'The head; -- used contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costardmonger', 'n.', 'A costermonger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costate', 'a.', 'Alt. of Costated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costated', 'a.', 'Having ribs, or the appearance of ribs; (Bot.) having one\n   or more longitudinal ribs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costean', 'v. i.', 'To search after lodes. See Costeaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costeaning', 'n.', 'The process by which miners seek to discover metallic\n   lodes. It consist in sinking small pits through the superficial\n   deposits to the solid rock, and then driving from one pit to another\n   across the direction of the vein, in such manner as to cross all the\n   veins between the two pits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costellate', 'a.', 'Finely ribbed or costated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coster', 'n.', 'One who hawks about fruit, green vegetables, fish, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costermonger', 'n.', 'An apple seller; a hawker of, or dealer in, any kind\n   of fruit or vegetables; a fruiterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costiferous', 'a.', 'Rib-bearing, as the dorsal vertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costive', 'a.', 'Retaining fecal matter in the bowels; having too slow a\n   motion of the bowels; constipated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costive', 'a.', 'Reserved; formal; close; cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costive', 'a.', 'Dry and hard; impermeable; unyielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costively', 'adv.', 'In a costive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costiveness', 'n.', 'An unnatural retention of the fecal matter of the\n   bowels; constipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costiveness', 'n.', 'Inability to express ones self; stiffness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costless', 'a.', 'Costing nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costlewe', 'a.', 'Costly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costliness', 'n.', 'The quality of being costy; expensiveness;\n   sumptuousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costly', 'a.', 'Of great cost; expensive; dear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costly', 'a.', 'Gorgeous; sumptuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costmary', 'n.', 'A garden plant (Chrysanthemum Balsamita) having a strong\n   balsamic smell, and nearly allied to tansy. It is used as a pot herb\n   and salad plant and in flavoring ale and beer. Called also alecost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costotome', 'n.', 'An instrument (chisel or shears) to cut the ribs and\n   open the thoracic cavity, in post-mortem examinations and dissections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costrel', 'n.', 'A bottle of leather, earthenware, or wood, having ears by\n   which it was suspended at the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costume', 'n.', 'Dress in general; esp., the distinctive style of dress of\n   a people, class, or period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costume', 'n.', 'Such an arrangement of accessories, as in a picture,\n   statue, poem, or play, as is appropriate to the time, place, or other\n   circumstances represented or described.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costume', 'n.', 'A character dress, used at fancy balls or for dramatic\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Costumer', 'n.', 'One who makes or deals in costumes, as for theaters,\n   fancy balls, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-sufferer', 'n.', 'One who suffers with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosupreme', 'n.', 'A partaker of supremacy; one jointly supreme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosureties', 'pl. ', 'of Cosurety');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosurety', 'n.', 'One who is surety with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cosy', 'a.', 'See Cozy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cot', 'n.', 'A small house; a cottage or hut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cot', 'n.', 'A pen, coop, or like shelter for small domestic animals, as\n   for sheep or pigeons; a cote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cot', 'n.', 'A cover or sheath; as, a roller cot (the clothing of a drawing\n   roller in a spinning frame); a cot for a sore finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cot', 'n.', 'A small, rudely-formed boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cot', 'n.', 'A sleeping place of limited size; a little bed; a cradle; a\n   piece of canvas extended by a frame, used as a bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotangent', 'n.', 'The tangent of the complement of an arc or angle. See\n   Illust. of Functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotarnine', 'n.', 'A white, crystalline substance, C12H13NO3, obtained as a\n   product of the decomposition of narcotine. It has weak basic\n   properties, and is usually regarded as an alkaloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cote', 'n.', 'A cottage or hut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cote', 'n.', 'A shed, shelter, or inclosure for small domestic animals, as\n   for sheep or doves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cote', 'v. t.', 'To go side by side with; hence, to pass by; to outrun and\n   get before; as, a dog cotes a hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cote', 'v. t.', 'To quote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotemporaneous', 'a.', 'Living or being at the same time; contemporaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotemporary', 'a.', 'Living or being at the same time; contemporary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotemporaries', 'pl. ', 'of Cotemporary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotemporary', 'n.', 'One who lives at the same time with another; a\n   contemporary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotenant', 'n.', 'A tenant in common, or a joint tenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coterie', 'n.', 'A set or circle of persons who meet familiarly, as for\n   social, literary, or other purposes; a clique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coterminous', 'a.', 'Bordering; conterminous; -- followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotgare', 'n.', 'Refuse wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cothurn', 'n.', 'A buskin anciently used by tragic actors on the stage;\n   hence, tragedy in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cothurnate', 'a.', 'Alt. of Cothurnated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cothurnated', 'a.', 'Wearing a cothurn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cothurnated', 'a.', 'Relating to tragedy; solemn; grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cothurnus', 'n.', 'Same as Cothurn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coticular', 'a.', 'Pertaining to whetstones; like or suitable for\n   whetstones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotidal', 'a.', 'Marking an equality in the tides; having high tide at the\n   same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotillon', 'n.', 'Alt. of Cotillion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotillion', 'n.', 'A brisk dance, performed by eight persons; a quadrille.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotillion', 'n.', 'A tune which regulates the dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotillion', 'n.', 'A kind of woolen material for womens skirts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotillion', 'n.', 'A formal ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotinga', 'n.', 'A bird of the family Cotingidae, including numerous\n   bright-colored South American species; -- called also chatterers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotise', 'n.', 'See Cottise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotised', 'a.', 'See Cottised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotland', 'n.', 'Land appendant to a cot or cottage, or held by a cottager\n   or cotter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotquean', 'n.', 'A man who busies himself with affairs which properly\n   belong to women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotquean', 'n.', 'A she-cuckold; a cucquean; a henhussy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotqueanity', 'n.', 'The condition, character, or conduct of a cotquean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotrustee', 'n.', 'A joint trustee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotswold', 'n.', 'An open country abounding in sheepcotes, as in the\n   Cotswold hills, in Gloucestershire, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottage', 'n.', 'A small house; a cot; a hut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottaged', 'a.', 'Set or covered with cottages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottagely', 'a.', 'Cottagelike; suitable for a cottage; rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottager', 'n.', 'One who lives in a cottage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottager', 'n.', 'One who lives on the common, without paying any rent, or\n   having land of his own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotter', 'n.', 'Alt. of Cottar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottar', 'n.', 'A cottager; a cottier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotter', 'n.', 'A piece of wood or metal, commonly wedge-shaped, used for\n   fastening together parts of a machine or structure. It is driven into\n   an opening through one or all of the parts. [See Illust.] In the United\n   States a cotter is commonly called a key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotter', 'n.', 'A toggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotter', 'v. t.', 'To fasten with a cotter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottier', 'n.', 'In Great Britain and Ireland, a person who hires a small\n   cottage, with or without a plot of land. Cottiers commonly aid in the\n   work of the landlords farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottise', 'n.', 'A diminutive of the bendlet, containing one half its area\n   or one quarter the area of the bend. When a single cottise is used\n   alone it is often called a cost. See also Couple-close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottised', 'a.', 'Set between two cottises, -- said of a bend; or between\n   two barrulets, -- said of a bar or fess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottoid', 'a.', 'Like a fish of the genus Cottus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottoid', 'n.', 'A fish belonging to, or resembling, the genus Cottus. See\n   Sculpin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottolene', 'n.', 'A product from cotton-seed, used as lard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotton', 'n.', 'A soft, downy substance, resembling fine wool, consisting\n   of the unicellular twisted hairs which grow on the seeds of the cotton\n   plant. Long-staple cotton has a fiber sometimes almost two inches long;\n   short-staple, from two thirds of an inch to an inch and a half.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotton', 'n.', 'The cotton plant. See Cotten plant, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotton', 'n.', 'Cloth made of cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotton', 'v. i.', 'To rise with a regular nap, as cloth does.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotton', 'v. i.', 'To go on prosperously; to succeed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotton', 'v. i.', 'To unite; to agree; to make friends; -- usually followed\n   by with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotton', 'v. i.', 'To take a liking to; to stick to one as cotton; -- used\n   with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottonade', 'n.', 'A somewhat stout and thick fabric of cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottonary', 'a.', 'Relating to, or composed of, cotton; cottony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottonous', 'a.', 'Resembling cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottontail', 'n.', 'The American wood rabbit (Lepus sylvaticus); -- also\n   called Molly cottontail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottonweed', 'n.', 'See Cudweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottonwood', 'n.', 'An American tree of the genus Populus or poplar, having\n   the seeds covered with abundant cottonlike hairs; esp., the P.\n   monilifera and P. angustifolia of the Western United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottony', 'a.', 'Covered with hairs or pubescence, like cotton; downy;\n   nappy; woolly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottony', 'a.', 'Of or pertaining to cotton; resembling cotton in\n   appearance or character; soft, like cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cottrel', 'n.', 'A trammel, or hook to support a pot over a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyla', 'n.', 'Alt. of Cotyle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyle', 'n.', 'A cuplike cavity or organ. Same as Acetabulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyledon', 'n.', 'One of the patches of villi found in some forms of\n   placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyledon', 'n.', 'A leaf borne by the caulicle or radicle of an embryo; a\n   seed leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyledonal', 'a.', 'Of, pertaining to, or resembling, a cotyledon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyledonary', 'a.', 'Having a cotyledon; tufted; as, the cotyledonary\n   placenta of the cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyledonous', 'a.', 'Of or pertaining to a cotyledon or cotyledons; having\n   a seed lobe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyliform', 'a.', 'Shaped like a cotyle or a cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyligerous', 'a.', 'Having cotyles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyloid', 'a.', 'Shaped like a cup; as, the cotyloid cavity, which\n   receives the head of the thigh bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cotyloid', 'a.', 'Pertaining to a cotyloid cavity; as, the cotyloid\n   ligament, or notch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coucal', 'n.', 'A large, Old World, ground cuckoo of the genus Centropus,\n   of several species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couched', 'imp. & p. p.', 'of Couch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couching', 'p. pr. & vb. n.', 'of Couch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'To lay upon a bed or other resting place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'To arrange or dispose as in a bed; -- sometimes followed\n   by the reflexive pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'To lay or deposit in a bed or layer; to bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'To transfer (as sheets of partly dried pulp) from the\n   wire cloth mold to a felt blanket, for further drying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'To conceal; to include or involve darkly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'To arrange; to place; to inlay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'To put into some form of language; to express; to phrase;\n   -- used with in and under.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'To treat by pushing down or displacing the opaque lens\n   with a needle; as, to couch a cataract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. i.', 'To lie down or recline, as on a bed or other place of\n   rest; to repose; to lie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. i.', 'To lie down for concealment; to hide; to be concealed; to\n   be included or involved darkly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. i.', 'To bend the body, as in reverence, pain, labor, etc.; to\n   stoop; to crouch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'A bed or place for repose or sleep; particularly, in the\n   United States, a lounge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'Any place for repose, as the lair of a beast, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'A mass of steeped barley spread upon a floor to\n   germinate, in malting; or the floor occupied by the barley; as, couch\n   of malt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch', 'v. t.', 'A preliminary layer, as of color, size, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couchancy', 'n.', 'State of lying down for repose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couchant', 'v. t.', 'Lying down with head erect; squatting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couchant', 'v. t.', 'Lying down with the head raised, which distinguishes\n   the posture of couchant from that of dormant, or sleeping; -- said of a\n   lion or other beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couche', 'v. t.', 'Not erect; inclined; -- said of anything that is usually\n   erect, as an escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couche', 'v. t.', 'Lying on its side; thus, a chevron couche is one which\n   emerges from one side of the escutcheon and has its apex on the\n   opposite side, or at the fess point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couched', 'a.', 'Same as Couch/.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couchee', 'v. t.', 'A reception held at the time of going to bed, as by a\n   sovereign or great prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coucher', 'n.', 'One who couches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coucher', 'n.', 'One who couches paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coucher', 'n.', 'A factor or agent resident in a country for traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coucher', 'n.', 'The book in which a corporation or other body registers\n   its particular acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couch grass', '', 'See Quitch grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couching', 'n.', 'The operation of putting down or displacing the opaque\n   lens in cataract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couching', 'n.', 'Embroidering by laying the materials upon the surface of\n   the foundation, instead of drawing them through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couchless', 'a.', 'Having no couch or bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coudee', 'n.', 'A measure of length; the distance from the elbow to the end\n   of the middle finger; a cubit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cougar', 'n.', 'An American feline quadruped (Felis concolor); resembling\n   the African panther in size and habits. Its color is tawny, without\n   spots; hence writers often called it the American lion. Called also\n   puma, panther, mountain lion, and catamount. See Puma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coughed', 'imp. & p. p.', 'of Cough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coughing', 'p. pr. & vb. n.', 'of Cough');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cough', 'v. i.', 'To expel air, or obstructing or irritating matter, from\n   the lungs or air passages, in a noisy and violent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cough', 'v. t.', 'To expel from the lungs or air passages by coughing; --\n   followed by up; as, to cough up phlegm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cough', 'v. t.', 'To bring to a specified state by coughing; as, he coughed\n   himself hoarse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cough', 'v. i.', 'A sudden, noisy, and violent expulsion of air from the\n   chest, caused by irritation in the air passages, or by the reflex\n   action of nervous or gastric disorder, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cough', 'v. i.', 'The more or less frequent repetition of coughing,\n   constituting a symptom of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cougher', 'n.', 'One who coughs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couhage', 'n.', 'See Cowhage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Could', 'imp.', 'Was, should be, or would be, able, capable, or\n   susceptible. Used as an auxiliary, in the past tense or in the\n   conditional present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coulee', 'n.', 'A stream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coulee', 'n.', 'a stream of lava. Also, in the Western United States, the\n   bed of a stream, even if dry, when deep and having inclined sides;\n   distinguished from a caon, which has precipitous sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coulisse', 'n.', 'A piece of timber having a groove in which something\n   glides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coulisse', 'n.', 'One of the side scenes of the stage in a theater, or the\n   space included between the side scenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couloir', 'n.', 'A deep gorge; a gully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couloir', 'n.', 'A dredging machine for excavating canals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coulomb', 'n.', 'The standard unit of quantity in electrical measurements.\n   It is the quantity of electricity conveyed in one second by the current\n   produced by an electro-motive force of one volt acting in a circuit\n   having a resistance of one ohm, or the quantity transferred by one\n   ampere in one second. Formerly called weber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coulter', 'n.', 'Same as Colter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coulterneb', 'n.', 'The puffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coumaric', 'a.', 'Relating to, derived from, or like, the Dipterix odorata,\n   a tree of Guiana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coumarin', 'n.', 'The concrete essence of the tonka bean, the fruit of\n   Dipterix (formerly Coumarouna) odorata and consisting essentially of\n   coumarin proper, which is a white crystalline substance, C9H6O2, of\n   vanilla-like odor, regarded as an anhydride of coumaric acid, and used\n   in flavoring. Coumarin in also made artificially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Council', 'n.', 'An assembly of men summoned or convened for consultation,\n   deliberation, or advice; as, a council of physicians for consultation\n   in a critical case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Council', 'n.', 'A body of man elected or appointed to constitute an\n   advisory or a legislative assembly; as, a governors council; a city\n   council.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Council', 'n.', 'Act of deliberating; deliberation; consultation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Councilist', 'n.', 'One who belong to a council; one who gives an opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Councilmen', 'pl. ', 'of Councilman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Councilman', 'n.', 'A member of a council, especially of the common council\n   of a city; a councilor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Councilor', 'n.', 'A member of a council.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-une', 'v. t.', 'To combine or unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-unite', 'v. t.', 'To unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Co-unite', 'a.', 'United closely with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counsel', 'n.', 'Interchange of opinions; mutual advising; consultation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counsel', 'n.', 'Examination of consequences; exercise of deliberate\n   judgment; prudence.');
INSERT INTO words (word, word_type, description) VALUES  (    'Counsel',    'n.',    'Result of consultation; advice; instruction.');
INSERT INTO words (word, word_type, description) VALUES  (    'Counsel',    'n.',    'Deliberate purpose; design; intent; scheme; plan.');
INSERT INTO words (word, word_type, description) VALUES  (    'Counsel',    'n.',    'A secret opinion or purpose; a private matter.');
