INSERT INTO words (word, word_type, description) VALUES  (    'Counsel',    'n.',    'One who gives advice, especially in legal matters; one\n   professionally engaged in the trial or management of a cause in court;\n   also, collectively, the legal advocates united in the management of a\n   case; as, the defendant has able counsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counseled', 'imp. & p. p.', 'of Counsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counselled', '', 'of Counsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counseling', 'p. pr. & vb. n.', 'of Counsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counselling', '', 'of Counsel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counsel', 'v. t.', 'To give advice to; to advice, admonish, or instruct, as\n   a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counsel', 'v. t.', 'To advise or recommend, as an act or course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counselable', 'a.', 'Willing to receive counsel or follow advice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counselable', 'a.', 'Suitable to be advised; advisable, wise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counselor', 'n.', 'One who counsels; an adviser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counselor', 'n.', 'A member of council; one appointed to advise a sovereign\n   or chief magistrate. [See under Consilor.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counselor', 'n.', 'One whose profession is to give advice in law, and\n   manage causes for clients in court; a barrister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counselorship', 'n.', 'The function and rank or office of a counselor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counted', 'imp. & p. p.', 'of Count');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counting', 'p. pr. & vb. n.', 'of Count');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'v. t.', 'To tell or name one by one, or by groups, for the purpose\n   of ascertaining the whole number of units in a collection; to number;\n   to enumerate; to compute; to reckon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'v. t.', 'To place to an account; to ascribe or impute; to consider\n   or esteem as belonging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'v. t.', 'To esteem; to account; to reckon; to think, judge, or\n   consider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'v. i.', 'To number or be counted; to possess value or carry\n   weight; hence, to increase or add to the strength or influence of some\n   party or interest; as, every vote counts; accidents count for nothing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'v. i.', 'To reckon; to rely; to depend; -- with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'v. i.', 'To take account or note; -- with');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'v. i.', 'To plead orally; to argue a matter in court; to recite a\n   count.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'v. t.', 'The act of numbering; reckoning; also, the number\n   ascertained by counting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'v. t.', 'An object of interest or account; value; estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'v. t.', 'A formal statement of the plaintiffs case in court; in a\n   more technical and correct sense, a particular allegation or charge in\n   a declaration or indictment, separately setting forth the cause of\n   action or prosecution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count', 'n.', 'A nobleman on the continent of Europe, equal in rank to an\n   English earl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countable', 'a.', 'Capable of being numbered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counttenance', 'n.', 'Appearance or expression of the face; look; aspect;\n   mien.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counttenance', 'n.', 'The face; the features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counttenance', 'n.', 'Approving or encouraging aspect of face; hence,\n   favor, good will, support; aid; encouragement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counttenance', 'n.', 'Superficial appearance; show; pretense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countenanced', 'imp. & p. p.', 'of Countenance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countenancing', 'p. pr. & vb. n.', 'of Countenance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countenance', 'v. t.', 'To encourage; to favor; to approve; to aid; to\n   abet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countenance', 'v. t.', 'To make a show of; to pretend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countenancer', 'n.', 'One who countenances, favors, or supports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'adv.', 'A prefix meaning contrary, opposite, in opposition; as,\n   counteract, counterbalance, countercheck. See Counter, adv. & a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'v. t.', 'One who counts, or reckons up; a calculator; a\n   reckoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'v. t.', 'A piece of metal, ivory, wood, or bone, used in\n   reckoning, in keeping account of games, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'v. t.', 'Money; coin; -- used in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'v. t.', 'A prison; either of two prisons formerly in London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'v. t.', 'A telltale; a contrivance attached to an engine,\n   printing press, or other machine, for the purpose of counting the\n   revolutions or the pulsations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'v. t.', 'A table or board on which money is counted and over\n   which business is transacted; a long, narrow table or bench, on which\n   goods are laid for examination by purchasers, or on which they are\n   weighed or measured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'adv.', 'Contrary; in opposition; in an opposite direction;\n   contrariwise; -- used chiefly with run or go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'adv.', 'In the wrong way; contrary to the right course; as, a\n   hound that runs counter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'adv.', 'At or against the front or face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'a.', 'Contrary; opposite; contrasted; opposed; adverse;\n   antagonistic; as, a counter current; a counter revolution; a counter\n   poison; a counter agent; counter fugue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'adv.', 'The after part of a vessels body, from the water line\n   to the stern, -- below and somewhat forward of the stern proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'adv.', 'Same as Contra. Formerly used to designate any under\n   part which served for contrast to a principal part, but now used as\n   equivalent to counter tenor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'adv.', 'The breast, or that part of a horse between the\n   shoulders and under the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'adv.', 'The back leather or heel part of a boot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'n.', 'An encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter', 'v. i.', 'To return a blow while receiving one, as in boxing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counteracted', 'imp. & p. p.', 'of Counteract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counteracting', 'p. pr. & vb. n.', 'of Counteract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counteract', 'v. t.', 'To act in opposition to; to hinder, defeat, or\n   frustrate, by contrary agency or influence; as, to counteract the\n   effect of medicines; to counteract good advice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counteraction', 'n.', 'Action in opposition; hindrance resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counteractive', 'a.', 'Tending to counteract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counteractive', 'n.', 'One who, or that which, counteracts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counteractibely', 'adv.', 'By counteraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbalanced', 'imp. & p. p.', 'of Counterbalance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbalancing', 'p. pr. & vb. n.', 'of Counterbalance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbalance', 'v. t.', 'To oppose with an equal weight or power; to\n   counteract the power or effect of; to countervail; to equiponderate; to\n   balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbalance', 'n.', 'A weight, power, or agency, acting against or\n   balancing another');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbalance', 'n.', 'A mass of metal in one side of a driving wheel or\n   fly wheel, to balance the weight of a crank pin, etc., on the opposite\n   side of the wheel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbalance', 'n.', 'A counterpoise to balance the weight of anything,\n   as of a drawbridge or a scale beam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbore', 'n.', 'A flat-bottomed cylindrical enlargement of the mouth\n   of a hole, usually of slight depth, as for receiving a cylindrical\n   screw head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbore', 'n.', 'A kind of pin drill with the cutting edge or edges\n   normal to the axis; -- used for enlarging a hole, or for forming a\n   flat-bottomed recess at its mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbore', 'v. t.', 'To form a counterbore in, by boring, turning, or\n   drilling; to enlarge, as a hole, by means of a counterbore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter brace', '', 'The brace of the fore-topsail on the leeward side of a\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter brace', '', 'A brace, in a framed structure, which resists a strain\n   of a character opposite to that which a main brace is designed to\n   receive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbrace', 'v. t.', 'To brace in opposite directions; as, to\n   counterbrace the yards, i. e., to brace the head yards one way and the\n   after yards another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbrace', 'v. t.', 'To brace in such a way that opposite strains are\n   resisted; to apply counter braces to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbuff', 'v. t.', 'To strike or drive back or in an opposite\n   direction; to stop by a blow or impulse in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterbuff', 'n.', 'A blow in an opposite direction; a stroke that stops\n   motion or cause a recoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercast', 'n.', 'A trick; a delusive contrivance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercaster', 'n.', 'A caster of accounts; a reckoner; a bookkeeper; --\n   used contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterchanged', 'imp. & p. p.', 'of Counterchange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterchanging', 'p. pr. & vb. n.', 'of Counterchange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterchange', 'v. t.', 'To give and receive; to cause to change places;\n   to exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterchange', 'v. t.', 'To checker; to diversify, as in heraldic\n   counterchanging. See Counterchaged, a., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterchange', 'n.', 'Exchange; reciprocation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterchanged', 'a.', 'Exchanged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterchanged', 'a.', 'Having the tinctures exchanged mutually; thus, if\n   the field is divided palewise, or and azure, and cross is borne\n   counterchanged, that part of the cross which comes on the azure side\n   will be or, and that on the or side will be azure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercharge', 'n.', 'An opposing charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercharmed', 'imp. & p. p.', 'of Countercharm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercharming', 'p. pr. & vb. n.', 'of Countercharm');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercharm', 'v. t.', 'To destroy the effect of a charm upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercharm', 'n.', 'That which has the power of destroying the effect of\n   a charm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterchecked', 'imp. & p. p.', 'of Countercheck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterchecking', 'p. pr. & vb. n.', 'of Countercheck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercheck', 'v. t.', 'To oppose or check by some obstacle; to check by a\n   return check.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercheck', 'n.', 'A check; a stop; a rebuke, or censure to check a\n   reprover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercheck', 'n.', 'Any force or device designed to restrain another\n   restraining force; a check upon a check.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterclaim', 'n.', 'A claim made by a person as an offset to a claim made\n   on him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter-compony', 'a.', 'See Compony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter-couchant', 'a.', 'Lying down, with their heads in opposite\n   directions; -- said of animals borne in a coat of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter-courant', 'a.', 'Running in opposite directions; -- said of animals\n   borne in a coast of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercurrent', 'a.', 'Running in an opposite direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countercurrent', 'n.', 'A current running in an opposite direction to the\n   main current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterdrew', 'imp.', 'of Counterdraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterdrawn', 'p. p.', 'of Counterdraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterdrawing', 'p. pr. & vb. n.', 'of Counterdraw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterdraw', 'v. t.', 'To copy, as a design or painting, by tracing with a\n   pencil on oiled paper, or other transparent substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfaisance', 'n.', 'See Counterfesance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeit', 'adv.', 'Representing by imitation or likeness; having a\n   resemblance to something else; portrayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeit', 'adv.', 'Fabricated in imitation of something else, with a\n   view to defraud by passing the false copy for genuine or original; as,\n   counterfeit antiques; counterfeit coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeit', 'adv.', 'Assuming the appearance of something; false;\n   spurious; deceitful; hypocritical; as, a counterfeit philanthropist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeit', 'n.', 'That which resembles or is like another thing; a\n   likeness; a portrait; a counterpart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeit', 'n.', 'That which is made in imitation of something, with a\n   view to deceive by passing the false for the true; as, the bank note\n   was a counterfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeit', 'n.', 'One who pretends to be what he is not; one who\n   personates another; an impostor; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeited', 'imp. & p. p.', 'of Counterfeit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeiting', 'p. pr. & vb. n.', 'of Counterfeit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeit', 'v. t.', 'To imitate, or put on a semblance of; to mimic; as,\n   to counterfeit the voice of another person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeit', 'v. t.', 'To imitate with a view to deceiving, by passing the\n   copy for that which is original or genuine; to forge; as, to\n   counterfeit the signature of another, coins, notes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeit', 'v. i.', 'To carry on a deception; to dissemble; to feign; to\n   pretend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeit', 'v. i.', 'To make counterfeits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeiter', 'n.', 'One who counterfeits; one who copies or imitates;\n   especially, one who copies or forges bank notes or coin; a forger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeiter', 'n.', 'One who assumes a false appearance or semblance; one\n   who makes false pretenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfeitly', 'adv.', 'By forgery; falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfesance', 'a.', 'The act of forging; forgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfleury', 'a.', 'Counterflory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterflory', 'a.', 'Adorned with flowers (usually fleurs-de-lis) so\n   divided that the tops appear on one side and the bottoms on the others;\n   -- said of any ordinary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfoil', 'n.', 'That part of a tally, formerly in the exchequer, which\n   was kept by an officer in that court, the other, called the stock,\n   being delivered to the person who had lent the king money on the\n   account; -- called also counterstock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfoil', 'n.', 'The part of a writing (as the stub of a bank check) in\n   which are noted the main particulars contained in the corresponding\n   part, which has been issued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterforce', 'n.', 'An opposing force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfort', 'n.', 'A kind of buttress of masonry to strengthen a\n   revetment wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterfort', 'n.', 'A spur or projection of a mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countergage', 'n.', 'An adjustable gage, with double points for\n   transferring measurements from one timber to another, as the breadth of\n   a mortise to the place where the tenon is to be made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterguard', 'n.', 'A low outwork before a bastion or ravelin, consisting\n   of two lines of rampart parallel to the faces of the bastion, and\n   protecting them from a breaching fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterirritant', 'n.', 'Alt. of Counterirritation');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterirritation', 'n.', 'See Counter irritant, etc., under Counter, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterirritate', 'v. t.', 'To produce counter irritation in; to treat with\n   one morbid process for the purpose of curing another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterjumper', 'n.', 'A salesman in a shop; a shopman; -- used\n   contemptuously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermen', 'pl. ', 'of Counterman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterman', 'n.', 'A man who attends at the counter of a shop to sell\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermanded', 'imp. & p. p.', 'of Countermand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermanding', 'p. pr. & vb. n.', 'of Countermand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermand', 'v. t.', 'To revoke (a former command); to cancel or rescind\n   by giving an order contrary to one previously given; as, to countermand\n   an order for goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermand', 'v. t.', 'To prohibit; to forbid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermand', 'v. t.', 'To oppose; to revoke the command of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermand', 'n.', 'A contrary order; revocation of a former order or\n   command.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermandable', 'a.', 'Capable of being countermanded; revocable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermarched', 'imp. & p. p.', 'of Countermarch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermarching', 'p. pr. & vb. n.', 'of Countermarch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermarch', 'v. i.', 'To march back, or to march in reversed order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermarch', 'n.', 'A marching back; retrocession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermarch', 'n.', 'An evolution by which a body of troops change front\n   or reverse the direction of march while retaining the same men in the\n   front rank; also, a movement by which the rear rank becomes the front\n   one, either with or without changing the right to the left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermarch', 'n.', 'A change of measures; alteration of conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermark', 'n.', 'A mark or token added to those already existing, in\n   order to afford security or proof; as, an additional or special mark\n   put upon a package of goods belonging to several persons, that it may\n   not be opened except in the presence of all; a mark added to that of an\n   artificer of gold or silver work by the Goldsmiths Company of London,\n   to attest the standard quality of the gold or silver; a mark added to\n   an ancient coin or medal, to show either its change of value or that it\n   was taken from an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermark', 'n.', 'An artificial cavity made in the teeth of horses that\n   have outgrown their natural mark, to disguise their age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermark', 'v. t.', 'To apply a countermark to; as, to countermark\n   silverware; to countermark a horses teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermine', 'n.', 'An underground gallery excavated to intercept and\n   destroy the mining of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermine', 'n.', 'A stratagem or plot by which another sratagem or\n   project is defeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermined', 'imp. & p. p.', 'of Countermine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermining', 'p. pr. & vb. n.', 'of Countermine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermine', 'v. t.', 'To oppose by means of a countermine; to intercept\n   with a countermine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermine', 'v. t.', 'To frustrate or counteract by secret measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermine', 'v. i.', 'To make a countermine or counterplot; to plot\n   secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermove', 'v. t. & i.', 'To move in a contrary direction to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermove', '', 'Alt. of Countermovement');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermovement', '', 'A movement in opposition to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermure', 'n.', 'A wall raised behind another, to supply its place when\n   breached or destroyed. [R.] Cf. Contramure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermured', 'imp. & p. p.', 'of Countermure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermuring', 'p. pr. & vb. n.', 'of Countermure');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countermure', 'v. t.', 'To fortify with a wall behind another wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counternatural', 'a.', 'Contrary to nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter-paly', 'a.', 'Paly, and then divided fesswise, so that each\n   vertical piece is cut into two, having the colors used alternately or\n   counterchanged. Thus the escutcheon in the illustration may also be\n   blazoned paly of six per fess counterchanged argent and azure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpane', 'n.', 'A coverlet for a bed, -- originally stitched or woven\n   in squares or figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpane', 'n.', 'A duplicate part or copy of an indenture, deed, etc.,\n   corresponding with the original; -- now called counterpart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpart', 'n.', 'A part corresponding to another part; anything which\n   answers, or corresponds, to another; a copy; a duplicate; a facsimile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpart', 'n.', 'One of two corresponding copies of an instrument; a\n   duplicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpart', 'n.', 'A person who closely resembles another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpart', 'n.', 'A thing may be applied to another thing so as to fit\n   perfectly, as a seal to its impression; hence, a thing which is adapted\n   to another thing, or which supplements it; that which serves to\n   complete or complement anything; hence, a person or thing having\n   qualities lacking in another; an opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpassant', 'a.', 'Passant in opposite directions; -- said of two\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterplead', 'v. t.', 'To plead the contrary of; to plead against; to\n   deny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterplotted', 'imp. & p. p.', 'of Counterplot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterplotting', 'p. pr. & vb. n.', 'of Counterplot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterplot', 'v. t.', 'To oppose, as another plot, by plotting; to attempt\n   to frustrate, as a stratagem, by stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterplot', 'n.', 'A plot or artifice opposed to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoint', 'n.', 'An opposite point');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoint', 'n.', 'The setting of note against note in harmony; the\n   adding of one or more parts to a given canto fermo or melody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoint', 'n.', 'The art of polyphony, or composite melody, i. e.,\n   melody not single, but moving attended by one or more related melodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoint', 'n.', 'Music in parts; part writing; harmony; polyphonic\n   music. See Polyphony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoint', 'n.', 'A coverlet; a cover for a bed, often stitched or\n   broken into squares; a counterpane. See 1st Counterpane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoised', 'imp. & p. p.', 'of Counterpoise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoising', 'p. pr. & vb. n.', 'of Counterpoise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoise', 'v. t.', 'To act against with equal weight; to equal in\n   weight; to balance the weight of; to counterbalance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoise', 'v. t.', 'To act against with equal power; to balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoise', 'n.', 'A weight sufficient to balance another, as in the\n   opposite scale of a balance; an equal weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoise', 'n.', 'An equal power or force acting in opposition; a force\n   sufficient to balance another force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpoise', 'n.', 'The relation of two weights or forces which balance\n   each other; equilibrium; equiponderance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterpole', 'n.', 'The exact opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterponderate', 'v. t.', 'To equal in weight; to counterpoise; to\n   equiponderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterproved', 'imp. & p. p.', 'of Counterprove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterproving', 'p. pr. & vb. n.', 'of Counterprove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterprove', 'v. t.', 'To take a counter proof of, or a copy in reverse,\n   by taking an impression directly from the face of an original. See\n   Counter proof, under Counter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter-roll', 'n.', 'A duplicate roll (record or account) kept by an\n   officer as a check upon another officers roll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterrolment', 'n.', 'A counter account. See Control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter-salient', 'a.', 'Leaping from each other; -- said of two figures on\n   a coast of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterscale', 'n.', 'Counterbalance; balance, as of one scale against\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterscarf', 'n.', 'The exterior slope or wall of the ditch; --\n   sometimes, the whole covered way, beyond the ditch, with its parapet\n   and glacis; as, the enemy have lodged themselves on the counterscarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersealed', 'imp. & p. p.', 'of Counterseal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersealing', 'p. pr. & vb. n.', 'of Counterseal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterseal', 'v. t.', 'To seal or ratify with another or others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersecure', 'v. t.', 'To give additional security to or for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countershaft', 'n.', 'An intermediate shaft; esp., one which receives\n   motion from a line shaft in a factory and transmits it to a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersigned', 'imp. & p. p.', 'of Countersign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersigning', 'p. pr. & vb. n.', 'of Countersign');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersign', 'v. t.', 'To sign on the opposite side of (an instrument or\n   writing); hence, to sign in addition to the signature of a principal or\n   superior, in order to attest the authenticity of a writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersign', 'a.', 'The signature of a secretary or other officer to a\n   writing signed by a principal or superior, to attest its authenticity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersign', 'a.', 'A private signal, word, or phrase, which must be given\n   in order to pass a sentry; a watchword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersunk', 'imp. & p. p.', 'of Countersink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersinking', 'p. pr. & vb. n.', 'of Countersink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersink', 'v. t.', 'To chamfer or form a depression around the top of\n   (a hole in wood, metal, etc.) for the reception of the head of a screw\n   or bolt below the surface, either wholly or in part; as, to countersink\n   a hole for a screw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersink', 'v. t.', 'To cause to sink even with or below the surface;\n   as, to countersink a screw or bolt into woodwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersink', 'n.', 'An enlargement of the upper part of a hole, forming a\n   cavity or depression for receiving the head of a screw or bolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersink', 'n.', 'A drill or cutting tool for countersinking holes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterstand', 'n.', 'Resistance; opposition; a stand against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterstep', 'n.', 'A contrary method of procedure; opposite course of\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterstock', 'n.', 'See Counterfoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterstroke', 'n.', 'A stroke or blow in return.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersunk', 'p. p. & a.', 'Chamfered at the top; -- said of a hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersunk', 'p. p. & a.', 'Sunk into a chamfer; as, a countersunk bolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersunk', 'p. p. & a.', 'Beveled on the lower side, so as to fit a\n   chamfered countersink; as, a countersunk nailhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countersway', 'n.', 'A swaying in a contrary direction; an opposing\n   influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter tenor', '', 'One of the middle parts in music, between the tenor\n   and the treble; high tenor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterterm', 'n.', 'A term or word which is the opposite of, or antithesis\n   to, another; an antonym; -- the opposite of synonym; as, \"foe\" is the\n   counterterm of \"friend\".');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countertime', 'n.', 'The resistance of a horse, that interrupts his cadence\n   and the measure of his manege, occasioned by a bad horseman, or the bad\n   temper of the horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countertime', 'n.', 'Resistance; opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countertrippant', 'a.', 'Trippant in opposite directions. See Trippant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countertripping', 'a.', 'Same as Countertrippant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterturn', 'n.', 'The critical moment in a play, when, contrary to\n   expectation, the action is embroiled in new difficulties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countervailed', 'imp. & p. p.', 'of Countervail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countervailing', 'p. pr. & vb. n.', 'of Countervail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countervail', 'v. t.', 'To act against with equal force, power, or effect;\n   to thwart or overcome by such action; to furnish an equivalent to or\n   for; to counterbalance; to compensate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countervail', 'n.', 'Power or value sufficient to obviate any effect; equal\n   weight, strength, or value; equivalent; compensation; requital.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countervallation', 'n.', 'See Contravallation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterview', 'n.', 'An opposite or opposing view; opposition; a posture in\n   which two persons front each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterview', 'n.', 'A position in which two dissimilar things illustrate\n   each other by opposition; contrast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countervote', 'v. t.', 'To vote in opposition to; to balance or overcome by\n   voting; to outvote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterwait', 'v. t.', 'To wait or watch for; to be on guard against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterweigh', 'v. t.', 'To weigh against; to counterbalance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counter weight', 'n.', 'A counterpoise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterwheel', 'v. t.', 'To cause to wheel or turn in an opposite\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counterwork', 'v. t.', 'To work in opposition to; to counteract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countesses', 'pl. ', 'of Countess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countess', 'n.', 'The wife of an earl in the British peerage, or of a count\n   in the Continental nobility; also, a lady possessed of the same dignity\n   in her own right. See the Note under Count.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countinghouse', 'v.', 'Alt. of Countingroom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countingroom', 'v.', 'The house or room in which a merchant, trader, or\n   manufacturer keeps his books and transacts business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countless', 'a.', 'Incapable of being counted; not ascertainable;\n   innumerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countor', 'v. t.', 'An advocate or professional pleader; one who counted\n   for his client, that is, orally pleaded his cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countour', 'n.', 'Alt. of Countourhouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countourhouse', 'n.', 'A merchants office; a countinghouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countre-', '', 'Same as prefix Counter-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countreplete', 'v. t.', 'To counterplead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countretaille', 'n.', 'A counter tally; correspondence (in sound).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countrified', 'p. a.', 'Having the appearance and manners of a rustic;\n   rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countrify', 'v. t.', 'To give a rural appearance to; to cause to appear\n   rustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countries', 'pl. ', 'of Country');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country', 'adv.', 'A tract of land; a region; the territory of an\n   independent nation; (as distinguished from any other region, and with a\n   personal pronoun) the region of ones birth, permanent residence, or\n   citizenship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country', 'adv.', 'Rural regions, as opposed to a city or town.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country', 'adv.', 'The inhabitants or people of a state or a region; the\n   populace; the public. Hence: (a) Ones constituents. (b) The whole body\n   of the electors of state; as, to dissolve Parliament and appeal to the\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country', 'adv.', 'A jury, as representing the citizens of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country', 'adv.', 'The inhabitants of the district from which a jury is\n   drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country', 'adv.', 'The rock through which a vein runs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country', 'a.', 'Pertaining to the regions remote from a city; rural;\n   rustic; as, a country life; a country town; the country party, as\n   opposed to city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country', 'a.', 'Destitute of refinement; rude; unpolished; rustic; not\n   urbane; as, country manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country', 'a.', 'Pertaining, or peculiar, to ones own country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country-base', 'n.', 'Same as Prison base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country-dance', 'n.', 'See Contradance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countrymen', 'pl. ', 'of Countryman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countryman', 'n.', 'An inhabitant or native of a region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countryman', 'n.', 'One born in the same country with another; a\n   compatriot; -- used with a possessive pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countryman', 'n.', 'One who dwells in the country, as distinguished from a\n   townsman or an inhabitant of a city; a rustic; a husbandman or farmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Country seat', '', 'A dwelling in the country, used as a place of\n   retirement from the city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countryside', 'n.', 'A particular rural district; a country neighborhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countrywomen', 'pl. ', 'of Countrywoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Countrywoman', 'n.', 'A woman born, or dwelling, in the country, as opposed\n   to the city; a woman born or dwelling in the same country with another\n   native or inhabitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Count-wheel', 'n.', 'The wheel in a clock which regulates the number of\n   strokes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Counties', 'pl. ', 'of County');
  INSERT INTO words (word, word_type, description) VALUES  (    'County', 'n.', 'An earldom; the domain of a count or earl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'County', 'n.', 'A circuit or particular portion of a state or kingdom,\n   separated from the rest of the territory, for certain purposes in the\n   administration of justice and public affairs; -- called also a shire.\n   See Shire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'County', 'n.', 'A count; an earl or lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coup', 'n.', 'A sudden stroke; an unexpected device or stratagem; -- a term\n   used in various ways to convey the idea of promptness and force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupable', 'a.', 'Culpable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupe', 'n.', 'The front compartment of a French diligence; also, the front\n   compartment (usually for three persons) of a car or carriage on British\n   railways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupe', 'n.', 'A four-wheeled close carriage for two persons inside, with\n   an outside seat for the driver; -- so called because giving the\n   appearance of a larger carriage cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couped', 'a.', 'Cut off smoothly, as distinguished from erased; -- used\n   especially for the head or limb of an animal. See Erased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupee', 'n.', 'A motion in dancing, when one leg is a little bent, and\n   raised from the floor, and with the other a forward motion is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupe-gorge', 'n.', 'Any position giving the enemy such advantage that the\n   troops occupying it must either surrender or be cut to pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple', 'a.', 'That which joins or links two things together; a bond or\n   tie; a coupler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple', 'a.', 'Two of the same kind connected or considered together; a\n   pair; a brace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple', 'a.', 'A male and female associated together; esp., a man and\n   woman who are married or betrothed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple', 'a.', 'See Couple-close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple', 'a.', 'One of the pairs of plates of two metals which compose a\n   voltaic battery; -- called a voltaic couple or galvanic couple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple', 'a.', 'Two rotations, movements, etc., which are equal in amount\n   but opposite in direction, and acting along parallel lines or around\n   parallel axes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupled', 'imp. & p. p.', 'of Couple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupling', 'p. pr. & vb. n.', 'of Couple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple', 'v.', 'To link or tie, as one thing to another; to connect or\n   fasten together; to join.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple', 'v.', 'To join in wedlock; to marry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple', 'v. i.', 'To come together as male and female; to copulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple-beggar', 'n.', 'One who makes it his business to marry beggars to\n   each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple-closes', 'pl. ', 'of Couple-close');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple-close', 'n.', 'A diminutive of the chevron, containing one fourth of\n   its surface. Couple-closes are generally borne one on each side of a\n   chevron, and the blazoning may then be either a chevron between two\n   couple-closes or chevron cottised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couple-close', 'n.', 'A pair of rafters framed together with a tie fixed at\n   their feet, or with a collar beam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couplement', 'n.', 'Union; combination; a coupling; a pair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupler', 'n.', 'One who couples; that which couples, as a link, ring, or\n   shackle, to connect cars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couplet', 'n.', 'Two taken together; a pair or couple; especially two lines\n   of verse that rhyme with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupling', 'n.', 'The act of bringing or coming together; connection;\n   sexual union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupling', 'n.', 'A device or contrivance which serves to couple or connect\n   adjacent parts or objects; as, a belt coupling, which connects the ends\n   of a belt; a car coupling, which connects the cars in a train; a shaft\n   coupling, which connects the ends of shafts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupon', 'n.', 'A certificate of interest due, printed at the bottom of\n   transferable bonds (state, railroad, etc.), given for a term of years,\n   designed to be cut off and presented for payment when the interest is\n   due; an interest warrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupon', 'n.', 'A section of a ticket, showing the holder to be entitled to\n   some specified accomodation or service, as to a passage over a\n   designated line of travel, a particular seat in a theater, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coupure', 'n.', 'A passage cut through the glacis to facilitate sallies by\n   the besieged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courage', 'n.', 'The heart; spirit; temper; disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courage', 'n.', 'Heart; inclination; desire; will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courage', 'n.', 'That quality of mind which enables one to encounter danger\n   and difficulties with firmness, or without fear, or fainting of heart;\n   valor; boldness; resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couage', 'v. t.', 'To inspire with courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courageous', 'a.', 'Possessing, or characterized by, courage; brave; bold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courageously', 'adv.', 'In a courageous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courageousness', 'n.', 'The quality of being courageous; courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courant', 'a.', 'Represented as running; -- said of a beast borne in a coat\n   of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courant', 'p. pr.', 'A piece of music in triple time; also, a lively dance;\n   a coranto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courant', 'p. pr.', 'A circulating gazette of news; a newspaper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couranto', 'n.', 'A sprightly dance; a coranto; a courant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courap', 'n.', 'A skin disease, common in India, in which there is\n   perpetual itching and eruption, esp. of the groin, breast, armpits, and\n   face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courb', 'a.', 'Curved; rounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courb', 'v. i.', 'To bend; to stop; to bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courbaril', 'n.', 'See Anime, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courche', 'n.', 'A square piece of linen used formerly by women instead of\n   a cap; a kerchief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courier', 'n.', 'A messenger sent with haste to convey letters or\n   dispatches, usually on public business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courier', 'n.', 'An attendant on travelers, whose business it is to make\n   arrangements for their convenience at hotels and on the way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courlan', 'n.', 'A South American bird, of the genus Aramus, allied to the\n   rails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'The act of moving from one point to another; progress;\n   passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'The ground or path traversed; track; way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'Motion, considered as to its general or resultant direction\n   or to its goal; line progress or advance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'Progress from point to point without change of direction;\n   any part of a progress from one place to another, which is in a\n   straight line, or on one direction; as, a ship in a long voyage makes\n   many courses; a course measured by a surveyor between two stations;\n   also, a progress without interruption or rest; a heat; as, one course\n   of a race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'Motion considered with reference to manner; or derly\n   progress; procedure in a certain line of thought or action; as, the\n   course of an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'Customary or established sequence of events; recurrence of\n   events according to natural laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'Method of procedure; manner or way of conducting; conduct;\n   behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'A series of motions or acts arranged in order; a succession\n   of acts or practices connectedly followed; as, a course of medicine; a\n   course of lectures on chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'The succession of one to another in office or duty; order;\n   turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'That part of a meal served at one time, with its\n   accompaniments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'A continuous level range of brick or stones of the same\n   height throughout the face or faces of a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'The lowest sail on any mast of a square-rigged vessel; as,\n   the fore course, main course, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'n.', 'The menses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coursed', 'imp. & p. p.', 'of Course');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coursing', 'p. pr. & vb. n.', 'of Course');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'v. t.', 'To run, hunt, or chase after; to follow hard upon; to\n   pursue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'v. t.', 'To cause to chase after or pursue game; as, to course\n   greyhounds after deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'v. t.', 'To run through or over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'v. i.', 'To run as in a race, or in hunting; to pursue the sport\n   of coursing; as, the sportsmen coursed over the flats of Lancashire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Course', 'v. i.', 'To move with speed; to race; as, the blood courses\n   through the veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coursed', 'a.', 'Hunted; as, a coursed hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coursed', 'a.', 'Arranged in courses; as, coursed masonry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courser', 'n.', 'One who courses or hunts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courser', 'n.', 'A swift or spirited horse; a racer or a war horse; a\n   charger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courser', 'n.', 'A grallatorial bird of Europe (Cursorius cursor),\n   remarkable for its speed in running. Sometimes, in a wider sense,\n   applied to running birds of the Ostrich family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coursey', 'n.', 'A space in the galley; a part of the hatches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coursing', 'n.', 'The pursuit or running game with dogs that follow by\n   sight instead of by scent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'An inclosed space; a courtyard; an uncovered area shut in by\n   the walls of a building, or by different building; also, a space\n   opening from a street and nearly surrounded by houses; a blind alley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'The residence of a sovereign, prince, nobleman, or ether\n   dignitary; a palace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'The collective body of persons composing the retinue of a\n   sovereign or person high in authority; all the surroundings of a\n   sovereign in his regal state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'Any formal assembling of the retinue of a sovereign; as, to\n   hold a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'Attention directed to a person in power; conduct or address\n   designed to gain favor; courtliness of manners; civility; compliment;\n   flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'The hall, chamber, or place, where justice is administered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'The persons officially assembled under authority of law, at\n   the appropriate time and place, for the administration of justice; an\n   official assembly, legally met together for the transaction of judicial\n   business; a judge or judges sitting for the hearing or trial of causes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'A tribunal established for the administration of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'The judge or judges; as distinguished from the counsel or\n   jury, or both.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'The session of a judicial assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'Any jurisdiction, civil, military, or ecclesiastical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'n.', 'A place arranged for playing the game of tennis; also, one\n   of the divisions of a tennis court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courted', 'imp. & p. p.', 'of Court');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courting', 'p. pr. & vb. n.', 'of Court');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'v. t.', 'To endeavor to gain the favor of by attention or\n   flattery; to try to ingratiate ones self with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'v. t.', 'To endeavor to gain the affections of; to seek in\n   marriage; to woo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'v. t.', 'To attempt to gain; to solicit; to seek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'v. t.', 'To invite by attractions; to allure; to attract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court', 'v. i.', 'To play the lover; to woo; as, to go courting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court-baron', 'n.', 'An inferior court of civil jurisdiction, attached to a\n   manor, and held by the steward; a barons court; -- now fallen into\n   disuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtbred', 'a.', 'Bred, or educated, at court; polished; courtly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court-craft', 'n.', 'The artifices, intrigues, and plottings, at courts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court-cupboard', 'n.', 'A movable sideboard or buffet, on which plate and\n   other articles of luxury were displayed on special ocasions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courteous', 'a.', 'Of courtlike manners; pertaining to, or expressive of,\n   courtesy; characterized by courtesy; civil; obliging; well bred;\n   polite; affable; complaisant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courteously', 'adv.', 'In a courteous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courteousness', 'n.', 'The quality of being courteous; politeness;\n   courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtepy', 'n.', 'A short coat of coarse cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courter', 'n.', 'One who courts; one who plays the lover, or who solicits\n   in marriage; one who flatters and cajoles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesan', 'n.', 'A woman who prostitutes herself for hire; a prostitute;\n   a harlot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesanship', 'n.', 'Harlotry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesies', 'pl. ', 'of Courtesy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesy', 'n.', 'Politeness; civility; urbanity; courtliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesy', 'n.', 'An act of civility or respect; an act of kindness or\n   favor performed with politeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesy', 'n.', 'Favor or indulgence, as distinguished from right; as, a\n   title given one by courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesy', 'n.', 'An act of civility, respect, or reverence, made by women,\n   consisting of a slight depression or dropping of the body, with bending\n   of the knees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesied', 'imp. & p. p.', 'of Courtesy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesying', 'p. pr. & vb. n.', 'of Courtesy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesy', 'v. i.', 'To make a respectful salutation or movement of\n   respect; esp. (with reference to women), to bow the body slightly, with\n   bending of the knes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtesy', 'v. t.', 'To treat with civility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtehouse', 'n.', 'A house in which established courts are held, or a\n   house appropriated to courts and public meetings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtehouse', 'n.', 'A county town; -- so called in Virginia and some\n   others of the Southern States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtier', 'n.', 'One who is in attendance at the court of a prince; one\n   who has an appointment at court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtier', 'n.', 'One who courts or solicits favor; one who flatters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtiery', 'n.', 'The manners of a courtier; courtliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court-leet', 'n.', 'A court of record held once a year, in a particular\n   hundred, lordship, or manor, before the steward of the leet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtlike', 'a.', 'After the manner of a court; elegant; polite; courtly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtliness', 'n.', 'The quality of being courtly; elegance or dignity of\n   manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtling', 'n.', 'A sycophantic courtier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtly', 'a.', 'Relating or belonging to a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtly', 'a.', 'Elegant; polite; courtlike; flattering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtly', 'a.', 'Disposed to favor the great; favoring the policy or party\n   of the court; obsequious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtly', 'adv.', 'In the manner of courts; politely; gracefully;\n   elegantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courts-martial', 'pl. ', 'of Court-martial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court-martial', 'n.', 'A court consisting of military or naval officers,\n   for the trial of one belonging to the army or navy, or of offenses\n   against military or naval law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court-martialed', 'imp. & p. p.', 'of Court-martial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court-martialing', 'p. pr. & vb. n.', 'of Court-martial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court-martial', 'v. t.', 'To subject to trial by a court-martial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court-plaster', 'n.', 'Sticking plaster made by coating taffeta or silk on\n   one side with some adhesive substance, commonly a mixture of isinglass\n   and glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtship', 'n.', 'The act of paying court, with the intent to solicit a\n   favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtship', 'n.', 'The act of wooing in love; solicitation of woman to\n   marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtship', 'n.', 'Courtliness; elegance of manners; courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtship', 'n.', 'Court policy; the character of a courtier; artifice of a\n   court; court-craft; finesse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Court tennis', '', 'See under Tennis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Courtyard', 'n.', 'A court or inclosure attached to a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couscous', 'n.', 'A kind of food used by the natives of Western Africa,\n   made of millet flour with flesh, and leaves of the baobab; -- called\n   also lalo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couscousou', 'n.', 'A favorite dish in Barbary. See Couscous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cousin', 'n.', 'One collaterally related more remotely than a brother or\n   sister; especially, the son or daughter of an uncle or aunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cousin', 'n.', 'A title formerly given by a king to a nobleman,\n   particularly to those of the council. In English writs, etc., issued by\n   the crown, it signifies any earl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cousin', 'n.', 'Allied; akin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cousinage', 'n.', 'Relationship; kinship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cousin-german', 'n.', 'A first cousin. See Note under Cousin, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cousinhood', 'n.', 'The state or condition of a cousin; also, the\n   collective body of cousins; kinsfolk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cousinly', 'a.', 'Like or becoming a cousin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cousinry', 'n.', 'A body or collection of cousins; the whole number of\n   persons who stand in the relation of cousins to a given person or\n   persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cousinship', 'n.', 'The relationship of cousins; state of being cousins;\n   cousinhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coussinet', 'n.', 'A stone placed on the impost of a pier for receiving the\n   first stone of an arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coussinet', 'n.', 'That part of the Ionic capital between the abacus and\n   quarter round, which forms the volute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couteau', 'n.', 'A knife; a dagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couth', 'imp. & p. p.', 'Could; was able; knew or known; understood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Couvade', 'n.', 'A custom, among certain barbarous tribes, that when a\n   woman gives birth to a child her husband takes to his bed, as if ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covariant', 'n.', 'A function involving the coefficients and the variables\n   of a quantic, and such that when the quantic is lineally transformed\n   the same function of the new variables and coefficients shall be equal\n   to the old function multiplied by a factor. An invariant is a like\n   function involving only the coefficients of the quantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cove', 'n.', 'A retired nook; especially, a small, sheltered inlet, creek,\n   or bay; a recess in the shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cove', 'n.', 'A strip of prairie extending into woodland; also, a recess in\n   the side of a mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cove', 'n.', 'A concave molding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cove', 'n.', 'A member, whose section is a concave curve, used especially\n   with regard to an inner roof or ceiling, as around a skylight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coved', 'imp. & p. p.', 'of Cove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coving', 'p. pr. & vb. n.', 'of Cove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cove', 'v. t.', 'To arch over; to build in a hollow concave form; to make\n   in the form of a cove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cove', 'v. t.', 'To brood, cover, over, or sit over, as birds their eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cove', 'n.', 'A boy or man of any age or station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covelline', 'n.', 'Alt. of Covellite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covellite', 'n.', 'A native sulphide of copper, occuring in masses of a\n   dark blue color; -- hence called indigo copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenable', 'a.', 'Fit; proper; suitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenably', 'adv.', 'Fitly; suitably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenant', 'n.', 'A mutual agreement of two or more persons or parties, or\n   one of the stipulations in such an agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenant', 'n.', 'An agreement made by the Scottish Parliament in 1638, and\n   by the English Parliament in 1643, to preserve the reformed religion in\n   Scotland, and to extirpate popery and prelacy; -- usually called the\n   \"Solemn League and Covenant.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenant', 'n.', 'The promises of God as revealed in the Scriptures,\n   conditioned on certain terms on the part of man, as obedience,\n   repentance, faith, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenant', 'n.', 'A solemn compact between members of a church to maintain\n   its faith, discipline, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenant', 'n.', 'An undertaking, on sufficient consideration, in writing\n   and under seal, to do or to refrain from some act or thing; a contract;\n   a stipulation; also, the document or writing containing the terms of\n   agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenant', 'n.', 'A form of action for the violation of a promise or\n   contract under seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenanted', 'imp. & p. p.', 'of Covenant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenanting', 'p. pr. & vb. n.', 'of Covenant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenant', 'v. i.', 'To agree (with); to enter into a formal agreement; to\n   bind ones self by contract; to make a stipulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenant', 'v. t.', 'To grant or promise by covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenantee', 'n.', 'The person in whose favor a covenant is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenanter', 'n.', 'One who makes a covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenanter', 'n.', 'One who subscribed and defended the \"Solemn League and\n   Covenant.\" See Covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenanting', 'a.', 'Belonging to a covenant. Specifically, belonging to\n   the Scotch Covenanters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenantor', 'n.', 'The party who makes a covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covenous', 'a.', 'See Covinous, and Covin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covent', 'n.', 'A convent or monastery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coventry', 'n.', 'A town in the county of Warwick, England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covered', 'imp. & p. p.', 'of Cover');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covering', 'p. pr. & vb. n.', 'of Cover');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. t.', 'To overspread the surface of (one thing) with another;\n   as, to cover wood with paint or lacquer; to cover a table with a cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. t.', 'To envelop; to clothe, as with a mantle or cloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. t.', 'To invest (ones self with something); to bring upon\n   (ones self); as, he covered himself with glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. t.', 'To hide sight; to conceal; to cloak; as, the enemy were\n   covered from our sight by the woods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. t.', 'To brood or sit on; to incubate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. t.', 'To shelter, as from evil or danger; to protect; to\n   defend; as, the cavalry covered the retreat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. t.', 'To remove from remembrance; to put away; to remit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. t.', 'To extend over; to be sufficient for; to comprehend,\n   include, or embrace; to account for or solve; to counterbalance; as, a\n   mortgage which fully covers a sum loaned on it; a law which covers all\n   possible cases of a crime; receipts than do not cover expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. t.', 'To put the usual covering or headdress on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. t.', 'To copulate with (a female); to serve; as, a horse covers\n   a mare; -- said of the male.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'n.', 'Anything which is laid, set, or spread, upon, about, or\n   over, another thing; an envelope; a lid; as, the cover of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'n.', 'Anything which veils or conceals; a screen; disguise; a\n   cloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'n.', 'Shelter; protection; as, the troops fought under cover of\n   the batteries; the woods afforded a good cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'n.', 'The woods, underbrush, etc., which shelter and conceal game;\n   covert; as, to beat a cover; to ride to cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'n.', 'The lap of a slide valve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'n.', 'A tablecloth, and the other table furniture; esp., the table\n   furniture for the use of one person at a meal; as, covers were laid for\n   fifty guests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover', 'v. i.', 'To spread a table for a meal; to prepare a banquet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coverchief', 'n.', 'A covering for the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covercle', 'n.', 'A small cover; a lid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covered', 'a.', 'Under cover; screened; sheltered; not exposed; hidden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coverer', 'n.', 'One who, or that which, covers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covering', 'n.', 'Anything which covers or conceals, as a roof, a screen, a\n   wrapper, clothing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coverlet', 'n.', 'The uppermost cover of a bed or of any piece of\n   furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coverlid', 'n.', 'A coverlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover-point', 'n.', 'The fielder in the games of cricket and lacrosse who\n   supports \"point.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coversed sine', '', 'The versed sine of the complement of an arc or angle.\n   See Illust. of Functions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cover-shame', 'n.', 'Something used to conceal infamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covert', 'v. t.', 'Covered over; private; hid; secret; disguised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covert', 'v. t.', 'Sheltered; not open or exposed; retired; protected; as,\n   a covert nook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covert', 'v. t.', 'Under cover, authority or protection; as, a feme covert,\n   a married woman who is considered as being under the protection and\n   control of her husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covert', 'a.', 'A place that covers and protects; a shelter; a defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covert', 'a.', 'One of the special feathers covering the bases of the\n   quills of the wings and tail of a bird. See Illust. of Bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covert baron', '', 'Under the protection of a husband; married.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covertly', 'adv.', 'Secretly; in private; insidiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covertness', 'n.', 'Secrecy; privacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coverture', 'n.', 'Covering; shelter; defense; hiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coverture', 'n.', 'The condition of a woman during marriage, because she is\n   considered under the cover, influence, power, and protection of her\n   husband, and therefore called a feme covert, or femme couverte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covered', 'imp. & p. p.', 'of Covet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coveting', 'p. pr. & vb. n.', 'of Covet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covet', 'v. t.', 'To wish for with eagerness; to desire possession of; --\n   used in a good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covet', 'v. t.', 'To long for inordinately or unlawfully; to hanker after\n   (something forbidden).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covet', 'v. i.', 'To have or indulge inordinate desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covetable', 'a.', 'That may be coveted; desirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coveter', 'n.', 'One who covets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covetise', 'v. t.', 'Avarice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covetiveness', 'n.', 'Acquisitiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covetous', 'v. t.', 'Very desirous; eager to obtain; -- used in a good\n   sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covetous', 'v. t.', 'Inordinately desirous; excessively eager to obtain and\n   possess (esp. money); avaricious; -- in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covetously', 'adv.', 'In a covetous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covetousness', 'n.', 'Strong desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covetousness', 'n.', 'A strong or inordinate desire of obtaining and\n   possessing some supposed good; excessive desire for riches or money; --\n   in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covey', 'n.', 'A brood or hatch of birds; an old bird with her brood of\n   young; hence, a small flock or number of birds together; -- said of\n   game; as, a covey of partridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covey', 'n.', 'A company; a bevy; as, a covey of girls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covey', 'v. i.', 'To brood; to incubate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covey', 'n.', 'A pantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covin', 'n.', 'A collusive agreement between two or more persons to\n   prejudice a third.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covin', 'n.', 'Deceit; fraud; artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Covinous', 'a.', 'Deceitful; collusive; fraudulent; dishonest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cow', 'n.', 'A chimney cap; a cowl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cows', 'pl. ', 'of Cow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kine', 'pl. ', 'of Cow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cow', 'n.', 'The mature female of bovine animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cow', 'n.', 'The female of certain large mammals, as whales, seals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowed', 'imp. & p. p.', 'of Cow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowing', 'p. pr. & vb. n.', 'of Cow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cow', 'v. t.', 'To depress with fear; to daunt the spirits or courage of;\n   to overawe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cow', 'n.', 'A wedge, or brake, to check the motion of a machine or car; a\n   chock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowage', 'n.', 'See Cowhage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowan', 'n.', 'One who works as a mason without having served a regular\n   apprenticeship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coward', 'a.', 'Borne in the escutcheon with his tail doubled between his\n   legs; -- said of a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coward', 'a.', 'Destitute of courage; timid; cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coward', 'a.', 'Belonging to a coward; proceeding from, or expressive of,\n   base fear or timidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coward', 'n.', 'A person who lacks courage; a timid or pusillanimous\n   person; a poltroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coward', 'v. t.', 'To make timorous; to frighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowardice', 'n.', 'Want of courage to face danger; extreme timidity;\n   pusillanimity; base fear of danger or hurt; lack of spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowardie', 'n.', 'Cowardice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowardish', 'a.', 'Cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowardize', 'v. t. ', 'To render cowardly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowardliness', 'n.', 'Cowardice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowardly', 'a.', 'Wanting courage; basely or weakly timid or fearful;\n   pusillanimous; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowardly', 'a.', 'Proceeding from fear of danger or other consequences;\n   befitting a coward; dastardly; base; as, cowardly malignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowardly', 'adv.', 'In the manner of a coward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowardship', 'n.', 'Cowardice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowbane', 'n.', 'A poisonous umbelliferous plant; in England, the Cicuta\n   virosa; in the United States, the Cicuta maculata and the Archemora\n   rigida. See Water hemlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowberries', 'pl. ', 'of Cowberry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowberry', 'n.', 'A species of Vaccinium (V. Vitis-idaea), which bears acid\n   red berries which are sometimes used in cookery; -- locally called\n   mountain cranberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowbird', 'n.', 'The cow blackbird (Molothrus ater), an American starling.\n   Like the European cuckoo, it builds no nest, but lays its eggs in the\n   nests of other birds; -- so called because frequently associated with\n   cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowblakes', 'n. pl.', 'Dried cow dung used as fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowboy', 'n.', 'A cattle herder; a drover; specifically, one of an\n   adventurous class of herders and drovers on the plains of the Western\n   and Southwestern United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowboy', 'n.', 'One of the marauders who, in the Revolutionary War infested\n   the neutral ground between the American and British lines, and\n   committed depredations on the Americans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowcatxjer', 'n.', 'A strong inclined frame, usually of wrought-iron bars,\n   in front of a locomotive engine, for catching or throwing off\n   obstructions on a railway, as cattle; the pilot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowdie', 'n.', 'See Kauri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowered', 'imp. & p. p.', 'of Cower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowering', 'p. pr. & vb. n.', 'of Cower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cower', 'v. i.', 'To stoop by bending the knees; to crouch; to squat;\n   hence, to quail; to sink through fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cower', 'v. t.', 'To cherish with care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowfish', 'n.', 'The grampus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowfish', 'n.', 'A California dolphin (Tursiops Gillii).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowfish', 'n.', 'A marine plectognath fish (Ostracoin quadricorne, and\n   allied species), having two projections, like horns, in front; --\n   called also cuckold, coffer fish, trunkfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowhage', 'n.', 'A leguminous climbing plant of the genus Mucuna, having\n   crooked pods covered with sharp hairs, which stick to the fingers,\n   causing intolerable itching. The spiculae are sometimes used in\n   medicine as a mechanical vermifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowhearted', 'a.', 'Cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowherd', 'n.', 'One whose occupation is to tend cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowhide', 'n.', 'The hide of a cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowhide', 'n.', 'Leather made of the hide of a cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowhide', 'n.', 'A coarse whip made of untanned leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowhided', 'imp. & p. p.', 'of Cowhide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowhiding', 'p. pr. & vb. n.', 'of Cowhide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowhide', 'v. t.', 'To flog with a cowhide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowish', 'v. t.', 'Timorous; fearful; cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowish', 'n.', 'An umbelliferous plant (Peucedanum Cous) with edible\n   tuberous roots, found in Oregon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowitch', 'n.', 'See Cowhage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowl', 'n.', 'A monks hood; -- usually attached to the gown. The name was\n   also applied to the hood and garment together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowl', 'n.', 'A cowl-shaped cap, commonly turning with the wind, used to\n   improve the draft of a chimney, ventilating shaft, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowl', 'n.', 'A wire cap for the smokestack of a locomotive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowl', 'n.', 'A vessel carried on a pole between two persons, for\n   conveyance of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowled', 'a.', 'Wearing a cowl; hooded; as, a cowled monk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowleech', 'n.', 'One who heals diseases of cows; a cow doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowleeching', 'n.', 'Healing the distemper of cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowlick', 'n.', 'A tuft of hair turned up or awry (usually over the\n   forehead), as if licked by a cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowlike', 'a.', 'Resembling a cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowlstaff', 'n.', 'A staff or pole on which a vessel is supported between\n   two persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coworker', 'n.', 'One who works with another; a co/perator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cow parsley', '', 'An umbelliferous plant of the genus Chaerophyllum (C.\n   temulum and C. sylvestre).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cow parsnip', '', 'A coarse umbelliferous weed of the genus Heracleum (H.\n   sphondylium in England, and H. lanatum in America).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowpea', 'n.', 'The seed of one or more leguminous plants of the genus\n   Dolichos; also, the plant itself. Many varieties are cultivated in the\n   southern part of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowpers glands', '', 'Two small glands discharging into the male urethra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cow-pilot', 'n.', 'A handsomely banded, coral-reef fish, of Florida and the\n   West Indies (Pomacentrus saxatilis); -- called also mojarra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowpock', 'n.', 'See Cowpox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowpox', 'n.', 'A pustular eruptive disease of the cow, which, when\n   communicated to the human system, as by vaccination, protects from the\n   smallpox; vaccinia; -- called also kinepox, cowpock, and kinepock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowquake', 'n.', 'A genus of plants (Briza); quaking grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowrie', 'n.', 'Same as Kauri.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowries', 'pl. ', 'of Cowry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowrie', 'n.', 'Alt. of Cowry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowry', 'n.', 'A marine shell of the genus Cypraea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowslip', 'n.', 'A common flower in England (Primula veris) having yellow\n   blossoms and appearing in early spring. It is often cultivated in the\n   United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowslip', 'n.', 'In the United States, the marsh marigold (Caltha\n   palustris), appearing in wet places in early spring and often used as a\n   pot herb. It is nearer to a buttercup than to a true cowslip. See\n   Illust. of Marsh marigold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowslipped', 'a.', 'Adorned with cowslips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cows lungwort', '', 'Mullein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cow tree', '', 'A tree (Galactodendron utile or Brosimum Galactodendron) of\n   South America, which yields, on incision, a nourishing fluid,\n   resembling milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowweed', 'n.', 'Same as Cow parsley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cowwheat', 'n.', 'A weed of the genus Melampyrum, with black seeds, found\n   on European wheatfields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cox', 'n.', 'A coxcomb; a simpleton; a gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxa', 'n.', 'The first joint of the leg of an insect or crustacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxalgia', 'n.', 'Alt. of Coxalgy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxalgy', 'n.', 'Pain in the hip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxcomb', 'n.', 'A strip of red cloth notched like the comb of a cock,\n   which licensed jesters formerly wore in their caps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxcomb', 'n.', 'The cap itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxcomb', 'n.', 'The top of the head, or the head itself');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxcomb', 'n.', 'A vain, showy fellow; a conceited, silly man, fond of\n   display; a superficial pretender to knowledge or accomplishments; a\n   fop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxcomb', 'n.', 'A name given to several plants of different genera, but\n   particularly to Celosia cristata, or garden cockscomb. Same as\n   Cockscomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxcombical', 'a.', 'Befitting or indicating a coxcomb; like a coxcomb;\n   foppish; conceited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxcombly', 'a.', 'like a coxcomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxcombry', 'n.', 'The manners of a coxcomb; foppishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxcomical', 'a.', 'Coxcombical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxcomically', 'adv.', 'Conceitedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coxswain', 'n.', 'See Cockswain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coy', 'a.', 'Quiet; still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coy', 'a.', 'Shrinking from approach or familiarity; reserved; bashful;\n   shy; modest; -- usually applied to women, sometimes with an implication\n   of coquetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coy', 'a.', 'Soft; gentle; hesitating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coyed', 'imp. & p. p.', 'of Coy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coying', 'p. pr. & vb. n.', 'of Coy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coy', 'v. t.', 'To allure; to entice; to decoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coy', 'v. t.', 'To caress with the hand; to stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coy', 'v. i.', 'To behave with reserve or coyness; to shrink from approach\n   or familiarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coy', 'v. i.', 'To make difficulty; to be unwilling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coyish', 'a.', 'Somewhat coy or reserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coyly', 'adv.', 'In a coy manner; with reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coyness', 'n.', 'The quality of being coy; feigned o/ bashful unwillingness\n   to become familiar; reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coyote', 'n.', 'A carnivorous animal (Canis latrans), allied to the dog,\n   found in the western part of North America; -- called also prairie\n   wolf. Its voice is a snapping bark, followed by a prolonged, shrill\n   howl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coypu', 'n.', 'A South American rodent (Myopotamus coypus), allied to the\n   beaver. It produces a valuable fur called nutria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coystrel', 'n.', 'Same as Coistril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coz', 'n.', 'A contraction of cousin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozened', 'imp. & p. p.', 'of Cozen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozening', 'p. pr. & vb. n.', 'of Cozen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozen', 'v. t.', 'To cheat; to defraud; to beguile; to deceive, usually by\n   small arts, or in a pitiful way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozen', 'v. i.', 'To deceive; to cheat; to act deceitfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozenage', 'n.', 'The art or practice of cozening; artifice; fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozener', 'n.', 'One who cheats or defrauds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozier', 'n.', 'See Cosier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozily', 'adv.', 'Snugly; comfortably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Coziness', 'n.', 'The state or quality of being cozy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozy', 'superl.', 'Snug; comfortable; easy; contented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozy', 'superl.', 'Chatty; talkative; sociable; familiar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cozy', 'a.', 'A wadded covering for a teakettle or other vessel to keep the\n   contents hot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'n.', 'One of the brachyuran Crustacea. They are mostly marine, and\n   usually have a broad, short body, covered with a strong shell or\n   carapace. The abdomen is small and curled up beneath the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'n.', 'The zodiacal constellation Cancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'a.', 'A crab apple; -- so named from its harsh taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'a.', 'A cudgel made of the wood of the crab tree; a crabstick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'a.', 'A movable winch or windlass with powerful gearing, used with\n   derricks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'a.', 'A form of windlass, or geared capstan, for hauling ships into\n   dock, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'a.', 'A machine used in ropewalks to stretch the yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'a.', 'A claw for anchoring a portable machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'v. t.', 'To make sour or morose; to embitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'v. t.', 'To beat with a crabstick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'v. i.', 'To drift sidewise or to leeward, as a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab', 'a.', 'Sour; rough; austere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabbed', 'n.', 'Characterized by or manifesting, sourness, peevishness, or\n   moroseness; harsh; cross; cynical; -- applied to feelings, disposition,\n   or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabbed', 'n.', 'Characterized by harshness or roughness; unpleasant; --\n   applied to things; as, a crabbed taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabbed', 'n.', 'Obscure; difficult; perplexing; trying; as, a crabbed\n   author.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabbed', 'n.', 'Cramped; irregular; as, crabbed handwriting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabber', 'n.', 'One who catches crabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabbing', 'n.', 'The act or art of catching crabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabbing', 'n.', 'The fighting of hawks with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabbing', 'n.', 'A process of scouring cloth between rolls in a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabbish', 'a.', 'Somewhat sour or cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabby', 'a.', 'Crabbed; difficult, or perplexing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabeater', 'n.', 'The cobia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabeater', 'n.', 'An etheostomoid fish of the southern United States\n   (Hadropterus nigrofasciatus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabeater', 'n.', 'A small European heron (Ardea minuta, and other allied\n   species).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craber', 'n.', 'The water rat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabfaced', 'a.', 'Having a sour, disagreeable countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabsidle', 'v. i.', 'To move sidewise, as a crab. [Jocular].');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crabstick', 'n.', 'A stick, cane, or cudgel, made of the wood of the crab\n   tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab tree', '', 'See under Crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crab-yaws', 'n.', 'A disease in the West Indies. It is a kind of ulcer on\n   the soles of the feet, with very hard edges. See Yaws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crache', 'v.', 'To scratch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracked', 'imp. & p. p.', 'of Crack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracking', 'p. pr. & vb. n.', 'of Crack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'v. t.', 'To break or burst, with or without entire separation of\n   the parts; as, to crack glass; to crack nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'v. t.', 'To rend with grief or pain; to affect deeply with sorrow;\n   hence, to disorder; to distract; to craze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'v. t.', 'To cause to sound suddenly and sharply; to snap; as, to\n   crack a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'v. t.', 'To utter smartly and sententiously; as, to crack a joke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'v. t.', 'To cry up; to extol; -- followed by up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'v. i.', 'To burst or open in chinks; to break, with or without\n   quite separating into parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'v. i.', 'To be ruined or impaired; to fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'v. i.', 'To utter a loud or sharp, sudden sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'v. i.', 'To utter vain, pompous words; to brag; to boast; -- with\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'A partial separation of parts, with or without a perceptible\n   opening; a chink or fissure; a narrow breach; a crevice; as, a crack in\n   timber, or in a wall, or in glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'Rupture; flaw; breach, in a moral sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'A sharp, sudden sound or report; the sound of anything\n   suddenly burst or broken; as, the crack of a falling house; the crack\n   of thunder; the crack of a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'The tone of voice when changed at puberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'Mental flaw; a touch of craziness; partial insanity; as, he\n   has a crack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'A crazy or crack-brained person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'A boast; boasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'Breach of chastity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'A boy, generally a pert, lively boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'A brief time; an instant; as, to be with one in a crack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'n.', 'Free conversation; friendly chat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack', 'a.', 'Of superior excellence; having qualities to be boasted of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crack-brained', 'a.', 'Having an impaired intellect; whimsical; crazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracked', 'a.', 'Coarsely ground or broken; as, cracked wheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracked', 'a.', 'Crack-brained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracker', 'n.', 'One who, or that which, cracks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracker', 'n.', 'A noisy boaster; a swaggering fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracker', 'n.', 'A small firework, consisting of a little powder inclosed\n   in a thick paper cylinder with a fuse, and exploding with a sharp\n   noise; -- often called firecracker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracker', 'n.', 'A thin, dry biscuit, often hard or crisp; as, a Boston\n   cracker; a Graham cracker; a soda cracker; an oyster cracker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracker', 'n.', 'A nickname to designate a poor white in some parts of the\n   Southern United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracker', 'n.', 'The pintail duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracker', 'n.', 'A pair of fluted rolls for grinding caoutchouc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crackle', 'v. i.', 'To make slight cracks; to make small, sharp, sudden\n   noises, rapidly or frequently repeated; to crepitate; as, burning\n   thorns crackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crackle', 'n.', 'The noise of slight and frequent cracks or reports; a\n   crackling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crackle', 'n.', 'A kind of crackling sound or r/le, heard in some abnormal\n   states of the lungs; as, dry crackle; moist crackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crackle', 'n.', 'A condition produced in certain porcelain, fine\n   earthenware, or glass, in which the glaze or enamel appears to be\n   cracked in all directions, making a sort of reticulated surface; as,\n   Chinese crackle; Bohemian crackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crackled', 'a.', 'Covered with minute cracks in the glaze; -- said of some\n   kinds of porcelain and fine earthenware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crackleware', 'n.', 'See Crackle, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crackling', 'n.', 'The making of small, sharp cracks or reports, frequently\n   repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crackling', 'n.', 'The well-browned, crisp rind of roasted pork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crackling', 'n.', 'Food for dogs, made from the refuse of tallow melting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracknel', 'v. t.', 'A hard brittle cake or biscuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracksmen', 'pl. ', 'of Cracksman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracksman', 'n.', 'A burglar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracovian', 'a.', 'Of or pertaining to Cracow in Poland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracovienne', 'n.', 'A lively Polish dance, in 2-4 time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cracowes', 'n. pl.', 'Long-toed boots or shoes formerly worn in many parts\n   of Europe; -- so called from Cracow, in Poland, where they were first\n   worn in the fourteenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'A bed or cot for a baby, oscillating on rockers or swinging\n   on pivots; hence, the place of origin, or in which anything is nurtured\n   or protected in the earlier period of existence; as, a cradle of crime;\n   the cradle of liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'Infancy, or very early life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'An implement consisting of a broad scythe for cutting\n   grain, with a set of long fingers parallel to the scythe, designed to\n   receive the grain, and to lay it evenly in a swath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'A tool used in mezzotint engraving, which, by a rocking\n   motion, raises burrs on the surface of the plate, so preparing the\n   ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'A framework of timbers, or iron bars, moving upon ways or\n   rollers, used to support, lift, or carry ships or other vessels, heavy\n   guns, etc., as up an inclined plane, or across a strip of land, or in\n   launching a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'A case for a broken or dislocated limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'A frame to keep the bedclothes from contact with the\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'A machine on rockers, used in washing out auriferous earth;\n   -- also called a rocker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'A suspended scaffold used in shafts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'The ribbing for vaulted ceilings and arches intended to be\n   covered with plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'n.', 'The basket or apparatus in which, when a line has been made\n   fast to a wrecked ship from the shore, the people are brought off from\n   the wreck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradled', 'imp. & p. p.', 'of Cradle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradling', 'p. pr. & vb. n.', 'of Cradle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'v. t.', 'To lay to rest, or rock, as in a cradle; to lull or\n   quiet, as by rocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'v. t.', 'To nurse or train in infancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'v. t.', 'To cut and lay with a cradle, as grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'v. t.', 'To transport a vessel by means of a cradle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradle', 'v. i.', 'To lie or lodge, as in a cradle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradling', 'n.', 'The act of using a cradle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradling', 'n.', 'Cutting a cask into two pieces lengthwise, to enable it\n   to pass a narrow place, the two parts being afterward united and\n   rehooped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradling', 'n.', 'The framework in arched or coved ceilings to which the\n   laths are nailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craft', 'n.', 'Strength; might; secret power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craft', 'n.', 'Art or skill; dexterity in particular manual employment;\n   hence, the occupation or employment itself; manual art; a trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craft', 'n.', 'Those engaged in any trade, taken collectively; a guild; as,\n   the craft of ironmongers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craft', 'n.', 'Cunning, art, or skill, in a bad sense, or applied to bad\n   purposes; artifice; guile; skill or dexterity employed to effect\n   purposes by deceit or shrewd devices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craft', 'n.', 'A vessel; vessels of any kind; -- generally used in a\n   collective sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craft', 'v. t.', 'To play tricks; to practice artifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craftily', 'adv.', 'With craft; artfully; cunningly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craftiness', 'n.', 'Dexterity in devising and effecting a purpose; cunning;\n   artifice; stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craftless', 'a.', 'Without craft or cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craftsmen', 'pl. ', 'of Craftsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craftsman', 'n.', 'One skilled in some trade or manual occupation; an\n   artificer; a mechanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craftsmanship', 'n.', 'The work of a craftsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craftsmaster', 'n.', 'One skilled in his craft or trade; one of superior\n   cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crafty', 'a.', 'Relating to, or characterized by, craft or skill;\n   dexterous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crafty', 'a.', 'Possessing dexterity; skilled; skillful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crafty', 'a.', 'Skillful at deceiving others; characterized by craft;\n   cunning; wily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crag', 'n.', 'A steep, rugged rock; a rough, broken cliff, or point of a\n   rock, on a ledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crag', 'n.', 'A partially compacted bed of gravel mixed with shells, of the\n   Tertiary age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crag', 'n.', 'The neck or throat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crag', 'n.', 'The neck piece or scrag of mutton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cragged', 'a.', 'Full of crags, or steep, broken //cks; abounding with\n   prominences, points, and inequalities; rough; rugged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cradgedness', 'n.', 'The quality or state of being cragged; cragginess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cragginess', 'n.', 'The state of being craggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craggy', 'a.', 'Full of crags; rugged with projecting points of rocks; as,\n   the craggy side of a mountain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cragsmen', 'pl. ', 'of Cragsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cragsman', 'n.', 'One accustomed to climb rocks or crags; esp., one who\n   makes a business of climbing the cliffs overhanging the sea to get the\n   eggs of sea birds or the birds themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craie', 'n.', 'See Crare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craig flounder', '', 'The pole flounder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crail', 'n.', 'A creel or osier basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crake', 'v. t. & i.', 'To cry out harshly and loudly, like the bird called\n   crake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crake', 'v. t. & i.', 'To boast; to speak loudly and boastfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crake', 'n.', 'A boast. See Crack, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crake', 'n.', 'Any species or rail of the genera Crex and Porzana; -- so\n   called from its singular cry. See Corncrake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crakeberry', 'n.', 'See Crowberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craker', 'n.', 'One who boasts; a braggart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crammed', 'imp. & p. p.', 'of Cram');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramming', 'p. pr. & vb. n.', 'of Cram');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cram', 'v. t.', 'To press, force, or drive, particularly in filling, or in\n   thrusting one thing into another; to stuff; to crowd; to fill to\n   superfluity; as, to cram anything into a basket; to cram a room with\n   people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cram', 'v. t.', 'To fill with food to satiety; to stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cram', 'v. t.', 'To put hastily through an extensive course of memorizing\n   or study, as in preparation for an examination; as, a pupil is crammed\n   by his tutor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cram', 'v. i.', 'To eat greedily, and to satiety; to stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cram', 'v. i.', 'To make crude preparation for a special occasion, as an\n   examination, by a hasty and extensive course of memorizing or study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cram', 'n.', 'The act of cramming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cram', 'n.', 'Information hastily memorized; as, a cram from an\n   examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cram', 'n.', 'A warp having more than two threads passing through each dent\n   or split of the reed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crambo', 'a.', 'A game in which one person gives a word, to which another\n   finds a rhyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crambo', 'a.', 'A word rhyming with another word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crammer', 'n.', 'One who crams; esp., one who prepares a pupil hastily for\n   an examination, or a pupil who is thus prepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramoisie', 'a.', 'Alt. of Cramoisy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramoisy', 'a.', 'Crimson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'n.', 'That which confines or contracts; a restraint; a shackle; a\n   hindrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'n.', 'A device, usually of iron bent at the ends, used to hold\n   together blocks of stone, timbers, etc.; a cramp iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'n.', 'A rectangular frame, with a tightening screw, used for\n   compressing the joints of framework, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'n.', 'A piece of wood having a curve corresponding to that of the\n   upper part of the instep, on which the upper leather of a boot is\n   stretched to give it the requisite shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'n.', 'A spasmodic and painful involuntary contraction of a muscle\n   or muscles, as of the leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramped', 'imp. & p. p.', 'of Cramp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramping', 'p. pr. & vb. n.', 'of Cramp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'v. t.', 'To compress; to restrain from free action; to confine and\n   contract; to hinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'v. t.', 'To fasten or hold with, or as with, a cramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'v. t.', 'to bind together; to unite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'v. t.', 'To form on a cramp; as, to cramp boot legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'v. t.', 'To afflict with cramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp', 'n.', 'Knotty; difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crampet', 'n.', 'A cramp iron or cramp ring; a chape, as of a scabbard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crampfish', 'n.', 'The torpedo, or electric ray, the touch of which gives\n   an electric shock. See Electric fish, and Torpedo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramp iron', '', 'See Cramp, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crampit', 'n.', 'See Crampet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crampon', 'n.', 'An a/rial rootlet for support in climbing, as of ivy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cramponee', 'a.', 'Having a cramp or square piece at the end; -- said of a\n   cross so furnished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crampoons', 'n.', 'A clutch formed of hooked pieces of iron, like double\n   calipers, for raising stones, lumber, blocks of ice, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crampoons', 'n.', 'Iron instruments with sharp points, worn on the shoes to\n   assist in gaining or keeping a foothold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crampy', '', 'Affected with cramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crampy', '', 'Productive of, or abounding in, cramps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cran', 'n.', 'Alt. of Crane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crane', 'n.', 'A measure for fresh herrings, -- as many as will fill a\n   barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranage', 'n.', 'The liberty of using a crane, as for loading and unloading\n   vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranage', 'n.', 'The money or price paid for the use of a crane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranberries', 'pl. ', 'of Cranberry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranberry', 'n.', 'A red, acid berry, much used for making sauce, etc.;\n   also, the plant producing it (several species of Vaccinum or\n   Oxycoccus.) The high cranberry or cranberry tree is a species of\n   Viburnum (V. Opulus), and the other is sometimes called low cranberry\n   or marsh cranberry to distinguish it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranch', 'v. t.', 'See Craunch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crane', 'n.', 'A wading bird of the genus Grus, and allied genera, of\n   various species, having a long, straight bill, and long legs and neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crane', 'n.', 'A machine for raising and lowering heavy weights, and, while\n   holding them suspended, transporting them through a limited lateral\n   distance. In one form it consists of a projecting arm or jib of timber\n   or iron, a rotating post or base, and the necessary tackle, windlass,\n   etc.; -- so called from a fancied similarity between its arm and the\n   neck of a crane See Illust. of Derrick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crane', 'n.', 'An iron arm with horizontal motion, attached to the side or\n   back of a fireplace, for supporting kettles, etc., over a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crane', 'n.', 'A siphon, or bent pipe, for drawing liquors out of a cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crane', 'n.', 'A forked post or projecting bracket to support spars, etc.,\n   -- generally used in pairs. See Crotch, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craned', 'imp. & p. p.', 'of Crane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craning', 'p. pr. & vb. n.', 'of Crane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crane', 'v. t.', 'To cause to rise; to raise or lift, as by a crane; --\n   with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crane', 'v. t.', 'To stretch, as a crane stretches its neck; as, to crane\n   the neck disdainfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crane', 'v. i.', 'to reach forward with head and neck, in order to see\n   better; as, a hunter cranes forward before taking a leap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranes-bill', 'n.', 'The geranium; -- so named from the long axis of the\n   fruit, which resembles the beak of a crane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranes-bill', 'n.', 'A pair of long-beaked forceps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crang', 'n.', 'See Krang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crania', 'n.', 'A genus of living Brachiopoda; -- so called from its\n   fancied resemblance to the cranium or skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranial', 'a.', 'Of or pertaining to the cranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranioclasm', 'n.', 'The crushing of a childs head, as with the\n   cranioclast or craniotomy forceps in cases of very difficult delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranioclast', 'n.', 'An instrument for crushing the head of a fetus, to\n   facilitate delivery in difficult eases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniofacial', 'a.', 'Of or pertaining to the cranium and face; as, the\n   craniofacial angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniognomy', 'n.', 'The science of the form and characteristics of the\n   skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniological', 'a.', 'Of or pertaining to craniology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniologist', 'n.', 'One proficient in craniology; a phrenologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniology', 'n.', 'The department of science (as of ethnology or\n   archaeology) which deals with the shape, size, proportions,\n   indications, etc., of skulls; the study of skulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniometer', 'n.', 'An instrument for measuring the size of skulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniometric', 'a.', 'Alt. of Craniometrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniometrical', 'a.', 'Pertaining to craniometry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniometry', 'n.', 'The art or act of measuring skulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranioscopist', 'n.', 'One skilled in, or who practices, cranioscopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranioscopy', 'n.', 'Scientific examination of the cranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniota', 'n. pl.', 'A comprehensive division of the Vertebrata, including\n   all those that have a skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniotomy', 'n.', 'The operation of opening the fetal head, in order to\n   effect delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craniums', 'pl. ', 'of Cranium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crania', 'pl. ', 'of Cranium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranium', 'n.', 'The skull of an animal; especially, that part of the\n   skull, either cartilaginous or bony, which immediately incloses the\n   brain; the brain case or brainpan. See Skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crank', 'n.', 'A bent portion of an axle, or shaft, or an arm keyed at\n   right angles to the end of a shaft, by which motion is imparted to or\n   received from it; also used to change circular into reciprocating\n   motion, or reciprocating into circular motion. See Bell crank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crank', 'n.', 'Any bend, turn, or winding, as of a passage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crank', 'n.', 'A twist or turn in speech; a conceit consisting in a change\n   of the form or meaning of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crank', 'n.', 'A twist or turn of the mind; caprice; whim; crotchet; also,\n   a fit of temper or passion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crank', 'n.', 'A person full of crotchets; one given to fantastic or\n   impracticable projects; one whose judgment is perverted in respect to a\n   particular matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crank', 'n.', 'A sick person; an invalid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crank', 'n.', 'Sick; infirm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crank', 'n.', 'Liable to careen or be overset, as a ship when she is too\n   narrow, or has not sufficient ballast, or is loaded too high, to carry\n   full sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crank', 'n.', 'Full of spirit; brisk; lively; sprightly; overconfident;\n   opinionated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crank', 'n.', 'To run with a winding course; to double; to crook; to wind\n   and turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crankbird', 'n.', 'A small European woodpecker (Picus minor).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranked', 'a.', 'Formed with, or having, a bend or crank; as, a cranked\n   axle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crankiness', 'n.', 'Crankness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crankle', 'v. t.', 'To break into bends, turns, or angles; to crinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crankle', 'v. i.', 'To bend, turn, or wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crankle', 'n.', 'A bend or turn; a twist; a crinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crankness', 'n.', 'Liability to be overset; -- said of a ship or other\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crankness', 'n.', 'Sprightliness; vigor; health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranky', 'a.', 'Full of spirit; crank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranky', 'a.', 'Addicted to crotchets and whims; unreasonable in opinions;\n   crotchety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranky', 'a.', 'Unsteady; easy to upset; crank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crannied', 'a.', 'Having crannies, chinks, or fissures; as, a crannied\n   wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crannog', 'n.', 'Alt. of Crannoge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crannoge', 'n.', 'One of the stockaded islands in Scotland and Ireland\n   which in ancient times were numerous in the lakes of both countries.\n   They may be regarded as the very latest class of prehistoric\n   strongholds, reaching their greatest development in early historic\n   times, and surviving through the Middle Ages. See also Lake dwellings,\n   under Lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crannies', 'pl. ', 'of Cranny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranny', 'n.', 'A small, narrow opening, fissure, crevice, or chink, as in\n   a wall, or other substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranny', 'n.', 'A tool for forming the necks of bottles, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crannied', 'imp. & p. p.', 'of Cranny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crannying', 'p. pr. & vb. n.', 'of Cranny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranny', 'v. i.', 'To crack into, or become full of, crannies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranny', 'v. i.', 'To haunt, or enter by, crannies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cranny', 'a.', 'Quick; giddy; thoughtless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crantara', 'n.', 'The fiery cross, used as a rallying signal in the\n   Highlands of Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crants', 'n.', 'A garland carried before the bier of a maiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapaudine', 'n.', 'Turning on pivots at the top and bottom; -- said of a\n   door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapaudine', 'n.', 'An ulcer on the coronet of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crape', 'n.', 'A thin, crimped stuff, made of raw silk gummed and twisted\n   on the mill. Black crape is much used for mourning garments, also for\n   the dress of some clergymen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craped', 'imp. & p. p.', 'of Crape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craping', 'p. pr. & vb. n.', 'of Crape');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crape', 'n.', 'To form into ringlets; to curl; to crimp; to friz; as, to\n   crape the hair; to crape silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapefish', 'n.', 'Salted codfish hardened by pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapnel', 'n.', 'A hook or drag; a grapnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crappie', 'n.', 'A kind of fresh-water bass of the genus Pomoxys, found in\n   the rivers of the Southern United States and Mississippi valley. There\n   are several species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapple', 'n.', 'A claw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craps', 'n.', 'A gambling game with dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapula', 'n.', 'Alt. of Crapule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapule', 'n.', 'Same as Crapulence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapulence', 'n.', 'The sickness occasioned by intemperance; surfeit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapulent', 'a.', 'Alt. of Crapulous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapulous', 'a.', 'Surcharged with liquor; sick from excessive indulgence\n   in liquor; drunk; given to excesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crapy', 'a.', 'Resembling crape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crare', 'n.', 'A slow unwieldy trading vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crase', 'v. t.', 'To break in pieces; to crack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crashed', 'imp. & p. p.', 'of Crash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crashing', 'p. pr. & vb. n.', 'of Crash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crash', 'v. t. ', 'To break in pieces violently; to dash together with\n   noise and violence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crash', 'v. i.', 'To make a loud, clattering sound, as of many things\n   falling and breaking at once; to break in pieces with a harsh noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crash', 'v. i.', 'To break with violence and noise; as, the chimney in\n   falling crashed through the roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crash', 'n.', 'A loud, sudden, confused sound, as of many things falling\n   and breaking at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crash', 'n.', 'Ruin; failure; sudden breaking down, as of a business house\n   or a commercial enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crash', 'n.', 'Coarse, heavy, narrow linen cloth, used esp. for towels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crashing', 'n.', 'The noise of many things falling and breaking at once.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crasis', 'n.', 'A mixture of constituents, as of the blood; constitution;\n   temperament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crasis', 'n.', 'A contraction of two vowels (as the final and initial\n   vowels of united words) into one long vowel, or into a diphthong;\n   synaeresis; as, cogo for coago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craspedota', 'n. pl.', 'The hydroid or naked-eyed medusae. See Hydroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craspedote', 'a.', 'Of or pertaining to the Craspedota.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crass', 'a.', 'Gross; thick; dense; coarse; not elaborated or refined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crassament', 'a.', 'Alt. of Crassamentum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crassamentum', 'a.', 'A semisolid mass or clot, especially that formed in\n   coagulation of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crassiment', 'n.', 'See Crassament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crassitude', 'n.', 'Grossness; coarseness; thickness; density.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crassness', 'n.', 'Grossness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crastination', 'n.', 'Procrastination; a putting off till to-morrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crataegus', 'n.', 'A genus of small, hardy trees, including the hawthorn,\n   much used for ornamental purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cratch', 'n.', 'A manger or open frame for hay; a crib; a rack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crate', 'n.', 'A large basket or hamper of wickerwork, used for the\n   transportation of china, crockery, and similar wares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crate', 'n.', 'A box or case whose sides are of wooden slats with\n   interspaces, -- used especially for transporting fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crated', 'imp. & p. p.', 'of Crate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crating', 'p. pr. & vb. n.', 'of Crate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crate', 'v. t.', 'To pack in a crate or case for transportation; as, to\n   crate a sewing machine; to crate peaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crater', 'n.', 'The basinlike opening or mouth of a volcano, through which\n   the chief eruption comes; similarly, the mouth of a geyser, about which\n   a cone of silica is often built up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crater', 'n.', 'The pit left by the explosion of a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crater', 'n.', 'A constellation of the southen hemisphere; -- called also\n   the Cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crateriform', 'a.', 'Having the form of a shallow bowl; -- said of a\n   corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craterous', 'a.', 'Pertaining to, or resembling, a crater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craunched', 'imp. & p. p.', 'of Craunch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craunching', 'p. pr. & vb. n.', 'of Craunch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craunch', 'v. t. & i.', 'To crush with the teeth; to chew with violence and\n   noise; to crunch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cravat', 'n.', 'A neckcloth; a piece of silk, fine muslin, or other cloth,\n   worn by men about the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cravatted', 'a.', 'Wearing a cravat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craved', 'imp. & p. p.', 'of Crave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craving', 'p pr. & vb. n.', 'of Crave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crave', 'v. t.', 'To ask with earnestness or importunity; to ask with\n   submission or humility; to beg; to entreat; to beseech; to implore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crave', 'v. t.', 'To call for, as a gratification; to long for; hence, to\n   require or demand; as, the stomach craves food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crave', 'v. i.', 'To desire strongly; to feel an insatiable longing; as, a\n   craving appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craven', 'a.', 'Cowardly; fainthearted; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craven', 'n.', 'A recreant; a coward; a weak-hearted, spiritless fellow.\n   See Recreant, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cravened', 'imp. & p. p.', 'of Craven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cravening', 'p. pr. & vb. n.', 'of Craven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craven', 'v. t.', 'To make recreant, weak, spiritless, or cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craver', 'n.', 'One who craves or begs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craving', 'n.', 'Vehement or urgent desire; longing for; beseeching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craw', 'n.', 'The crop of a bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craw', 'n.', 'The stomach of an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-fishes', 'pl. ', 'of Crayfish');
  INSERT INTO words (word, word_type, description) VALUES  (    '-fish', 'pl. ', 'of Crayfish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawfish', 'n.', 'Alt. of Crayfish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crayfish', 'n.', 'Any crustacean of the family Astacidae, resembling the\n   lobster, but smaller, and found in fresh waters. Crawfishes are\n   esteemed very delicate food both in Europe and America. The North\n   American species are numerous and mostly belong to the genus Cambarus.\n   The blind crawfish of the Mammoth Cave is Cambarus pellucidus. The\n   common European species is Astacus fluviatilis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawford', 'n.', 'A Crawford peach; a well-known freestone peach, with\n   yellow flesh, first raised by Mr. William Crawford, of New Jersey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawled', 'imp. & p. p.', 'of Crawl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawling', 'p. pr. & vb. n.', 'of Crawl');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawl', 'v. i.', 'To move slowly by drawing the body along the ground, as a\n   worm; to move slowly on hands and knees; to creep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawl', 'v. i.', 'to move or advance in a feeble, slow, or timorous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawl', 'v. i.', 'To advance slowly and furtively; to insinuate ones self;\n   to advance or gain influence by servile or obsequious conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawl', 'v. i.', 'To have a sensation as of insect creeping over the body;\n   as, the flesh crawls. See Creep, v. i., 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawl', 'n.', 'The act or motion of crawling; slow motion, as of a creeping\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawl', 'n.', 'A pen or inclosure of stakes and hurdles on the seacoast,\n   for holding fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawler', 'n.', 'One who, or that which, crawls; a creeper; a reptile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crawly', 'a.', 'Creepy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cray', 'n.', 'Alt. of Crayer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crayer', 'n.', 'See Crare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crayfish', 'n.', 'See Crawfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crayon', 'n.', 'An implement for drawing, made of clay and plumbago, or of\n   some preparation of chalk, usually sold in small prisms or cylinders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crayon', 'n.', 'A crayon drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crayon', 'n.', 'A pencil of carbon used in producing electric light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crayoned', 'imp. & p. p.', 'of Crayon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crayoning', 'p. pr. & vb. n.', 'of Crayon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crayon', 'v. t.', 'To sketch, as with a crayon; to sketch or plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crazed', 'imp. & p. p.', 'of Craze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crazing', 'p. pr. & vb. n.', 'of Craze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craze', 'v. t.', 'To break into pieces; to crush; to grind to powder. See\n   Crase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craze', 'v. t.', 'To weaken; to impair; to render decrepit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craze', 'v. t.', 'To derange the intellect of; to render insane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craze', 'v. i.', 'To be crazed, or to act or appear as one that is crazed;\n   to rave; to become insane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craze', 'v. i.', 'To crack, as the glazing of porcelain or pottery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craze', 'n.', 'Craziness; insanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craze', 'n.', 'A strong habitual desire or fancy; a crotchet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craze', 'n.', 'A temporary passion or infatuation, as for same new\n   amusement, pursuit, or fashion; as, the bric-a-brac craze; the\n   aesthetic craze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crazedness', 'n.', 'A broken state; decrepitude; an impaired state of the\n   intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craze-mill', 'n.', 'Alt. of Crazing-mill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crazing-mill', 'n.', 'A mill for grinding tin ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crazily', 'adv.', 'In a crazy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craziness', 'n.', 'The state of being broken down or weakened; as, the\n   craziness of a ship, or of the limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Craziness', 'n.', 'The state of being broken in mind; imbecility or\n   weakness of intellect; derangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crazy', 'a.', 'Characterized by weakness or feebleness; decrepit; broken;\n   falling to decay; shaky; unsafe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crazy', 'a.', 'Broken, weakened, or dissordered in intellect; shattered;\n   demented; deranged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crazy', 'a.', 'Inordinately desirous; foolishly eager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creable', 'a.', 'Capable of being created.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaght', 'n.', 'A drove or herd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaght', 'v. i.', 'To graze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaked', 'imp. & p. p.', 'of Creak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaking', 'p. pr. & vb. n.', 'of Creak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creak', 'v. i.', 'To make a prolonged sharp grating or squeaking sound, as\n   by the friction of hard substances; as, shoes creak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creak', 'v. t.', 'To produce a creaking sound with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creak', 'n.', 'The sound produced by anything that creaks; a creaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaking', 'n.', 'A harsh grating or squeaking sound, or the act of making\n   such a sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream', 'n.', 'The rich, oily, and yellowish part of milk, which, when the\n   milk stands unagitated, rises, and collects on the surface. It is the\n   part of milk from which butter is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream', 'n.', 'The part of any liquor that rises, and collects on the\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream', 'n.', 'A delicacy of several kinds prepared for the table from\n   cream, etc., or so as to resemble cream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream', 'n.', 'A cosmetic; a creamlike medicinal preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream', 'n.', 'The best or choicest part of a thing; the quintessence; as,\n   the cream of a jest or story; the cream of a collection of books or\n   pictures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creamed', 'imp. & p. p.', 'of Cream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaming', 'p. pr. & vb. n.', 'of Cream');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream', 'v. t.', 'To skim, or take off by skimming, as cream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream', 'v. t.', 'To take off the best or choicest part of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream', 'v. t.', 'To furnish with, or as with, cream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream', 'v. i.', 'To form or become covered with cream; to become thick\n   like cream; to assume the appearance of cream; hence, to grow stiff or\n   formal; to mantle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creamcake', 'n.', 'A kind of cake filled with custard made of cream, eggs,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream-colored', 'a.', 'Of the color of cream; light yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creameries', 'pl. ', 'of Creamery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creamery', 'n.', 'A place where butter and cheese are made, or where milk\n   and cream are put up in cans for market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creamery', 'n.', 'A place or apparatus in which milk is set for raising\n   cream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creamery', 'n.', 'An establishment where cream is sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream-faced', 'a.', 'White or pale, as the effect of fear, or as the\n   natural complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream-fruit', 'n.', 'A plant of Sierra Leone which yields a wholesome,\n   creamy juice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaminess', 'n.', 'The quality of being creamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream laid', '', 'See under Laid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream-slice', 'n.', 'A wooden knife with a long thin blade, used in\n   handling cream or ice cream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cream-white', 'a.', 'As white as cream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creamy', 'a.', 'Full of, or containing, cream; resembling cream, in nature,\n   appearance, or taste; creamlike; unctuous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creance', 'n.', 'Faith; belief; creed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creance', 'n.', 'A fine, small line, fastened to a hawks leash, when it is\n   first lured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creance', 'v. i. & t.', 'To get on credit; to borrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creant', 'a.', 'Creative; formative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crease', 'n.', 'See Creese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crease', 'n.', 'A line or mark made by folding or doubling any pliable\n   substance; hence, a similar mark, however produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crease', 'n.', 'One of the lines serving to define the limits of the bowler\n   and the striker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creased', 'imp. & p. p.', 'of Crease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creasing', 'p. pr. & vb. n.', 'of Crease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crease', 'v. t.', 'To make a crease or mark in, as by folding or doubling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaser', 'n.', 'A tool, or a sewing-machine attachment, for making lines\n   or creases on leather or cloth, as guides to sew by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaser', 'n.', 'A tool for making creases or beads, as in sheet iron, or\n   for rounding small tubes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaser', 'n.', 'A tool for making the band impression distinct on the\n   back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creasing', 'n.', 'A layer of tiles forming a corona for a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creasote', 'n.', 'See Creosote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creasy', 'a.', 'Full of creases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creat', 'n.', 'An usher to a riding master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creatable', 'a.', 'That may be created.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Create', 'a.', 'Created; composed; begotten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Created', 'imp. & p. p.', 'of Create');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creating', 'p. pr. & vb. n.', 'of Create');
  INSERT INTO words (word, word_type, description) VALUES  (    'Create', 'v. t.', 'To bring into being; to form out of nothing; to cause to\n   exist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Create', 'v. t.', 'To effect by the agency, and under the laws, of\n   causation; to be the occasion of; to cause; to produce; to form or\n   fashion; to renew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Create', 'v. t.', 'To invest with a new form, office, or character; to\n   constitute; to appoint; to make; as, to create one a peer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creatic', 'a.', 'Relating to, or produced by, flesh or animal food; as,\n   creatic nausea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creatin', 'n.', 'A white, crystalline, nitrogenous substance found\n   abundantly in muscle tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creatinin', 'n.', 'A white, crystalline, nitrogenous body closely related\n   to creatin but more basic in its properties, formed from the latter by\n   the action of acids, and occurring naturally in muscle tissue and in\n   urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creation', 'n.', 'The act of creating or causing to exist. Specifically,\n   the act of bringing the universe or this world into existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creation', 'n.', 'That which is created; that which is produced or caused\n   to exist, as the world or some original work of art or of the\n   imagination; nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creation', 'n.', 'The act of constituting or investing with a new\n   character; appointment; formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creational', 'a.', 'Of or pertaining to creation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creationism', 'n.', 'The doctrine that a soul is specially created for each\n   human being as soon as it is formed in the womb; -- opposed to\n   traducianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creative', 'a.', 'Having the power to create; exerting the act of creation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creativeness', 'n.', 'The quality of being creative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretor', 'n.', 'One who creates, produces, or constitutes. Specifically,\n   the Supreme Being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creatorship', 'n.', 'State or condition of a creator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creatress', 'n.', 'She who creates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creatrix', 'n.', 'A creatress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creatural', 'a.', 'Belonging to a creature; having the qualities of a\n   creature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creature', 'n.', 'Anything created; anything not self-existent; especially,\n   any being created with life; an animal; a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creature', 'n.', 'A human being, in pity, contempt, or endearment; as, a\n   poor creature; a pretty creature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creature', 'n.', 'A person who owes his rise and fortune to another; a\n   servile dependent; an instrument; a tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creature', 'n.', 'A general term among farmers for horses, oxen, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cratureless', 'a.', 'Without created beings; alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaturely', 'a.', 'Creatural; characteristic of a creature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creatureship', 'n.', 'The condition of being a creature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaturize', 'v. t.', 'To make like a creature; to degrade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creaze', 'n.', 'The tin ore which collects in the central part of the\n   washing pit or buddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crebricostate', 'a.', 'Marked with closely set ribs or ridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crebrisulcate', 'a.', 'Marked with closely set transverse furrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crebritude', 'n.', 'Frequency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crebrous', 'a.', 'Frequent; numerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cr/che', 'n.', 'A public nursery, where the young children of poor women\n   are cared for during the day, while their mothers are at work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credence', 'n.', 'Reliance of the mind on evidence of facts derived from\n   other sources than personal knowledge; belief; credit; confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credence', 'n.', 'That which gives a claim to credit, belief, or\n   confidence; as, a letter of credence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credence', 'n.', 'The small table by the side of the altar or communion\n   table, on which the bread and wine are placed before being consecrated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credence', 'n.', 'A cupboard, sideboard, or cabinet, particularly one\n   intended for the display of rich vessels or plate, and consisting\n   chiefly of open shelves for that purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credence', 'v. t.', 'To give credence to; to believe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credenda', 'pl. ', 'of Credendum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credendum', 'n.', 'A thing to be believed; an article of faith; --\n   distinguished from agendum, a practical duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credent', 'a.', 'Believing; giving credence; credulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credent', 'a.', 'Having credit or authority; credible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credential', 'a.', 'Giving a title or claim to credit or confidence;\n   accrediting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credential', 'n.', 'That which gives a title to credit or confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credential', 'n.', 'Testimonials showing that a person is entitled to\n   credit, or has right to exercise official power, as the letters given\n   by a government to an ambassador or envoy, or a certificate that one is\n   a duly elected delegate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credibility', 'n.', 'The quality of being credible; credibleness; as, the\n   credibility of facts; the credibility of witnesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credible', 'a.', 'Capable of being credited or believed; worthy of belief;\n   entitled to confidence; trustworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credibleness', 'n.', 'The quality or state of being credible; worthiness of\n   belief; credibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credibly', 'adv.', 'In a manner inducing belief; as, I have been credibly\n   informed of the event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'n.', 'Reliance on the truth of something said or done; belief;\n   faith; trust; confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'n.', 'Reputation derived from the confidence of others; esteem;\n   honor; good name; estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'n.', 'A ground of, or title to, belief or confidence; authority\n   derived from character or reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'n.', 'That which tends to procure, or add to, reputation or\n   esteem; an honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'n.', 'Influence derived from the good opinion, confidence, or\n   favor of others; interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'n.', 'Trust given or received; expectation of future playment for\n   property transferred, or of fulfillment or promises given; mercantile\n   reputation entitling one to be trusted; -- applied to individuals,\n   corporations, communities, or nations; as, to buy goods on credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'n.', 'The time given for payment for lands or goods sold on\n   trust; as, a long credit or a short credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'n.', 'The side of an account on which are entered all items\n   reckoned as values received from the party or the category named at the\n   head of the account; also, any one, or the sum, of these items; -- the\n   opposite of debit; as, this sum is carried to ones credit, and that to\n   his debit; A has several credits on the books of B.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credited', 'imp. & p. p.', 'of Credit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crediting', 'p. pr. & vb. n.', 'of Credit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'v. t.', 'To confide in the truth of; to give credence to; to put\n   trust in; to believe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'v. t.', 'To bring honor or repute upon; to do credit to; to raise\n   the estimation of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit', 'v. t.', 'To enter upon the credit side of an account; to give\n   credit for; as, to credit the amount paid; to set to the credit of; as,\n   to credit a man with the interest paid on a bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creditable', 'a.', 'Worthy of belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creditable', 'a.', 'Deserving or possessing reputation or esteem;\n   reputable; estimable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creditable', 'a.', 'Bringing credit, reputation, or honor; honorable; as,\n   such conduct is highly creditable to him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creditableness', 'n.', 'The quality of being creditable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creditably', 'adv.', 'In a creditable manner; reputably; with credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit foncier', '', 'A company licensed for the purpose of carrying out\n   improvements, by means of loans and advances upon real securities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credit mobilier', '', 'A joint stock company, formed for general banking\n   business, or for the construction of public works, by means of loans on\n   personal estate, after the manner of the credit foncier on real estate.\n   In practice, however, this distinction has not been strictly observed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creditor', 'n.', 'One who credits, believes, or trusts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creditor', 'n.', 'One who gives credit in business matters; hence, one to\n   whom money is due; -- correlative to debtor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creditress', 'n.', 'Alt. of Creditrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creditrix', 'n.', 'A female creditor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credo', 'n.', 'The creed, as sung or read in the Roman Catholic church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credulity', 'n.', 'Readiness of belief; a disposition to believe on slight\n   evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credulous', 'a.', 'Apt to believe on slight evidence; easily imposed upon;\n   unsuspecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credulous', 'a.', 'Believed too readily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credulously', 'adv.', 'With credulity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Credulousness', 'n.', 'Readiness to believe on slight evidence; credulity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creed', 'v. t.', 'A definite summary of what is believed; esp., a summary\n   of the articles of Christian faith; a confession of faith for public\n   use; esp., one which is brief and comprehensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creed', 'v. t.', 'Any summary of principles or opinions professed or\n   adhered to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creed', 'v. t.', 'To believe; to credit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creedless', 'a.', 'Without a creed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creek', 'n.', 'A small inlet or bay, narrower and extending further into\n   the land than a cove; a recess in the shore of the sea, or of a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creek', 'n.', 'A stream of water smaller than a river and larger than a\n   brook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creek', 'n.', 'Any turn or winding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creekfish', 'n.', 'The chub sucker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeks', 'n. pl.', 'A tribe or confederacy of North American Indians,\n   including the Muskogees, Seminoles, Uchees, and other subordinate\n   tribes. They formerly inhabited Georgia, Florida, and Alabama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeky', 'a.', 'Containing, or abounding in, creeks; characterized by\n   creeks; like a creek; winding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creel', 'n.', 'An osier basket, such as anglers use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creel', 'n.', 'A bar or set of bars with skewers for holding paying-off\n   bobbins, as in the roving machine, throstle, and mule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crept', 'imp.', 'of Creep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crope', '', 'of Creep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crept', 'p. p.', 'of Creep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeping', 'p. pr. & vb. n.', 'of Creep');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'v. t.', 'To move along the ground, or on any other surface, on the\n   belly, as a worm or reptile; to move as a child on the hands and knees;\n   to crawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'v. t.', 'To move slowly, feebly, or timorously, as from\n   unwillingness, fear, or weakness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'v. t.', 'To move in a stealthy or secret manner; to move\n   imperceptibly or clandestinely; to steal in; to insinuate itself or\n   ones self; as, age creeps upon us.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'v. t.', 'To slip, or to become slightly displaced; as, the\n   collodion on a negative, or a coat of varnish, may creep in drying; the\n   quicksilver on a mirror may creep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'v. t.', 'To move or behave with servility or exaggerated humility;\n   to fawn; as, a creeping sycophant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'v. t.', 'To grow, as a vine, clinging to the ground or to some\n   other support by means of roots or rootlets, or by tendrils, along its\n   length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'v. t.', 'To have a sensation as of insects creeping on the skin of\n   the body; to crawl; as, the sight made my flesh creep. See Crawl, v.\n   i., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'v. i.', 'To drag in deep water with creepers, as for recovering a\n   submarine cable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'n.', 'The act or process of creeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'n.', 'A distressing sensation, or sound, like that occasioned by\n   the creeping of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creep', 'n.', 'A slow rising of the floor of a gallery, occasioned by the\n   pressure of incumbent strata upon the pillars or sides; a gradual\n   movement of mining ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeper', 'n.', 'One who, or that which, creeps; any creeping thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeper', 'n.', 'A plant that clings by rootlets, or by tendrils, to the\n   ground, or to trees, etc.; as, the Virginia creeper (Ampelopsis\n   quinquefolia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeper', 'n.', 'A small bird of the genus Certhia, allied to the wrens.\n   The brown or common European creeper is C. familiaris, a variety of\n   which (var. Americana) inhabits America; -- called also tree creeper\n   and creeptree. The American black and white creeper is Mniotilta varia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeper', 'n.', 'A kind of patten mounted on short pieces of iron instead\n   of rings; also, a fixture with iron points worn on a shoe to prevent\n   one from slipping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeper', 'n.', 'A spurlike device strapped to the boot, which enables one\n   to climb a tree or pole; -- called often telegraph creepers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeper', 'n.', 'A small, low iron, or dog, between the andirons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeper', 'n.', 'An instrument with iron hooks or claws for dragging at the\n   bottom of a well, or any other body of water, and bringing up what may\n   lie there.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeper', 'n.', 'Any device for causing material to move steadily from one\n   part of a machine to another, as an apron in a carding machine, or an\n   inner spiral in a grain screen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeper', 'n.', 'Crockets. See Crocket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creephole', 'n.', 'A hole or retreat into which an animal may creep, to\n   escape notice or danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creephole', 'n.', 'A subterfuge; an excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creepie', 'n.', 'A low stool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creepiness', 'n.', 'An uneasy sensation as of insects creeping on the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeping', 'a.', 'Crawling, or moving close to the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeping', 'a.', 'Growing along, and clinging to, the ground, or to a wall,\n   etc., by means of rootlets or tendrils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creepingly', 'adv.', 'by creeping slowly; in the manner of a reptile;\n   insidiously; cunningly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeple', 'n.', 'A creeping creature; a reptile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creeple', 'n.', 'One who is lame; a cripple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creepy', 'a.', 'Crawly; having or producing a sensation like that caused by\n   insects creeping on the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crees', 'n. pl.', 'An Algonquin tribe of Indians, inhabiting a large part\n   of British America east of the Rocky Mountains and south of Hudsons\n   Bay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creese', 'n.', 'A dagger or short sword used by the Malays, commonly having\n   a serpentine blade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremaillere', 'n.', 'An indented or zigzaged line of intrenchment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremaster', 'n.', 'A thin muscle which serves to draw up the testicle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremaster', 'n.', 'The apex of the last abdominal segment of an insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremasteric', 'a.', 'Of or pertaining to the cremaster; as, the cremasteric\n   artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremate', 'v. t.', 'To burn; to reduce to ashes by the action of fire,\n   either directly or in an oven or retort; to incremate or incinerate;\n   as, to cremate a corpse, instead of burying it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremation', 'n.', 'A burning; esp., the act or practice of cremating the\n   dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremationist', 'n.', 'One who advocates the practice of cremation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremator', 'n.', 'One who, or that which, cremates or consumes to ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crematoriums', 'pl. ', 'of Crematory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crematories', 'pl. ', 'of Crematory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crematorium', 'n.', 'Alt. of Crematory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crematory', 'n.', 'A furnace for cremating corpses; a building containing\n   such a furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crematory', 'a.', 'Pertaining to, or employed in, cremation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremocarp', 'n.', 'The peculiar fruit of fennel, carrot, parsnip, and the\n   like, consisting of a pair of carpels pendent from a supporting axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremona', 'n.', 'A superior kind of violin, formerly made at Cremona, in\n   Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremor', 'n.', 'Cream; a substance resembling cream; yeast; scum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cremosin', 'n.', 'See Crimson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crems', 'n.', 'See Krems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenate', 'a.', 'Alt. of Crenated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenated', 'a.', 'Having the margin cut into rounded teeth notches, or\n   scallops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenation', 'n.', 'A rounded tooth on the edge of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenation', 'n.', 'The condition of being crenate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenature', 'n.', 'A rounded tooth or notch of a crenate leaf, or any part\n   that is crenate; -- called also crenelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenature', 'n.', 'The state of being crenated or notched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenel', 'n.', 'See Crenelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenelated', 'imp. & p. p.', 'of Crenelate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenelating', 'p. pr. & vb. n.', 'of Crenelate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenelate', 'v. t.', 'To furnish with crenelles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenelate', 'v. t.', 'To indent; to notch; as, a crenelated leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenelation', 'n.', 'The act of crenelating, or the state of being\n   crenelated; an indentation or an embrasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenelle', 'n.', 'Alt. of Crenel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenel', 'n.', 'An embrasure or indentation in a battlement; a loophole in\n   a fortress; an indentation; a notch. See Merlon, and Illust. of\n   Battlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenel', 'n.', 'Same as Crenature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenelled', 'a.', 'Same as Crenate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crengle', 'n.', 'Alt. of Crenkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenkle', 'n.', 'See Cringle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenulate', 'a.', 'Alt. of Crenulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenulated', 'a.', 'Minutely crenate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenulation', 'n.', 'A minute crenation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crenulation', 'n.', 'The state of being minutely scalloped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creole', 'n.', 'One born of European parents in the American colonies of\n   France or Spain or in the States which were once such colonies, esp. a\n   person of French or Spanish descent, who is a native inhabitant of\n   Louisiana, or one of the States adjoining, bordering on the Gulf of of\n   Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creole', 'a.', 'Of or pertaining to a Creole or the Creoles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creolean', 'a.', 'Alt. of Creolian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creolian', 'a.', 'Pertaining to, or characteristic of, the Creoles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creolian', 'n. ', 'A Creole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creosol', 'n.', 'A colorless liquid resembling phenol or carbolic acid,\n   homologous with pyrocatechin, and obtained from beechwood tar and gum\n   guaiacum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creosote', 'n.', 'Wood-tar oil; an oily antiseptic liquid, of a burning\n   smoky taste, colorless when pure, but usually colored yellow or brown\n   by impurity or exposure. It is a complex mixture of various phenols and\n   their ethers, and is obtained by the distillation of wood tar,\n   especially that of beechwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creosoted', 'imp. & p. p.', 'of Creosote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creosoting', 'p. pr. & vb. n.', 'of Creosote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creosote', 'v. t.', 'To saturate or impregnate with creosote, as timber,\n   for the prevention of decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepance', 'n.', 'Alt. of Crepane');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepane', 'n.', 'An injury in a horses leg, caused by the shoe of one hind\n   foot striking and cutting the other leg. It sometimes forms an ulcer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepe', 'n.', 'Same as Crape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepitant', 'a.', 'Having a crackling sound; crackling; rattling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepitated', 'imp. & p. p.', 'of Crepitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepitating', 'p. pr. & vb. n.', 'of Crepitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepitate', 'v.', 'To make a series of small, sharp, rapidly repeated\n   explosions or sounds, as salt in fire; to crackle; to snap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepitation', 'n.', 'The act of crepitating or crackling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepitation', 'n.', 'A grating or crackling sensation or sound, as that\n   produced by rubbing two fragments of a broken bone together, or by\n   pressing upon cellular tissue containing air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepitation', 'n.', 'A crepitant rale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepitus', 'n.', 'The noise produced by a sudden discharge of wind from the\n   bowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepitus', 'n.', 'Same as Crepitation, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepon', 'n.', 'A thin stuff made of the finest wool or silk, or of wool\n   and silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crept', '', 'imp. & p. p. of Creep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepuscle', 'n.', 'Alt. of Crepuscule');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepuscule', 'n.', 'Twilight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepuscular', 'a.', 'Alt. of Crepusculous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepusculous', 'a.', 'Pertaining to twilight; glimmering; hence,\n   imperfectly clear or luminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepusculous', 'a.', 'Flying in the twilight or evening, or before sunrise;\n   -- said certain birds and insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crepusculine', 'a.', 'Crepuscular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescence', 'n.', 'Increase; enlargement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescendo', 'a. & adv.', 'With a constantly increasing volume of voice;\n   with gradually increasing strength and fullness of tone; -- a direction\n   for the performance of music, indicated by the mark, or by writing the\n   word on the score.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescendo', 'n.', 'A gradual increase in the strength and fullness of tone\n   with which a passage is performed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescendo', 'n.', 'A passage to be performed with constantly increasing\n   volume of tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'n.', 'The increasing moon; the moon in her first quarter, or\n   when defined by a concave and a convex edge; also, applied improperly\n   to the old or decreasing moon in a like state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'n.', 'Anything having the shape of a crescent or new moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'n.', 'A representation of the increasing moon, often used as an\n   emblem or badge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'n.', 'A symbol of Artemis, or Diana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'n.', 'The ancient symbol of Byzantium or Constantinople.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'n.', 'The emblem of the Turkish Empire, adopted after the\n   taking of Constantinople.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'n.', 'Any one of three orders of knighthood; the first\n   instituted by Charles I., king of Naples and Sicily, in 1268; the\n   second by Rene of Anjou, in 1448; and the third by the Sultan Selim\n   III., in 1801, to be conferred upon foreigners to whom Turkey might be\n   indebted for valuable services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'n.', 'The emblem of the increasing moon with horns directed\n   upward, when used in a coat of arms; -- often used as a mark of cadency\n   to distinguish a second son and his descendants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'a.', 'Shaped like a crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'a.', 'Increasing; growing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'v. t.', 'To form into a crescent, or something resembling a\n   crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescent', 'v. t.', 'To adorn with crescents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescentic', 'a.', 'Crescent-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescentwise', 'adv.', 'In the form of a crescent; like a crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crescive', 'a.', 'Increasing; growing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cresol', 'n.', 'Any one of three metameric substances, CH3.C6H4.OH,\n   homologous with and resembling phenol. They are obtained from coal tar\n   and wood tar, and are colorless, oily liquids or solids. [Called also\n   cresylic acid.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cresorcin', 'n.', 'Same as Isorcin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cresses', 'pl. ', 'of Cress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cress', 'n.', 'A plant of various species, chiefly cruciferous. The leaves\n   have a moderately pungent taste, and are used as a salad and\n   antiscorbutic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cresselle', 'n.', 'A wooden rattle sometimes used as a substitute for a\n   bell, in the Roman Catholic church, during the latter part of Holy\n   Week, or the last week of Lent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cresset', 'n.', 'An open frame or basket of iron, filled with combustible\n   material, to be burned as a beacon; an open lamp or firrepan carried on\n   a pole in nocturnal processions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cresset', 'n.', 'A small furnace or iron cage to hold fire for charring the\n   inside of a cask, and making the staves flexible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cressy', 'a.', 'Abounding in cresses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'n.', 'A tuft, or other excrescence or natural ornament, growing on\n   an animals head; the comb of a cock; the swelling on the head of a\n   serpent; the lengthened feathers of the crown or nape of bird, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'n.', 'The plume of feathers, or other decoration, worn on a\n   helmet; the distinctive ornament of a helmet, indicating the rank of\n   the wearer; hence, also, the helmet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'n.', 'A bearing worn, not upon the shield, but usually above it,\n   or separately as an ornament for plate, liveries, and the like. It is a\n   relic of the ancient cognizance. See Cognizance, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'n.', 'The upper curve of a horses neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'n.', 'The ridge or top of a wave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'n.', 'The summit of a hill or mountain ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'n.', 'The helm or head, as typical of a high spirit; pride;\n   courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'n.', 'The ornamental finishing which surmounts the ridge of a\n   roof, canopy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'n.', 'The top line of a slope or embankment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crested', 'imp. & p. p.', 'of Crest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cresting', 'p. pr. & vb. n.', 'of Crest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'v. t.', 'To furnish with, or surmount as, a crest; to serve as a\n   crest for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'v. t.', 'To mark with lines or streaks, like, or regarded as like,\n   waving plumes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crest', 'v. i.', 'To form a crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crested', 'a.', 'Having a crest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crested', 'a.', 'Having a crest of feathers or hair upon the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crested', 'a.', 'Bearing any elevated appendage like a crest, as an\n   elevated line or ridge, or a tuft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crestfallen', 'a.', 'With hanging head; hence, dispirited; dejected; cowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crestfallen', 'a.', 'Having the crest, or upper part of the neck, hanging\n   to one side; -- said of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cresting', 'n.', 'An ornamental finish on the top of a wall or ridge of a\n   roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crestless', 'a.', 'Without a crest or escutcheon; of low birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cresylic', 'a.', 'Pertaining to, or derived from, cresol, creosote, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretaceous', 'a.', 'Having the qualities of chalk; abounding with chalk;\n   chalky; as, cretaceous rocks and formations. See Chalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretaceously', 'adv.', 'In a chalky manner; as chalk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretan', 'a.', 'Pertaining to Crete, or Candia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretan', 'n.', 'A native or inhabitant of Crete or Candia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crete', 'n.', 'A Cretan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretian', 'a. & n.', 'See Cretan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretic', 'n.', 'A poetic foot, composed of one short syllable between two\n   long ones (- / -).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creticism', 'n.', 'Falsehood; lying; cretism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretin', 'n.', 'One afflicted with cretinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretinism', 'n.', 'A condition of endemic or inherited idiocy, accompanied\n   by physical degeneracy and deformity (usually with goiter), frequent in\n   certain mountain valleys, esp. of the Alps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretinous', 'a.', 'Having the characteristics of a cretin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretism', 'n.', 'A Cretan practice; lying; a falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretonne', 'n.', 'A strong white fabric with warp of hemp and weft of flax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretonne', 'n.', 'A fabric with cotton warp and woolen weft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretonne', 'n.', 'A kind of chintz with a glossy surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cretose', 'a.', 'Chalky; cretaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creutzer', 'n.', 'See Kreutzer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creux', 'n.', 'Used in English only in the expression en creux. Thus,\n   engraving en creux is engraving in intaglio, or by sinking or hollowing\n   out the design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crevalle', 'n.', 'The cavally or jurel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crevalle', 'n.', 'The pompano (Trachynotus Carolinus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crevasse', 'n.', 'A deep crevice or fissure, as in embankment; one of the\n   clefts or fissure by which the mass of a glacier is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crevasse', 'n.', 'A breach in the levee or embankment of a river, caused by\n   the pressure of the water, as on the lower Mississippi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crevet', 'n.', 'A crucible or melting pot; a cruset.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crevice', 'n.', 'A narrow opening resulting from a split or crack or the\n   separation of a junction; a cleft; a fissure; a rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crevice', 'v. t.', 'To crack; to flaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Creviced', 'a.', 'Having a crevice or crevices; as, a creviced structure\n   for storing ears of corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crevis', 'n.', 'The crawfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crew', 'n.', 'The Manx shearwater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crew', 'n.', 'A company of people associated together; an assemblage; a\n   throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crew', 'n.', 'The company of seamen who man a ship, vessel, or at; the\n   company belonging to a vessel or a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crew', 'n.', 'In an extended sense, any small body of men associated for a\n   purpose; a gang; as (Naut.), the carpenters crew; the boatswains\n   crew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crew', '', 'imp. of Crow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crewel', 'n.', 'Worsted yarn,, slackly twisted, used for embroidery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crewelwork', 'n.', 'Embroidery in crewels, commonly done upon some plain\n   material, such as linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crewet', 'n.', 'See Cruet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'A manger or rack; a feeding place for animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'A stall for oxen or other cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'A small inclosed bedstead or cot for a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'A box or bin, or similar wooden structure, for storing grain,\n   salt, etc.; as, a crib for corn or oats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'A hovel; a hut; a cottage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'A structure or frame of timber for a foundation, or for\n   supporting a roof, or for lining a shaft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'A structure of logs to be anchored with stones; -- used for\n   docks, pier, dams, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'A small raft of timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'A small theft; anything purloined;; a plagiaris/; hence, a\n   translation or key, etc., to aid a student in preparing or reciting his\n   lessons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'A miners luncheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'n.', 'The discarded cards which the dealer can use in scoring\n   points in cribbage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribbed', 'imp. & p. p.', 'of Crib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribbing', 'p. pr. & vb. n.', 'of Crib');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'v. t.', 'To shut up or confine in a narrow habitation; to cage; to\n   cramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'v. t.', 'To pilfer or purloin; hence, to steal from an author; to\n   appropriate; to plagiarize; as, to crib a line from Milton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'v. i.', 'To crowd together, or to be confined, as in a crib or in\n   narrow accommodations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'v. i.', 'To make notes for dishonest use in recitation or\n   examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib', 'v. i.', 'To seize the manger or other solid object with the teeth\n   and draw in wind; -- said of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribbage', 'v. t.', 'A game of cards, played by two or four persons, in\n   which there is a crib. (See Crib, 11.) It is characterized by a great\n   variety of chances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criber', 'n.', 'Alt. of Crib-biter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib-biter', 'n.', 'A horse that has the habit of cribbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribbing', 'n.', 'The act of inclosing or confining in a crib or in close\n   quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribbing', 'n.', 'Purloining; stealing; plagiarizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribbing', 'n.', 'A framework of timbers and plank backing for a shaft\n   lining, to prevent caving, percolation of water, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribbing', 'n.', 'A vicious habit of a horse; crib-biting. The horse lays\n   hold of the crib or manger with his teeth and draws air into the\n   stomach with a grunting sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crib-biting', 'n.', 'Same as Cribbing, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribble', 'n.', 'A coarse sieve or screen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribble', 'n.', 'Coarse flour or meal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribbled', 'imp. & p. p.', 'of Cribble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribbling', 'p. pr. & vb. n.', 'of Cribble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribble', 'v. t.', 'To cause to pass through a sieve or riddle; to sift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribble', 'a.', 'Coarse; as, cribble bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribellum', 'n.', 'A peculiar perforated organ of certain spiders\n   (Ciniflonidae), used for spinning a special kind of silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribrate', 'a.', 'Cribriform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribration', 'n.', 'The act or process of separating the finer parts of\n   drugs from the coarser by sifting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribriform', 'a.', 'Resembling, or having the form of, a sieve; pierced\n   with holes; as, the cribriform plate of the ethmoid bone; a cribriform\n   compress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cribrose', 'a.', 'Perforated like a sieve; cribriform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cric', 'n.', 'The ring which turns inward and condenses the flame of a\n   lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crick', 'n.', 'The creaking of a door, or a noise resembling it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crick', 'n.', 'A painful, spasmodic affection of the muscles of some part\n   of the body, as of the neck or back, rendering it difficult to move the\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crick', 'n.', 'A small jackscrew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cricket', 'n.', 'An orthopterous insect of the genus Gryllus, and allied\n   genera. The males make chirping, musical notes by rubbing together the\n   basal parts of the veins of the front wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cricket', 'n.', 'A low stool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cricket', 'n.', 'A game much played in England, and sometimes in America,\n   with a ball, bats, and wickets, the players being arranged in two\n   contesting parties or sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cricket', 'n.', 'A small false roof, or the raising of a portion of a roof,\n   so as to throw off water from behind an obstacle, such as a chimney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cricket', 'v. i.', 'To play at cricket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cricketer', 'n.', 'One who plays at cricket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cricoid', 'a.', 'Resembling a ring; -- said esp. of the cartilage at the\n   larynx, and the adjoining parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cricothyroid', 'a.', 'Of or pertaining both to the cricoid and the thyroid\n   cartilages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cried', '', 'imp. & p. p. of Cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crier', 'n.', 'One who cries; one who makes proclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crier', 'n.', 'an officer who proclaims the orders or directions of a\n   court, or who gives public notice by loud proclamation; as, a\n   town-crier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crime', 'n.', 'Any violation of law, either divine or human; an omission of\n   a duty commanded, or the commission of an act forbidden by law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crime', 'n.', 'Gross violation of human law, in distinction from a\n   misdemeanor or trespass, or other slight offense. Hence, also, any\n   aggravated offense against morality or the public welfare; any outrage\n   or great wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crime', 'n.', 'Any great wickedness or sin; iniquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crime', 'n.', 'That which occasion crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimeful', 'a.', 'Criminal; wicked; contrary to law, right, or dury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimeless', 'a.', 'Free from crime; innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminal', 'a.', 'Guilty of crime or sin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminal', 'a.', 'Involving a crime; of the nature of a crime; -- said of\n   an act or of conduct; as, criminal carelessness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminal', 'a.', 'Relating to crime; -- opposed to civil; as, the criminal\n   code.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminal', 'n.', 'One who has commited a crime; especially, one who is\n   found guilty by verdict, confession, or proof; a malefactor; a felon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminalist', 'n.', 'One versed in criminal law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminality', 'n.', 'The quality or state of being criminal; that which\n   constitutes a crime; guiltiness; guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminally', 'adv.', 'In violation of law; wickedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminalness', 'n.', 'Criminality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminated', 'imp. & p. p.', 'of Criminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminating', 'p. pr. & vb. n.', 'of Criminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminate', 'v. t. ', 'To accuse of, or charge with, a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminate', 'v. t. ', 'To involve in a crime or in its consequences; to\n   render liable to a criminal charge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimination', 'n.', 'The act of accusing; accusation; charge; complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminative', 'a.', 'Charging with crime; accusing; criminatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminatory', 'a.', 'Relating to, or involving, crimination; accusing; as,\n   a criminatory conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminology', 'n.', 'A treatise on crime or the criminal population.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criminous', 'a.', 'Criminal; involving great crime or grave charges; very\n   wicked; heinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimosin', 'n.', 'See Crimson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimped', 'imp. & p. p.', 'of Crimp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimping', 'p. pr. & vb. n.', 'of Crimp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'v. t.', 'To fold or plait in regular undulation in such a way that\n   the material will retain the shape intended; to give a wavy appearance\n   to; as, to crimp the border of a cap; to crimp a ruffle. Cf. Crisp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'v. t.', 'To pinch and hold; to seize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'v. t.', 'to entrap into the military or naval service; as, to\n   crimp seamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'v. t.', 'To cause to contract, or to render more crisp, as the\n   flesh of a fish, by gashing it, when living, with a knife; as, to crimp\n   skate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'a.', 'Easily crumbled; friable; brittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'a.', 'Weak; inconsistent; contradictory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'n.', 'A coal broker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'n.', 'One who decoys or entraps men into the military or naval\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'n.', 'A keeper of a low lodging house where sailors and emigrants\n   are entrapped and fleeced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'n.', 'Hair which has been crimped; -- usually in pl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimp', 'n.', 'A game at cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimpage', 'n.', 'The act or practice of crimping; money paid to a crimp\n   for shipping or enlisting men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimper', 'n.', 'One who, or that which, crimps');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimper', 'n.', 'A curved board or frame over which the upper of a boot or\n   shoe is stretched to the required shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimper', 'n.', 'A device for giving hair a wavy appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimper', 'n.', 'A machine for crimping or ruffling textile fabrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimpled', 'imp. & p. p.', 'of Crimple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimpling', 'p. pr. & vb. n.', 'of Crimple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimple', 'v. t.', 'To cause to shrink or draw together; to contract; to\n   curl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimpy', 'a.', 'Having a crimped appearance; frizzly; as, the crimpy wool\n   of the Saxony sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimson', 'n.', 'A deep red color tinged with blue; also, red color in\n   general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimson', 'a.', 'Of a deep red color tinged with blue; deep red.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimsoned', 'imp. & p. p.', 'of Crimson');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimsoning', 'p. pr. & vb. n.', 'of Crimson');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimson', 'v. t.', 'To dye with crimson or deep red; to redden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crimson', 'b. t.', 'To become crimson; to blush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinal', 'a.', 'Of or pertaining to the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinated', 'a.', 'Having hair; hairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinatory', 'a.', 'Crinitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crincum', 'n.', 'A twist or bend; a turn; a whimsey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crincum-crancum', 'n.', 'A twist; a whimsey or whim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crined', 'a.', 'Having the hair of a different tincture from the rest of\n   the body; as, a charge crined of a red tincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinel', 'n.', 'Alt. of Crinet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinet', 'n.', 'A very fine, hairlike feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crnged', 'imp. & p. p.', 'of Cringe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cringing', 'p. pr. & vb. n.', 'of Cringe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cringe', 'v. t.', 'To draw ones self together as in fear or servility; to\n   bend or crouch with base humility; to wince; hence; to make court in a\n   degrading manner; to fawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cringe', 'v. t.', 'To contract; to draw together; to cause to shrink or\n   wrinkle; to distort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cringe', 'n.', 'Servile civility; fawning; a shrinking or bowing, as in\n   fear or servility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cringeling', 'n.', 'One who cringes meanly; a fawner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cringer', 'n.', 'One who cringes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cringingly', 'adv.', 'In a cringing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cringle', 'n.', 'A withe for fastening a gate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cringle', 'n.', 'An iron or pope thimble or grommet worked into or attached\n   to the edges and corners of a sail; -- usually in the plural. The\n   cringles are used for making fast the bowline bridles, earings, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinicultural', 'a.', 'Relating to the growth of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinigerous', 'a.', 'Bearing hair; hairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinital', 'a.', 'Same as Crinite, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinite', 'a.', 'Having the appearance of a tuft of hair; having a hairlike\n   tail or train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinite', 'a.', 'Bearded or tufted with hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinitory', 'a.', 'Of or relating to hair; as, a crinitory covering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinkled', 'imp. & p. p.', 'of Crinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinkling', 'p. pr. & vb. n.', 'of Crinkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinkle', 'v. t.', 'To form with short turns, bends, or wrinkles; to mold\n   into inequalities or sinuosities; to cause to wrinkle or curl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinkle', 'v. i.', 'To turn or wind; to run in and out in many short bends\n   or turns; to curl; to run in waves; to wrinkle; also, to rustle, as\n   stiff cloth when moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinkle', 'n.', 'A winding or turn; wrinkle; sinuosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinkled', 'a.', 'Having short bends, turns, or wrinkles; wrinkled; wavy;\n   zigzag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinkly', 'a.', 'Having crinkles; wavy; wrinkly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinoid', 'a.', 'Crinoidal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinoid', 'n.', 'One of the Crinoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinoidal', 'a.', 'Of pertaining to crinoids; consisting of, or containing,\n   crinoids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinoidea', 'n. pl.', 'A large class of Echinodermata, including numerous\n   extinct families and genera, but comparatively few living ones. Most of\n   the fossil species, like some that are recent, were attached by a\n   jointed stem. See Blastoidea, Cystoidea, Comatula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinoidean', 'n.', 'One of the Crinoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinoline', 'n.', 'A kind of stiff cloth, used chiefly by women, for\n   underskirts, to expand the gown worn over it; -- so called because\n   originally made of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinoline', 'n.', 'A ladys skirt made of any stiff material; latterly, a\n   hoop skirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinose', 'a.', 'Hairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinosity', 'n.', 'Hairiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crinum', 'n.', 'A genus of bulbous plants, of the order Amaryllidace/,\n   cultivated as greenhouse plants on account of their beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criosphinx', 'n.', 'A sphinx with the head of a ram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cripple', 'n.', 'One who creeps, halts, or limps; one who has lost, or\n   never had, the use of a limb or limbs; a lame person; hence, one who is\n   partially disabled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cripple', 'a.', 'Lame; halting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crippled', 'imp. & p. p.', 'of Cripple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crippling', 'p. pr. & vb. n.', 'of Cripple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cripple', 'v. t.', 'To deprive of the use of a limb, particularly of a leg\n   or foot; to lame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cripple', 'v. t.', 'To deprive of strength, activity, or capability for\n   service or use; to disable; to deprive of resources; as, to be\n   financially crippled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crippled', 'a.', 'Lamed; lame; disabled; impeded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crippleness', 'n.', 'Lameness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crippler', 'n.', 'A wooden tool used in graining leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crippling', 'n.', 'Spars or timbers set up as a support against the side of\n   a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cripply', 'a.', 'Lame; disabled; in a crippled condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crises', 'pl. ', 'of Crisis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisis', 'n.', 'The point of time when it is to be decided whether any\n   affair or course of action must go on, or be modified or terminate; the\n   decisive moment; the turning point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisis', 'n.', 'That change in a disease which indicates whether the result\n   is to be recovery or death; sometimes, also, a striking change of\n   symptoms attended by an outward manifestation, as by an eruption or\n   sweat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'a.', 'Curling in stiff curls or ringlets; as, crisp hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'a.', 'Curled with the ripple of the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'a.', 'Brittle; friable; in a condition to break with a short,\n   sharp fracture; as, crisp snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'a.', 'Possessing a certain degree of firmness and freshness; in a\n   fresh, unwilted condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'a.', 'Lively; sparking; effervescing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'a.', 'Brisk; crackling; cheerful; lively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisped', 'imp. & p. p.', 'of Crisp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisping', 'p. pr. & vb. n.', 'of Crisp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'a.', 'To curl; to form into ringlets, as hair, or the nap of\n   cloth; to interweave, as the branches of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'a.', 'To cause to undulate irregularly, as crape or water; to\n   wrinkle; to cause to ripple. Cf. Crimp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'a.', 'To make crisp or brittle, as in cooking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'v. i.', 'To undulate or ripple. Cf. Crisp, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisp', 'n.', 'That which is crisp or brittle; the state of being crisp or\n   brittle; as, burned to a crisp; specifically, the rind of roasted pork;\n   crackling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crispate', 'a.', 'Alt. of Crispated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crispated', 'a.', 'Having a crisped appearance; irregularly curled or\n   twisted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crispation', 'n.', 'The act or process of curling, or the state of being\n   curled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crispation', 'n.', 'A very slight convulsive or spasmodic contraction of\n   certain muscles, external or internal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crispature', 'n.', 'The state of being crispate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisper', 'n.', 'One who, or that which, crisps or curls; an instrument for\n   making little curls in the nap of cloth, as in chinchilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crispin', 'n.', 'A shoemaker; -- jocularly so called from the patron saint\n   of the craft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crispin', 'n.', 'A member of a union or association of shoemakers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisply', 'adv.', 'In a crisp manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crispness', 'n.', 'The state or quality of being crisp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crispy', 'a.', 'Formed into short, close ringlets; frizzed; crisp; as,\n   crispy locks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crispy', 'a.', 'Crisp; brittle; as, a crispy pie crust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crissal', 'a.', 'Pertaining to the crissum; as, crissal feathers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crissal', 'a.', 'Having highly colored under tail coverts; as, the crissal\n   thrasher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisscross', 'n.', 'A mark or cross, as the signature of a person who is\n   unable to write.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisscross', 'n.', 'A childs game played on paper or on a slate,\n   consisting of lines arranged in the form of a cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisscross', 'v. t.', 'To mark or cover with cross lines; as, a paper was\n   crisscrossed with red marks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisscross', 'adv.', 'In opposite directions; in a way to cross something\n   else; crossing one another at various angles and in various ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisscross', 'adv.', 'With opposition or hindrance; at cross purposes;\n   contrarily; as, things go crisscross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crisscross-row', 'n.', 'See Christcross-row.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crissa', 'pl. ', 'of Crissum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crissum', 'n.', 'That part of a bird, or the feathers, surrounding the\n   cloacal opening; the under tail coverts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cristate', 'a.', 'Crested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criteria', 'pl. ', 'of Criterion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criterions', 'pl. ', 'of Criterion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criterion', 'n.', 'A standard of judging; any approved or established rule\n   or test, by which facts, principles opinions, and conduct are tried in\n   forming a correct judgment respecting them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crith', 'n.', 'The unit for estimating the weight of a/riform substances;\n   -- the weight of a liter of hydrogen at 0/ centigrade, and with a\n   tension of 76 centimeters of mercury. It is 0.0896 of a gram, or\n   1.38274 grains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crithomancu', 'n.', 'A kind of divination by means of the dough of the\n   cakes offered in the ancient sacrifices, and the meal strewed over the\n   victims.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critic', 'n.', 'One skilled in judging of the merits of literary or\n   artistic works; a connoisseur; an adept; hence, one who examines\n   literary or artistic works, etc., and passes judgment upon them; a\n   reviewer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critic', 'n.', 'One who passes a rigorous or captious judgment; one who\n   censures or finds fault; a harsh examiner or judge; a caviler; a\n   carper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critic', 'n.', 'The art of criticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critic', 'n.', 'An act of criticism; a critique.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critic', 'a.', 'Of or pertaining to critics or criticism; critical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critic', 'v. i.', 'To criticise; to play the critic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critical', 'n.', 'Qualified to criticise, or pass judgment upon, literary\n   or artistic productions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critical', 'n.', 'Pertaining to criticism or the critics art; of the\n   nature of a criticism; accurate; as, critical knowledge; a critical\n   dissertation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critical', 'n.', 'Inclined to make nice distinctions, or to exercise\n   careful judgment and selection; exact; nicely judicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critical', 'n.', 'Inclined to criticise or find fault; fastidious;\n   captious; censorious; exacting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critical', 'n.', 'Characterized by thoroughness and a reference to\n   principles, as becomes a critic; as, a critical analysis of a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critical', 'n.', 'Pertaining to, or indicating, a crisis, turning point, or\n   specially important juncture; important as regards consequences; hence,\n   of doubtful issue; attended with risk; dangerous; as, the critical\n   stage of a fever; a critical situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critically', 'adv.', 'In a critical manner; with nice discernment;\n   accurately; exactly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critically', 'adv.', 'At a crisis; at a critical time; in a situation,\n   place, or condition of decisive consequence; as, a fortification\n   critically situated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticalness', 'n.', 'The state or quality of being critical, or of\n   occurring at a critical time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticalness', 'n.', 'Accuracy in examination or decision; exactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticaster', 'n.', 'A contemptible or vicious critic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticisable', 'a.', 'Capable of being criticised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticised', 'imp. & p. p.', 'of Criticise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticising', 'p. pr. & vb. n.', 'of Criticise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticise', 'v. t.', 'To examine and judge as a critic; to pass literary or\n   artistic judgment upon; as, to criticise an author; to criticise a\n   picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticise', 'v. t.', 'To express ones views as to the merit or demerit of;\n   esp., to animadvert upon; to find fault with; as, to criticise conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticise', 'v. i.', 'To act as a critic; to pass literary or artistic\n   judgment; to play the critic; -- formerly used with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticise', 'v. i.', 'To discuss the merits or demerits of a thing or\n   person; esp., to find fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticiser', 'n.', 'One who criticises; a critic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticism', 'n.', 'The rules and principles which regulate the practice of\n   the critic; the art of judging with knowledge and propriety of the\n   beauties and faults of a literary performance, or of a production in\n   the fine arts; as, dramatic criticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Criticism', 'n.', 'The act of criticising; a critical judgment passed or\n   expressed; a critical observation or detailed examination and review; a\n   critique; animadversion; censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critique', 'n.', 'The art of criticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critique', 'n.', 'A critical examination or estimate of a work of\n   literature or art; a critical dissertation or essay; a careful and\n   through analysis of any subject; a criticism; as, Kants \"Critique of\n   Pure Reason.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critique', 'n.', 'A critic; one who criticises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Critique', 'v.', 'To criticise or pass judgment upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crizzel', 'n.', 'A kind of roughness on the surface of glass, which clouds\n   its transparency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croaked', 'imp. & p. p.', 'of Croak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croaking', 'p. pr. & vb. n.', 'of Croak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croak', 'v. i.', 'To make a low, hoarse noise in the throat, as a frog, a\n   raven, or a crow; hence, to make any hoarse, dismal sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croak', 'v. i.', 'To complain; especially, to grumble; to forebode evil; to\n   utter complaints or forebodings habitually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croak', 'v. t.', 'To utter in a low, hoarse voice; to announce by croaking;\n   to forebode; as, to croak disaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croak', 'n.', 'The coarse, harsh sound uttered by a frog or a raven, or a\n   like sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croaker', 'n.', 'One who croaks, murmurs, grumbles, or complains\n   unreasonably; one who habitually forebodes evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croaker', 'n.', 'A small American fish (Micropogon undulatus), of the\n   Atlantic coast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croaker', 'n.', 'An American fresh-water fish (Aplodinotus grunniens); --\n   called also drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croaker', 'n.', 'The surf fish of California.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croat', 'n.', 'A native of Croatia, in Austria; esp., one of the native\n   Slavic race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croat', 'n.', 'An irregular soldier, generally from Croatia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croatian', 'a.', 'Of or pertaining to Croatia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croatian', 'n.', 'A Croat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocein', 'n.', 'A name given to any one of several yellow or scarlet\n   dyestuffs of artificial production and complex structure. In general\n   they are diazo and sulphonic acid derivatives of benzene and naphthol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croceous', 'a.', 'Of, pertaining to, or like, saffron; deep reddish yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocetin', 'n.', 'A dyestuff, obtained from the Chinese crocin, which\n   produces a brilliant yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croche', 'n.', 'A little bud or knob at the top of a deers antler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crochet', 'n.', 'A kind of knitting done by means of a hooked needle, with\n   worsted, silk, or cotton; crochet work. Commonly used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocheted', 'imp. & p. p.', 'of Crochet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocheting', 'p. pr. & vb. n.', 'of Crochet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crochet', 'v. t. & i.', 'To knit with a crochet needle or hook; as, to\n   crochet a shawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crociary', 'n.', 'One who carries the cross before an archbishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocidolite', 'n.', 'A mineral occuring in silky fibers of a lavender blue\n   color. It is related to hornblende and is essentially a silicate of\n   iron and soda; -- called also blue asbestus. A silicified form, in\n   which the fibers penetrating quartz are changed to oxide of iron, is\n   the yellow brown tiger-eye of the jewelers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocin', 'n.', 'The coloring matter of Chinese yellow pods, the fruit of\n   Gardenia grandiflora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocin', 'n.', 'A red powder (called also polychroite), which is made from\n   the saffron (Crocus sativus). See Polychroite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crock', 'n.', 'The loose black particles collected from combustion, as on\n   pots and kettles, or in a chimney; soot; smut; also, coloring matter\n   which rubs off from cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocked', 'imp. & p. p.', 'of Crock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocking', 'p. pr. & vb. n.', 'of Crock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crock', 'v. t.', 'To soil by contact, as with soot, or with the coloring\n   matter of badly dyed cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crock', 'v. i.', 'To give off crock or smut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crock', 'n.', 'A low stool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crock', 'n.', 'Any piece of crockery, especially of coarse earthenware; an\n   earthen pot or pitcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crock', 'v. t.', 'To lay up in a crock; as, to crock butter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocker', 'n.', 'A potter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crockery', 'n.', 'Earthenware; vessels formed of baked clay, especially the\n   coarser kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocket', 'n.', 'An ornament often resembling curved and bent foliage,\n   projecting from the sloping edge of a gable, spire, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocket', 'n.', 'A croche, or knob, on the top of a stags antler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocketed', 'a.', 'Ornamented with crockets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocketing', 'n.', 'Ornamentation with crockets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocky', 'a.', 'Smutty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocodile', 'n.', 'A large reptile of the genus Crocodilus, of several\n   species. They grow to the length of sixteen or eighteen feet, and\n   inhabit the large rivers of Africa, Asia, and America. The eggs, laid\n   in the sand, are hatched by the suns heat. The best known species is\n   that of the Nile (C. vulgaris, or C. Niloticus). The Florida crocodile\n   (C. Americanus) is much less common than the alligator and has longer\n   jaws. The name is also sometimes applied to the species of other\n   related genera, as the gavial and the alligator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocodile', 'n.', 'A fallacious dilemma, mythically supposed to have been\n   first used by a crocodile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocodilia', 'n. pl.', 'An order of reptiles including the crocodiles,\n   gavials, alligators, and many extinct kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocodilian', 'a.', 'Like, or pertaining to, the crocodile; characteristic\n   of the crocodile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocodilian', 'n.', 'One of the Crocodilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocodility', 'n.', 'A caption or sophistical mode of arguing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocoisite', 'n.', 'Same as Crocoite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocoite', 'n.', 'Lead chromate occuring in crystals of a bright hyacinth\n   red color; -- called also red lead ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croconate', 'n.', 'A salt formed by the union of croconic acid with a base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croconic', 'a.', 'Of, pertaining to, or resembling saffron; having the\n   color of saffron; as, croconic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croconic', 'a.', 'Pertaining to, or derived from, croconic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocose', 'n.', 'A white crystalline sugar, metameric with glucose,\n   obtained from the coloring matter of saffron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocus', 'n.', 'A genus of iridaceous plants, with pretty blossoms rising\n   separately from the bulb or corm. C. vernus is one of the earliest of\n   spring-blooming flowers; C. sativus produces the saffron, and blossoms\n   in the autumn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crocus', 'n.', 'A deep yellow powder; the oxide of some metal calcined to a\n   red or deep yellow color; esp., the oxide of iron (Crocus of Mars or\n   colcothar) thus produced from salts of iron, and used as a polishing\n   powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croesus', 'n.', 'A king of Lydia who flourished in the 6th century b. c.,\n   and was renowned for his vast wealth; hence, a common appellation for a\n   very rich man; as, he is a veritable Croesus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croft', 'n.', 'A small, inclosed field, adjoining a house; a small farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crofter', 'n.', 'One who rents and tills a small farm or helding; as, the\n   crofters of Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crefting', 'n.', 'Croftland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crefting', 'n.', 'Exposing linen to the sun, on the grass, in the process\n   of bleaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croftland', 'n.', 'Land of superior quality, on which successive crops are\n   raised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crois', 'n.', 'See Cross, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croisade', 'n.', 'Alt. of Croisado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croisado', 'n.', 'A holy war; a crusade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croise', 'n.', 'A pilgrim bearing or wearing a cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croise', 'n.', 'A crusader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croissante', 'a.', 'Terminated with crescent; -- said of a cross the ends\n   of which are so terminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croker', 'n.', 'A cultivator of saffron; a dealer in saffron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croma', 'n.', 'A quaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cromlech', 'n.', 'A monument of rough stones composed of one or more large\n   ones supported in a horizontal position upon others. They are found\n   chiefly in countries inhabited by the ancient Celts, and are of a\n   period anterior to the introduction of Christianity into these\n   countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cromorna', 'n.', 'A certain reed stop in the organ, of a quality of tone\n   resembling that of the oboe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crone', 'n.', 'An old ewe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crone', 'n.', 'An old woman; -- usually in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crone', 'n.', 'An old man; especially, a man who talks and acts like an old\n   woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cronel', 'n.', 'The iron head of a tilting spear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cronet', 'n.', 'The coronet of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cronian', 'a.', 'Saturnian; -- applied to the North Polar Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cronstedtite', 'n.', 'A mineral consisting principally of silicate of iron,\n   and crystallizing in hexagonal prisms with perfect basal cleavage; --\n   so named from the Swedish mineralogist Cronstedt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cronies', 'pl. ', 'of Crony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crony', 'n.', 'A crone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crony', 'n.', 'An intimate companion; a familiar frend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croodle', 'v. i.', 'To cower or cuddle together, as from fear or cold; to\n   lie close and snug together, as pigs in straw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croodle', 'v. i.', 'To fawn or coax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croodle', 'v. i.', 'To coo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'n.', 'A bend, turn, or curve; curvature; flexure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'n.', 'Any implement having a bent or crooked end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'n.', 'The staff used by a shepherd, the hook of which serves to\n   hold a runaway sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'n.', 'A bishops staff of office. Cf. Pastoral staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'n.', 'A pothook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'n.', 'An artifice; trick; tricky device; subterfuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'n.', 'A small tube, usually curved, applied to a trumpet, horn,\n   etc., to change its pitch or key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'n.', 'A person given to fraudulent practices; an accomplice of\n   thieves, forgers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crooked', 'imp. & p. p.', 'of Crook');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crooking', 'p. pr. & vb. n.', 'of Crook');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'n.', 'To turn from a straight line; to bend; to curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'n.', 'To turn from the path of rectitude; to pervert; to misapply;\n   to twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crook', 'v. i.', 'To bend; to curve; to wind; to have a curvature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crookback', 'n.', 'A crooked back; one who has a crooked or deformed back;\n   a hunchback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crookack', 'a.', 'Hunched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crookbill', 'n.', 'A New Zealand plover (Anarhynchus frontalis), remarkable\n   for having the end of the beak abruptly bent to the right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crooked', 'a.', 'Characterized by a crook or curve; not straight; turning;\n   bent; twisted; deformed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crooked', 'a.', 'Not straightforward; deviating from rectitude; distorted\n   from the right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crooked', 'a.', 'False; dishonest; fraudulent; as, crooked dealings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crookedly', 'adv.', 'In a curved or crooked manner; in a perverse or\n   untoward manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crookedness', 'n.', 'The condition or quality of being crooked; hence,\n   deformity of body or of mind; deviation from moral rectitude;\n   perverseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crooken', 'v. t.', 'To make crooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crookes tube', '', 'A vacuum tube in which the exhaustion is carried to a\n   very high degree, with the production of a distinct class of effects;\n   -- so called from W. Crookes who introduced it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croon', 'v. i.', 'To make a continuous hollow moan, as cattle do when in\n   pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croon', 'v. i.', 'To hum or sing in a low tone; to murmur softly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crooned', 'imp. & p. p.', 'of Croon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crooning', 'p. pr. & vb. n.', 'of Croon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croon', 'v. t.', 'To sing in a low tone, as if to ones self; to hum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croon', 'v. t.', 'To soothe by singing softly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croon', 'n.', 'A low, continued moan; a murmur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croon', 'n.', 'A low singing; a plain, artless melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'n.', 'The pouchlike enlargement of the gullet of birds, serving as\n   a receptacle for food; the craw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'n.', 'The top, end, or highest part of anything, especially of a\n   plant or tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'n.', 'That which is cropped, cut, or gathered from a single felld,\n   or of a single kind of grain or fruit, or in a single season;\n   especially, the product of what is planted in the earth; fruit;\n   harvest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'n.', 'Grain or other product of the field while standing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'n.', 'Anything cut off or gathered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'n.', 'Hair cut close or short, or the act or style of so cutting;\n   as, a convicts crop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'n.', 'A projecting ornament in carved stone. Specifically, a\n   finial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'n.', 'Tin ore prepared for smelting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'n.', 'Outcrop of a vein or seam at the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'n.', 'A riding whip with a loop instead of a lash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cropped', 'imp. & p. p.', 'of Crop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cropping', 'p. pr. & vb. n.', 'of Crop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'v. t.', 'To cut off the tops or tips of; to bite or pull off; to\n   browse; to pluck; to mow; to reap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'v. t.', 'Fig.: To cut off, as if in harvest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'v. t.', 'To cause to bear a crop; as, to crop a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop', 'v. i.', 'To yield harvest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop-ear', 'n.', 'A person or animal whose ears are cropped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop-eared', 'a.', 'Having the ears cropped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cropful', 'a.', 'Having a full crop or belly; satiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cropper', 'n.', 'One that crops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cropper', 'n.', 'A variety of pigeon with a large crop; a pouter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cropper', 'n.', 'A machine for cropping, as for shearing off bolts or rod\n   iron, or for facing cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cropper', 'n.', 'A fall on ones head when riding at full speed, as in\n   hunting; hence, a sudden failure or collapse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cropsick', 'a.', 'Sick from excess in eating or drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crop-tailed', 'a.', 'Having the tail cropped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croquet', 'n.', 'An open-air game in which two or more players endeavor to\n   drive wooden balls, by means of mallets, through a series of hoops or\n   arches set in the ground according to some pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croquet', 'n.', 'The act of croqueting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croqueted', 'imp. & p. p.', 'of Croquet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croqueting', 'p. pr. & vb. n.', 'of Croquet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croquet', 'v. t.', 'In the game of croquet, to drive away an opponents\n   ball, after putting ones own in contact with it, by striking ones own\n   ball with the mallet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cro-quette', 'n.', 'A ball of minced meat, fowl, rice, or other\n   ingredients, highly seasoned, and fried.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crore', 'n.', 'Ten millions; as, a crore of rupees (which is nearly\n   $5,000,000).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosier', 'n.', 'The pastoral staff of a bishop (also of an archbishop,\n   being the symbol of his office as a shepherd of the flock of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosiered', 'a.', 'Bearing a crosier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croslet', 'n.', 'See Crosslet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'A gibbet, consisting of two pieces of timber placed\n   transversely upon one another, in various forms, as a T, or +, with the\n   horizontal piece below the upper end of the upright, or as an X. It was\n   anciently used in the execution of criminals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'The sign or mark of the cross, made with the finger, or in\n   ink, etc., or actually represented in some material; the symbol of\n   Christs death; the ensign and chosen symbol of Christianity, of a\n   Christian people, and of Christendom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'Affiction regarded as a test of patience or virtue; trial;\n   disappointment; opposition; misfortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'A piece of money stamped with the figure of a cross, also,\n   that side of such a piece on which the cross is stamped; hence, money\n   in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'An appendage or ornament or anything in the form of a cross;\n   a badge or ornamental device of the general shape of a cross; hence,\n   such an ornament, even when varying considerably from that form; thus,\n   the Cross of the British Order of St. George and St. Michael consists\n   of a central medallion with seven arms radiating from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'A monument in the form of a cross, or surmounted by a cross,\n   set up in a public place; as, a market cross; a boundary cross; Charing\n   Cross in London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'A common heraldic bearing, of which there are many\n   varieties. See the Illustration, above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'The crosslike mark or symbol used instead of a signature by\n   those unable to write.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'Church lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'A line drawn across or through another line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'A mixing of breeds or stock, especially in cattle breeding;\n   or the product of such intermixture; a hybrid of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'An instrument for laying of offsets perpendicular to the\n   main course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'n.', 'A pipe-fitting with four branches the axes of which usually\n   forms right angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'a.', 'Not parallel; lying or falling athwart; transverse; oblique;\n   intersecting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'a.', 'Not accordant with what is wished or expected; interrupting;\n   adverse; contrary; thwarting; perverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'a.', 'Characterized by, or in a state of, peevishness,\n   fretfulness, or ill humor; as, a cross man or woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'a.', 'Made in an opposite direction, or an inverse relation;\n   mutually inverse; interchanged; as, cross interrogatories; cross\n   marriages, as when a brother and sister marry persons standing in the\n   same relation to each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'prep.', 'Athwart; across.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossed', 'imp. & p. p.', 'of Cross');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossing', 'p. pr. & vb. n.', 'of Cross');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. t.', 'To put across or athwart; to cause to intersect; as, to\n   cross the arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. t.', 'To lay or draw something, as a line, across; as, to cross\n   the letter t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. t.', 'To pass from one side to the other of; to pass or move\n   over; to traverse; as, to cross a stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. t.', 'To pass, as objects going in an opposite direction at the\n   same time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. t.', 'To run counter to; to thwart; to obstruct; to hinder; to\n   clash or interfere with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. t.', 'To interfere and cut off; to debar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. t.', 'To make the sign of the cross upon; -- followed by the\n   reflexive pronoun; as, he crossed himself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. t.', 'To cancel by marking crosses on or over, or drawing a\n   line across; to erase; -- usually with out, off, or over; as, to cross\n   out a name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. t.', 'To cause to interbreed; -- said of different stocks or\n   races; to mix the breed of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. i.', 'To lie or be athwart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. i.', 'To move or pass from one side to the other, or from place\n   to place; to make a transit; as, to cross from New York to Liverpool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. i.', 'To be inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross', 'v. i.', 'To interbreed, as races; to mix distinct breeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-armed', 'a.', 'With arms crossed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-banded', 'a.', 'A term used when a narrow ribbon of veneer is\n   inserted into the surface of any piece of furniture, wainscoting, etc.,\n   so that the grain of it is contrary to the general surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbar', 'n.', 'A transverse bar or piece, as a bar across a door, or as\n   the iron bar or stock which passes through the shank of an anchor to\n   insure its turning fluke down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbarred', 'a.', 'Secured by, or furnished with, crossbars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbarred', 'a.', 'Made or patterned in lines crossing each other; as,\n   crossbarred muslin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbeak', 'n.', 'Same as Crossbill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbeam', 'n.', 'A girder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbeam', 'n.', 'A beam laid across the bitts, to which the cable is\n   fastened when riding at anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-bearer', 'n.', 'A subdeacon who bears a cross before an archbishop or\n   primate on solemn occasions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbill', '', 'A bill brought by a defendant, in an equity or chancery\n   suit, against the plaintiff, respecting the matter in question in that\n   suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbill', 'n.', 'A bird of the genus Loxia, allied to the finches. Their\n   mandibles are strongly curved and cross each other; the crossbeak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-birth', 'n.', 'Any preternatural labor, in which the body of the\n   child lies across the pelvis of the mother, so that the shoulder, arm,\n   or trunk is the part first presented at the mouth of the uterus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbite', 'n.', 'A deception; a cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbite', 'b. t.', 'To deceive; to trick; to gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbones', 'n. pl.', 'A representation of two of the leg bones or arm\n   bones of a skeleton, laid crosswise, often surmounted with a skull, and\n   serving as a symbol of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbow', 'n.', 'A weapon, used in discharging arrows, formed by placing a\n   bow crosswise on a stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbower', 'n.', 'A crossbowman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbowman', 'n.', 'One who shoots with a crossbow. See Arbalest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbred', 'a.', 'Produced by mixing distinct breeds; mongrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbreed', 'n.', 'A breed or an animal produced from parents of different\n   breeds; a new variety, as of plants, combining the qualities of two\n   parent varieties or stocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossbreed', 'n.', 'Anything partaking of the natures of two different\n   things; a hybrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-bun', 'n.', 'A bun or cake marked with a cross, and intended to be\n   eaten on Good Friday.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-crosslet', 'n.', 'A cross having the three upper ends crossed, so as\n   to from three small crosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosscut', 'v. t.', 'To cut across or through; to intersect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosscut', 'n.', 'A short cut across; a path shorter than by the high road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosscut', 'n.', 'A level driven across the course of a vein, or across the\n   main workings, as from one gangway to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-days', 'n. pl.', 'The three days preceding the Feast of the\n   Ascension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossette', 'n.', 'A return in one of the corners of the architrave of a\n   door or window; -- called also ancon, ear, elbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossette', 'n.', 'The shoulder of a joggled keystone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-examination', 'n.', 'The interrogating or questioning of a witness by\n   the party against whom he has been called and examined. See\n   Examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-examined', 'imp. & p. p.', 'of Cross-examine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-examining', 'p. pr. & vb. n.', 'of Cross-examine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-examine', 'v. t.', 'To examine or question, as a witness who has been\n   called and examined by the opposite party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-examiner', 'n.', 'One who cross-examines or conducts a\n   crosse-examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-eye', 'n.', 'See Strabismus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-eyed', 'a.', 'Affected with strabismus; squint-eyed; squinting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossfish', 'n.', 'A starfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossflow', 'v. i.', 'To flow across, or in a contrary direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-garnet', 'n.', 'A hinge having one strap perpendicular and the other\n   strap horizontal giving it the form of an Egyptian or T cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossgrained', 'a.', 'Having the grain or fibers run diagonally, or more or\n   less transversely an irregularly, so as to interfere with splitting or\n   planing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossgrained', 'a.', 'Perverse; untractable; contrary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossnath', 'v. t.', 'To shade by means of crosshatching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosshatching', 'n.', 'In drawing and line engraving, shading with lines\n   that cross one another at an angle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosshead', 'n.', 'A beam or bar across the head or end of a rod, etc., or\n   a block attached to it and carrying a knuckle pin; as the solid\n   crosspiece running between parallel slides, which receives motion from\n   the piston of a steam engine and imparts it to the connecting rod,\n   which is hinged to the crosshead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossing', 'v. t.', 'The act by which anything is crossed; as, the crossing\n   of the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossing', 'v. t.', 'The act of making the sign of the cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossing', 'v. t.', 'The act of interbreeding; a mixing of breeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossing', 'v. t.', 'Intersection, as of two paths or roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossing', 'v. t.', 'A place where anything (as a stream) is crossed; a\n   paved walk across a street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossing', 'v. t.', 'Contradiction; thwarting; obstruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossjack', 'n.', 'The lowest square sail, or the lower yard of the\n   mizzenmast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'CRosslegged', 'a.', 'Having the legs crossed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosslet', 'n.', 'A small cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosslet', 'n.', 'A crucible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosslet', 'a.', 'Crossed again; -- said of a cross the arms of which are\n   crossed. SeeCross-crosslet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossly', 'adv.', 'Athwart; adversely; unfortunately; peevishly; fretfully;\n   with ill humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossness', 'n.', 'The quality or state of being cross; peevishness;\n   fretfulness; ill humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossopterygian', 'a.', 'Of or pertaining to the Crossopterygii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossopterygian', 'n.', 'One of the Crossopterygii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossopterygii', 'n. pl.', 'An order of ganoid fishes including among\n   living species the bichir (Polypterus). See Brachioganoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosspatch', 'n.', 'An ill-natured person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-pawl', 'n.', 'Same as Cross-spale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosspiece', 'n.', 'A piece of any structure which is fitted or framed\n   crosswise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosspiece', 'n.', 'A bar or timber connecting two knightheads or two\n   bitts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-purpose', 'n.', 'A counter or opposing purpose; hence, that which is\n   inconsistent or contradictory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-purpose', 'n.', 'A conversational game, in which questions and\n   answers are made so as to involve ludicrous combinations of ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-questioned', 'imp. & p. p.', 'of Cross-question');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-questioning', 'p. pr. & vb. n.', 'of Cross-question');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-question', 'v. t.', 'To cross-examine; to subject to close\n   questioning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-reading', 'n.', 'The reading of the lines of a newspaper directly\n   across the page, instead of down the columns, thus producing a\n   ludicrous combination of ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossroad', 'n.', 'A road that crosses another; an obscure road\n   intersecting or avoiding the main road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossrow', 'n.', 'The alphabet; -- called also Christcross-row.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossrow', 'n.', 'A row that crosses others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossruff', 'n.', 'The play in whist where partners trump each a different\n   suit, and lead to each other for that purpose; -- called also seesaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-spale', 'n.', 'Alt. of Cross-spall');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-spall', 'n.', 'One of the temporary wooden braces, placed\n   horizontally across a frame to hold it in position until the deck beams\n   are in; a cross-pawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-springer', 'n.', 'One of the ribs in a groined arch, springing from\n   the corners in a diagonal direction. [See Illustr. of Groined vault.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-staff', 'n.', 'An instrument formerly used at sea for taking the\n   altitudes of celestial bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-staff', 'n.', 'A surveyors instrument for measuring offsets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-stitch', 'n.', 'A form of stitch, where the stitches are diagonal and\n   in pairs, the thread of one stitch crossing that of the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-stone', 'n.', 'See Harmotome, and Staurotide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-tail', 'n.', 'A bar connecting the ends of the side rods or levers of\n   a backaction or side-lever engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-tie', 'n.', 'A sleeper supporting and connecting the rails, and\n   holding them in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-tining', 'n.', 'A mode of harrowing crosswise, or transversely to the\n   ridges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosstrees', 'n. pl.', 'Pieces of timber at a masthead, to which are\n   attached the upper shrouds. At the head of lower masts in large\n   vessels, they support a semicircular platform called the \"top.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-vaulting', 'n.', 'Vaulting formed by the intersection of two or more\n   simple vaults.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crossway', 'n.', 'See Crossroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cross-week', 'n.', 'Rogation week, when the cross was borne in processions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosswise', 'adv.', 'In the form of a cross; across; transversely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crosswort', 'n.', 'A name given to several inconspicuous plants having\n   leaves in whorls of four, as species of Crucianella, Valantia, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotalaria', 'n.', 'A genus of leguminous plants; rattlebox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotaline', 'a.', 'Resembling, or pertaining to, the Crotalidae, or\n   Rattlesnake family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotalo', 'n.', 'A Turkish musical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotalum', 'n.', 'A kind of castanet used by the Corybantes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotalus', 'n.', 'A genus of poisonous serpents, including the\n   rattlesnakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotaphite', 'n.', 'The temple or temporal fossa. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotaphitic', 'n.', 'Pertaining to the temple; temporal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotches', 'pl. ', 'of Crotch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotch', 'n.', 'The angle formed by the parting of two legs or branches; a\n   fork; the point where a trunk divides; as, the crotch of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotch', 'n.', 'A stanchion or post of wood or iron, with two arms for\n   supporting a boom, spare yards, etc.; -- called also crane and crutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotched', 'a.', 'Having a crotch; forked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotched', 'a.', 'Cross; peevish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotchet', 'n.', 'A forked support; a crotch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotchet', 'n.', 'A time note, with a stem, having one fourth the value of\n   a semibreve, one half that of a minim, and twice that of a quaver; a\n   quarter note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotchet', 'n.', 'An indentation in the glacis of the covered way, at a\n   point where a traverse is placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotchet', 'n.', 'The arrangement of a body of troops, either forward or\n   rearward, so as to form a line nearly perpendicular to the general line\n   of battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotchet', 'n.', 'A bracket. See Bracket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotchet', 'n.', 'An instrument of a hooked form, used in certain cases in\n   the extraction of a fetus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotchet', 'n.', 'A perverse fancy; a whim which takes possession of the\n   mind; a conceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotchet', 'v. i.', 'To play music in measured time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotcheted', 'a.', 'Marked or measured by crotchets; having musical\n   notation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotchetiness', 'n.', 'The state or character of being crotchety, or\n   whimsical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotchety', 'a.', 'Given to crotchets; subject to whims; as, a crotchety\n   man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croton', 'n.', 'A genus of euphorbiaceous plants belonging to tropical\n   countries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croton bug', '', 'A small, active, winged species of cockroach (Ectobia\n   Germanica), the water bug. It is common aboard ships, and in houses in\n   cities, esp. in those with hot-water pipes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotonic', 'a.', 'Of or pertaining to, or derived from, a plant of the\n   genus Croton, or from croton oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotonine', 'n.', 'A supposed alkaloid obtained from croton oil by boiling\n   it with water and magnesia, since found to be merely a magnesia soap of\n   the oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crotonylene', 'n.', 'A colorless, volatile, pungent liquid, C4H6, produced\n   artificially, and regarded as an unsaturated hydrocarbon of the\n   acetylene series, and analogous to crotonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crottles', 'n. pl.', 'A name given to various lichens gathered for dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouched', 'imp. & p. p.', 'of Crouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouching', 'p. pr. & vb. n.', 'of Crouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouch', 'v. i.', 'To bend down; to stoop low; to lie close to the ground\n   with the logs bent, as an animal when waiting for prey, or in fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouch', 'v. i.', 'To bend servilely; to stoop meanly; to fawn; to cringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouch', 'v. t.', 'To sign with the cross; to bless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouch', 'v. t.', 'To bend, or cause to bend, as in humility or fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouched', 'a.', 'Marked with the sign of the cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croud', 'n.', 'See Crowd, a violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouke', 'n.', 'A crock; a jar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croup', 'n.', 'The hinder part or buttocks of certain quadrupeds,\n   especially of a horse; hence, the place behind the saddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croup', 'n.', 'An inflammatory affection of the larynx or trachea,\n   accompanied by a hoarse, ringing cough and stridulous, difficult\n   breathing; esp., such an affection when associated with the development\n   of a false membrane in the air passages (also called membranous croup).\n   See False croup, under False, and Diphtheria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croupade', 'n.', 'A leap in which the horse pulls up his hind legs toward\n   his belly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croupal', 'a.', 'Croupy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouper', 'n.', 'See Crupper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croupier', 'n.', 'One who presides at a gaming table and collects the\n   stakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croupier', 'n.', 'One who, at a public dinner party, sits at the lower end\n   of the table as assistant chairman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croupous', 'a.', 'Relating to or resembling croup; especially, attended\n   with the formation of a deposit or membrane like that found in\n   membranous croup; as, croupous laryngitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croupy', 'a.', 'Of or pertaining to croup; resembling or indicating croup;\n   as, a croupy cough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouse', 'a.', 'Brisk; lively; bold; self-complacent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croustade', 'n.', 'Bread baked in a mold, and scooped out, to serve minces\n   upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crout', 'n.', 'See Sourkrout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crouton', 'n.', 'Bread cut in various forms, and fried lightly in butter or\n   oil, to garnish hashes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crew', 'imp.', 'of Crow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowed', '', 'of Crow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowed', 'p. p.', 'of Crow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', '', 'of Crow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowing', 'p. pr. & vb. n.', 'of Crow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crow', 'v. i.', 'To make the shrill sound characteristic of a cock, either\n   in joy, gayety, or defiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crow', 'v. i.', 'To shout in exultation or defiance; to brag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crow', 'v. i.', 'To utter a sound expressive of joy or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crow', 'v. i.', 'A bird, usually black, of the genus Corvus, having a\n   strong conical beak, with projecting bristles. It has a harsh, croaking\n   note. See Caw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crow', 'v. i.', 'A bar of iron with a beak, crook, or claw; a bar of iron\n   used as a lever; a crowbar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crow', 'v. i.', 'The cry of the cock. See Crow, v. i., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crow', 'v. i.', 'The mesentery of a beast; -- so called by butchers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowbar', 'n.', 'A bar of iron sharpened at one end, and used as a lever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowberry', 'n.', 'A heathlike plant of the genus Empetrum, and its fruit,\n   a black, scarcely edible berry; -- also called crakeberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowded', 'imp. & p. p.', 'of Crowd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowding', 'p. pr. & vb. n.', 'of Crowd');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'v. t.', 'To push, to press, to shove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'v. t.', 'To press or drive together; to mass together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'v. t.', 'To fill by pressing or thronging together; hence, to\n   encumber by excess of numbers or quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'v. t.', 'To press by solicitation; to urge; to dun; hence, to\n   treat discourteously or unreasonably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'v. i.', 'To press together or collect in numbers; to swarm; to\n   throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'v. i.', 'To urge or press forward; to force ones self; as, a man\n   crowds into a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'v. t.', 'A number of things collected or closely pressed together;\n   also, a number of things adjacent to each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'v. t.', 'A number of persons congregated or collected into a close\n   body without order; a throng.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'v. t.', 'The lower orders of people; the populace; the vulgar; the\n   rabble; the mob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'n.', 'An ancient instrument of music with six strings; a kind of\n   violin, being the oldest known stringed instrument played with a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowd', 'v. t.', 'To play on a crowd; to fiddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowder', 'n.', 'One who plays on a crowd; a fiddler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowder', 'n.', 'One who crowds or pushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowdy', 'n.', 'A thick gruel of oatmeal and milk or water; food of the\n   porridge kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowflower', 'n.', 'A kind of campion; according to Gerarde, the Lychnis\n   Flos-cuculi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowfoot', 'n.', 'The genus Ranunculus, of many species; some are common\n   weeds, others are flowering plants of considerable beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowfoot', 'n.', 'A number of small cords rove through a long block, or\n   euphroe, to suspend an awning by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowfoot', 'n.', 'A caltrop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowfoot', 'n.', 'A tool with a side claw for recovering broken rods, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowkeeper', 'n.', 'A person employed to scare off crows; hence, a\n   scarecrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', '', 'p. p. of Crow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'A wreath or garland, or any ornamental fillet encircling the\n   head, especially as a reward of victory or mark of honorable\n   distinction; hence, anything given on account of, or obtained by,\n   faithful or successful effort; a reward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'A royal headdress or cap of sovereignty, worn by emperors,\n   kings, princes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The person entitled to wear a regal or imperial crown; the\n   sovereign; -- with the definite article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'Imperial or regal power or dominion; sovereignty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'Anything which imparts beauty, splendor, honor, dignity, or\n   finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'Highest state; acme; consummation; perfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The topmost part of anything; the summit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The topmost part of the head (see Illust. of Bird.); that\n   part of the head from which the hair descends toward the sides and\n   back; also, the head or brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The part of a hat above the brim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The part of a tooth which projects above the gum; also, the\n   top or grinding surface of a tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The vertex or top of an arch; -- applied generally to about\n   one third of the curve, but in a pointed arch to the apex only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'Same as Corona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'That part of an anchor where the arms are joined to the\n   shank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The rounding, or rounded part, of the deck from a level\n   line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The bights formed by the several turns of a cable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The upper range of facets in a rose diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The dome of a furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'The area inclosed between two concentric perimeters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'A round spot shaved clean on the top of the head, as a mark\n   of the clerical state; the tonsure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'A size of writing paper. See under Paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'A coin stamped with the image of a crown; hence,a\n   denomination of money; as, the English crown, a silver coin of the\n   value of five shillings sterling, or a little more than $1.20; the\n   Danish or Norwegian crown, a money of account, etc., worth nearly\n   twenty-seven cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'An ornaments or decoration representing a crown; as, the\n   paper is stamped with a crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowned', 'imp. & p. p.', 'of Crown');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowning', 'p. pr. & vb. n.', 'of Crown');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'To cover, decorate, or invest with a crown; hence, to invest\n   with royal dignity and power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'To bestow something upon as a mark of honor, dignity, or\n   recompense; to adorn; to dignify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'To form the topmost or finishing part of; to complete; to\n   consummate; to perfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'To cause to round upward; to make anything higher at the\n   middle than at the edges, as the face of a machine pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown', 'n.', 'To effect a lodgment upon, as upon the crest of the glacis,\n   or the summit of the breach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowned', 'p. p. & a.', 'Having or wearing a crown; surmounted, invested,\n   or adorned, with a crown, wreath, garland, etc.; honored; rewarded;\n   completed; consummated; perfected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowned', 'p. p. & a.', 'Great; excessive; supreme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowner', 'n.', 'One who, or that which, crowns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowner', 'n.', 'A coroner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crownet', 'n.', 'A coronet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crownet', 'n.', 'The ultimate end and result of an undertaking; a chief\n   end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown-imperial', 'n.', 'A spring-blooming plant (Fritillaria imperialis) of\n   the Lily family, having at the top of the stalk a cluster of pendent\n   bell-shaped flowers surmounted with a tuft of green leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crownless', 'a.', 'Without a crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crownlet', 'n.', 'A coronet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown office', '', 'The criminal branch of the Court of Kings or Queens\n   Bench, commonly called the crown side of the court, which takes\n   cognizance of all criminal cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crownpiece', 'n.', 'A piece or part which passes over the head, as in a\n   bridle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crownpiece', 'n.', 'A coin [In sense (b) properly crown piece.] See Crown,\n   19.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown-post', 'n.', 'Same as King-post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown-saw', 'n.', 'A saw in the form of a hollow cylinder, with teeth on\n   the end or edge, and operated by a rotative motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown side', '', 'See Crown office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crown wheel', '', 'A wheel with cogs or teeth set at right angles to its\n   plane; -- called also a contrate wheel or face wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crownwork', 'n.', 'A work consisting of two or more bastioned fronts, with\n   their outworks, covering an enceinte, a bridgehead, etc., and connected\n   by wings with the main work or the river bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crow-quill', 'n.', 'A quill of the crow, or a very fine pen made from such\n   a quill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crows', 'n. pl.', 'A tribe of Indians of the Dakota stock, living in\n   Montana; -- also called Upsarokas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crows-feet', 'pl. ', 'of Crows-foot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crows-foot', 'n.', 'The wrinkles that appear, as the effect of age or\n   dissipation, under and around the outer corners of the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crows-foot', 'n.', 'A caltrop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crows-foot', 'n.', 'Same as Birds-mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crow-silk', 'n.', 'A filamentous fresh-water alga (Conferva rivularis of\n   Linnaeus, Rhizoclonium rivulare of Kutzing).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crows-nest', 'n.', 'A box or perch near the top of a mast, esp. in\n   whalers, to shelter the man on the lookout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowstep', 'n.', 'See Corriestep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowstone', 'n.', 'The top stone of the gable end of a house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowth', 'n.', 'An ancient musical instrument. See 4th Crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowtoe', 'n.', 'The Lotus corniculatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crowtoe', 'n.', 'An unidentified plant, probably the crowfoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crow-trodden', 'a.', 'Marked with crows-feet, or wrinkles, about the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croylstone', 'n.', 'Crystallized cawk, in which the crystals are small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croys', 'n.', 'See Cross, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croze', 'n.', 'A coopers tool for making the grooves for the heads of\n   casks, etc.; also, the groove itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crozier', 'n.', 'See Crosier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Croziered', 'a.', 'Crosiered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucial', 'a.', 'Having the form of a cross; appertaining to a cross;\n   cruciform; intersecting; as, crucial ligaments; a crucial incision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucial', 'a.', 'Severe; trying or searching, as if bringing to the cross;\n   decisive; as, a crucial test.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucian carp', '', 'A kind of European carp (Carasius vulgaris), inferior\n   to the common carp; -- called also German carp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruciate', 'a.', 'Tormented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruciate', 'a.', 'Having the leaves or petals arranged in the form of a\n   cross; cruciform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruciate', 'v. t.', 'To torture; to torment. [Obs.] See Excruciate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruciation', 'n.', 'The act of torturing; torture; torment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucible', 'n.', 'A vessel or melting pot, composed of some very refractory\n   substance, as clay, graphite, platinum, and used for melting and\n   calcining substances which require a strong degree of heat, as metals,\n   ores, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucible', 'n.', 'A hollow place at the bottom of a furnace, to receive the\n   melted metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucible', 'n.', 'A test of the most decisive kind; a severe trial; as, the\n   crucible of affliction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucifer', 'n.', 'Any plant of the order Cruciferae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruciferous', 'a.', 'Bearing a cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruciferous', 'a.', 'Of, pertaining to, or resembling, a family of plants\n   which have four petals arranged like the arms of a cross, as the\n   mustard, radish, turnip, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucifier', 'n.', 'One who crucifies; one who subjects himself or another\n   to a painful trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucifixes', 'pl. ', 'of Crucifix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucifix', 'n.', 'A representation in art of the figure of Christ upon the\n   cross; esp., the sculptured figure affixed to a real cross of wood,\n   ivory, metal, or the like, used by the Roman Catholics in their\n   devotions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucifix', 'n.', 'The cross or religion of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucifixion', 'n.', 'The act of nailing or fastening a person to a cross,\n   for the purpose of putting him to death; the use of the cross as a\n   method of capital punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucifixion', 'n.', 'The state of one who is nailed or fastened to a cross;\n   death upon a cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucifixion', 'n.', 'Intense suffering or affliction; painful trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruciform', 'a.', 'Cross-shaped; (Bot.) having four parts arranged in the\n   form of a cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucified', 'imp. & p. p.', 'of Crucify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucifying', 'p. pr. & vb. n.', 'of Crucify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucify', 'v. t.', 'To fasten to a cross; to put to death by nailing the\n   hands and feet to a cross or gibbet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucify', 'v. t.', 'To destroy the power or ruling influence of; to subdue\n   completely; to mortify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucify', 'v. t.', 'To vex or torment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crucigerous', 'a.', 'Bearing the cross; marked with the figure of a cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crud', 'n.', 'See Curd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruddle', 'v. i.', 'To curdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crude', 'superl.', 'In its natural state; not cooked or prepared by fire or\n   heat; undressed; not altered, refined, or prepared for use by any\n   artificial process; raw; as, crude flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crude', 'superl.', 'Unripe; not mature or perfect; immature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crude', 'superl.', 'Not reduced to order or form; unfinished; not arranged\n   or prepared; ill-considered; immature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crude', 'superl.', 'Undigested; unconcocted; not brought into a form to\n   give nourishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crude', 'superl.', 'Having, or displaying, superficial and undigested\n   knowledge; without culture or profundity; as, a crude reasoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crude', 'superl.', 'Harsh and offensive, as a color; tawdry or in bad\n   taste, as a combination of colors, or any design or work of art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crudely', 'adv.', 'In a crude, immature manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crudeness', 'n.', 'A crude, undigested, or unprepared state; rawness;\n   unripeness; immatureness; unfitness for a destined use or purpose; as,\n   the crudeness of iron ore; crudeness of theories or plans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crudities', 'pl. ', 'of Crudity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crudity', 'n.', 'The condition of being crude; rawness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crudity', 'n.', 'That which is in a crude or undigested state; hence,\n   superficial, undigested views, not reduced to order or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crudle', 'v. i.', 'See Cruddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crudy', 'a.', 'Coagulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crudy', 'a.', 'Characterized by crudeness; raw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruel', 'n.', 'See Crewel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruel', 'a.', 'Disposed to give pain to others; willing or pleased to hurt,\n   torment, or afflict; destitute of sympathetic kindness and pity;\n   savage; inhuman; hard-hearted; merciless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruel', 'a.', 'Causing, or fitted to cause, pain, grief, or misery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruel', 'a.', 'Attended with cruetly; painful; harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruelly', 'adv.', 'In a cruel manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruelly', 'adv.', 'Extremely; very.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruelness', 'n.', 'Cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruels', 'n. pl.', 'Glandular scrofulous swellings in the neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruelties', 'pl. ', 'of Cruelty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruelty', 'n.', 'The attribute or quality of being cruel; a disposition to\n   give unnecessary pain or suffering to others; inhumanity; barbarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruelty', 'n.', 'A cruel and barbarous deed; inhuman treatment; the act of\n   willfully causing unnecessary pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruentate', 'a.', 'Smeared with blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruentous', 'a.', 'Bloody; cruentate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruet', 'n.', 'A bottle or vessel; esp., a vial or small glass bottle for\n   holding vinegar, oil, pepper, or the like, for the table; a caster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruet', 'n.', 'A vessel used to hold wine, oil, or water for the service of\n   the altar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruise', 'n.', 'See Cruse, a small bottle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruised', 'imp. & p. p.', 'of Cruise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruising', 'p. pr. & vb. n.', 'of Cruise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruise', 'v. i.', 'To sail back and forth on the ocean; to sail, as for the\n   potection of commerce, in search of an enemy, for plunder, or for\n   pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruise', 'v. i.', 'To wander hither and thither on land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruise', 'n.', 'A voyage made in various directions, as of an armed vessel,\n   for the protection of other vessels, or in search of an enemy; a\n   sailing to and fro, as for exploration or for pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruiser', 'n.', 'One who, or a vessel that, cruises; -- usually an armed\n   vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruive', 'n.', 'A kind of weir or dam for trapping salmon; also, a hovel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crull', 'a.', 'Curly; curled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruller', 'n.', 'A kind of sweet cake cut in strips and curled or twisted,\n   and fried crisp in boiling fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumb', 'n.', 'A small fragment or piece; especially, a small piece of\n   bread or other food, broken or cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumb', 'n.', 'Fig.: A little; a bit; as, a crumb of comfort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumb', 'n.', 'The soft part of bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumbed', 'imp. & p. p.', 'of Crumb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumbing', 'p. pr. & vb. n.', 'of Crumb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumb', 'v. t.', 'To break into crumbs or small pieces with the fingers;\n   as, to crumb bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumbcloth', 'n.', 'A cloth to be laid under a dining table to receive\n   falling fragments, and keep the carpet or floor clean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumbled', 'imp. & p. p.', 'of Crumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumbling', 'p. pr. & vb. n.', 'of Crumble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumble', 'v. t.', 'To break into small pieces; to cause to fall in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumble', 'v. i.', 'To fall into small pieces; to break or part into small\n   fragments; hence, to fall to decay or ruin; to become disintegrated; to\n   perish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumbly', 'a.', 'EAsily crumbled; friable; brittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumenal', 'n.', 'A purse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crummable', 'a.', 'Capable of being crumbed or broken into small pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crummy', 'a.', 'Full of crumb or crumbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crummy', 'a.', 'Soft, as the crumb of bread is; not crusty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crump', 'a.', 'Crooked; bent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crump', 'a.', 'Hard or crusty; dry baked; as, a crump loaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumpet', 'n.', 'A kind of large, thin muffin or cake, light and spongy,\n   and cooked on a griddle or spider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumpled', 'imp. & p. p.', 'of Crumple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumpling', 'p. pr. & vb. n.', 'of Crumple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumple', 'v. t.', 'To draw or press into wrinkles or folds; to crush\n   together; to rumple; as, to crumple paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumple', 'v. i.', 'To contract irregularly; to show wrinkles after being\n   crushed together; as, leaves crumple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crumpy', 'a.', 'Brittle; crisp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crunched', 'imp. & p. p.', 'of Crunch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crunching', 'p. pr. & vb. n.', 'of Crunch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crunch', 'v. i.', 'To chew with force and noise; to craunch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crunch', 'v. i.', 'To grind or press with violence and noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crunch', 'v. i.', 'To emit a grinding or craunching noise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crunch', 'v. t.', 'To crush with the teeth; to chew with a grinding noise;\n   to craunch; as, to crunch a biscuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crunk', 'v. i.', 'Alt. of Crunkle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crunkle', 'v. i.', 'To cry like a crane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crunodal', 'a.', 'Possessing, or characterized by, a crunode; -- used of\n   curves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crunode', 'n.', 'A point where one branch of a curve crosses another\n   branch. See Double point, under Double, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruor', 'n.', 'The coloring matter of the blood; the clotted portion of\n   coagulated blood, containing the coloring matter; gore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruorin', 'n.', 'The coloring matter of the blood in the living animal;\n   haemoglobin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crup', 'a.', 'Short; brittle; as, crup cake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crup', 'n.', 'See Croup, the rump of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crupper', 'n.', 'The buttocks or rump of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crupper', 'n.', 'A leather loop, passing under a horses tail, and buckled\n   to the saddle to keep it from slipping forwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crupper', 'v. t.', 'To fit with a crupper; to place a crupper upon; as, to\n   crupper a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crura', 'n. pl.', 'See Crus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crural', 'a.', 'Of or pertaining to the thigh or leg, or to any of the\n   parts called crura; as, the crural arteries; crural arch; crural canal;\n   crural ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crura', 'pl. ', 'of Crus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crus', 'n.', 'That part of the hind limb between the femur, or thigh, and\n   the ankle, or tarsus; the shank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crus', 'n.', 'Often applied, especially in the plural, to parts which are\n   supposed to resemble a pair of legs; as, the crura of the diaphragm, a\n   pair of muscles attached to it; crura cerebri, two bundles of nerve\n   fibers in the base of the brain, connecting the medulla and the\n   forebrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusade', 'n.', 'Any one of the military expeditions undertaken by\n   Christian powers, in the 11th, 12th, and 13th centuries, for the\n   recovery of the Holy Land from the Mohammedans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusade', 'n.', 'Any enterprise undertaken with zeal and enthusiasm; as, a\n   crusade against intemperance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusade', 'n.', 'A Portuguese coin. See Crusado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusaded', 'imp. & p. p.', 'of Crusade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusading', 'p. pr. & vb. n.', 'of Crusade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusade', 'v. i.', 'To engage in a crusade; to attack in a zealous or\n   hot-headed manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusader', 'n.', 'One engaged in a crusade; as, the crusaders of the Middle\n   Ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusading', 'a.', 'Of or pertaining to a crusade; as, a crusading spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusado', 'n.', 'An old Portuguese coin, worth about seventy cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruse', 'n.', 'A cup or dish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruse', 'n.', 'A bottle for holding water, oil, honey, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruset', 'n.', 'A goldsmiths crucible or melting pot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crushed', 'imp. & p. p.', 'of Crush');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crushing', 'p. pr. & vb. n.', 'of Crush');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crush', 'v. t.', 'To press or bruise between two hard bodies; to squeeze,\n   so as to destroy the natural shape or integrity of the parts, or to\n   force together into a mass; as, to crush grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crush', 'v. t.', 'To reduce to fine particles by pounding or grinding; to\n   comminute; as, to crush quartz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crush', 'v. t.', 'To overwhelm by pressure or weight; to beat or force\n   down, as by an incumbent weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crush', 'v. t.', 'To oppress or burden grievously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crush', 'v. t.', 'To overcome completely; to subdue totally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crush', 'v. i.', 'To be or become broken down or in, or pressed into a\n   smaller compass, by external weight or force; as, an eggshell crushes\n   easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crush', 'n.', 'A violent collision or compression; a crash; destruction;\n   ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crush', 'n.', 'Violent pressure, as of a crowd; a crowd which produced\n   uncomfortable pressure; as, a crush at a peception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusher', 'n.', 'One who, or that which, crushes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crushing', 'a.', 'That crushes; overwhelming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crust', 'n.', 'The hard external coat or covering of anything; the hard\n   exterior surface or outer shell; an incrustation; as, a crust of snow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crust', 'n.', 'The hard exterior or surface of bread, in distinction from\n   the soft part or crumb; or a piece of bread grown dry or hard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crust', 'n.', 'The cover or case of a pie, in distinction from the soft\n   contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crust', 'n.', 'The dough, or mass of doughy paste, cooked with a potpie; --\n   also called dumpling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crust', 'n.', 'The exterior portion of the earth, formerly universally\n   supposed to inclose a molten interior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crust', 'n.', 'The shell of crabs, lobsters, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crust', 'n.', 'A hard mass, made up of dried secretions blood, or pus,\n   occurring upon the surface of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crust', 'n.', 'An incrustation on the interior of wine bottles, the result\n   of the ripening of the wine; a deposit of tartar, etc. See Beeswing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusted', 'imp. & p. p.', 'of Crust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusting', 'p. pr. & vb. n.', 'of Crust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crust', 'n.', 'To cover with a crust; to cover or line with an\n   incrustation; to incrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crust', 'v. i.', 'To gather or contract into a hard crust; to become\n   incrusted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusta', 'n.', 'A crust or shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusta', 'n.', 'A gem engraved, or a plate embossed in low relief, for\n   inlaying a vase or other object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustacea', 'n. pl.', 'One of the classes of the arthropods, including\n   lobsters and crabs; -- so called from the crustlike shell with which\n   they are covered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustacean', 'a.', 'Of or pertaining to the Crustacea; crustaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustacean', 'n.', 'An animal belonging to the class Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustaceological', 'a.', 'Pertaining to crustaceology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustaceologist', 'n.', 'One versed in crustaceology; a crustalogist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustaceology', 'n.', 'That branch of Zoology which treats of the\n   Crustacea; malacostracology; carcinology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustaceous', 'a.', 'Pertaining to, or of the nature of, crust or shell;\n   having a crustlike shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustaceous', 'a.', 'Belonging to the Crustacea; crustacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustaceousness', 'n.', 'The state or quality of being crustaceous or\n   having a crustlike shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustal', 'a.', 'Relating to a crust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustalogical', 'a.', 'Pertaining to crustalogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustalogist', 'n.', 'One versed in crustalogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustalogy', 'n.', 'Crustaceology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustated', 'a.', 'Covered with a crust; as, crustated basalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustation', 'n.', 'An adherent crust; an incrustation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusted', 'a.', 'Incrusted; covered with, or containing, crust; as, old,\n   crusted port wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustific', 'a.', 'Producing or forming a crust or skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustily', 'adv.', 'In a crusty or surly manner; morosely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustiness', 'n.', 'The state or quality of having crust or being like\n   crust; hardness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crustiness', 'n.', 'The quality of being crusty or surly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusty', 'a.', 'Having the nature of crust; pertaining to a hard covering;\n   as, a crusty coat; a crusty surface or substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crusty', 'a.', 'Having a hard exterior, or a short, rough manner, though\n   kind at heart; snappish; peevish; surly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crut', 'n.', 'The rough, shaggy part of oak bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crutches', 'pl. ', 'of Crutch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crutch', 'n.', 'A staff with a crosspiece at the head, to be placed under\n   the arm or shoulder, to support the lame or infirm in walking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crutch', 'n.', 'A form of pommel for a womans saddle, consisting of a\n   forked rest to hold the leg of the rider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crutch', 'n.', 'A knee, or piece of knee timber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crutch', 'n.', 'A forked stanchion or post; a crotch. See Crotch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crutch', 'v. t.', 'To support on crutches; to prop up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crutched', 'a.', 'Supported upon crutches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crutched', 'a.', 'Marked with the sign of the cross; crouched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruth', 'n.', 'See 4th Crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruxes', 'pl. ', 'of Crux');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruces', 'pl. ', 'of Crux');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crux', 'n.', 'Anything that is very puzzling or difficult to explain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cruzado', 'n.', 'A coin. See Crusado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crwth', 'n.', 'See 4th Crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cried', 'imp. & p. p.', 'of Cry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crying', 'p. pr. & vb. n.', 'of Cry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'To make a loud call or cry; to call or exclaim vehemently\n   or earnestly; to shout; to vociferate; to proclaim; to pray; to\n   implore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'To utter lamentations; to lament audibly; to express pain,\n   grief, or distress, by weeping and sobbing; to shed tears; to bawl, as\n   a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'To utter inarticulate sounds, as animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. t.', 'To utter loudly; to call out; to shout; to sound abroad; to\n   declare publicly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. t.', 'To cause to do something, or bring to some state, by crying\n   or weeping; as, to cry ones self to sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. t.', 'To make oral and public proclamation of; to declare\n   publicly; to notify or advertise by outcry, especially things lost or\n   found, goods to be sold, ets.; as, to cry goods, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. t.', 'to publish the banns of, as for marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cries', 'pl. ', 'of Cry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'A loud utterance; especially, the inarticulate sound\n   produced by one of the lower animals; as, the cry of hounds; the cry of\n   wolves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'Outcry; clamor; tumult; popular demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'Any expression of grief, distress, etc., accompanied with\n   tears or sobs; a loud sound, uttered in lamentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'Loud expression of triumph or wonder or of popular\n   acclamation or favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'Importunate supplication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'Public advertisement by outcry; proclamation, as by hawkers\n   of their wares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'Common report; fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'A word or phrase caught up by a party or faction and\n   repeated for effect; as, the party cry of the Tories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'A pack of hounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'A pack or company of persons; -- in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cry', 'v. i.', 'The crackling noise made by block tin when it is bent back\n   and forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryal', 'n.', 'The heron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryer', 'n.', 'The female of the hawk; a falcon-gentil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crying', 'a.', 'Calling for notice; compelling attention; notorious;\n   heinous; as, a crying evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryohydrate', 'n.', 'A substance, as salt, ammonium chloride, etc., which\n   crystallizes with water of crystallization only at low temperatures, or\n   below the freezing point of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryolite', 'n.', 'A fluoride of sodium and aluminum, found in Greenland, in\n   white cleavable masses; -- used as a source of soda and alumina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryophorus', 'n.', 'An instrument used to illustrate the freezing of water\n   by its own evaporation. The ordinary form consists of two glass bulbs,\n   connected by a tube of the same material, and containing only a\n   quantity of water and its vapor, devoid of air. The water is in one of\n   the bulbs, and freezes when the other is cooled below 32 Fahr.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crypt', 'n.', 'A vault wholly or partly under ground; especially, a vault\n   under a church, whether used for burial purposes or for a subterranean\n   chapel or oratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crypt', 'n.', 'A simple gland, glandular cavity, or tube; a follicle; as,\n   the crypts of Lieberk/hn, the simple tubular glands of the small\n   intestines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptal', 'a.', 'Of or pertaining to crypts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptic', 'a.', 'Alt. of Cryptical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptical', 'a.', 'Hidden; secret; occult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptically', 'adv.', 'Secretly; occultly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptidine', 'n.', 'One of the quinoline bases, obtained from coal tar as\n   an oily liquid, C11H11N; also, any one of several substances metameric\n   with, and resembling, cryptidine proper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptobranchiata', 'n. pl.', 'A division of the Amphibia; the Derotremata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptobranchiata', 'n. pl.', 'A group of nudibranch mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptobranchiate', 'a.', 'Having concealed or rudimentary gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptocrystalline', 'a.', 'Indistinctly crystalline; -- applied to rocks\n   and minerals, whose state of aggregation is so fine that no distinct\n   particles are visible, even under the microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptogam', 'n.', 'A plant belonging to the Cryptogamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptogamiae', 'pl. ', 'of Cryptogamia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptogamia', 'n.', 'The series or division of flowerless plants, or those\n   never having true stamens and pistils, but propagated by spores of\n   various kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptogamian', 'a.', 'Alt. of Cryptogamous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptogamic', 'a.', 'Alt. of Cryptogamous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptogamous', 'a.', 'Of or pertaining to the series Cryptogamia, or to\n   plants of that series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptogamist', 'n.', 'One skilled in cryptogamic botany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptogram', 'n.', 'A cipher writing. Same as Cryptograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptograph', 'n.', 'Cipher; something written in cipher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptographal', 'a.', 'Pertaining to cryptography; cryptographical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptographer', 'n.', 'One who writes in cipher, or secret characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptographic', 'a.', 'Alt. of Cryptographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptographical', 'a.', 'Relating to cryptography; written in secret\n   characters or in cipher, or with sympathetic ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptographist', 'n.', 'Same as Cryptographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptography', 'n.', 'The act or art of writing in secret characters; also,\n   secret characters, or cipher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptology', 'n.', 'Secret or enigmatical language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptonym', 'n.', 'A secret name; a name by which a person is known only to\n   the initiated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cryptopine', 'n.', 'A colorless crystalline alkaloid obtained in small\n   quantities from opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crypturi', 'n. pl.', 'An order of flying, drom/ognathous birds, including\n   the tinamous of South America. See Tinamou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystal', 'n.', 'The regular form which a substance tends to assume in\n   solidifying, through the inherent power of cohesive attraction. It is\n   bounded by plane surfaces, symmetrically arranged, and each species of\n   crystal has fixed axial ratios. See Crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystal', 'n.', 'The material of quartz, in crystallization transparent or\n   nearly so, and either colorless or slightly tinged with gray, or the\n   like; -- called also rock crystal. Ornamental vessels are made of it.\n   Cf. Smoky quartz, Pebble; also Brazilian pebble, under Brazilian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystal', 'n.', 'A species of glass, more perfect in its composition and\n   manufacture than common glass, and often cut into ornamental forms. See\n   Flint glass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystal', 'n.', 'The glass over the dial of a watch case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystal', 'n.', 'Anything resembling crystal, as clear water, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystal', 'a.', 'Consisting of, or like, crystal; clear; transparent;\n   lucid; pellucid; crystalline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallin', 'n.', 'See Gobulin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystalline', 'a.', 'Consisting, or made, of crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystalline', 'a.', 'Formed by crystallization; like crystal in texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystalline', 'a.', 'Imperfectly crystallized; as, granite is only\n   crystalline, while quartz crystal is perfectly crystallized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystalline', 'a.', 'Fig.: Resembling crystal; pure; transparent; pellucid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystalline', 'n.', 'A crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystalline', 'n.', 'See Aniline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallite', 'n.', 'A minute mineral form like those common in glassy\n   volcanic rocks and some slags, not having a definite crystalline\n   outline and not referable to any mineral species, but marking the first\n   step in the crystallization process. According to their form\n   crystallites are called trichites, belonites, globulites, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallizable', 'a.', 'Capable of being crystallized; that may be formed\n   into crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallization', 'n.', 'The act or process by which a substance in\n   solidifying assumes the form and structure of a crystal, or becomes\n   crystallized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallization', 'n.', 'The body formed by crystallizing; as, silver on\n   precipitation forms arborescent crystallizations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallized', 'imp. & p. p.', 'of Crystallize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallizing', 'p. pr. & vb. n.', 'of Crystallize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallize', 'v. t.', 'To cause to form crystals, or to assume the\n   crystalline form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallize', 'v. i.', 'To be converted into a crystal; to take on a\n   crystalline form, through the action of crystallogenic or cohesive\n   attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallogenic', 'a.', 'Alt. of Crystallogenical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallogenical', 'a.', 'Pertaining to the production of crystals;\n   crystal-producing; as, crystallogenic attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallogeny', 'n.', 'The science which pertains to the production of\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallographer', 'n.', 'One who describes crystals, or the manner of\n   their formation; one versed in crystallography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallographic', 'a.', 'Alt. of Crystallographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallographical', 'a.', 'Pertaining to crystallography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallographically', 'adv.', 'In the manner of crystallography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallography', 'n.', 'The doctrine or science of crystallization,\n   teaching the system of forms among crystals, their structure, and their\n   methods of formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallography', 'n.', 'A discourse or treatise on crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystalloid', 'a.', 'Crystal-like; transparent like crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystalloid', 'n.', 'A body which, in solution, diffuses readily through\n   animal membranes, and generally is capable of being crystallized; --\n   opposed to colloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystalloid', 'n.', 'One of the microscopic particles resembling crystals,\n   consisting of protein matter, which occur in certain plant cells; --\n   called also protein crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cristallology', 'n.', 'The science of the crystalline structure of\n   inorganic bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallomancy', 'n.', 'Divination by means of a crystal or other\n   transparent body, especially a beryl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallometry', 'n.', 'The art of measuring crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Crystallurgy', 'n.', 'Crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenocyst', 'n.', 'An organ of the Ctenophora, supposed to be sensory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenoid', 'a.', 'Having a comblike margin, as a ctenoid scale');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenoid', 'a.', 'Pertaining to the Ctenoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenoid', 'n.', 'A ctenoidean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenoidean', 'a.', 'Relating to the Ctenoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenoidean', 'n.', 'One of the Ctenoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenoidei', 'n. pl.', 'A group of fishes, established by Agassiz,\n   characterized by having scales with a pectinated margin, as in the\n   perch. The group is now generally regarded as artificial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenophora', 'n. pl.', 'A class of Coelenterata, commonly ellipsoidal in\n   shape, swimming by means of eight longitudinal rows of paddles. The\n   separate paddles somewhat resemble combs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenophore', 'n.', 'One of the Ctenophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenophoric', 'a.', 'Alt. of Ctenophorous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenophorous', 'a.', 'Of or pertaining to the Ctenophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ctenostomata', 'n. pl.', 'A suborder of Bryozoa, usually having a circle of\n   bristles below the tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cub', 'n.', 'A young animal, esp. the young of the bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cub', 'n.', 'Jocosely or in contempt, a boy or girl, esp. an awkward, rude,\n   ill-mannered boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubbed', 'imp. & p. p.', 'of Cub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubbing', 'p. pr. & vb. n.', 'of Cub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cub', 'v. t. & i.', 'To bring forth; -- said of animals, or in contempt, of\n   persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cub', 'n.', 'A stall for cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cub', 'n.', 'A cupboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cub', 'v. t.', 'To shut up or confine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuban', 'a.', 'Of or pertaining to Cuba or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuban', 'n.', 'A native or an inhabitant of Cuba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubation', 'n.', 'The act of lying down; a reclining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubatory', 'a.', 'Lying down; recumbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubature', 'n.', 'The process of determining the solid or cubic contents of\n   a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubbridge-head', 'n.', 'A bulkhead on the forecastle and half deck of a\n   ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubby', 'n.', 'Alt. of Cubbyhole');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubbyhole', 'n.', 'A snug or confined place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubdrawn', 'a.', 'Sucked by cubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cube', 'n.', 'A regular solid body, with six equal square sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cube', 'n.', 'The product obtained by taking a number or quantity three\n   times as a factor; as, 4x4=16, and 16x4=64, the cube of 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubed', 'imp. & p. p.', 'of Cube');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubing', 'p. pr. & vb. n.', 'of Cube');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cube', 'v. t.', 'To raise to the third power; to obtain the cube of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubeb', 'n.', 'The small, spicy berry of a species of pepper (Piper Cubeba;\n   in med., Cubeba officinalis), native in Java and Borneo, but now\n   cultivated in various tropical countries. The dried unripe fruit is\n   much used in medicine as a stimulant and purgative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubebic', 'a.', 'Pertaining to, or derived from, cubebs; as, cubebic acid\n   (a soft olive-green resin extracted from cubebs).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubhood', 'n.', 'The state of being a cub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubic', 'a.', 'Alt. of Cubical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubical', 'a.', 'Having the form or properties of a cube; contained, or\n   capable of being contained, in a cube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubical', 'a.', 'Isometric or monometric; as, cubic cleavage. See\n   Crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubic', 'n.', 'A curve of the third degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubically', 'adv.', 'In a cubical method.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubicalness', 'n.', 'The quality of being cubical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubicle', 'n.', 'A loding room; esp., a sleeping place partitioned off from\n   a large dormitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubicular', 'a.', 'Belonging to a chamber or bedroom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubiform', 'a.', 'Of the form of a cube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubile', 'n.', 'The lowest course of stones in a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubilose', 'n.', 'A mucilagenous secretion of certain birds found as the\n   characteristic ingredient of edible birds-nests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubit', 'n.', 'The forearm; the ulna, a bone of the arm extending from\n   elbow to wrist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubit', 'n.', 'A measure of length, being the distance from the elbow to\n   the extremity of the middle finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubital', 'a.', 'Of or pertaining to the cubit or ulna; as, the cubital\n   nerve; the cubital artery; the cubital muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubital', 'a.', 'Of the length of a cubit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubital', 'n.', 'A sleeve covering the arm from the elbow to the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubited', 'a.', 'Having the measure of a cubit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubless', 'a.', 'Having no cubs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuboid', 'a.', 'Cube-shaped, or nearly so; as, the cuboid bone of the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuboid', 'n.', 'The bone of the tarsus, which, in man and most mammals,\n   supports the metatarsals of the fourth and fifth toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuboidal', 'a.', 'Cuboid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubo-octahedral', 'a.', 'Presenting a combination of a cube and an\n   octahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cubo-octahedron', 'n.', 'A combination of a cube and octahedron, esp. one\n   in which the octahedral faces meet at the middle of the cubic edges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuca', 'n.', 'See Coca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucking stool', '', 'A kind of chair formerly used for punishing scolds,\n   and also dishonest tradesmen, by fastening them in it, usually in front\n   of their doors, to be pelted and hooted at by the mob, but sometimes to\n   be taken to the water and ducked; -- called also a castigatory, a\n   tumbrel, and a trebuchet; and often, but not so correctly, a ducking\n   stool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckold', 'n.', 'A man whose wife is unfaithful; the husband of an\n   adulteress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckold', 'n.', 'A West Indian plectognath fish (Ostracion triqueter).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckold', 'n.', 'The cowfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckolded', 'imp. & p. p.', 'of Cuckold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckolding', 'p. pr. & vb. n.', 'of Cuckold');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckold', 'v. t.', 'To make a cuckold of, as a husband, by seducing his\n   wife, or by her becoming an adulteress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckoldize', 'v. t.', 'To cuckold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckoldly', 'a.', 'Having the qualities of a cuckold; mean-spirited;\n   sneaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckoldom', 'n.', 'The state of a cuckold; cuckolds, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckoldry', 'n.', 'The state of being a cuckold; the practice of making\n   cuckolds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckolds knot', '', 'A hitch or knot, by which a rope is secured to a\n   spar, the two parts of the rope being crossed and seized together; --\n   called also cuckolds neck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckoo', 'n.', 'A bird belonging to Cuculus, Coccyzus, and several allied\n   genera, of many species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckoobud', 'n.', 'A species of Ranunculus (R. bulbosus); -- called also\n   butterflower, buttercup, kingcup, goldcup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckooflower', 'n.', 'A species of Cardamine (C. pratensis), or ladys\n   smock. Its leaves are used in salads. Also, the ragged robin (Lychnis\n   Flos-cuculi).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuckoopint', 'n.', 'A plant of the genus Arum (A. maculatum); the European\n   wake-robin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucquean', 'n.', 'A woman whose husband is unfaithful to her.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucujo', 'n.', 'The fire beetle of Mexico and the West Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucullate', 'a.', 'Alt. of Cucullated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucullated', 'a.', 'Hooded; cowled; covered, as with a hood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucullated', 'a.', 'Having the edges toward the base rolled inward, as the\n   leaf of the commonest American blue violet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucullated', 'a.', 'Having the prothorax elevated so as to form a sort of\n   hood, receiving the head, as in certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucullated', 'a.', 'Having a hoodlike crest on the head, as certain birds,\n   mammals, and reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuculoid', 'a.', 'Like or belonging to the cuckoos (Cuculidae).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucumber', 'n.', 'A creeping plant, and its fruit, of several species of\n   the genus Cucumis, esp. Cucumis sativus, the unripe fruit of which is\n   eaten either fresh or picked. Also, similar plants or fruits of several\n   other genera. See below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucumiform', 'a.', 'Having the form of a cucumber; having the form of a\n   cylinder tapered and rounded at the ends, and either straight or\n   curved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucumis', 'n.', 'A genus of plants including the cucumber, melon, and same\n   kinds of gourds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucurbit', 'n.', 'Alt. of Cucurbite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucurbite', 'n.', 'A vessel or flask for distillation, used with, or\n   forming part of, an alembic; a matrass; -- originally in the shape of a\n   gourd, with a wide mouth. See Alembic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucurbitaceous', 'a.', 'Of, pertaining to, or resembling, a family of\n   plants of which the cucumber, melon, and gourd are common examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cucurbitive', 'a.', 'Having the shape of a gourd seed; -- said of certain\n   small worms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cud', 'n.', 'That portion of food which is brought up into the mouth by\n   ruminating animals from their first stomach, to be chewed a second\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cud', 'n.', 'A portion of tobacco held in the mouth and chewed; a quid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cud', 'n.', 'The first stomach of ruminating beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudbear', 'n.', 'A powder of a violet red color, difficult to moisten with\n   water, used for making violet or purple dye. It is prepared from\n   certain species of lichen, especially Lecanora tartarea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudbear', 'n.', 'A lichen (Lecanora tartarea), from which the powder is\n   obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudden', 'n.', 'A clown; a low rustic; a dolt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudden', 'n.', 'The coalfish. See 3d Cuddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuddled', 'imp. & p. p.', 'of Cuddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuddling', 'p. pr. & vb. n.', 'of Cuddle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuddle', 'v. i.', 'To lie close or snug; to crouch; to nestle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuddle', 'v. t.', 'To embrace closely; to fondle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuddle', 'n.', 'A close embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuddy', 'n.', 'An ass; esp., one driven by a huckster or greengrocer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuddy', 'n.', 'A blockhead; a lout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuddy', 'n.', 'A lever mounted on a tripod for lifting stones, leveling up\n   railroad ties, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuddy', 'n.', 'A small cabin: also, the galley or kitchen of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuddy', 'n.', 'The coalfish (Pollachius carbonarius).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudgel', 'n.', 'A staff used in cudgel play, shorter than the quarterstaff,\n   and wielded with one hand; hence, any heavy stick used as a weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudgeled', 'imp. & p. p.', 'of Cudgel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudgelled', '', 'of Cudgel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudgeling', 'p. pr. & vb. n.', 'of Cudgel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudgelling', '', 'of Cudgel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudgel', 'v. t.', 'To beat with a cudgel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudgeler', 'n.', 'One who beats with a cudgel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cudweed', 'n.', 'A small composite plant with cottony or silky stem and\n   leaves, primarily a species of Gnaphalium, but the name is now given to\n   many plants of different genera, as Filago, Antennaria, etc.;\n   cottonweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cue', 'n.', 'The tail; the end of a thing; especially, a tail-like twist of\n   hair worn at the back of the head; a queue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cue', 'n.', 'The last words of a play actors speech, serving as an\n   intimation for the next succeeding player to speak; any word or words\n   which serve to remind a player to speak or to do something; a\n   catchword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cue', 'n.', 'A hint or intimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cue', 'n.', 'The part one has to perform in, or as in, a play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cue', 'n.', 'Humor; temper of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cue', 'n.', 'A straight tapering rod used to impel the balls in playing\n   billiards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cue', 'v. t.', 'To form into a cue; to braid; to twist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cue', 'n.', 'A small portion of bread or beer; the quantity bought with a\n   farthing or half farthing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuerpo', 'n.', 'The body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuffed', 'imp. & p. p.', 'of Cuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuffing', 'p. pr. & vb. n.', 'of Cuff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuff', 'v. t.', 'To strike; esp., to smite with the palm or flat of the\n   hand; to slap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuff', 'v. t.', 'To buffet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuff', 'v. i.', 'To fight; to scuffle; to box.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuff', 'n.', 'A blow; esp.,, a blow with the open hand; a box; a slap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuff', 'n.', 'The fold at the end of a sleeve; the part of a sleeve turned\n   back from the hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuff', 'n.', 'Any ornamental appendage at the wrist, whether attached to\n   the sleeve of the garment or separate; especially, in modern times,\n   such an appendage of starched linen, or a substitute for it of paper,\n   or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuffy', 'n.', 'A name for a negro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cufic', 'a.', 'Of or pertaining to the older characters of the Arabic\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuinage', 'n.', 'The stamping of pigs of tin, by the proper officer, with\n   the arms of the duchy of Cornwall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuirasses', 'pl. ', 'of Cuirass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuirass', 'n.', 'A piece of defensive armor, covering the body from the\n   neck to the girdle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuirass', 'n.', 'The breastplate taken by itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuirass', 'n.', 'An armor of bony plates, somewhat resembling a cuirass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuirassed', 'a.', 'Wearing a cuirass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuirassed', 'a.', 'Having a covering of bony plates, resembling a cuirass;\n   -- said of certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuirassier', 'n.', 'A soldier armed with a cuirass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuish', 'n.', 'Defensive armor for the thighs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuisine', 'n.', 'The kitchen or cooking department.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuisine', 'n.', 'Manner or style of cooking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culasse', 'n.', 'The lower faceted portion of a brilliant-cut diamond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culdee', 'n.', 'One of a class of anchorites who lived in various parts of\n   Scotland, Ireland, and Wales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culs-de-sac', 'pl. ', 'of Cul-de-sac');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cul-de-sac', 'n.', 'A passage with only one outlet, as a street closed at\n   one end; a blind alley; hence, a trap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cul-de-sac', 'n.', 'a position in which an army finds itself with no way of\n   exit but to the front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cul-de-sac', 'n.', 'Any bag-shaped or tubular cavity, vessel, or organ,\n   open only at one end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culerage', 'n.', 'See Culrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culex', 'n.', 'A genus of dipterous insects, including the gnat and\n   mosquito.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culiciform', 'a.', 'Gnat-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culinarily', 'adv.', 'In the manner of a kitchen; in connection with a\n   kitchen or cooking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culinary', 'a.', 'Relating to the kitchen, or to the art of cookery; used\n   in kitchens; as, a culinary vessel; the culinary art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culled', 'imp. & p. p.', 'of Cull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culling', 'p. pr. & vb. n.', 'of Cull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cull', 'v. t.', 'To separate, select, or pick out; to choose and gather or\n   collect; as, to cull flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cull', 'n.', 'A cully; a dupe; a gull. See Cully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullender', 'n.', 'A strainer. See Colander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culler', 'n.', 'One who picks or chooses; esp., an inspector who selects\n   wares suitable for market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullet', 'v. t.', 'Broken glass for remelting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullet', 'n.', 'A small central plane in the back of a cut gem. See Collet,\n   3 (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullibility', 'n.', 'Gullibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullible', 'a.', 'Easily deceived; gullible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culling', 'n.', 'The act of one who culls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culling', 'n.', 'Anything separated or selected from a mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullion', 'n.', 'A mean wretch; a base fellow; a poltroon; a scullion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullionly', 'a.', 'Mean; base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullis', 'n.', 'A strong broth of meat, strained and made clear for\n   invalids; also, a savory jelly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullises', 'pl. ', 'of Cullis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullis', 'n.', 'A gutter in a roof; a channel or groove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culls', 'v. t.', 'Refuse timber, from which the best part has been culled\n   out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culls', 'v. t.', 'Any refuse stuff, as rolls not properly baked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullies', 'pl. ', 'of Cully');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cully', 'n.', 'A person easily deceived, tricked, or imposed on; a mean\n   dupe; a gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cully', 'n.', 'To trick, cheat, or impose on; to deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cullyism', 'n.', 'The state of being a cully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culm', 'n.', 'The stalk or stem of grain and grasses (including the\n   bamboo), jointed and usually hollow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culm', 'n.', 'Mineral coal that is not bituminous; anthracite, especially\n   when found in small masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culm', 'n.', 'The waste of the Pennsylvania anthracite mines, consisting of\n   fine coal, dust, etc., and used as fuel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culmen', 'n.', 'Top; summit; acme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culmen', 'n.', 'The dorsal ridge of a birds bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culmiferous', 'a.', 'Having jointed stems or culms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culmiferous', 'a.', 'Containing, or abounding in, culm or glance coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culminal', 'a.', 'Pertaining to a culmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culminant', 'a.', 'Being vertical, or at the highest point of altitude;\n   hence, predominant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culminated', 'imp. & p. p.', 'of Culminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culminating', 'p. pr. & vb. n.', 'of Culminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culminate', 'v. i.', 'To reach its highest point of altitude; to come to\n   the meridian; to be vertical or directly overhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culminate', 'v. i.', 'To reach the highest point, as of rank, size, power,\n   numbers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culminate', 'a.', 'Growing upward, as distinguished from a lateral growth;\n   -- applied to the growth of corals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culmination', 'n.', 'The attainment of the highest point of altitude\n   reached by a heavently body; passage across the meridian; transit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culmination', 'n.', 'Attainment or arrival at the highest pitch of glory,\n   power, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culpa', 'n.', 'Negligence or fault, as distinguishable from dolus (deceit,\n   fraud), which implies intent, culpa being imputable to defect of\n   intellect, dolus to defect of heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culpabilities', 'pl. ', 'of Culpability');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culpability', 'n.', 'The state of being culpable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culpable', 'a.', 'Deserving censure; worthy of blame; faulty; immoral;\n   criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culpable', 'a.', 'Guilty; as, culpable of a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culpatory', 'a.', 'Expressing blame; censuring; reprehensory; inculpating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culpe', 'n.', 'Blameworthiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culpon', 'n.', 'A shred; a fragment; a strip of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culprit', 'p. p.', 'One accused of, or arraigned for, a crime, as before a\n   judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culprit', 'p. p.', 'One quilty of a fault; a criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culrage', 'n.', 'Smartweed (Polygonum Hydropiper).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cult', 'n .', 'Attentive care; homage; worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cult', 'n .', 'A system of religious belief and worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultch', 'n.', 'Empty oyster shells and other substances laid down on\n   oyster grounds to furnish points for the attachment of the spawn of the\n   oyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culter', 'n.', 'A colter. See Colter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultirostral', 'a.', 'Having a bill shaped like the colter of a plow, or\n   like a knife, as the heron, stork, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultirostres', 'n. pl.', 'A tribe of wading birds including the stork,\n   heron, crane, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivable', 'a.', 'Capable of being cultivated or tilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivatable', 'a.', 'Cultivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivated', 'imp. & p. p.', 'of Cultivate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivating', 'p. pr. & vb. n.', 'of Cultivate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivate', 'v. t.', 'To bestow attention, care, and labor upon, with a\n   view to valuable returns; to till; to fertilize; as, to cultivate soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivate', 'v. t.', 'To direct special attention to; to devote time and\n   thought to; to foster; to cherish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivate', 'v. t.', 'To seek the society of; to court intimacy with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivate', 'v. t.', 'To improve by labor, care, or study; to impart\n   culture to; to civilize; to refine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivate', 'v. t.', 'To raise or produce by tillage; to care for while\n   growing; as, to cultivate corn or grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivation', 'n.', 'The art or act of cultivating; improvement for\n   agricultural purposes or by agricultural processes; tillage; production\n   by tillage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivation', 'n.', 'Bestowal of time or attention for self-improvement or\n   for the benefit of others; fostering care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivation', 'n.', 'The state of being cultivated; advancement in\n   physical, intellectual, or moral condition; refinement; culture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivator', 'n.', 'One who cultivates; as, a cultivator of the soil; a\n   cultivator of literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultivator', 'n.', 'An agricultural implement used in the tillage of\n   growing crops, to loosen the surface of the earth and kill the weeds;\n   esp., a triangular frame set with small shares, drawn by a horse and by\n   handles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultrate', 'a.', 'Alt. of Cultrated');
