  INSERT INTO words (word, word_type, description) VALUES  (    'Cultriform', 'a.', 'Shaped like a pruning knife; cultrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultrivorous', 'a.', 'Devouring knives; swallowing, or pretending to\n   swallow, knives; -- applied to persons who have swallowed, or have\n   seemed to swallow, knives with impunity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culturable', 'a.', 'Capable of, or fit for, being cultivated; capable or\n   becoming cultured.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultural', 'a.', 'Of or pertaining to culture.');
