  INSERT INTO words (word, word_type, description) VALUES  (    'Cultured', 'imp. & p. p.', 'of Culture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culturing', 'p. pr. & vb. n.', 'of Culture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culture', 'v. t.', 'To cultivate; to educate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultured', 'a.', 'Under culture; cultivated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultured', 'a.', 'Characterized by mental and moral training; disciplined;\n   refined; well-educated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultureless', 'a.', 'Having no culture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culturist', 'n.', 'A cultivator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culturist', 'n.', 'One who is an advocate of culture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultuses', 'pl. ', 'of Cultus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultus', 'n. sing. & pl.', 'Established or accepted religious rites or\n   usages of worship; state of religious development. Cf. Cult, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cultus cod', '', 'See Cod, and Buffalo cod, under Buffalo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culver', 'n.', 'A dove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culver', 'n.', 'A culverin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culverhouse', 'n.', 'A dovecote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culverin', 'n.', 'A long cannon of the 16th century, usually an 18-pounder\n   with serpent-shaped handles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culverkey', 'n.', 'A bunch of the keys or samaras of the ash tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culverkey', 'n.', 'An English meadow plant, perhaps the columbine or the\n   bluebell squill (Scilla nutans).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culvert', 'n.', 'A transverse drain or waterway of masonry under a road,\n   railroad, canal, etc.; a small bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culvertail', 'n.', 'Dovetail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Culvertailed', 'a.', 'United or fastened by a dovetailed joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumacea', 'n. pl.', 'An order of marine Crustacea, mostly of small size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumbent', 'a.', 'Lying down; recumbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumbered', 'imp. & p. p.', 'of Cumber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumbering', 'p. pr. & vb. n.', 'of Cumber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumber', 'v. t.', 'To rest upon as a troublesome or useless weight or load;\n   to be burdensome or oppressive to; to hinder or embarrass in attaining\n   an object, to obstruct or occupy uselessly; to embarrass; to trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumber', 'v.', 'Trouble; embarrassment; distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumbersome', 'a.', 'Burdensome or hindering, as a weight or drag;\n   embarrassing; vexatious; cumbrous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumbersome', 'a.', 'Not easily managed; as, a cumbersome contrivance or\n   machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumbrance', 'n.', 'Encumbrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumbrian', 'a.', 'Pertaining to Cumberland, England, or to a system of\n   rocks found there.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumbrous', 'a.', 'Rendering action or motion difficult or toilsome; serving\n   to obstruct or hinder; burdensome; clogging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumbrous', 'a.', 'Giving trouble; vexatious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumene', 'n.', 'A colorless oily hydrocarbon, C6H5.C3H7, obtained by the\n   distillation of cuminic acid; -- called also cumol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumfrey', 'n.', 'See Comfrey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumic', 'a.', 'See Cuming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumidine', 'n.', 'A strong, liquid, organic base, C3H7.C6H4.NH2, homologous\n   with aniline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumin', 'n.', 'A dwarf umbelliferous plant, somewhat resembling fennel\n   (Cuminum Cyminum), cultivated for its seeds, which have a bitterish,\n   warm taste, with an aromatic flavor, and are used like those of anise\n   and caraway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuminic', 'a.', 'Pertaining to, or derived from, cumin, or from oil of\n   caraway; as, cuminic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuminil', 'n .', 'A substance, analogous to benzil, obtained from oil of\n   caraway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuminol', 'n.', 'A liquid, C3H7.C6H4.CHO, obtained from oil of caraway; --\n   called also cuminic aldehyde.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cummin', 'n.', 'Same as Cumin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumshaw', 'n.', 'A present or bonus; -- originally applied to that paid on\n   ships which entered the port of Canton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumshaw', 'v. t.', 'To give or make a present to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumu-cirro-stratus', 'n.', 'Nimbus, or rain cloud. See Nimbus, and Cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulated', 'imp. & p. p.', 'of Cumulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulating', 'p. pr. & vb. n.', 'of Cumulate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulate', 'v. t.', 'To gather or throw into a heap; to heap together; to\n   accumulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulation', 'n.', 'The act of heaping together; a heap. See Accumulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulatist', 'n.', 'One who accumulates; one who collects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulative', 'a.', 'Composed of parts in a heap; forming a mass;\n   aggregated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulative', 'a.', 'Augmenting, gaining, or giving force, by successive\n   additions; as, a cumulative argument, i. e., one whose force increases\n   as the statement proceeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulative', 'a.', 'Tending to prove the same point to which other evidence\n   has been offered; -- said of evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulative', 'a.', 'Given by same testator to the same legatee; -- said of\n   a legacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulose', 'a.', 'Full of heaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulostratus', 'n.', 'A form of cloud. See Cloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumuli', 'pl. ', 'of Cumulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cumulus', 'n.', 'One of the four principal forms of clouds. SeeCloud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cun', 'v. t.', 'To con (a ship).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cun', 'v. t.', 'To know. See Con.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunabula', 'n. pl.', 'The earliest abode; original dwelling place;\n   originals; as, the cunabula of the human race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunabula', 'n. pl.', 'The extant copies of the first or earliest printed\n   books, or of such as were printed in the 15th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunctation', 'n.', 'Delay; procrastination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunctative', 'a.', 'Slow; tardy; dilatory; causing delay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunctator', 'n.', 'One who delays or lingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunctipotent', 'a.', 'All-powerful; omnipotent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cund', 'v. t.', 'To con (a ship).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cundurango', 'n.', 'The bark of a South American vine (Gonolobus\n   Condurango) of the Milkweed family. It has been supposed, but\n   erroneously, to be a cure for cancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuneal', '', 'Relating to a wedge; wedge-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuneate', 'a.', 'Alt. of Cuneated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuneated', 'a.', 'Wedge-shaped');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuneated', 'a.', 'wedge-shaped, with the point at the base; as, a cuneate\n   leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuneatic', 'a.', 'Cuneiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuneiform', 'a.', 'Alt. of Cuniform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuniform', 'a.', 'Wedge-shaped; as, a cuneiform bone; -- especially applied\n   to the wedge-shaped or arrowheaded characters of ancient Persian and\n   Assyrian inscriptions. See Arrowheaded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuniform', 'a.', 'Pertaining to, or versed in, the ancient wedge-shaped\n   characters, or the inscriptions in them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuneiform', 'n.', 'Alt. of Cuniform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuniform', 'n.', 'The wedge-shaped characters used in ancient Persian and\n   Assyrian inscriptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuniform', 'n.', 'One of the three tarsal bones supporting the first,\n   second third metatarsals. They are usually designated as external,\n   middle, and internal, or ectocuniform, mesocuniform, and entocuniform,\n   respectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuniform', 'n.', 'One of the carpal bones usually articulating with the\n   ulna; -- called also pyramidal and ulnare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunette', 'n.', 'A drain trench, in a ditch or moat; -- called also\n   cuvette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunner', 'n.', 'A small edible fish of the Atlantic coast (Ctenolabrus\n   adspersus); -- called also chogset, burgall, blue perch, and bait\n   stealer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunner', 'n.', 'A small shellfish; the limpet or patella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunning', 'a.', 'Knowing; skillful; dexterous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunning', 'a.', 'Wrought with, or exhibiting, skill or ingenuity;\n   ingenious; curious; as, cunning work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunning', 'a.', 'Crafty; sly; artful; designing; deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunning', 'a.', 'Pretty or pleasing; as, a cunning little boy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunning', 'a.', 'Knowledge; art; skill; dexterity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunning', 'a.', 'The faculty or act of using stratagem to accomplish a\n   purpose; fraudulent skill or dexterity; deceit; craft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunningly', 'adv.', 'In a cunning manner; with cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunningman', 'n.', 'A fortune teller; one who pretends to reveal mysteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cunningness', 'n.', 'Quality of being cunning; craft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup', 'n.', 'A small vessel, used commonly to drink from; as, a tin cup, a\n   silver cup, a wine cup; especially, in modern times, the pottery or\n   porcelain vessel, commonly with a handle, used with a saucer in\n   drinking tea, coffee, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup', 'n.', 'The contents of such a vessel; a cupful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup', 'n.', 'Repeated potations; social or excessive indulgence in\n   intoxicating drinks; revelry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup', 'n.', 'That which is to be received or indured; that which is\n   allotted to one; a portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup', 'n.', 'Anything shaped like a cup; as, the cup of an acorn, or of a\n   flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup', 'n.', 'A cupping glass or other vessel or instrument used to produce\n   the vacuum in cupping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupped', 'imp. & p. p.', 'of Cup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupping', 'p. pr. & vb. n.', 'of Cup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup', 'v. t.', 'To supply with cups of wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup', 'v. t.', 'To apply a cupping apparatus to; to subject to the\n   operation of cupping. See Cupping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup', 'v. t.', 'To make concave or in the form of a cup; as, to cup the end\n   of a screw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupbearer', 'n.', 'One whose office it is to fill and hand the cups at an\n   entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupbearer', 'n.', 'One of the attendants of a prince or noble, permanently\n   charged with the performance of this office for his master.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupboard', 'n.', 'A board or shelf for cups and dishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupboard', 'n.', 'A small closet in a room, with shelves to receive cups,\n   dishes, food, etc.; hence, any small closet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupboard', 'v. t.', 'To collect, as into a cupboard; to hoard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupel', 'n.', 'A shallow porous cup, used in refining precious metals,\n   commonly made of bone ashes (phosphate of lime).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupelled', 'imp. & p. p.', 'of Cupel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupelling', 'p. pr. & vb. n.', 'of Cupel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupel', 'v. t.', 'To refine by means of a cupel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupellation', 'n.', 'The act or process of refining gold or silver, etc.,\n   in a cupel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupfuls', 'pl. ', 'of Cupful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupful', 'n.', 'As much as a cup will hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup-gall', 'n.', 'A kind of oak-leaf gall. See Gall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupid', 'n .', 'The god of love, son of Venus; usually represented as a\n   naked, winged boy with bow and arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupidity', 'n.', 'A passionate desire; love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupidity', 'n.', 'Eager or inordinate desire, especially for wealth; greed\n   of gain; avarice; covetousness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup-moss', 'n.', 'A kind of lichen, of the genus Cladonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupolas', 'pl. ', 'of Cupola');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupola', 'n.', 'A roof having a rounded form, hemispherical or nearly so;\n   also, a ceiling having the same form. When on a large scale it is\n   usually called dome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupola', 'n.', 'A small structure standing on the top of a dome; a lantern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupola', 'n.', 'A furnace for melting iron or other metals in large\n   quantity, -- used chiefly in foundries and steel works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupola', 'n.', 'A revolving shot-proof turret for heavy ordnance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupola', 'n.', 'The top of the spire of the cochlea of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupper', 'n.', 'One who performs the operation of cupping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupping', 'n.', 'The operation of drawing blood to or from the surface of\n   the person by forming a partial vacuum over the spot. Also, sometimes,\n   a similar operation for drawing pus from an abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupreous', 'a.', 'Consisting of copper or resembling copper; coppery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupric', 'a.', 'Of, pertaining to, or derived from, copper; containing\n   copper; -- said of those compounds of copper in which this element is\n   present in its lowest proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupriferous', 'a.', 'Containing copper; as, cupriferous silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuprite', 'n.', 'The red oxide of copper; red copper; an important ore of\n   copper, occurring massive and in isometric crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuproid', 'n.', 'A solid related to a tetrahedron, and contained under\n   twelve equal triangles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cup-rose', 'n.', 'Red poppy. See Cop-rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuprous', 'a.', 'Of, pertaining to, or derived from, copper; containing\n   copper; -- said of those compounds of copper in which this element is\n   present in its highest proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuprum', 'n.', 'Copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupulate', 'a.', 'Having or bearing cupules; cupuliferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupule', 'n.', 'A cuplet or little cup, as of the acorn; the husk or bur of\n   the filbert, chestnut, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupule', 'n.', 'A sucker or acetabulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cupuliferous', 'a.', 'Of, pertaining to, or resembling, the family of\n   plants of which the oak and the chestnut are examples, -- trees bearing\n   a smooth, solid nut inclosed in some kind of cup or bur; bearing, or\n   furnished with, a cupule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cur', 'n.', 'A mongrel or inferior dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cur', 'n.', 'A worthless, snarling fellow; -- used in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curability', 'n.', 'The state of being curable; curableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curable', 'v. t.', 'Capable of being cured; admitting remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curacao', 'n.', 'Alt. of Curacoa');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curacoa', 'n.', 'A liqueur, or cordial, flavored with orange peel,\n   cinnamon, and mace; -- first made at the island of Curaccao.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curacies', 'pl. ', 'of Curacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curacy', 'n.', 'The office or employment of a curate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curare', 'n.', 'Alt. of Curari');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curari', 'n.', 'A black resinoid extract prepared by the South American\n   Indians from the bark of several species of Strychnos (S. toxifera,\n   etc.). It sometimes has little effect when taken internally, but is\n   quickly fatal when introduced into the blood, and used by the Indians\n   as an arrow poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curarine', 'n.', 'A deadly alkaloid extracted from the curare poison and\n   from the Strychnos toxifera. It is obtained in crystalline colorless\n   salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curarize', 'v. t.', 'To poison with curare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curassow', 'n.', 'A large gallinaceous bird of the American genera Crax,\n   Ourax, etc., of the family Cracidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curat', 'n.', 'A cuirass or breastplate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curate', 'n.', 'One who has the cure of souls; originally, any clergyman,\n   but now usually limited to one who assists a rector or vicar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curateship', 'n.', 'A curacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curation', 'n.', 'Cure; healing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curative', 'v. t.', 'Relating to, or employed in, the cure of diseases;\n   tending to cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curator', 'n.', 'One who has the care and superintendence of anything, as\n   of a museum; a custodian; a keeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curator', 'n.', 'One appointed to act as guardian of the estate of a person\n   not legally competent to manage it, or of an absentee; a trustee; a\n   guardian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curatorship', 'n.', 'The office of a curator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curatrix', 'n.', 'A woman who cures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curatrix', 'n.', 'A woman who is a guardian or custodian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curbed', 'imp. & p. p.', 'of Curb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curbing', 'p. pr. & vb. n.', 'of Curb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curb', 'v. t.', 'To bend or curve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curb', 'v. t.', 'To guide and manage, or restrain, as with a curb; to bend\n   to ones will; to subject; to subdue; to restrain; to confine; to keep\n   in check.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curb', 'v. t.', 'To furnish wich a curb, as a well; also, to restrain by a\n   curb, as a bank of earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curb', 'v. i.', 'To bend; to crouch; to cringe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curb', 'n.', 'That which curbs, restrains, or subdues; a check or\n   hindrance; esp., a chain or strap attached to the upper part of the\n   branches of a bit, and capable of being drawn tightly against the lower\n   jaw of the horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curb', 'n.', 'An assemblage of three or more pieces of timber, or a metal\n   member, forming a frame around an opening, and serving to maintain the\n   integrity of that opening; also, a ring of stone serving a similar\n   purpose, as at the eye of a dome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curb', 'n.', 'A frame or wall round the mouth of a well; also, a frame\n   within a well to prevent the earth caving in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curb', 'n.', 'A curbstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curb', 'n.', 'A swelling on the back part of the hind leg of a horse, just\n   behind the lowest part of the hock joint, generally causing lameness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curbless', 'a.', 'Having no curb or restraint.');
