  INSERT INTO words (word, word_type, description) VALUES  (    'Curculios', 'pl. ', 'of Curculio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curculio', 'n.', 'One of a large group of beetles (Rhynchophora) of many\n   genera; -- called also weevils, snout beetles, billbeetles, and\n   billbugs. Many of the species are very destructive, as the plum\n   curculio, the corn, grain, and rice weevils, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curculionidous', 'a.', 'Pertaining to the Curculionideae, or weevil tribe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curcuma', 'n.', 'A genus of plants of the order Scitamineae, including the\n   turmeric plant (Curcuma longa).');
