  INSERT INTO words (word, word_type, description) VALUES  (    'Cure', 'n.', 'Care, heed, or attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cure', 'n.', 'Spiritual charge; care of soul; the office of a parish priest\n   or of a curate; hence, that which is committed to the charge of a\n   parish priest or of a curate; a curacy; as, to resign a cure; to obtain\n   a cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cure', 'n.', 'Medical or hygienic care; remedial treatment of disease; a\n   method of medical treatment; as, to use the water cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cure', 'n.', 'Act of healing or state of being healed; restoration to\n   health from disease, or to soundness after injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cure', 'n.', 'Means of the removal of disease or evil; that which heals; a\n   remedy; a restorative.');
