  INSERT INTO words (word, word_type, description) VALUES  (    'Cure', 'v. t.', 'To set free from (something injurious or blameworthy), as\n   from a bad habit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cure', 'v. t.', 'To prepare for preservation or permanent keeping; to\n   preserve, as by drying, salting, etc.; as, to cure beef or fish; to\n   cure hay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cure', 'v. i.', 'To pay heed; to care; to give attention.');
