  INSERT INTO words (word, word_type, description) VALUES  (    'Cursores', 'n. pl.', 'An order of running birds including the ostrich, emu,\n   and allies; the Ratitaae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cursores', 'n. pl.', 'A group of running spiders; the wolf spiders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cursorial', 'a.', 'Adapted to running or walking, and not to prehension;\n   as, the limbs of the horse are cursorial. See Illust. of Aves.');
