  INSERT INTO words (word, word_type, description) VALUES  (    'Cursorily', 'adv.', 'In a running or hasty manner; carelessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cursoriness', 'n.', 'The quality of being cursory; superficial performance;\n   as, cursoriness of view.');
