  INSERT INTO words (word, word_type, description) VALUES  (    'Cursory', 'a.', 'Characterized by haste; hastily or superficially\n   performed; slight; superficial; careless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curst', '', 'imp. & p. p. of Curse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curst', 'a.', 'Froward; malignant; mischievous; malicious; snarling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curstfully', 'adv.', 'Peevishly; vexatiously; detestably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curstness', 'n.', 'Peevishness; malignity; frowardness; crabbedness;\n   surliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curt', 'a.', 'Characterized by excessive brevity; short; rudely concise;\n   as, curt limits; a curt answer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtailed', 'imp. & p. p.', 'of Curtail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtailing', 'p. pr. & vb. n.', 'of Curtail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtail', 'v. t.', 'To cut off the end or tail, or any part, of; to\n   shorten; to abridge; to diminish; to reduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtail', 'n.', 'The scroll termination of any architectural member, as of\n   a step, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtail dog', '', 'A dog with a docked tail; formerly, the dog of a person\n   not qualified to course, which, by the forest laws, must have its tail\n   cut short, partly as a mark, and partly from a notion that the tail is\n   necessary to a dog in running; hence, a dog not fit for sporting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtailer', 'n.', 'One who curtails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtailment', 'n.', 'The act or result of curtailing or cutting off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtain', 'n.', 'A hanging screen intended to darken or conceal, and\n   admitting of being drawn back or up, and reclosed at pleasure; esp.,\n   drapery of cloth or lace hanging round a bed or at a window; in\n   theaters, and like places, a movable screen for concealing the stage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtain', 'n.', 'That part of the rampart and parapet which is between two\n   bastions or two gates. See Illustrations of Ravelin and Bastion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtain', 'n.', 'That part of a wall of a building which is between two\n   pavilions, towers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtain', 'n.', 'A flag; an ensign; -- in contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtained', 'imp. & p. p.', 'of Curtain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtaining', 'p. pr. & vb. n.', 'of Curtain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtain', 'v. t.', 'To inclose as with curtains; to furnish with curtains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtal', 'a.', 'Curt; brief; laconic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtal', 'n.', 'A horse with a docked tail; hence, anything cut short.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtal ax', '', 'Alt. of Curtelasse');
