  INSERT INTO words (word, word_type, description) VALUES  (    'Curtelasse', '', 'A corruption of Cutlass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtal friar', '', 'A friar who acted as porter at the gate of a monastery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtana', 'n.', 'The pointless sword carried before English monarchs at\n   their coronation, and emblematically considered as the sword of mercy;\n   -- also called the sword of Edward the Confessor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtate', 'a.', 'Shortened or reduced; -- said of the distance of a planet\n   from the sun or earth, as measured in the plane of the ecliptic, or the\n   distance from the sun or earth to that point where a perpendicular, let\n   fall from the planet upon the plane of the ecliptic, meets the\n   ecliptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtation', 'n.', 'The interval by which the curtate distance of a planet\n   is less than the true distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtein', 'n.', 'Same as Curtana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtes', 'a.', 'Courteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtesies', 'pl. ', 'of Curtesy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtesy', 'n.', 'the life estate which a husband has in the lands of his\n   deceased wife, which by the common law takes effect where he has had\n   issue by her, born alive, and capable of inheriting the lands.');
