  INSERT INTO words (word, word_type, description) VALUES  (    'Curtly', 'adv.', 'In a curt manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtness', 'n.', 'The quality of bing curt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curtsy', 'n.', 'Same as Courtesy, an act of respect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curule', 'a.', 'Of or pertaining to a chariot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curule', 'a.', 'Of or pertaining to a kind of chair appropriated to Roman\n   magistrates and dignitaries; pertaining to, having, or conferring, the\n   right to sit in the curule chair; hence, official.');
