  INSERT INTO words (word, word_type, description) VALUES  (    'Curve', 'a.', 'To bend; to crook; as, to curve a line; to curve a pipe; to\n   cause to swerve from a straight course; as, to curve a ball in pitching\n   it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curve', 'v. i.', 'To bend or turn gradually from a given direction; as, the\n   road curves to the right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Curvedness', 'n.', 'The state of being curved.');
