  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'v. i.', 'To interfere, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'v. i.', 'To move or make off quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'v. i.', 'To divide a pack of cards into two portion to decide the\n   deal or trump, or to change the order of the cards to be dealt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'An opening made with an edged instrument; a cleft; a gash; a\n   slash; a wound made by cutting; as, a sword cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'A stroke or blow or cutting motion with an edged instrument; a\n   stroke or blow with a whip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'That which wounds the feelings, as a harsh remark or\n   criticism, or a sarcasm; personal discourtesy, as neglecting to\n   recognize an acquaintance when meeting him; a slight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'A notch, passage, or channel made by cutting or digging; a\n   furrow; a groove; as, a cut for a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'The surface left by a cut; as, a smooth or clear cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'A portion severed or cut off; a division; as, a cut of beef; a\n   cut of timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'An engraved block or plate; the impression from such an\n   engraving; as, a book illustrated with fine cuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'The act of dividing a pack cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'The right to divide; as, whose cut is it?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'Manner in which a thing is cut or formed; shape; style;\n   fashion; as, the cut of a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'A common work horse; a gelding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'The failure of a college officer or student to be present at\n   any appointed exercise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'n.', 'A skein of yarn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'a.', 'Gashed or divided, as by a cutting instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'a.', 'Formed or shaped as by cutting; carved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut', 'a.', 'Overcome by liquor; tipsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutaneous', 'a.', 'Of pertaining to the skin; existing on, or affecting,\n   the skin; as, a cutaneous disease; cutaneous absorption; cutaneous\n   respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutaway', 'a.', 'Having a part cut off or away; having the corners rounded\n   or cut away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutch', 'n.', 'See Catechu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutch', 'n.', 'See Cultch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutchery', 'n.', 'A hindoo hall of justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cute', 'a.', 'Clever; sharp; shrewd; ingenious; cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuteness', 'n.', 'Acuteness; cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutgrass', '', 'A grass with leaves having edges furnished with very minute\n   hooked prickles, which form a cutting edge; one or more species of\n   Leersia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuticle', 'n.', 'The scarfskin or epidermis. See Skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuticle', 'n.', 'The outermost skin or pellicle of a plant, found\n   especially in leaves and young stems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuticle', 'n.', 'A thin skin formed on the surface of a liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuticular', 'a.', 'Pertaining to the cuticle, or external coat of the skin;\n   epidermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutin', 'n.', 'The substance which, added to the material of a cell wall,\n   makes it waterproof, as in cork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutinization', 'n.', 'The conversion of cell walls into a material which\n   repels water, as in cork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutinize', 'v. t. & i.', 'To change into cutin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutis', 'n.', 'See Dermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutlasses', 'pl. ', 'of Cutlass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutlass', 'n.', 'A short, heavy, curving sword, used in the navy. See\n   Curtal ax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutler', 'n.', 'One who makes or deals in cutlery, or knives and other\n   cutting instruments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutlery', 'n.', 'The business of a cutler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutlery', 'n.', 'Edged or cutting instruments, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutlet', 'n.', 'A piece of meat, especially of veal or mutton, cut for\n   broiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutling', 'n.', 'The art of making edged tools or cutlery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut-off', 'n.', 'That which cuts off or shortens, as a nearer passage or\n   road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut-off', 'n.', 'The valve gearing or mechanism by which steam is cut off\n   from entering the cylinder of a steam engine after a definite point in\n   a stroke, so as to allow the remainder of the stroke to be made by the\n   expansive force of the steam already let in. See Expansion gear, under\n   Expansion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut-off', 'n.', 'Any device for stopping or changing a current, as of grain\n   or water in a spout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutose', 'n.', 'A variety of cellulose, occuring as a fine transparent\n   membrane covering the aerial organs of plants, and forming an essential\n   ingredient of cork; by oxidation it passes to suberic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut-out', 'n.', 'A species of switch for changing the current from one\n   circuit to another, or for shortening a circuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cut-out', 'n.', 'A device for breaking or separating a portion of circuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutpurse', 'n.', 'One who cuts purses for the sake of stealing them or\n   their contents (an act common when men wore purses fastened by a string\n   to their girdles); one who steals from the person; a pickpocket');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutter', 'n.', 'One who cuts; as, a stone cutter; a die cutter; esp., one\n   who cuts out garments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutter', 'n.', 'That which cuts; a machine or part of a machine, or a tool\n   or instrument used for cutting, as that part of a mower which severs\n   the stalk, or as a paper cutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutter', 'n.', 'A fore tooth; an incisor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutter', 'n.', 'A boat used by ships of war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutter', 'n.', 'A fast sailing vessel with one mast, rigged in most\n   essentials like a sloop. A cutter is narrower end deeper than a sloop\n   of the same length, and depends for stability on a deep keel, often\n   heavily weighted with lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutter', 'n.', 'A small armed vessel, usually a steamer, in the revenue\n   marine service; -- also called revenue cutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutter', 'n.', 'A small, light one-horse sleigh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutter', 'n.', 'An officer in the exchequer who notes by cutting on the\n   tallies the sums paid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutter', 'n.', 'A ruffian; a bravo; a destroyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutter', 'n.', 'A kind of soft yellow brick, used for facework; -- so\n   called from the facility with which it can be cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutthroat', 'n.', 'One who cuts throats; a murderer; an assassin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutthroat', 'a.', 'Murderous; cruel; barbarous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutting', 'n.', 'The act or process of making an incision, or of severing,\n   felling, shaping, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutting', 'n.', 'Something cut, cut off, or cut out, as a twig or scion cut\n   off from a stock for the purpose of grafting or of rooting as an\n   independent plant; something cut out of a newspaper; an excavation cut\n   through a hill or elsewhere to make a way for a railroad, canal, etc.;\n   a cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutting', 'a.', 'Adapted to cut; as, a cutting tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutting', 'a.', 'Chilling; penetrating; sharp; as, a cutting wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutting', 'a.', 'Severe; sarcastic; biting; as, a cutting reply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuttingly', 'adv.', 'In a cutting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuttle', 'n.', 'A knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuttle', 'n.', 'Alt. of Cuttlefish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuttlefish', 'n.', 'A cephalopod of the genus Sepia, having an internal\n   shell, large eyes, and ten arms furnished with denticulated suckers, by\n   means of which it secures its prey. The name is sometimes applied to\n   dibranchiate cephalopods generally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuttlefish', 'n.', 'A foul-mouthed fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuttle bone', '', 'The shell or bone of cuttlefishes, used for various\n   purposes, as for making polishing powder, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuttoo plate', '', 'A hood over the end of a wagon wheel hub to keep dirt\n   away from the axle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytty', 'a.', 'Short; as, a cutty knife; a cutty sark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutty', 'n.', 'A short spoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutty', 'n.', 'A short tobacco pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutty', 'n.', 'A light or unchaste woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuttystool', 'n.', 'A low stool');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuttystool', 'n.', 'A seat in old Scottish churches, where offenders were\n   made to sit, for public rebuke by the minister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutwal', 'n.', 'The chief police officer of a large city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutwater', 'n.', 'The fore part of a ships prow, which cuts the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutwater', 'n.', 'A starling or other structure attached to the pier of a\n   bridge, with an angle or edge directed up stream, in order better to\n   resist the action of water, ice, etc.; the sharpened upper end of the\n   pier itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutwater', 'n.', 'A sea bird of the Atlantic (Rhynchops nigra); -- called\n   also black skimmer, scissorsbill, and razorbill. See Skimmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutwork', 'n.', 'An ancient term for embroidery, esp. applied to the\n   earliest form of lace, or to that early embroidery on linen and the\n   like, from which the manufacture of lace was developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cutworm', 'n.', 'A caterpillar which at night eats off young plants of\n   cabbage, corn, etc., usually at the ground. Some kinds ascend fruit\n   trees and eat off the flower buds. During the day, they conceal\n   themselves in the earth. The common cutworms are the larvae of various\n   species of Agrotis and related genera of noctuid moths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuvette', 'n.', 'A pot, bucket, or basin, in which molten plate glass is\n   carried from the melting pot to the casting table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuvette', 'n.', 'A cunette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cuvette', 'n.', 'A small vessel with at least two flat and transparent\n   sides, used to hold a liquid sample to be analysed in the light path of\n   a spectrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyamelide', 'n.', 'A white amorphous substance, regarded as a polymeric\n   modification of isocyanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyamellone', 'n.', 'A complex derivative of cyanogen, regarded as an acid,\n   and known chiefly in its salts; -- called also hydromellonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanate', 'n.', 'A salt of cyanic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanaurate', 'n.', 'See Aurocyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanean', 'a.', 'Having an azure color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanic', 'a.', 'Pertaining to, or containing, cyanogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanic', 'a.', 'Of or pertaining to a blue color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanide', 'n.', 'A compound formed by the union of cyanogen with an element\n   or radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanin', 'n.', 'The blue coloring matter of flowers; -- called also\n   anthokyan and anthocyanin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanine', 'n.', 'One of a series of artificial blue or red dyes obtained\n   from quinoline and lepidine and used in calico printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanite', 'n.', 'A mineral occuring in thin-bladed crystals and crystalline\n   aggregates, of a sky-blue color. It is a silicate of aluminium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanogen', 'n.', 'A colorless, inflammable, poisonous gas, C2N2, with a\n   peach-blossom odor, so called from its tendency to form blue compounds;\n   obtained by heating ammonium oxalate, mercuric cyanide, etc. It is\n   obtained in combination, forming an alkaline cyanide when nitrogen or a\n   nitrogenous compound is strongly ignited with carbon and soda or\n   potash. It conducts itself like a member of the halogen group of\n   elements, and shows a tendency to form complex compounds. The name is\n   also applied to the univalent radical, CN (the half molecule of\n   cyanogen proper), which was one of the first compound radicals\n   recognized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanometer', 'n.', 'An instrument for measuring degress of blueness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanopathy', 'n.', 'A disease in which the body is colored blue in its\n   surface, arising usually from a malformation of the heart, which causes\n   an imperfect arterialization of the blood; blue jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanophyll', 'n.', 'A blue coloring matter supposed by some to be one of\n   the component parts of chlorophyll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanosed', 'a.', 'Rendered blue, as the surface of the body, from cyanosis\n   or deficient a/ration of the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanosis', 'n.', 'A condition in which, from insufficient a/ration of the\n   blood, the surface of the body becomes blue. See Cyanopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanosite', 'n.', 'Native sulphate of copper. Cf. Blue vitriol, under Blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanotic', 'a.', 'Relating to cyanosis; affected with cyanosis; as, a\n   cyanotic patient; having the hue caused by cyanosis; as, a cyanotic\n   skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanotype', 'n.', 'A photographic picture obtained by the use of a cyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanurate', 'n.', 'A salt of cyanuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanuret', 'n.', 'A cyanide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanuric', 'a.', 'Pertaining to, or derived from, cyanic and uric acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyanuric acid', '', 'an organic acid, C3O3N3H3, first obtained by heating\n   uric acid or urea, and called pyrouric acid; afterwards obtained from\n   isocyanic acid. It is a white crystalline substance, odorless and\n   almost tasteless; -- called also tricarbimide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyathiform', 'a.', 'In the form of a cup, a little widened at the top.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyatholith', 'n.', 'A kind of coccolith, which in shape resembles a minute\n   cup widened at the top, and varies in size from / to / of an inch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyathophylloid', 'a.', 'Like, or pertaining to, the family Cyathophyllidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyathophylloid', 'n.', 'A fossil coral of the family Cyathophyllidae;\n   sometimes extended to fossil corals of other related families belonging\n   to the group Rugosa; -- also called cup corals. Thay are found in\n   paleozoic rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycad', 'n.', 'Any plant of the natural order Cycadaceae, as the sago palm,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycadaceous', 'a.', 'Pertaining to, or resembling, an order of plants like\n   the palms, but having exogenous wood. The sago palm is an example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycas', 'n.', 'A genus of trees, intermediate in character between the\n   palms and the pines. The pith of the trunk of some species furnishes a\n   valuable kind of sago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclamen', 'n.', 'A genus of plants of the Primrose family, having\n   depressed rounded corms, and pretty nodding flowers with the petals so\n   reflexed as to point upwards, whence it is called rabbits ears. It is\n   also called sow bread, because hogs are said to eat the corms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclamin', 'n.', 'A white amorphous substance, regarded as a glucoside,\n   extracted from the corm of Cyclamen Europaeum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclas', 'n.', 'A long gown or surcoat (cut off in front), worn in the\n   Middle Ages. It was sometimes embroidered or interwoven with gold.\n   Also, a rich stuff from which the gown was made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycle', 'n.', 'An imaginary circle or orbit in the heavens; one of the\n   celestial spheres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycle', 'n.', 'An interval of time in which a certain succession of events\n   or phenomena is completed, and then returns again and again, uniformly\n   and continually in the same order; a periodical space of time marked by\n   the recurrence of something peculiar; as, the cycle of the seasons, or\n   of the year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycle', 'n.', 'An age; a long period of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycle', 'n.', 'An orderly list for a given time; a calendar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycle', 'n.', 'The circle of subjects connected with the exploits of the\n   hero or heroes of some particular period which have served as a popular\n   theme for poetry, as the legend of Arthur and the knights of the Round\n   Table, and that of Charlemagne and his paladins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycle', 'n.', 'One entire round in a circle or a spire; as, a cycle or set\n   of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycle', 'n.', 'A bicycle or tricycle, or other light velocipede.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycled', 'imp. & p. p.', 'of Cycle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycling', 'p. pr. & vb. n.', 'of Cycle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycle', 'v. i.', 'To pass through a cycle of changes; to recur in cycles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycle', 'v. i.', 'To ride a bicycle, tricycle, or other form of cycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclic', 'a.', 'Alt. of Cyclical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclical', 'a.', 'Of or pertaining to a cycle or circle; moving in cycles;\n   as, cyclical time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclide', 'n.', 'A surface of the fourth degree, having certain special\n   relations to spherical surfaces. The tore or anchor ring is one of the\n   cyclides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycling', 'n.', 'The act, art, or practice, of riding a cycle, esp. a\n   bicycle or tricycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclist', 'n.', 'A cycler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclo-', '', 'A combining form meaning circular, of a circle or wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclobranchiate', 'a.', 'Having the gills around the margin of the body, as\n   certain limpets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycloganoid', 'a.', 'Of or pertaining to the Cycloganoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycloganoid', 'n.', 'One of the Cycloganoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycloganoidei', 'n. pl.', 'An order of ganoid fishes, having cycloid\n   scales. The bowfin (Amia calva) is a living example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclograph', 'n.', 'See Arcograph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycloid', 'n.', 'A curve generated by a point in the plane of a circle when\n   the circle is rolled along a straight line, keeping always in the same\n   plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycloid', 'a.', 'Of or pertaining to the Cycloidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycloid', 'n.', 'One of the Cycloidei.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycloidal', 'a.', 'Pertaining to, or resembling, a cycloid; as, the\n   cycloidal space is the space contained between a cycloid and its base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycloidei', 'n. pl.', 'An order of fishes, formerly proposed by Agassiz,\n   for those with thin, smooth scales, destitute of marginal spines, as\n   the herring and salmon. The group is now regarded as artificial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycloidian', 'a. & n.', 'Same as 2d and 3d Cycloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclometer', 'n.', 'A contrivance for recording the revolutions of a wheel,\n   as of a bicycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclometry', 'n.', 'The art of measuring circles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclone', 'n.', 'A violent storm, often of vast extent, characterized by\n   high winds rotating about a calm center of low atmospheric pressure.\n   This center moves onward, often with a velocity of twenty or thirty\n   miles an hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclonic', 'a.', 'Pertaining to a cyclone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclop', 'n.', 'See Note under Cyclops, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclopean', 'a.', 'Pertaining to the Cyclops; characteristic of the\n   Cyclops; huge; gigantic; vast and rough; massive; as, Cyclopean labors;\n   Cyclopean architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclopedia', 'n.', 'Alt. of Cyclopaedia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclopaedia', 'n.', 'The circle or compass of the arts and sciences\n   (originally, of the seven so-called liberal arts and sciences); circle\n   of human knowledge. Hence, a work containing, in alphabetical order,\n   information in all departments of knowledge, or on a particular\n   department or branch; as, a cyclopedia of the physical sciences, or of\n   mechanics. See Encyclopedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclopedic', 'a.', 'Belonging to the circle of the sciences, or to a\n   cyclopedia; of the nature of a cyclopedia; hence, of great range,\n   extent, or amount; as, a man of cyclopedic knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclopedist', 'n.', 'A maker of, or writer for, a cyclopedia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclopic', 'a.', 'Pertaining to the Cyclops; Cyclopean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclops', 'n. sing. & pl.', 'One of a race of giants, sons of Neptune and\n   Amphitrite, having but one eye, and that in the middle of the forehead.\n   They were fabled to inhabit Sicily, and to assist in the workshops of\n   Vulcan, under Mt. Etna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclops', 'n. sing. & pl.', 'A genus of minute Entomostraca, found both in\n   fresh and salt water. See Copepoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclops', 'n. sing. & pl.', 'A portable forge, used by tinkers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclorama', 'n.', 'A pictorial view which is extended circularly, so that\n   the spectator is surrounded by the objects represented as by things in\n   nature. The realistic effect is increased by putting, in the space\n   between the spectator and the picture, things adapted to the scene\n   represented, and in some places only parts of these objects, the\n   completion of them being carried out pictorially.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cycloscope', 'n.', 'A machine for measuring at any moment velocity of\n   rotation, as of a wheel of a steam engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclosis', 'n.', 'The circulation or movement of protoplasmic granules\n   within a living vegetable cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclostomata', 'n. pl.', 'Alt. of Cyclostoma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclostoma', 'n. pl.', 'A division of Bryozoa, in which the cells have\n   circular apertures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclostome', 'a.', 'Alt. of Cyclostomous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclostomous', 'a.', 'Pertaining to the Cyclostomi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclostomi', 'n. pl.', 'A glass of fishes having a suckerlike mouth,\n   without jaws, as the lamprey; the Marsipobranchii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclostylar', 'a.', 'Relating to a structure composed of a circular range\n   of columns, without a core or building within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyclostyle', 'n.', 'A contrivance for producing manifold copies of writing\n   or drawing. The writing or drawing is done with a style carrying a\n   small wheel at the end which makes minute punctures in the paper, thus\n   converting it into a stencil. Copies are transferred with an inked\n   roller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyder', 'n.', 'See Cider.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cydonin', 'n.', 'A peculiar mucilaginous substance extracted from the seeds\n   of the quince (Cydonia vulgaris), and regarded as a variety of amylose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cygnet', 'n.', 'A young swan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cygnus', 'n.', 'A constellation of the northern hemisphere east of, or\n   following, Lyra; the Swan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylinder', 'n.', 'A solid body which may be generated by the rotation of a\n   parallelogram round one its sides; or a body of rollerlike form, of\n   which the longitudinal section is oblong, and the cross section is\n   circular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylinder', 'n.', 'The space inclosed by any cylindrical surface. The space\n   may be limited or unlimited in length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylinder', 'n.', 'Any hollow body of cylindrical form');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylinder', 'n.', 'The chamber of a steam engine in which the piston is\n   moved by the force of steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylinder', 'n.', 'The barrel of an air or other pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylinder', 'n.', 'The revolving platen or bed which produces the impression\n   or carries the type in a cylinder press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylinder', 'n.', 'The bore of a gun; the turning chambered breech of a\n   revolver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylinder', 'n.', 'The revolving square prism carrying the cards in a\n   Jacquard loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylindraceous', 'a.', 'Cylindrical, or approaching a cylindrical form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylindric', 'a.', 'Alt. of Cylindrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylindrical', 'a.', 'Having the form of a cylinder, or of a section of its\n   convex surface; partaking of the properties of the cylinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylindrically', 'adv.', 'In the manner or shape of a cylinder; so as to be\n   cylindrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylindricity', 'n.', 'The quality or condition of being cylindrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylindriform', 'a.', 'Having the form of a cylinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylindroid', 'n.', 'A solid body resembling a right cylinder, but having\n   the bases or ends elliptical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylindroid', 'n.', 'A certain surface of the third degree, described by a\n   moving straight line; -- used to illustrate the motions of a rigid body\n   and also the forces acting on the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cylindrometric', 'a.', 'Belonging to a scale used in measuring cylinders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyma', 'n.', 'A member or molding of the cornice, the profile of which is\n   wavelike in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyma', 'n.', 'A cyme. See Cyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymar', 'n.', 'A slight covering; a scarf. See Simar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymatium', 'n.', 'A capping or crowning molding in classic architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymbal', 'n.', 'A musical instrument used by the ancients. It is supposed\n   to have been similar to the modern kettle drum, though perhaps smaller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymbal', 'n.', 'A musical instrument of brass, shaped like a circular dish\n   or a flat plate, with a handle at the back; -- used in pairs to produce\n   a sharp ringing sound by clashing them together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymbal', 'n.', 'A musical instrument used by gypsies and others, made of\n   steel wire, in a triangular form, on which are movable rings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymbalist', 'n.', 'A performer upon cymbals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymbiform', 'a.', 'Shaped like a boat; (Bot.) elongated and having the\n   upper surface decidedly concave, as the glumes of many grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymbium', 'n.', 'A genus of marine univalve shells; the gondola.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyme', 'n.', 'A flattish or convex flower cluster, of the centrifugal or\n   determinate type, differing from a corymb chiefly in the order of the\n   opening of the blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymene', 'n.', 'A colorless, liquid, combustible hydrocarbon,\n   CH3.C6H4.C3H7, of pleasant odor, obtained from oil of cumin, oil of\n   caraway, carvacrol, camphor, etc.; -- called also paracymene, and\n   formerly camphogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymenol', 'n.', 'See Carvacrol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymidine', 'n.', 'A liquid organic base, C10H13.NH2, derived from cymene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymiferous', 'a.', 'Producing cymes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymling', 'n.', 'Alt. of Cymbling');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymbling', 'n.', 'A scalloped or \"pattypan\" variety of summer squash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymogene', 'n.', 'A highly volatile liquid, condensed by cold and pressure\n   from the first products of the distillation of petroleum; -- used for\n   producing low temperatures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymoid', 'a.', 'Having the form of a cyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymophane', 'n.', 'See Chrysoberyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymophanous', 'a.', 'Having a wavy, floating light; opalescent; chatoyant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymose', 'a.', 'Alt. of Cymous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymous', 'a.', 'Having the nature of a cyme, or derived from a cyme;\n   bearing, or pertaining to, a cyme or cymes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymric', 'a.', 'Welsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymric', 'n.', 'The Welsh language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymry', 'n.', 'A collective term for the Welsh race; -- so called by\n   themselves .');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cymule', 'n.', 'A small cyme, or one of very few flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynanche', 'n.', 'Any disease of the tonsils, throat, or windpipe, attended\n   with inflammation, swelling, and difficulty of breathing and\n   swallowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynanthropy', 'n.', 'A kind of madness in which men fancy themselves\n   changed into dogs, and imitate the voice and habits of that animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynarctomachy', 'n.', 'Bear baiting with a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynarrhodium', 'n.', 'A fruit like that of the rose, consisting of a cup\n   formed of the calyx tube and receptacle, and containing achenes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynegetics', 'n.', 'The art of hunting with dogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynic', 'a.', 'Alt. of Cynical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynical', 'a.', 'Having the qualities of a surly dog; snarling; captious;\n   currish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynical', 'a.', 'Pertaining to the Dog Star; as, the cynic, or Sothic,\n   year; cynic cycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynical', 'a.', 'Belonging to the sect of philosophers called cynics;\n   having the qualities of a cynic; pertaining to, or resembling, the\n   doctrines of the cynics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynical', 'a.', 'Given to sneering at rectitude and the conduct of life by\n   moral principles; disbelieving in the reality of any human purposes\n   which are not suggested or directed by self-interest or\n   self-indulgence; as, a cynical man who scoffs at pretensions of\n   integrity; characterized by such opinions; as, cynical views of human\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynic', 'n.', 'One of a sect or school of philosophers founded by\n   Antisthenes, and of whom Diogenes was a disciple. The first Cynics were\n   noted for austere lives and their scorn for social customs and current\n   philosophical opinions. Hence the term Cynic symbolized, in the popular\n   judgment, moroseness, and contempt for the views of others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynic', 'n.', 'One who holds views resembling those of the Cynics; a\n   snarler; a misanthrope; particularly, a person who believes that human\n   conduct is directed, either consciously or unconsciously, wholly by\n   self-interest or self-indulgence, and that appearances to the contrary\n   are superficial and untrustworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynically', 'adv.', 'In a cynical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynicalness', 'n.', 'The quality of being cynical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynicism', 'n.', 'The doctrine of the Cynics; the quality of being cynical;\n   the mental state, opinions, or conduct, of a cynic; morose and\n   contemptuous views and opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynoidea', 'n. pl.', 'A division of Carnivora, including the dogs, wolves,\n   and foxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynorexia', 'n.', 'A voracious appetite, like that of a starved dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynosural', 'a.', 'Of or pertaining to a cynosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynosure', 'n.', 'The constellation of the Lesser Bear, to which, as\n   containing the polar star, the eyes of mariners and travelers were\n   often directed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynosure', 'n.', 'That which serves to direct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cynosure', 'n.', 'Anything to which attention is strongly turned; a center\n   of attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyon', 'n.', 'See Cion, and Scion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyperaceous', 'a.', 'Of, pertaining to, or resembling, a large family of\n   plants of which the sedge is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyperus', 'n.', 'A large genus of plants belonging to the Sedge family, and\n   including the species called galingale, several bulrushes, and the\n   Egyptian papyrus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypher', 'n. & v.', 'See Cipher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyphonautes', 'n.', 'The free-swimming, bivalve larva of certain Bryozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyphonism', 'n.', 'A punishment sometimes used by the ancients, consisting\n   in the besmearing of the criminal with honey, and exposing him to\n   insects. It is still in use among some Oriental nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypraea', 'n.', 'A genus of mollusks, including the cowries. See Cowrie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypres', 'n.', 'A rule for construing written instruments so as to conform\n   as nearly to the intention of the parties as is consistent with law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypresses', 'pl. ', 'of Cypress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypress', 'n.', 'A coniferous tree of the genus Cupressus. The species are\n   mostly evergreen, and have wood remarkable for its durability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprian', 'a.', 'Belonging to Cyprus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprian', 'a.', 'Of, pertaining, or conducing to, lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprian', 'n.', 'A native or inhabitant of Cyprus, especially of ancient\n   Cyprus; a Cypriot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprian', 'n.', 'A lewd woman; a harlot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprine', 'a.', 'Of or pertaining to the cypress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprine', 'a.', 'Cyprinoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprinodont', 'n.', 'One of the Cyprinodontidae, a family of fishes\n   including the killifishes or minnows. See Minnow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprinoid', 'a.', 'Like the carp (Cyprinus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprinoid', 'n.', 'One of the Cyprinidae, or Carp family, as the goldfish,\n   barbel, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypriot', 'n.', 'A native or inhabitant of Cyprus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypripedium', 'n.', 'A genus of orchidaceous plants including the ladys\n   slipper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprides', 'pl. ', 'of Cypris');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypris', 'n.', 'A genus of small, bivalve, fresh-water Crustacea, belonging\n   to the Ostracoda; also, a member of this genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyprus', 'n.', 'A thin, transparent stuff, the same as, or corresponding\n   to, crape. It was either white or black, the latter being most common,\n   and used for mourning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypruslawn', 'n.', 'Same as Cyprus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypsela', 'n.', 'A one-seeded, one-celled, indehiscent fruit; an achene\n   with the calyx tube adherent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cypseliform', 'a.', 'Like or belonging to the swifts (Cypselidae.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyrenaic', 'a.', 'Pertaining to Cyrenaica, an ancient country of northern\n   Africa, and to Cyrene, its principal city; also, to a school of\n   philosophy founded by Aristippus, a native of Cyrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyrenaic', 'n.', 'A native of Cyrenaica; also, a disciple of the school of\n   Aristippus. See Cyrenian, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyrenian', 'a.', 'Pertaining to Cyrene, in Africa; Cyrenaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyrenian', 'n.', 'A native or inhabitant of Cyrene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyrenian', 'n.', 'One of a school of philosophers, established at Cyrene by\n   Aristippus, a disciple of Socrates. Their doctrines were nearly the\n   same as those of the Epicureans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyriologic', 'a.', 'Relating to capital letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyrtostyle', 'n.', 'A circular projecting portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyst', 'n.', 'A pouch or sac without opening, usually membranous and\n   containing morbid matter, which is accidentally developed in one of the\n   natural cavities or in the substance of an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyst', 'n.', 'In old authors, the urinary bladder, or the gall bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyst', 'n.', 'One of the bladders or air vessels of certain algae, as of\n   the great kelp of the Pacific, and common rockweeds (Fuci) of our\n   shores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyst', 'n.', 'A small capsule or sac of the kind in which many immature\n   entozoans exist in the tissues of living animals; also, a similar form\n   in Rotifera, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cyst', 'n.', 'A form assumed by Protozoa in which they become saclike and\n   quiescent. It generally precedes the production of germs. See\n   Encystment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cysted', 'a.', 'Inclosed in a cyst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystic', 'a.', 'Having the form of, or living in, a cyst; as, the cystic\n   entozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystic', 'a.', 'Containing cysts; cystose; as, cystic sarcoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystic', 'a.', 'Pertaining to, or contained in, a cyst; esp., pertaining\n   to, or contained in, either the urinary bladder or the gall bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cysticerce', 'n.', 'Alt. of Cysticercus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cysticercus', 'n.', 'The larval form of a tapeworm, having the head and\n   neck of a tapeworm attached to a saclike body filled with fluid; --\n   called also bladder worm, hydatid, and measle (as, pork measle).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cysticule', 'n.', 'An appendage of the vestibular ear sac of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystid', 'n.', 'One of the Cystidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystidea', 'n. pl.', 'An order of Crinoidea, mostly fossils of the\n   Paleozoic rocks. They were usually roundish or egg-shaped, and often\n   unsymmetrical; some were sessile, others had short stems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystidean', 'n.', 'One of the Cystidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystine', 'n.', 'A white crystalline substance, C3H7NSO2, containing\n   sulphur, occuring as a constituent of certain rare urinary calculi, and\n   occasionally found as a sediment in urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystis', 'n.', 'A cyst. See Cyst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystitis', 'n.', 'Inflammation of the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystocarp', 'n.', 'A minute vesicle in a red seaweed, which contains the\n   reproductive spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystocele', 'n.', 'Hernia in which the urinary bladder protrudes; vesical\n   hernia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystoid', 'n.', 'Alt. of Cystoidean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystoidean', 'n.', 'Same as Cystidean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystoidea', 'n.', 'Same as Cystidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystolith', 'n.', 'A concretion of mineral matter within a leaf or other\n   part of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystolith', 'n.', 'A urinary calculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystolithic', 'a.', 'Relating to stone in the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystoplast', 'n.', 'A nucleated cell having an envelope or cell wall, as a\n   red blood corpuscle or an epithelial cell; a cell concerned in growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystose', 'a.', 'Containing, or resembling, a cyst or cysts; cystic;\n   bladdery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystotome', 'n.', 'A knife or instrument used in cystotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cystotomy', 'n.', 'The act or practice of opening cysts; esp., the\n   operation of cutting into the bladder, as for the extraction of a\n   calculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytherean', 'a.', 'Pertaining to the goddess Venus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytoblast', 'n.', 'The nucleus of a cell; the germinal or active spot of a\n   cellule, through or in which cell development takes place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytoblastema', 'n.', 'See Protoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytococci', 'pl. ', 'of Cytococcus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytococcus', 'n.', 'The nucleus of the cytula or parent cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytode', 'n.', 'A nonnucleated mass of protoplasm, the supposed simplest\n   form of independent life differing from the amoeba, in which nuclei are\n   present.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytogenesis', 'n.', 'Development of cells in animal and vegetable\n   organisms. See Gemmation, Budding, Karyokinesis; also Cell development,\n   under Cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytogenic', 'a.', 'Alt. of Cytogenetic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytogenetic', 'a.', 'Of or pertaining to cytogenesis or cell development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytogenous', 'a.', 'Producing cells; -- applied esp. to lymphatic, or\n   adenoid, tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytogeny', 'n .', 'Cell production or development; cytogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytoid', 'a.', 'Cell-like; -- applied to the corpuscles of lymph, blood,\n   chyle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytoplasm', 'n.', 'The substance of the body of a cell, as distinguished\n   from the karyoplasma, or substance of the nucleus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Cytula', 'n.', 'The fertilized egg cell or parent cell, from the\n   development of which the child or other organism is formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czar', 'n.', 'A king; a chief; the title of the emperor of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czarevna', 'n.', 'The title of the wife of the czarowitz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czarina', 'n.', 'The title of the empress of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czarinian', 'a.', 'Of or pertaining to the czar or the czarina; czarish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czarish', 'a.', 'Of or pertaining to the czar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czarowitzes', 'pl. ', 'of Czarowitz');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czarowitz', 'n.', 'The title of the eldest son of the czar of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czech', 'n.', 'One of the Czechs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czech', 'n.', 'The language of the Czechs (often called Bohemian), the\n   harshest and richest of the Slavic languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czechic', 'a.', 'Of or pertaining to the Czechs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Czechs', 'n. pl.', 'The most westerly branch of the great Slavic family of\n   nations, numbering now more than 6,000,000, and found principally in\n   Bohemia and Moravia.\n   D () The fourth letter of the English alphabet, and a vocal consonant.\n   The English letter is from Latin, which is from Greek, which took it\n   from Ph/nician, the probable ultimate origin being Egyptian. It is\n   related most nearly to t and th; as, Eng. deep, G. tief; Eng. daughter,\n   G. tochter, Gr. qyga`thr, Skr. duhitr. See Guide to Pronunciation,\n   178, 179, 229.');
  INSERT INTO words (word, word_type, description) VALUES  (    'D', '', 'The nominal of the second tone in the model major scale (that in\n   C), or of the fourth tone in the relative minor scale of C (that in A\n   minor), or of the key tone in the relative minor of F.');
  INSERT INTO words (word, word_type, description) VALUES  (    'D', '', 'As a numeral D stands for 500. in this use it is not the initial\n   of any word, or even strictly a letter, but one half of the sign / (or\n   / ) the original Tuscan numeral for 1000.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dab', 'n.', 'A skillful hand; a dabster; an expert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dab', 'n.', 'A name given to several species of flounders, esp. to the\n   European species, Pleuronectes limanda. The American rough dab is\n   Hippoglossoides platessoides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabbed', 'imp. & p. p.', 'of Dab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabbing', 'p. pr. & vb. n.', 'of Dab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dab', 'v. i.', 'To strike or touch gently, as with a soft or moist\n   substance; to tap; hence, to besmear with a dabber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dab', 'v. i.', 'To strike by a thrust; to hit with a sudden blow or thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dab', 'n.', 'A gentle blow with the hand or some soft substance; a sudden\n   blow or hit; a peck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dab', 'n.', 'A small mass of anything soft or moist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabb', 'n.', 'A large, spine-tailed lizard (Uromastix spinipes), found in\n   Egypt, Arabia, and Palestine; -- called also dhobb, and dhabb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabber', 'n.', 'That with which one dabs; hence, a pad or other device used\n   by printers, engravers, etc., as for dabbing type or engraved plates\n   with ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabbled', 'imp. & p. p.', 'of Dabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabbling', 'p. pr. & vb. n.', 'of Dabble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabble', 'v. t.', 'To wet by little dips or strokes; to spatter; to\n   sprinkle; to moisten; to wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabble', 'v. i.', 'To play in water, as with the hands; to paddle or splash\n   in mud or water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabble', 'v. i.', 'To work in slight or superficial manner; to do in a\n   small way; to tamper; to meddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabbler', 'n.', 'One who dabbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabbler', 'n.', 'One who dips slightly into anything; a superficial\n   meddler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabblingly', 'adv.', 'In a dabbling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabchick', 'n.', 'A small water bird (Podilymbus podiceps), allied to the\n   grebes, remarkable for its quickness in diving; -- called also\n   dapchick, dobchick, dipchick, didapper, dobber, devil-diver,\n   hell-diver, and pied-billed grebe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daboia', 'n.', 'A large and highly venomous Asiatic viper (Daboia\n   xanthica).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dabster', 'n.', 'One who is skilled; a master of his business; a\n   proficient; an adept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dacapo', '', 'From the beginning; a direction to return to, and end with,\n   the first strain; -- indicated by the letters D. C. Also, the strain so\n   repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dace', 'n.', 'A small European cyprinoid fish (Squalius leuciscus or\n   Leuciscus vulgaris); -- called also dare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dachshund', 'n.', 'One of a breed of small dogs with short crooked legs,\n   and long body; -- called also badger dog. There are two kinds, the\n   rough-haired and the smooth-haired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dacian', 'a.', 'Of or pertaining to Dacia or the Dacians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dacian', 'n.', 'A native of ancient Dacia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dacoit', 'n.', 'One of a class of robbers, in India, who act in gangs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dacoity', 'n.', 'The practice of gang robbery in India; robbery committed\n   by dacoits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dacotahs', 'n. pl.', 'Same as Dacotas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactyl', 'n.', 'A poetical foot of three sylables (-- ~ ~), one long\n   followed by two short, or one accented followed by two unaccented; as,\n   L. tegm/n/, E. mer\\b6ciful; -- so called from the similarity of its\n   arrangement to that of the joints of a finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactyl', 'n.', 'A finger or toe; a digit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactyl', 'n.', 'The claw or terminal joint of a leg of an insect or\n   crustacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylar', 'a.', 'Pertaining to dactyl; dactylic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylar', 'a.', 'Of or pertaining to a finger or toe, or to the claw of an\n   insect crustacean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylet', 'n.', 'A dactyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylic', 'a.', 'Pertaining to, consisting chiefly or wholly of, dactyls;\n   as, dactylic verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylic', 'n.', 'A line consisting chiefly or wholly of dactyls; as, these\n   lines are dactylics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylic', 'n.', 'Dactylic meters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylioglyph', 'n.', 'An engraver of gems for rings and other ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylioglyph', 'n.', 'The inscription of the engravers name on a finger\n   ring or gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylioglyphi', 'n.', 'The art or process of gem engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactyliography', 'n.', 'The art of writing or engraving upon gems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactyliography', 'n.', 'In general, the literature or history of the art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactyliology', 'n.', 'That branch of archaeology which has to do with gem\n   engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactyliology', 'n.', 'That branch of archaeology which has to do with\n   finger rings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactyliomancy', 'n.', 'Divination by means of finger rings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylist', 'n.', 'A writer of dactylic verse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylitis', 'n.', 'An inflammatory affection of the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylology', 'n.', 'The art of communicating ideas by certain movements\n   and positions of the fingers; -- a method of conversing practiced by\n   the deaf and dumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylomancy', 'n.', 'Dactyliomancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylonomy', 'n.', 'The art of numbering or counting by the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylopterous', 'a.', 'Having the inferior rays of the pectoral fins\n   partially or entirely free, as in the gurnards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylotheca', 'n.', 'The scaly covering of the toes, as in birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dactylozooid', 'n.', 'A kind of zooid of Siphonophora which has an\n   elongated or even vermiform body, with one tentacle, but no mouth. See\n   Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dad', 'n.', 'Father; -- a word sometimes used by children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daddled', 'imp. & p. p.', 'of Dadle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daddling', 'p. pr. & vb. n.', 'of Dadle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dadle', 'v. i.', 'To toddle; to walk unsteadily, like a child or an old\n   man; hence, to do anything slowly or feebly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daddock', 'n.', 'The rotten body of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daddy', 'n.', 'Diminutive of Dad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daddy longlegs', '', 'An arachnidan of the genus Phalangium, and allied\n   genera, having a small body and four pairs of long legs; -- called also\n   harvestman, carter, and grandfather longlegs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daddy longlegs', '', 'A name applied to many species of dipterous insects\n   of the genus Tipula, and allied genera, with slender bodies, and very\n   long, slender legs; the crane fly; -- called also father longlegs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dade', 'v. t.', 'To hold up by leading strings or by the hand, as a child\n   while he toddles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dade', 'v. i.', 'To walk unsteadily, as a child in leading strings, or just\n   learning to walk; to move slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dadoes', 'pl. ', 'of Dado');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dado', 'n.', 'That part of a pedestal included between the base and the\n   cornice (or surbase); the die. See Illust. of Column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dado', 'n.', 'In any wall, that part of the basement included between the\n   base and the base course. See Base course, under Base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dado', 'n.', 'In interior decoration, the lower part of the wall of an\n   apartment when adorned with moldings, or otherwise specially decorated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daedal', 'a.', 'Alt. of Daedalian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daedalian', 'a.', 'Cunningly or ingeniously formed or working; skillful;\n   artistic; ingenious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daedalian', 'a.', 'Crafty; deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daedalous', 'a.', 'Having a variously cut or incised margin; -- said of\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daemon', 'a.', 'Alt. of Daemonic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daemonic', 'a.', 'See Demon, Demonic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daff', 'v. t.', 'To cast aside; to put off; to doff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daff', 'n.', 'A stupid, blockish fellow; a numskull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daff', 'v. i.', 'To act foolishly; to be foolish or sportive; to toy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daff', 'v. t.', 'To daunt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daffodil', 'n.', 'A plant of the genus Asphodelus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daffodil', 'n.', 'A plant of the genus Narcissus (N. Pseudo-narcissus). It\n   has a bulbous root and beautiful flowers, usually of a yellow hue.\n   Called also daffodilly, daffadilly, daffadowndilly, daffydowndilly,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daft', 'a.', 'Stupid; foolish; idiotic; also, delirious; insane; as, he has\n   gone daft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daft', 'a.', 'Gay; playful; frolicsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daftness', 'n.', 'The quality of being daft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dag', 'n.', 'A dagger; a poniard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dag', 'n.', 'A large pistol formerly used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dag', 'n.', 'The unbranched antler of a young deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dag', 'n.', 'A misty shower; dew.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dag', 'n.', 'A loose end; a dangling shred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dag', 'v. t.', 'To daggle or bemire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dag', 'v. t.', 'To cut into jags or points; to slash; as, to dag a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dag', 'v. i.', 'To be misty; to drizzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dagger', 'n.', 'A short weapon used for stabbing. This is the general term:\n   cf. Poniard, Stiletto, Bowie knife, Dirk, Misericorde, Anlace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dagger', 'n.', 'A mark of reference in the form of a dagger [/]. It is the\n   second in order when more than one reference occurs on a page; --\n   called also obelisk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dagger', 'v. t.', 'To pierce with a dagger; to stab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dagger', 'n.', 'A timber placed diagonally in a ships frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dagges', 'n. pl.', 'An ornamental cutting of the edges of garments,\n   introduced about a. d. 1346, according to the Chronicles of St Albans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daggled', 'imp. & p. p.', 'of Daggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daggling', 'p. pr. & vb. n.', 'of Daggle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daggle', 'v. t.', 'To trail, so as to wet or befoul; to make wet and limp;\n   to moisten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daggle', 'v. i.', 'To run, go, or trail ones self through water, mud, or\n   slush; to draggle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daggle-tail', 'a.', 'Alt. of Daggle-tailed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daggle-tailed', 'a.', 'Having the lower ends of garments defiled by\n   trailing in mire or filth; draggle-tailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daggle-tail', 'n.', 'A slovenly woman; a slattern; a draggle-tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daglock', 'n.', 'A dirty or clotted lock of wool on a sheep; a taglock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dagos', 'pl. ', 'of Dago');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dago', 'n.', 'A nickname given to a person of Spanish (or, by extension,\n   Portuguese or Italian) descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dagoba', 'n.', 'A dome-shaped structure built over relics of Buddha or some\n   Buddhist saint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dagon', '', 'The national god of the Philistines, represented with the face\n   and hands and upper part of a man, and the tail of a fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dagon', 'n.', 'A slip or piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dagswain', 'n.', 'A coarse woolen fabric made of daglocks, or the refuse of\n   wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dag-tailed', 'a.', 'Daggle-tailed; having the tail clogged with daglocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerrean', 'a.', 'Alt. of Daguerreian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerreian', 'a.', 'Pertaining to Daguerre, or to his invention of the\n   daguerreotype.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerreotype', 'n.', 'An early variety of photograph, produced on a silver\n   plate, or copper plate covered with silver, and rendered sensitive by\n   the action of iodine, or iodine and bromine, on which, after exposure\n   in the camera, the latent image is developed by the vapor of mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerreotype', 'n.', 'The process of taking such pictures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerreotyped', 'imp. & p. p.', 'of Daguerreotype');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerreotyping', 'p. pr. & vb. n.', 'of Daguerreotype');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerreotype', 'v. t.', 'To produce or represent by the daguerreotype\n   process, as a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerreotype', 'v. t.', 'To impress with great distinctness; to imprint;\n   to imitate exactly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerreotyper', 'n.', 'Alt. of Daguerreotypist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerreotypist', 'n.', 'One who takes daguerreotypes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daguerreotypy', 'n.', 'The art or process of producing pictures by method\n   of Daguerre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dahabeah', 'n.', 'A Nile boat constructed on the model of a floating house,\n   having large lateen sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dahlias', 'pl. ', 'of Dahlia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dahlia', 'n.', 'A genus of plants native to Mexico and Central America, of\n   the order Compositae; also, any plant or flower of the genus. The\n   numerous varieties of cultivated dahlias bear conspicuous flowers which\n   differ in color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dahlin', 'n.', 'A variety of starch extracted from the dahlia; -- called\n   also inulin. See Inulin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dailiness', 'n.', 'Daily occurence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daily', 'a.', 'Happening, or belonging to, each successive day; diurnal;\n   as, daily labor; a daily bulletin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dailies', 'pl. ', 'of Daily');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daily', 'n.', 'A publication which appears regularly every day; as, the\n   morning dailies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daily', 'adv.', 'Every day; day by day; as, a thing happens daily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daimios', 'pl. ', 'of Daimio');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daimio', 'n.', 'The title of the feudal nobles of Japan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daint', 'n.', 'Something of exquisite taste; a dainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daint', 'a.', 'Dainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daintified', 'imp. & p. p.', 'of Daintify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daintifying', 'p. pr. & vb. n.', 'of Daintify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daintify', 'v. t.', 'To render dainty, delicate, or fastidious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daintily', 'adv.', 'In a dainty manner; nicely; scrupulously; fastidiously;\n   deliciously; prettily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daintiness', 'n.', 'The quality of being dainty; nicety; niceness;\n   elegance; delicacy; deliciousness; fastidiousness; squeamishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daintrel', 'n.', 'Adelicacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dainties', 'pl. ', 'of Dainty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dainty', 'n.', 'Value; estimation; the gratification or pleasure taken in\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dainty', 'n.', 'That which is delicious or delicate; a delicacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dainty', 'n.', 'A term of fondness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dainty', 'superl.', 'Rare; valuable; costly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dainty', 'superl.', 'Delicious to the palate; toothsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dainty', 'superl.', 'Nice; delicate; elegant, in form, manner, or breeding;\n   well-formed; neat; tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dainty', 'superl.', 'Requiring dainties. Hence: Overnice; hard to please;\n   fastidious; squeamish; scrupulous; ceremonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dairies', 'pl. ', 'of Dairy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dairy', 'n.', 'The place, room, or house where milk is kept, and converted\n   into butter or cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dairy', 'n.', 'That department of farming which is concerned in the\n   production of milk, and its conversion into butter and cheese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dairy', 'n.', 'A dairy farm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dairying', 'n.', 'The business of conducting a dairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dairymaid', 'n.', 'A female servant whose business is the care of the\n   dairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dairymen', 'pl. ', 'of Dairyman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dairyman', 'n.', 'A man who keeps or takes care of a dairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dairywomen', 'pl. ', 'of Dairywoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dairywoman', 'n.', 'A woman who attends to a dairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dais', 'n.', 'The high or principal table, at the end of a hall, at which\n   the chief guests were seated; also, the chief seat at the high table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dais', 'n.', 'A platform slightly raised above the floor of a hall or large\n   room, giving distinction to the table and seats placed upon it for the\n   chief guests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dais', 'n.', 'A canopy over the seat of a person of dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daisied', 'a.', 'Full of daisies; adorned with daisies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daisies', 'pl. ', 'of Daisy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daisy', 'n.', 'A genus of low herbs (Bellis), belonging to the family\n   Compositae. The common English and classical daisy is B. prennis, which\n   has a yellow disk and white or pinkish rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daisy', 'n.', 'The whiteweed (Chrysanthemum Leucanthemum), the plant\n   commonly called daisy in North America; -- called also oxeye daisy. See\n   Whiteweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dak', 'n.', 'Post; mail; also, the mail or postal arrangements; -- spelt\n   also dawk, and dauk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daker', 'n.', 'Alt. of Dakir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dakir', 'n.', 'A measure of certain commodities by number, usually ten or\n   twelve, but sometimes twenty; as, a daker of hides consisted of ten\n   skins; a daker of gloves of ten pairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daker hen', '', 'The corncrake or land rail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dakoit', 'n.', 'Alt. of Dakoity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dakoity', 'n.', 'See Dacoit, Dacoity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dakota group', '', 'A subdivision at the base of the cretaceous formation\n   in Western North America; -- so named from the region where the strata\n   were first studied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dakotas', 'n. pl', 'An extensive race or stock of Indians, including many\n   tribes, mostly dwelling west of the Mississippi River; -- also, in\n   part, called Sioux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dal', 'n.', 'Split pulse, esp. of Cajanus Indicus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dale', 'n.', 'A low place between hills; a vale or valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dale', 'n.', 'A trough or spout to carry off water, as from a pump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalesmen', 'pl. ', 'of Dalesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalesman', 'n.', 'One living in a dale; -- a term applied particularly to\n   the inhabitants of the valleys in the north of England, Norway, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalf', '', 'imp. of Delve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalliance', 'n.', 'The act of dallying, trifling, or fondling; interchange\n   of caresses; wanton play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalliance', 'n.', 'Delay or procrastination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalliance', 'n.', 'Entertaining discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dallier', 'n.', 'One who fondles; a trifler; as, dalliers with pleasant\n   words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dallop', 'n.', 'A tuft or clump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dallied', 'imp. & p. p.', 'of Dally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dallying', 'p. pr. & vb. n.', 'of Dally');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dally', 'v. i.', 'To waste time in effeminate or voluptuous pleasures, or\n   in idleness; to fool away time; to delay unnecessarily; to tarry; to\n   trifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dally', 'v. i.', 'To interchange caresses, especially with one of the\n   opposite sex; to use fondling; to wanton; to sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dally', 'v. t.', 'To delay unnecessarily; to while away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalmania', 'n.', 'A genus of trilobites, of many species, common in the\n   Upper Silurian and Devonian rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalmanites', 'n.', 'Same as Dalmania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalmatian', 'a.', 'Of or pertaining to Dalmatia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalmatica', 'n.', 'Alt. of Dalmatic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalmatic', 'n.', 'A vestment with wide sleeves, and with two stripes, worn\n   at Mass by deacons, and by bishops at pontifical Mass; -- imitated from\n   a dress originally worn in Dalmatia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dalmatic', 'n.', 'A robe worn on state ocasions, as by English kings at\n   their coronation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dal segno', '', 'A direction to go back to the sign / and repeat from\n   thence to the close. See Segno.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daltonian', 'n.', 'One afflicted with color blindness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daltonism', 'n.', 'Inability to perceive or distinguish certain colors,\n   esp. red; color blindness. It has various forms and degrees. So called\n   from the chemist Dalton, who had this infirmity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dam', 'n.', 'A female parent; -- used of beasts, especially of quadrupeds;\n   sometimes applied in contempt to a human mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dam', 'n.', 'A kind or crowned piece in the game of draughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dam', 'n.', 'A barrier to prevent the flow of a liquid; esp., a bank of\n   earth, or wall of any kind, as of masonry or wood, built across a water\n   course, to confine and keep back flowing water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dam', 'n.', 'A firebrick wall, or a stone, which forms the front of the\n   hearth of a blast furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dammed', 'imp. & p. p.', 'of Dam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damming', 'p. pr. & vb. n.', 'of Dam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dam', 'v. t.', 'To obstruct or restrain the flow of, by a dam; to confine\n   by constructing a dam, as a stream of water; -- generally used with in\n   or up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dam', 'v. t.', 'To shut up; to stop up; to close; to restrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damage', 'n.', 'Injury or harm to person, property, or reputation; an\n   inflicted loss of value; detriment; hurt; mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damage', 'n.', 'The estimated reparation in money for detriment or injury\n   sustained; a compensation, recompense, or satisfaction to one party,\n   for a wrong or injury actually done to him by another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damages', 'imp. & p. p.', 'of Damage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damaging', 'p. pr. & vb. n.', 'of Damage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damage', 'n.', 'To ocassion damage to the soudness, goodness, or value of;\n   to hurt; to injure; to impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damage', 'v. i.', 'To receive damage or harm; to be injured or impaired in\n   soudness or value; as. some colors in /oth damage in sunlight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damageable', 'a.', 'Capable of being injured or impaired; liable to, or\n   susceptible of, damage; as, a damageable cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damageable', 'a.', 'Hurtful; pernicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damage feasant', '', 'Doing injury; trespassing, as cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daman', 'n.', 'A small herbivorous mammal of the genus Hyrax. The species\n   found in Palestine and Syria is Hyrax Syriacus; that of Northern Africa\n   is H. Brucei; -- called also ashkoko, dassy, and rock rabbit. See Cony,\n   and Hyrax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damar', 'n.', 'See Dammar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damascene', 'a.', 'Of or relating to Damascus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damascene', 'n.', 'A kind of plume, now called damson. See Damson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damascene', 'v. t.', 'Same as Damask, or Damaskeen, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damascus', 'n.', 'A city of Syria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damask', 'n.', 'Damask silk; silk woven with an elaborate pattern of\n   flowers and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damask', 'n.', 'Linen so woven that a pattern in produced by the different\n   directions of the thread, without contrast of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damask', 'n.', 'A heavy woolen or worsted stuff with a pattern woven in the\n   same way as the linen damask; -- made for furniture covering and\n   hangings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damask', 'n.', 'Damask or Damascus steel; also, the peculiar markings or\n   \"water\" of such steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damask', 'n.', 'A deep pink or rose color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damask', 'a.', 'Pertaining to, or originating at, the city of Damascus;\n   resembling the products or manufactures of Damascus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damask', 'a.', 'Having the color of the damask rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damasked', 'imp. & p. p.', 'of Damask');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damasking', 'p. pr. & vb. n.', 'of Damask');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damask', 'v. t.', 'To decorate in a way peculiar to Damascus or attributed\n   to Damascus; particularly: (a) with flowers and rich designs, as silk;\n   (b) with inlaid lines of gold, etc., or with a peculiar marking or\n   \"water,\" as metal. See Damaskeen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damaskeen', 'v.', 'Alt. of Damasken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damasken', 'v.', 'To decorate, as iron, steel, etc., with a peculiar\n   marking or \"water\" produced in the process of manufacture, or with\n   designs produced by inlaying or incrusting with another metal, as\n   silver or gold, or by etching, etc., to damask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damaskin', 'n.', 'A sword of Damask steel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damasse', 'a.', 'Woven like damask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damasse', 'n.', 'A damasse fabric, esp. one of linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damassin', 'n.', 'A kind of modified damask or brocade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dambonite', 'n.', 'A white, crystalline, sugary substance obtained from an\n   African caoutchouc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dambose', 'n.', 'A crystalline variety of fruit sugar obtained from\n   dambonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dame', 'n.', 'A mistress of a family, who is a lady; a woman in authority;\n   especially, a lady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dame', 'n.', 'The mistress of a family in common life, or the mistress of a\n   common school; as, a dames school.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dame', 'n.', 'A woman in general, esp. an elderly woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dame', 'n.', 'A mother; -- applied to human beings and quadrupeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damewort', 'n.', 'A cruciferrous plant (Hesperis matronalis), remarkable\n   for its fragrance, especially toward the close of the day; -- called\n   also rocket and dames violet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damiana', 'n.', 'A Mexican drug, used as an aphrodisiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damianist', 'n.', 'A follower of Damian, patriarch of Alexandria in the 6th\n   century, who held heretical opinions on the doctrine of the Holy\n   Trinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dammar', 'n.', 'Alt. of Dammara');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dammara', 'n.', 'An oleoresin used in making varnishes; dammar gum; dammara\n   resin. It is obtained from certain resin trees indigenous to the East\n   Indies, esp. Shorea robusta and the dammar pine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dammara', 'n.', 'A large tree of the order Coniferae, indigenous to the\n   East Indies and Australasia; -- called also Agathis. There are several\n   species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damned', 'imp. & p. p.', 'of Damn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damning', 'p. pr. & vb. n.', 'of Damn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damn', 'v. t.', 'To condemn; to declare guilty; to doom; to adjudge to\n   punishment; to sentence; to censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damn', 'v. t.', 'To doom to punishment in the future world; to consign to\n   perdition; to curse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damn', 'v. t.', 'To condemn as bad or displeasing, by open expression, as\n   by denuciation, hissing, hooting, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damn', 'v. i.', 'To invoke damnation; to curse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnability', 'n.', 'The quality of being damnable; damnableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnable', 'a.', 'Liable to damnation; deserving, or for which one\n   deserves, to be damned; of a damning nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnable', 'a.', 'Odious; pernicious; detestable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnableness', 'n.', 'The state or quality of deserving damnation;\n   execrableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnably', 'adv.', 'In a manner to incur severe censure, condemnation, or\n   punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnably', 'adv.', 'Odiously; detestably; excessively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnation', 'n.', 'The state of being damned; condemnation; openly\n   expressed disapprobation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnation', 'n.', 'Condemnation to everlasting punishment in the future\n   state, or the punishment itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnation', 'n.', 'A sin deserving of everlasting punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnatory', 'a.', 'Dooming to damnation; condemnatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damned', 'a.', 'Sentenced to punishment in a future state; condemned;\n   consigned to perdition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damned', 'a.', 'Hateful; detestable; abominable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnific', 'a.', 'Procuring or causing loss; mischievous; injurious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnification', 'n.', 'That which causes damage or loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damnify', 'v. t.', 'To cause loss or damage to; to injure; to impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damning', 'a.', 'That damns; damnable; as, damning evidence of guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damningness', 'n.', 'Tendency to bring damnation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'damnum', 'n.', 'Harm; detriment, either to character or property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damosel', 'n.', 'Alt. of Damoiselle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damosella', 'n.', 'Alt. of Damoiselle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damoiselle', 'n.', 'See Damsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damourite', 'n.', 'A kind of Muscovite, or potash mica, containing water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damp', 'n.', 'Moisture; humidity; fog; fogginess; vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damp', 'n.', 'Dejection; depression; cloud of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damp', 'n.', 'A gaseous product, formed in coal mines, old wells, pints,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damp', 'superl.', 'Being in a state between dry and wet; moderately wet;\n   moist; humid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damp', 'superl.', 'Dejected; depressed; sunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damped', 'imp. & p. p.', 'of Damp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damping', 'p. pr. & vb. n.', 'of Damp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damp', 'n.', 'To render damp; to moisten; to make humid, or moderately wet;\n   to dampen; as, to damp cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damp', 'n.', 'To put out, as fire; to depress or deject; to deaden; to\n   cloud; to check or restrain, as action or vigor; to make dull; to\n   weaken; to discourage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dampened', 'imp. & p. p.', 'of Dampen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dampening', 'p. pr. & vb. n.', 'of Dampen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dampen', 'v. t.', 'To make damp or moist; to make slightly wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dampen', 'v. t.', 'To depress; to check; to make dull; to lessen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dampen', 'v. i.', 'To become damp; to deaden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damper', 'n.', 'That which damps or checks; as: (a) A valve or movable\n   plate in the flue or other part of a stove, furnace, etc., used to\n   check or regulate the draught of air. (b) A contrivance, as in a\n   pianoforte, to deaden vibrations; or, as in other pieces of mechanism,\n   to check some action at a particular time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dampish', 'a.', 'Moderately damp or moist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dampne', 'v. t.', 'To damn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dampness', 'n.', 'Moderate humidity; moisture; fogginess; moistness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damp off', '', 'To decay and perish through excessive moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dampy', 'a.', 'Somewhat damp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dampy', 'a.', 'Dejected; gloomy; sorrowful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damsel', 'n.', 'A young person, either male or female, of noble or gentle\n   extraction; as, Damsel Pepin; Damsel Richard, Prince of Wales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damsel', 'n.', 'A young unmarried woman; a girl; a maiden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damsel', 'n.', 'An attachment to a millstone spindle for shaking the\n   hopper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Damson', 'n.', 'A small oval plum of a blue color, the fruit of a variety\n   of the Prunus domestica; -- called also damask plum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dan', 'n.', 'A title of honor equivalent to master, or sir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dan', 'n.', 'A small truck or sledge used in coal mines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Danaide', 'n.', 'A water wheel having a vertical axis, and an inner and\n   outer tapering shell, between which are vanes or floats attached\n   usually to both shells, but sometimes only to one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Danaite', 'n.', 'A cobaltiferous variety of arsenopyrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Danalite', 'n.', 'A mineral occuring in octahedral crystals, also massive,\n   of a reddish color. It is a silicate of iron, zinc manganese, and\n   glucinum, containing sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Danburite', 'n.', 'A borosilicate of lime, first found at Danbury, Conn. It\n   is near the topaz in form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Danced', 'imp. & p. p.', 'of Dance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dancing', 'p. pr. & vb. n.', 'of Dance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dance', 'v. i.', 'To move with measured steps, or to a musical\n   accompaniment; to go through, either alone or in company with others,\n   with a regulated succession of movements, (commonly) to the sound of\n   music; to trip or leap rhythmically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dance', 'v. i.', 'To move nimbly or merrily; to express pleasure by motion;\n   to caper; to frisk; to skip about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dance', 'v. t.', 'To cause to dance, or move nimbly or merrily about, or up\n   and down; to dandle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dance', 'v. i.', 'The leaping, tripping, or measured stepping of one who\n   dances; an amusement, in which the movements of the persons are\n   regulated by art, in figures and in accord with music.');
