  INSERT INTO words (word, word_type, description) VALUES  (    'Dart', 'n.', 'A pointed missile weapon, intended to be thrown by the hand;\n   a short lance; a javelin; hence, any sharp-pointed missile weapon, as\n   an arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dart', 'n.', 'Anything resembling a dart; anything that pierces or wounds\n   like a dart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dart', 'n.', 'A spear set as a prize in running.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dart', 'n.', 'A fish; the dace. See Dace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Darted', 'imp. & p. p.', 'of Dart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Darting', 'p. pr. & vb. n.', 'of Dart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dart', 'v. t.', 'To throw with a sudden effort or thrust, as a dart or\n   other missile weapon; to hurl or launch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dart', 'v. t.', 'To throw suddenly or rapidly; to send forth; to emit; to\n   shoot; as, the sun darts forth his beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dart', 'v. i.', 'To fly or pass swiftly, as a dart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dart', 'v. i.', 'To start and run with velocity; to shoot rapidly along;\n   as, the deer darted from the thicket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dartars', 'n.', 'A kind of scab or ulceration on the skin of lambs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Darter', 'n.', 'One who darts, or who throw darts; that which darts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Darter', 'n.', 'The snakebird, a water bird of the genus Plotus; -- so\n   called because it darts out its long, snakelike neck at its prey. See\n   Snakebird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Darter', 'n.', 'A small fresh-water etheostomoid fish. The group includes\n   numerous genera and species, all of them American. See Etheostomoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dartingly', 'adv.', 'Like a dart; rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dartle', 'v. t. & i.', 'To pierce or shoot through; to dart repeatedly: --\n   frequentative of dart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dartoic', 'a.', 'Of or pertaining to the dartos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dartoid', 'a.', 'Like the dartos; dartoic; as, dartoid tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dartos', 'n.', 'A thin layer of peculiar contractile tissue directly\n   beneath the skin of the scrotum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dartrous', 'a.', 'Relating to, or partaking of the nature of, the disease\n   called tetter; herpetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Darwinian', 'a.', 'Pertaining to Darwin; as, the Darwinian theory, a theory\n   of the manner and cause of the supposed development of living things\n   from certain original forms or elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Darwinian', 'n.', 'An advocate of Darwinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Darwinianism', 'n.', 'Darwinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Darwinism', 'n.', 'The theory or doctrines put forth by Darwin. See above.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dase', 'v. t.', 'See Daze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dasewe', 'v. i.', 'To become dim-sighted; to become dazed or dazzled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dashed', 'imp. & p. p.', 'of Dash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dashing', 'p. pr. & vb. n.', 'of Dash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'v. t.', 'To throw with violence or haste; to cause to strike\n   violently or hastily; -- often used with against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'v. t.', 'To break, as by throwing or by collision; to shatter; to\n   crust; to frustrate; to ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'v. t.', 'To put to shame; to confound; to confuse; to abash; to\n   depress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'v. t.', 'To throw in or on in a rapid, careless manner; to mix,\n   reduce, or adulterate, by throwing in something of an inferior quality;\n   to overspread partially; to bespatter; to touch here and there; as, to\n   dash wine with water; to dash paint upon a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'v. t.', 'To form or sketch rapidly or carelessly; to execute\n   rapidly, or with careless haste; -- with off; as, to dash off a review\n   or sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'v. t.', 'To erase by a stroke; to strike out; knock out; -- with\n   out; as, to dash out a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'v. i.', 'To rust with violence; to move impetuously; to strike\n   violently; as, the waves dash upon rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'n.', 'Violent striking together of two bodies; collision; crash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'n.', 'A sudden check; abashment; frustration; ruin; as, his hopes\n   received a dash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'n.', 'A slight admixture, infusion, or adulteration; a partial\n   overspreading; as, wine with a dash of water; red with a dash of\n   purple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'n.', 'A rapid movement, esp. one of short duration; a quick stroke\n   or blow; a sudden onset or rush; as, a bold dash at the enemy; a dash\n   of rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'n.', 'Energy in style or action; animation; spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'n.', 'A vain show; a blustering parade; a flourish; as, to make or\n   cut a great dash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'n.', 'A mark or line [--], in writing or printing, denoting a\n   sudden break, stop, or transition in a sentence, or an abrupt change in\n   its construction, a long or significant pause, or an unexpected or\n   epigrammatic turn of sentiment. Dashes are also sometimes used instead\n   of marks or parenthesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'n.', 'The sign of staccato, a small mark [/] denoting that the note\n   over which it is placed is to be performed in a short, distinct manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'n.', 'The line drawn through a figure in the thorough bass, as a\n   direction to raise the interval a semitone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dash', 'n.', 'A short, spirited effort or trial of speed upon a race\n   course; -- used in horse racing, when a single trial constitutes the\n   race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dashboard', 'n.', 'A board placed on the fore part of a carriage, sleigh,\n   or other vehicle, to intercept water, mud, or snow, thrown up by the\n   heels of the horses; -- in England commonly called splashboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dashboard', 'n.', 'The float of a paddle wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dashboard', 'n.', 'A screen at the bow af a steam launch to keep off the\n   spray; -- called also sprayboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dasher', 'n.', 'That which dashes or agitates; as, the dasher of a churn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dasher', 'n.', 'A dashboard or splashboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dasher', 'n.', 'One who makes an ostentatious parade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dashing', 'a.', 'Bold; spirited; showy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dashingly', 'adv.', 'Conspicuously; showily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dashism', 'n.', 'The character of making ostentatious or blustering parade\n   or show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dashpot', 'n.', 'A pneumatic or hydraulic cushion for a falling weight, as\n   in the valve gear of a steam engine, to prevent shock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dashy', 'a.', 'Calculated to arrest attention; ostentatiously fashionable;\n   showy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dastard', 'n.', 'One who meanly shrinks from danger; an arrant coward; a\n   poltroon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dastard', 'a.', 'Meanly shrinking from danger; cowardly; dastardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dastard', 'v. t.', 'To dastardize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dastardized', 'imp. & p. p.', 'of Dastardize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dastardizing', 'p. pr. & vb. n.', 'of Dastardize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dastardize', 'v. t.', 'To make cowardly; to intimidate; to dispirit; as, to\n   dastardize my courage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dastardliness', 'n.', 'The quality of being dastardly; cowardice; base\n   fear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dastardly', 'a.', 'Meanly timid; cowardly; base; as, a dastardly outrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dastardness', 'n.', 'Dastardliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dastardy', 'n.', 'Base timidity; cowardliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daswe', 'v. i.', 'See Dasewe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dasymeter', 'n.', 'An instrument for testing the density of gases,\n   consisting of a thin glass globe, which is weighed in the gas or gases,\n   and then in an atmosphere of known density.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dasypaedal', 'a.', 'Dasypaedic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dasypaedes', 'n. pl.', 'Those birds whose young are covered with down when\n   hatched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dasypaedic', 'a.', 'Pertaining to the Dasypaedes; ptilopaedic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dasyure', 'n.', 'A carnivorous marsupial quadruped of Australia, belonging\n   to the genus Dasyurus. There are several species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dasyurine', 'a.', 'Pertaining to, or like, the dasyures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Data', 'n. pl.', 'See Datum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Datable', 'a.', 'That may be dated; having a known or ascertainable date.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dataria', 'n.', 'Formerly, a part of the Roman chancery; now, a separate\n   office from which are sent graces or favors, cognizable in foro\n   externo, such as appointments to benefices. The name is derived from\n   the word datum, given or dated (with the indications of the time and\n   place of granting the gift or favor).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Datary', 'n.', 'An officer in the popes court, having charge of the\n   Dataria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Datary', 'n.', 'The office or employment of a datary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Date', 'n.', 'The fruit of the date palm; also, the date palm itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Date', 'n.', 'That addition to a writing, inscription, coin, etc., which\n   specifies the time (as day, month, and year) when the writing or\n   inscription was given, or executed, or made; as, the date of a letter,\n   of a will, of a deed, of a coin. etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Date', 'n.', 'The point of time at which a transaction or event takes\n   place, or is appointed to take place; a given point of time; epoch; as,\n   the date of a battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Date', 'n.', 'Assigned end; conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Date', 'n.', 'Given or assigned length of life; dyration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dated', 'imp. & p. p.', 'of Date');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dating', 'p. pr. & vb. n.', 'of Date');
  INSERT INTO words (word, word_type, description) VALUES  (    'Date', 'v. t.', 'To note the time of writing or executing; to express in an\n   instrument the time of its execution; as, to date a letter, a bond, a\n   deed, or a charter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Date', 'v. t.', 'To note or fix the time of, as of an event; to give the\n   date of; as, to date the building of the pyramids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Date', 'v. i.', 'To have beginning; to begin; to be dated or reckoned; --\n   with from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dateless', 'a.', 'Without date; having no fixed time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dater', 'n.', 'One who dates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Datiscin', 'n.', 'A white crystalline glucoside extracted from the bastard\n   hemp (Datisca cannabina).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dative', 'a.', 'Noting the case of a noun which expresses the remoter\n   object, and is generally indicated in English by to or for with the\n   objective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dative', 'a.', 'In ones gift; capable of being disposed of at will and\n   pleasure, as an office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dative', 'a.', 'Removable, as distinguished from perpetual; -- said of an\n   officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dative', 'a.', 'Given by a magistrate, as distinguished from being cast\n   upon a party by the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dative', 'n.', 'The dative case. See Dative, a., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Datively', 'adv.', 'As a gift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Datolite', 'n.', 'A borosilicate of lime commonly occuring in glassy,,\n   greenish crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Data', 'pl. ', 'of Datum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Datum', 'n.', 'Something given or admitted; a fact or principle granted;\n   that upon which an inference or an argument is based; -- used chiefly\n   in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Datum', 'n.', 'The quantities or relations which are assumed to be given in\n   any problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Datura', 'n.', 'A genus of solanaceous plants, with large funnel-shaped\n   flowers and a four-celled, capsular fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daturine', 'n.', 'Atropine; -- called also daturia and daturina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daubed', 'imp. & p. p.', 'of Daub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daubing', 'p. pr. & vb. n.', 'of Daub');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daub', 'v. t.', 'To smear with soft, adhesive matter, as pitch, slime, mud,\n   etc.; to plaster; to bedaub; to besmear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daub', 'v. t.', 'To paint in a coarse or unskillful manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daub', 'v. t.', 'To cover with a specious or deceitful exterior; to\n   disguise; to conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daub', 'v. t.', 'To flatter excessively or glossy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daub', 'v. t.', 'To put on without taste; to deck gaudily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daub', 'v. i.', 'To smear; to play the flatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daub', 'n.', 'A viscous, sticky application; a spot smeared or dabed; a\n   smear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daub', 'n.', 'A picture coarsely executed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauber', 'n.', 'One who, or that which, daubs; especially, a coarse,\n   unskillful painter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauber', 'n.', 'A pad or ball of rags, covered over with canvas, for inking\n   plates; a dabber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauber', 'n.', 'A low and gross flatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauber', 'n.', 'The mud wasp; the mud dauber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daubery', 'n.', 'Alt. of Daubry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daubry', 'n.', 'A daubing; specious coloring; false pretenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daubing', 'n.', 'The act of one who daubs; that which is daubed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daubing', 'n.', 'A rough coat of mortar put upon a wall to give it the\n   appearance of stone; rough-cast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daubing', 'n.', 'In currying, a mixture of fish oil and tallow worked into\n   leather; -- called also dubbing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daubreelite', 'n.', 'A sulphide of chromium observed in some meteoric\n   irons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauby', 'a.', 'Smeary; viscous; glutinous; adhesive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daughters', 'pl. ', 'of Daughter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daughtren', 'pl. ', 'of Daughter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daughter', 'n.', 'The female offspring of the human species; a female child\n   of any age; -- applied also to the lower animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daughter', 'n.', 'A female descendant; a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daughter', 'n.', 'A sons wife; a daughter-in-law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daughter', 'n.', 'A term of address indicating parental interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daughters-in-law', 'pl. ', 'of Daughter-in-law');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daughter-in-law', 'n.', 'The wife of ones son.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daughterliness', 'n.', 'The state of a daughter, or the conduct becoming a\n   daughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daughterly', 'a.', 'Becoming a daughter; filial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauk', 'v. t.', 'See Dawk, v. t., to cut or gush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daun', 'n.', 'A variant of Dan, a title of honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daunted', 'imp. & p. p.', 'of Daunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daunting', 'p. pr. & vb. n.', 'of Daunt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daunt', 'v. t.', 'To overcome; to conquer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daunt', 'v. t.', 'To repress or subdue the courage of; to check by fear of\n   danger; to cow; to intimidate; to dishearten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daunter', 'n.', 'One who daunts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauntless', 'a.', 'Incapable of being daunted; undaunted; bold; fearless;\n   intrepid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauphin', 'n.', 'The title of the eldest son of the king of France, and\n   heir to the crown. Since the revolution of 1830, the title has been\n   discontinued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauphiness', 'n.', 'Alt. of Dauphine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauphine', 'n.', 'The title of the wife of the dauphin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dauw', 'n.', 'The striped quagga, or Burchells zebra, of South Africa\n   (Asinus Burchellii); -- called also peechi, or peetsi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Davenport', 'n.', 'A kind of small writing table, generally somewhat\n   ornamental, and forming a piece of furniture for the parlor or boudoir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Davidic', 'a.', 'Of or pertaining to David, the king and psalmist of\n   Israel, or to his family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Davit', 'n.', 'A spar formerly used on board of ships, as a crane to hoist\n   the flukes of the anchor to the top of the bow, without injuring the\n   sides of the ship; -- called also the fish davit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Davit', 'n.', 'Curved arms of timber or iron, projecting over a ships side\n   of stern, having tackle to raise or lower a boat, swing it in on deck,\n   rig it out for lowering, etc.; -- called also boat davits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Davy Jones', '', 'The spirit of the sea; sea devil; -- a term used by\n   sailors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Davy lamp', '', 'See Safety lamp, under Lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Davyne', 'n.', 'A variety of nephelite from Vesuvius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Davyum', 'n.', 'A rare metallic element found in platinum ore. It is a\n   white malleable substance. Symbol Da. Atomic weight 154.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daw', 'n.', 'A European bird of the Crow family (Corvus monedula), often\n   nesting in church towers and ruins; a jackdaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daw', 'v. i.', 'To dawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daw', 'v. t.', 'To rouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daw', 'v. t.', 'To daunt; to terrify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawdled', 'imp. & p. p.', 'of Dawdle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawdling', 'p. pr. & vb. n.', 'of Dawdle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawdle', 'v. i.', 'To waste time in trifling employment; to trifle; to\n   saunter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawdle', 'v. t.', 'To waste by trifling; as, to dawdle away a whole\n   morning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawdle', 'n.', 'A dawdler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawdler', 'n.', 'One who wastes time in trifling employments; an idler; a\n   trifler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawe', 'n.', 'Day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawish', 'a.', 'Like a daw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawk', 'n.', 'See Dak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawk', 'v. t.', 'To cut or mark with an incision; to gash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawk', 'n.', 'A hollow, crack, or cut, in timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawned', 'imp. & p. p.', 'of Dawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawning', 'p. pr. & vb. n.', 'of Dawn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawn', 'v. i.', 'To begin to grow light in the morning; to grow light; to\n   break, or begin to appear; as, the day dawns; the morning dawns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawn', 'v. i.', 'To began to give promise; to begin to appear or to expand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawn', 'n.', 'The break of day; the first appearance of light in the\n   morning; show of approaching sunrise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawn', 'n.', 'First opening or expansion; first appearance; beginning;\n   rise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dawsonite', 'n.', 'A hydrous carbonate of alumina and soda, occuring in\n   white, bladed crustals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day', 'n.', 'The time of light, or interval between one night and the next;\n   the time between sunrise and sunset, or from dawn to darkness; hence,\n   the light; sunshine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day', 'n.', 'The period of the earths revolution on its axis. --\n   ordinarily divided into twenty-four hours. It is measured by the\n   interval between two successive transits of a celestial body over the\n   same meridian, and takes a specific name from that of the body. Thus,\n   if this is the sun, the day (the interval between two successive\n   transits of the suns center over the same meridian) is called a solar\n   day; if it is a star, a sidereal day; if it is the moon, a lunar day.\n   See Civil day, Sidereal day, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day', 'n.', 'Those hours, or the daily recurring period, allotted by usage\n   or law for work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day', 'n.', 'A specified time or period; time, considered with reference to\n   the existence or prominence of a person or thing; age; time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day', 'n.', '(Preceded by the) Some day in particular, as some day of\n   contest, some anniversary, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dayaks', 'n. pl.', 'See Dyaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daybook', 'n.', 'A journal of accounts; a primary record book in which are\n   recorded the debts and credits, or accounts of the day, in their order,\n   and from which they are transferred to the journal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daybreak', 'n.', 'The time of the first appearance of light in the morning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day-coal', 'n.', 'The upper stratum of coal, as nearest the light or\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daydream', 'n.', 'A vain fancy speculation; a reverie; a castle in the air;\n   unfounded hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daydreamer', 'n.', 'One given to daydreams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dayflower', 'n.', 'A genus consisting mostly of tropical perennial herbs\n   (Commelina), having ephemeral flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dayfly', 'n.', 'A neuropterous insect of the genus Ephemera and related\n   genera, of many species, and inhabiting fresh water in the larval\n   state; the ephemeral fly; -- so called because it commonly lives but\n   one day in the winged or adult state. See Ephemeral fly, under\n   Ephemeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day-labor', 'n.', 'Labor hired or performed by the day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day-laborer', 'n.', 'One who works by the day; -- usually applied to a farm\n   laborer, or to a workman who does not work at any particular trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daylight', 'n.', 'The light of day as opposed to the darkness of night; the\n   light of the sun, as opposed to that of the moon or to artificial\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daylight', 'n.', 'The eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day lily', '', 'A genus of plants (Hemerocallis) closely resembling true\n   lilies, but having tuberous rootstocks instead of bulbs. The common\n   species have long narrow leaves and either yellow or tawny-orange\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day lily', '', 'A genus of plants (Funkia) differing from the last in\n   having ovate veiny leaves, and large white or blue flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daymaid', 'n.', 'A dairymaid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daymare', 'n.', 'A kind of incubus which occurs during wakefulness,\n   attended by the peculiar pressure on the chest which characterizes\n   nightmare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day-net', 'n.', 'A net for catching small birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day-peep', 'n.', 'The dawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daysman', 'n.', 'An umpire or arbiter; a mediator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dayspring', 'n.', 'The beginning of the day, or first appearance of light;\n   the dawn; hence, the beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day-star', 'n.', 'The morning star; the star which ushers in the day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Day-star', 'n.', 'The sun, as the orb of day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daytime', 'n.', 'The time during which there is daylight, as distinguished\n   from the night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daywoman', 'n.', 'A dairymaid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazed', 'imp. & p. p.', 'of Daze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazing', 'p. pr. & vb. n.', 'of Daze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daze', 'v. t.', 'To stupefy with excess of light; with a blow, with cold,\n   or with fear; to confuse; to benumb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daze', 'n.', 'The state of being dazed; as, he was in a daze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Daze', 'n.', 'A glittering stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazzled', 'imp. & p. p.', 'of Dazzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazzling', 'p. pr. & vb. n.', 'of Dazzle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazzle', 'v. t.', 'To overpower with light; to confuse the sight of by\n   brilliance of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazzle', 'v. t.', 'To bewilder or surprise with brilliancy or display of\n   any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazzle', 'v. i.', 'To be overpoweringly or intensely bright; to excite\n   admiration by brilliancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazzle', 'v. i.', 'To be overpowered by light; to be confused by excess of\n   brightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazzle', 'n.', 'A light of dazzling brilliancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazzlement', 'n.', 'Dazzling flash, glare, or burst of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dazzlingly', 'adv.', 'In a dazzling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'De-', '', 'A prefix from Latin de down, from, away; as in debark, decline,\n   decease, deduct, decamp. In words from the French it is equivalent to\n   Latin dis-apart, away; or sometimes to de. Cf. Dis-. It is negative and\n   opposite in derange, deform, destroy, etc. It is intensive in deprave,\n   despoil, declare, desolate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deacon', 'n.', 'An officer in Christian churches appointed to perform\n   certain subordinate duties varying in different communions. In the\n   Roman Catholic and Episcopal churches, a person admitted to the lowest\n   order in the ministry, subordinate to the bishops and priests. In\n   Presbyterian churches, he is subordinate to the minister and elders,\n   and has charge of certain duties connected with the communion service\n   and the care of the poor. In Congregational churches, he is subordinate\n   to the pastor, and has duties as in the Presbyterian church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deacon', 'n.', 'The chairman of an incorporated company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deacon', 'v. t.', 'To read aloud each line of (a psalm or hymn) before\n   singing it, -- usually with off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaconess', 'n.', 'A female deacon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaconess', 'n.', 'One of an order of women whose duties resembled those of\n   deacons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaconess', 'n.', 'A woman set apart for church work by a bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaconess', 'n.', 'A woman chosen as a helper in church work, as among the\n   Congregationalists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaconhood', 'n.', 'The state of being a deacon; office of a deacon;\n   deaconship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaconry', 'n.', 'See Deaconship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaconship', 'n.', 'The office or ministry of a deacon or deaconess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Deprived of life; -- opposed to alive and living; reduced to\n   that state of a being in which the organs of motion and life have\n   irrevocably ceased to perform their functions; as, a dead tree; a dead\n   man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Destitute of life; inanimate; as, dead matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Resembling death in appearance or quality; without show of\n   life; deathlike; as, a dead sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Still as death; motionless; inactive; useless; as, dead calm;\n   a dead load or weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'So constructed as not to transmit sound; soundless; as, a\n   dead floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Unproductive; bringing no gain; unprofitable; as, dead\n   capital; dead stock in trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Lacking spirit; dull; lusterless; cheerless; as, dead eye;\n   dead fire; dead color, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Monotonous or unvaried; as, a dead level or pain; a dead\n   wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Sure as death; unerring; fixed; complete; as, a dead shot; a\n   dead certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Bringing death; deadly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Wanting in religious spirit and vitality; as, dead faith;\n   dead works.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Flat; without gloss; -- said of painting which has been\n   applied purposely to have this effect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Not brilliant; not rich; thus, brown is a dead color, as\n   compared with crimson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Cut off from the rights of a citizen; deprived of the power\n   of enjoying the rights of property; as, one banished or becoming a monk\n   is civilly dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'a.', 'Not imparting motion or power; as, the dead spindle of a\n   lathe, etc. See Spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'adv.', 'To a degree resembling death; to the last degree;\n   completely; wholly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'n.', 'The most quiet or deathlike time; the period of profoundest\n   repose, inertness, or gloom; as, the dead of winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'n.', 'One who is dead; -- commonly used collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'v. t.', 'To make dead; to deaden; to deprive of life, force, or\n   vigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead', 'v. i.', 'To die; to lose life or force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead beat', '', 'See Beat, n., 7.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadbeat', 'a.', 'Making a beat without recoil; giving indications by a\n   single beat or excursion; -- said of galvanometers and other\n   instruments in which the needle or index moves to the extent of its\n   deflection and stops with little or no further oscillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadborn', 'a.', 'Stillborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadened', 'imp. & p. p.', 'of Deaden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadening', 'p. pr. & vb. n.', 'of Deaden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaden', 'a.', 'To make as dead; to impair in vigor, force, activity, or\n   sensation; to lessen the force or acuteness of; to blunt; as, to deaden\n   the natural powers or feelings; to deaden a sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaden', 'a.', 'To lessen the velocity or momentum of; to retard; as, to\n   deaden a ships headway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaden', 'a.', 'To make vapid or spiritless; as, to deaden wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaden', 'a.', 'To deprive of gloss or brilliancy; to obscure; as, to\n   deaden gilding by a coat of size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadener', 'n.', 'One who, or that which, deadens or checks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead-eye', 'n.', 'A round, flattish, wooden block, encircled by a rope, or\n   an iron band, and pierced with three holes to receive the lanyard; --\n   used to extend the shrouds and stays, and for other purposes. Called\n   also deadmans eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadhead', 'n.', 'One who receives free tickets for theaters, public\n   conveyances, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadhead', 'n.', 'A buoy. See under Dead, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead-hearted', 'a.', 'Having a dull, faint heart; spiritless; listless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadhouse', 'n.', 'A morgue; a place for the temporary reception and\n   exposure of dead bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadish', 'a.', 'Somewhat dead, dull, or lifeless; deathlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadlatch', 'n.', 'A kind of latch whose bolt may be so locked by a detent\n   that it can not be opened from the inside by the handle, or from the\n   outside by the latch key.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadlight', 'n.', 'A strong shutter, made to fit open ports and keep out\n   water in a storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadlihood', 'n.', 'State of the dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadliness', 'n.', 'The quality of being deadly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadlock', 'n.', 'A lock which is not self-latching, but requires a key to\n   throw the bolt forward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadlock', 'n.', 'A counteraction of things, which produces an entire\n   stoppage; a complete obstruction of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadly', 'a.', 'Capable of causing death; mortal; fatal; destructive;\n   certain or likely to cause death; as, a deadly blow or wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadly', 'a.', 'Aiming or willing to destroy; implacable; desperately\n   hostile; flagitious; as, deadly enemies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadly', 'a.', 'Subject to death; mortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadly', 'adv.', 'In a manner resembling, or as if produced by, death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadly', 'adv.', 'In a manner to occasion death; mortally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadly', 'adv.', 'In an implacable manner; destructively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadly', 'adv.', 'Extremely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadness', 'n.', 'The state of being destitute of life, vigor, spirit,\n   activity, etc.; dullness; inertness; languor; coldness; vapidness;\n   indifference; as, the deadness of a limb, a body, or a tree; the\n   deadness of an eye; deadness of the affections; the deadness of beer or\n   cider; deadness to the world, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead-pay', 'n.', 'Pay drawn for soldiers, or others, really dead, whose\n   names are kept on the rolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead-reckoning', 'n.', 'See under Dead, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deads', 'n. pl.', 'The substances which inclose the ore on every side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dead-stroke', 'a.', 'Making a stroke without recoil; deadbeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadwood', 'n.', 'A mass of timbers built into the bow and stern of a\n   vessel to give solidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadwood', 'n.', 'Dead trees or branches; useless material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deadworks', 'n. pl.', 'The parts of a ship above the water when she is\n   laden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaf', 'a.', 'Wanting the sense of hearing, either wholly or in part;\n   unable to perceive sounds; hard of hearing; as, a deaf man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaf', 'a.', 'Unwilling to hear or listen; determinedly inattentive;\n   regardless; not to be persuaded as to facts, argument, or exhortation;\n   -- with to; as, deaf to reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaf', 'a.', 'Deprived of the power of hearing; deafened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaf', 'a.', 'Obscurely heard; stifled; deadened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaf', 'a.', 'Decayed; tasteless; dead; as, a deaf nut; deaf corn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaf', 'v. t.', 'To deafen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deafened', 'imp. & p. p.', 'of Deafen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deafening', 'p. pr. & vb. n.', 'of Deafen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deafen', 'v. t.', 'To make deaf; to deprive of the power of hearing; to\n   render incapable of perceiving sounds distinctly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deafen', 'v. t.', 'To render impervious to sound, as a partition or floor,\n   by filling the space within with mortar, by lining with paper, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deafening', 'n.', 'The act or process of rendering impervious to sound, as\n   a floor or wall; also, the material with which the spaces are filled in\n   this process; pugging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deafly', 'adv.', 'Without sense of sounds; obscurely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deafly', 'a.', 'Lonely; solitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaf-mute', 'n.', 'A person who is deaf and dumb; one who, through\n   deprivation or defect of hearing, has either failed the acquire the\n   power of speech, or has lost it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaf-mutism', 'n.', 'The condition of being a deaf-mute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deafness', 'n.', 'Incapacity of perceiving sounds; the state of the organs\n   which prevents the impression which constitute hearing; want of the\n   sense of hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deafness', 'n.', 'Unwillingness to hear; voluntary rejection of what is\n   addressed to the understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'n.', 'A part or portion; a share; hence, an indefinite quantity,\n   degree, or extent, degree, or extent; as, a deal of time and trouble; a\n   deal of cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'n.', 'The process of dealing cards to the players; also, the\n   portion disturbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'n.', 'Distribution; apportionment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'n.', 'An arrangement to attain a desired result by a combination of\n   interested parties; -- applied to stock speculations and political\n   bargains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'n.', 'The division of a piece of timber made by sawing; a board or\n   plank; particularly, a board or plank of fir or pine above seven inches\n   in width, and exceeding six feet in length. If narrower than this, it\n   is called a batten; if shorter, a deal end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'n.', 'Wood of the pine or fir; as, a floor of deal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dealt', 'imp. & p. p.', 'of Deal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dealing', 'p. pr. & vb. n.', 'of Deal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'n.', 'To divide; to separate in portions; hence, to give in\n   portions; to distribute; to bestow successively; -- sometimes with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'n.', 'Specifically: To distribute, as cards, to the players at the\n   commencement of a game; as, to deal the cards; to deal one a jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'v. i.', 'To make distribution; to share out in portions, as cards\n   to the players.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'v. i.', 'To do a distributing or retailing business, as\n   distinguished from that of a manufacturer or producer; to traffic; to\n   trade; to do business; as, he deals in flour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'v. i.', 'To act as an intermediary in business or any affairs; to\n   manage; to make arrangements; -- followed by between or with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'v. i.', 'To conduct ones self; to behave or act in any affair or\n   towards any one; to treat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deal', 'v. i.', 'To contend (with); to treat (with), by way of opposition,\n   check, or correction; as, he has turbulent passions to deal with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dealbate', 'v. t.', 'To whiten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dealbation', 'n.', 'Act of bleaching; a whitening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dealer', 'n.', 'One who deals; one who has to do, or has concern, with\n   others; esp., a trader, a trafficker, a shopkeeper, a broker, or a\n   merchant; as, a dealer in dry goods; a dealer in stocks; a retail\n   dealer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dealer', 'n.', 'One who distributes cards to the players.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dealfish', 'n.', 'A long, thin fish of the arctic seas (Trachypterus\n   arcticus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dealing', 'n.', 'The act of one who deals; distribution of anything, as of\n   cards to the players; method of business; traffic; intercourse;\n   transaction; as, to have dealings with a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dealth', 'n.', 'Share dealt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deambulate', 'v. i.', 'To walk abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deambulation', 'n.', 'A walking abroad; a promenading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deambulatory', 'a.', 'Going about from place to place; wandering; of or\n   pertaining to a deambulatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deambulatory', 'n.', 'A covered place in which to walk; an ambulatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dean', 'n.', 'A dignitary or presiding officer in certain ecclesiastical\n   and lay bodies; esp., an ecclesiastical dignitary, subordinate to a\n   bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dean', 'n.', 'The collegiate officer in the universities of Oxford and\n   Cambridge, England, who, besides other duties, has regard to the moral\n   condition of the college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dean', 'n.', 'The head or presiding officer in the faculty of some colleges\n   or universities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dean', 'n.', 'A registrar or secretary of the faculty in a department of a\n   college, as in a medical, or theological, or scientific department.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dean', 'n.', 'The chief or senior of a company on occasion of ceremony; as,\n   the dean of the diplomatic corps; -- so called by courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaneries', 'pl. ', 'of Deanery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deanery', 'n.', 'The office or the revenue of a dean. See the Note under\n   Benefice, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deanery', 'n.', 'The residence of a dean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deanery', 'n.', 'The territorial jurisdiction of a dean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deanship', 'n.', 'The office of a dean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear', 'superl.', 'Bearing a high price; high-priced; costly; expensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear', 'superl.', 'Marked by scarcity or dearth, and exorbitance of price;\n   as, a dear year.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear', 'superl.', 'Highly valued; greatly beloved; cherished; precious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear', 'superl.', 'Hence, close to the heart; heartfelt; present in mind;\n   engaging the attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear', 'superl.', 'Of agreeable things and interests.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear', 'superl.', 'Of disagreeable things and antipathies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear', 'n.', 'A dear one; lover; sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear', 'adv.', 'Dearly; at a high price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear', 'v. t.', 'To endear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearborn', 'n.', 'A four-wheeled carriage, with curtained sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear-bought', 'a.', 'Bought at a high price; as, dear-bought experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deare', '', 'variant of Dere, v. t. & n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearie', 'n.', 'Same as Deary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearling', 'n.', 'A darling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dear-loved', 'a.', 'Greatly beloved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearly', 'adv.', 'In a dear manner; with affection; heartily; earnestly;\n   as, to love one dearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearly', 'adv.', 'At a high rate or price; grievously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearly', 'adv.', 'Exquisitely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearn', 'a.', 'Secret; lonely; solitary; dreadful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearn', 'v. t.', 'Same as Darn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearness', 'n.', 'The quality or state of being dear; costliness; excess of\n   price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearness', 'n.', 'Fondness; preciousness; love; tenderness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearth', 'n.', 'Scarcity which renders dear; want; lack; specifically, lack\n   of food on account of failure of crops; famine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearticulate', 'v. t.', 'To disjoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dearworth', 'a.', 'Precious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deary', 'n.', 'A dear; a darling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deas', 'n.', 'See Dais.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Death', 'v. i.', 'The cessation of all vital phenomena without capability\n   of resuscitation, either in animals or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Death', 'v. i.', 'Total privation or loss; extinction; cessation; as, the\n   death of memory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Death', 'v. i.', 'Manner of dying; act or state of passing from life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Death', 'v. i.', 'Cause of loss of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Death', 'v. i.', 'Personified: The destroyer of life, -- conventionally\n   represented as a skeleton with a scythe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Death', 'v. i.', 'Danger of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Death', 'v. i.', 'Murder; murderous character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Death', 'v. i.', 'Loss of spiritual life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Death', 'v. i.', 'Anything so dreadful as to be like death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathbed', 'n.', 'The bed in which a person dies; hence, the closing hours\n   of life of one who dies by sickness or the like; the last sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathbird', 'n.', 'Tengmalms or Richardsons owl (Nyctale Tengmalmi); --\n   so called from a superstition of the North American Indians that its\n   note presages death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathblow', 'n.', 'A mortal or crushing blow; a stroke or event which kills\n   or destroys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathful', 'a.', 'Full of death or slaughter; murderous; destructive;\n   bloody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathful', 'a.', 'Liable to undergo death; mortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathfulness', 'n.', 'Appearance of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathless', 'a.', 'Not subject to death, destruction, or extinction;\n   immortal; undying; imperishable; as, deathless beings; deathless fame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathlike', 'a.', 'Resembling death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathlike', 'a.', 'Deadly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathliness', 'n.', 'The quality of being deathly; deadliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathly', 'a.', 'Deadly; fatal; mortal; destructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathly', 'adv.', 'Deadly; as, deathly pale or sick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaths-head', 'n.', 'A naked human skull as the emblem of death; the head\n   of the conventional personification of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaths-herb', 'n.', 'The deadly nightshade (Atropa belladonna).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathsman', 'n.', 'An executioner; a headsman or hangman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathward', 'adv.', 'Toward death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathwatch', 'n.', 'A small beetle (Anobium tessellatum and other allied\n   species). By forcibly striking its head against woodwork it makes a\n   ticking sound, which is a call of the sexes to each other, but has been\n   imagined by superstitious people to presage death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathwatch', 'n.', 'A small wingless insect, of the family Psocidae, which\n   makes a similar but fainter sound; -- called also deathtick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deathwatch', 'n.', 'The guard set over a criminal before his execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaurate', 'a.', 'Gilded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deaurate', 'v. t.', 'To gild.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deauration', 'n.', 'Act of gilding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deave', 'v. t.', 'To stun or stupefy with noise; to deafen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debacchate', 'v. i.', 'To rave as a bacchanal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debacchation', 'n.', 'Wild raving or debauchery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debacle', 'n.', 'A breaking or bursting forth; a violent rush or flood of\n   waters which breaks down opposing barriers, and hurls forward and\n   disperses blocks of stone and other debris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debarred', 'imp. & p. p.', 'of Debar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debarring', 'p. pr. & vb. n.', 'of Debar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debar', 'v. t.', 'To cut off from entrance, as if by a bar or barrier; to\n   preclude; to hinder from approach, entry, or enjoyment; to shut out or\n   exclude; to deny or refuse; -- with from, and sometimes with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debarb', 'v. t.', 'To deprive of the beard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debarked', 'imp. & p. p.', 'of Debark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debarking', 'p. pr. & vb. n.', 'of Debark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debark', 'v. t. & i.', 'To go ashore from a ship or boat; to disembark; to\n   put ashore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debarkation', 'n.', 'Disembarkation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debarment', 'n.', 'Hindrance from approach; exclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debarrass', 'v. t.', 'To disembarrass; to relieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debased', 'imp. & p. p.', 'of Debase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debasing', 'p. pr. & vb. n.', 'of Debase');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debase', 'a.', 'To reduce from a higher to a lower state or grade of worth,\n   dignity, purity, station, etc.; to degrade; to lower; to deteriorate;\n   to abase; as, to debase the character by crime; to debase the mind by\n   frivolity; to debase style by vulgar words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debased', 'a.', 'Turned upside down from its proper position; inverted;\n   reversed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debasement', 'n.', 'The act of debasing or the state of being debased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debaser', 'n.', 'One who, or that which, debases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debasingly', 'adv.', 'In a manner to debase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debatable', 'a.', 'Liable to be debated; disputable; subject to controversy\n   or contention; open to question or dispute; as, a debatable question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debated', 'imp. & p. p.', 'of Debate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debating', 'p. pr. & vb. n.', 'of Debate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debate', 'v. t.', 'To engage in combat for; to strive for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debate', 'v. t.', 'To contend for in words or arguments; to strive to\n   maintain by reasoning; to dispute; to contest; to discuss; to argue for\n   and against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debate', 'v. i.', 'To engage in strife or combat; to fight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debate', 'v. i.', 'To contend in words; to dispute; hence, to deliberate;\n   to consider; to discuss or examine different arguments in the mind; --\n   often followed by on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debate', 'v. t.', 'A fight or fighting; contest; strife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debate', 'v. t.', 'Contention in words or arguments; discussion for the\n   purpose of elucidating truth or influencing action; strife in argument;\n   controversy; as, the debates in Parliament or in Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debate', 'v. t.', 'Subject of discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debateful', 'a.', 'Full of contention; contentious; quarrelsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debatefully', 'adv.', 'With contention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debatement', 'n.', 'Controversy; deliberation; debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debater', 'n.', 'One who debates; one given to argument; a disputant; a\n   controvertist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debating', 'n.', 'The act of discussing or arguing; discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debatingly', 'adv.', 'In the manner of a debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauched', 'imp. & p. p.', 'of Debauch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauching', 'p. pr. & vb. n.', 'of Debauch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauch', 'n.', 'To lead away from purity or excellence; to corrupt in\n   character or principles; to mar; to vitiate; to pollute; to seduce; as,\n   to debauch ones self by intemperance; to debauch a woman; to debauch\n   an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauch', 'n.', 'Excess in eating or drinking; intemperance; drunkenness;\n   lewdness; debauchery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauch', 'n.', 'An act or occasion of debauchery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauched', 'a.', 'Dissolute; dissipated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauchedly', 'adv.', 'In a profligate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauchedness', 'n.', 'The state of being debauched; intemperance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauchee', 'v. t.', 'One who is given to intemperance or bacchanalian\n   excesses; a man habitually lewd; a libertine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debaucher', 'n.', 'One who debauches or corrupts others; especially, a\n   seducer to lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debaucheries', 'pl. ', 'of Debauchery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauchery', 'n.', 'Corruption of fidelity; seduction from virtue, duty, or\n   allegiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauchery', 'n.', 'Excessive indulgence of the appetites; especially,\n   excessive indulgence of lust; intemperance; sensuality; habitual\n   lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauchment', 'n.', 'The act of corrupting; the act of seducing from virtue\n   or duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debauchness', 'n.', 'Debauchedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debeige', 'n.', 'A kind of woolen or mixed dress goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debel', 'v. t.', 'To conquer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debellate', 'v. t.', 'To subdue; to conquer in war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debellation', 'n.', 'The act of conquering or subduing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'De bene esse', '', 'Of well being; of formal sufficiency for the time;\n   conditionally; provisionally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debenture', 'n.', 'A writing acknowledging a debt; a writing or certificate\n   signed by a public officer, as evidence of a debt due to some person;\n   the sum thus due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debenture', 'n.', 'A customhouse certificate entitling an exporter of\n   imported goods to a drawback of duties paid on their importation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debentured', 'a.', 'Entitled to drawback or debenture; as, debentured\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debile', 'a.', 'Weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debilitant', 'a.', 'Diminishing the energy of organs; reducing excitement;\n   as, a debilitant drug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debilitated', 'imp. & p. p.', 'of Debilitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debilitating', 'p. pr. & vb. n.', 'of Debilitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debilitate', 'v. t.', 'To impair the strength of; to weaken; to enfeeble;\n   as, to debilitate the body by intemperance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debilitation', 'n.', 'The act or process of debilitating, or the condition\n   of one who is debilitated; weakness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debility', 'a.', 'The state of being weak; weakness; feebleness; languor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debit', 'n.', 'A debt; an entry on the debtor (Dr.) side of an account; --\n   mostly used adjectively; as, the debit side of an account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debited', 'imp. & p. p.', 'of Debit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debiting', 'p. pr. & vb. n.', 'of Debit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debit', 'v. t.', 'To charge with debt; -- the opposite of, and correlative\n   to, credit; as, to debit a purchaser for the goods sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debit', 'v. t.', 'To enter on the debtor (Dr.) side of an account; as, to\n   debit the amount of goods sold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debitor', 'n.', 'A debtor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debituminization', 'n.', 'The act of depriving of bitumen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debituminize', 'v. t.', 'To deprive of bitumen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deblai', 'n.', 'The cavity from which the earth for parapets, etc.\n   (remblai), is taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debonair', 'a.', 'Characterized by courteousness, affability, or\n   gentleness; of good appearance and manners; graceful; complaisant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debonairity', 'n.', 'Debonairness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debonairly', 'adv.', 'Courteously; elegantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debonairness', 'n.', 'The quality of being debonair; good humor;\n   gentleness; courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debosh', 'v. t.', 'To debauch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deboshment', 'n.', 'Debauchment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debouched', 'imp. & p. p.', 'of Debouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debouching', 'p. pr. & vb. n.', 'of Debouch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debouch', 'v. i.', 'To march out from a wood, defile, or other confined\n   spot, into open ground; to issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debouche', 'n.', 'A place for exit; an outlet; hence, a market for goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debouchure', 'n.', 'The outward opening of a river, of a valley, or of a\n   strait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debris', 'n.', 'Broken and detached fragments, taken collectively;\n   especially, fragments detached from a rock or mountain, and piled up at\n   the base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debris', 'n.', 'Rubbish, especially such as results from the destruction of\n   anything; remains; ruins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debruised', 'a.', 'Surmounted by an ordinary; as, a lion is debruised when\n   a bend or other ordinary is placed over it, as in the cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debt', 'n.', 'That which is due from one person to another, whether money,\n   goods, or services; that which one person is bound to pay to another,\n   or to perform for his benefit; thing owed; obligation; liability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debt', 'n.', 'A duty neglected or violated; a fault; a sin; a trespass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debt', 'n.', 'An action at law to recover a certain specified sum of money\n   alleged to be due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debted', 'p. a.', 'Indebted; obliged to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debtee', 'n.', 'One to whom a debt is due; creditor; -- correlative to\n   debtor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debtless', 'a.', 'Free from debt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debtor', 'n.', 'One who owes a debt; one who is indebted; -- correlative to\n   creditor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debulliate', 'v. i.', 'To boil over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debulition', 'n.', 'A bubbling or boiling over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deburse', 'v. t. & i.', 'To disburse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debuscope', 'n.', 'A modification of the kaleidoscope; -- used to reflect\n   images so as to form beautiful designs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debut', 'n.', 'A beginning or first attempt; hence, a first appearance\n   before the public, as of an actor or public speaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debutant', '', 'Alt. of Debutante');
  INSERT INTO words (word, word_type, description) VALUES  (    'Debutante', '', 'A person who makes his (or her) first appearance before\n   the public.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deca-', '', 'A prefix, from Gr. de`ka, signifying ten; specifically (Metric\n   System), a prefix signifying the weight or measure that is ten times\n   the principal unit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decacerata', 'n. pl.', 'The division of Cephalopoda which includes the\n   squids, cuttlefishes, and others having ten arms or tentacles; --\n   called also Decapoda. [Written also Decacera.] See Dibranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decachord', 'n.', 'Alt. of Decachordon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decachordon', 'n.', 'An ancient Greek musical instrument of ten strings,\n   resembling the harp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decachordon', 'n.', 'Something consisting of ten parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decucuminated', 'a.', 'Having the point or top cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decad', 'n.', 'A decade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decadal', 'a.', 'Pertaining to ten; consisting of tens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decade', 'n.', 'A group or division of ten; esp., a period of ten years; a\n   decennium; as, a decade of years or days; a decade of soldiers; the\n   second decade of Livy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decadence', 'n.', 'Alt. of Decadency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decadency', 'n.', 'A falling away; decay; deterioration; declension. \"The\n   old castle, where the family lived in their decadence.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decadent', 'a.', 'Decaying; deteriorating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decadist', 'n.', 'A writer of a book divided into decades; as, Livy was a\n   decadist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decagon', 'n.', 'A plane figure having ten sides and ten angles; any figure\n   having ten angles. A regular decagon is one that has all its sides and\n   angles equal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decagonal', 'a.', 'Pertaining to a decagon; having ten sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decagram', 'n.', 'Alt. of Decagramme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decagramme', 'n.', 'A weight of the metric system; ten grams, equal to\n   about 154.32 grains avoirdupois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decagynia', 'n. pl.', 'A Linnaean order of plants characterized by having\n   ten styles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decagynian', 'a.', 'Alt. of Deccagynous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deccagynous', 'a.', 'Belonging to the Decagynia; having ten styles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decahedral', 'a.', 'Having ten sides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decahedrons', 'pl. ', 'of Decahedron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decahedra', 'pl. ', 'of Decahedron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decahedron', 'n.', 'A solid figure or body inclosed by ten plane surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decalcification', 'n.', 'The removal of calcareous matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decalcified', 'imp. & p. p.', 'of Decalcify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decalcifying', 'p. pr. & vb. n.', 'of Decalcify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decalcify', 'v. t.', 'To deprive of calcareous matter; thus, to decalcify\n   bones is to remove the stony part, and leave only the gelatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decalcomania', 'n.', 'Alt. of Decalcomanie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decalcomanie', 'n.', 'The art or process of transferring pictures and\n   designs to china, glass, marble, etc., and permanently fixing them\n   thereto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decaliter', 'n.', 'Alt. of Decalitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decalitre', 'n.', 'A measure of capacity in the metric system; a cubic\n   volume of ten liters, equal to about 610.24 cubic inches, that is,\n   2.642 wine gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decalog', 'n.', 'Decalogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decalogist', 'n.', 'One who explains the decalogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decalogue', 'n.', 'The Ten Commandments or precepts given by God to Moses\n   on Mount Sinai, and originally written on two tables of stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decameron', 'n.', 'A celebrated collection of tales, supposed to be related\n   in ten days; -- written in the 14th century, by Boccaccio, an Italian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decameter', 'n.', 'Alt. of Decametre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decametre', 'n.', 'A measure of length in the metric system; ten meters,\n   equal to about 393.7 inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decamped', 'imp. & p. p.', 'of Decamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decamping', 'p. pr. & vb. n.', 'of Decamp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decamp', 'v. i.', 'To break up a camp; to move away from a camping ground,\n   usually by night or secretly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decamp', 'v. i.', 'Hence, to depart suddenly; to run away; -- generally\n   used disparagingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decampment', 'n.', 'Departure from a camp; a marching off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decanal', 'a.', 'Pertaining to a dean or deanery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decandria', 'n. pl.', 'A Linnaean class of plants characterized by having\n   ten stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decandrian', 'a.', 'Alt. of Decandrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decandrous', 'a.', 'Belonging to the Decandria; having ten stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decane', 'n.', 'A liquid hydrocarbon, C10H22, of the paraffin series,\n   including several isomeric modifications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decangular', 'a.', 'Having ten angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decani', 'a.', 'Used of the side of the choir on which the deans stall is\n   placed; decanal; -- correlative to cantoris; as, the decanal, or\n   decani, side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decanted', 'imp. & p. p.', 'of Decant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decanting', 'p. pr. & vb. n.', 'of Decant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decant', 'v. t.', 'To pour off gently, as liquor, so as not to disturb the\n   sediment; or to pour from one vessel into another; as, to decant wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decantate', 'v. t.', 'To decant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decantation', 'n.', 'The act of pouring off a clear liquor gently from its\n   lees or sediment, or from one vessel into another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decanter', 'n.', 'A vessel used to decant liquors, or for receiving\n   decanted liquors; a kind of glass bottle used for holding wine or other\n   liquors, from which drinking glasses are filled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decanter', 'n.', 'One who decants liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decaphyllous', 'a.', 'Having ten leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decapitated', 'imp. & p. p.', 'of Decapitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decapitating', 'p. pr. & vb. n.', 'of Decapitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decapitate', 'v. t.', 'To cut off the head of; to behead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decapitate', 'v. t.', 'To remove summarily from office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decapitation', 'n.', 'The act of beheading; beheading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decapod', 'n.', 'A crustacean with ten feet or legs, as a crab; one of the\n   Decapoda. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decapoda', 'n. pl.', 'The order of Crustacea which includes the shrimps,\n   lobsters, crabs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decapoda', 'n. pl.', 'A division of the dibranchiate cephalopods including\n   the cuttlefishes and squids. See Decacera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deccapodal', 'a.', 'Alt. of Deccapodous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deccapodous', 'a.', 'Belonging to the decapods; having ten feet;\n   ten-footed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decarbonate', 'v. t.', 'To deprive of carbonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decarbonization', 'n.', 'The action or process of depriving a substance of\n   carbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decarbonized', 'imp. & p. p.', 'of Decarbonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decarbonizing', 'p. pr. & vb. n.', 'of Decarbonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decarbonize', 'v. t.', 'To deprive of carbon; as, to decarbonize steel; to\n   decarbonize the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decarbonizer', 'n.', 'He who, or that which, decarbonizes a substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decarburization', 'n.', 'The act, process, or result of decarburizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decarburize', 'v. t.', 'To deprive of carbon; to remove the carbon from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decard', 'v. t.', 'To discard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decardinalize', 'v. t.', 'To depose from the rank of cardinal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decastere', 'n.', 'A measure of capacity, equal to ten steres, or ten cubic\n   meters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decastich', 'n.', 'A poem consisting of ten lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decastyle', 'a.', 'Having ten columns in front; -- said of a portico,\n   temple, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decastyle', 'n.', 'A portico having ten pillars or columns in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decasyllabic', 'a.', 'Having, or consisting of, ten syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decatoic', 'a.', 'Pertaining to, or derived from, decane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decayed', 'imp. & p. p.', 'of Decay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decaying', 'p. pr. & vb. n.', 'of Decay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decay', 'v. i.', 'To pass gradually from a sound, prosperous, or perfect\n   state, to one of imperfection, adversity, or dissolution; to waste\n   away; to decline; to fail; to become weak, corrupt, or disintegrated;\n   to rot; to perish; as, a tree decays; fortunes decay; hopes decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decay', 'v. t.', 'To cause to decay; to impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decay', 'v. t.', 'To destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decay', 'n.', 'Gradual failure of health, strength, soundness, prosperity,\n   or of any species of excellence or perfection; tendency toward\n   dissolution or extinction; corruption; rottenness; decline;\n   deterioration; as, the decay of the body; the decay of virtue; the\n   decay of the Roman empire; a castle in decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decay', 'n.', 'Destruction; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decay', 'n.', 'Cause of decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decayed', 'a.', 'Fallen, as to physical or social condition; affected with\n   decay; rotten; as, decayed vegetation or vegetables; a decayed fortune\n   or gentleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decayer', 'n.', 'A causer of decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decease', 'n.', 'Departure, especially departure from this life; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceased', 'imp. & p. p.', 'of Decease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceasing', 'p. pr. & vb. n.', 'of Decease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decease', 'v. i.', 'To depart from this life; to die; to pass away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceased', 'a.', 'Passed away; dead; gone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decede', 'n.', 'To withdraw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decedent', 'a.', 'Removing; departing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decedent', 'n.', 'A deceased person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceit', 'n.', 'An attempt or disposition to deceive or lead into error;\n   any declaration, artifice, or practice, which misleads another, or\n   causes him to believe what is false; a contrivance to entrap;\n   deception; a wily device; fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceit', 'n.', 'Any trick, collusion, contrivance, false representation, or\n   underhand practice, used to defraud another. When injury is thereby\n   effected, an action of deceit, as it called, lies for compensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceitful', 'a.', 'Full of, or characterized by, deceit; serving to mislead\n   or insnare; trickish; fraudulent; cheating; insincere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceitfully', 'adv.', 'With intent to deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceitfulness', 'n.', 'The disposition to deceive; as, a mans\n   deceitfulness may be habitual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceitfulness', 'n.', 'The quality of being deceitful; as, the\n   deceitfulness of a mans practices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceitfulness', 'n.', 'Tendency to mislead or deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceitless', 'a.', 'Free from deceit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceivable', 'a.', 'Fitted to deceive; deceitful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceivable', 'a.', 'Subject to deceit; capable of being misled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceivableness', 'n.', 'Capability of deceiving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceivableness', 'n.', 'Liability to be deceived or misled; as, the\n   deceivableness of a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceivably', 'adv.', 'In a deceivable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceived', 'imp. & p. p.', 'of Deceive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceiving', 'p. pr. & vb. n.', 'of Deceive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceive', 'v. t.', 'To lead into error; to cause to believe what is false,\n   or disbelieve what is true; to impose upon; to mislead; to cheat; to\n   disappoint; to delude; to insnare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceive', 'v. t.', 'To beguile; to amuse, so as to divert the attention; to\n   while away; to take away as if by deception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceive', 'v. t.', 'To deprive by fraud or stealth; to defraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceiver', 'n.', 'One who deceives; one who leads into error; a cheat; an\n   impostor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'December', 'n.', 'The twelfth and last month of the year, containing\n   thirty-one days. During this month occurs the winter solstice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'December', 'n.', 'Fig.: With reference to the end of the year and to the\n   winter season; as, the December of his life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemdentate', 'a.', 'Having ten points or teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemfid', 'a.', 'Cleft into ten parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemlocular', 'a.', 'Having ten cells for seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decempedal', 'a.', 'Ten feet in length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decempedal', 'a.', 'Having ten feet; decapodal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemvirs', 'pl. ', 'of Decemvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemviri', 'pl. ', 'of Decemvir');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemvir', 'n.', 'One of a body of ten magistrates in ancient Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemvir', 'n.', 'A member of any body of ten men in authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemviral', 'a.', 'Pertaining to the decemvirs in Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemvirate', 'n.', 'The office or term of office of the decemvirs in Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemvirate', 'n.', 'A body of ten men in authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decemvirship', 'n.', 'The office of a decemvir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decence', 'n.', 'Decency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decencies', 'pl. ', 'of Decency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decency', 'n.', 'The quality or state of being decent, suitable, or\n   becoming, in words or behavior; propriety of form in social\n   intercourse, in actions, or in discourse; proper formality; becoming\n   ceremony; seemliness; hence, freedom from obscenity or indecorum;\n   modesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decency', 'n.', 'That which is proper or becoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decene', 'n.', 'One of the higher hydrocarbons, C10H20, of the ethylene\n   series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decennaries', 'pl. ', 'of Decennary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decennary', 'n.', 'A period of ten years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decennary', 'n.', 'A tithing consisting of ten neighboring families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decennial', 'a.', 'Consisting of ten years; happening every ten years; as,\n   a decennial period; decennial games.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decennial', 'n.', 'A tenth year or tenth anniversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decenniums', 'pl. ', 'of Decennium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decennia', 'pl. ', 'of Decennium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decennium', 'n.', 'A period of ten years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decennoval', 'a.', 'Alt. of Decennovary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decennovary', 'a.', 'Pertaining to the number nineteen; of nineteen years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decent', 'a.', 'Suitable in words, behavior, dress, or ceremony; becoming;\n   fit; decorous; proper; seemly; as, decent conduct; decent language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decent', 'a.', 'Free from immodesty or obscenity; modest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decent', 'a.', 'Comely; shapely; well-formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decent', 'a.', 'Moderate, but competent; sufficient; hence, respectable;\n   fairly good; reasonably comfortable or satisfying; as, a decent\n   fortune; a decent person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decentralization', 'n.', 'The action of decentralizing, or the state of\n   being decentralized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decentralize', 'v. t.', 'To prevent from centralizing; to cause to withdraw\n   from the center or place of concentration; to divide and distribute\n   (what has been united or concentrated); -- esp. said of authority, or\n   the administration of public affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceptible', 'a.', 'Capable of being deceived; deceivable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deception', 'n.', 'The act of deceiving or misleading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deception', 'n.', 'The state of being deceived or misled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deception', 'n.', 'That which deceives or is intended to deceive; false\n   representation; artifice; cheat; fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceptious', 'a.', 'Tending deceive; delusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceptive', 'a.', 'Tending to deceive; having power to mislead, or impress\n   with false opinions; as, a deceptive countenance or appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceptively', 'adv.', 'In a manner to deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceptiveness', 'n.', 'The power or habit of deceiving; tendency or aptness\n   to deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceptivity', 'n.', 'Deceptiveness; a deception; a sham.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deceptory', 'a.', 'Deceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decern', 'v. t.', 'To perceive, discern, or decide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decern', 'v. t.', 'To decree; to adjudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decerniture', 'n.', 'A decree or sentence of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decerp', 'v. t.', 'To pluck off; to crop; to gather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decerpt', 'a.', 'Plucked off or away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decerptible', 'a.', 'That may be plucked off, cropped, or torn away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decerption', 'n.', 'The act of plucking off; a cropping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decerption', 'n.', 'That which is plucked off or rent away; a fragment; a\n   piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decertation', 'n.', 'Contest for mastery; contention; strife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decession', 'n.', 'Departure; decrease; -- opposed to accesion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decharm', 'v. t.', 'To free from a charm; to disenchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dechristianized', 'imp. & p. p.', 'of Dechristianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dechristianizing', 'p. pr. & vb. n.', 'of Dechristianize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dechristianize', 'v. t.', 'To turn from, or divest of, Christianity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decidable', 'a.', 'Capable of being decided; determinable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decided', 'imp. & p. p.', 'of Decide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deciding', 'p. pr. & vb. n.', 'of Decide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decide', 'v. t.', 'To cut off; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decide', 'v. t.', 'To bring to a termination, as a question, controversy,\n   struggle, by giving the victory to one side or party; to render\n   judgment concerning; to determine; to settle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decide', 'v. i.', 'To determine; to form a definite opinion; to come to a\n   conclusion; to give decision; as, the court decided in favor of the\n   defendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decided', 'a.', 'Free from ambiguity; unequivocal; unmistakable;\n   unquestionable; clear; evident; as, a decided advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decided', 'a.', 'Free from doubt or wavering; determined; of fixed purpose;\n   fully settled; positive; resolute; as, a decided opinion or purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decidedly', 'adv.', 'In a decided manner; indisputably; clearly;\n   thoroughly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decidement', 'n.', 'Means of forming a decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decidence', 'n.', 'A falling off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decider', 'n.', 'One who decides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decidua', 'n.', 'The inner layer of the wall of the uterus, which envelops\n   the embryo, forms a part of the placenta, and is discharged with it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deciduata', 'n. pl.', 'A group of Mammalia in which a decidua is thrown off\n   with, or after, the fetus, as in the human species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deciduate', 'a.', 'Possessed of, or characterized by, a decidua.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deciduity', 'n.', 'Deciduousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deciduous', 'a.', 'Falling off, or subject to fall or be shed, at a certain\n   season, or a certain stage or interval of growth, as leaves (except of\n   evergreens) in autumn, or as parts of animals, such as hair, teeth,\n   antlers, etc.; also, shedding leaves or parts at certain seasons,\n   stages, or intervals; as, deciduous trees; the deciduous membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deciduousness', 'n.', 'The quality or state of being deciduous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decigram', 'n.', 'Alt. of Decigramme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decigramme', 'n.', 'A weight in the metric system; one tenth of a gram,\n   equal to 1.5432 grains avoirdupois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decil', 'n.', 'Alt. of Decile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decile', 'n.', 'An aspect or position of two planets, when they are distant\n   from each other a tenth part of the zodiac, or 36.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deciliter', 'n.', 'Alt. of Decilitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decilitre', 'n.', 'A measure of capacity or volume in the metric system;\n   one tenth of a liter, equal to 6.1022 cubic inches, or 3.38 fluid\n   ounces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decillion', 'n.', 'According to the English notation, a million involved to\n   the tenth power, or a unit with sixty ciphers annexed; according to the\n   French and American notation, a thousand involved to the eleventh\n   power, or a unit with thirty-three ciphers annexed. [See the Note under\n   Numeration.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decillionth', 'a.', 'Pertaining to a decillion, or to the quotient of unity\n   divided by a decillion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decillionth', 'n.', 'The quotient of unity divided by a decillion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decillionth', 'n.', 'One of a decillion equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimal', 'a.', 'Of or pertaining to decimals; numbered or proceeding by\n   tens; having a tenfold increase or decrease, each unit being ten times\n   the unit next smaller; as, decimal notation; a decimal coinage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimal', 'n.', 'A number expressed in the scale of tens; specifically, and\n   almost exclusively, used as synonymous with a decimal fraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimalism', 'n.', 'The system of a decimal currency, decimal weights,\n   measures, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimalize', 'v. t.', 'To reduce to a decimal system; as, to decimalize the\n   currency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimally', 'adv.', 'By tens; by means of decimals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimated', 'imp. & p. p.', 'of Decimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimating', 'p. pr. & vb. n.', 'of Decimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimate', 'v. t.', 'To take the tenth part of; to tithe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimate', 'v. t.', 'To select by lot and punish with death every tenth man\n   of; as, to decimate a regiment as a punishment for mutiny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimate', 'v. t.', 'To destroy a considerable part of; as, to decimate an\n   army in battle; to decimate a people by disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimation', 'n.', 'A tithing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimation', 'n.', 'A selection of every tenth person by lot, as for\n   punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimation', 'n.', 'The destruction of any large proportion, as of people\n   by pestilence or war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimator', 'n.', 'One who decimates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decime', 'n.', 'A French coin, the tenth part of a franc, equal to about\n   two cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimeter', 'n.', 'Alt. of Decimetre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimetre', 'n.', 'A measure of length in the metric system; one tenth of a\n   meter, equal to 3.937 inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimosexto', 'n.', 'A book consisting of sheets, each of which is folded\n   into sixteen leaves; hence, indicating, more or less definitely, a size\n   of book; -- usually written 16mo or 16.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decimosexto', 'a.', 'Having sixteen leaves to a sheet; as, a decimosexto\n   form, book, leaf, size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decine', 'n.', 'One of the higher hydrocarbons, C10H15, of the acetylene\n   series; -- called also decenylene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deciphered', 'imp. & p. p.', 'of Decipher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deciphering', 'p. pr. & vb. n.', 'of Decipher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decipher', 'v. t.', 'To translate from secret characters or ciphers into\n   intelligible terms; as, to decipher a letter written in secret\n   characters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decipher', 'v. t.', 'To find out, so as to be able to make known the\n   meaning of; to make out or read, as words badly written or partly\n   obliterated; to detect; to reveal; to unfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decipher', 'v. t.', 'To stamp; to detect; to discover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decipherable', 'a.', 'Capable of being deciphered; as, old writings not\n   decipherable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decipherer', 'n.', 'One who deciphers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decipheress', 'n.', 'A woman who deciphers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decipherment', 'n.', 'The act of deciphering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decipiency', 'n.', 'State of being deceived; hallucination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decipium', 'n.', 'A supposed rare element, said to be associated with\n   cerium, yttrium, etc., in the mineral samarskite, and more recently\n   called samarium. Symbol Dp. See Samarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decision', 'n.', 'Cutting off; division; detachment of a part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decision', 'n.', 'The act of deciding; act of settling or terminating, as a\n   controversy, by giving judgment on the matter at issue; determination,\n   as of a question or doubt; settlement; conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decision', 'n.', 'An account or report of a conclusion, especially of a\n   legal adjudication or judicial determination of a question or cause;\n   as, a decision of arbitrators; a decision of the Supreme Court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decision', 'n.', 'The quality of being decided; prompt and fixed\n   determination; unwavering firmness; as, to manifest great decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decisive', 'a.', 'Having the power or quality of deciding a question or\n   controversy; putting an end to contest or controversy; final;\n   conclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decisive', 'a.', 'Marked by promptness and decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decisory', 'a.', 'Able to decide or determine; having a tendency to decide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decistere', 'n.', 'The tenth part of the stere or cubic meter, equal to\n   3.531 cubic feet. See Stere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decitizenize', 'v. t.', 'To deprive of the rights of citizenship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decivilize', 'v. t.', 'To reduce from civilization to a savage state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decked', 'imp. & p. p.', 'of Deck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decking', 'p. pr. & vb. n.', 'of Deck');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deck', 'v. t.', 'To cover; to overspread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deck', 'v. t.', 'To dress, as the person; to clothe; especially, to clothe\n   with more than ordinary elegance; to array; to adorn; to embellish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deck', 'v. t.', 'To furnish with a deck, as a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deck', 'v.', 'The floorlike covering of the horizontal sections, or\n   compartments, of a ship. Small vessels have only one deck; larger ships\n   have two or three decks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deck', 'v.', 'The upper part or top of a mansard roof or curb roof when\n   made nearly flat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deck', 'v.', 'The roof of a passenger car.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deck', 'v.', 'A pack or set of playing cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deck', 'v.', 'A heap or store.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deckel', 'n.', 'Same as Deckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decker', 'n.', 'One who, or that which, decks or adorns; a coverer; as, a\n   table decker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decker', 'n.', 'A vessel which has a deck or decks; -- used esp. in\n   composition; as, a single-decker; a three-decker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deckle', 'n.', 'A separate thin wooden frame used to form the border of a\n   hand mold, or a curb of India rubber or other material which rests on,\n   and forms the edge of, the mold in a paper machine and determines the\n   width of the paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaimed', 'imp. & p. p.', 'of Declaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaiming', 'p. pr. & vb. n.', 'of Declaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaim', 'v. i.', 'To speak rhetorically; to make a formal speech or\n   oration; to harangue; specifically, to recite a speech, poem, etc., in\n   public as a rhetorical exercise; to practice public speaking; as, the\n   students declaim twice a week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaim', 'v. i.', 'To speak for rhetorical display; to speak pompously,\n   noisily, or theatrically; to make an empty speech; to rehearse trite\n   arguments in debate; to rant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaim', 'v. t.', 'To utter in public; to deliver in a rhetorical or set\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaim', 'v. t.', 'To defend by declamation; to advocate loudly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaimant', 'n.', 'A declaimer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaimer', 'n.', 'One who declaims; an haranguer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declamation', 'n.', 'The act or art of declaiming; rhetorical delivery;\n   haranguing; loud speaking in public; especially, the public recitation\n   of speeches as an exercise in schools and colleges; as, the practice\n   declamation by students.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declamation', 'n.', 'A set or harangue; declamatory discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declamation', 'n.', 'Pretentious rhetorical display, with more sound than\n   sense; as, mere declamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declamator', 'n.', 'A declaimer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declamatory', 'a.', 'Pertaining to declamation; treated in the manner of a\n   rhetorician; as, a declamatory theme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declamatory', 'a.', 'Characterized by rhetorical display; pretentiously\n   rhetorical; without solid sense or argument; bombastic; noisy; as, a\n   declamatory way or style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declarable', 'a.', 'Capable of being declared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declarant', 'n.', 'One who declares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaration', 'n.', 'The act of declaring, or publicly announcing; explicit\n   asserting; undisguised token of a ground or side taken on any subject;\n   proclamation; exposition; as, the declaration of an opinion; a\n   declaration of war, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaration', 'n.', 'That which is declared or proclaimed; announcement;\n   distinct statement; formal expression; avowal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaration', 'n.', 'The document or instrument containing such statement\n   or proclamation; as, the Declaration of Independence (now preserved in\n   Washington).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaration', 'n.', 'That part of the process in which the plaintiff sets\n   forth in order and at large his cause of complaint; the narration of\n   the plaintiffs case containing the count, or counts. See Count, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declarative', 'a.', 'Making declaration, proclamation, or publication;\n   explanatory; assertive; declaratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaratively', 'adv.', 'By distinct assertion; not impliedly; in the form\n   of a declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declarator', 'n.', 'A form of action by which some right or interest is\n   sought to be judicially declared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaratorily', 'adv.', 'In a declaratory manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaratory', 'a.', 'Making declaration, explanation, or exhibition; making\n   clear or manifest; affirmative; expressive; as, a clause declaratory of\n   the will of the legislature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declared', 'imp. & p. p.', 'of Declare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaring', 'p. pr. & vb. n.', 'of Declare');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declare', 'v. t.', 'To make clear; to free from obscurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declare', 'v. t.', 'To make known by language; to communicate or manifest\n   explicitly and plainly in any way; to exhibit; to publish; to proclaim;\n   to announce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declare', 'v. t.', 'To make declaration of; to assert; to affirm; to set\n   forth; to avow; as, he declares the story to be false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declare', 'v. t.', 'To make full statement of, as goods, etc., for the\n   purpose of paying taxes, duties, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declare', 'v. i.', 'To make a declaration, or an open and explicit avowal;\n   to proclaim ones self; -- often with for or against; as, victory\n   declares against the allies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declare', 'v. i.', 'To state the plaintiffs cause of action at law in a\n   legal form; as, the plaintiff declares in trespass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaredly', 'adv.', 'Avowedly; explicitly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declaredness', 'n.', 'The state of being declared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declarement', 'n.', 'Declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declarer', 'n.', 'One who makes known or proclaims; that which exhibits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declension', 'n.', 'The act or the state of declining; declination;\n   descent; slope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declension', 'n.', 'A falling off towards a worse state; a downward\n   tendency; deterioration; decay; as, the declension of virtue, of\n   science, of a state, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declension', 'n.', 'Act of courteously refusing; act of declining; a\n   declinature; refusal; as, the declension of a nomination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declension', 'n.', 'Inflection of nouns, adjectives, etc., according to the\n   grammatical cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declension', 'n.', 'The form of the inflection of a word declined by cases;\n   as, the first or the second declension of nouns, adjectives, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declension', 'n.', 'Rehearsing a word as declined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declensional', 'a.', 'Belonging to declension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declinable', 'a.', 'Capable of being declined; admitting of declension or\n   inflection; as, declinable parts of speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declinal', 'a.', 'Declining; sloping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declinate', 'a.', 'Bent downward or aside; (Bot.) bending downward in a\n   curve; declined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declination', 'n.', 'The act or state of bending downward; inclination; as,\n   declination of the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declination', 'n.', 'The act or state of falling off or declining from\n   excellence or perfection; deterioration; decay; decline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declination', 'n.', 'The act of deviating or turning aside; oblique motion;\n   obliquity; withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declination', 'n.', 'The act or state of declining or refusing; withdrawal;\n   refusal; averseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declination', 'n.', 'The angular distance of any object from the celestial\n   equator, either northward or southward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declination', 'n.', 'The arc of the horizon, contained between the vertical\n   plane and the prime vertical circle, if reckoned from the east or west,\n   or between the meridian and the plane, reckoned from the north or\n   south.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declination', 'n.', 'The act of inflecting a word; declension. See Decline,\n   v. t., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declinator', 'n.', 'An instrument for taking the declination or angle which\n   a plane makes with the horizontal plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declinator', 'n.', 'A dissentient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declinatory', 'a.', 'Containing or involving a declination or refusal, as\n   of submission to a charge or sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declinature', 'n.', 'The act of declining or refusing; as, the declinature\n   of an office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declined', 'imp. & p. p.', 'of Decline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declining', 'p. pr. & vb. n.', 'of Decline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. i.', 'To bend, or lean downward; to take a downward\n   direction; to bend over or hang down, as from weakness, weariness,\n   despondency, etc.; to condescend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. i.', 'To tend or draw towards a close, decay, or extinction;\n   to tend to a less perfect state; to become diminished or impaired; to\n   fail; to sink; to diminish; to lessen; as, the day declines; virtue\n   declines; religion declines; business declines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. i.', 'To turn or bend aside; to deviate; to stray; to\n   withdraw; as, a line that declines from straightness; conduct that\n   declines from sound morals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. i.', 'To turn away; to shun; to refuse; -- the opposite of\n   accept or consent; as, he declined, upon principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. t.', 'To bend downward; to bring down; to depress; to cause\n   to bend, or fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. t.', 'To cause to decrease or diminish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. t.', 'To put or turn aside; to turn off or away from; to\n   refuse to undertake or comply with; reject; to shun; to avoid; as, to\n   decline an offer; to decline a contest; he declined any participation\n   with them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. t.', 'To inflect, or rehearse in order the changes of\n   grammatical form of; as, to decline a noun or an adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. t.', 'To run through from first to last; to repeat like a\n   schoolboy declining a noun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. i.', 'A falling off; a tendency to a worse state; diminution\n   or decay; deterioration; also, the period when a thing is tending\n   toward extinction or a less perfect state; as, the decline of life; the\n   decline of strength; the decline of virtue and religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. i.', 'That period of a disorder or paroxysm when the symptoms\n   begin to abate in violence; as, the decline of a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decline', 'v. i.', 'A gradual sinking and wasting away of the physical\n   faculties; any wasting disease, esp. pulmonary consumption; as, to die\n   of a decline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declined', 'a.', 'Declinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decliner', 'n.', 'He who declines or rejects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declinometer', 'n.', 'An instrument for measuring the declination of the\n   magnetic needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declinous', 'a.', 'Declinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declivitous', 'a.', 'Alt. of Declivous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declivous', 'a.', 'Descending gradually; moderately steep; sloping;\n   downhill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declivities', 'pl. ', 'of Declivity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declivity', 'n.', 'Deviation from a horizontal line; gradual descent of\n   surface; inclination downward; slope; -- opposed to acclivity, or\n   ascent; the same slope, considered as descending, being a declivity,\n   which, considered as ascending, is an acclivity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Declivity', 'n.', 'A descending surface; a sloping place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decocted', 'imp. & p. p.', 'of Decoct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decocting', 'p. pr. & vb. n.', 'of Decoct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoct', 'v. t.', 'To prepare by boiling; to digest in hot or boiling\n   water; to extract the strength or flavor of by boiling; to make an\n   infusion of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoct', 'v. t.', 'To prepare by the heat of the stomach for assimilation;\n   to digest; to concoct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoct', 'v. t.', 'To warm, strengthen, or invigorate, as if by boiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoctible', 'a.', 'Capable of being boiled or digested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoction', 'n.', 'The act or process of boiling anything in a watery fluid\n   to extract its virtues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoction', 'n.', 'An extract got from a body by boiling it in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decocture', 'n.', 'A decoction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decollated', 'imp. & p. p.', 'of Decollate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decollating', 'p. pr. & vb. n.', 'of Decollate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decollate', 'v. t.', 'To sever from the neck; to behead; to decapitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decollated', 'a.', 'Decapitated; worn or cast off in the process of growth,\n   as the apex of certain univalve shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decollation', 'n.', 'The act of beheading or state of one beheaded; --\n   especially used of the execution of St. John the Baptist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decollation', 'n.', 'A painting representing the beheading of a saint or\n   martyr, esp. of St. John the Baptist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decollete', 'a.', 'Leaving the neck and shoulders uncovered; cut low in the\n   neck, or low-necked, as a dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decolling', 'n.', 'Beheading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decolor', 'v. t.', 'To deprive of color; to bleach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decolorant', 'n.', 'A substance which removes color, or bleaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decolorate', 'a.', 'Deprived of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decolorate', 'v. t.', 'To decolor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoloration', 'n.', 'The removal or absence of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decolorize', 'v. t.', 'To deprive of color; to whiten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomplex', 'a.', 'Repeatedly compound; made up of complex constituents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomposable', 'a.', 'Capable of being resolved into constituent elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomposed', 'imp. & p. p.', 'of Decompose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomposing', 'p. pr. & vb. n.', 'of Decompose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decompose', 'v. t.', 'To separate the constituent parts of; to resolve into\n   original elements; to set free from previously existing forms of\n   chemical combination; to bring to dissolution; to rot or decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decompose', 'v. i.', 'To become resolved or returned from existing\n   combinations; to undergo dissolution; to decay; to rot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomposed', 'a.', 'Separated or broken up; -- said of the crest of birds\n   when the feathers are divergent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomposite', 'a.', 'Compounded more than once; compounded with things\n   already composite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomposite', 'a.', 'See Decompound, a., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomposite', 'n.', 'Anything decompounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomposition', 'n.', 'The act or process of resolving the constituent\n   parts of a compound body or substance into its elementary parts;\n   separation into constituent part; analysis; the decay or dissolution\n   consequent on the removal or alteration of some of the ingredients of a\n   compound; disintegration; as, the decomposition of wood, rocks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomposition', 'n.', 'The state of being reduced into original elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decomposition', 'n.', 'Repeated composition; a combination of compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decompounded', 'imp. & p. p.', 'of Decompound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decompounding', 'p. pr. & vb. n.', 'of Decompound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decompound', 'v. t.', 'To compound or mix with that is already compound; to\n   compound a second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decompound', 'v. t.', 'To reduce to constituent parts; to decompose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decompound', 'a.', 'Compound of what is already compounded; compounded a\n   second time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decompound', 'a.', 'Several times compounded or divided, as a leaf or stem;\n   decomposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decompound', 'n.', 'A decomposite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decompoundable', 'a.', 'Capable of being decompounded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deconcentrate', 'v. t.', 'To withdraw from concentration; to decentralize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deconcentration', 'n.', 'Act of deconcentrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deconcoct', 'v. t.', 'To decompose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deconsecrate', 'v. t.', 'To deprive of sacredness; to secularize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorament', 'v. t.', 'Ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorated', 'imp. & p. p.', 'of Decorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorating', 'p. pr. & vb. n.', 'of Decorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorate', 'v. t.', 'To deck with that which is becoming, ornamental, or\n   honorary; to adorn; to beautify; to embellish; as, to decorate the\n   person; to decorate an edifice; to decorate a lawn with flowers; to\n   decorate the mind with moral beauties; to decorate a hero with honors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoration', 'n.', 'The act of adorning, embellishing, or honoring;\n   ornamentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoration', 'n.', 'That which adorns, enriches, or beautifies; something\n   added by way of embellishment; ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoration', 'n.', 'Specifically, any mark of honor to be worn upon the\n   person, as a medal, cross, or ribbon of an order of knighthood,\n   bestowed for services in war, great achievements in literature, art,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorative', 'a.', 'Suited to decorate or embellish; adorning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorator', 'n.', 'One who decorates, adorns, or embellishes; specifically,\n   an artisan whose business is the decoration of houses, esp. their\n   interior decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decore', 'v. t.', 'To decorate; to beautify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorement', 'n.', 'Ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorous', 'a.', 'Suitable to a character, or to the time, place, and\n   occasion; marked with decorum; becoming; proper; seemly; befitting; as,\n   a decorous speech; decorous behavior; a decorous dress for a judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorticated', 'imp. & p. p.', 'of Decorticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorticating', 'p. pr. & vb. n.', 'of Decorticate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorticate', 'v. t.', 'To divest of the bark, husk, or exterior coating;\n   to husk; to peel; to hull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decortication', 'n.', 'The act of stripping off the bark, rind, hull, or\n   outer coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorticator', 'n.', 'A machine for decorticating wood, hulling grain,\n   etc.; also, an instrument for removing surplus bark or moss from fruit\n   trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decorum', 'n.', 'Propriety of manner or conduct; grace arising from\n   suitableness of speech and behavior to ones own character, or to the\n   place and occasion; decency of conduct; seemliness; that which is\n   seemly or suitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoyed', 'imp. & p. p.', 'of Decoy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoying', 'p. pr. & vb. n.', 'of Decoy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoy', 'v. t.', 'To lead into danger by artifice; to lure into a net or\n   snare; to entrap; to insnare; to allure; to entice; as, to decoy troops\n   into an ambush; to decoy ducks into a net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoy', 'n.', 'Anything intended to lead into a snare; a lure that deceives\n   and misleads into danger, or into the power of an enemy; a bait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoy', 'n.', 'A fowl, or the likeness of one, used by sportsmen to entice\n   other fowl into a net or within shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoy', 'n.', 'A place into which wild fowl, esp. ducks, are enticed in\n   order to take or shoot them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoy', 'n.', 'A person employed by officers of justice, or parties exposed\n   to injury, to induce a suspected person to commit an offense under\n   circumstances that will lead to his detection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoy-duck', 'n.', 'A duck used to lure wild ducks into a decoy; hence, a\n   person employed to lure others into danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoyer', 'n.', 'One who decoys another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoy-men', 'pl. ', 'of Decoy-man');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decoy-man', 'n.', 'A man employed in decoying wild fowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decreased', 'imp. & p. p.', 'of Decrease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decreasing', 'p. pr. & vb. n.', 'of Decrease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrease', 'n.', 'To grow less, -- opposed to increase; to be diminished\n   gradually, in size, degree, number, duration, etc., or in strength,\n   quality, or excellence; as, they days decrease in length from June to\n   December.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrease', 'v. t.', 'To cause to grow less; to diminish gradually; as,\n   extravagance decreases ones means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrease', 'v.', 'A becoming less; gradual diminution; decay; as, a\n   decrease of revenue or of strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrease', 'v.', 'The wane of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decreaseless', 'a.', 'Suffering no decrease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decreasing', 'a.', 'Becoming less and less; diminishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decreation', 'n.', 'Destruction; -- opposed to creation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decree', 'n.', 'An order from one having authority, deciding what is to be\n   done by a subordinate; also, a determination by one having power,\n   deciding what is to be done or to take place; edict, law; authoritative\n   ru// decision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decree', 'n.', 'A decision, order, or sentence, given in a cause by a court\n   of equity or admiralty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decree', 'n.', 'A determination or judgment of an umpire on a case\n   submitted to him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decree', 'n.', 'An edict or law made by a council for regulating any\n   business within their jurisdiction; as, the decrees of ecclesiastical\n   councils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decreed', 'imp. & p. p.', 'of Decree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decreeing', 'p. pr. & vb. n.', 'of Decree');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decree', 'v. t.', 'To determine judicially by authority, or by decree; to\n   constitute by edict; to appoint by decree or law; to determine; to\n   order; to ordain; as, a court decrees a restoration of property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decree', 'v. t.', 'To ordain by fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decree', 'v. i.', 'To make decrees; -- used absolutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decreeable', 'a.', 'Capable of being decreed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decreer', 'n.', 'One who decrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decreet', 'n.', 'The final judgment of the Court of Session, or of an\n   inferior court, by which the question at issue is decided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrement', 'n.', 'The state of becoming gradually less; decrease;\n   diminution; waste; loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrement', 'n.', 'The quantity lost by gradual diminution or waste; --\n   opposed to increment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrement', 'n.', 'A name given by Hauy to the successive diminution of the\n   layers of molecules, applied to the faces of the primitive form, by\n   which he supposed the secondary forms to be produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrement', 'n.', 'The quantity by which a variable is diminished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrepit', 'a.', 'Broken down with age; wasted and enfeebled by the\n   infirmities of old age; feeble; worn out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrepitated', 'imp. & p. p.', 'of Decrepitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrepitating', 'p. pr. & vb. n.', 'of Decrepitate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrepitate', 'v. t.', 'To roast or calcine so as to cause a crackling\n   noise; as, to decrepitate salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrepitate', 'v. i.', 'To crackle, as salt in roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrepitation', 'n.', 'The act of decrepitating; a crackling noise, such as\n   salt makes when roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrepitness', 'n.', 'Decrepitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrepitude', 'n.', 'The broken state produced by decay and the infirmities\n   of age; infirm old age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrescendo', 'a. & adv.', 'With decreasing volume of sound; -- a direction\n   to performers, either written upon the staff (abbreviated Dec., or\n   Decresc.), or indicated by the sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrescent', 'a.', 'Becoming less by gradual diminution; decreasing; as, a\n   decrescent moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrescent', 'n.', 'A crescent with the horns directed towards the\n   sinister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decretal', 'a.', 'Appertaining to a decree; containing a decree; as, a\n   decretal epistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decretal', 'a.', 'An authoritative order or decree; especially, a letter of\n   the pope, determining some point or question in ecclesiastical law. The\n   decretals form the second part of the canon law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decretal', 'a.', 'The collection of ecclesiastical decrees and decisions\n   made, by order of Gregory IX., in 1234, by St. Raymond of Pennafort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrete', 'n.', 'A decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decretion', 'n.', 'A decrease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decretist', 'n.', 'One who studies, or professes the knowledge of, the\n   decretals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decretive', 'n.', 'Having the force of a decree; determining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decretorial', 'a.', 'Decretory; authoritative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decretorily', 'adv.', 'In a decretory or definitive manner; by decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decretory', 'a.', 'Established by a decree; definitive; settled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decretory', 'a.', 'Serving to determine; critical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrew', 'v. i.', 'To decrease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrial', 'n.', 'A crying down; a clamorous censure; condemnation by\n   censure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrier', 'n.', 'One who decries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrown', 'v. t.', 'To deprive of a crown; to discrown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrustation', 'n.', 'The removal of a crust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decried', 'imp. & p. p.', 'of Decry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decrying', 'p. pr. & vb. n.', 'of Decry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decry', 'v. t.', 'To cry down; to censure as faulty, mean, or worthless; to\n   clamor against; to blame clamorously; to discredit; to disparage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decubation', 'n.', 'Act of lying down; decumbence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decubitus', 'n.', 'An attitude assumed in lying down; as, the dorsal\n   decubitus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decuman', 'a.', 'Large; chief; -- applied to an extraordinary billow,\n   supposed by some to be every tenth in order. [R.] Also used\n   substantively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decumbence', 'n.', 'Alt. of Decumbency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decumbency', 'n.', 'The act or posture of lying down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decumbent', 'a.', 'Lying down; prostrate; recumbent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decumbent', 'a.', 'Reclining on the ground, as if too weak to stand, and\n   tending to rise at the summit or apex; as, a decumbent stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decumbently', 'adv.', 'In a decumbent posture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decumbiture', 'n.', 'Confinement to a sick bed, or time of taking to ones\n   bed from sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decumbiture', 'n.', 'Aspect of the heavens at the time of taking to ones\n   sick bed, by which the prognostics of recovery or death were made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decuple', 'a.', 'Tenfold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decuple', 'n.', 'A number ten times repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decupled', 'imp. & p. p.', 'of Decuple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decupling', 'p. pr. & vb. n.', 'of Decuple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decuple', 'v. t.', 'To make tenfold; to multiply by ten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decurion', 'n.', 'A head or chief over ten; especially, an officer who\n   commanded a division of ten soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decurionate', 'n.', 'The office of a decurion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decurrence', 'n.', 'The act of running down; a lapse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decurrent', 'a.', 'Extending downward; -- said of a leaf whose base extends\n   downward and forms a wing along the stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decursion', 'n.', 'A flowing; also, a hostile incursion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decursive', 'a.', 'Running down; decurrent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decursively', 'adv.', 'In a decursive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decurt', 'v. t.', 'To cut short; to curtail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decurtation', 'n.', 'Act of cutting short.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decuries', 'pl. ', 'of Decury');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decury', 'n.', 'A set or squad of ten men under a decurion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussated', 'imp. & p. p.', 'of Decussate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussating', 'p. pr. & vb. n.', 'of Decussate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussate', 'v. t.', 'To cross at an acute angle; to cut or divide in the\n   form of X; to intersect; -- said of lines in geometrical figures, rays\n   of light, nerves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussate', 'a.', 'Alt. of Decussated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussated', 'a.', 'Crossed; intersected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussated', 'a.', 'Growing in pairs, each of which is at right angles to\n   the next pair above or below; as, decussated leaves or branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussated', 'a.', 'Consisting of two rising and two falling clauses,\n   placed in alternate opposition to each other; as, a decussated period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussately', 'adv.', 'In a decussate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussation', 'n.', 'Act of crossing at an acute angle, or state of being\n   thus crossed; an intersection in the form of an X; as, the decussation\n   of lines, nerves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussative', 'a.', 'Intersecting at acute angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decussatively', 'adv.', 'Crosswise; in the form of an X.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decyl', 'n.', 'A hydrocarbon radical, C10H21, never existing alone, but\n   regarded as the characteristic constituent of a number of compounds of\n   the paraffin series.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Decylic', 'a.', 'Allied to, or containing, the radical decyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedalian', 'a.', 'See Daedalian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedalous', 'a.', 'See Daedalous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedans', 'n.', 'A division, at one end of a tennis court, for spectators.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dede', 'a.', 'Dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedecorate', 'v. t.', 'To bring to shame; to disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedecoration', 'n.', 'Disgrace; dishonor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedecorous', 'a.', 'Disgraceful; unbecoming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedentition', 'n.', 'The shedding of teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicate', 'p. a.', 'Dedicated; set apart; devoted; consecrated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicated', 'imp. & p. p.', 'of Dedicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicating', 'p. pr. & vb. n.', 'of Dedicate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicate', 'v. t.', 'To set apart and consecrate, as to a divinity, or for\n   sacred uses; to devote formally and solemnly; as, to dedicate vessels,\n   treasures, a temple, or a church, to a religious use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicate', 'v. t.', 'To devote, set apart, or give up, as ones self, to a\n   duty or service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicate', 'v. t.', 'To inscribe or address, as to a patron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicatee', 'n.', 'One to whom a thing is dedicated; -- correlative to\n   dedicator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedication', 'n.', 'The act of setting apart or consecrating to a divine\n   Being, or to a sacred use, often with religious solemnities; solemn\n   appropriation; as, the dedication of Solomons temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedication', 'n.', 'A devoting or setting aside for any particular purpose;\n   as, a dedication of lands to public use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedication', 'n.', 'An address to a patron or friend, prefixed to a book,\n   testifying respect, and often recommending the work to his special\n   protection and favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicator', 'n.', 'One who dedicates; more especially, one who inscribes a\n   book to the favor of a patron, or to one whom he desires to compliment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicatorial', 'a.', 'Dedicatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicatory', 'a.', 'Constituting or serving as a dedication; complimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedicatory', 'n.', 'Dedication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedimus', 'n.', 'A writ to commission private persons to do some act in\n   place of a judge, as to examine a witness, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedition', 'n.', 'The act of yielding; surrender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dedolent', 'a.', 'Feeling no compunction; apathetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduced', 'imp. & p. p.', 'of Deduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deducing', 'p. pr. & vb. n.', 'of Deduce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduce', 'v. t.', 'To lead forth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduce', 'v. t.', 'To take away; to deduct; to subtract; as, to deduce a\n   part from the whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduce', 'v. t.', 'To derive or draw; to derive by logical process; to\n   obtain or arrive at as the result of reasoning; to gather, as a truth\n   or opinion, from what precedes or from premises; to infer; -- with from\n   or out of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deducement', 'n.', 'Inference; deduction; thing deduced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deducibility', 'n.', 'Deducibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deducible', 'a.', 'Capable of being deduced or inferred; derivable by\n   reasoning, as a result or consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deducible', 'a.', 'Capable of being brought down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deducibleness', 'n.', 'The quality of being deducible; deducibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deducibly', 'adv.', 'By deduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deducive', 'a.', 'That deduces; inferential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deducted', 'imp. & p. p.', 'of Deduct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deducting', 'p. pr. & vb. n.', 'of Deduct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduct', 'v. t.', 'To lead forth or out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduct', 'v. t.', 'To take away, separate, or remove, in numbering,\n   estimating, or calculating; to subtract; -- often with from or out of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduct', 'v. t.', 'To reduce; to diminish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deductible', 'a.', 'Capable of being deducted, taken away, or withdrawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deductible', 'a.', 'Deducible; consequential.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduction', 'n.', 'Act or process of deducing or inferring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduction', 'n.', 'Act of deducting or taking away; subtraction; as, the\n   deduction of the subtrahend from the minuend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduction', 'n.', 'That which is deduced or drawn from premises by a\n   process of reasoning; an inference; a conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduction', 'n.', 'That which is deducted; the part taken away; abatement;\n   as, a deduction from the yearly rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deductive', 'a.', 'Of or pertaining to deduction; capable of being deduced\n   from premises; deducible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deductively', 'adv.', 'By deduction; by way of inference; by consequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deductor', 'n.', 'The pilot whale or blackfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduit', 'n.', 'Delight; pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deduplication', 'n.', 'The division of that which is morphologically one\n   organ into two or more, as the division of an organ of a plant into a\n   pair or cluster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deed', 'a.', 'Dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deed', 'v. t.', 'That which is done or effected by a responsible agent; an\n   act; an action; a thing done; -- a word of extensive application,\n   including, whatever is done, good or bad, great or small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deed', 'v. t.', 'Illustrious act; achievement; exploit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deed', 'v. t.', 'Power of action; agency; efficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deed', 'v. t.', 'Fact; reality; -- whence we have indeed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deed', 'v. t.', 'A sealed instrument in writing, on paper or parchment,\n   duly executed and delivered, containing some transfer, bargain, or\n   contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deed', 'v. t.', 'Performance; -- followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deed', 'v. t.', 'To convey or transfer by deed; as, he deeded all his\n   estate to his eldest son.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deedful', 'a.', 'Full of deeds or exploits; active; stirring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deedless', 'a.', 'Not performing, or not having performed, deeds or\n   exploits; inactive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deed poll', '', 'A deed of one part, or executed by only one party, and\n   distinguished from an indenture by having the edge of the parchment or\n   paper cut even, or polled as it was anciently termed, instead of being\n   indented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deedy', 'a.', 'Industrious; active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deemed', 'imp. & p. p.', 'of Deem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deeming', 'p. pr. & vb. n.', 'of Deem');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deem', 'v.', 'To decide; to judge; to sentence; to condemn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deem', 'v.', 'To account; to esteem; to think; to judge; to hold in\n   opinion; to regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deem', 'v. i.', 'To be of opinion; to think; to estimate; to opine; to\n   suppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deem', 'v. i.', 'To pass judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deem', 'n.', 'Opinion; judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deemster', 'n.', 'A judge in the Isle of Man who decides controversies\n   without process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'superl.', 'Extending far below the surface; of great perpendicular\n   dimension (measured from the surface downward, and distinguished from\n   high, which is measured upward); far to the bottom; having a certain\n   depth; as, a deep sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'superl.', 'Extending far back from the front or outer part; of\n   great horizontal dimension (measured backward from the front or nearer\n   part, mouth, etc.); as, a deep cave or recess or wound; a gallery ten\n   seats deep; a company of soldiers six files deep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'superl.', 'Low in situation; lying far below the general surface;\n   as, a deep valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'superl.', 'Hard to penetrate or comprehend; profound; -- opposed to\n   shallow or superficial; intricate; mysterious; not obvious; obscure;\n   as, a deep subject or plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'superl.', 'Of penetrating or far-reaching intellect; not\n   superficial; thoroughly skilled; sagacious; cunning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'superl.', 'Profound; thorough; complete; unmixed; intense; heavy;\n   heartfelt; as, deep distress; deep melancholy; deep horror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'superl.', 'Strongly colored; dark; intense; not light or thin; as,\n   deep blue or crimson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'superl.', 'Of low tone; full-toned; not high or sharp; grave;\n   heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'superl.', 'Muddy; boggy; sandy; -- said of roads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'adv.', 'To a great depth; with depth; far down; profoundly; deeply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'n.', 'That which is deep, especially deep water, as the sea or\n   ocean; an abyss; a great depth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep', 'n.', 'That which is profound, not easily fathomed, or\n   incomprehensible; a moral or spiritual depth or abyss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deepened', 'imp. & p. p.', 'of Deepen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deepening', 'p. pr. & vb. n.', 'of Deepen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deepen', 'v. t.', 'To make deep or deeper; to increase the depth of; to\n   sink lower; as, to deepen a well or a channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deepen', 'v. t.', 'To make darker or more intense; to darken; as, the event\n   deepened the prevailing gloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deepen', 'v. t.', 'To make more poignant or affecting; to increase in\n   degree; as, to deepen grief or sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deepen', 'v. t.', 'To make more grave or low in tone; as, to deepen the\n   tones of an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deepen', 'v. i.', 'To become deeper; as, the water deepens at every cast of\n   the lead; the plot deepens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep-fet', 'a.', 'Deeply fetched or drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep-laid', 'a.', 'Laid deeply; formed with cunning and sagacity; as,\n   deep-laid plans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deeply', 'adv.', 'At or to a great depth; far below the surface; as, to\n   sink deeply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deeply', 'adv.', 'Profoundly; thoroughly; not superficially; in a high\n   degree; intensely; as, deeply skilled in ethics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deeply', 'adv.', 'Very; with a tendency to darkness of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deeply', 'adv.', 'Gravely; with low or deep tone; as, a deeply toned\n   instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deeply', 'adv.', 'With profound skill; with art or intricacy; as, a deeply\n   laid plot or intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep-mouthed', 'a.', 'Having a loud and sonorous voice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deepness', 'n.', 'The state or quality of being deep, profound, mysterious,\n   secretive, etc.; depth; profundity; -- opposed to shallowness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deepness', 'n.', 'Craft; insidiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep-read', 'a.', 'Profoundly book- learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep-sea', 'a.', 'Of or pertaining to the deeper parts of the sea; as, a\n   deep-sea line (i. e., a line to take soundings at a great depth);\n   deep-sea lead; deep-sea soundings, explorations, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deep-waisted', 'a.', 'Having a deep waist, as when, in a ship, the poop and\n   forecastle are much elevated above the deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deer', 'n. sing. & pl.', 'Any animal; especially, a wild animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deer', 'n. sing. & pl.', 'A ruminant of the genus Cervus, of many species,\n   and of related genera of the family Cervidae. The males, and in some\n   species the females, have solid antlers, often much branched, which are\n   shed annually. Their flesh, for which they are hunted, is called\n   venison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deerberry', 'n.', 'A shrub of the blueberry group (Vaccinium stamineum);\n   also, its bitter, greenish white berry; -- called also squaw\n   huckleberry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deergrass', 'n.', 'An American genus (Rhexia) of perennial herbs, with\n   opposite leaves, and showy flowers (usually bright purple), with four\n   petals and eight stamens, -- the only genus of the order Melastomaceae\n   inhabiting a temperate clime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deerhound', 'n.', 'One of a large and fleet breed of hounds used in hunting\n   deer; a staghound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deerlet', 'n.', 'A chevrotain. See Kanchil, and Napu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deer-neck', 'n.', 'A deerlike, or thin, ill-formed neck, as of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deerskin', 'n.', 'The skin of a deer, or the leather which is made from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deerstalker', 'n.', 'One who practices deerstalking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deerstalking', 'n.', 'The hunting of deer on foot, by stealing upon them\n   unawares.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deers-tongue', 'n.', 'A plant (Liatris odoratissima) whose fleshy leaves\n   give out a fragrance compared to vanilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dees', 'n. pl.', 'Dice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dees', 'n.', 'A dais.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deesis', 'n.', 'An invocation of, or address to, the Supreme Being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deess', 'n.', 'A goddess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deev', 'n.', 'See Dev.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defaced', 'imp. & p. p.', 'of Deface');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defacing', 'p. pr. & vb. n.', 'of Deface');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deface', 'v. t.', 'To destroy or mar the face or external appearance of; to\n   disfigure; to injure, spoil, or mar, by effacing or obliterating\n   important features or portions of; as, to deface a monument; to deface\n   an edifice; to deface writing; to deface a note, deed, or bond; to\n   deface a record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deface', 'v. t.', 'To destroy; to make null.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defacement', 'n.', 'The act of defacing, or the condition of being defaced;\n   injury to the surface or exterior; obliteration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defacement', 'n.', 'That which mars or disfigures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defacer', 'n.', 'One who, or that which, defaces or disfigures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'De facto', '', 'Actually; in fact; in reality; as, a king de facto, --\n   distinguished from a king de jure, or by right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defail', 'v. t.', 'To cause to fail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defailance', 'n.', 'Failure; miscarriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defailure', 'n.', 'Failure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defalcated', 'imp. & p. p.', 'of Defalcate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defalcating', 'p. pr. & vb. n.', 'of Defalcate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defalcate', 'v. t.', 'To cut off; to take away or deduct a part of; -- used\n   chiefly of money, accounts, rents, income, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defalcate', 'v. i.', 'To commit defalcation; to embezzle money held in\n   trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defalcation', 'n.', 'A lopping off; a diminution; abatement; deficit.\n   Specifically: Reduction of a claim by deducting a counterclaim; set-\n   off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defalcation', 'n.', 'That which is lopped off, diminished, or abated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defalcation', 'n.', 'An abstraction of money, etc., by an officer or agent\n   having it in trust; an embezzlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defalcator', 'n.', 'A defaulter or embezzler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defalk', 'v. t.', 'To lop off; to abate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defamation', 'n.', 'Act of injuring anothers reputation by any slanderous\n   communication, written or oral; the wrong of maliciously injuring the\n   good name of another; slander; detraction; calumny; aspersion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defamatory', 'a.', 'Containing defamation; injurious to reputation;\n   calumnious; slanderous; as, defamatory words; defamatory writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defamed', 'imp. & p. p.', 'of Defame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defaming', 'p. pr. & vb. n.', 'of Defame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defame', 'v. t.', 'To harm or destroy the good fame or reputation of; to\n   disgrace; especially, to speak evil of maliciously; to dishonor by\n   slanderous reports; to calumniate; to asperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defame', 'v. t.', 'To render infamous; to bring into disrepute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defame', 'v. t.', 'To charge; to accuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defame', 'n.', 'Dishonor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defamer', 'n.', 'One who defames; a slanderer; a detractor; a calumniator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defamingly', 'adv.', 'In a defamatory manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defamous', 'a.', 'Defamatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defatigable', 'a.', 'Capable of being wearied or tired out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defatigate', 'v. t.', 'To weary or tire out; to fatigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defatigation', 'n.', 'Weariness; fatigue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Default', 'n.', 'A failing or failure; omission of that which ought to be\n   done; neglect to do what duty or law requires; as, this evil has\n   happened through the governors default.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Default', 'n.', 'Fault; offense; ill deed; wrong act; failure in virtue or\n   wisdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Default', 'n.', 'A neglect of, or failure to take, some step necessary to\n   secure the benefit of law, as a failure to appear in court at a day\n   assigned, especially of the defendant in a suit when called to make\n   answer; also of jurors, witnesses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defaulted', 'imp. & p. p.', 'of Default');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defaulting', 'p. pr. & vb. n.', 'of Default');
  INSERT INTO words (word, word_type, description) VALUES  (    'Default', 'v. i.', 'To fail in duty; to offend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Default', 'v. i.', 'To fail in fulfilling a contract, agreement, or duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Default', 'v. i.', 'To fail to appear in court; to let a case go by\n   default.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Default', 'v. t.', 'To fail to perform or pay; to be guilty of neglect of;\n   to omit; as, to default a dividend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Default', 'v. t.', 'To call a defendant or other party whose duty it is to\n   be present in court, and make entry of his default, if he fails to\n   appear; to enter a default against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Default', 'v. t.', 'To leave out of account; to omit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defaulter', 'n.', 'One who makes default; one who fails to appear in court\n   when court when called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defaulter', 'n.', 'One who fails to perform a duty; a delinquent;\n   particularly, one who fails to account for public money intrusted to\n   his care; a peculator; a defalcator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeasance', 'n.', 'A defeat; an overthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeasance', 'n.', 'A rendering null or void.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeasance', 'n.', 'A condition, relating to a deed, which being performed,\n   the deed is defeated or rendered void; or a collateral deed, made at\n   the same time with a feoffment, or other conveyance, containing\n   conditions, on the performance of which the estate then created may be\n   defeated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeasanced', 'a.', 'Liable to defeasance; capable of being made void or\n   forfeited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeasible', 'a.', 'Capable of being annulled or made void; as, a\n   defeasible title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeated', 'imp. & p. p.', 'of Defeat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeating', 'p. pr. & vb. n.', 'of Defeat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeat', 'v. t.', 'To undo; to disfigure; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeat', 'v. t.', 'To render null and void, as a title; to frustrate, as\n   hope; to deprive, as of an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeat', 'v. t.', 'To overcome or vanquish, as an army; to check, disperse,\n   or ruin by victory; to overthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeat', 'v. t.', 'To resist with success; as, to defeat an assault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeat', 'v.', 'An undoing or annulling; destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeat', 'v.', 'Frustration by rendering null and void, or by prevention of\n   success; as, the defeat of a plan or design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeat', 'v.', 'An overthrow, as of an army in battle; loss of a battle;\n   repulse suffered; discomfiture; -- opposed to victory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeature', 'n.', 'Overthrow; defeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeature', 'n.', 'Disfigurement; deformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeatured', 'p. p.', 'Changed in features; deformed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defecate', 'a.', 'Freed from anything that can pollute, as dregs, lees,\n   etc.; refined; purified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defecated', 'imp. & p. p.', 'of Defecate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defecating', 'p. pr. & vb. n.', 'of Defecate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defecate', 'v. t.', 'To clear from impurities, as lees, dregs, etc.; to\n   clarify; to purify; to refine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defecate', 'v. t.', 'To free from extraneous or polluting matter; to clear;\n   to purify, as from that which materializes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defecate', 'v. i.', 'To become clear, pure, or free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defecate', 'v. i.', 'To void excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defecation', 'n.', 'The act of separating from impurities, as lees or\n   dregs; purification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defecation', 'n.', 'The act or process of voiding excrement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defecator', 'n.', 'That which cleanses or purifies; esp., an apparatus for\n   removing the feculencies of juices and sirups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defect', 'n.', 'Want or absence of something necessary for completeness or\n   perfection; deficiency; -- opposed to superfluity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defect', 'n.', 'Failing; fault; imperfection, whether physical or moral;\n   blemish; as, a defect in the ear or eye; a defect in timber or iron; a\n   defect of memory or judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defect', 'v. i.', 'To fail; to become deficient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defect', 'v. t.', 'To injure; to damage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defectibility', 'n.', 'Deficiency; imperfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defectible', 'a.', 'Liable to defect; imperfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defection', 'n.', 'Act of abandoning a person or cause to which one is\n   bound by allegiance or duty, or to which one has attached himself;\n   desertion; failure in duty; a falling away; apostasy; backsliding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defectionist', 'n.', 'One who advocates or encourages defection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defectious', 'a.', 'Having defects; imperfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defective', 'a.', 'Wanting in something; incomplete; lacking a part;\n   deficient; imperfect; faulty; -- applied either to natural or moral\n   qualities; as, a defective limb; defective timber; a defective copy or\n   account; a defective character; defective rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defective', 'a.', 'Lacking some of the usual forms of declension or\n   conjugation; as, a defective noun or verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defectuosity', 'n.', 'Great imperfection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defectuous', 'a.', 'Full of defects; imperfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defedation', 'n.', 'The act of making foul; pollution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defence', 'n. & v. t.', 'See Defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defended', 'imp. & p. p.', 'of Defend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defending', 'p. pr. & vb. n.', 'of Defend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defend', 'v. t.', 'To ward or fend off; to drive back or away; to repel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defend', 'v. t.', 'To prohibit; to forbid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defend', 'v. t.', 'To repel danger or harm from; to protect; to secure\n   against; attack; to maintain against force or argument; to uphold; to\n   guard; as, to defend a town; to defend a cause; to defend character; to\n   defend the absent; -- sometimes followed by from or against; as, to\n   defend ones self from, or against, ones enemies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defend', 'v. t.', 'To deny the right of the plaintiff in regard to (the\n   suit, or the wrong charged); to oppose or resist, as a claim at law; to\n   contest, as a suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defendable', 'a.', 'Capable of being defended; defensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defendant', 'a.', 'Serving, or suitable, for defense; defensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defendant', 'a.', 'Making defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defendant', 'n.', 'One who defends; a defender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defendant', 'n.', 'A person required to make answer in an action or suit;\n   -- opposed to plaintiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defendee', 'n.', 'One who is defended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defender', 'n.', 'One who defends; one who maintains, supports, protects,\n   or vindicates; a champion; an advocate; a vindicator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defendress', 'n.', 'A female defender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensative', 'n.', 'That which serves to protect or defend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defense', 'n.', 'Alt. of Defence');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defence', 'n.', 'The act of defending, or the state of being defended;\n   protection, as from violence or danger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defence', 'n.', 'That which defends or protects; anything employed to\n   oppose attack, ward off violence or danger, or maintain security; a\n   guard; a protection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defence', 'n.', 'Protecting plea; vindication; justification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defence', 'n.', 'The defendants answer or plea; an opposing or denial of\n   the truth or validity of the plaintiffs or prosecutors case; the\n   method of proceeding adopted by the defendant to protect himself\n   against the plaintiffs action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defence', 'n.', 'Act or skill in making defense; defensive plan or policy;\n   practice in self defense, as in fencing, boxing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defence', 'n.', 'Prohibition; a prohibitory ordinance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defense', 'v. t.', 'To furnish with defenses; to fortify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defenseless', 'a.', 'Destitute of defense; unprepared to resist attack;\n   unable to oppose; unprotected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defenser', 'n.', 'Defender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensibility', 'n.', 'Capability of being defended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensible', 'a.', 'Capable of being defended; as, a defensible city, or a\n   defensible cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensible', 'a.', 'Capable of offering defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensibleness', 'n.', 'Capability of being defended; defensibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensive', 'a.', 'Serving to defend or protect; proper for defense;\n   opposed to offensive; as, defensive armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensive', 'a.', 'Carried on by resisting attack or aggression; -- opposed\n   to offensive; as, defensive war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensive', 'a.', 'In a state or posture of defense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensive', 'n.', 'That which defends; a safeguard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensively', 'adv.', 'On the defensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensor', 'n.', 'A defender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensor', 'n.', 'A defender or an advocate in court; a guardian or\n   protector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensor', 'n.', 'The patron of a church; an officer having charge of the\n   temporal affairs of a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defensory', 'a.', 'Tending to defend; defensive; as, defensory\n   preparations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deferred', 'imp. & p. p.', 'of Defer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deferring', 'p. pr. & vb. n.', 'of Defer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defer', 'v. t.', 'To put off; to postpone to a future time; to delay the\n   execution of; to delay; to withhold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defer', 'v. i.', 'To put off; to delay to act; to wait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defer', 'v. t.', 'To render or offer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defer', 'v. t.', 'To lay before; to submit in a respectful manner; to\n   refer; -- with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defer', 'v. i.', 'To yield deference to the wishes of another; to submit to\n   the opinion of another, or to authority; -- with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deference', 'n.', 'A yielding of judgment or preference from respect to the\n   wishes or opinion of another; submission in opinion; regard; respect;\n   complaisance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deferent', 'a.', 'Serving to carry; bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deferent', 'n.', 'That which carries or conveys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deferent', 'n.', 'An imaginary circle surrounding the earth, in whose\n   periphery either the heavenly body or the center of the heavenly bodys\n   epicycle was supposed to be carried round.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deferential', 'a.', 'Expressing deference; accustomed to defer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deferentially', 'adv.', 'With deference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deferment', 'n.', 'The act of delaying; postponement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deferrer', 'n.', 'One who defers or puts off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defervescence', 'n.', 'Alt. of Defervescency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defervescency', 'n.', 'A subsiding from a state of ebullition; loss of\n   heat; lukewarmness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defervescency', 'n.', 'The subsidence of a febrile process; as, the stage\n   of defervescence in pneumonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defeudalize', 'v. t.', 'To deprive of the feudal character or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiance', 'n.', 'The act of defying, putting in opposition, or provoking\n   to combat; a challenge; a provocation; a summons to combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiance', 'n.', 'A state of opposition; willingness to flight; disposition\n   to resist; contempt of opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiance', 'n.', 'A casting aside; renunciation; rejection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiant', 'a.', 'Full of defiance; bold; insolent; as, a defiant spirit or\n   act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiatory', 'a.', 'Bidding or manifesting defiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defibrinate', 'v. t.', 'To deprive of fibrin, as fresh blood or lymph by\n   stirring with twigs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defibrination', 'n.', 'The act or process of depriving of fibrin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defibrinize', 'v. t.', 'To defibrinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deficience', 'n.', 'Same as Deficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deficiencies', 'pl. ', 'of Deficiency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deficiency', 'n.', 'The state of being deficient; inadequacy; want;\n   failure; imperfection; shortcoming; defect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deficient', 'a.', 'Wanting, to make up completeness; wanting, as regards a\n   requirement; not sufficient; inadequate; defective; imperfect;\n   incomplete; lacking; as, deficient parts; deficient estate; deficient\n   strength; deficient in judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deficit', 'n.', 'Deficiency in amount or quality; a falling short; lack;\n   as, a deficit in taxes, revenue, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defier', 'n.', 'One who dares and defies; a contemner; as, a defier of the\n   laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiguration', 'n.', 'Disfiguration; mutilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defigure', 'v. t.', 'To delineate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiladed', 'imp. & p. p.', 'of Defilade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defilading', 'p. pr. & vb. n.', 'of Defilade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defilade', 'v. t.', 'To raise, as a rampart, so as to shelter interior\n   works commanded from some higher point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defilading', 'n.', 'The art or act of determining the directions and\n   heights of the lines of rampart with reference to the protection of the\n   interior from exposure to an enemys fire from any point within range,\n   or from any works which may be erected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiled', 'imp. & p. p.', 'of Defile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiling', 'p. pr. & vb. n.', 'of Defile');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defile', 'v. i.', 'To march off in a line, file by file; to file off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defile', 'v. t.', 'Same as Defilade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defile', 'n.', 'Any narrow passage or gorge in which troops can march only\n   in a file, or with a narrow front; a long, narrow pass between hills,\n   rocks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defile', 'n.', 'The act of defilading a fortress, or of raising the\n   exterior works in order to protect the interior. See Defilade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defile', 'v. t.', 'To make foul or impure; to make filthy; to dirty; to\n   befoul; to pollute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defile', 'v. t.', 'To soil or sully; to tarnish, as reputation; to taint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defile', 'v. t.', 'To injure in purity of character; to corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defile', 'v. t.', 'To corrupt the chastity of; to debauch; to violate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defile', 'v. t.', 'To make ceremonially unclean; to pollute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defilement', 'n.', 'The protection of the interior walls of a fortification\n   from an enfilading fire, as by covering them, or by a high parapet on\n   the exposed side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defilement', 'n.', 'The act of defiling, or state of being defiled, whether\n   physically or morally; pollution; foulness; dirtiness; uncleanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiler', 'n.', 'One who defiles; one who corrupts or violates; that which\n   pollutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defiliation', 'n.', 'Abstraction of a child from its parents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definable', 'a.', 'Capable of being defined, limited, or explained;\n   determinable; describable by definition; ascertainable; as, definable\n   limits; definable distinctions or regulations; definable words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defined', 'imp. & p. p.', 'of Define');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defining', 'p. pr. & vb. n.', 'of Define');
  INSERT INTO words (word, word_type, description) VALUES  (    'Define', 'v. t.', 'To fix the bounds of; to bring to a termination; to end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Define', 'v. t.', 'To determine or clearly exhibit the boundaries of; to\n   mark the limits of; as, to define the extent of a kingdom or country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Define', 'v. t.', 'To determine with precision; to mark out with\n   distinctness; to ascertain or exhibit clearly; as, the defining power\n   of an optical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Define', 'v. t.', 'To determine the precise signification of; to fix the\n   meaning of; to describe accurately; to explain; to expound or\n   interpret; as, to define a word, a phrase, or a scientific term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Define', 'v. i.', 'To determine; to decide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definement', 'n.', 'The act of defining; definition; description.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definer', 'n.', 'One who defines or explains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definite', 'a.', 'Having certain or distinct; determinate in extent or\n   greatness; limited; fixed; as, definite dimensions; a definite measure;\n   a definite period or interval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definite', 'a.', 'Having certain limits in signification; determinate;\n   certain; precise; fixed; exact; clear; as, a definite word, term, or\n   expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definite', 'a.', 'Determined; resolved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definite', 'a.', 'Serving to define or restrict; limiting; determining; as,\n   the definite article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definite', 'n.', 'A thing defined or determined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definitely', 'adv.', 'In a definite manner; with precision; precisely;\n   determinately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definiteness', 'n.', 'The state of being definite; determinateness;\n   precision; certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definition', 'n.', 'The act of defining; determination of the limits; as, a\n   telescope accurate in definition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definition', 'n.', 'Act of ascertaining and explaining the signification; a\n   description of a thing by its properties; an explanation of the meaning\n   of a word or term; as, the definition of \"circle;\" the definition of\n   \"wit;\" an exact definition; a loose definition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definition', 'n.', 'Description; sort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definition', 'n.', 'An exact enunciation of the constituents which make up\n   the logical essence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definition', 'n.', 'Distinctness or clearness, as of an image formed by an\n   optical instrument; precision in detail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definitional', 'a.', 'Relating to definition; of the nature of a\n   definition; employed in defining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definitive', 'a.', 'Determinate; positive; final; conclusive;\n   unconditional; express.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definitive', 'a.', 'Limiting; determining; as, a definitive word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definitive', 'a.', 'Determined; resolved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definitive', 'n.', 'A word used to define or limit the extent of the\n   signification of a common noun, such as the definite article, and some\n   pronouns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definitively', 'adv.', 'In a definitive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definitiveness', 'n.', 'The quality of being definitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Definitude', 'n.', 'Definiteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defix', 'v. t.', 'To fix; to fasten; to establish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflagrability', 'n.', 'The state or quality of being deflagrable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflagrable', 'a.', 'Burning with a sudden and sparkling combustion, as\n   niter; hence, slightly explosive; liable to snap and crackle when\n   heated, as salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflagrated', 'imp. & p. p.', 'of Deflagrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflagrating', 'p. pr. & vb. n.', 'of Deflagrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflagrate', 'v. i.', 'To burn with a sudden and sparkling combustion, as\n   niter; also, to snap and crackle with slight explosions when heated, as\n   salt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflagrate', 'v. t.', 'To cause to burn with sudden and sparkling\n   combustion, as by the action of intense heat; to burn or vaporize\n   suddenly; as, to deflagrate refractory metals in the oxyhydrogen flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflagration', 'n.', 'A burning up; conflagration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflagration', 'n.', 'The act or process of deflagrating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflagrator', 'n.', 'A form of the voltaic battery having large plates,\n   used for producing rapid and powerful combustion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflate', 'v. t.', 'To reduce from an inflated condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflected', 'imp. & p. p.', 'of Deflect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflecting', 'p. pr. & vb. n.', 'of Deflect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflect', 'v. t.', 'To cause to turn aside; to bend; as, rays of light are\n   often deflected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflect', 'v. i.', 'To turn aside; to deviate from a right or a horizontal\n   line, or from a proper position, course or direction; to swerve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflectable', 'a.', 'Capable of being deflected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflected', 'a.', 'Turned aside; deviating from a direct line or course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflected', 'a.', 'Bent downward; deflexed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflection', 'n.', 'The act of turning aside, or state of being turned\n   aside; a turning from a right line or proper course; a bending, esp.\n   downward; deviation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflection', 'n.', 'The deviation of a shot or ball from its true course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflection', 'n.', 'A deviation of the rays of light toward the surface of\n   an opaque body; inflection; diffraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflection', 'n.', 'The bending which a beam or girder undergoes from its\n   own weight or by reason of a load.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflectionization', 'n.', 'The act of freeing from inflections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflectionize', 'v. t.', 'To free from inflections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflective', 'a.', 'Causing deflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflector', 'n.', 'That which deflects, as a diaphragm in a furnace, or a\n   cone in a lamp (to deflect and mingle air and gases and help\n   combustion).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflexed', 'a.', 'Bent abruptly downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflexion', 'n.', 'See Deflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflexure', 'n.', 'A bending or turning aside; deflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflorate', 'a.', 'Past the flowering state; having shed its pollen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defloration', 'n.', 'The act of deflouring; as, the defloration of a\n   virgin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defloration', 'n.', 'That which is chosen as the flower or choicest part;\n   careful culling or selection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defloured', 'imp. & p. p.', 'of Deflour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflouring', 'p. pr. & vb. n.', 'of Deflour');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflour', 'v. t.', 'To deprive of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflour', 'v. t.', 'To take away the prime beauty and grace of; to rob of\n   the choicest ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflour', 'v. t.', 'To deprive of virginity, as a woman; to violate; to\n   ravish; also, to seduce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflourer', 'n.', 'One who deflours; a ravisher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflow', 'v. i.', 'To flow down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflower', 'v. t.', 'Same as Deflour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflowerer', 'n.', 'See Deflourer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defluous', 'a.', 'Flowing down; falling off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deflux', 'n.', 'Downward flow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defluxion', 'n.', 'A discharge or flowing of humors or fluid matter, as\n   from the nose in catarrh; -- sometimes used synonymously with\n   inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defly', 'adv.', 'Deftly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defoedation', 'n.', 'Defedation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defoliate', 'a.', 'Alt. of Defoliated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defoliated', 'a.', 'Deprived of leaves, as by their natural fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defoliation', 'n.', 'The separation of ripened leaves from a branch or\n   stem; the falling or shedding of the leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforced', 'imp. & p. p.', 'of Deforce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforcing', 'p. pr. & vb. n.', 'of Deforce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforce', 'v.', 'To keep from the rightful owner; to withhold wrongfully\n   the possession of, as of lands or a freehold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforce', 'v.', 'To resist the execution of the law; to oppose by force, as\n   an officer in the execution of his duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforcement', 'n.', 'A keeping out by force or wrong; a wrongful\n   withholding, as of lands or tenements, to which another has a right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforcement', 'n.', 'Resistance to an officer in the execution of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforceor', 'n.', 'Same as Deforciant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforciant', 'n.', 'One who keeps out of possession the rightful owner of\n   an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforciant', 'n.', 'One against whom a fictitious action of fine was\n   brought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforciation', 'n.', 'Same as Deforcement, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforest', 'v. t.', 'To clear of forests; to disforest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deformed', 'imp. & p. p.', 'of Deform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforming', 'p. pr. & vb. n.', 'of Deform');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deform', 'v. t.', 'To spoil the form of; to mar in form; to misshape; to\n   disfigure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deform', 'v. t.', 'To render displeasing; to deprive of comeliness, grace,\n   or perfection; to dishonor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deform', 'a.', 'Deformed; misshapen; shapeless; horrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deformation', 'n.', 'The act of deforming, or state of anything deformed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deformation', 'n.', 'Transformation; change of shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deformed', 'a.', 'Unnatural or distorted in form; having a deformity;\n   misshapen; disfigured; as, a deformed person; a deformed head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deformer', 'n.', 'One who deforms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deformities', 'pl. ', 'of Deformity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deformity', 'a.', 'The state of being deformed; want of proper form or\n   symmetry; any unnatural form or shape; distortion; irregularity of\n   shape or features; ugliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deformity', 'a.', 'Anything that destroys beauty, grace, or propriety;\n   irregularity; absurdity; gross deviation from order or the established\n   laws of propriety; as, deformity in an edifice; deformity of character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deforser', 'n.', 'A deforciant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defoul', 'v. t.', 'To tread down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defoul', 'v. t.', 'To make foul; to defile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defrauded', 'imp. & p. p.', 'of Defraud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defrauding', 'p. pr. & vb. n.', 'of Defraud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defraud', 'v. t.', 'To deprive of some right, interest, or property, by a\n   deceitful device; to withhold from wrongfully; to injure by\n   embezzlement; to cheat; to overreach; as, to defraud a servant, or a\n   creditor, or the state; -- with of before the thing taken or withheld.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defraudation', 'n.', 'The act of defrauding; a taking by fraud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defrauder', 'n.', 'One who defrauds; a cheat; an embezzler; a peculator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defraudment', 'n.', 'Privation by fraud; defrauding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defrayed', 'imp. & p. p.', 'of Defray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defraying', 'p. pr. & vb. n.', 'of Defray');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defray', 'v. t.', 'To pay or discharge; to serve in payment of; to provide\n   for, as a charge, debt, expenses, costs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defray', 'v. t.', 'To avert or appease, as by paying off; to satisfy; as,\n   to defray wrath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defrayal', 'n.', 'The act of defraying; payment; as, the defrayal of\n   necessary costs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defrayer', 'n.', 'One who pays off expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defrayment', 'n.', 'Payment of charges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deft', 'a.', 'Apt; fit; dexterous; clever; handy; spruce; neat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deftly', 'adv.', 'Aptly; fitly; dexterously; neatly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deftness', 'n.', 'The quality of being deft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defunct', 'a.', 'Having finished the course of life; dead; deceased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defunct', 'n.', 'A dead person; one deceased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defunction', 'n.', 'Death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defunctive', 'a.', 'Funereal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defuse', 'v. t.', 'To disorder; to make shapeless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defied', 'imp. & p. p.', 'of Defy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defying', 'p. pr. & vb. n.', 'of Defy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defy', 'v. t.', 'To renounce or dissolve all bonds of affiance, faith, or\n   obligation with; to reject, refuse, or renounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defy', 'v. t.', 'To provoke to combat or strife; to call out to combat; to\n   challenge; to dare; to brave; to set at defiance; to treat with\n   contempt; as, to defy an enemy; to defy the power of a magistrate; to\n   defy the arguments of an opponent; to defy public opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Defy', 'n.', 'A challenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degarnished', 'imp. & p. p.', 'of Degarnish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degarnishing', 'p. pr. & vb. n.', 'of Degarnish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degarnish', 'v. t.', 'To strip or deprive of entirely, as of furniture,\n   ornaments, etc.; to disgarnish; as, to degarnish a house, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degarnish', 'v. t.', 'To deprive of a garrison, or of troops necessary for\n   defense; as, to degarnish a city or fort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degarnishment', 'n.', 'The act of depriving, as of furniture, apparatus, or\n   a garrison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degender', 'v. i.', 'Alt. of Degener');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degener', 'v. i.', 'To degenerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degeneracy', 'a.', 'The act of becoming degenerate; a growing worse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degeneracy', 'a.', 'The state of having become degenerate; decline in good\n   qualities; deterioration; meanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerate', 'a.', 'Having become worse than ones kind, or ones former\n   state; having declined in worth; having lost in goodness; deteriorated;\n   degraded; unworthy; base; low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerated', 'imp. & p. p.', 'of Degenerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerating', 'p. pr. & vb. n.', 'of Degenerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerate', 'v. i.', 'To be or grow worse than ones kind, or than one was\n   originally; hence, to be inferior; to grow poorer, meaner, or more\n   vicious; to decline in good qualities; to deteriorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerate', 'v. i.', 'To fall off from the normal quality or the healthy\n   structure of its kind; to become of a lower type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerately', 'adv.', 'In a degenerate manner; unworthily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerateness', 'n.', 'Degeneracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degeneration', 'n.', 'The act or state of growing worse, or the state of\n   having become worse; decline; degradation; debasement; degeneracy;\n   deterioration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degeneration', 'n.', 'That condition of a tissue or an organ in which its\n   vitality has become either diminished or perverted; a substitution of a\n   lower for a higher form of structure; as, fatty degeneration of the\n   liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degeneration', 'n.', 'A gradual deterioration, from natural causes, of any\n   class of animals or plants or any particular organ or organs;\n   hereditary degradation of type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degeneration', 'n.', 'The thing degenerated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerationist', 'n.', 'A believer in the theory of degeneration, or\n   hereditary degradation of type; as, the degenerationists hold that\n   savagery is the result of degeneration from a superior state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerative', 'a.', 'Undergoing or producing degeneration; tending to\n   degenerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerous', 'a.', 'Degenerate; base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degenerously', 'adv.', 'Basely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deglazing', 'n.', 'The process of giving a dull or ground surface to glass\n   by acid or by mechanical means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degloried', 'a.', 'Deprived of glory; dishonored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deglutinated', 'imp. & p. p.', 'of Deglutinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deglutinating', 'p. pr. & vb. n.', 'of Deglutinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deglutinate', 'v. t.', 'To loosen or separate by dissolving the glue which\n   unties; to unglue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deglutination', 'n.', 'The act of ungluing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deglutition', 'n.', 'The act or process of swallowing food; the power of\n   swallowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deglutitious', 'a.', 'Pertaining to deglutition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deglutitory', 'a.', 'Serving for, or aiding in, deglutition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degradation', 'n.', 'The act of reducing in rank, character, or reputation,\n   or of abasing; a lowering from ones standing or rank in office or\n   society; diminution; as, the degradation of a peer, a knight, a\n   general, or a bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degradation', 'n.', 'The state of being reduced in rank, character, or\n   reputation; baseness; moral, physical, or intellectual degeneracy;\n   disgrace; abasement; debasement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degradation', 'n.', 'Diminution or reduction of strength, efficacy, or\n   value; degeneration; deterioration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degradation', 'n.', 'A gradual wearing down or wasting, as of rocks and\n   banks, by the action of water, frost etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degradation', 'n.', 'The state or condition of a species or group which\n   exhibits degraded forms; degeneration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degradation', 'n.', 'Arrest of development, or degeneration of any organ,\n   or of the body as a whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degraded', 'imp. & p. p.', 'of Degrade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degrading', 'p. pr. & vb. n.', 'of Degrade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degrade', 'v. t.', 'To reduce from a higher to a lower rank or degree; to\n   lower in rank; to deprive of office or dignity; to strip of honors; as,\n   to degrade a nobleman, or a general officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degrade', 'v. t.', 'To reduce in estimation, character, or reputation; to\n   lessen the value of; to lower the physical, moral, or intellectual\n   character of; to debase; to bring shame or contempt upon; to disgrace;\n   as, vice degrades a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degrade', 'v. t.', 'To reduce in altitude or magnitude, as hills and\n   mountains; to wear down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degrade', 'v. i.', 'To degenerate; to pass from a higher to a lower type of\n   structure; as, a family of plants or animals degrades through this or\n   that genus or group of genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degraded', 'a.', 'Reduced in rank, character, or reputation; debased;\n   sunken; low; base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degraded', 'a.', 'Having the typical characters or organs in a partially\n   developed condition, or lacking certain parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degraded', 'a.', 'Having steps; -- said of a cross each of whose\n   extremities finishes in steps growing larger as they leave the center;\n   -- termed also on degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degradement', 'n.', 'Deprivation of rank or office; degradation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degradingly', 'adv.', 'In a degrading manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degravation', 'a.', 'The act of making heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'A step, stair, or staircase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'One of a series of progressive steps upward or downward, in\n   quality, rank, acquirement, and the like; a stage in progression;\n   grade; gradation; as, degrees of vice and virtue; to advance by slow\n   degrees; degree of comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'The point or step of progression to which a person has\n   arrived; rank or station in life; position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'Measure of advancement; quality; extent; as, tastes differ\n   in kind as well as in degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'Grade or rank to which scholars are admitted by a college\n   or university, in recognition of their attainments; as, the degree of\n   bachelor of arts, master, doctor, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'A certain distance or remove in the line of descent,\n   determining the proximity of blood; one remove in the chain of\n   relationship; as, a relation in the third or fourth degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'Three figures taken together in numeration; thus, 140 is\n   one degree, 222,140 two degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'State as indicated by sum of exponents; more particularly,\n   the degree of a term is indicated by the sum of the exponents of its\n   literal factors; thus, a2b3c is a term of the sixth degree. The degree\n   of a power, or radical, is denoted by its index, that of an equation by\n   the greatest sum of the exponents of the unknown quantities in any\n   term; thus, ax4 + bx2 = c, and mx2y2 + nyx = p, are both equations of\n   the fourth degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'A 360th part of the circumference of a circle, which part\n   is taken as the principal unit of measure for arcs and angles. The\n   degree is divided into 60 minutes and the minute into 60 seconds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'A division, space, or interval, marked on a mathematical or\n   other instrument, as on a thermometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degree', 'n.', 'A line or space of the staff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degu', 'n.', 'A small South American rodent (Octodon Cumingii), of the\n   family Octodontidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degust', 'v. t.', 'To taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Degustation', 'n.', 'Tasting; the appreciation of sapid qualities by the\n   taste organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehisce', 'v. i.', 'To gape; to open by dehiscence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehiscence', 'n.', 'The act of gaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehiscence', 'n.', 'A gaping or bursting open along a definite line of\n   attachment or suture, without tearing, as in the opening of pods, or\n   the bursting of capsules at maturity so as to emit seeds, etc.; also,\n   the bursting open of follicles, as in the ovaries of animals, for the\n   expulsion of their contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehiscent', 'a.', 'Characterized by dehiscence; opening in some definite\n   way, as the capsule of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehonestate', 'v. t.', 'To disparage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehonestation', 'n.', 'A dishonoring; disgracing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehorned', 'imp. & p. p.', 'of Dehorn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehorning', 'p. pr. & vb. n.', 'of Dehorn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehorn', 'v. t.', 'To deprive of horns; to prevent the growth of the horns\n   of (cattle) by burning their ends soon after they start. See Dishorn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehors', 'prep.', 'Out of; without; foreign to; out of the agreement,\n   record, will, or other instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehors', 'n.', 'All sorts of outworks in general, at a distance from the\n   main works; any advanced works for protection or cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dehorted', 'imp. & p. p.', 'of Dehort');
