  INSERT INTO words (word, word_type, description) VALUES  (    'Delicacy', 'a.', 'The state or condition of being delicate; agreeableness\n   to the senses; delightfulness; as, delicacy of flavor, of odor, and the\n   like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicacy', 'a.', 'Nicety or fineness of form, texture, or constitution;\n   softness; elegance; smoothness; tenderness; and hence, frailty or\n   weakness; as, the delicacy of a fiber or a thread; delicacy of a hand\n   or of the human form; delicacy of the skin; delicacy of frame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicacy', 'a.', 'Nice propriety of manners or conduct; susceptibility or\n   tenderness of feeling; refinement; fastidiousness; and hence, in an\n   exaggerated sense, effeminacy; as, great delicacy of behavior; delicacy\n   in doing a kindness; delicacy of character that unfits for earnest\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicacy', 'a.', 'Addiction to pleasure; luxury; daintiness; indulgence;\n   luxurious or voluptuous treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicacy', 'a.', 'Nice and refined perception and discrimination; critical\n   niceness; fastidious accuracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicacy', 'a.', 'The state of being affected by slight causes;\n   sensitiveness; as, the delicacy of a chemists balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicacy', 'a.', 'That which is alluring, delicate, or refined; a luxury or\n   pleasure; something pleasant to the senses, especially to the sense of\n   taste; a dainty; as, delicacies of the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicacy', 'a.', 'Pleasure; gratification; delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Addicted to pleasure; luxurious; voluptuous; alluring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Pleasing to the senses; refinedly agreeable; hence,\n   adapted to please a nice or cultivated taste; nice; fine; elegant; as,\n   a delicate dish; delicate flavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Slight and shapely; lovely; graceful; as, \"a delicate\n   creature.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Fine or slender; minute; not coarse; -- said of a thread,\n   or the like; as, delicate cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Slight or smooth; light and yielding; -- said of texture;\n   as, delicate lace or silk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Soft and fair; -- said of the skin or a surface; as, a\n   delicate cheek; a delicate complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Light, or softly tinted; -- said of a color; as, a\n   delicate blue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Refined; gentle; scrupulous not to trespass or offend;\n   considerate; -- said of manners, conduct, or feelings; as, delicate\n   behavior; delicate attentions; delicate thoughtfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Tender; not able to endure hardship; feeble; frail;\n   effeminate; -- said of constitution, health, etc.; as, a delicate\n   child; delicate health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Requiring careful handling; not to be rudely or hastily\n   dealt with; nice; critical; as, a delicate subject or question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Of exacting tastes and habits; dainty; fastidious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Nicely discriminating or perceptive; refinedly critical;\n   sensitive; exquisite; as, a delicate taste; a delicate ear for music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'a.', 'Affected by slight causes; showing slight changes; as, a\n   delicate thermometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'n.', 'A choice dainty; a delicacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicate', 'n.', 'A delicate, luxurious, or effeminate person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicately', 'adv.', 'In a delicate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicateness', 'n.', 'The quality of being delicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delices', 'n. pl.', 'Delicacies; delights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliciate', 'v. t.', 'To delight ones self; to indulge in feasting; to\n   revel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicious', 'a.', 'Affording exquisite pleasure; delightful; most sweet or\n   grateful to the senses, especially to the taste; charming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delicious', 'a.', 'Addicted to pleasure; seeking enjoyment; luxurious;\n   effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliciously', 'adv.', 'Delightfully; as, to feed deliciously; to be\n   deliciously entertained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliciousness', 'n.', 'The quality of being delicious; as, the\n   deliciousness of a repast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliciousness', 'n.', 'Luxury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delict', 'n.', 'An offense or transgression against law; (Scots Law) an\n   offense of a lesser degree; a misdemeanor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deligate', 'v. t.', 'To bind up; to bandage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deligation', 'n.', 'A binding up; a bandaging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delight', 'v. t.', 'A high degree of gratification of mind; a high- wrought\n   state of pleasurable feeling; lively pleasure; extreme satisfaction;\n   joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delight', 'v. t.', 'That which gives great pleasure or delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delight', 'v. t.', 'Licentious pleasure; lust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delighted', 'imp. & p. p.', 'of Delight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delighting', 'p. pr. & vb. n.', 'of Delight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delight', 'v. t.', 'To give delight to; to affect with great pleasure; to\n   please highly; as, a beautiful landscape delights the eye; harmony\n   delights the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delight', 'v. i.', 'To have or take great delight or pleasure; to be\n   greatly pleased or rejoiced; -- followed by an infinitive, or by in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delightable', 'a.', 'Capable of delighting; delightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delighted', 'a.', 'Endowed with delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delightedly', 'adv.', 'With delight; gladly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delighter', 'n.', 'One who gives or takes delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delightful', 'a.', 'Highly pleasing; affording great pleasure and\n   satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delighting', 'a.', 'Giving delight; gladdening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delightless', 'a.', 'Void of delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delightous', 'a.', 'Delightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delightsome', 'a.', 'Very pleasing; delightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delilah', 'n.', 'The mistress of Samson, who betrayed him (Judges xvi.);\n   hence, a harlot; a temptress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delimit', 'v. t.', 'To fix the limits of; to demarcate; to bound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delimitation', 'n.', 'The act or process of fixing limits or boundaries;\n   limitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deline', 'v. t.', 'To delineate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deline', 'v. t.', 'To mark out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineable', 'a.', 'Capable of being, or liable to be, delineated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineament', '/.', 'Delineation; sketch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineate', 'a.', 'Delineated; portrayed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineated', 'imp. & p. p.', 'of Delineate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineating', 'p. pr. & vb. n.', 'of Delineate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineate', 'v. t.', 'To indicate by lines drawn in the form or figure of;\n   to represent by sketch, design, or diagram; to sketch out; to portray;\n   to picture; in drawing and engraving, to represent in lines, as with\n   the pen, pencil, or graver; hence, to represent with accuracy and\n   minuteness. See Delineation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineate', 'v. t.', 'To portray to the mind or understanding by words; to\n   set forth; to describe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineation', 'n.', 'The act of representing, portraying, or describing, as\n   by lines, diagrams, sketches, etc.; drawing an outline; as, the\n   delineation of a scene or face; in drawing and engraving,\n   representation by means of lines, as distinguished from representation\n   by means of tints and shades; accurate and minute representation, as\n   distinguished from art that is careless of details, or subordinates\n   them excessively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineation', 'n.', 'A delineated picture; representation; sketch;\n   description in words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineator', 'n.', 'One who, or that which, delineates; a sketcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineator', 'n.', 'A perambulator which records distances and delineates a\n   profile, as of a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineatory', 'a.', 'That delineates; descriptive; drawing the outline;\n   delineating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delineature', 'n.', 'Delineation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delinition', 'n.', 'A smearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delinquencies', 'pl. ', 'of Delinquency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delinquency', 'n.', 'Failure or omission of duty; a fault; a misdeed; an\n   offense; a misdemeanor; a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delinquent', 'n.', 'Failing in duty; offending by neglect of duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delinquent', 'n.', 'One who fails or neglects to perform his duty; an\n   offender or transgressor; one who commits a fault or a crime; a\n   culprit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delinquently', 'adv.', 'So as to fail in duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquate', 'v. i.', 'To melt or be dissolved; to deliquesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquate', 'v. t.', 'To cause to melt away; to dissolve; to consume; to\n   waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquation', 'n.', 'A melting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquesced', 'imp. & p. p.', 'of Deliquesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquescing', 'p. pr. & vb. n.', 'of Deliquesce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquesce', 'v. i.', 'To dissolve gradually and become liquid by\n   attracting and absorbing moisture from the air, as certain salts,\n   acids, and alkalies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquescence', 'n.', 'The act of deliquescing or liquefying; process by\n   which anything deliquesces; tendency to melt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquescent', 'a.', 'Dissolving; liquefying by contact with the air;\n   capable of attracting moisture from the atmosphere and becoming liquid;\n   as, deliquescent salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquescent', 'a.', 'Branching so that the stem is lost in branches, as in\n   most deciduous trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquiate', 'v. i.', 'To melt and become liquid by absorbing water from\n   the air; to deliquesce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquiation', 'n.', 'The act of deliquiating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquium', 'n.', 'A melting or dissolution in the air, or in a moist\n   place; a liquid condition; as, a salt falls into a deliquium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquium', 'n.', 'A sinking away; a swooning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliquium', 'n.', 'A melting or maudlin mood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliracy', 'n.', 'Delirium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delirament', 'n.', 'A wandering of the mind; a crazy fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delirancy', 'n.', 'Delirium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delirant', 'a.', 'Delirious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delirate', 'v. t. & i.', 'To madden; to rave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliration', 'n.', 'Aberration of mind; delirium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliriant', 'n.', 'A poison which occasions a persistent delirium, or\n   mental aberration (as belladonna).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delirifacient', 'a.', 'Producing, or tending to produce, delirium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delirifacient', 'n.', 'Any substance which tends to cause delirium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delirious', 'a.', 'Having a delirium; wandering in mind; light-headed;\n   insane; raving; wild; as, a delirious patient; delirious fancies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delirium', 'n.', 'A state in which the thoughts, expressions, and actions\n   are wild, irregular, and incoherent; mental aberration; a roving or\n   wandering of the mind, -- usually dependent on a fever or some other\n   disease, and so distinguished from mania, or madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delirium', 'n.', 'Strong excitement; wild enthusiasm; madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delit', 'n.', 'Delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delitable', 'a.', 'Delightful; delectable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delitescence', 'n.', 'Concealment; seclusion; retirement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delitescence', 'n.', 'The sudden disappearance of inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delitescency', 'n.', 'Concealment; seclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delitescent', 'a.', 'Lying hid; concealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delitigate', 'v. i.', 'To chide; to rail heartily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delitigation', 'n.', 'Chiding; brawl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delivered', 'imp. & p. p.', 'of Deliver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delivering', 'p. pr. & vb. n.', 'of Deliver');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliver', 'v. t.', 'To set free from restraint; to set at liberty; to\n   release; to liberate, as from control; to give up; to free; to save; to\n   rescue from evil actual or feared; -- often with from or out of; as, to\n   deliver one from captivity, or from fear of death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliver', 'v. t.', 'To give or transfer; to yield possession or control of;\n   to part with (to); to make over; to commit; to surrender; to resign; --\n   often with up or over, to or into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliver', 'v. t.', 'To make over to the knowledge of another; to\n   communicate; to utter; to speak; to impart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliver', 'v. t.', 'To give forth in action or exercise; to discharge; as,\n   to deliver a blow; to deliver a broadside, or a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliver', 'v. t.', 'To free from, or disburden of, young; to relieve of a\n   child in childbirth; to bring forth; -- often with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliver', 'v. t.', 'To discover; to show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliver', 'v. t.', 'To deliberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliver', 'v. t.', 'To admit; to allow to pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliver', 'v. t.', 'Free; nimble; sprightly; active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverable', 'a.', 'Capable of being, or about to be, delivered; necessary\n   to be delivered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverance', 'n.', 'The act of delivering or freeing from restraint,\n   captivity, peril, and the like; rescue; as, the deliverance of a\n   captive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverance', 'n.', 'Act of bringing forth children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverance', 'n.', 'Act of speaking; utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverance', 'n.', 'The state of being delivered, or freed from restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverance', 'n.', 'Anything delivered or communicated; esp., an opinion\n   or decision expressed publicly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverance', 'n.', 'Any fact or truth which is decisively attested or\n   intuitively known as a psychological or philosophical datum; as, the\n   deliverance of consciousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverer', 'n.', 'One who delivers or rescues; a preserver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverer', 'n.', 'One who relates or communicates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliveress', 'n.', 'A female deliverer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverly', 'adv.', 'Actively; quickly; nimbly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliverness', 'n.', 'Nimbleness; agility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deliveries', 'pl. ', 'of Delivery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delivery', 'n.', 'The act of delivering from restraint; rescue; release;\n   liberation; as, the delivery of a captive from his dungeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delivery', 'n.', 'The act of delivering up or over; surrender; transfer of\n   the body or substance of a thing; distribution; as, the delivery of a\n   fort, of hostages, of a criminal, of goods, of letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delivery', 'n.', 'The act or style of utterance; manner of speaking; as, a\n   good delivery; a clear delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delivery', 'n.', 'The act of giving birth; parturition; the expulsion or\n   extraction of a fetus and its membranes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delivery', 'n.', 'The act of exerting ones strength or limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delivery', 'n.', 'The act or manner of delivering a ball; as, the pitcher\n   has a swift delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dell', 'n.', 'A small, retired valley; a ravine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dell', 'n.', 'A young woman; a wench.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Della Crusca', '', 'A shortened form of Accademia della Crusca, an academy\n   in Florence, Italy, founded in the 16th century, especially for\n   conserving the purity of the Italian language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dellacruscan', 'a.', 'Of or pertaining to the Accademia della Crusca in\n   Florence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deloo', 'n.', 'The duykerbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deloul', 'n.', 'A special breed of the dromedary used for rapid traveling;\n   the swift camel; -- called also herire, and maharik.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delph', 'n.', 'Delftware.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delph', 'n.', 'The drain on the land side of a sea embankment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphian', 'a.', 'Delphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphic', 'a.', 'Of or relating to Delphi, or to the famous oracle of that\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphic', 'a.', 'Ambiguous; mysterious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphin', 'a.', 'Alt. of Delphine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphine', 'a.', 'Pertaining to the dauphin of France; as, the Delphin\n   classics, an edition of the Latin classics, prepared in the reign of\n   Louis XIV., for the use of the dauphin (in usum Delphini).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphin', 'n.', 'A fatty substance contained in the oil of the dolphin and\n   the porpoise; -- called also phocenin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphine', 'a.', 'Pertaining to the dolphin, a genus of fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphinic', 'n.', 'Pertaining to, or derived from, the dolphin; phocenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphinic', 'a.', 'Pertaining to, or derived from, the larkspur;\n   specifically, relating to the stavesacre (Delphinium staphisagria).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphinine', 'n.', 'A poisonous alkaloid extracted from the stavesacre\n   (Delphinium staphisagria), as a colorless amorphous powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphinoid', 'a.', 'Pertaining to, or resembling, the dolphin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphinoidea', 'n. pl.', 'The division of Cetacea which comprises the\n   dolphins, porpoises, and related forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphinus', 'n.', 'A genus of Cetacea, including the dolphin. See Dolphin,\n   1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delphinus', 'n.', 'The Dolphin, a constellation near the equator and east\n   of Aquila.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deltas', 'pl. ', 'of Delta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delta', 'n.', 'A tract of land shaped like the letter delta (/), especially\n   when the land is alluvial and inclosed between two or more mouths of a\n   river; as, the delta of the Ganges, of the Nile, or of the Mississippi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deltafication', 'n.', 'The formation of a delta or of deltas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deltaic', 'a.', 'Relating to, or like, a delta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delthyris', 'n.', 'A name formerly given to certain Silurian brachiopod\n   shells of the genus Spirifer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deltic', 'a.', 'Deltaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deltidium', 'n.', 'The triangular space under the beak of many brachiopod\n   shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deltohedron', 'n.', 'A solid bounded by twelve quadrilateral faces. It is a\n   hemihedral form of the isometric system, allied to the tetrahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deltoid', 'a.', 'Shaped like the Greek / (delta); delta-shaped; triangular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deludable', 'a.', 'Capable of being deluded; liable to be imposed on;\n   gullible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deluded', 'imp. & p. p.', 'of Delude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deluding', 'p. pr. & vb. n.', 'of Delude');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delude', 'v. t.', 'To lead from truth or into error; to mislead the mind or\n   judgment of; to beguile; to impose on; to dupe; to make a fool of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delude', 'v. t.', 'To frustrate or disappoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deluder', 'n.', 'One who deludes; a deceiver; an impostor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deluge', 'n.', 'A washing away; an overflowing of the land by water; an\n   inundation; a flood; specifically, The Deluge, the great flood in the\n   days of Noah (Gen. vii.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deluge', 'n.', 'Fig.: Anything which overwhelms, or causes great\n   destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deluged', 'imp. & p. p.', 'of Deluge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deluging', 'p. pr. & vb. n.', 'of Deluge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deluge', 'v. t.', 'To overflow with water; to inundate; to overwhelm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deluge', 'v. t.', 'To overwhelm, as with a deluge; to cover; to overspread;\n   to overpower; to submerge; to destroy; as, the northern nations deluged\n   the Roman empire with their armies; the land is deluged with woe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delundung', 'n.', 'An East Indian carnivorous mammal (Prionodon gracilis),\n   resembling the civets, but without scent pouches. It is handsomely\n   spotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delusion', 'n.', 'The act of deluding; deception; a misleading of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delusion', 'n.', 'The state of being deluded or misled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delusion', 'n.', 'That which is falsely or delusively believed or\n   propagated; false belief; error in belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delusional', 'a.', 'Of or pertaining to delusions; as, delusional\n   monomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delusive', 'a.', 'Apt or fitted to delude; tending to mislead the mind;\n   deceptive; beguiling; delusory; as, delusive arts; a delusive dream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delusory', 'a.', 'Delusive; fallacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delved', 'imp. & p. p.', 'of Delve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delving', 'p. pr. & vb. n.', 'of Delve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delve', 'v. t.', 'To dig; to open (the ground) as with a spade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delve', 'v. t.', 'To dig into; to penetrate; to trace out; to fathom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delve', 'v. i.', 'To dig or labor with a spade, or as with a spade; to\n   labor as a drudge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delve', 'v. t.', 'A place dug; a pit; a ditch; a den; a cave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Delver', 'n.', 'One who digs, as with a spade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demagnetize', 'v. t.', 'To deprive of magnetic properties. See Magnetize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demagnetize', 'v. t.', 'To free from mesmeric influence; to demesmerize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demagog', 'n.', 'Demagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demagogic', 'a.', 'Alt. of Demagogical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demagogical', 'a.', 'Relating to, or like, a demagogue; factious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demagogism', 'n.', 'The practices of a demagogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demagogue', 'n.', 'A leader of the rabble; one who attempts to control the\n   multitude by specious or deceitful arts; an unprincipled and factious\n   mob orator or political leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demagogy', 'n.', 'Demagogism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demain', 'n.', 'Rule; management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demain', 'n.', 'See Demesne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demanded', 'imp. & p. p.', 'of Demand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demanding', 'p. pr. & vb. n.', 'of Demand');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'To ask or call for with authority; to claim or seek\n   from, as by authority or right; to claim, as something due; to call for\n   urgently or peremptorily; as, to demand a debt; to demand obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'To inquire authoritatively or earnestly; to ask, esp. in\n   a peremptory manner; to question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'To require as necessary or useful; to be in urgent need\n   of; hence, to call for; as, the case demands care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'To call into court; to summon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. i.', 'To make a demand; to inquire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'The act of demanding; an asking with authority; a\n   peremptory urging of a claim; a claiming or challenging as due;\n   requisition; as, the demand of a creditor; a note payable on demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'Earnest inquiry; question; query.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'A diligent seeking or search; manifested want; desire to\n   possess; request; as, a demand for certain goods; a persons company is\n   in great demand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'That which one demands or has a right to demand; thing\n   claimed as due; claim; as, demands on an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'The asking or seeking for what is due or claimed as due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'The right or title in virtue of which anything may be\n   claimed; as, to hold a demand against a person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demand', 'v. t.', 'A thing or amount claimed to be due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demandable', 'a.', 'That may be demanded or claimed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demandant', 'n.', 'One who demands; the plaintiff in a real action; any\n   plaintiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demander', 'n.', 'One who demands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demandress', 'n.', 'A woman who demands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demantoid', 'n.', 'A yellow-green, transparent variety of garnet found in\n   the Urals. It is valued as a gem because of its brilliancy of luster,\n   whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demarcate', 'v. t.', 'To mark by bounds; to set the limits of; to separate;\n   to discriminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demarcation', 'n.', 'The act of marking, or of ascertaining and setting a\n   limit; separation; distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demarch', 'n.', 'March; walk; gait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demarch', 'n.', 'A chief or ruler of a deme or district in Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demarkation', 'n.', 'Same as Demarcation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dematerialize', 'v. t.', 'To deprive of material or physical qualities or\n   characteristics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deme', 'n.', 'A territorial subdivision of Attica (also of modern Greece),\n   corresponding to a township.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deme', 'n.', 'An undifferentiated aggregate of cells or plastids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demeaned', 'imp. & p. p.', 'of Demean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demeaning', 'p. pr. & vb. n.', 'of Demean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demean', 'v. t.', 'To manage; to conduct; to treat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demean', 'v. t.', 'To conduct; to behave; to comport; -- followed by the\n   reflexive pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demean', 'v. t.', 'To debase; to lower; to degrade; -- followed by the\n   reflexive pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demean', 'v. t.', 'Management; treatment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demean', 'v. t.', 'Behavior; conduct; bearing; demeanor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demean', 'n.', 'Demesne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demean', 'n.', 'Resources; means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demeanance', 'n.', 'Demeanor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demeanor', 'v. t.', 'Management; treatment; conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demeanor', 'v. t.', 'Behavior; deportment; carriage; bearing; mien.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demeanure', 'n.', 'Behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demency', 'n.', 'Dementia; loss of mental powers. See Insanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dement', 'v. t.', 'To deprive of reason; to make mad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dement', 'a.', 'Demented; dementate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dementate', 'v. t.', 'Deprived of reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dementate', 'v. t.', 'To deprive of reason; to dement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dementation', 'n.', 'The act of depriving of reason; madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demented', 'a.', 'Insane; mad; of unsound mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dementia', 'n.', 'Insanity; madness; esp. that form which consists in\n   weakness or total loss of thought and reason; mental imbecility;\n   idiocy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demephitized', 'imp. & p. p.', 'of Demephitize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demephitizing', 'p. pr. & vb. n.', 'of Demephitize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demephitize', 'v. t.', 'To purify from mephitic or foul air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demerge', 'v. t.', 'To plunge down into; to sink; to immerse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demerit', 'n.', 'That which one merits or deserves, either of good or ill;\n   desert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demerit', 'n.', 'That which deserves blame; ill desert; a fault; a vice;\n   misconduct; -- the opposite of merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demerit', 'n.', 'The state of one who deserves ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demerit', 'n.', 'To deserve; -- said in reference to both praise and blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demerit', 'n.', 'To depreciate or cry down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demerit', 'v. i.', 'To deserve praise or blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demerse', 'v. t.', 'To immerse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demersed', 'a.', 'Situated or growing under water, as leaves; submersed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demersion', 'n.', 'The act of plunging into a fluid; a drowning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demersion', 'n.', 'The state of being overwhelmed in water, or as if in\n   water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demesmerize', 'v. t.', 'To relieve from mesmeric influence. See Mesmerize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demesne', 'n.', 'A lords chief manor place, with that part of the lands\n   belonging thereto which has not been granted out in tenancy; a house,\n   and the land adjoining, kept for the proprietors own use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demesnial', 'a.', 'Of or pertaining to a demesne; of the nature of a\n   demesne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demi-', '', 'A prefix, signifying half.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demi', 'n.', 'See Demy, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demibastion', 'n.', 'A half bastion, or that part of a bastion consisting\n   of one face and one flank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demibrigade', 'n.', 'A half brigade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demicadence', 'n.', 'An imperfect or half cadence, falling on the dominant\n   instead of on the key note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demicannon', 'n.', 'A kind of ordnance, carrying a ball weighing from\n   thirty to thirty-six pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demicircle', 'n.', 'An instrument for measuring angles, in surveying, etc.\n   It resembles a protractor, but has an alidade, sights, and a compass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demiculverin', 'n.', 'A kind of ordnance, carrying a ball weighing from\n   nine to thirteen pounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demideify', 'v. t.', 'To deify in part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demidevil', 'n.', 'A half devil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demigod', 'n.', 'A half god, or an inferior deity; a fabulous hero, the\n   offspring of a deity and a mortal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demigoddess', 'n.', 'A female demigod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demigorge', 'n.', 'Half the gorge, or entrance into a bastion, taken from\n   the angle of the flank to the center of the bastion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demigrate', 'v. i.', 'To emigrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demigration', 'n.', 'Emigration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demigroat', 'n.', 'A half groat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demi-island', 'n.', 'Peninsula.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demijohn', 'n.', 'A glass vessel or bottle with a large body and small\n   neck, inclosed in wickerwork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demilance', 'n.', 'A light lance; a short spear; a half pike; also, a\n   demilancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demilancer', 'n.', 'A soldier of light cavalry of the 16th century, who\n   carried a demilance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demilune', 'n.', 'A work constructed beyond the main ditch of a fortress,\n   and in front of the curtain between two bastions, intended to defend\n   the curtain; a ravelin. See Ravelin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demilune', 'n.', 'A crescentic mass of granular protoplasm present in the\n   salivary glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demiman', 'n.', 'A half man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demimonde', 'n.', 'Persons of doubtful reputation; esp., women who are kept\n   as mistresses, though not public prostitutes; demireps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deminatured', 'a.', 'Having half the nature of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demiquaver', 'n.', 'A note of half the length of the quaver; a semiquaver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demirelief', 'n.', 'Alt. of Demirelievo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demirelievo', 'n.', 'Half relief. See Demi-rilievo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demirep', 'n.', 'A woman of doubtful reputation or suspected character; an\n   adventuress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demi-rilievo', 'n.', 'Half relief; sculpture in relief of which the figures\n   project from the background by one half their full roundness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demi-rilievo', 'n.', 'A work of sculpture of the above character. See\n   Alto-rilievo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demisability', 'n.', 'The state of being demisable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demisable', 'a.', 'Capable of being leased; as, a demisable estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demise', 'n.', 'Transmission by formal act or conveyance to an heir or\n   successor; transference; especially, the transfer or transmission of\n   the crown or royal authority to a successor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demise', 'n.', 'The decease of a royal or princely person; hence, also, the\n   death of any illustrious person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demise', 'n.', 'The conveyance or transfer of an estate, either in fee for\n   life or for years, most commonly the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demised', 'imp. & p. p.', 'of Demise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demising', 'p. pr. & vb. n.', 'of Demise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demise', 'v. t.', 'To transfer or transmit by succession or inheritance; to\n   grant or bestow by will; to bequeath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demise', 'v. t.', 'To convey; to give.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demise', 'v. t.', 'To convey, as an estate, by lease; to lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demisemiquaver', 'n.', 'A short note, equal in time to the half of a\n   semiquaver, or the thirty-second part of a whole note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demiss', 'a.', 'Cast down; humble; submissive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demission', 'n.', 'The act of demitting, or the state of being demitted; a\n   letting down; a lowering; dejection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demission', 'n.', 'Resignation of an office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demissionary', 'a.', 'Pertaining to transfer or conveyance; as, a\n   demissionary deed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demissionary', 'a.', 'Tending to lower, depress, or degrade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demissive', 'a.', 'Downcast; submissive; humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demissly', 'adv.', 'In a humble manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demisuit', 'n.', 'A suit of light armor covering less than the whole body,\n   as having no protection for the legs below the thighs, no vizor to the\n   helmet, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demitted', 'imp. & p. p.', 'of Demit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demitting', 'p. pr. & vb. n.', 'of Demit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demit', 'v. t.', 'To let fall; to depress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demit', 'v. t.', 'To yield or submit; to humble; to lower; as, to demit\n   ones self to humble duties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demit', 'v. t.', 'To lay down, as an office; to resign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demitint', 'n.', 'That part of a painting, engraving, or the like, which is\n   neither in full darkness nor full light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demitint', 'n.', 'The shade itself; neither the darkest nor the lightest in\n   a composition. Also called half tint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demitone', 'n.', 'Semitone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demiurge', 'n.', 'The chief magistrate in some of the Greek states.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demiurge', 'n.', 'God, as the Maker of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demiurge', 'n.', 'According to the Gnostics, an agent or one employed by\n   the Supreme Being to create the material universe and man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demiurgic', 'a.', 'Pertaining to a demiurge; formative; creative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demivill', 'n.', 'A half vill, consisting of five freemen or frankpledges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demivolt', 'n.', 'A half vault; one of the seven artificial motions of a\n   horse, in which he raises his fore legs in a particular manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demiwolf', 'n.', 'A half wolf; a mongrel dog, between a dog and a wolf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demobilization', 'n.', 'The disorganization or disarming of troops which\n   have previously been mobilized or called into active service; the\n   change from a war footing to a peace footing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demobilize', 'v. t.', 'To disorganize, or disband and send home, as troops\n   which have been mobilized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democracies', 'pl. ', 'of Democracy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democracy', 'n.', 'Government by the people; a form of government in which\n   the supreme power is retained and directly exercised by the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democracy', 'n.', 'Government by popular representation; a form of\n   government in which the supreme power is retained by the people, but is\n   indirectly exercised through a system of representation and delegated\n   authority periodically renewed; a constitutional representative\n   government; a republic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democracy', 'n.', 'Collectively, the people, regarded as the source of\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democracy', 'n.', 'The principles and policy of the Democratic party, so\n   called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democrat', 'n.', 'One who is an adherent or advocate of democracy, or\n   government by the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democrat', 'n.', 'A member of the Democratic party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democratic', 'a.', 'Pertaining to democracy; favoring democracy, or\n   constructed upon the principle of government by the people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democratic', 'a.', 'Relating to a political party so called.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democratic', 'a.', 'Befitting the common people; -- opposed to\n   aristocratic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democratical', 'a.', 'Democratic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democratically', 'adv.', 'In a democratic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democratism', 'n.', 'The principles or spirit of a democracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democratist', 'n.', 'A democrat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democratize', 'v. t.', 'To render democratic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Democraty', 'n.', 'Democracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demogorgon', 'n.', 'A mysterious, terrible, and evil divinity, regarded by\n   some as the author of creation, by others as a great magician who was\n   supposed to command the spirits of the lower world. See Gorgon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demography', 'n.', 'The study of races, as to births, marriages, mortality,\n   health, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoiselle', 'n.', 'A young lady; a damsel; a ladys maid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoiselle', 'n.', 'The Numidian crane (Anthropoides virgo); -- so called\n   on account of the grace and symmetry of its form and movements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoiselle', 'n.', 'A beautiful, small dragon fly of the genus Agrion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demolished', 'imp. & p. p.', 'of Demolish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demolishing', 'p. pr. & vb. n.', 'of Demolish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demolish', 'v. t.', 'To throw or pull down; to raze; to destroy the fabric\n   of; to pull to pieces; to ruin; as, to demolish an edifice, or a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demolisher', 'n.', 'One who, or that which, demolishes; as, a demolisher of\n   towns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demolishment', 'n.', 'Demolition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demolition', 'n.', 'The act of overthrowing, pulling down, or destroying a\n   pile or structure; destruction by violence; utter overthrow; -- opposed\n   to construction; as, the demolition of a house, of military works, of a\n   town, or of hopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demolitionist', 'n.', 'A demolisher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demon', 'n.', 'A spirit, or immaterial being, holding a middle place\n   between men and deities in pagan mythology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demon', 'n.', 'Ones genius; a tutelary spirit or internal voice; as, the\n   demon of Socrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demon', 'n.', 'An evil spirit; a devil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoness', 'n.', 'A female demon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonetization', 'n.', 'The act of demonetizing, or the condition of being\n   demonetized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonetize', 'v. t.', 'To deprive of current value; to withdraw from use,\n   as money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoniac', 'a.', 'Alt. of Demoniacal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoniacal', 'a.', 'Pertaining to, or characteristic of, a demon or evil\n   spirit; devilish; as, a demoniac being; demoniacal practices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoniacal', 'a.', 'Influenced or produced by a demon or evil spirit; as,\n   demoniac or demoniacal power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoniac', 'n.', 'A human being possessed by a demon or evil spirit; one\n   whose faculties are directly controlled by a demon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoniac', 'n.', 'One of a sect of Anabaptists who maintain that the demons\n   or devils will finally be saved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoniacally', 'adv.', 'In a demoniacal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoniacism', 'n.', 'The state of being demoniac, or the practices of\n   demoniacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonial', 'a.', 'Of or pertaining to a demon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonian', 'a.', 'Relating to, or having the nature of, a demon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonianism', 'n.', 'The state of being possessed by a demon or by demons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoniasm', 'n.', 'See Demonianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonic', 'a.', 'Of or pertaining to a demon or to demons; demoniac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonism', 'n.', 'The belief in demons or false gods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonist', 'n.', 'A believer in, or worshiper of, demons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonized', 'imp. & p. p.', 'of Demonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonizing', 'p. pr. & vb. n.', 'of Demonize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonize', 'v. t.', 'To convert into a demon; to infuse the principles or\n   fury of a demon into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonize', 'v. t.', 'To control or possess by a demon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonocracy', 'n.', 'The power or government of demons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonographer', 'n.', 'A demonologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonolatry', 'n.', 'The worship of demons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonologer', 'n.', 'One versed in demonology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonologic', 'a.', 'Alt. of Demonological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonological', 'a.', 'Of or pertaining to demonology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonologist', 'n.', 'One who writes on, or is versed in, demonology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonology', 'n.', 'A treatise on demons; a supposititious science which\n   treats of demons and their manifestations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonomagy', 'n.', 'Magic in which the aid of demons is invoked; black or\n   infernal magic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonomania', 'n.', 'A form of madness in which the patient conceives\n   himself possessed of devils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonomist', 'n.', 'One in subjection to a demon, or to demons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonomy', 'n.', 'The dominion of demons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonry', 'n.', 'Demoniacal influence or possession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonship', 'n.', 'The state of a demon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrability', 'n.', 'The quality of being demonstrable;\n   demonstrableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrable', 'a.', 'Capable of being demonstrated; that can be proved\n   beyond doubt or question.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrable', 'a.', 'Proved; apparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrableness', 'n.', 'The quality of being demonstrable;\n   demonstrability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrably', 'adv.', 'In a demonstrable manner; incontrovertibly;\n   clearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrance', 'n.', 'Demonstration; proof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrate', 'v. t.', 'To point out; to show; to exhibit; to make evident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrate', 'v. t.', 'To show, or make evident, by reasoning or proof; to\n   prove by deduction; to establish so as to exclude the possibility of\n   doubt or denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrate', 'v. t.', 'To exhibit and explain (a dissection or other\n   anatomical preparation).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrater', 'n.', 'See Demonstrator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstration', 'n.', 'The act of demonstrating; an exhibition; proof;\n   especially, proof beyond the possibility of doubt; indubitable\n   evidence, to the senses or reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstration', 'n.', 'An expression, as of the feelings, by outward signs;\n   a manifestation; a show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstration', 'n.', 'The exhibition and explanation of a dissection or\n   other anatomical preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstration', 'n.', '(Mil.) a decisive exhibition of force, or a movement\n   indicating an attack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstration', 'n.', 'The act of proving by the syllogistic process, or\n   the proof itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstration', 'n.', 'A course of reasoning showing that a certain result\n   is a necessary consequence of assumed premises; -- these premises being\n   definitions, axioms, and previously established propositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrative', 'a.', 'Having the nature of demonstration; tending to\n   demonstrate; making evident; exhibiting clearly or conclusively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrative', 'a.', 'Expressing, or apt to express, much; displaying\n   feeling or sentiment; as, her nature was demonstrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrative', 'a.', 'Consisting of eulogy or of invective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrative', 'n.', 'A demonstrative pronoun; as, \"this\" and \"that\" are\n   demonstratives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstratively', 'adv.', 'In a manner fitted to demonstrate; clearly;\n   convincingly; forcibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrativeness', 'n.', 'The state or quality of being demonstrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrator', 'n.', 'One who demonstrates; one who proves anything with\n   certainty, or establishes it by indubitable evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstrator', 'n.', 'A teacher of practical anatomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demonstratory', 'a.', 'Tending to demonstrate; demonstrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demorage', 'n.', 'Demurrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoralization', 'n.', 'The act of corrupting or subverting morals.\n   Especially: The act of corrupting or subverting discipline, courage,\n   hope, etc., or the state of being corrupted or subverted in discipline,\n   courage, etc.; as, the demoralization of an army or navy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoralized', 'imp. & p. p.', 'of Demoralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoralizing', 'p. pr. & vb. n.', 'of Demoralize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demoralize', 'v. t.', 'To corrupt or undermine in morals; to destroy or\n   lessen the effect of moral principles on; to render corrupt or\n   untrustworthy in morals, in discipline, in courage, spirit, etc.; to\n   weaken in spirit or efficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demosthenic', 'a.', 'Pertaining to, or in the style of, Demosthenes, the\n   Grecian orator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demotic', 'a.', 'Of or pertaining to the people; popular; common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demount', 'v. i.', 'To dismount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dempne', 'v. t.', 'To damn; to condemn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dempster', 'n.', 'Alt. of Demster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demster', 'n.', 'A deemster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demster', 'n.', 'An officer whose duty it was to announce the doom or\n   sentence pronounced by the court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demulce', 'v. t.', 'To soothe; to mollify; to pacify; to soften.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demulcent', 'a.', 'Softening; mollifying; soothing; assuasive; as, oil is\n   demulcent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demulcent', 'n.', 'A substance, usually of a mucilaginous or oily nature,\n   supposed to be capable of soothing an inflamed nervous membrane, or\n   protecting it from irritation. Gum Arabic, glycerin, olive oil, etc.,\n   are demulcents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demulsion', 'n.', 'The act of soothing; that which soothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demurred', 'imp. & p. p.', 'of Demur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demurring', 'p. pr. & vb. n.', 'of Demur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demur', 'v. i.', 'To linger; to stay; to tarry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demur', 'v. i.', 'To delay; to pause; to suspend proceedings or judgment in\n   view of a doubt or difficulty; to hesitate; to put off the\n   determination or conclusion of an affair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demur', 'v. i.', 'To scruple or object; to take exception; as, I demur to\n   that statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demur', 'v. i.', 'To interpose a demurrer. See Demurrer, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demur', 'v. t.', 'To suspend judgment concerning; to doubt of or hesitate\n   about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demur', 'v. t.', 'To cause delay to; to put off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demur', 'v. i.', 'Stop; pause; hesitation as to proceeding; suspense of\n   decision or action; scruple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demure', 'a.', 'Of sober or serious mien; composed and decorous in bearing;\n   of modest look; staid; grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demure', 'a.', 'Affectedly modest, decorous, or serious; making a show of\n   gravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demure', 'v. i.', 'To look demurely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demurely', 'adv.', 'In a demure manner; soberly; gravely; -- now, commonly,\n   with a mere show of gravity or modesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demureness', 'n.', 'The state of being demure; gravity; the show of gravity\n   or modesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demurity', 'n.', 'Demureness; also, one who is demure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demurrable', 'a.', 'That may be demurred to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demurrage', 'n.', 'The detention of a vessel by the freighter beyond the\n   time allowed in her charter party for loading, unloading, or sailing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demurrage', 'n.', 'The allowance made to the master or owner of the ship\n   for such delay or detention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demurral', 'n.', 'Demur; delay in acting or deciding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demurrer', 'n.', 'One who demurs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demurrer', 'n.', 'A stop or pause by a party to an action, for the judgment\n   of the court on the question, whether, assuming the truth of the matter\n   alleged by the opposite party, it is sufficient in law to sustain the\n   action or defense, and hence whether the party resting is bound to\n   answer or proceed further.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demies', 'pl. ', 'of Demy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demy', 'n.', 'A printing and a writing paper of particular sizes. See under\n   Paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demy', 'n.', 'A half fellow at Magdalen College, Oxford.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Demy', 'a.', 'Pertaining to, or made of, the size of paper called demy; as,\n   a demy book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Den', 'n.', 'A small cavern or hollow place in the side of a hill, or among\n   rocks; esp., a cave used by a wild beast for shelter or concealment;\n   as, a lions den; a den of robbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Den', 'n.', 'A squalid place of resort; a wretched dwelling place; a haunt;\n   as, a den of vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Den', 'n.', 'Any snug or close retreat where one goes to be alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Den', 'n.', 'A narrow glen; a ravine; a dell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Den', 'v. i.', 'To live in, or as in, a den.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denarcotize', 'v. t.', 'To deprive of narcotine; as, to denarcotize opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denarii', 'pl. ', 'of Denarius');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denarius', 'n.', 'A Roman silver coin of the value of about fourteen cents;\n   the \"penny\" of the New Testament; -- so called from being worth\n   originally ten of the pieces called as.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denary', 'a.', 'Containing ten; tenfold; proceeding by tens; as, the\n   denary, or decimal, scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denary', 'n.', 'The number ten; a division into ten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denary', 'n.', 'A coin; the Anglicized form of denarius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denationalization', 'n.', 'The or process of denationalizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denationalized', 'imp. & p. p.', 'of Denationalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denationalizing', 'p. pr. & vb. n.', 'of Denationalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denationalize', 'v. t.', 'To divest or deprive of national character or\n   rights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denaturalized', 'imp. & p. p.', 'of Denaturalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denaturalizing', 'p. pr. & vb. n.', 'of Denaturalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denaturalize', 'v. t.', 'To render unnatural; to alienate from nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denaturalize', 'v. t.', 'To renounce the natural rights and duties of; to\n   deprive of citizenship; to denationalize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denay', 'v. t.', 'To deny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denay', 'n.', 'Denial; refusal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendrachate', 'n.', 'Arborescent or dendritic agate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendriform', 'a.', 'Resembling in structure a tree or shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendrite', 'n.', 'A stone or mineral on or in which are branching figures\n   resembling shrubs or trees, produced by a foreign mineral, usually an\n   oxide of manganese, as in the moss agate; also, a crystallized mineral\n   having an arborescent form, e. g., gold or silver; an arborization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendritic', 'a.', 'Alt. of Dendritical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendritical', 'a.', 'Pertaining to a dendrite, or to arborescent\n   crystallization; having a form resembling a shrub or tree; arborescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendroc/la', 'n. pl.', 'A division of the Turbellaria in which the\n   digestive cavity gives off lateral branches, which are often divided\n   into smaller branchlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendroid', 'a.', 'Alt. of Dendroidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendroidal', 'a.', 'Resembling a shrub or tree in form; treelike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendrolite', 'n.', 'A petrified or fossil shrub, plant, or part of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendrologist', 'n.', 'One versed in the natural history of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendrologous', 'a.', 'Relating to dendrology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendrology', 'n.', 'A discourse or treatise on trees; the natural history\n   of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dendrometer', 'n.', 'An instrument to measure the height and diameter of\n   trees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denegate', 'v. t.', 'To deny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denegation', 'n.', 'Denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dengue', 'n.', 'A specific epidemic disease attended with high fever,\n   cutaneous eruption, and severe pains in the head and limbs, resembling\n   those of rheumatism; -- called also breakbone fever. It occurs in\n   India, Egypt, the West Indies, etc., is of short duration, and rarely\n   fatal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deniable', 'a.', 'Capable of being, or liable to be, denied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denial', 'n.', 'The act of gainsaying, refusing, or disowning; negation; --\n   the contrary of affirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denial', 'n.', 'A refusal to admit the truth of a statement, charge,\n   imputation, etc.; assertion of the untruth of a thing stated or\n   maintained; a contradiction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denial', 'n.', 'A refusal to grant; rejection of a request.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denial', 'n.', 'A refusal to acknowledge; disclaimer of connection with;\n   disavowal; -- the contrary of confession; as, the denial of a fault\n   charged on one; a denial of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deniance', 'n.', 'Denial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denier', 'n.', 'One who denies; as, a denier of a fact, or of the faith, or\n   of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denier', 'n.', 'A small copper coin of insignificant value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denigrate', 'v. t.', 'To blacken thoroughly; to make very black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denigrate', 'v. t.', 'Fig.: To blacken or sully; to defame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denigration', 'n.', 'The act of making black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denigration', 'n.', 'Fig.: A blackening; defamation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denigrator', 'n.', 'One who, or that which, blackens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denim', 'n.', 'A coarse cotton drilling used for overalls, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denitration', 'n.', 'A disengaging, or removal, of nitric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denitrification', 'n.', 'The act or process of freeing from nitrogen; also,\n   the condition resulting from the removal of nitrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denitrify', 'v. t.', 'To deprive of, or free from, nitrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denization', 'n.', 'The act of making one a denizen or adopted citizen;\n   naturalization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denize', 'v. t.', 'To make a denizen; to confer the rights of citizenship\n   upon; to naturalize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denizen', 'n.', 'A dweller; an inhabitant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denizen', 'n.', 'One who is admitted by favor to all or a part of the\n   rights of citizenship, where he did not possess them by birth; an\n   adopted or naturalized citizen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denizen', 'n.', 'One admitted to residence in a foreign country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denizen', 'v. t.', 'To constitute (one) a denizen; to admit to residence,\n   with certain rights and privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denizen', 'v. t.', 'To provide with denizens; to populate with adopted or\n   naturalized occupants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denizenation', 'n.', 'Denization; denizening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denizenize', 'v. t.', 'To constitute (one) a denizen; to denizen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denizenship', 'n.', 'State of being a denizen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denmark satin', '', 'See under Satin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dennet', 'n.', 'A light, open, two-wheeled carriage for one horse; a kind\n   of gig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominable', 'a.', 'Capable of being denominated or named.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominated', 'imp. & p. p.', 'of Denominate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominating', 'p. pr. & vb. n.', 'of Denominate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominate', 'v. t.', 'To give a name to; to characterize by an epithet; to\n   entitle; to name; to designate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominate', 'a.', 'Having a specific name or denomination; specified in\n   the concrete as opposed to abstract; thus, 7 feet is a denominate\n   quantity, while 7 is mere abstract quantity or number. See Compound\n   number, under Compound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denomination', 'n.', 'The act of naming or designating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denomination', 'n.', 'That by which anything is denominated or styled; an\n   epithet; a name, designation, or title; especially, a general name\n   indicating a class of like individuals; a category; as, the\n   denomination of units, or of thousands, or of fourths, or of shillings,\n   or of tons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denomination', 'n.', 'A class, or society of individuals, called by the\n   same name; a sect; as, a denomination of Christians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominational', 'a.', 'Pertaining to a denomination, especially to a sect\n   or society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominationalism', 'n.', 'A denominational or class spirit or policy;\n   devotion to the interests of a sect or denomination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominationalist', 'n.', 'One imbued with a denominational spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominationally', 'adv.', 'In a denominational manner; by denomination or\n   sect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominative', 'a.', 'Conferring a denomination or name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominative', 'a.', 'Connotative; as, a denominative name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominative', 'a.', 'Possessing, or capable of possessing, a distinct\n   denomination or designation; denominable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominative', 'a.', 'Derived from a substantive or an adjective; as, a\n   denominative verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominative', 'n.', 'A denominative name or term; denominative verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominatively', 'adv.', 'By denomination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominator', 'n.', 'One who, or that which, gives a name; origin or source\n   of a name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominator', 'n.', 'That number placed below the line in vulgar fractions\n   which shows into how many parts the integer or unit is divided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denominator', 'n.', 'That part of any expression under a fractional form\n   which is situated below the horizontal line signifying division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denotable', 'a.', 'Capable of being denoted or marked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denotate', 'v. t.', 'To mark off; to denote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denotation', 'n.', 'The marking off or separation of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denotative', 'a.', 'Having power to denote; designating or marking off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denoted', 'imp. & p. p.', 'of Denote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denoting', 'p. pr. & vb. n.', 'of Denote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denote', 'v. t.', 'To mark out plainly; to signify by a visible sign; to\n   serve as the sign or name of; to indicate; to point out; as, the hands\n   of the clock denote the hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denote', 'v. t.', 'To be the sign of; to betoken; to signify; to mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denotement', 'n.', 'Sign; indication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denotive', 'a.', 'Serving to denote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denouement', 'n.', 'The unraveling or discovery of a plot; the catastrophe,\n   especially of a drama or a romance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denouement', 'n.', 'The solution of a mystery; issue; outcome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denounced', 'imp. & p. p.', 'of Denounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denouncing', 'p. pr. & vb. n.', 'of Denounce');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denounce', 'v. t.', 'To make known in a solemn or official manner; to\n   declare; to proclaim (especially an evil).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denounce', 'v. t.', 'To proclaim in a threatening manner; to threaten by\n   some outward sign or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denounce', 'v. t.', 'To point out as deserving of reprehension or\n   punishment, etc.; to accuse in a threatening manner; to invoke censure\n   upon; to stigmatize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denouncement', 'n.', 'Solemn, official, or menacing announcement;\n   denunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denouncer', 'n.', 'One who denounces, or declares, as a menace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dense', 'a.', 'Having the constituent parts massed or crowded together;\n   close; compact; thick; containing much matter in a small space; heavy;\n   opaque; as, a dense crowd; a dense forest; a dense fog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dense', 'a.', 'Stupid; gross; crass; as, dense ignorance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Densely', 'adv.', 'In a dense, compact manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denseless', 'n.', 'The quality of being dense; density.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Densimeter', 'n.', 'An instrument for ascertaining the specific gravity or\n   density of a substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Density', 'n.', 'The quality of being dense, close, or thick; compactness;\n   -- opposed to rarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Density', 'n.', 'The ratio of mass, or quantity of matter, to bulk or\n   volume, esp. as compared with the mass and volume of a portion of some\n   substance used as a standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Density', 'n.', 'Depth of shade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dent', 'n.', 'A stroke; a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dent', 'n.', 'A slight depression, or small notch or hollow, made by a blow\n   or by pressure; an indentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dented', 'imp. & p. p.', 'of Dent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denting', 'p. pr. & vb. n.', 'of Dent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dent', 'v. t.', 'To make a dent upon; to indent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dent', 'n.', 'A tooth, as of a card, a gear wheel, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dental', 'a.', 'Of or pertaining to the teeth or to dentistry; as, dental\n   surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dental', 'a.', 'Formed by the aid of the teeth; -- said of certain\n   articulations and the letters representing them; as, d t are dental\n   letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dental', 'a.', 'An articulation or letter formed by the aid of the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dental', 'a.', 'A marine mollusk of the genus Dentalium, with a curved\n   conical shell resembling a tooth. See Dentalium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentalism', 'n.', 'The quality of being formed by the aid of the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentalium', 'n.', 'A genus of marine mollusks belonging to the Scaphopoda,\n   having a tubular conical shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentary', 'a.', 'Pertaining to, or bearing, teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentary', 'n.', 'The distal bone of the lower jaw in many animals, which\n   may or may not bear teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentate', 'a.', 'Alt. of Dentated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentated', 'a.', 'Toothed; especially, with the teeth projecting straight\n   out, not pointed either forward or backward; as, a dentate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentated', 'a.', 'Having teeth or toothlike points. See Illust. of\n   Antennae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentate-ciliate', 'a.', 'Having the margin dentate and also ciliate or\n   fringed with hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentately', 'adv.', 'In a dentate or toothed manner; as, dentately\n   ciliated, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentate-sinuate', 'a.', 'Having a form intermediate between dentate and\n   sinuate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentation', 'n.', 'Formation of teeth; toothed form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dented', 'v. t.', 'Indented; impressed with little hollows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentel', 'n.', 'Same as Dentil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentelle', 'n.', 'An ornamental tooling like lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentelli', 'n. pl.', 'Modillions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentex', 'n.', 'An edible European marine fish (Sparus dentex, or Dentex\n   vulgaris) of the family Percidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denticete', 'n. pl.', 'The division of Cetacea in which the teeth are\n   developed, including the sperm whale, dolphins, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denticle', 'n.', 'A small tooth or projecting point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denticulate', 'a.', 'Alt. of Denticulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denticulated', 'a.', 'Furnished with denticles; notched into little\n   toothlike projections; as, a denticulate leaf of calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denticulation', 'n.', 'The state of being set with small notches or teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denticulation', 'n.', 'A diminutive tooth; a denticle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentiferous', 'a.', 'Bearing teeth; dentigerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentiform', 'a.', 'Having the form of a tooth or of teeth; tooth-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentifrice', 'n.', 'A powder or other substance to be used in cleaning the\n   teeth; tooth powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentigerous', 'a.', 'Bearing teeth or toothlike structures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentil', 'n.', 'A small square block or projection in cornices, a number of\n   which are ranged in an ornamental band; -- used particularly in the\n   Ionic, Corinthian, and Composite orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentilabial', 'a.', 'Formed by the teeth and the lips, or representing a\n   sound so formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentilabial', 'n.', 'A dentilabial sound or letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentilated', 'a.', 'Toothed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentilation', 'n.', 'Dentition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentilave', 'n.', 'A wash for cleaning the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentile', 'n.', 'A small tooth, like that of a saw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentilingual', 'a.', 'Produced by applying the tongue to the teeth or to\n   the gums; or representing a sound so formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentilingual', 'n.', 'A dentilingual sound or letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentiloquist', 'n.', 'One who speaks through the teeth, that is, with the\n   teeth closed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentiloquy', 'n.', 'The habit or practice of speaking through the teeth, or\n   with them closed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dential', 'a.', 'Of or pertaining to dentine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentine', 'n.', 'The dense calcified substance of which teeth are largely\n   composed. It contains less animal matter than bone, and in the teeth of\n   man is situated beneath the enamel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentiphone', 'n.', 'An instrument which, placed against the teeth, conveys\n   sound to the auditory nerve; an audiphone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentirostres', 'pl. ', 'of Dentiroster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentiroster', 'n.', 'A dentirostral bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentirostral', 'a.', 'Having a toothed bill; -- applied to a group of\n   passerine birds, having the bill notched, and feeding chiefly on\n   insects, as the shrikes and vireos. See Illust. (N) under Beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentirostrate', 'a.', 'Dentirostral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentiscalp', 'n.', 'An instrument for scraping the teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentist', 'n.', 'One whose business it is to clean, extract, or repair\n   natural teeth, and to make and insert artificial ones; a dental\n   surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentistic', 'a.', 'Alt. of Dentistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentistical', 'a.', 'Pertaining to dentistry or to dentists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentistry', 'n.', 'The art or profession of a dentist; dental surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentition', 'n.', 'The development and cutting of teeth; teething.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentition', 'n.', 'The system of teeth peculiar to an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentized', 'imp. & p. p.', 'of Dentize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentizing', 'p. pr. & vb. n.', 'of Dentize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentize', 'v. t. & i.', 'To breed or cut new teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentoid', 'a.', 'Shaped like a tooth; tooth-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dentolingual', 'a.', 'Dentilingual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denture', 'n.', 'An artificial tooth, block, or set of teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denudate', 'v. t.', 'To denude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denudation', 'n.', 'The act of stripping off covering, or removing the\n   surface; a making bare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denudation', 'n.', 'The laying bare of rocks by the washing away of the\n   overlying earth, etc.; or the excavation and removal of them by the\n   action of running water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denude', 'v. t.', 'To divest of all covering; to make bare or naked; to\n   strip; to divest; as, to denude one of clothing, or lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denunciate', 'v. t.', 'To denounce; to condemn publicly or solemnly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denunciation', 'n.', 'Proclamation; announcement; a publishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denunciation', 'n.', 'The act of denouncing; public menace or accusation;\n   the act of inveighing against, stigmatizing, or publicly arraigning;\n   arraignment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denunciation', 'n.', 'That by which anything is denounced; threat of evil;\n   public menace or accusation; arraignment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denunciative', 'a.', 'Same as Denunciatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denunciator', 'n.', 'One who denounces, publishes, or proclaims, especially\n   intended or coming evil; one who threatens or accuses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denunciatory', 'a.', 'Characterized by or containing a denunciation;\n   minatory; accusing; threatening; as, severe and denunciatory language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denutrition', 'n.', 'The opposition of nutrition; the failure of nutrition\n   causing the breaking down of tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denied', 'imp. & p. p.', 'of Deny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denying', 'p. pr. & vb. n.', 'of Deny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deny', 'v. t.', 'To declare not to be true; to gainsay; to contradict; --\n   opposed to affirm, allow, or admit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deny', 'v. t.', 'To refuse (to do something or to accept something); to\n   reject; to decline; to renounce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deny', 'v. t.', 'To refuse to grant; to withhold; to refuse to gratify or\n   yield to; as, to deny a request.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deny', 'v. t.', 'To disclaim connection with, responsibility for, and the\n   like; to refuse to acknowledge; to disown; to abjure; to disavow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deny', 'v. i.', 'To answer in /// negative; to declare an assertion not to\n   be true.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Denyingly', 'adv.', 'In the manner of one denies a request.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deobstruct', 'v. t.', 'To remove obstructions or impediments in; to clear\n   from anything that hinders the passage of fluids; as, to deobstruct the\n   pores or lacteals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deobstruent', 'a.', 'Removing obstructions; having power to clear or open\n   the natural ducts of the fluids and secretions of the body; aperient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deobstruent', 'n.', 'A medicine which removes obstructions; an aperient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deodand', 'n.', 'A personal chattel which had caused the death of a person,\n   and for that reason was given to God, that is, forfeited to the crown,\n   to be applied to pious uses, and distributed in alms by the high\n   almoner. Thus, if a cart ran over a man and killed him, it was\n   forfeited as a deodand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deodar', 'n.', 'A kind of cedar (Cedrus Deodara), growing in India, highly\n   valued for its size and beauty as well as for its timber, and also\n   grown in England as an ornamental tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deodate', 'n.', 'A gift or offering to God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deodorant', 'n.', 'A deodorizer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deodorization', 'n.', 'The act of depriving of odor, especially of\n   offensive odors resulting from impurities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deodorize', 'v. t.', 'To deprive of odor, especially of such as results\n   from impurities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deodorizer', 'n.', 'He who, or that which, deodorizes; esp., an agent that\n   destroys offensive odors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deonerate', 'v. t.', 'To unload; to disburden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deontological', 'a.', 'Pertaining to deontology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deontologist', 'n.', 'One versed in deontology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deontology', 'n.', 'The science relat/ to duty or moral obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deoperculate', 'a.', 'Having the lid removed; -- said of the capsules of\n   mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deoppilate', 'v. t.', 'To free from obstructions; to clear a passage\n   through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deoppilation', 'n.', 'Removal of whatever stops up the passages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deoppilative', 'a. & n.', 'Deobstruent; aperient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deordination', 'n.', 'Disorder; dissoluteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deosculate', 'v. t.', 'To kiss warmly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deoxidate', 'v. t.', 'To deoxidize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deoxidation', 'n.', 'The act or process of reducing from the state of an\n   oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deoxidization', 'n.', 'Deoxidation.');
