  INSERT INTO words (word, word_type, description) VALUES  (    'Depopulate', 'v. t.', 'To deprive of inhabitants, whether by death or by\n   expulsion; to reduce greatly the populousness of; to dispeople; to\n   unpeople.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depopulate', 'v. i.', 'To become dispeopled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depopulation', 'n.', 'The act of depopulating, or condition of being\n   depopulated; destruction or explusion of inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depopulator', 'n.', 'One who depopulates; a dispeopler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deported', 'imp. & p. p.', 'of Deport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deporting', 'p. pr. & vb. n.', 'of Deport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deport', 'v. t.', 'To transport; to carry away; to exile; to send into\n   banishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deport', 'v. t.', 'To carry or demean; to conduct; to behave; -- followed\n   by the reflexive pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deport', 'n.', 'Behavior; carriage; demeanor; deportment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deportation', 'n.', 'The act of deporting or exiling, or the state of being\n   deported; banishment; transportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deportment', 'n.', 'Manner of deporting or demeaning ones self; manner of\n   acting; conduct; carriage; especially, manner of acting with respect to\n   the courtesies and duties of life; behavior; demeanor; bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deporture', 'n.', 'Deportment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposable', 'a.', 'Capable of being deposed or deprived of office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposal', 'n.', 'The act of deposing from office; a removal from the\n   throne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposed', 'imp. & p. p.', 'of Depose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposing', 'p. pr. & vb. n.', 'of Depose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depose', 'v. t.', 'To lay down; to divest ones self of; to lay aside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depose', 'v. t.', 'To let fall; to deposit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depose', 'v. t.', 'To remove from a throne or other high station; to\n   dethrone; to divest or deprive of office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depose', 'v. t.', 'To testify under oath; to bear testimony to; -- now\n   usually said of bearing testimony which is officially written down for\n   future use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depose', 'v. t.', 'To put under oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depose', 'v. i.', 'To bear witness; to testify under oath; to make\n   deposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposer', 'n.', 'One who deposes or degrades from office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposer', 'n.', 'One who testifies or deposes; a deponent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposited', 'imp. & p. p.', 'of Deposit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depositing', 'p. pr. & vb. n.', 'of Deposit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposit', 'n.', 'To lay down; to place; to put; to let fall or throw down\n   (as sediment); as, a crocodile deposits her eggs in the sand; the\n   waters deposited a rich alluvium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposit', 'n.', 'To lay up or away for safe keeping; to put up; to store;\n   as, to deposit goods in a warehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposit', 'n.', 'To lodge in some ones hands for safe keeping; to commit\n   to the custody of another; to intrust; esp., to place in a bank, as a\n   sum of money subject to order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposit', 'n.', 'To lay aside; to rid ones self of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposit', 'v. t.', 'That which is deposited, or laid or thrown down; as, a\n   deposit in a flue; especially, matter precipitated from a solution (as\n   the siliceous deposits of hot springs), or that which is mechanically\n   deposited (as the mud, gravel, etc., deposits of a river).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposit', 'v. t.', 'A natural occurrence of a useful mineral under the\n   conditions to invite exploitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposit', 'v. t.', 'That which is placed anywhere, or in any ones hands,\n   for safe keeping; something intrusted to the care of another; esp.,\n   money lodged with a bank or banker, subject to order; anything given as\n   pledge or security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposit', 'v. t.', 'A bailment of money or goods to be kept gratuitously\n   for the bailor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposit', 'v. t.', 'Money lodged with a party as earnest or security for\n   the performance of a duty assumed by the person depositing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposit', 'v. t.', 'A place of deposit; a depository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depositaries', 'pl. ', 'of Depositary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depositary', 'n.', 'One with whom anything is lodged in the trust; one who\n   receives a deposit; -- the correlative of depositor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depositary', 'n.', 'A storehouse; a depository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depositary', 'n.', 'One to whom goods are bailed, to be kept for the bailor\n   without a recompense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposition', 'n.', 'The act of depositing or deposing; the act of laying\n   down or thrown down; precipitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposition', 'n.', 'The act of bringing before the mind; presentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposition', 'n.', 'The act of setting aside a sovereign or a public\n   officer; deprivation of authority and dignity; displacement; removal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposition', 'n.', 'That which is deposited; matter laid or thrown down;\n   sediment; alluvial matter; as, banks are sometimes depositions of\n   alluvial matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposition', 'n.', 'An opinion, example, or statement, laid down or\n   asserted; a declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deposition', 'n.', 'The act of laying down ones testimony in writing;\n   also, testimony laid or taken down in writing, under oath or\n   affirmation, before some competent officer, and in reply to\n   interrogatories and cross-interrogatories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depositor', 'n.', 'One who makes a deposit, especially of money in a bank;\n   -- the correlative of depository.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depositories', 'pl. ', 'of Depository');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depository', 'n.', 'A place where anything is deposited for sale or\n   keeping; as, warehouse is a depository for goods; a clerks office is a\n   depository for records.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depository', 'n.', 'One with whom something is deposited; a depositary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depositum', 'n.', 'Deposit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depositure', 'n.', 'The act of depositing; deposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depot', 'n.', 'A place of deposit for the storing of goods; a warehouse; a\n   storehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depot', 'n.', 'A military station where stores and provisions are kept, or\n   where recruits are assembled and drilled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depot', 'n.', 'The headquarters of a regiment, where all supplies are\n   received and distributed, recruits are assembled and instructed, infirm\n   or disabled soldiers are taken care of, and all the wants of the\n   regiment are provided for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depot', 'n.', 'A railway station; a building for the accommodation and\n   protection of railway passengers or freight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depper', 'a.', 'Deeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depravation', 'n.', 'Detraction; depreciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depravation', 'n.', 'The act of depraving, or making anything bad; the act\n   of corrupting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depravation', 'n.', 'The state of being depraved or degenerated;\n   degeneracy; depravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depravation', 'n.', 'Change for the worse; deterioration; morbid\n   perversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depraved', 'imp. & p. p.', 'of Deprave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depraving', 'p. pr. & vb. n.', 'of Deprave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprave', 'n. t.', 'To speak ill of; to depreciate; to malign; to revile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprave', 'n. t.', 'To make bad or worse; to vitiate; to corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depravedly', 'adv.', 'In a depraved manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depravedness', 'n.', 'Depravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depravement', 'n.', 'Depravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depraver', 'n.', 'One who deprave or corrupts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depravingly', 'adv.', 'In a depraving manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depravity', 'n.', 'The state of being depraved or corrupted; a vitiated\n   state of moral character; general badness of character; wickedness of\n   mind or heart; absence of religious feeling and principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecable', 'a.', 'That may or should be deprecated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecated', 'imp. & p. p.', 'of Deprecate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecating', 'p. pr. & vb. n.', 'of Deprecate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecate', 'v. t.', 'To pray against, as an evil; to seek to avert by\n   prayer; to desire the removal of; to seek deliverance from; to express\n   deep regret for; to disapprove of strongly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecatingly', 'adv.', 'In a deprecating manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecation', 'n.', 'The act of deprecating; a praying against evil; prayer\n   that an evil may be removed or prevented; strong expression of\n   disapprobation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecation', 'n.', 'Entreaty for pardon; petitioning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecation', 'n.', 'An imprecation or curse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecative', 'a.', 'Serving to deprecate; deprecatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecator', 'n.', 'One who deprecates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprecatory', 'a.', 'Serving to deprecate; tending to remove or avert evil\n   by prayer; apologetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreciated', 'imp. & p. p.', 'of Depreciate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreciating', 'p. pr. & vb. n.', 'of Depreciate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreciate', 'v. t.', 'To lessen in price or estimated value; to lower the\n   worth of; to represent as of little value or claim to esteem; to\n   undervalue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreciate', 'v. i.', 'To fall in value; to become of less worth; to sink\n   in estimation; as, a paper currency will depreciate, unless it is\n   convertible into specie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreciation', 'n.', 'The act of lessening, or seeking to lessen, price,\n   value, or reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreciation', 'n.', 'The falling of value; reduction of worth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreciation', 'n.', 'the state of being depreciated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreciative', 'a.', 'Tending, or intended, to depreciate; expressing\n   depreciation; undervaluing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreciator', 'n.', 'One who depreciates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreciatory', 'a.', 'Tending to depreciate; undervaluing; depreciative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depredable', 'a.', 'Liable to depredation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depredated', 'imp. & p. p.', 'of Depredate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depredating', 'p. pr. & vb. n.', 'of Depredate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depredate', 'v. t.', 'To subject to plunder and pillage; to despoil; to lay\n   waste; to prey upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depredate', 'v. i.', 'To take plunder or prey; to commit waste; as, the\n   troops depredated on the country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depredation', 'n.', 'The act of depredating, or the state of being\n   depredated; the act of despoiling or making inroads; as, the sea often\n   makes depredation on the land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depredator', 'n.', 'One who plunders or pillages; a spoiler; a robber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depredatory', 'a.', 'Tending or designed to depredate; characterized by\n   depredation; plundering; as, a depredatory incursion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depreicate', 'v. t.', 'To proclaim; to celebrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprehended', 'imp. & p. p.', 'of Deprehend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprehending', 'p. pr. & vb. n.', 'of Deprehend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprehend', 'v. t.', 'To take unwares or by surprise; to seize, as a person\n   commiting an unlawful act; to catch; to apprehend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprehend', 'v. t.', 'To detect; to discover; to find out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprehensible', 'a.', 'That may be caught or discovered; apprehensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprehension', 'n.', 'A catching; discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressed', 'imp. & p. p.', 'of Depress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressing', 'p. pr. & vb. n.', 'of Depress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depress', 'v. t.', 'To press down; to cause to sink; to let fall; to lower;\n   as, to depress the muzzle of a gun; to depress the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depress', 'v. t.', 'To bring down or humble; to abase, as pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depress', 'v. t.', 'To cast a gloom upon; to sadden; as, his spirits were\n   depressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depress', 'v. t.', 'To lessen the activity of; to make dull; embarrass, as\n   trade, commerce, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depress', 'v. t.', 'To lessen in price; to cause to decline in value; to\n   cheapen; to depreciate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depress', 'v. t.', 'To reduce (an equation) in a lower degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depress', 'a.', 'Having the middle lower than the border; concave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressant', 'n.', 'An agent or remedy which lowers the vital powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressed', 'a.', 'Pressed or forced down; lowed; sunk; dejected;\n   dispirited; sad; humbled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressed', 'a.', 'Concave on the upper side; -- said of a leaf whose disk\n   is lower than the border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressed', 'a.', 'Lying flat; -- said of a stem or leaf which lies close\n   to the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressed', 'a.', 'Having the vertical diameter shorter than the horizontal\n   or transverse; -- said of the bodies of animals, or of parts of the\n   bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressingly', 'adv.', 'In a depressing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depression', 'n.', 'The act of depressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depression', 'n.', 'The state of being depressed; a sinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depression', 'n.', 'A falling in of the surface; a sinking below its true\n   place; a cavity or hollow; as, roughness consists in little\n   protuberances and depressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depression', 'n.', 'Humiliation; abasement, as of pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depression', 'n.', 'Dejection; despondency; lowness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depression', 'n.', 'Diminution, as of trade, etc.; inactivity; dullness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depression', 'n.', 'The angular distance of a celestial object below the\n   horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depression', 'n.', 'The operation of reducing to a lower degree; -- said of\n   equations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depression', 'n.', 'A method of operating for cataract; couching. See\n   Couch, v. t., 8.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressive', 'a.', 'Able or tending to depress or cast down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressomotor', 'a.', 'Depressing or diminishing the capacity for movement,\n   as depressomotor nerves, which lower or inhibit muscular activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressomotor', 'n.', 'Any agent that depresses the activity of the motor\n   centers, as bromides, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressor', 'n.', 'One who, or that which, presses down; an oppressor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depressor', 'n.', 'A muscle that depresses or tends to draw down a part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depriment', 'a.', 'Serving to depress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprisure', 'n.', 'Low estimation; disesteem; contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprivable', 'a.', 'Capable of being, or liable to be, deprived; liable to\n   be deposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprivation', 'n.', 'The act of depriving, dispossessing, or bereaving; the\n   act of deposing or divesting of some dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprivation', 'n.', 'The state of being deprived; privation; loss; want;\n   bereavement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprivation', 'n.', 'the taking away from a clergyman his benefice, or\n   other spiritual promotion or dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprived', 'imp. & p. p.', 'of Deprive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depriving', 'p. pr. & vb. n.', 'of Deprive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprive', 'v. t.', 'To take away; to put an end; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprive', 'v. t.', 'To dispossess; to bereave; to divest; to hinder from\n   possessing; to debar; to shut out from; -- with a remoter object,\n   usually preceded by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprive', 'v. t.', 'To divest of office; to depose; to dispossess of\n   dignity, especially ecclesiastical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprivement', 'n.', 'Deprivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depriver', 'n.', 'One who, or that which, deprives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprostrate', 'a.', 'Fully prostrate; humble; low; rude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deprovincialize', 'v. t.', 'To divest of provincial quality or\n   characteristics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depth', 'n.', 'The quality of being deep; deepness; perpendicular\n   measurement downward from the surface, or horizontal measurement\n   backward from the front; as, the depth of a river; the depth of a body\n   of troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depth', 'n.', 'Profoundness; extent or degree of intensity; abundance;\n   completeness; as, depth of knowledge, or color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depth', 'n.', 'Lowness; as, depth of sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depth', 'n.', 'That which is deep; a deep, or the deepest, part or place;\n   the deep; the middle part; as, the depth of night, or of winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depth', 'n.', 'The number of simple elements which an abstract conception\n   or notion includes; the comprehension or content.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depth', 'n.', 'A pair of toothed wheels which work together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depthen', 'v. t.', 'To deepen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depthless', 'a.', 'Having no depth; shallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depthless', 'a.', 'Of measureless depth; unfathomable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depucelate', 'v. t.', 'To deflour; to deprive of virginity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depudicate', 'v. t.', 'To deflour; to dishonor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depulse', 'v. t.', 'To drive away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depulsion', 'n.', 'A driving or thrusting away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depulsory', 'a.', 'Driving or thrusting away; averting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depurant', 'a. & n.', 'Depurative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depurate', 'a.', 'Depurated; cleansed; freed from impurities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depurated', 'imp. & p. p.', 'of Depurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depurating', 'p. pr. & vb. n.', 'of Depurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depurate', 'v. t.', 'To free from impurities, heterogeneous matter, or\n   feculence; to purify; to cleanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depuration', 'n.', 'The act or process of depurating or freeing from\n   foreign or impure matter, as a liquid or wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depurative', 'a.', 'Purifying the blood or the humors; depuratory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depurative', 'n.', 'A depurative remedy or agent; or a disease which is\n   believed to be depurative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depurator', 'n.', 'One who, or that which, cleanses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depuratory', 'a.', 'Depurating; tending to depurate or cleanse; depurative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depure', 'v. t.', 'To depurate; to purify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depurgatory', 'a.', 'Serving to purge; tending to cleanse or purify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depurition', 'n.', 'See Depuration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deputable', 'a.', 'Fit to be deputed; suitable to act as a deputy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deputation', 'n.', 'The act of deputing, or of appointing or commissioning\n   a deputy or representative; office of a deputy or delegate;\n   vicegerency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deputation', 'n.', 'The person or persons deputed or commissioned by\n   another person, party, or public body to act in his or its behalf;\n   delegation; as, the general sent a deputation to the enemy to propose a\n   truce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deputator', 'n.', 'One who deputes, or makes a deputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deputed', 'imp. & p. p.', 'of Depute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deputing', 'p. pr. & vb. n.', 'of Depute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depute', 'v. t.', 'To appoint as deputy or agent; to commission to act in\n   ones place; to delegate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depute', 'v. t.', 'To appoint; to assign; to choose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Depute', 'n.', 'A person deputed; a deputy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deputize', 'v. t.', 'To appoint as ones deputy; to empower to act in ones\n   stead; to depute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deputies', 'pl. ', 'of Deputy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deputy', 'n.', 'One appointed as the substitute of another, and empowered\n   to act for him, in his name or his behalf; a substitute in office; a\n   lieutenant; a representative; a delegate; a vicegerent; as, the deputy\n   of a prince, of a sheriff, of a township, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deputy', 'n.', 'A member of the Chamber of Deputies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dequantitate', 'v. t.', 'To diminish the quantity of; to disquantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deracinated', 'imp. & p. p.', 'of Deracinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deracinating', 'p. pr. & vb. n.', 'of Deracinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deracinate', 'v. t.', 'To pluck up by the roots; to extirpate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deraination', 'n.', 'The act of pulling up by the roots; eradication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deraign', 'v. t.', 'Alt. of Derain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derain', 'v. t.', 'To prove or to refute by proof; to clear (ones self).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deraignment', 'n.', 'Alt. of Derainment');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derainment', 'n.', 'The act of deraigning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derainment', 'n.', 'The renunciation of religious or monastic vows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derailed', 'imp. & p. p.', 'of Derail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derailing', 'p. pr. & vb. n.', 'of Derail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derail', 'v. t.', 'To cause to run off from the rails of a railroad, as a\n   locomotive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derailment', 'n.', 'The act of going off, or the state of being off, the\n   rails of a railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deranged', 'imp. & p. p.', 'of Derange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deranging', 'p. pr. & vb. n.', 'of Derange');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derange', 'v. t.', 'To put out of place, order, or rank; to disturb the\n   proper arrangement or order of; to throw into disorder, confusion, or\n   embarrassment; to disorder; to disarrange; as, to derange the plans of\n   a commander, or the affairs of a nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derange', 'v. t.', 'To disturb in action or function, as a part or organ,\n   or the whole of a machine or organism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derange', 'v. t.', 'To disturb in the orderly or normal action of the\n   intellect; to render insane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deranged', 'a.', 'Disordered; especially, disordered in mind; crazy;\n   insane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derangement', 'n.', 'The act of deranging or putting out of order, or the\n   state of being deranged; disarrangement; disorder; confusion;\n   especially, mental disorder; insanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deranger', 'n.', 'One who deranges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deray', 'n.', 'Disorder; merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derbio', 'n.', 'A large European food fish (Lichia glauca).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derby', 'n.', 'A race for three-old horses, run annually at Epsom (near\n   London), for the Derby stakes. It was instituted by the 12th Earl of\n   Derby, in 1780.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derby', 'n.', 'A stiff felt hat with a dome-shaped crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derbyshire spar', '', 'A massive variety of fluor spar, found in\n   Derbyshire, England, and wrought into vases and other ornamental work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derdoing', 'v. t.', 'Doing daring or chivalrous deeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dere', 'v. t.', 'To hurt; to harm; to injure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dere', 'n.', 'Harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dereine', 'v. t.', 'Alt. of Dereyne');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dereyne', 'v. t.', 'Same as Darraign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derelict', 'a.', 'Given up or forsaken by the natural owner or guardian;\n   left and abandoned; as, derelict lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derelict', 'a.', 'Lost; adrift; hence, wanting; careless; neglectful;\n   unfaithful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derelict', 'n.', 'A thing voluntary abandoned or willfully cast away by its\n   proper owner, especially a ship abandoned at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derelict', 'n.', 'A tract of land left dry by the sea, and fit for\n   cultivation or use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dereliction', 'n.', 'The act of leaving with an intention not to reclaim or\n   resume; an utter forsaking abandonment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dereliction', 'n.', 'A neglect or omission as if by willful abandonment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dereliction', 'n.', 'The state of being left or abandoned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dereliction', 'n.', 'A retiring of the sea, occasioning a change of\n   high-water mark, whereby land is gained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dereligionize', 'v. t.', 'To make irreligious; to turn from religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dereling', 'n.', 'Darling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dereling', 'n.', 'Darling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derf', 'a.', 'Strong; powerful; fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derided', 'imp. & p. p.', 'of Deride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deriding', 'p. pr. & vb. n.', 'of Deride');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deride', 'v. t.', 'To laugh at with contempt; to laugh to scorn; to turn to\n   ridicule or make sport of; to mock; to scoff at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derider', 'n.', 'One who derides, or laughs at, another in contempt; a\n   mocker; a scoffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deridingly', 'adv.', 'By way of derision or mockery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derision', 'n.', 'The act of deriding, or the state of being derided;\n   mockery; scornful or contemptuous treatment which holds one up to\n   ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derision', 'n.', 'An object of derision or scorn; a laughing-stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derisive', 'a.', 'Expressing, serving for, or characterized by, derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derisory', 'a.', 'Derisive; mocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivable', 'a.', 'That can be derived; obtainable by transmission; capable\n   of being known by inference, as from premises or data; capable of being\n   traced, as from a radical; as, income is derivable from various\n   sources.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivably', 'adv.', 'By derivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derival', 'n.', 'Derivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivate', 'a.', 'Derived; derivative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivate', 'n.', 'A thing derived; a derivative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivate', 'v. t.', 'To derive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivation', 'n.', 'A leading or drawing off of water from a stream or\n   source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivation', 'n.', 'The act of receiving anything from a source; the act of\n   procuring an effect from a cause, means, or condition, as profits from\n   capital, conclusions or opinions from evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivation', 'n.', 'The act of tracing origin or descent, as in grammar or\n   genealogy; as, the derivation of a word from an Aryan root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivation', 'n.', 'The state or method of being derived; the relation of\n   origin when established or asserted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivation', 'n.', 'That from which a thing is derived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivation', 'n.', 'That which is derived; a derivative; a deduction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivation', 'n.', 'The operation of deducing one function from another\n   according to some fixed law, called the law of derivation, as the of\n   differentiation or of integration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivation', 'n.', 'A drawing of humors or fluids from one part of the body\n   to another, to relieve or lessen a morbid process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivational', 'a.', 'Relating to derivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivative', 'a.', 'Obtained by derivation; derived; not radical, original,\n   or fundamental; originating, deduced, or formed from something else;\n   secondary; as, a derivative conveyance; a derivative word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivative', 'n.', 'That which is derived; anything obtained or deduced\n   from another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivative', 'n.', 'A word formed from another word, by a prefix or suffix,\n   an internal modification, or some other change; a word which takes its\n   origin from a root.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivative', 'n.', 'A chord, not fundamental, but obtained from another by\n   inversion; or, vice versa, a ground tone or root implied in its\n   harmonics in an actual chord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivative', 'n.', 'An agent which is adapted to produce a derivation (in\n   the medical sense).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivative', 'n.', 'A derived function; a function obtained from a given\n   function by a certain algebraic process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivative', 'n.', 'A substance so related to another substance by\n   modification or partial substitution as to be regarded as derived from\n   it; thus, the amido compounds are derivatives of ammonia, and the\n   hydrocarbons are derivatives of methane, benzene, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derived', 'imp. & p. p.', 'of Derive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deriving', 'p. pr. & vb. n.', 'of Derive');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derive', 'v. t.', 'To turn the course of, as water; to divert and\n   distribute into subordinate channels; to diffuse; to communicate; to\n   transmit; -- followed by to, into, on, upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derive', 'v. t.', 'To receive, as from a source or origin; to obtain by\n   descent or by transmission; to draw; to deduce; -- followed by from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derive', 'v. t.', 'To trace the origin, descent, or derivation of; to\n   recognize transmission of; as, he derives this word from the\n   Anglo-Saxon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derive', 'v. t.', 'To obtain one substance from another by actual or\n   theoretical substitution; as, to derive an organic acid from its\n   corresponding hydrocarbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derive', 'v. i.', 'To flow; to have origin; to descend; to proceed; to be\n   deduced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derivement', 'n.', 'That which is derived; deduction; inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deriver', 'n.', 'One who derives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derk', 'a.', 'Dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-derm', 'n.', 'A suffix or terminal formative, much used in anatomical\n   terms, and signifying skin, integument, covering; as, blastoderm,\n   ectoderm, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derm', 'v. t.', 'The integument of animal; the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derm', 'v. t.', 'See Dermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derma', 'n.', 'See Dermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermal', 'a.', 'Pertaining to the integument or skin of animals; dermic;\n   as, the dermal secretions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermal', 'a.', 'Pertaining to the dermis or true skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermaptera', 'n.', 'Alt. of Dermapteran');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermapteran', 'n.', 'See Dermoptera, Dermopteran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatic', 'a.', 'Alt. of Dermatine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatine', 'a.', 'Of or pertaining to the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatitis', 'n.', 'Inflammation of the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatogen', 'n.', 'Nascent epidermis, or external cuticle of plants in a\n   forming condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatogen', 'n.', 'Nascent epidermis, or external cuticle of plants in a\n   forming condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatography', 'n.', 'An anatomical description of, or treatise on, the\n   skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatoid', 'a.', 'Resembling skin; skinlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatologist', 'n.', 'One who discourses on the skin and its diseases; one\n   versed in dermatology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatology', 'n.', 'The science which treats of the skin, its structure,\n   functions, and diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatopathic', 'a.', 'Of or pertaining to skin diseases, or their cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermatophyte', 'n.', 'A vegetable parasite, infesting the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermestes', 'n.', 'A genus of coleopterous insects, the larvae of which\n   feed animal substances. They are very destructive to dries meats,\n   skins, woolens, and furs. The most common species is D. lardarius,\n   known as the bacon beetle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermestoid', 'a.', 'Pertaining to or resembling the genus Dermestes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermic', 'a.', 'Relating to the derm or skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermic', 'a.', 'Pertaining to the dermis; dermal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermis', 'n.', 'The deep sensitive layer of the skin beneath the scarfskin\n   or epidermis; -- called also true skin, derm, derma, corium, cutis, and\n   enderon. See Skin, and Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermobranchiata', 'n. pl.', 'A group of nudibranch mollusks without special\n   gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermobranchiate', 'a.', 'Having the skin modified to serve as a gill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermohaemal', 'a.', 'Pertaining to, or in relation with, both dermal and\n   haemal structures; as, the dermohaemal spines or ventral fin rays of\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermoid', 'a.', 'Same as Dermatoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermoneural', 'a.', 'Pertaining to, or in relation with, both dermal and\n   neural structures; as, the dermoneural spines or dorsal fin rays of\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermopathic', 'a.', 'Dermatopathic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermophyte', 'n.', 'A dermatophyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermoptera', 'n. pl.', 'The division of insects which includes the earwigs\n   (Forticulidae).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermoptera', 'n. pl.', 'A group of lemuroid mammals having a parachutelike\n   web of skin between the fore and hind legs, of which the colugo\n   (Galeopithecus) is the type. See Colugo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermoptera', 'n. pl.', 'An order of Mammalia; the Cheiroptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermopteran', 'n.', 'An insect which has the anterior pair of wings\n   coriaceous, and does not use them in flight, as the earwig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermopteri', 'n. pl.', 'Same as Dermopterygii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermopterygii', 'n. pl.', 'A group of fishlike animals including the\n   Marsipobranchiata and Leptocardia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermoskeleton', 'n.', 'See Exoskeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dermostosis', 'n.', 'Ossification of the dermis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dern', 'n.', 'A gatepost or doorpost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dern', 'a.', 'Hidden; concealed; secret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dern', 'a.', 'Solitary; sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derne', 'a.', 'To hide; to skulk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dernful', 'a.', 'Secret; hence, lonely; sad; mournful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dernier', 'a.', 'Last; final.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dernly', 'adv.', 'Secretly; grievously; mournfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogant', 'a.', 'Derogatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogated', 'imp. & p. p.', 'of Derogate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogating', 'p. pr. & vb. n.', 'of Derogate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogate', 'v. t.', 'To annul in part; to repeal partly; to restrict; to\n   limit the action of; -- said of a law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogate', 'v. t.', 'To lessen; to detract from; to disparage; to\n   depreciate; -- said of a person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogate', 'v. i.', 'To take away; to detract; to withdraw; -- usually with\n   from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogate', 'v. i.', 'To act beneath one-s rank, place, birth, or character;\n   to degenerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogate', 'n.', 'Diminished in value; dishonored; degraded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogately', 'adv.', 'In a derogatory manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogation', 'n.', 'The act of derogating, partly repealing, or lessening\n   in value; disparagement; detraction; depreciation; -- followed by of,\n   from, or to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogation', 'n.', 'An alteration of, or subtraction from, a contract for a\n   sale of stocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogative', 'a.', 'Derogatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogator', 'n.', 'A detractor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogatorily', 'adv.', 'In a derogatory manner; disparagingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogatoriness', 'n.', 'Quality of being derogatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derogatory', 'a.', 'Tending to derogate, or lessen in value; expressing\n   derogation; detracting; injurious; -- with from to, or unto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derotremata', 'n. pl.', 'The tribe of aquatic Amphibia which includes\n   Amphiuma, Menopoma, etc. They have permanent gill openings, but no\n   external gills; -- called also Cryptobranchiata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derre', 'a.', 'Dearer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derrick', 'n.', 'A mast, spar, or tall frame, supported at the top by stays\n   or guys, with suitable tackle for hoisting heavy weights, as stones in\n   building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derring', 'a.', 'Daring or warlike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derringer', 'n.', 'A kind of short-barreled pocket pistol, of very large\n   caliber, often carrying a half-ounce ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derth', 'n.', 'Dearth; scarcity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dertrotheca', 'n.', 'The horny covering of the end of the bill of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dervish', 'n.', 'Alt. of Dervis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dervise', 'n.', 'Alt. of Dervis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dervis', 'n.', 'A Turkish or Persian monk, especially one who professes\n   extreme poverty and leads an austere life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Derworth', 'a.', 'Precious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descant', 'v. i.', 'Originally, a double song; a melody or counterpoint\n   sung above the plain song of the tenor; a variation of an air; a\n   variation by ornament of the main subject or plain song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descant', 'v. i.', 'The upper voice in part music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descant', 'v. i.', 'The canto, cantus, or soprano voice; the treble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descant', 'v. i.', 'A discourse formed on its theme, like variations on a\n   musical air; a comment or comments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descanted', 'imp. & p. p.', 'of Descant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descanting', 'p. pr. & vb. n.', 'of Descant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descant', 'v. i.', 'To sing a variation or accomplishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descant', 'v. i.', 'To comment freely; to discourse with fullness and\n   particularity; to discourse at large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descanter', 'n.', 'One who descants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descended', 'imp. & p. p.', 'of Descend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descending', 'p. pr. & vb. n.', 'of Descend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descend', 'v. i.', 'To pass from a higher to a lower place; to move\n   downwards; to come or go down in any way, as by falling, flowing,\n   walking, etc.; to plunge; to fall; to incline downward; -- the opposite\n   of ascend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descend', 'v. i.', 'To enter mentally; to retire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descend', 'v. i.', 'To make an attack, or incursion, as if from a vantage\n   ground; to come suddenly and with violence; -- with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descend', 'v. i.', 'To come down to a lower, less fortunate, humbler, less\n   virtuous, or worse, state or station; to lower or abase ones self; as,\n   he descended from his high estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descend', 'v. i.', 'To pass from the more general or important to the\n   particular or less important matters to be considered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descend', 'v. i.', 'To come down, as from a source, original, or stock; to\n   be derived; to proceed by generation or by transmission; to fall or\n   pass by inheritance; as, the beggar may descend from a prince; a crown\n   descends to the heir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descend', 'v. i.', 'To move toward the south, or to the southward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descend', 'v. i.', 'To fall in pitch; to pass from a higher to a lower\n   tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descend', 'v. t.', 'To go down upon or along; to pass from a higher to a\n   lower part of; as, they descended the river in boats; to descend a\n   ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descendant', 'a.', 'Descendent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descendant', 'n.', 'One who descends, as offspring, however remotely; --\n   correlative to ancestor or ascendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descendent', 'a.', 'Descending; falling; proceeding from an ancestor or\n   source.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descender', 'n.', 'One who descends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descendibility', 'n.', 'The quality of being descendible; capability of\n   being transmitted from ancestors; as, the descendibility of an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descendible', 'a.', 'Admitting descent; capable of being descended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descendible', 'a.', 'That may descend from an ancestor to an heir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descending', 'a.', 'Of or pertaining to descent; moving downwards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descendingly', 'adv.', 'In a descending manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descension', 'n.', 'The act of going downward; descent; falling or sinking;\n   declension; degradation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descensional', 'a.', 'Pertaining to descension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descensive', 'a.', 'Tending to descend; tending downwards; descending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descensory', 'n.', 'A vessel used in alchemy to extract oils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descent', 'n.', 'The act of descending, or passing downward; change of\n   place from higher to lower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descent', 'n.', 'Incursion; sudden attack; especially, hostile invasion\n   from sea; -- often followed by upon or on; as, to make a descent upon\n   the enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descent', 'n.', 'Progress downward, as in station, virtue, as in station,\n   virtue, and the like, from a higher to a lower state, from a higher to\n   a lower state, from the more to the less important, from the better to\n   the worse, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descent', 'n.', 'Derivation, as from an ancestor; procedure by generation;\n   lineage; birth; extraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descent', 'n.', 'Transmission of an estate by inheritance, usually, but not\n   necessarily, in the descending line; title to inherit an estate by\n   reason of consanguinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descent', 'n.', 'Inclination downward; a descending way; inclined or\n   sloping surface; declivity; slope; as, a steep descent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descent', 'n.', 'That which is descended; descendants; issue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descent', 'n.', 'A step or remove downward in any scale of gradation; a\n   degree in the scale of genealogy; a generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descent', 'n.', 'Lowest place; extreme downward place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descent', 'n.', 'A passing from a higher to a lower tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Describable', 'a.', 'That can be described; capable of description.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Described', 'imp. & p. p.', 'of Describe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Describing', 'p. pr. & vb. n.', 'of Describe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Describe', 'v. t.', 'To represent by drawing; to draw a plan of; to\n   delineate; to trace or mark out; as, to describe a circle by the\n   compasses; a torch waved about the head in such a way as to describe a\n   circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Describe', 'v. t.', 'To represent by words written or spoken; to give an\n   account of; to make known to others by words or signs; as, the\n   geographer describes countries and cities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Describe', 'v. t.', 'To distribute into parts, groups, or classes; to mark\n   off; to class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Describe', 'v. i.', 'To use the faculty of describing; to give a\n   description; as, Milton describes with uncommon force and beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Describent', 'n.', 'Same as Generatrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Describer', 'n.', 'One who describes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descrier', 'n.', 'One who descries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Description', 'n.', 'The act of describing; a delineation by marks or\n   signs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Description', 'n.', 'A sketch or account of anything in words; a\n   portraiture or representation in language; an enumeration of the\n   essential qualities of a thing or species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Description', 'n.', 'A class to which a certain representation is\n   applicable; kind; sort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descriptive', 'a.', 'Tending to describe; having the quality of\n   representing; containing description; as, a descriptive figure; a\n   descriptive phrase; a descriptive narration; a story descriptive of the\n   age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descrive', 'v. t.', 'To describe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descried', 'imp. & p. p.', 'of Descry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descrying', 'p. pr. & vb. n.', 'of Descry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descry', 'v. t.', 'To spy out or discover by the eye, as objects distant or\n   obscure; to espy; to recognize; to discern; to discover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descry', 'v. t.', 'To discover; to disclose; to reveal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Descry', 'n.', 'Discovery or view, as of an army seen at a distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desecate', 'v. t.', 'To cut, as with a scythe; to mow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desecrated', 'imp. & p. p.', 'of Desecrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desecrating', 'p. pr. & vb. n.', 'of Desecrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desecrate', 'v. t.', 'To divest of a sacred character or office; to divert\n   from a sacred purpose; to violate the sanctity of; to profane; to put\n   to an unworthy use; -- the opposite of consecrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desecrater', 'n.', 'One who desecrates; a profaner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desecration', 'n.', 'The act of desecrating; profanation; condition of\n   anything desecrated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desecrator', 'n.', 'One who desecrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desegmentation', 'n.', 'The loss or obliteration of division into segments;\n   as, a desegmentation of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desert', 'n.', 'That which is deserved; the reward or the punishment justly\n   due; claim to recompense, usually in a good sense; right to reward;\n   merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desert', 'n.', 'A deserted or forsaken region; a barren tract incapable of\n   supporting population, as the vast sand plains of Asia and Africa are\n   destitute and vegetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desert', 'n.', 'A tract, which may be capable of sustaining a population,\n   but has been left unoccupied and uncultivated; a wilderness; a solitary\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desert', 'a.', 'Of or pertaining to a desert; forsaken; without life or\n   cultivation; unproductive; waste; barren; wild; desolate; solitary; as,\n   they landed on a desert island.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserted', 'imp. & p. p.', 'of Desert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserting', 'p. pr. & vb. n.', 'of Desert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desert', 'v. t.', 'To leave (especially something which one should stay by\n   and support); to leave in the lurch; to abandon; to forsake; --\n   implying blame, except sometimes when used of localities; as, to desert\n   a friend, a principle, a cause, ones country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desert', 'v. t.', 'To abandon (the service) without leave; to forsake in\n   violation of duty; to abscond from; as, to desert the army; to desert\n   ones colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desert', 'v. i.', 'To abandon a service without leave; to quit military\n   service without permission, before the expiration of ones term; to\n   abscond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserter', 'n.', 'One who forsakes a duty, a cause or a party, a friend, or\n   any one to whom he owes service; especially, a soldier or a seaman who\n   abandons the service without leave; one guilty of desertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desertful', 'a.', 'Meritorious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desertion', 'n.', 'The act of deserting or forsaking; abandonment of a\n   service, a cause, a party, a friend, or any post of duty; the quitting\n   of ones duties willfully and without right; esp., an absconding from\n   military or naval service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desertion', 'n.', 'The state of being forsaken; desolation; as, the king in\n   his desertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desertion', 'n.', 'Abandonment by God; spiritual despondency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desertless', 'a.', 'Without desert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desertlessly', 'adv.', 'Undeservedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desertness', 'n.', 'A deserted condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desertrix', 'n.', 'Alt. of Desertrice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desertrice', 'n.', 'A feminine deserter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserved', 'imp. & p. p.', 'of Deserve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserving', 'p. pr. & vb. n.', 'of Deserve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserve', 'v. t.', 'To earn by service; to be worthy of (something due,\n   either good or evil); to merit; to be entitled to; as, the laborer\n   deserves his wages; a work of value deserves praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserve', 'v. t.', 'To serve; to treat; to benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserve', 'v. i.', 'To be worthy of recompense; -- usually with ill or with\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deservedly', 'adv.', 'According to desert (whether good or evil); justly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deservedness', 'n.', 'Meritoriousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserver', 'n.', 'One who deserves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserving', 'n.', 'Desert; merit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deserving', 'a.', 'Meritorious; worthy; as, a deserving person or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deshabille', 'n.', 'An undress; a careless toilet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccant', 'a.', 'Drying; desiccative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccant', 'n.', 'A medicine or application for drying up a sore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccated', 'imp. & p. p.', 'of Desiccate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccating', 'p. pr. & vb. n.', 'of Desiccate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccate', 'v. t.', 'To dry up; to deprive or exhaust of moisture; to\n   preserve by drying; as, to desiccate fish or fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccate', 'v. i.', 'To become dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccation', 'n.', 'The act of desiccating, or the state of being\n   desiccated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccative', 'a.', 'Drying; tending to dry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccative', 'n.', 'An application for drying up secretions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccator', 'n.', 'One who, or that which, desiccates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccator', 'n.', 'A short glass jar fitted with an air-tight cover, and\n   containing some desiccating agent, as sulphuric acid or calcium\n   chloride, above which is suspended the material to be dried, or\n   preserved from moisture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiccatory', 'a.', 'Desiccative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiderable', 'a.', 'Desirable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiderata', 'n. pl.', 'See Desideratum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiderated', 'imp. & p. p.', 'of Desiderate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiderating', 'p. pr. & vb. n.', 'of Desiderate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiderate', 'v. t.', 'To desire; to feel the want of; to lack; to miss; to\n   want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desideration', 'n.', 'Act of desiderating; also, the thing desired.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiderative', 'a.', 'Denoting desire; as, desiderative verbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiderative', 'n.', 'An object of desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiderative', 'n.', 'A verb formed from another verb by a change of\n   termination, and expressing the desire of doing that which is indicated\n   by the primitive verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desiderata', 'pl. ', 'of Desideratum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desideratum', 'n.', 'Anything desired; that of which the lack is felt; a\n   want generally felt and acknowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desidiose', 'a.', 'Alt. of Desidious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desidious', 'a.', 'Idle; lazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desidiousness', 'n.', 'The state or quality of being desidiose, or\n   indolent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desight', 'n.', 'An unsightly object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desightment', 'n.', 'The act of making unsightly; disfigurement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designed', 'imp. & p. p.', 'of Design');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designing', 'p. pr. & vb. n.', 'of Design');
  INSERT INTO words (word, word_type, description) VALUES  (    'Design', 'n.', 'To draw preliminary outline or main features of; to sketch\n   for a pattern or model; to delineate; to trace out; to draw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Design', 'n.', 'To mark out and exhibit; to designate; to indicate; to\n   show; to point out; to appoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Design', 'n.', 'To create or produce, as a work of art; to form a plan or\n   scheme of; to form in idea; to invent; to project; to lay out in the\n   mind; as, a man designs an essay, a poem, a statue, or a cathedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Design', 'n.', 'To intend or purpose; -- usually with for before the remote\n   object, but sometimes with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Design', 'v. i.', 'To form a design or designs; to plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Design', 'n.', 'A preliminary sketch; an outline or pattern of the main\n   features of something to be executed, as of a picture, a building, or a\n   decoration; a delineation; a plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Design', 'n.', 'A plan or scheme formed in the mind of something to be\n   done; preliminary conception; idea intended to be expressed in a\n   visible form or carried into action; intention; purpose; -- often used\n   in a bad sense for evil intention or purpose; scheme; plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Design', 'n.', 'Specifically, intention or purpose as revealed or inferred\n   from the adaptation of means to an end; as, the argument from design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Design', 'n.', 'The realization of an inventive or decorative plan; esp., a\n   work of decorative art considered as a new creation; conception or plan\n   shown in completed work; as, this carved panel is a fine design, or of\n   a fine design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Design', 'n.', 'The invention and conduct of the subject; the disposition\n   of every part, and the general order of the whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designable', 'a.', 'Capable of being designated or distinctly marked out;\n   distinguishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designate', 'v. t.', 'Designated; appointed; chosen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designated', 'imp. & p. p.', 'of Designate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designating', 'p. pr. & vb. n.', 'of Designate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designate', 'v. t.', 'To mark out and make known; to point out; to name; to\n   indicate; to show; to distinguish by marks or description; to specify;\n   as, to designate the boundaries of a country; to designate the rioters\n   who are to be arrested.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designate', 'v. t.', 'To call by a distinctive title; to name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designate', 'v. t.', 'To indicate or set apart for a purpose or duty; --\n   with to or for; to designate an officer for or to the command of a post\n   or station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designation', 'n.', 'The act of designating; a pointing out or showing;\n   indication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designation', 'n.', 'Selection and appointment for a purpose; allotment;\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designation', 'n.', 'That which designates; a distinguishing mark or name;\n   distinctive title; appellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designation', 'n.', 'Use or application; import; intention; signification,\n   as of a word or phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designative', 'a.', 'Serving to designate or indicate; pointing out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designator', 'n.', 'An officer who assigned to each his rank and place in\n   public shows and ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designator', 'n.', 'One who designates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designatory', 'a.', 'Serving to designate; designative; indicating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designedly', 'adv.', 'By design; purposely; intentionally; -- opposed to\n   accidentally, ignorantly, or inadvertently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designer', 'n.', 'One who designs, marks out, or plans; a contriver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designer', 'n.', 'One who produces or creates original works of art or\n   decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designer', 'n.', 'A plotter; a schemer; -- used in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designful', 'a.', 'Full of design; scheming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designing', 'a.', 'Intriguing; artful; scheming; as, a designing man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designing', 'n.', 'The act of making designs or sketches; the act of\n   forming designs or plans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designless', 'a.', 'Without design.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designment', 'n.', 'Delineation; sketch; design; ideal; invention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Designment', 'n.', 'Design; purpose; scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desilver', 'v. t.', 'To deprive of silver; as, to desilver lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desilverization', 'n.', 'The act or the process of freeing from silver;\n   also, the condition resulting from the removal of silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desilverize', 'v. t.', 'To deprive, or free from, silver; to remove silver\n   from.');
