  INSERT INTO words (word, word_type, description) VALUES  (    'Destinies', 'pl. ', 'of Destiny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destiny', 'n.', 'That to which any person or thing is destined;\n   predetermined state; condition foreordained by the Divine or by human\n   will; fate; lot; doom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destiny', 'n.', 'The fixed order of things; invincible necessity; fate; a\n   resistless power or agency conceived of as determining the future,\n   whether in general or of an individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destituent', 'a.', 'Deficient; wanting; as, a destituent condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destitute', 'a.', 'Forsaken; not having in possession (something necessary,\n   or desirable); deficient; lacking; devoid; -- often followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destitute', 'a.', 'Not possessing the necessaries of life; in a condition\n   of want; needy; without possessions or resources; very poor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destitute', 'v. t.', 'To leave destitute; to forsake; to abandon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destitute', 'v. t.', 'To make destitute; to cause to be in want; to\n   deprive; -- followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destitute', 'v. t.', 'To disappoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destitutely', 'adv.', 'In destitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destituteness', 'n.', 'Destitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destitution', 'n.', 'The state of being deprived of anything; the state or\n   condition of being destitute, needy, or without resources; deficiency;\n   lack; extreme poverty; utter want; as, the inundation caused general\n   destitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destrer', 'n.', 'Alt. of Dextrer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dextrer', 'n.', 'A war horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destrie', 'v. t.', 'To destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destroyed', 'imp. & p. p.', 'of Destroy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destroying', 'p. pr. & vb. n.', 'of Destroy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destroy', 'v. t.', 'To unbuild; to pull or tear down; to separate\n   virulently into its constituent parts; to break up the structure and\n   organic existence of; to demolish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destroy', 'v. t.', 'To ruin; to bring to naught; to put an end to; to\n   annihilate; to consume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destroy', 'v. t.', 'To put an end to the existence, prosperity, or beauty\n   of; to kill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destroyable', 'a.', 'Destructible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destroyer', 'n.', 'One who destroys, ruins, kills, or desolates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destruct', 'v. t.', 'To destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructibility', 'n.', 'The quality of being capable of destruction;\n   destructibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructible', 'a.', 'Liable to destruction; capable of being destroyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructibleness', 'n.', 'The quality of being destructible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destruction', 'n.', 'The act of destroying; a tearing down; a bringing to\n   naught; subversion; demolition; ruin; slaying; devastation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destruction', 'n.', 'The state of being destroyed, demolished, ruined,\n   slain, or devastated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destruction', 'n.', 'A destroying agency; a cause of ruin or of\n   devastation; a destroyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructionist', 'n.', 'One who delights in destroying that which is\n   valuable; one whose principles and influence tend to destroy existing\n   institutions; a destructive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructionist', 'n.', 'One who believes in the final destruction or\n   complete annihilation of the wicked; -- called also annihilationist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructive', 'a.', 'Causing destruction; tending to bring about ruin,\n   death, or devastation; ruinous; fatal; productive of serious evil;\n   mischievous; pernicious; -- often with of or to; as, intemperance is\n   destructive of health; evil examples are destructive to the morals of\n   youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructive', 'n.', 'One who destroys; a radical reformer; a\n   destructionist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructively', 'adv.', 'In a destructive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructiveness', 'n.', 'The quality of destroying or ruining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructiveness', 'n.', 'The faculty supposed to impel to the commission of\n   acts of destruction; propensity to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destructor', 'n.', 'A destroyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Destruie', 'v. t.', 'To destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desudation', 'n.', 'A sweating; a profuse or morbid sweating, often\n   succeeded by an eruption of small pimples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desuete', 'a.', 'Disused; out of use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desuetude', 'n.', 'The cessation of use; disuse; discontinuance of\n   practice, custom, or fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desulphurated', 'imp. & p. p.', 'of Desulphurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desulphurating', 'p. pr. & vb. n.', 'of Desulphurate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desulphurate', 'v. t.', 'To deprive of sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desulphuration', 'n.', 'The act or process of depriving of sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desulphurize', 'v. t.', 'To desulphurate; to deprive of sulphur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desultorily', 'adv.', 'In a desultory manner; without method; loosely;\n   immethodically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desultoriness', 'n.', 'The quality of being desultory or without order or\n   method; unconnectedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desultorious', 'a.', 'Desultory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desultory', 'a.', 'Leaping or skipping about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desultory', 'a.', 'Jumping, or passing, from one thing or subject to\n   another, without order or rational connection; without logical\n   sequence; disconnected; immethodical; aimless; as, desultory minds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desultory', 'a.', 'Out of course; by the way; as a digression; not\n   connected with the subject; as, a desultory remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desume', 'v. t.', 'To select; to borrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desynonymization', 'n.', 'The act of desynonymizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Desynonymize', 'v. t.', 'To deprive of synonymous character; to\n   discriminate in use; -- applied to words which have been employed as\n   synonyms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detached', 'imp. & p. p.', 'of Detach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detaching', 'p. pr. & vb. n.', 'of Detach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detach', 'v. t.', 'To part; to separate or disunite; to disengage; -- the\n   opposite of attach; as, to detach the coats of a bulbous root from each\n   other; to detach a man from a leader or from a party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detach', 'v. t.', 'To separate for a special object or use; -- used\n   especially in military language; as, to detach a ship from a fleet, or\n   a company from a regiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detach', 'v. i.', 'To push asunder; to come off or separate from anything;\n   to disengage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detachable', 'a.', 'That can be detached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detached', 'a.', 'Separate; unconnected, or imperfectly connected; as,\n   detached parcels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detachment', 'n.', 'The act of detaching or separating, or the state of\n   being detached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detachment', 'n.', 'That which is detached; especially, a body of troops or\n   part of a fleet sent from the main body on special service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detachment', 'n.', 'Abstraction from worldly objects; renunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detail', 'n.', 'A minute portion; one of the small parts; a particular; an\n   item; -- used chiefly in the plural; as, the details of a scheme or\n   transaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detail', 'n.', 'A narrative which relates minute points; an account which\n   dwells on particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detail', 'n.', 'The selection for a particular service of a person or a\n   body of men; hence, the person or the body of men so selected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detailed', 'imp. & p. p.', 'of Detail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detailing', 'p. pr. & vb. n.', 'of Detail');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detail', 'n.', 'To relate in particulars; to particularize; to report\n   minutely and distinctly; to enumerate; to specify; as, he detailed all\n   the facts in due order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detail', 'n.', 'To tell off or appoint for a particular service, as an\n   officer, a troop, or a squadron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detailer', 'n.', 'One who details.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detained', 'imp. & p. p.', 'of Detain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detaining', 'p. pr. & vb. n.', 'of Detain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detain', 'v. t.', 'To keep back or from; to withhold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detain', 'v. t.', 'To restrain from proceeding; to stay or stop; to delay;\n   as, we were detained by an accident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detain', 'v. t.', 'To hold or keep in custody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detain', 'n.', 'Detention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detainder', 'n.', 'A writ. See Detinue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detainer', 'n.', 'One who detains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detainer', 'n.', 'The keeping possession of what belongs to another;\n   detention of what is anothers, even though the original taking may\n   have been lawful. Forcible detainer is indictable at common law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detainer', 'n.', 'A writ authorizing the keeper of a prison to continue to\n   keep a person in custody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detainment', 'n.', 'Detention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detect', 'a.', 'Detected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detected', 'imp. & p. p.', 'of Detect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detecting', 'p. pr. & vb. n.', 'of Detect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detect', 'v. t.', 'To uncover; to discover; to find out; to bring to light;\n   as, to detect a crime or a criminal; to detect a mistake in an account.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detect', 'v. t.', 'To inform against; to accuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detectable', 'a.', 'Alt. of Detectible');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detectible', 'a.', 'Capable of being detected or found out; as, parties not\n   detectable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detecter', 'n.', 'One who, or that which, detects or brings to light; one\n   who finds out what another attempts to conceal; a detector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detection', 'n.', 'The act of detecting; the laying open what was concealed\n   or hidden; discovery; as, the detection of a thief; the detection of\n   fraud, forgery, or a plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detective', 'a.', 'Fitted for, or skilled in, detecting; employed in\n   detecting crime or criminals; as, a detective officer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detective', 'n.', 'One who business it is so detect criminals or discover\n   matters of secrecy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detector', 'n.', 'One who, or that which, detects; a detecter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detenebrate', 'v. t.', 'To remove darkness from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detent', 'n.', 'That which locks or unlocks a movement; a catch, pawl, or\n   dog; especially, in clockwork, the catch which locks and unlocks the\n   wheelwork in striking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detention', 'n.', 'The act of detaining or keeping back; a withholding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detention', 'n.', 'The state of being detained (stopped or hindered); delay\n   from necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detention', 'n.', 'Confinement; restraint; custody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deterred', 'imp. & p. p.', 'of Deter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deterring', 'p. pr. & vb. n.', 'of Deter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deter', 'v. t.', 'To prevent by fear; hence, to hinder or prevent from\n   action by fear of consequences, or difficulty, risk, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deterged', 'imp. & p. p.', 'of Deterge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deterging', 'p. pr. & vb. n.', 'of Deterge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deterge', 'v. t.', 'To cleanse; to purge away, as foul or offending matter\n   from the body, or from an ulcer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detergency', 'n.', 'A cleansing quality or power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detergent', 'a.', 'Cleansing; purging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detergent', 'n.', 'A substance which cleanses the skin, as water or soap; a\n   medicine to cleanse wounds, ulcers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deteriorated', 'imp. & p. p.', 'of Deteriorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deteriorating', 'p. pr. & vb. n.', 'of Deteriorate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deteriorate', 'v. t.', 'To make worse; to make inferior in quality or\n   value; to impair; as, to deteriorate the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deteriorate', 'v. i.', 'To grow worse; to be impaired in quality; to\n   degenerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deterioration', 'n.', 'The process of growing worse, or the state of having\n   grown worse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deteriority', 'n.', 'Worse state or quality; inferiority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determent', 'n.', 'The act of deterring; also, that which deters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinability', 'n.', 'The quality of being determinable;\n   determinableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinable', 'v. t.', 'Capable of being determined, definitely\n   ascertained, decided upon, or brought to a conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinableness', 'n.', 'Capability of being determined; determinability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinacy', 'n.', 'Determinateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinant', 'a.', 'Serving to determine or limit; determinative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinant', 'n.', 'That which serves to determine; that which causes\n   determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinant', 'n.', 'The sum of a series of products of several numbers,\n   these products being formed according to certain specified laws');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinant', 'n.', 'A mark or attribute, attached to the subject or\n   predicate, narrowing the extent of both, but rendering them more\n   definite and precise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinate', 'a.', 'Having defined limits; not uncertain or arbitrary;\n   fixed; established; definite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinate', 'a.', 'Conclusive; decisive; positive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinate', 'a.', 'Determined or resolved upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinate', 'a.', 'Of determined purpose; resolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinate', 'v. t.', 'To bring to an end; to determine. See Determine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinately', 'adv.', 'In a determinate manner; definitely;\n   ascertainably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinately', 'adv.', 'Resolutely; unchangeably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinateness', 'n.', 'State of being determinate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'The act of determining, or the state of being\n   determined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'Bringing to an end; termination; limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'Direction or tendency to a certain end; impulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'The quality of mind which reaches definite\n   conclusions; decision of character; resoluteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'The state of decision; a judicial decision, or\n   ending of controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'That which is determined upon; result of\n   deliberation; purpose; conclusion formed; fixed resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'A flow, rush, or tendency to a particular part; as,\n   a determination of blood to the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'The act, process, or result of any accurate\n   measurement, as of length, volume, weight, intensity, etc.; as, the\n   determination of the ohm or of the wave length of light; the\n   determination of the salt in sea water, or the oxygen in the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'The act of defining a concept or notion by giving\n   its essential constituents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'The addition of a differentia to a concept or\n   notion, thus limiting its extent; -- the opposite of generalization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determination', 'n.', 'The act of determining the relations of an object,\n   as regards genus and species; the referring of minerals, plants, or\n   animals, to the species to which they belong; classification; as, I am\n   indebted to a friend for the determination of most of these shells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinative', 'a.', 'Having power to determine; limiting; shaping;\n   directing; conclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinative', 'n.', 'That which serves to determine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinator', 'n.', 'One who determines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determined', 'imp. & p. p.', 'of Determine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determining', 'p. pr. & vb. n.', 'of Determine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. t.', 'To fix the boundaries of; to mark off and separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. t.', 'To set bounds to; to fix the determination of; to\n   limit; to bound; to bring to an end; to finish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. t.', 'To fix the form or character of; to shape; to\n   prescribe imperatively; to regulate; to settle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. t.', 'To fix the course of; to impel and direct; -- with a\n   remoter object preceded by to; as, anothers will determined me to this\n   course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. t.', 'To ascertain definitely; to find out the specific\n   character or name of; to assign to its true place in a system; as, to\n   determine an unknown or a newly discovered plant or its name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. t.', 'To bring to a conclusion, as a question or\n   controversy; to settle authoritative or judicial sentence; to decide;\n   as, the court has determined the cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. t.', 'To resolve on; to have a fixed intention of; also, to\n   cause to come to a conclusion or decision; to lead; as, this determined\n   him to go immediately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. t.', 'To define or limit by adding a differentia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. t.', 'To ascertain the presence, quantity, or amount of;\n   as, to determine the parallax; to determine the salt in sea water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. i.', 'To come to an end; to end; to terminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determine', 'v. i.', 'To come to a decision; to decide; to resolve; --\n   often with on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determined', 'a.', 'Decided; resolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinedly', 'adv.', 'In a determined manner; with determination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determiner', 'n.', 'One who, or that which, determines or decides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinism', 'n.', 'The doctrine that the will is not free, but is\n   inevitably and invincibly determined by motives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Determinist', 'n.', 'One who believes in determinism. Also adj.; as,\n   determinist theories.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deterration', 'n.', 'The uncovering of anything buried or covered with\n   earth; a taking out of the earth or ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deterrence', 'n.', 'That which deters; a deterrent; a hindrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deterrent', 'a.', 'Serving to deter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Deterrent', 'n.', 'That which deters or prevents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detersion', 'n.', 'The act of deterging or cleansing, as a sore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detersive', 'a.', 'Cleansing; detergent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detersive', 'n.', 'A cleansing agent; a detergent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detersively', 'adv.', 'In a way to cleanse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detersiveness', 'n.', 'The quality of cleansing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detested', 'imp. & p. p.', 'of Detest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detesting', 'p. pr. & vb. n.', 'of Detest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detest', 'v. t.', 'To witness against; to denounce; to condemn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detest', 'v. t.', 'To hate intensely; to abhor; to abominate; to loathe;\n   as, we detest what is contemptible or evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detestability', 'n.', 'Capacity of being odious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detestable', 'a.', 'Worthy of being detested; abominable; extremely\n   hateful; very odious; deserving abhorrence; as, detestable vices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detestableness', 'n.', 'The quality or state of being detestable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detestably', 'adv.', 'In a detestable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detesttate', 'v. t.', 'To detest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detestation', 'n.', 'The act of detesting; extreme hatred or dislike;\n   abhorrence; loathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detester', 'n.', 'One who detes//');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dethroned', 'imp. & p. p.', 'of Dethrone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dethroning', 'p. pr. & vb. n.', 'of Dethrone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dethrone', 'v. t.', 'To remove or drive from a throne; to depose; to divest\n   of supreme authority and dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dethronement', 'n.', 'Deposal from a throne; deposition from regal power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dethroner', 'n.', 'One who dethrones.');
