  INSERT INTO words (word, word_type, description) VALUES  (    'Detonate', 'v. t.', 'To cause to explode; to cause to burn or inflame with\n   a sudden report.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detonating', 'a. & n.', 'from Detonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detonation', 'n.', 'An explosion or sudden report made by the instantaneous\n   decomposition or combustion of unstable substances as, the detonation\n   of gun cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detonator', 'n.', 'One who, or that which, detonates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Detonization', 'n.', 'The act of detonizing; detonation.');
