  INSERT INTO words (word, word_type, description) VALUES  (    'Digne', 'a.', 'Suitable; adequate; fit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digne', 'a.', 'Haughty; disdainful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignification', 'n.', 'The act of dignifying; exaltation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignified', 'a.', 'Marked with dignity; stately; as, a dignified judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignified', 'imp. & p. p.', 'of Dignify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignifying', 'p. pr. & vb. n.', 'of Dignify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignify', 'v. t.', 'To invest with dignity or honor; to make illustrious;\n   to give distinction to; to exalt in rank; to honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignitaries', 'pl. ', 'of Dignitary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignitary', 'n.', 'One who possesses exalted rank or holds a position of\n   dignity or honor; especially, one who holds an ecclesiastical rank\n   above that of a parochial priest or clergyman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignities', 'pl. ', 'of Dignity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignity', 'n.', 'The state of being worthy or honorable; elevation of mind\n   or character; true worth; excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignity', 'n.', 'Elevation; grandeur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignity', 'n.', 'Elevated rank; honorable station; high office, political\n   or ecclesiastical; degree of excellence; preferment; exaltation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignity', 'n.', 'Quality suited to inspire respect or reverence; loftiness\n   and grace; impressiveness; stateliness; -- said of //en, manner, style,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignity', 'n.', 'One holding high rank; a dignitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignity', 'n.', 'Fundamental principle; axiom; maxim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dignotion', 'n.', 'Distinguishing mark; diagnostic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digonous', 'a.', 'Having two angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digram', 'n.', 'A digraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digraph', 'n.', 'Two signs or characters combined to express a single\n   articulated sound; as ea in head, or th in bath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digraphic', 'a.', 'Of or pertaining to a digraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digressed', 'imp. & p. p.', 'of Digress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digressing', 'p. pr. & vb. n.', 'of Digress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digress', 'v. i.', 'To step or turn aside; to deviate; to swerve;\n   especially, to turn aside from the main subject of attention, or course\n   of argument, in writing or speaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digress', 'v. i.', 'To turn aside from the right path; to transgress; to\n   offend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digress', 'n.', 'Digression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digression', 'n.', 'The act of digressing or deviating, esp. from the main\n   subject of a discourse; hence, a part of a discourse deviating from its\n   main design or subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digression', 'n.', 'A turning aside from the right path; transgression;\n   offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digression', 'n.', 'The elongation, or angular distance from the sun; --\n   said chiefly of the inferior planets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digressional', 'a.', 'Pertaining to, or having the character of, a\n   digression; departing from the main purpose or subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digressive', 'a.', 'Departing from the main subject; partaking of the\n   nature of digression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digressively', 'adv.', 'By way of digression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Digue', 'n.', 'A bank; a dike.');
