  INSERT INTO words (word, word_type, description) VALUES  (    'Dihedron', 'n.', 'A figure with two sides or surfaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dihexagonal', 'a.', 'Consisting of two hexagonal parts united; thus, a\n   dihexagonal pyramid is composed of two hexagonal pyramids placed base\n   to base.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dihexagonal', 'a.', 'Having twelve similar faces; as, a dihexagonal prism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diiamb', 'n.', 'A diiambus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diiambus', 'n.', 'A double iambus; a foot consisting of two iambuses (/ / /\n   /).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diiodide', 'n.', 'A compound of a binary type containing two atoms of\n   iodine; -- called also biniodide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diisatogen', 'n.', 'A red crystalline nitrogenous substance or artificial\n   production, which by reduction passes directly to indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dijudicant', 'n.', 'One who dijudicates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dijudicated', 'imp. & p. p.', 'of Dijudicate');
