  INSERT INTO words (word, word_type, description) VALUES  (    'Diked', 'imp. & p. p.', 'of Dike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diking', 'p. pr. & vb. n.', 'of Dike');
