  INSERT INTO words (word, word_type, description) VALUES  (    'Dike', 'v. t.', 'To drain by a dike or ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dike', 'v. i.', 'To work as a ditcher; to dig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diker', 'n.', 'A ditcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diker', 'n.', 'One who builds stone walls; usually, one who builds them\n   without lime.');
