  INSERT INTO words (word, word_type, description) VALUES  (    'Dilapidated', 'imp. & p. p.', 'of Dilapidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilapidating', 'p. pr. & vb. n.', 'of Dilapidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilapidate', 'v. t.', 'To bring into a condition of decay or partial ruin,\n   by misuse or through neglect; to destroy the fairness and good\n   condition of; -- said of a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilapidate', 'v. t.', 'To impair by waste and abuse; to squander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilapidate', 'v. i.', 'To get out of repair; to fall into partial ruin; to\n   become decayed; as, the church was suffered to dilapidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilapidated', 'a.', 'Decayed; fallen into partial ruin; injured by bad\n   usage or neglect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilapidation', 'n.', 'The act of dilapidating, or the state of being\n   dilapidated, reduced to decay, partially ruined, or squandered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilapidation', 'n.', 'Ecclesiastical waste; impairing of church property by\n   an incumbent, through neglect or by intention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilapidation', 'n.', 'The pulling down of a building, or suffering it to\n   fall or be in a state of decay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilapidator', 'n.', 'One who causes dilapidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilatability', 'n.', 'The quality of being dilatable, or admitting\n   expansion; -- opposed to contractibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilatable', 'a.', 'Capable of expansion; that may be dilated; -- opposed to\n   contractible; as, the lungs are dilatable by the force of air; air is\n   dilatable by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilatation', 'n.', 'Prolixity; diffuse discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilatation', 'n.', 'The act of dilating; expansion; an enlarging on al/\n   sides; the state of being dilated; dilation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilatation', 'n.', 'A dilation or enlargement of a canal or other organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilatator', 'n.', 'A muscle which dilates any part; a dilator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilated', 'imp. & p. p.', 'of Dilate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilating', 'p. pr. & vb. n.', 'of Dilate');
