  INSERT INTO words (word, word_type, description) VALUES  (    'Dilate', 'v. t.', 'To enlarge upon; to relate at large; to tell copiously\n   or diffusely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilate', 'v. i.', 'To grow wide; to expand; to swell or extend in all\n   directions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilate', 'v. i.', 'To speak largely and copiously; to dwell in narration;\n   to enlarge; -- with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilate', 'a.', 'Extensive; expanded.');
