  INSERT INTO words (word, word_type, description) VALUES  (    'Dilatorily', 'adv.', 'With delay; tardily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilatoriness', 'n.', 'The quality of being dilatory; lateness; slowness;\n   tardiness; sluggishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilatory', 'a.', 'Inclined to defer or put off what ought to be done at\n   once; given the procrastination; delaying; procrastinating; loitering;\n   as, a dilatory servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilatory', 'a.', 'Marked by procrastination or delay; tardy; slow;\n   sluggish; -- said of actions or measures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dildo', 'n.', 'A burden in popular songs.');
