  INSERT INTO words (word, word_type, description) VALUES  (    'Dilection', 'n.', 'Love; choice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilemma', 'n.', 'An argument which presents an antagonist with two or more\n   alternatives, but is equally conclusive against him, whichever\n   alternative he chooses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilemma', 'n.', 'A state of things in which evils or obstacles present\n   themselves on every side, and it is difficult to determine what course\n   to pursue; a vexatious alternative or predicament; a difficult choice\n   or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilettant', 'a.', 'Of or pertaining to dilettanteism; amateur; as,\n   dilettant speculation.');
