  INSERT INTO words (word, word_type, description) VALUES  (    'Dilettanti', 'pl. ', 'of Dilettante');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilettante', 'v. t.', 'An admirer or lover of the fine arts; popularly, an\n   amateur; especially, one who follows an art or a branch of knowledge,\n   desultorily, or for amusement only.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilettanteish', 'a.', 'Somewhat like a dilettante.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilettanteism', 'n.', 'The state or quality of being a dilettante; the\n   desultory pursuit of art, science, or literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilettantish', 'a.', 'Dilettanteish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilettantism', 'n.', 'Same as Dilettanteism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diligence', 'n.', 'The quality of being diligent; carefulness; careful\n   attention; -- the opposite of negligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diligence', 'n.', 'Interested and persevering application; devoted and\n   painstaking effort to accomplish what is undertaken; assiduity in\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diligence', 'n.', 'Process by which persons, lands, or effects are seized\n   for debt; process for enforcing the attendance of witnesses or the\n   production of writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diligence', 'n.', 'A four-wheeled public stagecoach, used in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diligency', 'n.', 'Diligence; care; persevering endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diligent', 'a.', 'Prosecuted with careful attention and effort; careful;\n   painstaking; not careless or negligent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diligent', 'a.', 'Interestedly and perseveringly attentive; steady and\n   earnest in application to a subject or pursuit; assiduous; industrious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diligently', 'adv.', 'In a diligent manner; not carelessly; not\n   negligently; with industry or assiduity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dill', 'n.', 'An herb (Peucedanum graveolens), the seeds of which are\n   moderately warming, pungent, and aromatic, and were formerly used as a\n   soothing medicine for children; -- called also dillseed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dill', 'a.', 'To still; to calm; to soothe, as one in pain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilling', 'n.', 'A darling; a favorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilluing', 'n.', 'A process of sorting ore by washing in a hand sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilly', 'n.', 'A kind of stagecoach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilly-dally', 'v. i.', 'To loiter or trifle; to waste time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilogical', 'a.', 'Ambiguous; of double meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilogies', 'pl. ', 'of Dilogy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilogy', 'n.', 'An ambiguous speech; a figure in which a word is used an\n   equivocal sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilucid', 'a.', 'Clear; lucid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilucidate', 'v. t.', 'To elucidate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilucidation', 'n.', 'The act of making clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluent', 'a.', 'Diluting; making thinner or weaker by admixture, esp. of\n   water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluent', 'n.', 'That which dilutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluent', 'n.', 'An agent used for effecting dilution of the blood; a weak\n   drink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluted', 'imp. & p. p.', 'of Dilute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluting', 'p. pr. & vb. n.', 'of Dilute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilute', 'v. t.', 'To make thinner or more liquid by admixture with\n   something; to thin and dissolve by mixing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilute', 'v. t.', 'To diminish the strength, flavor, color, etc., of, by\n   mixing; to reduce, especially by the addition of water; to temper; to\n   attenuate; to weaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilute', 'v. i.', 'To become attenuated, thin, or weak; as, it dilutes\n   easily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilute', 'a.', 'Diluted; thin; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluted', 'a.', 'Reduced in strength; thin; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluteness', 'n.', 'The quality or state of being dilute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluter', 'n.', 'One who, or that which, dilutes or makes thin, more\n   liquid, or weaker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dilution', 'n.', 'The act of diluting, or the state of being diluted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluvial', 'a.', 'Of or pertaining to a flood or deluge, esp. to the great\n   deluge in the days of Noah; diluvian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluvial', 'a.', 'Effected or produced by a flood or deluge of water; --\n   said of coarse and imperfectly stratified deposits along ancient or\n   existing water courses. Similar unstratified deposits were formed by\n   the agency of ice. The time of deposition has been called the Diluvian\n   epoch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluvialist', 'n.', 'One who explains geological phenomena by the Noachian\n   deluge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluvian', 'a.', 'Of or pertaining to a deluge, esp. to the Noachian\n   deluge; diluvial; as, of diluvian origin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluviate', 'v. i.', 'To run as a flood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluviums', 'pl. ', 'of Diluvium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluvia', 'pl. ', 'of Diluvium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diluvium', 'n.', 'A deposit of superficial loam, sand, gravel, stones,\n   etc., caused by former action of flowing waters, or the melting of\n   glacial ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dim', 'superl.', 'Not bright or distinct; wanting luminousness or\n   clearness; obscure in luster or sound; dusky; darkish; obscure;\n   indistinct; overcast; tarnished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dim', 'superl.', 'Of obscure vision; not seeing clearly; hence, dull of\n   apprehension; of weak perception; obtuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimmed', 'imp. & p. p.', 'of Dim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimming', 'p. pr. & vb. n.', 'of Dim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dim', 'v. t.', 'To render dim, obscure, or dark; to make less bright or\n   distinct; to take away the luster of; to darken; to dull; to obscure;\n   to eclipse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dim', 'v. t.', 'To deprive of distinct vision; to hinder from seeing\n   clearly, either by dazzling or clouding the eyes; to darken the senses\n   or understanding of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dim', 'v. i.', 'To grow dim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimble', 'n.', 'A bower; a dingle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dime', 'n.', 'A silver coin of the United States, of the value of ten\n   cents; the tenth of a dollar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimension', 'n.', 'Measure in a single line, as length, breadth, height,\n   thickness, or circumference; extension; measurement; -- usually, in the\n   plural, measure in length and breadth, or in length, breadth, and\n   thickness; extent; size; as, the dimensions of a room, or of a ship;\n   the dimensions of a farm, of a kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimension', 'n.', 'Extent; reach; scope; importance; as, a project of large\n   dimensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimension', 'n.', 'The degree of manifoldness of a quantity; as, time is\n   quantity having one dimension; volume has three dimensions, relative to\n   extension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimension', 'n.', 'A literal factor, as numbered in characterizing a term.\n   The term dimensions forms with the cardinal numbers a phrase equivalent\n   to degree with the ordinal; thus, a2b2c is a term of five dimensions,\n   or of the fifth degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimension', 'n.', 'The manifoldness with which the fundamental units of\n   time, length, and mass are involved in determining the units of other\n   physical quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimensional', 'a.', 'Pertaining to dimension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimensioned', 'a.', 'Having dimensions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimensionless', 'a.', 'Without dimensions; having no appreciable or\n   noteworthy extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimensity', 'n.', 'Dimension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimensive', 'a.', 'Without dimensions; marking dimensions or the limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimera', 'n. pl.', 'A division of Coleoptera, having two joints to the\n   tarsi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimera', 'n. pl.', 'A division of the Hemiptera, including the aphids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimeran', 'n.', 'One of the Dimera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimerous', 'a.', 'Composed of, or having, two parts of each kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimeter', 'a.', 'Having two poetical measures or meters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimeter', 'n.', 'A verse of two meters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimethyl', 'n.', 'Ethane; -- sometimes so called because regarded as\n   consisting of two methyl radicals. See Ethane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimetric', 'a.', 'Same as Tetragonal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimication', 'n.', 'A fight; contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimidiate', 'a.', 'Divided into two equal parts; reduced to half in shape\n   or form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimidiate', 'a.', 'Consisting of only one half of what the normal condition\n   requires; having the appearance of lacking one half; as, a dimidiate\n   leaf, which has only one side developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimidiate', 'a.', 'Having the organs of one side, or half, different in\n   function from the corresponding organs on the other side; as, dimidiate\n   hermaphroditism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimidiated', 'imp. & p. p.', 'of Dimidiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimidiating', 'p. pr. & vb. n.', 'of Dimidiate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimidiate', 'v. t.', 'To divide into two equal parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimidiate', 'v. t.', 'To represent the half of; to halve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimidiation', 'n.', 'The act of dimidiating or halving; the state of being\n   dimidiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminished', 'imp. & p. p.', 'of Diminish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminishing', 'p. pr. & vb. n.', 'of Diminish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminish', 'v. t.', 'To make smaller in any manner; to reduce in bulk or\n   amount; to lessen; -- opposed to augment or increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminish', 'v. t.', 'To lessen the authority or dignity of; to put down; to\n   degrade; to abase; to weaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminish', 'v. t.', 'To make smaller by a half step; to make (an interval)\n   less than minor; as, a diminished seventh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminish', 'v. t.', 'To take away; to subtract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminish', 'v. i.', 'To become or appear less or smaller; to lessen; as,\n   the apparent size of an object diminishes as we recede from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminishable', 'a.', 'Capable of being diminished or lessened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminisher', 'n.', 'One who, or that which, diminishes anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminishingly', 'adv.', 'In a manner to diminish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminishment', 'n.', 'Diminution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminuendo', 'adv.', 'In a gradually diminishing manner; with abatement of\n   tone; decrescendo; -- expressed on the staff by Dim., or Dimin., or the\n   sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminuent', 'a.', 'Lessening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutal', 'a.', 'Indicating or causing diminution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminute', 'a.', 'Small; diminished; diminutive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutely', 'adv.', 'Diminutively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminution', 'n.', 'The act of diminishing, or of making or becoming less;\n   state of being diminished; reduction in size, quantity, or degree; --\n   opposed to augmentation or increase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminution', 'n.', 'The act of lessening dignity or consideration, or the\n   state of being deprived of dignity; a lowering in estimation;\n   degradation; abasement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminution', 'n.', 'Omission, inaccuracy, or defect in a record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminution', 'n.', 'In counterpoint, the imitation of, or reply to, a\n   subject, in notes of half the length or value of those the subject\n   itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutival', 'a.', 'Indicating diminution; diminutive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutival', 'n.', 'A diminutive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutive', 'a.', 'Below the average size; very small; little.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutive', 'a.', 'Expressing diminution; as, a diminutive word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutive', 'a.', 'Tending to diminish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutive', 'n.', 'Something of very small size or value; an insignificant\n   thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutive', 'n.', 'A derivative from a noun, denoting a small or a young\n   object of the same kind with that denoted by the primitive; as,\n   gosling, eaglet, lambkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutively', 'adv.', 'In a diminutive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diminutiveness', 'n.', 'The quality of being diminutive; smallness;\n   littleness; minuteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimish', 'a.', 'See Dimmish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimission', 'n.', 'Leave to depart; a dismissing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimissory', 'a.', 'Sending away; dismissing to another jurisdiction;\n   granting leave to depart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimit', 'v. t.', 'To dismiss, let go, or release.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimity', 'n.', 'A cotton fabric employed for hangings and furniture\n   coverings, and formerly used for womens under-garments. It is of many\n   patterns, both plain and twilled, and occasionally is printed in\n   colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimly', 'adv.', 'In a dim or obscure manner; not brightly or clearly; with\n   imperfect sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimmish', 'a.', 'Alt. of Dimmy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimmy', 'a.', 'Somewhat dim; as, dimmish eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimness', 'n.', 'The state or quality / being dim; lack of brightness,\n   clearness, or distinctness; dullness; obscurity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimness', 'n.', 'Dullness, or want of clearness, of vision or of\n   intellectual perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimorph', 'n.', 'Either one of the two forms of a dimorphous substance; as,\n   calcite and aragonite are dimorphs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimorphic', 'a.', 'Having the property of dimorphism; dimorphous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimorphism', 'n.', 'Difference of form between members of the same species,\n   as when a plant has two kinds of flowers, both hermaphrodite (as in the\n   partridge berry), or when there are two forms of one or both sexes of\n   the same species of butterfly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimorphism', 'n.', 'Crystallization in two independent forms of the same\n   chemical compound, as of calcium carbonate as calcite and aragonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimorphous', 'a.', 'Characterized by dimorphism; occurring under two\n   distinct forms, not dependent on sex; dimorphic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimorphous', 'a.', 'Crystallizing under two forms fundamentally different,\n   while having the same chemical composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimple', 'n.', 'A slight natural depression or indentation on the surface\n   of some part of the body, esp. on the cheek or chin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimple', 'n.', 'A slight indentation on any surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimpled', 'imp. & p. p.', 'of Dimple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimpling', 'p. pr. & vb. n.', 'of Dimple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimple', 'v. i.', 'To form dimples; to sink into depressions or little\n   inequalities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimple', 'v. t.', 'To mark with dimples or dimplelike depressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimplement', 'n.', 'The state of being dimpled, or marked with gentle\n   depressions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimply', 'a.', 'Full of dimples, or small depressions; dimpled; as, the\n   dimply pool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dim-sighted', 'a.', 'Having dim sight; lacking perception.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimya', 'n. pl.', 'Alt. of Dimyaria');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimyaria', 'n. pl.', 'An order of lamellibranchiate mollusks having an\n   anterior and posterior adductor muscle, as the common clam. See\n   Bivalve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimyarian', 'a.', 'Like or pertaining to the Dimya.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimyarian', 'n.', 'One of the Dimya.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dimyary', 'a. & n.', 'Same as Dimyarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Din', 'n.', 'Loud, confused, harsh noise; a loud, continuous, rattling or\n   clanging sound; clamor; roar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinned', 'imp. & p. p.', 'of Din');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinning', 'p. pr. & vb. n.', 'of Din');
  INSERT INTO words (word, word_type, description) VALUES  (    'Din', 'n.', 'To strike with confused or clanging sound; to stun with loud\n   and continued noise; to harass with clamor; as, to din the ears with\n   cries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Din', 'n.', 'To utter with a din; to repeat noisily; to ding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Din', 'v. i.', 'To sound with a din; a ding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinaphthyl', 'n.', 'A colorless, crystalline hydrocarbon, C20H14, obtained\n   from naphthylene, and consisting of a doubled naphthylene radical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinar', 'n.', 'A petty money of accounts of Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinar', 'n.', 'An ancient gold coin of the East.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinarchy', 'n.', 'See Diarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dined', 'imp. & p. p.', 'of Dine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dining', 'p. pr. & vb. n.', 'of Dine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dine', 'v. i.', 'To eat the principal regular meal of the day; to take\n   dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dine', 'v. t.', 'To give a dinner to; to furnish with the chief meal; to\n   feed; as, to dine a hundred men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dine', 'v. t.', 'To dine upon; to have to eat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diner', 'n.', 'One who dines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diner-out', 'n.', 'One who often takes his dinner away from home, or in\n   company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinetical', 'a.', 'Revolving on an axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinged', 'imp. & p. p.', 'of Ding');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dang', '', 'of Ding');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dung', '', 'of Ding');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinging', 'p. pr. & vb. n.', 'of Ding');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ding', 'v. t.', 'To dash; to throw violently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ding', 'v. t.', 'To cause to sound or ring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ding', 'v. i.', 'To strike; to thump; to pound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ding', 'v. i.', 'To sound, as a bell; to ring; to clang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ding', 'v. i.', 'To talk with vehemence, importunity, or reiteration; to\n   bluster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ding', 'n.', 'A thump or stroke, especially of a bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dingdong', 'n.', 'The sound of, or as of, repeated strokes on a metallic\n   body, as a bell; a repeated and monotonous sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dingdong', 'n.', 'An attachment to a clock by which the quarter hours are\n   struck upon bells of different tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dingey', 'n.', 'Alt. of Dinghy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dingy', 'n.', 'Alt. of Dinghy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinghy', 'n.', 'A kind of boat used in the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinghy', 'n.', 'A ships smallest boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dingily', 'adv.', 'In a dingy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinginess', 'n.', 'Quality of being dingy; a dusky hue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dingle', 'n.', 'A narrow dale; a small dell; a small, secluded, and\n   embowered valley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dingle-dangle', 'adv.', 'In a dangling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dingo', 'n.', 'A wild dog found in Australia, but supposed to have\n   introduced at a very early period. It has a wolflike face, bushy tail,\n   and a reddish brown color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dingthrift', 'n.', 'A spendthrift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dingy', 'superl.', 'Soiled; sullied; of a dark or dusky color; dark brown;\n   dirty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinichthys', 'n.', 'A genus of large extinct Devonian ganoid fishes. In\n   some parts of Ohio remains of the Dinichthys are abundant, indicating\n   animals twenty feet in length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dining', 'n. & a.', 'from Dine, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dink', 'a.', 'Trim; neat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dink', 'v. t.', 'To deck; -- often with out or up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinmont', 'n.', 'A wether sheep between one and two years old.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinner', 'n.', 'The principal meal of the day, eaten by most people about\n   midday, but by many (especially in cities) at a later hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinner', 'n.', 'An entertainment; a feast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinnerless', 'a.', 'Having no dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinnerly', 'a.', 'Of or pertaining to dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinoceras', 'n.', 'A genus of large extinct Eocene mammals from Wyoming; --\n   called also Uintatherium. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinornis', 'n.', 'A genus of extinct, ostrichlike birds of gigantic size,\n   which formerly inhabited New Zealand. See Moa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinosaur', 'n.', 'Alt. of Dinosaurian');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinosaurian', 'n.', 'One of the Dinosauria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinosauria', 'n. pl.', 'An order of extinct mesozoic reptiles, mostly of\n   large size (whence the name). Notwithstanding their size, they present\n   birdlike characters in the skeleton, esp. in the pelvis and hind limbs.\n   Some walked on their three-toed hind feet, thus producing the large\n   \"bird tracks,\" so-called, of mesozoic sandstones; others were five-toed\n   and quadrupedal. See Illust. of Compsognathus, also Illustration of\n   Dinosaur in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinothere', 'n.', 'Alt. of Dinotherium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinotherium', 'n.', 'A large extinct proboscidean mammal from the miocene\n   beds of Europe and Asia. It is remarkable fora pair of tusks directed\n   downward from the decurved apex of the lower jaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinoxide', 'n.', 'Same as Dioxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinsome', 'a.', 'Full of din.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dint', 'n.', 'A blow; a stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dint', 'n.', 'The mark left by a blow; an indentation or impression made by\n   violence; a dent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dint', 'n.', 'Force; power; -- esp. in the phrase by dint of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinted', 'imp. & p. p.', 'of Dint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinting', 'p. pr. & vb. n.', 'of Dint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dint', 'v. t.', 'To make a mark or cavity on or in, by a blow or by\n   pressure; to dent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dinumeration', 'n.', 'Enumeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diocesan', 'a.', 'Of or pertaining to a diocese; as, diocesan missions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diocesan', 'n.', 'A bishop, viewed in relation to his diocese; as, the\n   diocesan of New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diocesan', 'n.', 'The clergy or the people of a diocese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioceses', 'pl. ', 'of Diocese');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diocese', 'n.', 'The circuit or extent of a bishops jurisdiction; the\n   district in which a bishop exercises his ecclesiastical authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diocesener', 'n.', 'One who belongs to a diocese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diodon', 'n.', 'A genus of spinose, plectognath fishes, having the teeth of\n   each jaw united into a single beaklike plate. They are able to inflate\n   the body by taking in air or water, and, hence, are called globefishes,\n   swellfishes, etc. Called also porcupine fishes, and sea hedgehogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diodon', 'n.', 'A genus of whales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diodont', 'a.', 'Like or pertaining to the genus Diodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diodont', 'n.', 'A fish of the genus Diodon, or an allied genus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioecia', 'n. pl.', 'A Linnaean class of plants having the stamens and\n   pistils on different plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioecia', 'n. pl.', 'A subclass of gastropod mollusks in which the sexes\n   are separate. It includes most of the large marine species, like the\n   conchs, cones, and cowries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioecian', 'a.', 'Alt. of Dioecious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioecious', 'a.', 'Having the sexes in two separate individuals; -- applied\n   to plants in which the female flowers occur on one individual and the\n   male flowers on another of the same species, and to animals in which\n   the ovum is produced by one individual and the sperm cell by another;\n   -- opposed to monoecious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioeciously', 'adv.', 'In a dioecious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioeciousness', 'n.', 'The state or quality of being dioecious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioecism', 'n.', 'The condition of being dioecious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diogenes', 'n.', 'A Greek Cynic philosopher (412?-323 B. C.) who lived much\n   in Athens and was distinguished for contempt of the common aims and\n   conditions of life, and for sharp, caustic sayings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioicous', 'a.', 'See Dioecious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diomedea', 'n.', 'A genus of large sea birds, including the albatross. See\n   Albatross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dionaea', 'n.', 'An insectivorous plant. See Venuss flytrap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dionysian', 'a.', 'Relating to Dionysius, a monk of the 6th century; as,\n   the Dionysian, or Christian, era.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diophantine', 'a.', 'Originated or taught by Diophantus, the Greek writer\n   on algebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diopside', 'n.', 'A crystallized variety of pyroxene, of a clear, grayish\n   green color; mussite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioptase', 'n.', 'A hydrous silicate of copper, occurring in emerald-green\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diopter', 'n.', 'Alt. of Dioptra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioptra', 'n.', 'An optical instrument, invented by Hipparchus, for taking\n   altitudes, leveling, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioptre', 'n.', 'A unit employed by oculists in numbering glasses according\n   to the metric system; a refractive power equal to that of a glass whose\n   principal focal distance is one meter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioptric', 'a.', 'Of or pertaining to the dioptre, or to the metric system\n   of numbering glasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioptric', 'n.', 'A dioptre. See Dioptre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioptric', 'a.', 'Alt. of Dioptrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioptrical', 'a.', 'Of or pertaining to dioptrics; assisting vision by\n   means of the refraction of light; refractive; as, the dioptric system;\n   a dioptric glass or telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioptrics', 'n.', 'The science of the refraction of light; that part of\n   geometrical optics which treats of the laws of the refraction of light\n   in passing from one medium into another, or through different mediums,\n   as air, water, or glass, and esp. through different lenses; --\n   distinguished from catoptrics, which refers to reflected light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioptry', 'n.', 'A dioptre.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diorama', 'n.', 'A mode of scenic representation, invented by Daguerre and\n   Bouton, in which a painting is seen from a distance through a large\n   opening. By a combination of transparent and opaque painting, and of\n   transmitted and reflected light, and by contrivances such as screens\n   and shutters, much diversity of scenic effect is produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diorama', 'n.', 'A building used for such an exhibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioramic', 'a.', 'Pertaining to a diorama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diorism', 'n.', 'Definition; logical direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioristic', 'a.', 'Distinguishing; distinctive; defining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diorite', 'n.', 'An igneous, crystalline in structure, consisting\n   essentially of a triclinic feldspar and hornblende. It includes part of\n   what was called greenstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioritic', 'a.', 'Containing diorite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diorthotic', 'a.', 'Relating to the correcting or straightening out of\n   something; corrective.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioscorea', 'n.', 'A genus of plants. See Yam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diota', 'n.', 'A vase or drinking cup having two handles or ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioxide', 'n.', 'An oxide containing two atoms of oxygen in each molecule;\n   binoxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioxide', 'n.', 'An oxide containing but one atom or equivalent of oxygen\n   to two of a metal; a suboxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dioxindol', 'n.', 'A white, crystalline, nitrogenous substance obtained by\n   the reduction of isatin. It is a member of the indol series; -- hence\n   its name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipped', 'imp. & p. p.', 'of Dip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipt', '', 'of Dip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipping', 'p. pr. & vb. n.', 'of Dip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. t.', 'To plunge or immerse; especially, to put for a moment into\n   a liquid; to insert into a fluid and withdraw again.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. t.', 'To immerse for baptism; to baptize by immersion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. t.', 'To wet, as if by immersing; to moisten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. t.', 'To plunge or engage thoroughly in any affair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. t.', 'To take out, by dipping a dipper, ladle, or other\n   receptacle, into a fluid and removing a part; -- often with out; as, to\n   dip water from a boiler; to dip out water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. t.', 'To engage as a pledge; to mortgage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. i.', 'To immerse ones self; to become plunged in a liquid; to\n   sink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. i.', 'To perform the action of plunging some receptacle, as a\n   dipper, ladle. etc.; into a liquid or a soft substance and removing a\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. i.', 'To pierce; to penetrate; -- followed by in or into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. i.', 'To enter slightly or cursorily; to engage ones self\n   desultorily or by the way; to partake limitedly; -- followed by in or\n   into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. i.', 'To incline downward from the plane of the horizon; as,\n   strata of rock dip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'v. i.', 'To dip snuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'n.', 'The action of dipping or plunging for a moment into a liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'n.', 'Inclination downward; direction below a horizontal line;\n   slope; pitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'n.', 'A liquid, as a sauce or gravy, served at table with a ladle or\n   spoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dip', 'n.', 'A dipped candle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipaschal', 'a.', 'Including two passovers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipchick', 'n.', 'See Dabchick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipetalous', 'a.', 'Having two petals; two-petaled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphenyl', 'n.', 'A white crystalline substance, C6H5.C6H5, obtained by\n   leading benzene through a heated iron tube. It consists of two benzene\n   or phenyl radicals united.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphtheria', 'n.', 'A very dangerous contagious disease in which the air\n   passages, and especially the throat, become coated with a false\n   membrane, produced by the solidification of an inflammatory exudation.\n   Cf. Group.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphtherial', 'a.', 'Alt. of Diphtheric');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphtheric', 'a.', 'Relating to diphtheria; diphtheritic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphtheritic', 'a.', 'Pertaining to, or connected with, diphtheria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphtheritic', 'a.', 'Having characteristics resembling those of\n   diphtheria; as, diphtheritic inflammation of the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphthong', 'n.', 'A coalition or union of two vowel sounds pronounced in\n   one syllable; as, ou in out, oi in noise; -- called a proper diphthong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphthong', 'n.', 'A vowel digraph; a union of two vowels in the same\n   syllable, only one of them being sounded; as, ai in rain, eo in people;\n   -- called an improper diphthong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphthong', 'v. t.', 'To form or pronounce as a diphthong; diphthongize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphthongal', 'a.', 'Relating or belonging to a diphthong; having the\n   nature of a diphthong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphthongalize', 'v. t.', 'To make into a diphthong; to pronounce as a\n   diphthong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphthongation', 'n.', 'See Diphthongization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphthongic', 'a.', 'Of the nature of diphthong; diphthongal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphthongization', 'n.', 'The act of changing into a diphthong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphthongize', 'v. t. & i.', 'To change into a diphthong, as by affixing\n   another vowel to a simple vowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphycercal', 'a.', 'Having the tail fin divided into two equal parts by\n   the notochord, or end of the vertebral column; protocercal. See\n   Protocercal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphygenic', 'a.', 'Having two modes of embryonic development.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphyllous', 'a.', 'Having two leaves, as a calyx, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphyodont', 'a.', 'Having two successive sets of teeth (deciduous and\n   permanent), one succeeding the other; as, a diphyodont mammal;\n   diphyodont dentition; -- opposed to monophyodont.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphyodont', 'n.', 'An animal having two successive sets of teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diphyozooid', 'n.', 'One of the free-swimming sexual zooids of\n   Siphonophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplanar', 'a.', 'Of or pertaining to two planes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipleidoscope', 'n.', 'An instrument for determining the time of apparent\n   noon. It consists of two mirrors and a plane glass disposed in the form\n   of a prism, so that, by the reflections of the suns rays from their\n   surfaces, two images are presented to the eye, moving in opposite\n   directions, and coinciding at the instant the suns center is on the\n   meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diploblastic', 'a.', 'Characterizing the ovum when it has two primary\n   germinal layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplocardiac', 'a.', 'Having the heart completely divided or double, one\n   side systemic, the other pulmonary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplococci', 'pl. ', 'of Diplococcus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplococcus', 'n.', 'A form of micrococcus in which cocci are united in a\n   binary manner. See Micrococcus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diploe', 'n.', 'The soft, spongy, or cancellated substance between the\n   plates of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diploetic', 'a.', 'Diploic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplogenic', 'a.', 'Partaking of the nature of two bodies; producing two\n   substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diploic', 'a.', 'Of or pertaining to the diploe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diploid', 'n.', 'A solid bounded by twenty-four similar quadrilateral\n   faces. It is a hemihedral form of the hexoctahedron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomas', 'pl. ', 'of Diploma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diploma', 'n.', 'A letter or writing, usually under seal, conferring some\n   privilege, honor, or power; a document bearing record of a degree\n   conferred by a literary society or educational institution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomacy', 'n.', 'The art and practice of conducting negotiations between\n   nations (particularly in securing treaties), including the methods and\n   forms usually employed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomacy', 'n.', 'Dexterity or skill in securing advantages; tact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomacy', 'n.', 'The body of ministers or envoys resident at a court; the\n   diplomatic body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomat', 'n.', 'Alt. of Diplomate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomate', 'n.', 'A diplomatist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomate', 'v. t.', 'To invest with a title o/ privilege by diploma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomatial', 'a.', 'Diplomatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomatic', 'a.', 'Alt. of Diplomatical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomatical', 'a.', 'Pertaining to diplomacy; relating to the foreign\n   ministers at a court, who are called the diplomatic body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomatical', 'a.', 'Characterized by tact and shrewdness; dexterous;\n   artful; as, diplomatic management.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomatical', 'a.', 'Pertaining to diplomatics; paleographic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomatic', 'n.', 'A minister, official agent, or envoy to a foreign\n   court; a diplomatist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomatically', 'adv.', 'According to the rules of diplomacy; in the\n   manner of a diplomatist; artfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomatic', 'n.', 'The science of diplomas, or the art of deciphering\n   ancient writings, and determining their age, authenticity, etc.;\n   paleography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomatism', 'n.', 'Diplomacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplomatist', 'n.', 'A person employed in, or skilled in, diplomacy; a\n   diplomat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplopia', 'n.', 'Alt. of Diplopy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplopy', 'n.', 'The act or state of seeing double.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplopod', 'n.', 'One of the Diplopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplopoda', 'n. pl.', 'An order of myriapods having two pairs of legs on\n   each segment; the Chilognatha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplostemonous', 'a.', 'Having twice as many stamens as petals, as the\n   geranium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diplostemony', 'n.', 'The condition of being diplostemonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipneumona', 'n. pl.', 'A group of spiders having only two lunglike organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipnoi', 'n. pl.', 'A group of ganoid fishes, including the living genera\n   Ceratodus and Lepidosiren, which present the closest approximation to\n   the Amphibia. The air bladder acts as a lung, and the nostrils open\n   inside the mouth. See Ceratodus, and Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipodies', 'pl. ', 'of Dipody');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipody', 'n.', 'Two metrical feet taken together, or included in one\n   measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipolar', 'a.', 'Having two poles, as a magnetic bar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dippels oil', '', 'See Bone oil, under Bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipper', 'n.', 'One who, or that which, dips; especially, a vessel used to\n   dip water or other liquid; a ladle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipper', 'n.', 'A small grebe; the dabchick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipper', 'n.', 'The buffel duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipper', 'n.', 'The water ouzel (Cinolus aquaticus) of Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipper', 'n.', 'The American dipper or ouzel (Cinclus Mexicanus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipping', 'n.', 'The act or process of immersing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipping', 'n.', 'The act of inclining downward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipping', 'n.', 'The act of lifting or moving a liquid with a dipper,\n   ladle, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipping', 'n.', 'The process of cleaning or brightening sheet metal or\n   metalware, esp. brass, by dipping it in acids, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipping', 'n.', 'The practice of taking snuff by rubbing the teeth or gums\n   with a stick or brush dipped in snuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diprismatic', 'a.', 'Doubly prismatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipropargyl', 'n.', 'A pungent, mobile, volatile liquid, C6H6, produced\n   artificially from certain allyl derivatives. Though isomeric with\n   benzine, it is very different in its chemical relations. Called also\n   dipropinyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipropyl', 'n.', 'One of the hexane paraffins, found in petroleum,\n   consisting of two propyl radicals. See Hexane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diprotodon', 'n.', 'An extinct Quaternary marsupial from Australia, about\n   as large as the hippopotamus; -- so named because of its two large\n   front teeth. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipsas', 'n.', 'A serpent whose bite was fabled to produce intense thirst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipsas', 'n.', 'A genus of harmless colubrine snakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipsetic', 'a.', 'Tending to produce thirst.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipsomania', 'n.', 'A morbid an uncontrollable craving (often periodic) for\n   drink, esp. for alcoholic liquors; also improperly used to denote acute\n   and chronic alcoholism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipsomaniac', 'n.', 'One who has an irrepressible desire for alcoholic\n   drinks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipsomaniacal', 'a.', 'Of or pertaining to dipsomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipsosis', 'n.', 'Excessive thirst produced by disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diptera', 'n. pl.', 'An extensive order of insects having only two\n   functional wings and two balancers, as the house fly, mosquito, etc.\n   They have a suctorial proboscis, often including two pairs of sharp\n   organs (mandibles and maxillae) with which they pierce the skin of\n   animals. They undergo a complete metamorphosis, their larvae (called\n   maggots) being usually without feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipteral', 'a.', 'Having two wings only; belonging to the order Diptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipteral', 'a.', 'Having a double row of columns on each on the flanks, as\n   well as in front and rear; -- said of a temple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipteran', 'n.', 'An insect of the order Diptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipterocarpus', 'n.', 'A genus of trees found in the East Indies, some\n   species of which produce a fragrant resin, other species wood oil. The\n   fruit has two long wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipterous', 'a.', 'Having two wings, as certain insects; belonging to the\n   order Diptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipterous', 'a.', 'Having two wings; two-winged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipterygian', 'a.', 'Having two dorsal fins; -- said of certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diptote', 'n.', 'A noun which has only two cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diptych', 'n.', 'Anything consisting of two leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diptych', 'n.', 'A writing tablet consisting of two leaves of rigid\n   material connected by hinges and shutting together so as to protect the\n   writing within.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diptych', 'n.', 'A picture or series of pictures painted on two tablets\n   connected by hinges. See Triptych.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diptych', 'n.', 'A double catalogue, containing in one part the names of\n   living, and in the other of deceased, ecclesiastics and benefactors of\n   the church; a catalogue of saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipyre', 'n.', 'A mineral of the scapolite group; -- so called from the\n   double effect of fire upon it, in fusing it, and rendering it\n   phosphorescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipyrenous', 'a.', 'Containing two stones or nutlets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipyridine', 'n.', 'A polymeric form of pyridine, C10H10N2, obtained as a\n   colorless oil by the action of sodium on pyridine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dipyridil', 'n.', 'A crystalline nitrogenous base, C10H8N2, obtained by the\n   reduction of pyridine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diradiation', 'n.', 'The emission and diffusion of rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dire', 'superl.', 'Ill-boding; portentous; as, dire omens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dire', 'superl.', 'Evil in great degree; dreadful; dismal; horrible;\n   terrible; lamentable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'a.', 'Straight; not crooked, oblique, or circuitous; leading by\n   the short or shortest way to a point or end; as, a direct line; direct\n   means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'a.', 'Straightforward; not of crooked ways, or swerving from\n   truth and openness; sincere; outspoken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'a.', 'Immediate; express; plain; unambiguous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'a.', 'In the line of descent; not collateral; as, a descendant in\n   the direct line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'a.', 'In the direction of the general planetary motion, or from\n   west to east; in the order of the signs; not retrograde; -- said of the\n   motion of a celestial body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directed', 'imp. & p. p.', 'of Direct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directing', 'p. pr. & vb. n.', 'of Direct');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'v. t.', 'To arrange in a direct or straight line, as against a\n   mark, or towards a goal; to point; to aim; as, to direct an arrow or a\n   piece of ordnance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'v. t.', 'To point out or show to (any one), as the direct or\n   right course or way; to guide, as by pointing out the way; as, he\n   directed me to the left-hand road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'v. t.', 'To determine the direction or course of; to cause to go\n   on in a particular manner; to order in the way to a certain end; to\n   regulate; to govern; as, to direct the affairs of a nation or the\n   movements of an army.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'v. t.', 'To point out to with authority; to instruct as a\n   superior; to order; as, he directed them to go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'v. t.', 'To put a direction or address upon; to mark with the\n   name and residence of the person to whom anything is sent; to\n   superscribe; as, to direct a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'v. i.', 'To give direction; to point out a course; to act as\n   guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct', 'n.', 'A character, thus [/], placed at the end of a staff on the\n   line or space of the first note of the next staff, to apprise the\n   performer of its situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direct-acting', 'a.', 'Acting directly, as one part upon another, without\n   the intervention of other working parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directer', 'n.', 'One who directs; a director.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direction', 'n.', 'The act of directing, of aiming, regulating, guiding, or\n   ordering; guidance; management; superintendence; administration; as,\n   the direction o/ public affairs or of a bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direction', 'n.', 'That which is imposed by directing; a guiding or\n   authoritative instruction; prescription; order; command; as, he grave\n   directions to the servants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direction', 'n.', 'The name and residence of a person to whom any thing is\n   sent, written upon the thing sent; superscription; address; as, the\n   direction of a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direction', 'n.', 'The line or course upon which anything is moving or\n   aimed to move, or in which anything is lying or pointing; aim; line or\n   point of tendency; direct line or course; as, the ship sailed in a\n   southeasterly direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direction', 'n.', 'The body of managers of a corporation or enterprise;\n   board of directors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direction', 'n.', 'The pointing of a piece with reference to an imaginary\n   vertical axis; -- distinguished from elevation. The direction is given\n   when the plane of sight passes through the object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directive', 'a.', 'Having power to direct; tending to direct, guide, or\n   govern; showing the way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directive', 'a.', 'Able to be directed; manageable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directly', 'adv.', 'In a direct manner; in a straight line or course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directly', 'adv.', 'In a straightforward way; without anything intervening;\n   not by secondary, but by direct, means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directly', 'adv.', 'Without circumlocution or ambiguity; absolutely; in\n   express terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directly', 'adv.', 'Exactly; just.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directly', 'adv.', 'Straightforwardly; honestly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directly', 'adv.', 'Manifestly; openly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directly', 'adv.', 'Straightway; next in order; without delay; immediately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directly', 'adv.', 'Immediately after; as soon as.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directness', 'n.', 'The quality of being direct; straightness;\n   straightforwardness; immediateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Director', 'n.', 'One who, or that which, directs; one who regulates,\n   guides, or orders; a manager or superintendent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Director', 'n.', 'One of a body of persons appointed to manage the affairs\n   of a company or corporation; as, the directors of a bank, insurance\n   company, or railroad company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Director', 'n.', 'A part of a machine or instrument which directs its\n   motion or action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Director', 'n.', 'A slender grooved instrument upon which a knife is made\n   to slide when it is wished to limit the extent of motion of the latter,\n   or prevent its injuring the parts beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directorate', 'n.', 'The office of director; also, a body of directors\n   taken jointly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directorial', 'a.', 'Having the quality of a director, or authoritative\n   guide; directive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directorial', 'a.', 'Pertaining to: director or directory; specifically,\n   relating to the Directory of France under the first republic. See\n   Directory, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directorship', 'n.', 'The condition or office of a director; directorate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directory', 'a.', 'Containing directions; enjoining; instructing;\n   directorial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directories', 'pl. ', 'of Directory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directory', 'n.', 'A collection or body of directions, rules, or\n   ordinances; esp., a book of directions for the conduct of worship; as,\n   the Directory used by the nonconformists instead of the Prayer Book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directory', 'n.', 'A book containing the names and residences of the\n   inhabitants of any place, or of classes of them; an address book; as, a\n   business directory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directory', 'n.', 'A body of directors; board of management; especially, a\n   committee which held executive power in France under the first\n   republic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directory', 'n.', 'Direction; guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directress', 'n.', 'A woman who directs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directrixes', 'pl. ', 'of Directrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directrix', 'n.', 'A directress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directrix', 'n.', 'A line along which a point in another line moves, or\n   which in any way governs the motion of the point and determines the\n   position of the curve generated by it; the line along which the\n   generatrix moves in generating a surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Directrix', 'n.', 'A straight line so situated with respect to a conic\n   section that the distance of any point of the curve from it has a\n   constant ratio to the distance of the same point from the focus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direful', 'a.', 'Dire; dreadful; terrible; calamitous; woeful; as, a\n   direful fiend; a direful day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direly', 'adv.', 'In a dire manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirempt', 'a.', 'Divided; separated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirempt', 'v. t.', 'To separate by force; to tear apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diremption', 'n.', 'A tearing apart; violent separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direness', 'n.', 'Terribleness; horror; woefulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direption', 'n.', 'The act of plundering, despoiling, or snatching away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direptitious', 'a.', 'Characterized by direption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Direptitiously', 'adv.', 'With plundering violence; by violent injustice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirge', 'a.', 'A piece of music of a mournful character, to accompany\n   funeral rites; a funeral hymn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirgeful', 'a.', 'Funereal; moaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirige', 'n.', 'A service for the dead, in the Roman Catholic Church, being\n   the first antiphon of Matins for the dead, of which Dirige is the first\n   word; a dirge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirigent', 'a.', 'Directing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirigent', 'n.', 'The line of motion along which a describent line or\n   surface is carried in the genesis of any plane or solid figure; a\n   directrix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirigible', 'a.', 'Capable of being directed; steerable; as, a dirigible\n   balloon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diriment', 'a.', 'Absolute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirk', 'n.', 'A kind of dagger or poniard; -- formerly much used by the\n   Scottish Highlander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirked', 'imp. & p. p.', 'of Dirk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirking', 'p. pr. & vb. n.', 'of Dirk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirk', 'v. t.', 'To stab with a dirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirk', 'a.', 'Dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirk', 'v. t.', 'To darken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirkness', 'n.', 'Darkness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirl', 'v. i. & t.', 'To thrill; to vibrate; to penetrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirt', 'n.', 'Any foul of filthy substance, as excrement, mud, dust, etc.;\n   whatever, adhering to anything, renders it foul or unclean; earth; as,\n   a wagonload of dirt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirt', 'n.', 'Meanness; sordidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirt', 'n.', 'In placer mining, earth, gravel, etc., before washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirt', 'v. t.', 'To make foul of filthy; to dirty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirtily', 'adv.', 'In a dirty manner; foully; nastily; filthily; meanly;\n   sordidly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirtiness', 'n.', 'The state of being dirty; filthiness; foulness;\n   nastiness; baseness; sordidness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirty', 'superl.', 'Defiled with dirt; foul; nasty; filthy; not clean or\n   pure; serving to defile; as, dirty hands; dirty water; a dirty white.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirty', 'superl.', 'Sullied; clouded; -- applied to color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirty', 'superl.', 'Sordid; base; groveling; as, a dirty fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirty', 'superl.', 'Sleety; gusty; stormy; as, dirty weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirtied', 'imp. & p. p.', 'of Dirty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirtying', 'p. pr. & vb. n.', 'of Dirty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirty', 'v. t.', 'To foul; to make filthy; to soil; as, to dirty the\n   clothes or hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dirty', 'v. t.', 'To tarnish; to sully; to scandalize; -- said of\n   reputation, character, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diruption', 'a.', 'Disruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dis-', '', '.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dis-', '', 'A prefix from the Latin, whence F. des, or sometimes de-, dis-.\n   The Latin dis-appears as di-before b, d, g, l, m, n, r, v, becomes\n   dif-before f, and either dis-or di- before j. It is from the same root\n   as bis twice, and duo, E. two. See Two, and cf. Bi-, Di-, Dia-.\n   Dis-denotes separation, a parting from, as in distribute, disconnect;\n   hence it often has the force of a privative and negative, as in disarm,\n   disoblige, disagree. Also intensive, as in dissever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dis-', '', 'A prefix from Gr. di`s- twice. See Di-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dis', 'n.', 'The god Pluto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disabilities', 'pl. ', 'of Disability');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disability', 'n.', 'State of being disabled; deprivation or want of\n   ability; absence of competent physical, intellectual, or moral power,\n   means, fitness, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disability', 'n.', 'Want of legal qualification to do a thing; legal\n   incapacity or incompetency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disable', 'a.', 'Lacking ability; unable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disabled', 'imp. & p. p.', 'of Disable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disabling', 'p. pr. & vb. n.', 'of Disable');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disable', 'v. t.', 'To render unable or incapable; to destroy the force,\n   vigor, or power of action of; to deprive of competent physical or\n   intellectual power; to incapacitate; to disqualify; to make incompetent\n   or unfit for service; to impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disable', 'v. t.', 'To deprive of legal right or qualification; to render\n   legally incapable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disable', 'v. t.', 'To deprive of that which gives value or estimation; to\n   declare lacking in competency; to disparage; to undervalue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disablement', 'n.', 'Deprivation of ability; incapacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disabused', 'imp. & p. p.', 'of Disabuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disabusing', 'p. pr. & vb. n.', 'of Disabuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disabuse', 'v. t.', 'To set free from mistakes; to undeceive; to disengage\n   from fallacy or deception; to set right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaccommodate', 'v. t.', 'To put to inconvenience; to incommode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaccommodation', 'n.', 'A state of being unaccommodated or unsuited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaccord', 'v. i.', 'To refuse to assent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaccord', 'n.', 'Disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaccordant', 'a.', 'Not accordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaccustom', 'v. t.', 'To destroy the force of habit in; to wean from a\n   custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disacidify', 'v. t.', 'To free from acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disacknowledged', 'imp. & p. p.', 'of Disacknowledge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disacknowledging', 'p. pr. & vb. n.', 'of Disacknowledge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disacknowledge', 'v. t.', 'To refuse to acknowledge; to deny; to disown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disacquaint', 'v. t.', 'To render unacquainted; to make unfamiliar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disacquaintance', 'n.', 'Neglect of disuse of familiarity, or familiar\n   acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disacryl', 'n.', 'A white amorphous substance obtained as a polymeric\n   modification of acrolein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disadorn', 'v. t.', 'To deprive of ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disadvance', 'v. t. & i.', 'To draw back, or cause to draw back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disadvantage', 'n.', 'Deprivation of advantage; unfavorable or prejudicial\n   quality, condition, circumstance, or the like; that which hinders\n   success, or causes loss or injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disadvantage', 'n.', 'Loss; detriment; hindrance; prejudice to interest,\n   fame, credit, profit, or other good.');
