  INSERT INTO words (word, word_type, description) VALUES  (    'Disadvantageous', 'a.', 'Attended with disadvantage; unfavorable to success\n   or prosperity; inconvenient; prejudicial; -- opposed to advantageous;\n   as, the situation of an army is disadvantageous for attack or defense.');
