  INSERT INTO words (word, word_type, description) VALUES  (    'Disaffirm', 'v. t.', 'To refuse to confirm; to annul, as a judicial\n   decision, by a contrary judgment of a superior tribunal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaffirmance', 'n.', 'The act of disaffirming; denial; negation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaffirmance', 'n.', 'Overthrow or annulment by the decision of a superior\n   tribunal; as, disaffirmance of judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaffirmation', 'n.', 'The act of disaffirming; negation; refutation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disafforested', 'imp. & p. p.', 'of Disafforest');
