  INSERT INTO words (word, word_type, description) VALUES  (    'Disafforest', 'v. t.', 'To reduce from the privileges of a forest to the\n   state of common ground; to exempt from forest laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaggregate', 'v. t.', 'To destroy the aggregation of; to separate into\n   component parts, as an aggregate mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disaggregation', 'n.', 'The separation of an aggregate body into its\n   component parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disagreed', 'imp. & p. p.', 'of Disagree');
