  INSERT INTO words (word, word_type, description) VALUES  (    'Disagree', 'v. i.', 'To be unsuited; to have unfitness; as, medicine\n   sometimes disagrees with the patient; food often disagrees with the\n   stomach or the taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disagreeable', 'a.', 'Not agreeable, conformable, or congruous; contrary;\n   unsuitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disagreeable', 'a.', 'Exciting repugnance; offensive to the feelings or\n   senses; displeasing; unpleasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disagreeableness', 'n.', 'The state or quality of being; disagreeable;\n   unpleasantness.');
