  INSERT INTO words (word, word_type, description) VALUES  (    'Disagreement', 'n.', 'The state of disagreeing; a being at variance;\n   dissimilitude; diversity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disagreement', 'n.', 'Unsuitableness; unadaptedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disagreement', 'n.', 'Difference of opinion or sentiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disagreement', 'n.', 'A falling out, or controversy; difference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disagreer', 'n.', 'One who disagrees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disalliege', 'v. t.', 'To alienate from allegiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disallowed', 'imp. & p. p.', 'of Disallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disallowing', 'p. pr. & vb. n.', 'of Disallow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disallow', 'v. t.', 'To refuse to allow; to deny the force or validity of;\n   to disown and reject; as, the judge disallowed the executors charge.');
