  INSERT INTO words (word, word_type, description) VALUES  (    'Disangelical', 'a.', 'Not angelical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disanimated', 'imp. & p. p.', 'of Disanimate');
