  INSERT INTO words (word, word_type, description) VALUES  (    'Disannulment', 'n.', 'Complete annulment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disanoint', 'v. t.', 'To invalidate the consecration of; as, to disanoint a\n   king.');
