  INSERT INTO words (word, word_type, description) VALUES  (    'Disappearing', 'p. pr. & vb. n.', 'of Disappear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappear', 'v. i.', 'To cease to appear or to be perceived; to pass from\n   view, gradually or suddenly; to vanish; to be no longer seen; as,\n   darkness disappears at the approach of light; a ship disappears as she\n   sails from port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappear', 'v. i.', 'To cease to be or exist; as, the epidemic has\n   disappeared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappearance', 'n.', 'The act of disappearing; cessation of appearance;\n   removal from sight; vanishing.');
