  INSERT INTO words (word, word_type, description) VALUES  (    'Disappointing', 'p. pr. & vb. n.', 'of Disappoint');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappoint', 'v. t.', 'To defeat of expectation or hope; to hinder from the\n   attainment of that which was expected, hoped, or desired; to balk; as,\n   a man is disappointed of his hopes or expectations, or his hopes,\n   desires, intentions, expectations, or plans are disappointed; a bad\n   season disappoints the farmer of his crops; a defeat disappoints an\n   enemy of his spoil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappoint', 'v. t.', 'To frustrate; to fail; to hinder of result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappointed', 'a.', 'Defeated of expectation or hope; balked; as, a\n   disappointed person or hope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappointed', 'a.', 'Unprepared; unequipped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappointment', 'n.', 'The act of disappointing, or the state of being\n   disappointed; defeat or failure of expectation or hope; miscarriage of\n   design or plan; frustration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappointment', 'n.', 'That which disappoints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappreciate', 'v. t.', 'To undervalue; not to esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disapprobation', 'n.', 'The act of disapproving; mental condemnation of\n   what is judged wrong, unsuitable, or inexpedient; feeling of censure.');
