  INSERT INTO words (word, word_type, description) VALUES  (    'Disappropriate', 'v. t.', 'To sever from appropriation or possession a\n   spiritual corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disappropriation', 'n.', 'The act of disappropriating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disapproval', 'n.', 'Disapprobation; dislike; censure; adverse judgment.');
