  INSERT INTO words (word, word_type, description) VALUES  (    'Disbursing', 'p. pr. & vb. n.', 'of Disburse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disburse', 'v. t.', 'To pay out; to expend; -- usually from a public fund\n   or treasury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disbursement', 'n.', 'The act of disbursing or paying out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disbursement', 'n.', 'That which is disbursed or paid out; as, the annual\n   disbursements exceed the income.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disburser', 'n.', 'One who disburses money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disburthened', 'imp. & p. p.', 'of Disburthen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disburthening', 'p. pr. & vb. n.', 'of Disburthen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disburthen', 'v. t.', 'To disburden; to relieve of a load.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disc', 'n.', 'A flat round plate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disc', 'n.', 'A circular structure either in plants or animals; as, a blood\n   disc, a germinal disc, etc. Same as Disk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discage', 'v. t.', 'To uncage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discal', 'a.', 'Pertaining to, or resembling, a disk; as, discal cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discalceate', 'v. t.', 'To pull off shoes or sandals from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discalceated', 'a.', 'Deprived off shoes or sandals; unshod; discalced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discalced', 'a.', 'Unshod; barefooted; -- in distinction from calced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discalceation', 'n.', 'The act of pulling off the shoes or sandals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discamp', 'v. t.', 'To drive from a camp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discandy', 'v. i.', 'To melt; to dissolve; to thaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discant', 'n.', 'See Descant, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discapacitate', 'v. t.', 'To deprive of capacity; to incapacitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discarded', 'imp. & p. p.', 'of Discard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discarding', 'p. pr. & vb. n.', 'of Discard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discard', 'v. t.', 'To throw out of ones hand, as superfluous cards; to\n   lay aside (a card or cards).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discard', 'v. t.', 'To cast off as useless or as no longer of service; to\n   dismiss from employment, confidence, or favor; to discharge; to turn\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discard', 'v. t.', 'To put or thrust away; to reject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discard', 'v. i.', 'To make a discard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discard', 'n.', 'The act of discarding; also, the card or cards discarded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discardure', 'n.', 'Rejection; dismissal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discarnate', 'a.', 'Stripped of flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discase', 'v. t.', 'To strip; to undress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discede', 'v. i.', 'To yield or give up; to depart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discept', 'v. i.', 'To debate; to discuss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disceptation', 'n.', 'Controversy; disputation; discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disceptator', 'n.', 'One who arbitrates or decides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerned', 'imp. & p. p.', 'of Discern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerning', 'p. pr. & vb. n.', 'of Discern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discern', 'v. t.', 'To see and identify by noting a difference or\n   differences; to note the distinctive character of; to discriminate; to\n   distinguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discern', 'v. t.', 'To see by the eye or by the understanding; to perceive\n   and recognize; as, to discern a difference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discern', 'v. i.', 'To see or understand the difference; to make\n   distinction; as, to discern between good and evil, truth and falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discern', 'v. i.', 'To make cognizance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discernance', 'n.', 'Discernment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerner', 'n.', 'One who, or that which, discerns, distinguishes,\n   perceives, or judges; as, a discerner of truth, of right and wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discernible', 'a.', 'Capable of being discerned by the eye or the\n   understanding; as, a star is discernible by the eye; the identity of\n   difference of ideas is discernible by the understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discernibleness', 'n.', 'The quality of being discernible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discernibly', 'adv.', 'In a manner to be discerned; perceptibly; visibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerning', 'a.', 'Acute; shrewd; sagacious; sharp-sighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerningly', 'adv.', 'In a discerning manner; with judgment; judiciously;\n   acutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discernment', 'n.', 'The act of discerning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discernment', 'n.', 'The power or faculty of the mind by which it\n   distinguishes one thing from another; power of viewing differences in\n   objects, and their relations and tendencies; penetrative and\n   discriminate mental vision; acuteness; sagacity; insight; as, the\n   errors of youth often proceed from the want of discernment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerp', 'v. t.', 'To tear in pieces; to rend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerp', 'v. t.', 'To separate; to disunite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerpibility', 'n.', 'Alt. of Discerptibility');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerptibility', 'n.', 'Capability or liableness to be discerped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerpible', 'a.', 'Alt. of Discerptible');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerptible', 'a.', 'Capable of being discerped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerption', 'n.', 'The act of pulling to pieces, or of separating the\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discerptive', 'a.', 'Tending to separate or disunite parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discession', 'n.', 'Departure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharged', 'imp. & p. p.', 'of Discharge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharging', 'p. pr. & vb. n.', 'of Discharge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To relieve of a charge, load, or burden; to empty of\n   a load or cargo; to unburden; to unload; as, to discharge a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To free of the missile with which anything is charged\n   or loaded; to let go the charge of; as, to discharge a bow, catapult,\n   etc.; especially, said of firearms, -- to fire off; to shoot off; also,\n   to relieve from a state of tension, as a Leyden jar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To of something weighing upon or impeding over one,\n   as a debt, claim, obligation, responsibility, accusation, etc.; to\n   absolve; to acquit; to clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To relieve of an office or employment; to send away\n   from service; to dismiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To release legally from confinement; to set at\n   liberty; as, to discharge a prisoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To put forth, or remove, as a charge or burden; to\n   take out, as that with which anything is loaded or filled; as, to\n   discharge a cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To let fly, as a missile; to shoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To set aside; to annul; to dismiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To throw off the obligation of, as a duty or debt; to\n   relieve ones self of, by fulfilling conditions, performing duty,\n   trust, and the like; hence, to perform or execute, as an office, or\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To send away (a creditor) satisfied by payment; to\n   pay ones debt or obligation to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To give forth; to emit or send out; as, a pipe\n   discharges water; to let fly; to give expression to; to utter; as, to\n   discharge a horrible oath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'To prohibit; to forbid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. i.', 'To throw off or deliver a load, charge, or burden; to\n   unload; to emit or give vent to fluid or other contents; as, the water\n   pipe discharges freely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'The act of discharging; the act of relieving of a\n   charge or load; removal of a load or burden; unloading; as, the\n   discharge of a ship; discharge of a cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'Firing off; explosive removal of a charge; explosion;\n   letting off; as, a discharge of arrows, of artillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'Act of relieving of something which oppresses or\n   weighs upon one, as an obligation, liability, debt, accusation, etc.;\n   acquittance; as, the discharge of a debtor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'Act of removing, or getting rid of, an obligation,\n   liability, etc.; fulfillment, as by the payment of a debt, or the\n   performance of a trust or duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'Release or dismissal from an office, employment,\n   etc.; dismission; as, the discharge of a workman by his employer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'Legal release from confinement; liberation; as, the\n   discharge of a prisoner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'The state of being discharged or relieved of a debt,\n   obligation, office, and the like; acquittal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'That which discharges or releases from an obligation,\n   liability, penalty, etc., as a price of ransom, a legal document.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharge', 'v. t.', 'A flowing or issuing out; emission; vent; evacuation;\n   also, that which is discharged or emitted; as, a rapid discharge of\n   water from the pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discharger', 'n.', 'One who, or that which, discharges. Specifically, in\n   electricity, an instrument for discharging a Leyden jar, or electrical\n   battery, by making a connection between the two surfaces; a discharging\n   rod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dischevele', 'a.', 'Disheveled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dischurch', 'v. t.', 'To deprive of status as a church, or of membership in\n   a church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discide', 'v. t.', 'To divide; to cleave in two.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciferous', 'a.', 'Bearing disks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discifloral', 'a.', 'Alt. of Disciflorous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciflorous', 'a.', 'Bearing the stamens on a discoid outgrowth of the\n   receptacle; -- said of a subclass of plants. Cf. Calycifloral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciform', 'a.', 'Discoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discina', 'n.', 'A genus of Branchiopoda, having a disklike shell, attached\n   by one valve, which is perforated by the peduncle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discinct', 'a.', 'Ungirded; loosely dressed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discind', 'v. t.', 'To part; to divide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciple', 'n.', 'One who receives instruction from another; a scholar; a\n   learner; especially, a follower who has learned to believe in the truth\n   of the doctrine of his teacher; an adherent in doctrine; as, the\n   disciples of Plato; the disciples of our Savior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipled', 'imp. & p. p.', 'of Disciple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipling', 'p. pr. & vb. n.', 'of Disciple');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciple', 'v. t.', 'To teach; to train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciple', 'v. t.', 'To punish; to discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciple', 'v. t.', 'To make disciples of; to convert to doctrines or\n   principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipleship', 'n.', 'The state of being a disciple or follower in\n   doctrines and precepts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipless', 'n.', 'A female disciple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplinable', 'a.', 'Capable of being disciplined or improved by\n   instruction and training.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplinable', 'a.', 'Liable or deserving to be disciplined; subject to\n   disciplinary punishment; as, a disciplinable offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplinableness', 'n.', 'The quality of being improvable by discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplinal', 'a.', 'Relating to discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplinant', 'n.', 'A flagellant. See Flagellant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplinarian', 'a.', 'Pertaining to discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplinarian', 'n.', 'One who disciplines; one who excels in training,\n   especially with training, especially with regard to order and\n   obedience; one who enforces rigid discipline; a stickler for the\n   observance of rules and methods of training; as, he is a better\n   disciplinarian than scholar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplinarian', 'n.', 'A Puritan or Presbyterian; -- because of rigid\n   adherence to religious or church discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplinary', 'a.', 'Pertaining to discipline; intended for discipline;\n   corrective; belonging to a course of training.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'n.', 'The treatment suited to a disciple or learner;\n   education; development of the faculties by instruction and exercise;\n   training, whether physical, mental, or moral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'n.', 'Training to act in accordance with established rules;\n   accustoming to systematic and regular action; drill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'n.', 'Subjection to rule; submissiveness to order and\n   control; habit of obedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'n.', 'Severe training, corrective of faults; instruction by\n   means of misfortune, suffering, punishment, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'n.', 'Correction; chastisement; punishment inflicted by way\n   of correction and training.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'n.', 'The subject matter of instruction; a branch of\n   knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'n.', 'The enforcement of methods of correction against one\n   guilty of ecclesiastical offenses; reformatory or penal action toward a\n   church member.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'n.', 'Self-inflicted and voluntary corporal punishment, as\n   penance, or otherwise; specifically, a penitential scourge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'n.', 'A system of essential rules and duties; as, the Romish\n   or Anglican discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplined', 'imp. & p. p.', 'of Discipline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disciplining', 'p. pr. & vb. n.', 'of Discipline');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'v. t.', 'To educate; to develop by instruction and exercise;\n   to train.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'v. t.', 'To accustom to regular and systematic action; to\n   bring under control so as to act systematically; to train to act\n   together under orders; to teach subordination to; to form a habit of\n   obedience in; to drill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'v. t.', 'To improve by corrective and penal methods; to\n   chastise; to correct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipline', 'v. t.', 'To inflict ecclesiastical censures and penalties\n   upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discipliner', 'n.', 'One who disciplines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclaimed', 'imp. & p. p.', 'of Disclaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclaiming', 'p. pr. & vb. n.', 'of Disclaim');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclaim', 'v. t.', 'To renounce all claim to deny; ownership of, or\n   responsibility for; to disown; to disavow; to reject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclaim', 'v. t.', 'To deny, as a claim; to refuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclaim', 'v. t.', 'To relinquish or deny having a claim; to disavow\n   anothers claim; to decline accepting, as an estate, interest, or\n   office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclaim', 'v. t.', 'To disavow or renounce all part, claim, or share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclaimer', 'n.', 'One who disclaims, disowns, or renounces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclaimer', 'n.', 'A denial, disavowal, or renunciation, as of a title,\n   claim, interest, estate, or trust; relinquishment or waiver of an\n   interest or estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclaimer', 'n.', 'A public disavowal, as of pretensions, claims,\n   opinions, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclamation', 'n.', 'A disavowing or disowning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclame', 'v. t.', 'To disclaim; to expel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclaunder', 'v. t.', 'To injure ones good name; to slander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discloak', 'v. t.', 'To take off a cloak from; to uncloak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclosed', 'imp. & p. p.', 'of Disclose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclosing', 'p. pr. & vb. n.', 'of Disclose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclose', 'v. t.', 'To unclose; to open; -- applied esp. to eggs in the\n   sense of to hatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclose', 'v. t.', 'To remove a cover or envelope from;; to set free from\n   inclosure; to uncover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclose', 'v. t.', 'To lay open or expose to view; to cause to appear; to\n   bring to light; to reveal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclose', 'v. t.', 'To make known, as that which has been kept secret or\n   hidden; to reveal; to expose; as, events have disclosed his designs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclose', 'n.', 'Disclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclosed', 'p. a.', 'Represented with wings expanded; -- applied to doves\n   and other birds not of prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discloser', 'n.', 'One who discloses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclosure', 'v. t.', 'The act of disclosing, uncovering, or revealing;\n   bringing to light; exposure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclosure', 'v. t.', 'That which is disclosed or revealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discloud', 'v. t.', 'To clear from clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclout', 'v. t.', 'To divest of a clout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disclusion', 'n.', 'A shutting off; exclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoast', 'v. i.', 'To depart; to quit the coast (that is, the side or\n   border) of anything; to be separated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoblastic', 'a.', 'Applied to a form of egg cleavage seen in osseous\n   fishes, which occurs only in a small disk that separates from the rest\n   of the egg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoboli', 'pl. ', 'of Discobolus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discobolus', 'n.', 'A thrower of the discus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discobolus', 'n.', 'A statue of an athlete holding the discus, or about to\n   throw it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discodactyl', 'n.', 'One of the tree frogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discodactylia', 'n. pl.', 'A division of amphibians having suctorial disks\n   on the toes, as the tree frogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discodactylous', 'a.', 'Having sucking disks on the toes, as the tree\n   frogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoherent', 'a.', 'Incoherent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoid', 'a.', 'Having the form of a disk, as those univalve shells which\n   have the whorls in one plane, so as to form a disk, as the pearly\n   nautilus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoid', 'n.', 'Anything having the form of a discus or disk;\n   particularly, a discoid shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoidal', 'a.', 'Disk-shaped; discoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discolith', 'n.', 'One of a species of coccoliths, having an oval discoidal\n   body, with a thick strongly refracting rim, and a thinner central\n   portion. One of them measures about / of an inch in its longest\n   diameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discolored', 'imp. & p. p.', 'of Discolor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoloring', 'p. pr. & vb. n.', 'of Discolor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discolor', 'v. t.', 'To alter the natural hue or color of; to change to a\n   different color; to stain; to tinge; as, a drop of wine will discolor\n   water; silver is discolored by sea water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discolor', 'v. t.', 'To alter the true complexion or appearance of; to put\n   a false hue upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discolorate', 'v. t.', 'To discolor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoloration', 'n.', 'The act of discoloring, or the state of being\n   discolored; alteration of hue or appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoloration', 'n.', 'A discolored spot; a stain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discolored', 'a.', 'Altered in color; /tained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discolored', 'a.', 'Variegated; of divers colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfited', 'imp. & p. p.', 'of Discomfit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfiting', 'p. pr. & vb. n.', 'of Discomfit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfit', 'v. t.', 'To scatter in fight; to put to rout; to defeat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfit', 'v. t.', 'To break up and frustrate the plans of; to balk/ to\n   throw into perplexity and dejection; to disconcert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfit', 'a.', 'Discomfited; overthrown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfit', 'n.', 'Rout; overthrow; discomfiture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfiture', 'v. t.', 'The act of discomfiting, or the state of being\n   discomfited; rout; overthrow; defeat; frustration; confusion and\n   dejection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomforted', 'imp. & p. p.', 'of Discomfort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomforting', 'p. pr. & vb. n.', 'of Discomfort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfort', 'v. t.', 'To discourage; to deject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfort', 'v. t.', 'To destroy or disturb the comfort of; to deprive o/\n   quiet enjoyment; to make uneasy; to pain; as, a smoky chimney\n   discomforts a family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfort', 'v. t.', 'Discouragement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfort', 'v. t.', 'Want of comfort; uneasiness, mental or physical;\n   disturbance of peace; inquietude; pain; distress; sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfortable', 'a.', 'Causing discomfort; occasioning uneasiness; making\n   sad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomfortable', 'a.', 'Destitute of comfort; uncomfortable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommend', 'v. t.', 'To mention with disapprobation; to blame; to\n   disapprove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommend', 'v. t.', 'To expose to censure or ill favor; to put out of the\n   good graces of any one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommendable', 'a.', 'Deserving, disapprobation or blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommendation', 'n.', 'Blame; censure; reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommender', 'n.', 'One who discommends; a dispraiser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommission', 'v. t.', 'To deprive of a commission or trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommodate', 'v. t.', 'To discommode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommoded', 'imp. & p. p.', 'of Discommode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommoding', 'p. pr. & vb. n.', 'of Discommode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommode', 'v. t.', 'To put inconvenience; to incommode; to trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommodious', 'a.', 'Inconvenient; troublesome; incommodious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommodity', 'n.', 'Disadvantage; inconvenience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommon', 'v. t.', 'To deprive of the right of common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommon', 'v. t.', 'To deprive of privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommon', 'v. t.', 'To deprive of commonable quality, as lands, by\n   inclosing or appropriating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discommunity', 'n.', 'A lack of common possessions, properties, or\n   relationship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discompany', 'v. t.', 'To free from company; to dissociate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomplexion', 'v. t.', 'To change the complexion or hue of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discompliance', 'n.', 'Failure or refusal to comply; noncompliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomposed', 'imp. & p. p.', 'of Discompose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomposing', 'p. pr. & vb. n.', 'of Discompose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discompose', 'v. t.', 'To disarrange; to interfere with; to disturb; to\n   disorder; to unsettle; to break up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discompose', 'v. t.', 'To throw into disorder; to ruffle; to destroy the\n   composure or equanimity; to agitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discompose', 'v. t.', 'To put out of place or service; to discharge; to\n   displace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomposed', 'a.', 'Disordered; disturbed; disquieted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomposition', 'n.', 'Inconsistency; discordance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomposure', 'n.', 'The state of being discomposed; disturbance;\n   disorder; agitation; perturbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discomposure', 'n.', 'Discordance; disagreement of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discompt', 'v. t.', 'To discount. See Discount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconcerted', 'imp. & p. p.', 'of Disconcert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconcerting', 'p. pr. & vb. n.', 'of Disconcert');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconcert', 'v. t.', 'To break up the harmonious progress of; to throw\n   into disorder or confusion; as, the emperor disconcerted the plans of\n   his enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconcert', 'v. t.', 'To confuse the faculties of; to disturb the\n   composure of; to discompose; to abash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconcert', 'n.', 'Want of concert; disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconcertion', 'n.', 'The act of disconcerting, or state of being\n   disconcerted; discomposure; perturbation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconducive', 'a.', 'Not conductive; impeding; disadvantageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconformable', 'a.', 'Not conformable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconformity', 'n.', 'Want of conformity or correspondence; inconsistency;\n   disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discongruity', 'n.', 'Incongruity; disagreement; unsuitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconnected', 'imp. & p. p.', 'of Disconnect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconnecting', 'p. pr. & vb. n.', 'of Disconnect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconnect', 'v. t.', 'To dissolve the union or connection of; to disunite;\n   to sever; to separate; to disperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconnection', 'n.', 'The act of disconnecting, or state of being\n   disconnected; separation; want of union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconsecrate', 'v. t.', 'To deprive of consecration or sacredness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discosent', 'v. i.', 'To differ; to disagree; to dissent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconsolacy', 'n.', 'The state of being disconsolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconsolate', 'n.', 'Disconsolateness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconsolate', 'v. t.', 'Destitute of consolation; deeply dejected and\n   dispirited; hopelessly sad; comfortless; filled with grief; as, a\n   bereaved and disconsolate parent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconsolate', 'v. t.', 'Inspiring dejection; saddening; cheerless; as, the\n   disconsolate darkness of the winter nights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconsolated', 'a.', 'Disconsolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconsolation', 'n.', 'Dejection; grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontent', 'a.', 'Not content; discontented; dissatisfied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontented', 'imp. & p. p.', 'of Discontent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontenting', 'p. pr. & vb. n.', 'of Discontent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontent', 'v. t.', 'To deprive of content; to make uneasy; to\n   dissatisfy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontent', 'n.', 'Want of content; uneasiness and inquietude of mind;\n   dissatisfaction; disquiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontent', 'n.', 'A discontented person; a malcontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontentation', 'n.', 'Discontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontented', 'p. p. & a.', 'Dissatisfied; uneasy in mind; malcontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontentful', 'a.', 'Full of discontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontenting', 'a.', 'Discontented.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontenting', 'a.', 'Causing discontent; dissatisfying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontentive', 'a.', 'Relating or tending to discontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontentment', 'n.', 'The state of being discontented; uneasiness;\n   inquietude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuable', 'a.', 'Admitting of being discontinued.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuance', 'n.', 'The act of discontinuing, or the state of being\n   discontinued; want of continued connection or continuity; breaking off;\n   cessation; interruption; as, a discontinuance of conversation or\n   intercourse; discontinuance of a highway or of travel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuance', 'n.', 'A breaking off or interruption of an estate, which\n   happened when an alienation was made by a tenant in tail, or other\n   tenant, seized in right of another, of a larger estate than the tenant\n   was entitled to, whereby the party ousted or injured was driven to his\n   real action, and could not enter. This effect of such alienation is now\n   obviated by statute in both England and the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuance', 'n.', 'The termination of an action in practice by the\n   voluntary act of the plaintiff; an entry on the record that the\n   plaintiff discontinues his action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuance', 'n.', 'That technical interruption of the proceedings in\n   pleading in an action, which follows where a defendant does not answer\n   the whole of the plaintiffs declaration, and the plaintiff omits to\n   take judgment for the part unanswered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuation', 'n.', 'Breach or interruption of continuity; separation\n   of parts in a connected series; discontinuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinued', 'imp. & p. p.', 'of Discontinue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuing', 'p. pr. & vb. n.', 'of Discontinue');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinue', 'v. t.', 'To interrupt the continuance of; to intermit, as a\n   practice or habit; to put an end to; to cause to cease; to cease using,\n   to stop; to leave off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinue', 'v. i.', 'To lose continuity or cohesion of parts; to be\n   disrupted or broken off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinue', 'v. i.', 'To be separated or severed; to part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuee', 'n.', 'One whose possession of an estate is broken off, or\n   discontinued; one whose estate is subject to discontinuance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuer', 'n.', 'One who discontinues, or breaks off or away from; an\n   absentee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuity', 'n.', 'Want of continuity or cohesion; disunion of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuor', 'n.', 'One who deprives another of the possession of an\n   estate by discontinuance. See Discontinuance, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuous', 'a.', 'Not continuous; interrupted; broken off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discontinuous', 'a.', 'Exhibiting a dissolution of continuity; gaping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconvenience', 'n.', 'Unsuitableness; incongruity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disconvenient', 'a.', 'Not convenient or congruous; unsuitable;\n   ill-adapted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discophora', 'n. pl.', 'A division of acalephs or jellyfishes, including\n   most of the large disklike species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discord', 'v. i.', 'Want of concord or agreement; absence of unity or\n   harmony in sentiment or action; variance leading to contention and\n   strife; disagreement; -- applied to persons or to things, and to\n   thoughts, feelings, or purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discord', 'v. i.', 'Union of musical sounds which strikes the ear harshly\n   or disagreeably, owing to the incommensurability of the vibrations\n   which they produce; want of musical concord or harmony; a chord\n   demanding resolution into a concord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discord', 'n.', 'To disagree; to be discordant; to jar; to clash; not to\n   suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discordable', 'a.', 'That may produce discord; disagreeing; discordant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discordance', 'n.', 'Alt. of Discordancy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discordancy', 'n.', 'State or quality of being discordant; disagreement;\n   inconsistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discordant', 'n.', 'Disagreeing; incongruous; being at variance; clashing;\n   opposing; not harmonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discordant', 'n.', 'Dissonant; not in harmony or musical concord; harsh;\n   jarring; as, discordant notes or sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discordant', 'n.', 'Said of strata which lack conformity in direction of\n   bedding, either as in unconformability, or as caused by a fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discordful', 'a.', 'Full of discord; contentious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discordous', 'a.', 'Full of discord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discorporate', 'a.', 'Deprived of the privileges or form of a body\n   corporate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discorrespondent', 'a.', 'Incongruous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discost', 'v. i.', 'Same as Discoast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discounsel', 'v. t.', 'To dissuade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discounted', 'imp. & p. p.', 'of Discount');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discounting', 'p. pr. & vb. n.', 'of Discount');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discount', 'v.', 'To deduct from an account, debt, charge, and the like; to\n   make an abatement of; as, merchants sometimes discount five or six per\n   cent for prompt payment of bills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discount', 'v.', 'To lend money upon, deducting the discount or allowance\n   for interest; as, the banks discount notes and bills of exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discount', 'v.', 'To take into consideration beforehand; to anticipate and\n   form conclusions concerning (an event).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discount', 'v.', 'To leave out of account; to take no notice of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discount', 'v. i.', 'To lend, or make a practice of lending, money, abating\n   the discount; as, the discount for sixty or ninety days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discount', 'v. t.', 'A counting off or deduction made from a gross sum on\n   any account whatever; an allowance upon an account, debt, demand, price\n   asked, and the like; something taken or deducted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discount', 'v. t.', 'A deduction made for interest, in advancing money\n   upon, or purchasing, a bill or note not due; payment in advance of\n   interest upon money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discount', 'v. t.', 'The rate of interest charged in discounting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discountable', 'a.', 'Capable of being, or suitable to be, discounted; as,\n   certain forms are necessary to render notes discountable at a bank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discountenanced', 'imp. & p. p.', 'of Discountenance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discountenancing', 'p. pr. & vb. n.', 'of Discountenance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discountenance', 'v. t.', 'To ruffle or discompose the countenance of; to\n   put of countenance; to put to shame; to abash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discountenance', 'v. t.', 'To refuse to countenance, or give the support of\n   ones approval to; to give ones influence against; to restrain by cold\n   treatment; to discourage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discountenance', 'n.', 'Unfavorable aspect; unfriendly regard; cold\n   treatment; disapprobation; whatever tends to check or discourage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discountenancer', 'n.', 'One who discountenances; one who disfavors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discounter', 'n.', 'One who discounts; a discount broker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discouraged', 'imp. & p. p.', 'of Discourage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discouraging', 'p. pr. & vb. n.', 'of Discourage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourage', 'v. t.', 'To extinguish the courage of; to dishearten; to\n   depress the spirits of; to deprive of confidence; to deject; -- the\n   opposite of encourage; as, he was discouraged in his undertaking; he\n   need not be discouraged from a like attempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourage', 'v. t.', 'To dishearten one with respect to; to\n   discountenance; to seek to check by disfavoring; to deter one from; as,\n   they discouraged his efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourage', 'n.', 'Lack of courage; cowardliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourageable', 'a.', 'Capable of being discouraged; easily disheartened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discouragement', 'n.', 'The act of discouraging, or the state of being\n   discouraged; depression or weakening of confidence; dejection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discouragement', 'n.', 'That which discourages; that which deters, or tends\n   to deter, from an undertaking, or from the prosecution of anything; a\n   determent; as, the revolution was commenced under every possible\n   discouragement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourager', 'n.', 'One who discourages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discouraging', 'a.', 'Causing or indicating discouragement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoure', 'v. t.', 'To discover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'n.', 'The power of the mind to reason or infer by running, as\n   it were, from one fact or reason to another, and deriving a conclusion;\n   an exercise or act of this power; reasoning; range of reasoning\n   faculty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'n.', 'Conversation; talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'n.', 'The art and manner of speaking and conversing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'n.', 'Consecutive speech, either written or unwritten, on a\n   given line of thought; speech; treatise; dissertation; sermon, etc.;\n   as, the preacher gave us a long discourse on duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'n.', 'Dealing; transaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoursed', 'imp. & p. p.', 'of Discourse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoursing', 'p. pr. & vb. n.', 'of Discourse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'v. i.', 'To exercise reason; to employ the mind in judging and\n   inferring; to reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'v. i.', 'To express ones self in oral discourse; to expose\n   ones views; to talk in a continuous or formal manner; to hold forth;\n   to speak; to converse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'v. i.', 'To relate something; to tell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'v. i.', 'To treat of something in writing and formally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'v. t.', 'To treat of; to expose or set forth in language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'v. t.', 'To utter or give forth; to speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourse', 'v. t.', 'To talk to; to confer with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourser', 'n.', 'One who discourse; a narrator; a speaker; an haranguer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourser', 'n.', 'The writer of a treatise or dissertation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoursive', 'a.', 'Reasoning; characterized by reasoning; passing from\n   premises to consequences; discursive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoursive', 'a.', 'Containing dialogue or conversation; interlocutory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoursive', 'a.', 'Inclined to converse; conversable; communicative; as,\n   a discoursive man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discoursive', 'n.', 'The state or quality of being discoursive or able to\n   reason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourteous', 'a.', 'Uncivil; rude; wanting in courtesy or good manners;\n   uncourteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourtesy', 'n.', 'Rudeness of behavior or language; ill manners;\n   manifestation of disrespect; incivility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discourtship', 'n.', 'Want of courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Discous', 'a.', 'Disklike; discoid.');
