  INSERT INTO words (word, word_type, description) VALUES  (    'Disjuncttion', 'n.', 'The act of disjoining; disunion; separation; a\n   parting; as, the disjunction of soul and body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disjuncttion', 'n.', 'A disjunctive proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disjunctive', 'a.', 'Tending to disjoin; separating; disjoining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disjunctive', 'a.', 'Pertaining to disjunct tetrachords.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disjunctive', 'n.', 'A disjunctive conjunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disjunctive', 'n.', 'A disjunctive proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disjunctively', 'adv.', 'In a disjunctive manner; separately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disjuncture', 'n.', 'The act of disjoining, or state of being disjoined;\n   separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disk', 'n.', 'A discus; a quoit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disk', 'n.', 'A flat, circular plate; as, a disk of metal or paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disk', 'n.', 'The circular figure of a celestial body, as seen projected of\n   the heavens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disk', 'n.', 'A circular structure either in plants or animals; as, a blood\n   disk; germinal disk, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disk', 'n.', 'The whole surface of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disk', 'n.', 'The central part of a radiate compound flower, as in\n   sunflower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disk', 'n.', 'A part of the receptacle enlarged or expanded under, or\n   around, or even on top of, the pistil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disk', 'n.', 'The anterior surface or oral area of coelenterate animals, as\n   of sea anemones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disk', 'n.', 'The lower side of the body of some invertebrates, especially\n   when used for locomotion, when it is often called a creeping disk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disk', 'n.', 'In owls, the space around the eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diskindness', 'n.', 'Unkindness; disservice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diskless', 'a.', 'Having no disk; appearing as a point and not expanded\n   into a disk, as the image of a faint star in a telescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislade', 'v. t.', 'To unlade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disleal', 'a.', 'Disloyal; perfidious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disleave', 'v. t.', 'To deprive of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disliked', 'imp. & p. p.', 'of Dislike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disliking', 'p. pr. & vb. n.', 'of Dislike');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislike', 'v. t.', 'To regard with dislike or aversion; to disapprove; to\n   disrelish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislike', 'v. t.', 'To awaken dislike in; to displease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislike', 'n.', 'A feeling of positive and usually permanent aversion to\n   something unpleasant, uncongenial, or offensive; disapprobation;\n   repugnance; displeasure; disfavor; -- the opposite of liking or\n   fondness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislike', 'n.', 'Discord; dissension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislikeful', 'a.', 'Full of dislike; disaffected; malign; disagreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislikelihood', 'n.', 'The want of likelihood; improbability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disliken', 'v. t.', 'To make unlike; to disguise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislikeness', 'n.', 'Unlikeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disliker', 'n.', 'One who dislikes or disrelishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislimb', 'v. t.', 'To tear limb from limb; to dismember.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislimn', 'v. t.', 'To efface, as a picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislink', 'v. t.', 'To unlink; to disunite; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislive', 'v. t.', 'To deprive of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislocated', 'imp. & p. p.', 'of Dislocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislocating', 'p. pr. & vb. n.', 'of Dislocate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislocate', 'v. t.', 'To displace; to put out of its proper place.\n   Especially, of a bone: To remove from its normal connections with a\n   neighboring bone; to put out of joint; to move from its socket; to\n   disjoint; as, to dislocate your bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislocate', 'a.', 'Dislocated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislocation', 'n.', 'The act of displacing, or the state of being\n   displaced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislocation', 'n.', 'The displacement of parts of rocks or portions of\n   strata from the situation which they originally occupied. Slips,\n   faults, and the like, are dislocations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislocation', 'n.', 'The act of dislocating, or putting out of joint; also,\n   the condition of being thus displaced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislodged', 'imp. & p. p.', 'of Dislodge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislodging', 'p. pr. & vb. n.', 'of Dislodge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislodge', 'v. t.', 'To drive from a lodge or place of rest; to remove from\n   a place of quiet or repose; as, shells resting in the sea at a\n   considerate depth are not dislodged by storms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislodge', 'v. t.', 'To drive out from a place of hiding or defense; as, to\n   dislodge a deer, or an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislodge', 'v. i.', 'To go from a place of rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislodge', 'n.', 'Dwelling apart; separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dislodgment', 'n.', 'The act or process of dislodging, or the state of\n   being dislodged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disloign', 'v. t.', 'To put at a distance; to remove.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disloyal', 'a.', 'Not loyal; not true to a sovereign or lawful superior, or\n   to the government under which one lives; false where allegiance is due;\n   faithless; as, a subject disloyal to the king; a husband disloyal to\n   his wife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disloyally', 'adv.', 'In a disloyal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disloyalty', 'n.', 'Want of loyalty; lack of fidelity; violation of\n   allegiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismail', 'v. t.', 'To divest of coat of mail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismal', 'a.', 'Fatal; ill-omened; unlucky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismal', 'a.', 'Gloomy to the eye or ear; sorrowful and depressing to the\n   feelings; foreboding; cheerless; dull; dreary; as, a dismal outlook;\n   dismal stories; a dismal place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismally', 'adv.', 'In a dismal manner; gloomily; sorrowfully;\n   uncomfortably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismalness', 'n.', 'The quality of being dismal; gloominess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disman', 'v. t.', 'To unman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismantled', 'imp. & p. p.', 'of Dismantle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismantling', 'p. pr. & vb. n.', 'of Dismantle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismantle', 'v. t.', 'To strip or deprive of dress; to divest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismantle', 'v. t.', 'To strip of furniture and equipments, guns, etc.; to\n   unrig; to strip of walls or outworks; to break down; as, to dismantle a\n   fort, a town, or a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismantle', 'v. t.', 'To disable; to render useless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismarch', 'v. i.', 'To march away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismarry', 'v. t.', 'To free from the bonds of marriage; to divorce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismarshal', 'v. t.', 'To disarrange; to derange; to put in disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismask', 'v. t.', 'To divest of a mask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismasted', 'imp. & p. p.', 'of Dismast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismasting', 'p. pr. & vb. n.', 'of Dismast');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismast', 'v. t.', 'To deprive of a mast of masts; to break and carry away\n   the masts from; as, a storm dismasted the ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismastment', 'n.', 'The act of dismasting; the state of being dismasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismaw', 'v. t.', 'To eject from the maw; to disgorge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismayed', 'imp. & p. p.', 'of Dismay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismaying', 'p. pr. & vb. n.', 'of Dismay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismay', 'v. i.', 'To disable with alarm or apprehensions; to depress the\n   spirits or courage of; to deprive or firmness and energy through fear;\n   to daunt; to appall; to terrify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismay', 'v. i.', 'To render lifeless; to subdue; to disquiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismay', 'v. i.', 'To take dismay or fright; to be filled with dismay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismay', 'v. t.', 'Loss of courage and firmness through fear; overwhelming\n   and disabling terror; a sinking of the spirits; consternation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismay', 'v. t.', 'Condition fitted to dismay; ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismayedness', 'n.', 'A state of being dismayed; dejection of courage;\n   dispiritedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismayful', 'a.', 'Terrifying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disme', 'n.', 'A tenth; a tenth part; a tithe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismembered', 'imp. & p. p.', 'of Dismember');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismembering', 'p. pr. & vb. n.', 'of Dismember');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismember', 'v. t.', 'To tear limb from limb; to dilacerate; to disjoin\n   member from member; to tear or cut in pieces; to break up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismember', 'v. t.', 'To deprive of membership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismemberment', 'n.', 'The act of dismembering, or the state of being\n   dismembered; cutting in piece; m/tilation; division; separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismettled', 'a.', 'Destitute of mettle, that is, or fire or spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismissed', 'imp. & p. p.', 'of Dismiss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismissing', 'p. pr. & vb. n.', 'of Dismiss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismiss', 'v. t.', 'To send away; to give leave of departure; to cause or\n   permit to go; to put away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismiss', 'v. t.', 'To discard; to remove or discharge from office,\n   service, or employment; as, the king dismisses his ministers; the\n   matter dismisses his servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismiss', 'v. t.', 'To lay aside or reject as unworthy of attentions or\n   regard, as a petition or motion in court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismiss', 'n.', 'Dismission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismissal', 'n.', 'Dismission; discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismission', 'n.', 'The act dismissing or sending away; permission to\n   leave; leave to depart; dismissal; as, the dismission of the grand\n   jury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismission', 'n.', 'Removal from office or employment; discharge, either\n   with honor or with disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismission', 'n.', 'Rejection; a setting aside as trivial, invalid, or\n   unworthy of consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismissive', 'a.', 'Giving dismission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismortaged', 'imp. & p. p.', 'of Dismortgage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismortgaging', 'p. pr. & vb. n.', 'of Dismortgage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismortgage', 'v. t.', 'To redeem from mortgage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismounted', 'imp. & p. p.', 'of Dismount');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismounting', 'p. pr. & vb. n.', 'of Dismount');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismount', 'v. i.', 'To come down; to descend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismount', 'v. i.', 'To alight from a horse; to descend or get off, as a\n   rider from his beast; as, the troops dismounted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismount', 'v. t.', 'To throw or bring down from an elevation, place of\n   honor and authority, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismount', 'v. t.', 'To throw or remove from a horse; to unhorse; as, the\n   soldier dismounted his adversary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismount', 'v. t.', 'To take down, or apart, as a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dismount', 'v. t.', 'To throw or remove from the carriage, or from that on\n   which a thing is mounted; to break the carriage or wheels of, and\n   render useless; to deprive of equipments or mountings; -- said esp. of\n   artillery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disnaturalize', 'v. t.', 'To make alien; to deprive of the privileges of\n   birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disnatured', 'a.', 'Deprived or destitute of natural feelings; unnatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobedience', 'n.', 'Neglect or refusal to obey; violation of a command or\n   prohibition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobediency', 'n.', 'Disobedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobedient', 'a.', 'Neglecting or refusing to obey; omitting to do what is\n   commanded, or doing what is prohibited; refractory; not observant of\n   duty or rules prescribed by authority; -- applied to persons and acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobedient', 'a.', 'Not yielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobediently', 'adv.', 'In a disobedient manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobeisance', 'n.', 'Disobedience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobeisant', 'a.', 'Disobedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobeyed', 'imp. & p. p.', 'of Disobey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobeying', 'p. pr. & vb. n.', 'of Disobey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobey', 'v. t.', 'Not to obey; to neglect or refuse to obey (a superior\n   or his commands, the laws, etc.); to transgress the commands of (one in\n   authority); to violate, as an order; as, refractory children disobey\n   their parents; men disobey their Maker and the laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobey', 'v. i.', 'To refuse or neglect to obey; to violate commands; to\n   be disobedient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobeyer', 'n.', 'One who disobeys.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobligation', 'n.', 'The act of disobliging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobligation', 'n.', 'A disobliging act; an offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobligation', 'n.', 'Release from obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobligatory', 'a.', 'Releasing from obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobliged', 'imp. & p. p.', 'of Disoblige');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobliging', 'p. pr. & vb. n.', 'of Disoblige');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disoblige', 'v. t.', 'To do an act which contravenes the will or desires\n   of; to offend by an act of unkindness or incivility; to displease; to\n   refrain from obliging; to be unaccommodating to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disoblige', 'v. t.', 'To release from obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobligement', 'n.', 'Release from obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobliger', 'n.', 'One who disobliges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobliging', 'a.', 'Not obliging; not disposed to do a favor;\n   unaccommodating; as, a disobliging person or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disobliging', 'a.', 'Displeasing; offensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disoccident', 'v. t.', 'To turn away from the west; to throw out of\n   reckoning as to longitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disoccupation', 'n.', 'The state of being unemployed; want of occupation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disopinion', 'n.', 'Want or difference of belief; disbelief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disoppilate', 'v. t.', 'To open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorb', 'v. t.', 'To throw out of the proper orbit; to unsphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disord', 'n.', 'Disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disordeined', 'a.', 'Inordinate; irregular; vicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorder', 'n.', 'Want of order or regular disposition; lack of\n   arrangement; confusion; disarray; as, the troops were thrown into\n   disorder; the papers are in disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorder', 'n.', 'Neglect of order or system; irregularity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorder', 'n.', 'Breach of public order; disturbance of the peace of\n   society; tumult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorder', 'n.', 'Disturbance of the functions of the animal economy of the\n   soul; sickness; derangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disordered', 'imp. & p. p.', 'of Disorder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disordering', 'p. pr. & vb. n.', 'of Disorder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorder', 'v. t.', 'To disturb the order of; to derange or disarrange; to\n   throw into confusion; to confuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorder', 'v. t.', 'To disturb or interrupt the regular and natural\n   functions of (either body or mind); to produce sickness or\n   indisposition in; to discompose; to derange; as, to disorder the head\n   or stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorder', 'v. t.', 'To depose from holy orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disordered', 'a.', 'Thrown into disorder; deranged; as, a disordered house,\n   judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disordered', 'a.', 'Disorderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorderliness', 'n.', 'The state of being disorderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorderly', 'a.', 'Not in order; marked by disorder; disarranged;\n   immethodical; as, the books and papers are in a disorderly state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorderly', 'a.', 'Not acting in an orderly way, as the functions of the\n   body or mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorderly', 'a.', 'Not complying with the restraints of order and law;\n   tumultuous; unruly; lawless; turbulent; as, disorderly people;\n   disorderly assemblies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorderly', 'a.', 'Offensive to good morals and public decency;\n   notoriously offensive; as, a disorderly house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorderly', 'adv.', 'In a disorderly manner; without law or order;\n   irregularly; confusedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disordinance', 'n.', 'Disarrangement; disturbance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disordinate', 'a.', 'Inordinate; disorderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disordinately', 'adv.', 'Inordinately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disordination', 'n.', 'The state of being in disorder; derangement;\n   confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorganization', 'v. t.', 'The act of disorganizing; destruction of\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorganization', 'v. t.', 'The state of being disorganized; as, the\n   disorganization of the body, or of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorganized', 'imp. & p. p.', 'of Disorganize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorganizing', 'p. pr. & vb. n.', 'of Disorganize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorganize', 'v. t.', 'To destroy the organic structure or regular system\n   of (a government, a society, a party, etc.); to break up (what is\n   organized); to throw into utter disorder; to disarrange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorganizer', 'n.', 'One who disorganizes or causes disorder and\n   confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorient', 'v. t.', 'To turn away from the cast; to confuse as to which\n   way is east; to cause to lose ones bearings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disorientate', 'v. t.', 'To turn away from the east, or (figuratively) from\n   the right or the truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disowned', 'imp. & p. p.', 'of Disown');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disowning', 'p. pr. & vb. n.', 'of Disown');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disown', 'v. t.', 'To refuse to own or acknowledge as belonging to ones\n   self; to disavow or deny, as connected with ones self personally; as,\n   a parent can hardly disown his child; an author will sometimes disown\n   his writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disown', 'v. t.', 'To refuse to acknowledge or allow; to deny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disownment', 'n.', 'Act of disowning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disoxidate', 'v. t.', 'To deoxidate; to deoxidize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disoxidation', 'n.', 'Deoxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disoxygenate', 'v. t.', 'To deprive of oxygen; to deoxidize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disoxygenation', 'n.', 'Deoxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispace', 'v. i.', 'To roam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispair', 'v. t.', 'To separate (a pair).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispand', 'v. t.', 'To spread out; to expand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispansion', 'n.', 'Act of dispanding, or state of being dispanded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparadised', 'a.', 'Removed from paradise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparaged', 'imp. & p. p.', 'of Disparage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparaging', 'p. pr. & vb. n.', 'of Disparage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparage', 'v. t.', 'To match unequally; to degrade or dishonor by an\n   unequal marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparage', 'v. t.', 'To dishonor by a comparison with what is inferior; to\n   lower in rank or estimation by actions or words; to speak slightingly\n   of; to depreciate; to undervalue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparage', 'n.', 'Inequality in marriage; marriage with an inferior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparagement', 'n.', 'Matching any one in marriage under his or her\n   degree; injurious union with something of inferior excellence; a\n   lowering in rank or estimation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparagement', 'n.', 'Injurious comparison with an inferior; a\n   depreciating or dishonoring opinion or insinuation; diminution of\n   value; dishonor; indignity; reproach; disgrace; detraction; -- commonly\n   with to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparager', 'n.', 'One who disparages or dishonors; one who vilifies or\n   disgraces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparagingly', 'adv.', 'In a manner to disparage or dishonor; slightingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparate', 'a.', 'Unequal; dissimilar; separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparate', 'a.', 'Pertaining to two coordinate species or divisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparates', 'n. pl.', 'Things so unequal or unlike that they can not be\n   compared with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparition', 'n.', 'Act of disappearing; disappearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparities', 'pl. ', 'of Disparity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparity', 'n.', 'Inequality; difference in age, rank, condition, or\n   excellence; dissimilitude; -- followed by between, in, of, as to, etc.;\n   as, disparity in, or of, years; a disparity as to color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispark', 'v. t.', 'To throw (a park or inclosure); to treat (a private\n   park) as a common.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispark', 'v. t.', 'To set at large; to release from inclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparkle', 'v. t.', 'To scatter abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparted', 'imp. & p. p.', 'of Dispart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disparting', 'p. pr. & vb. n.', 'of Dispart');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispart', 'v. t.', 'To part asunder; to divide; to separate; to sever; to\n   rend; to rive or split; as, disparted air; disparted towers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispart', 'v. i.', 'To separate, to open; to cleave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispart', 'n.', 'The difference between the thickness of the metal at the\n   mouth and at the breech of a piece of ordnance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispart', 'n.', 'A piece of metal placed on the muzzle, or near the\n   trunnions, on the top of a piece of ordnance, to make the line of sight\n   parallel to the axis of the bore; -- called also dispart sight, and\n   muzzle sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispart', 'v. t.', 'To make allowance for the dispart in (a gun), when\n   taking aim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispart', 'v. t.', 'To furnish with a dispart sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispassion', 'n.', 'Freedom from passion; an undisturbed state; apathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispassionate', 'a.', 'Free from passion; not warped, prejudiced, swerved,\n   or carried away by passion or feeling; judicial; calm; composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispassionate', 'a.', 'Not dictated by passion; not proceeding from temper\n   or bias; impartial; as, dispassionate proceedings; a dispassionate\n   view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispassioned', 'a.', 'Free from passion; dispassionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatched', 'imp. & p. p.', 'of Dispatch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatching', 'p. pr. & vb. n.', 'of Dispatch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. t.', 'To dispose of speedily, as business; to execute\n   quickly; to make a speedy end of; to finish; to perform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. t.', 'To rid; to free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. t.', 'To get rid of by sending off; to send away hastily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. t.', 'To send off or away; -- particularly applied to\n   sending off messengers, messages, letters, etc., on special business,\n   and implying haste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. t.', 'To send out of the world; to put to death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. i.', 'To make haste; to conclude an affair; to finish a\n   matter of business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. t.', 'The act of sending a message or messenger in haste or\n   on important business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. t.', 'Any sending away; dismissal; riddance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. t.', 'The finishing up of a business; speedy performance, as\n   of business; prompt execution; diligence; haste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. t.', 'A message dispatched or sent with speed; especially,\n   an important official letter sent from one public officer to another;\n   -- often used in the plural; as, a messenger has arrived with\n   dispatches for the American minister; naval or military dispatches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatch', 'v. t.', 'A message transmitted by telegraph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatcher', 'n.', 'One who dispatches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatchful', 'a.', 'Bent on haste; intent on speedy execution of business\n   or any task; indicating haste; quick; as, dispatchful looks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispatchment', 'n.', 'The act of dispatching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispathies', 'pl. ', 'of Dispathy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispathy', 'n.', 'Lack of sympathy; want of passion; apathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispauper', 'v. t.', 'To deprive of the claim of a pauper to public\n   support; to deprive of the privilege of suing in forma pauperis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispauperize', 'v. t.', 'To free a state of pauperism, or from paupers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispeed', 'v. t.', 'To send off with speed; to dispatch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispelled', 'imp. & p. p.', 'of Dispel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispelling', 'p. pr. & vb. n.', 'of Dispel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispel', 'v. t.', 'To drive away by scattering, or so to cause to vanish;\n   to clear away; to banish; to dissipate; as, to dispel a cloud, vapors,\n   cares, doubts, illusions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispence', 'v. i. & n.', 'See Dispense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispend', 'v. t.', 'To spend; to lay out; to expend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispender', 'n.', 'One who dispends or expends; a steward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensable', 'a.', 'Capable of being dispensed or administered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensable', 'a.', 'Capable of being dispensed with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensableness', 'n.', 'Quality of being dispensable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensaries', 'pl. ', 'of Dispensary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensary', 'n.', 'A place where medicines are prepared and dispensed;\n   esp., a place where the poor can obtain medical advice and medicines\n   gratuitously or at a nominal price.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensary', 'n.', 'A dispensatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensation', 'n.', 'The act of dispensing or dealing out; distribution;\n   often used of the distribution of good and evil by God to man, or more\n   generically, of the acts and modes of his administration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensation', 'n.', 'That which is dispensed, dealt out, or appointed;\n   that which is enjoined or bestowed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensation', 'n.', 'A system of principles, promises, and rules ordained\n   and administered; scheme; economy; as, the Patriarchal, Mosaic, and\n   Christian dispensations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensation', 'n.', 'The relaxation of a law in a particular case;\n   permission to do something forbidden, or to omit doing something\n   enjoined; specifically, in the Roman Catholic Church, exemption from\n   some ecclesiastical law or obligation to God which a man has incurred\n   of his own free will (oaths, vows, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensative', 'a.', 'Granting dispensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensatively', 'adv.', 'By dispensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensator', 'n.', 'A distributer; a dispenser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensatorily', 'adv.', 'In the way of dispensation; dispensatively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensatory', 'v. t.', 'Granting, or authorized to grant, dispensations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensatories', 'pl. ', 'of Dispensatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensatory', 'n.', 'A book or medicinal formulary containing a systematic\n   description of drugs, and of preparations made from them. It is\n   usually, but not always, distinguished from a pharmacop/ia in that it\n   issued by private parties, and not by an official body or by\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensed', 'imp. & p. p.', 'of Dispense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispensing', 'p. pr. & vb. n.', 'of Dispense');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispense', 'v. t.', 'To deal out in portions; to distribute; to give; as,\n   the steward dispenses provisions according directions; Nature dispenses\n   her bounties; to dispense medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispense', 'v. t.', 'To apply, as laws to particular cases; to administer;\n   to execute; to manage; to direct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispense', 'v. t.', 'To pay for; to atone for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispense', 'v. t.', 'To exempt; to excuse; to absolve; -- with from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispense', 'v. i.', 'To compensate; to make up; to make amends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispense', 'v. i.', 'To give dispensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispense', 'v. t.', 'Dispensation; exemption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispense', 'n.', 'Expense; profusion; outlay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispenser', 'n.', 'One who, or that which, dispenses; a distributer; as, a\n   dispenser of favors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispeopled', 'imp. & p. p.', 'of Dispeople');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispeopling', 'p. pr. & vb. n.', 'of Dispeople');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispeople', 'v. t.', 'To deprive of inhabitants; to depopulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispeopler', 'n.', 'One who, or that which, dispeoples; a depopulator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disperge', 'v. t.', 'To sprinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disspermous', 'a.', 'Containing only two seeds; two-seeded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disperple', 'v. t.', 'To scatter; to sprinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispersal', 'n.', 'The act or result of dispersing or scattering;\n   dispersion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispersed', 'imp. & p. p.', 'of Disperse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispersing', 'p. pr. & vb. n.', 'of Disperse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disperse', 'v. t.', 'To scatter abroad; to drive to different parts; to\n   distribute; to diffuse; to spread; as, the Jews are dispersed among all\n   nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disperse', 'v. t.', 'To scatter, so as to cause to vanish; to dissipate;\n   as, to disperse vapors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disperse', 'v. i.', 'To separate; to go or move into different parts; to\n   vanish; as, the company dispersed at ten oclock; the clouds disperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disperse', 'v. i.', 'To distribute wealth; to share ones abundance with\n   others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispersed', 'a.', 'Scattered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disperseness', 'n.', 'Dispersedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disperser', 'n.', 'One that disperses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispersion', 'n.', 'The act or process of scattering or dispersing, or the\n   state of being scattered or separated; as, the Jews in their dispersion\n   retained their rites and ceremonies; a great dispersion of the human\n   family took place at the building of Babel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispersion', 'n.', 'The separation of light into its different colored\n   rays, arising from their different refrangibilities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispersive', 'a.', 'Tending to disperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispersonate', 'v. t.', 'To deprive of personality or individuality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispirited', 'imp. & p. p.', 'of Dispirit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispiriting', 'p. pr. & vb. n.', 'of Dispirit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispirit', 'v. t.', 'To deprive of cheerful spirits; to depress the spirits\n   of; to dishearten; to discourage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispirit', 'v. t.', 'To distill or infuse the spirit of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispirited', 'a.', 'Depressed in spirits; disheartened; daunted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispiritment', 'n.', 'Depression of spirits; discouragement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispiteous', 'a.', 'Full of despite; cruel; spiteful; pitiless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displaced', 'imp. & p. p.', 'of Displace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displacing', 'p. pr. & vb. n.', 'of Displace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displace', 'v. t.', 'To change the place of; to remove from the usual or\n   proper place; to put out of place; to place in another situation; as,\n   the books in the library are all displaced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displace', 'v. t.', 'To crowd out; to take the place of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displace', 'v. t.', 'To remove from a state, office, dignity, or\n   employment; to discharge; to depose; as, to displace an officer of the\n   revenue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displace', 'v. t.', 'To dislodge; to drive away; to banish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displaceable', 'a.', 'Capable of being displaced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displacement', 'n.', 'The act of displacing, or the state of being\n   displaced; a putting out of place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displacement', 'n.', 'The quantity of anything, as water, displaced by a\n   floating body, as by a ship, the weight of the displaced liquid being\n   equal to that of the displacing body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displacement', 'n.', 'The process of extracting soluble substances from\n   organic material and the like, whereby a quantity of saturated solvent\n   is displaced, or removed, for another quantity of the solvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displacency', 'n.', 'Want of complacency or gratification; envious\n   displeasure; dislike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displacer', 'n.', 'One that displaces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displacer', 'n.', 'The funnel part of the apparatus for solution by\n   displacement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Di/planted', 'imp. & p. p.', 'of Displant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displanting', 'p. pr. & vb. n.', 'of Displant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displant', 'v. t.', 'To remove (what is planted or fixed); to unsettle and\n   take away; to displace; to root out; as, to displant inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displant', 'v. t.', 'To strip of what is planted or settled; as, to\n   displant a country of inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displantation', 'n.', 'The act of displanting; removal; displacement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displat', 'v. t.', 'To untwist; to uncurl; to unplat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displayed', 'imp. & p. p.', 'of Display');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displaying', 'p. pr. & vb. n.', 'of Display');
  INSERT INTO words (word, word_type, description) VALUES  (    'Display', 'v. t.', 'To unfold; to spread wide; to expand; to stretch out;\n   to spread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Display', 'v. t.', 'To extend the front of (a column), bringing it into\n   line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Display', 'v. t.', 'To spread before the view; to show; to exhibit to the\n   sight, or to the mind; to make manifest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Display', 'v. t.', 'To make an exhibition of; to set in view conspicuously\n   or ostentatiously; to exhibit for the sake of publicity; to parade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Display', 'v. t.', 'To make conspicuous by large or prominent type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Display', 'v. t.', 'To discover; to descry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Display', 'v. i.', 'To make a display; to act as one making a show or\n   demonstration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Display', 'n.', 'An opening or unfolding; exhibition; manifestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Display', 'n.', 'Ostentatious show; exhibition for effect; parade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displayed', 'a.', 'Unfolded; expanded; exhibited conspicuously or\n   ostentatiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displayed', 'a.', 'With wings expanded; -- said of a bird of pray, esp. an\n   eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displayed', 'a.', 'Set with lines of prominent type interspersed, to catch\n   the eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displayer', 'n.', 'One who, or that which, displays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disple', 'v. t.', 'To discipline; to correct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeasance', 'n.', 'Displeasure; discontent; annoyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeasant', 'a.', 'Unpleasing; offensive; unpleasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeased', 'imp. & p. p.', 'of Displease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeasing', 'p. pr. & vb. n.', 'of Displease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displease', 'v. t.', 'To make not pleased; to excite a feeling of\n   disapprobation or dislike in; to be disagreeable to; to offend; to vex;\n   -- often followed by with or at. It usually expresses less than to\n   anger, vex, irritate, or provoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displease', 'v. t.', 'To fail to satisfy; to miss of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displease', 'v. i.', 'To give displeasure or offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeasedly', 'adv.', 'With displeasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeasedness', 'n.', 'Displeasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeaser', 'n.', 'One who displeases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeasing', 'a.', 'Causing displeasure or dissatisfaction; offensive;\n   disagreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeasure', 'n.', 'The feeling of one who is displeased; irritation or\n   uneasiness of the mind, occasioned by anything that counteracts desire\n   or command, or which opposes justice or a sense of propriety;\n   disapprobation; dislike; dissatisfaction; disfavor; indignation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeasure', 'n.', 'That which displeases; cause of irritation or\n   annoyance; offense; injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeasure', 'n.', 'State of disgrace or disfavor; disfavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displeasure', 'v. t.', 'To displease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displenish', 'v. t.', 'To deprive or strip, as a house of furniture, or a\n   barn of stock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displicence', 'n.', 'Alt. of Displicency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displicency', 'n.', 'Dislike; dissatisfaction; discontent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disploded', 'imp. & p. p.', 'of Displode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disploding', 'p. pr. & vb. n.', 'of Displode');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displode', 'v. t.', 'To discharge; to explode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displode', 'v. i.', 'To burst with a loud report; to explode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displosion', 'n.', 'Explosion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displosive', 'a.', 'Explosive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displumed', 'imp. & p. p.', 'of Displume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displuming', 'p. pr. & vb. n.', 'of Displume');
  INSERT INTO words (word, word_type, description) VALUES  (    'Displume', 'v. t.', 'To strip of, or as of, a plume, or plumes; to deprive\n   of decoration; to dishonor; to degrade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispoline', 'n.', 'One of several isomeric organic bases of the quinoline\n   series of alkaloids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispond', 'n.', 'See Despond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispondee', 'n.', 'A double spondee; a foot consisting of four long\n   syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispone', 'v. t.', 'To dispose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispone', 'v. t.', 'To dispose of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispone', 'v. t.', 'To make over, or convey, legally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disponee', 'n.', 'The person to whom any property is legally conveyed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disponer', 'n.', 'One who legally transfers property from himself to\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disponge', 'v. t.', 'To sprinkle, as with water from a sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispope', 'v. t.', 'To refuse to consider as pope; to depose from the\n   popedom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disporous', 'a.', 'Having two spores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disport', 'v. i.', 'Play; sport; pastime; diversion; playfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disported', 'imp. & p. p.', 'of Disport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disporting', 'p. pr. & vb. n.', 'of Disport');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disport', 'v. i.', 'To play; to wanton; to move in gayety; to move lightly\n   and without restraint; to amuse ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disport', 'v. i.', 'To divert or amuse; to make merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disport', 'v. i.', 'To remove from a port; to carry away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disportment', 'n.', 'Act of disporting; diversion; play.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposable', 'a.', 'Subject to disposal; free to be used or employed as\n   occasion may require; not assigned to any service or use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposal', 'n.', 'The act of disposing, or disposing of, anything;\n   arrangement; orderly distribution; a putting in order; as, the disposal\n   of the troops in two lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposal', 'n.', 'Ordering; regulation; adjustment; management; government;\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposal', 'n.', 'Regulation of the fate, condition, application, etc., of\n   anything; the transference of anything into new hands, a new place,\n   condition, etc.; alienation, or parting; as, a disposal of property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposal', 'n.', 'Power or authority to dispose of, determine the condition\n   of, control, etc., especially in the phrase at, or in, the disposal of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposed', 'imp. & p. p.', 'of Dispose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposing', 'p. pr. & vb. n.', 'of Dispose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispose', 'v. t.', 'To distribute and put in place; to arrange; to set in\n   order; as, to dispose the ships in the form of a crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispose', 'v. t.', 'To regulate; to adjust; to settle; to determine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispose', 'v. t.', 'To deal out; to assign to a use; to bestow for an\n   object or purpose; to apply; to employ; to dispose of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispose', 'v. t.', 'To give a tendency or inclination to; to adapt; to\n   cause to turn; especially, to incline the mind of; to give a bent or\n   propension to; to incline; to make inclined; -- usually followed by to,\n   sometimes by for before the indirect object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispose', 'v. t.', 'To exercise finally ones power of control over; to\n   pass over into the control of some one else, as by selling; to\n   alienate; to part with; to relinquish; to get rid of; as, to dispose of\n   a house; to dispose of ones time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispose', 'v. i.', 'To bargain; to make terms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispose', 'n.', 'Disposal; ordering; management; power or right of control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispose', 'n.', 'Cast of mind; disposition; inclination; behavior;\n   demeanor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposed', 'p. a.', 'Inclined; minded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposed', 'p. a.', 'Inclined to mirth; jolly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposedness', 'n.', 'The state of being disposed or inclined; inclination;\n   propensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposement', 'n.', 'Disposal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposer', 'n.', 'One who, or that which, disposes; a regulator; a\n   director; a bestower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposingly', 'adv.', 'In a manner to dispose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposited', 'a.', 'Disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposition', 'n.', 'The act of disposing, arranging, ordering, regulating,\n   or transferring; application; disposal; as, the disposition of a mans\n   property by will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposition', 'n.', 'The state or the manner of being disposed or arranged;\n   distribution; arrangement; order; as, the disposition of the trees in\n   an orchard; the disposition of the several parts of an edifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposition', 'n.', 'Tendency to any action or state resulting from natural\n   constitution; nature; quality; as, a disposition in plants to grow in a\n   direction upward; a disposition in bodies to putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposition', 'n.', 'Conscious inclination; propension or propensity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposition', 'n.', 'Natural or prevailing spirit, or temperament of mind,\n   especially as shown in intercourse with ones fellow-men; temper of\n   mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposition', 'n.', 'Mood; humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispositional', 'a.', 'Pertaining to disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispositioned', 'a.', 'Having (such) a disposition; -- used in compounds;\n   as, well-dispositioned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispositive', 'a.', 'Disposing; tending to regulate; decretive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispositive', 'a.', 'Belonging to disposition or natural, tendency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispositively', 'adv.', 'In a dispositive manner; by natural or moral\n   disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispositor', 'n.', 'A disposer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispositor', 'n.', 'The planet which is lord of the sign where another\n   planet is.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispossessed', 'imp. & p. p.', 'of Dispossess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispossessing', 'p. pr. & vb. n.', 'of Dispossess');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispossess', 'v. t.', 'To put out of possession; to deprive of the actual\n   occupancy of, particularly of land or real estate; to disseize; to\n   eject; -- usually followed by of before the thing taken away; as, to\n   dispossess a king of his crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispossession', 'n.', 'The act of putting out of possession; the state of\n   being dispossessed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispossession', 'n.', 'The putting out of possession, wrongfully or\n   otherwise, of one who is in possession of a freehold, no matter in what\n   title; -- called also ouster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispossessor', 'n.', 'One who dispossesses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispost', 'v. t.', 'To eject from a post; to displace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposure', 'n.', 'The act of disposing; power to dispose of; disposal;\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disposure', 'n.', 'Disposition; arrangement; position; posture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispraisable', 'a.', 'Blamable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispraised', 'imp. & p. p.', 'of Dispraise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispraising', 'p. pr. & vb. n.', 'of Dispraise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispraise', 'v. t.', 'To withdraw praise from; to notice with\n   disapprobation or some degree of censure; to disparage; to blame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispraise', 'v. t.', 'The act of dispraising; detraction; blame censure;\n   reproach; disparagement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispraiser', 'n.', 'One who blames or dispraises.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispraisingly', 'adv.', 'By way of dispraise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispread', 'v. t.', 'To spread abroad, or different ways; to spread apart;\n   to open; as, the sun dispreads his beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispread', 'v. i.', 'To extend or expand itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispreader', 'n.', 'One who spreads abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprejudice', 'v. t.', 'To free from prejudice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprepare', 'v. t.', 'To render unprepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprince', 'v. t.', 'To make unlike a prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprison', 'v. t.', 'To let loose from prison, to set at liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprivilege', 'v. t.', 'To deprive of a privilege or privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprize', 'v. t.', 'To depreciate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprofess', 'v. t.', 'To renounce the profession or pursuit of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprofit', 'n.', 'Loss; damage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprofit', 'v. i. & i.', 'To be, or to cause to be, without profit or\n   benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprofitable', 'a.', 'Unprofitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproof', 'n.', 'A proving to be false or erroneous; confutation;\n   refutation; as, to offer evidence in disproof of a statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproperty', 'v. t.', 'To cause to be no longer property; to dispossess\n   of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproportion', 'n.', 'Want of proportion in form or quantity; lack of\n   symmetry; as, the arm may be in disproportion to the body; the\n   disproportion of the length of a building to its height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproportion', 'n.', 'Want of suitableness, adequacy, or due proportion to\n   an end or use; unsuitableness; disparity; as, the disproportion of\n   strength or means to an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproportioned', 'imp. & p. p.', 'of Disproportion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproportioning', 'p. pr. & vb. n.', 'of Disproportion');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproportion', 'v. t.', 'To make unsuitable in quantity, form, or fitness\n   to an end; to violate symmetry in; to mismatch; to join unfitly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproportionable', 'a.', 'Disproportional; unsuitable in form, size,\n   quantity, or adaptation; disproportionate; inadequate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproportional', 'a.', 'Not having due proportion to something else; not\n   having proportion or symmetry of parts; unsuitable in form, quantity or\n   value; inadequate; unequal; as, a disproportional limb constitutes\n   deformity in the body; the studies of youth should not be\n   disproportional to their understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproportionality', 'n.', 'The state of being disproportional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproportionally', 'adv.', 'In a disproportional manner; unsuitably in\n   form, quantity, or value; unequally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproportionate', 'a.', 'Not proportioned; unsymmetrical; unsuitable to\n   something else in bulk, form, value, or extent; out of proportion;\n   inadequate; as, in a perfect body none of the limbs are\n   disproportionate; it is wisdom not to undertake a work disproportionate\n   means.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispropriate', 'v. t.', 'To cancel the appropriation of; to disappropriate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprovable', 'a.', 'Capable of being disproved or refuted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproval', 'n.', 'Act of disproving; disproof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproved', 'imp. & p. p.', 'of Disprove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disproving', 'p. pr. & vb. n.', 'of Disprove');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprove', 'v. t.', 'To prove to be false or erroneous; to confute; to\n   refute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprove', 'v. t.', 'To disallow; to disapprove of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprover', 'n.', 'One who disproves or confutes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disprovide', 'v. t.', 'Not to provide; to fail to provide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispunct', 'a.', 'Wanting in punctilious respect; discourteous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispunct', 'v. t.', 'To expunge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispunge', 'v. t.', 'To expunge; to erase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispunge', 'v. t.', 'See Disponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispunishable', 'a.', 'Without penal restraint; not punishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispurpose', 'v. t.', 'To dissuade; to frustrate; as, to dispurpose plots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispurse', 'v. t.', 'To disburse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispurvey', 'v. t.', 'To disfurnish; to strip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispurveyance', 'n.', 'Want of provisions; /ack of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputable', 'v. i.', 'Capable of being disputed; liable to be called in\n   question, controverted, or contested; or doubtful certainty or\n   propriety; controvertible; as, disputable opinions, propositions,\n   points, or questions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputable', 'v. i.', 'Disputatious; contentious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputableness', 'n.', 'State of being disputable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputacity', 'v. i.', 'Proneness to dispute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputant', 'v. i.', 'Disputing; engaged in controversy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputant', 'n.', 'One who disputes; one who argues // opposition to\n   another; one appointed to dispute; a controvertist; a reasoner in\n   opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputation', 'v. i.', 'The act of disputing; a reasoning or argumentation\n   in opposition to something, or on opposite sides; controversy in words;\n   verbal contest respecting the truth of some fact, opinion, proposition,\n   or argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputation', 'v. i.', 'A rhetorical exercise in which parties reason in\n   opposition to each other on some question proposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputatious', 'a.', 'Inclined to dispute; apt to civil or controvert;\n   characterized by dispute; as, a disputatious person or temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputative', 'a.', 'Disposed to dispute; inclined to cavil or to reason in\n   opposition; as, a disputative temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputed', 'imp. & p. p.', 'of Dispute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputing', 'p. pr. & vb. n.', 'of Dispute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispute', 'v. i.', 'To contend in argument; to argue against something\n   maintained, upheld, or claimed, by another; to discuss; to reason; to\n   debate; to altercate; to wrangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispute', 'v. t.', 'To make a subject of disputation; to argue pro and con;\n   to discuss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispute', 'v. t.', 'To oppose by argument or assertion; to attempt to\n   overthrow; to controvert; to express dissent or opposition to; to call\n   in question; to deny the truth or validity of; as, to dispute\n   assertions or arguments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispute', 'v. t.', 'To strive or contend about; to contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispute', 'v. t.', 'To struggle against; to resist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispute', 'v. i.', 'Verbal controversy; contest by opposing argument or\n   expression of opposing views or claims; controversial discussion;\n   altercation; debate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dispute', 'v. i.', 'Contest; struggle; quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputeless', 'a.', 'Admitting no dispute; incontrovertible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputer', 'n.', 'One who disputes, or who is given to disputes; a\n   controvertist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disputison', 'n.', 'Dispute; discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disqualification', 'n.', 'The act of disqualifying, or state of being\n   disqualified; want of qualification; incompetency; disability; as, the\n   disqualification of men for holding certain offices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disqualification', 'n.', 'That which disqualifies; that which incapacitates\n   or makes unfit; as, conviction of crime is a disqualification of a\n   person for office; sickness is a disqualification for labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disqualified', 'imp. & p. p.', 'of Disqualify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disqualifying', 'p. pr. & vb. n.', 'of Disqualify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disqualify', 'v. t.', 'To deprive of the qualities or properties necessary\n   for any purpose; to render unfit; to incapacitate; -- with for or from\n   before the purpose, state, or act.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disqualify', 'v. t.', 'To deprive of some power, right, or privilege, by\n   positive restriction; to disable; to debar legally; as, a conviction of\n   perjury disqualifies a man to be a witness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquantity', 'v. t.', 'To diminish the quantity of; to lessen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquiet', 'a.', 'Deprived of quiet; impatient; restless; uneasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquiet', 'n.', 'Want of quiet; want of tranquility in body or mind;\n   uneasiness; restlessness; disturbance; anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquieted', 'imp. & p. p.', 'of Disquiet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquieting', 'p. pr. & vb. n.', 'of Disquiet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquiet', 'v. t.', 'To render unquiet; to deprive of peace, rest, or\n   tranquility; to make uneasy or restless; to disturb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquietal', 'n.', 'The act of disquieting; a state of disquiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquieter', 'n.', 'One who, or that which, disquiets, or makes uneasy; a\n   disturber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquietful', 'a.', 'Producing inquietude or uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquietive', 'a.', 'Tending to disquiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquietly', 'adv.', 'In a disquiet manner; uneasily; as, he rested\n   disquietly that night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquietment', 'n.', 'State of being disquieted; uneasiness; harassment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquietness', 'n.', 'Disturbance of quiet in body or mind; restlessness;\n   uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquietous', 'a.', 'Causing uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquiettude', 'n.', 'Want of peace or tranquility; uneasiness;\n   disturbance; agitation; anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquisition', 'n.', 'A formal or systematic inquiry into, or discussion\n   of, any subject; a full examination or investigation of a matter, with\n   the arguments and facts bearing upon it; elaborate essay; dissertation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquisitional', 'a.', 'Pertaining to disquisition; of the nature of\n   disquisition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquisitionary', 'a.', 'Pertaining to disquisition; disquisitional.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquisitive', 'a.', 'Relating to disquisition; fond discussion or\n   investigation; examining; inquisitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquisitorial', 'a.', 'Disquisitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disquisitory', 'a.', 'Of or pertaining to disquisition; disquisitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrange', 'v. t.', 'To disarrange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrank', 'v. t.', 'To degrade from rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrank', 'v. t.', 'To throw out of rank or into confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrate', 'v. t.', 'To reduce to a lower rating or rank; to degrade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disray', 'variant', 'of Disarray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrealize', 'v. t.', 'To divest of reality; to make uncertain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disregarded', 'imp. & p. p.', 'of Disregard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disregarding', 'p. pr. & vb. n.', 'of Disregard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disregard', 'v. t.', 'Not to regard; to pay no heed to; to omit to take\n   notice of; to neglect to observe; to slight as unworthy of regard or\n   notice; as, to disregard the admonitions of conscience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disregard', 'n.', 'The act of disregarding, or the state of being\n   disregarded; intentional neglect; omission of notice; want of\n   attention; slight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disregarder', 'n.', 'One who disregards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disregardful', 'a.', 'Neglect; negligent; heedless; regardless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disregardfully', 'adv.', 'Negligently; heedlessly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrelish', 'n.', 'Want of relish; dislike (of the palate or of the mind);\n   distaste; a slight degree of disgust; as, a disrelish for some kinds of\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrelish', 'n.', 'Absence of relishing or palatable quality; bad taste;\n   nauseousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrelished', 'imp. & p. p.', 'of Disrelish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrelishing', 'p. pr. & vb. n.', 'of Disrelish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrelish', 'v. t.', 'Not to relish; to regard as unpalatable or offensive;\n   to feel a degree of disgust at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrelish', 'v. t.', 'To deprive of relish; to make nauseous or disgusting\n   in a slight degree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disremember', 'v. t.', 'To fail to remember; to forget.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrepair', 'n.', 'A state of being in bad condition, and wanting repair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disreputability', 'n.', 'The state of being disreputable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disreputable', 'a.', 'Not reputable; of bad repute; not in esteem;\n   dishonorable; disgracing the reputation; tending to bring into\n   disesteem; as, it is disreputable to associate familiarly with the\n   mean, the lewd, and the profane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disreputably', 'adv.', 'In a disreputable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disreputation', 'n.', 'Loss or want of reputation or good name; dishonor;\n   disrepute; disesteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrepute', 'n.', 'Loss or want of reputation; ill character; disesteem;\n   discredit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrepute', 'v. t.', 'To bring into disreputation; to hold in dishonor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrespect', 'n.', 'Want of respect or reverence; disesteem; incivility;\n   discourtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrespect', 'v. t.', 'To show disrespect to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrespectability', 'n.', 'Want of respectability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrespectable', 'a.', 'Not respectable; disreputable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrespecter', 'n.', 'One who disrespects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrespectful', 'a.', 'Wanting in respect; manifesting disesteem or lack of\n   respect; uncivil; as, disrespectful behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrespective', 'a.', 'Showing want of respect; disrespectful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disreverence', 'v. t.', 'To treat irreverently or with disrespect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrobed', 'imp. & p. p.', 'of Disrobe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrobing', 'p. pr. & vb. n.', 'of Disrobe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrobe', 'v. t. & i.', 'To divest of a robe; to undress; figuratively, to\n   strip of covering; to divest of that which clothes or decorates; as,\n   autumn disrobes the fields of verdure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrober', 'n.', 'One who, or that which, disrobes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disroof', 'v. t.', 'To unroof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrooted', 'imp. & p. p.', 'of Disroot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrooting', 'p. pr. & vb. n.', 'of Disroot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disroot', 'v. t.', 'To tear up the roots of, or by the roots; hence, to\n   tear from a foundation; to uproot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrout', 'v. i.', 'To put to rout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrudder', 'v. t.', 'To deprive of the rudder, as a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrulily', 'adv.', 'In a disorderly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disruly', 'a.', 'Unruly; disorderly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrupt', 'a.', 'Rent off; torn asunder; severed; disrupted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrupted', 'imp. & p. p.', 'of Disrupt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrupting', 'p. pr. & vb. n.', 'of Disrupt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrupt', 'v. t.', 'To break asunder; to rend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disruption', 'n.', 'The act or rending asunder, or the state of being rent\n   asunder or broken in pieces; breach; rent; dilaceration; rupture; as,\n   the disruption of rocks in an earthquake; disruption of a state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disruptive', 'a.', 'Causing, or tending to cause, disruption; caused by\n   disruption; breaking through; bursting; as, the disruptive discharge of\n   an electrical battery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disrupture', 'n.', 'Disruption.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissatisfaction', 'n.', 'The state of being dissatisfied, unsatisfied, or\n   discontented; uneasiness proceeding from the want of gratification, or\n   from disappointed wishes and expectations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissatisfactory', 'a.', 'Causing dissatisfaction; unable to give content;\n   unsatisfactory; displeasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissatisfied', 'imp. & p. p.', 'of Dissatisfy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissatisfying', 'p. pr. & vb. n.', 'of Dissatisfy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissatisfy', 'v. t.', 'To render unsatisfied or discontented; to excite\n   uneasiness in by frustrating wishes or expectations; to displease by\n   the want of something requisite; as, to be dissatisfied with ones\n   fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseat', 'v. t.', 'To unseat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissected', 'imp. & p. p.', 'of Dissect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissecting', 'p. pr. & vb. n.', 'of Dissect');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissect', 'v. t.', 'To divide into separate parts; to cut in pieces; to\n   separate and expose the parts of, as an animal or a plant, for\n   examination and to show their structure and relations; to anatomize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissect', 'v. t.', 'To analyze, for the purposes of science or criticism;\n   to divide and examine minutely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissected', 'a.', 'Cut into several parts; divided into sections; as, a\n   dissected map.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissected', 'a.', 'Cut deeply into many lobes or divisions; as, a dissected\n   leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissectible', 'a.', 'Capable of being dissected, or separated by\n   dissection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissecting', 'a.', 'Dividing or separating the parts of an animal or\n   vegetable body; as, a dissecting aneurism, one which makes its way\n   between or within the coats of an artery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissecting', 'a.', 'Of or pertaining to, or received during, a dissection;\n   as, a dissecting wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissecting', 'a.', 'Used for or in dissecting; as, a dissecting knife; a\n   dissecting microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissection', 'n.', 'The act of dissecting an animal or plant; as,\n   dissection of the human body was held sacrilege till the time of\n   Francis I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissection', 'n.', 'Fig.: The act of separating or dividing for the purpose\n   of critical examination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissection', 'n.', 'Anything dissected; especially, some part, or the\n   whole, of an animal or plant dissected so as to exhibit the structure;\n   an anatomical so prepared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissector', 'n.', 'One who dissects; an anatomist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseized', 'imp. & p. p.', 'of Disseize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseizing', 'p. pr. & vb. n.', 'of Disseize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseize', 'v. t.', 'To deprive of seizin or possession; to dispossess or\n   oust wrongfully (one in freehold possession of land); -- followed by\n   of; as, to disseize a tenant of his freehold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseizee', 'n.', 'A person disseized, or put out of possession of an\n   estate unlawfully; -- correlative to disseizor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseizin', 'n.', 'The act of disseizing; an unlawful dispossessing and\n   ouster of a person actually seized of the freehold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseizor', 'n.', 'One who wrongfully disseizes, or puts another out of\n   possession of a freehold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseizoress', 'n.', 'A woman disseizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseizure', 'n.', 'Disseizin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissemblance', 'n.', 'Want of resemblance; dissimilitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissemblance', 'n.', 'The act or art of dissembling; dissimulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissembled', 'imp. & p. p.', 'of Dissemble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissembling', 'p. pr. & vb. n.', 'of Dissemble');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissemble', 'v. t.', 'To hide under a false semblance or seeming; to feign\n   (something) not to be what it really is; to put an untrue appearance\n   upon; to disguise; to mask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissemble', 'v. t.', 'To put on the semblance of; to make pretense of; to\n   simulate; to feign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissemble', 'v. i.', 'To conceal the real fact, motives, /tention, or\n   sentiments, under some pretense; to assume a false appearance; to act\n   the hypocrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissembler', 'n.', 'One who dissembles; one who conceals his opinions or\n   dispositions under a false appearance; a hypocrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissembling', 'a.', 'That dissembles; hypocritical; false.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseminated', 'imp. & p. p.', 'of Disseminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseminating', 'p. pr. & vb. n.', 'of Disseminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseminate', 'v. t. & i.', 'To sow broadcast or as seed; to scatter for\n   growth and propagation, like seed; to spread abroad; to diffuse; as,\n   principles, ideas, opinions, and errors are disseminated when they are\n   spread abroad for propagation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseminate', 'v. t. & i.', 'To spread or extend by dispersion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseminated', 'p. a.', 'Occurring in small portions scattered through some\n   other substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissemination', 'n.', 'The act of disseminating, or the state of being\n   disseminated; diffusion for propagation and permanence; a scattering or\n   spreading abroad, as of ideas, beliefs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseminative', 'a.', 'Tending to disseminate, or to become disseminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseminator', 'n.', 'One who, or that which, disseminates, spreads, or\n   propagates; as, disseminators of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissension', 'n.', 'Disagreement in opinion, usually of a violent\n   character, producing warm debates or angry words; contention in words;\n   partisan and contentious divisions; breach of friendship and union;\n   strife; discord; quarrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissensious', 'a.', 'Disposed to discord; contentious; dissentious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissented', 'imp. & p. p.', 'of Dissent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissenting', 'p. pr. & vb. n.', 'of Dissent');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissent', 'v. i.', 'To differ in opinion; to be of unlike or contrary\n   sentiment; to disagree; -- followed by from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissent', 'v. i.', 'To differ from an established church in regard to\n   doctrines, rites, or government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissent', 'v. i.', 'To differ; to be of a contrary nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissent', 'n.', 'The act of dissenting; difference of opinion; refusal to\n   adopt something proposed; nonagreement, nonconcurrence, or\n   disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissent', 'n.', 'Separation from an established church, especially that of\n   England; nonconformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissent', 'n.', 'Contrariety of nature; diversity in quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissentaneous', 'a.', 'Disagreeing; contrary; differing; -- opposed to\n   consentaneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissentany', 'a.', 'Dissentaneous; inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissentation', 'n.', 'Dissension.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissenter', 'n.', 'One who dissents; one who differs in opinion, or\n   declares his disagreement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissenter', 'n.', 'One who separates from the service and worship of an\n   established church; especially, one who disputes the authority or\n   tenets of the Church of England; a nonconformist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissenterism', 'n.', 'The spirit or principles of dissenters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissentiate', 'v. t.', 'To throw into a state of dissent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissentient', 'v. i.', 'Disagreeing; declaring dissent; dissenting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissentient', 'n.', 'One who dissents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissentious', 'a.', 'Marked by dissensions; apt to breed discord;\n   quarrelsome; contentious; factious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissentive', 'a.', 'Disagreeing; inconsistent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissepiment', 'n.', 'A separating tissue; a partition; a septum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissepiment', 'n.', 'One of the partitions which divide a compound ovary\n   into cells.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissepiment', 'n.', 'One of the transverse, calcareous partitions between\n   the radiating septa of a coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissert', 'v. i.', 'To discourse or dispute; to discuss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissertate', 'v. i.', 'To deal in dissertation; to write dissertations; to\n   discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissertation', 'n.', 'A formal or elaborate argumentative discourse, oral\n   or written; a disquisition; an essay; a discussion; as, Dissertations\n   on the Prophecies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissertational', 'a.', 'Relating to dissertations; resembling a\n   dissertation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissertationist', 'n.', 'A writer of dissertations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissertator', 'n.', 'One who writers a dissertation; one who discourses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissertly', 'adv.', 'See Disertly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Di/////', 'imp. & p. p.', 'of Disserve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disserving', 'p. pr. & vb. n.', 'of Disserve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disserve', 'v. t.', 'To fail to serve; to do injury or mischief to; to\n   damage; to hurt; to harm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disservice', 'n.', 'Injury; mischief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disserviceable', 'a.', 'Calculated to do disservice or harm; not\n   serviceable; injurious; harmful; unserviceable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissettle', 'v. t.', 'To unsettle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissettlement', 'n.', 'The act of unsettling, or the state of being\n   unsettled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissevered', 'imp. & p. p.', 'of Dissever');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissevering', 'p. pr. & vb. n.', 'of Dissever');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissever', 'v. t.', 'To part in two; to sever thoroughly; to sunder; to\n   disunite; to separate; to disperse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissever', 'v. i.', 'To part; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseverance', 'n.', 'The act of disserving; separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseveration', 'n.', 'The act of disserving; disseverance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disseverment', 'n.', 'Disseverance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disshadow', 'v. t.', 'To free from shadow or shade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissheathe', 'v. i.', 'To become unsheathed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disship', 'v. t.', 'To dismiss from service on board ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disshiver', 'v. t. & i.', 'To shiver or break in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissidence', 'a.', 'Disagreement; dissent; separation from the established\n   religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissident', 'a.', 'No agreeing; dissenting; discordant; different.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissident', 'n.', 'One who disagrees or dissents; one who separates from\n   the established religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissidently', 'adv.', 'In a dissident manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissilience', 'n.', 'Alt. of Dissiliency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissiliency', 'n.', 'The act of leaping or starting asunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissilient', 'a.', 'Starting asunder; bursting and opening with an elastic\n   force; dehiscing explosively; as, a dissilient pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissilition', 'n.', 'The act of bursting or springing apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimilar', 'a.', 'Not similar; unlike; heterogeneous; as, the tempers of\n   men are as dissimilar as their features.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimilarity', 'n.', 'Want of resemblance; unlikeness; dissimilitude;\n   variety; as, the dissimilarity of human faces and forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimilarly', 'adv.', 'In a dissimilar manner; in a varied style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimilate', 'v. t.', 'To render dissimilar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimilation', 'n.', 'The act of making dissimilar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimile', 'n.', 'Comparison or illustration by contraries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimilitude', 'n.', 'Want of resemblance; unlikeness; dissimilarity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimilitude', 'n.', 'A comparison by contrast; a dissimile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimulate', 'a.', 'Feigning; simulating; pretending.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimulate', 'v. i.', 'To dissemble; to feign; to pretend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimulation', 'n.', 'The act of dissembling; a hiding under a false\n   appearance; concealment by feigning; false pretension; hypocrisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimulator', 'n.', 'One who dissimulates; a dissembler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimule', 'v. t. & i.', 'To dissemble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimuler', 'n.', 'A dissembler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissimulour', 'n.', 'A dissembler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipable', 'a.', 'Capable of being scattered or dissipated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipated', 'imp. & p. p.', 'of Dissipate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipating', 'p. pr. & vb. n.', 'of Dissipate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipate', 'v. t.', 'To scatter completely; to disperse and cause to\n   disappear; -- used esp. of the dispersion of things that can never\n   again be collected or restored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipate', 'v. t.', 'To destroy by wasteful extravagance or lavish use; to\n   squander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipate', 'v. i.', 'To separate into parts and disappear; to waste away;\n   to scatter; to disperse; to vanish; as, a fog or cloud gradually\n   dissipates before the rays or heat of the sun; the heat of a body\n   dissipates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipate', 'v. i.', 'To be extravagant, wasteful, or dissolute in the\n   pursuit of pleasure; to engage in dissipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipated', 'a.', 'Squandered; scattered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipated', 'a.', 'Wasteful of health, money, etc., in the pursuit of\n   pleasure; dissolute; intemperate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipation', 'n.', 'The act of dissipating or dispersing; a state of\n   dispersion or separation; dispersion; waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipation', 'n.', 'A dissolute course of life, in which health, money,\n   etc., are squandered in pursuit of pleasure; profuseness in vicious\n   indulgence, as late hours, riotous living, etc.; dissoluteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipation', 'n.', 'A trifle which wastes time or distracts attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipative', 'a.', 'Tending to dissipate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissipativity', 'n.', 'The rate at which palpable energy is dissipated away\n   into other forms of energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissite', 'a.', 'Lying apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disslander', 'v. t.', 'To slander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disslander', 'n.', 'Slander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disslanderous', 'a.', 'Slanderous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissociability', 'n.', 'Want of sociability; unsociableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissociable', 'a.', 'Not /ell associated or assorted; incongruous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissociable', 'a.', 'Having a tendency to dissolve social connections;\n   unsuitable to society; unsociable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissocial', 'v. t.', 'Unfriendly to society; contracted; selfish; as,\n   dissocial feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissocialize', 'v. t.', 'To render unsocial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissociated', 'imp. & p. p.', 'of Dissociate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissociating', 'p. pr. & vb. n.', 'of Dissociate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissociate', 'v. t.', 'To separate from fellowship or union; to disunite;\n   to disjoin; as, to dissociate the particles of a concrete substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissociation', 'n.', 'The act of dissociating or disuniting; a state of\n   separation; disunion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissociation', 'n.', 'The process by which a compound body breaks up into\n   simpler constituents; -- said particularly of the action of heat on\n   gaseous or volatile substances; as, the dissociation of the sulphur\n   molecules; the dissociation of ammonium chloride into hydrochloric acid\n   and ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissociative', 'a.', 'Tending or leading to dissociation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolubility', 'n.', 'The quality of being dissoluble; capacity of being\n   dissoluble; capacity of being dissolved by heat or moisture, and\n   converted into a fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissoluble', 'a.', 'Capable of being dissolved; having its parts separable\n   by heat or moisture; convertible into a fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissoluble', 'a.', 'Capable of being disunited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolubleness', 'n.', 'The quality of being dissoluble; dissolubility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolute', 'a.', 'With nerves unstrung; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolute', 'a.', 'Loosed from restraint; esp., loose in morals and\n   conduct; recklessly abandoned to sensual pleasures; profligate; wanton;\n   lewd; debauched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolutely', 'adv.', 'In a dissolute manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissoluteness', 'n.', 'State or quality of being dissolute; looseness of\n   morals and manners; addictedness to sinful pleasures; debauchery;\n   dissipation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolution', 'n.', 'The act of dissolving, sundering, or separating into\n   component parts; separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolution', 'n.', 'Change from a solid to a fluid state; solution by heat\n   or moisture; liquefaction; melting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolution', 'n.', 'Change of form by chemical agency; decomposition;\n   resolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolution', 'n.', 'The dispersion of an assembly by terminating its\n   sessions; the breaking up of a partnership.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolution', 'n.', 'The extinction of life in the human body; separation\n   of the soul from the body; death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolution', 'n.', 'The state of being dissolved, or of undergoing\n   liquefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolution', 'n.', 'The new product formed by dissolving a body; a\n   solution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolution', 'n.', 'Destruction of anything by the separation of its\n   parts; ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolution', 'n.', 'Corruption of morals; dissipation; dissoluteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolvability', 'n.', 'Capacity of being dissolved; solubility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolvable', 'a.', 'Capable of being dissolved, or separated into\n   component parts; capable of being liquefied; soluble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolvative', 'n.', 'Having the power to dissolve anything; solvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolved', 'imp. & p. p.', 'of Dissolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolving', 'p. pr. & vb. n.', 'of Dissolve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolve', 'v. t.', 'To separate into competent parts; to disorganize; to\n   break up; hence, to bring to an end by separating the parts, sundering\n   a relation, etc.; to terminate; to destroy; to deprive of force; as, to\n   dissolve a partnership; to dissolve Parliament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolve', 'v. t.', 'To break the continuity of; to disconnect; to\n   disunite; to sunder; to loosen; to undo; to separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolve', 'v. t.', 'To convert into a liquid by means of heat, moisture,\n   etc.,; to melt; to liquefy; to soften.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolve', 'v. t.', 'To solve; to clear up; to resolve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolve', 'v. t.', 'To relax by pleasure; to make powerless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolve', 'v. t.', 'To annul; to rescind; to discharge or release; as, to\n   dissolve an injunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolve', 'v. i.', 'To waste away; to be dissipated; to be decomposed or\n   broken up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolve', 'v. i.', 'To become fluid; to be melted; to be liquefied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolve', 'v. i.', 'To fade away; to fall to nothing; to lose power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolvent', 'a.', 'Having power to dissolve power to dissolve a solid\n   body; as, the dissolvent juices of the stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolvent', 'n.', 'That which has the power of dissolving or melting other\n   substances, esp. by mixture with them; a menstruum; a solvent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolvent', 'n.', 'A remedy supposed capable of dissolving concretions in\n   the body, such as calculi, tubercles, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolver', 'n.', 'One who, or that which, has power to dissolve or\n   dissipate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissolving', 'a.', 'Melting; breaking up; vanishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissonance', 'n.', 'A mingling of discordant sounds; an inharmonious\n   combination of sounds; discord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissonance', 'n.', 'Want of agreement; incongruity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissonancy', 'n.', 'Discord; dissonance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissonant', 'a.', 'Sounding harshly; discordant; unharmonious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissonant', 'a.', 'Disagreeing; incongruous; discrepant, -- with from or\n   to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disspirit', 'v. t.', 'See Dispirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissuaded', 'imp. & p. p.', 'of Dissuade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissuading', 'p. pr. & vb. n.', 'of Dissuade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissuade', 'v. t.', 'To advise or exhort against; to try to persuade (one\n   from a course).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissuade', 'v. t.', 'To divert by persuasion; to turn from a purpose by\n   reasons or motives; -- with from; as, I could not dissuade him from his\n   purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissuader', 'n.', 'One who dissuades; a dehorter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissuasion', 'n.', 'The act of dissuading; exhortation against a thing;\n   dehortation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissuasion', 'n.', 'A motive or consideration tending to dissuade; a\n   dissuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissuasive', 'a.', 'Tending to dissuade or divert from a measure or\n   purpose; dehortatory; as, dissuasive advice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissuasive', 'n.', 'A dissuasive argument or counsel; dissuasion;\n   dehortation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissuasory', 'n.', 'A dissuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissundered', 'imp. & p. p.', 'of Dissunder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissundering', 'p. pr. & vb. n.', 'of Dissunder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissunder', 'v. t.', 'To separate; to sunder; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissweeten', 'v. t.', 'To deprive of sweetness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissyllabic', 'a.', 'Consisting of two syllables only; as, a dissyllabic\n   foot in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissyllabification', 'n.', 'A forming into two syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissyllabify', 'v. t.', 'To form into two syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissyllabize', 'v. t.', 'To form into two syllables; to dissyllabify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissyllable', 'n.', 'A word of two syllables; as, pa-per.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissymmetrical', 'a.', 'Not having symmetry; asymmetrical; unsymmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissymmetry', 'n.', 'Absence or defect of symmetry; asymmetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dissympathy', 'n.', 'Lack of sympathy; want of interest; indifference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distad', 'adv.', 'Toward a distal part; on the distal side of; distally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaffs', 'pl. ', 'of Distaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaves', 'pl. ', 'of Distaff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaff', 'n.', 'The staff for holding a bunch of flax, tow, or wool, from\n   which the thread is drawn in spinning by hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaff', 'n.', 'Used as a symbol of the holder of a distaff; hence, a\n   woman; women, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distained', 'imp. & p. p.', 'of Distain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaining', 'p. pr. & vb. n.', 'of Distain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distain', 'v. t.', 'To tinge with a different color from the natural or\n   proper one; to stain; to discolor; to sully; to tarnish; to defile; --\n   used chiefly in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distal', 'a.', 'Remote from the point of attachment or origin; as, the\n   distal end of a bone or muscle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distal', 'a.', 'Pertaining to that which is distal; as, the distal\n   tuberosities of a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distally', 'adv.', 'Toward a distal part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'The space between two objects; the length of a line,\n   especially the shortest line joining two points or things that are\n   separate; measure of separation in place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'Remoteness of place; a remote place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'A space marked out in the last part of a race course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'Relative space, between troops in ranks, measured from\n   front to rear; -- contrasted with interval, which is measured from\n   right to left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'Space between two antagonists in fencing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'The part of a picture which contains the representation\n   of those objects which are the farthest away, esp. in a landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'Ideal disjunction; discrepancy; contrariety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'Length or interval of time; period, past or future,\n   between two eras or events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'The remoteness or reserve which respect requires; hence,\n   respect; ceremoniousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'A withholding of intimacy; alienation; coldness;\n   disagreement; variance; restraint; reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'Remoteness in succession or relation; as, the distance\n   between a descendant and his ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'n.', 'The interval between two notes; as, the distance of a\n   fourth or seventh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distanced', 'imp. & p. p.', 'of Distance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distancing', 'p. pr. & vb. n.', 'of Distance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'v. t.', 'To place at a distance or remotely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'v. t.', 'To cause to appear as if at a distance; to make seem\n   remote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distance', 'v. t.', 'To outstrip by as much as a distance (see Distance,\n   n., 3); to leave far behind; to surpass greatly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distancy', 'n.', 'Distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distant', 'a.', 'Separated; having an intervening space; at a distance;\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distant', 'a.', 'Far separated; far off; not near; remote; -- in place,\n   time, consanguinity, or connection; as, distant times; distant\n   relatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distant', 'a.', 'Reserved or repelling in manners; cold; not cordial;\n   somewhat haughty; as, a distant manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distant', 'a.', 'Indistinct; faint; obscure, as from distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distant', 'a.', 'Not conformable; discrepant; repugnant; as, a practice so\n   widely distant from Christianity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distantial', 'a.', 'Distant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distantly', 'adv.', 'At a distance; remotely; with reserve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaste', 'n.', 'Aversion of the taste; dislike, as of food or drink;\n   disrelish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaste', 'n.', 'Discomfort; uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaste', 'n.', 'Alienation of affection; displeasure; anger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distasted', 'imp. & p. p.', 'of Distaste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distasting', 'p. pr. & vb. n.', 'of Distaste');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaste', 'v. t.', 'Not to have relish or taste for; to disrelish; to\n   loathe; to dislike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaste', 'v. t.', 'To offend; to disgust; to displease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaste', 'v. t.', 'To deprive of taste or relish; to make unsavory or\n   distasteful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distaste', 'v. i.', 'To be distasteful; to taste ill or disagreeable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distasteful', 'a.', 'Unpleasant or disgusting to the taste; nauseous;\n   loathsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distasteful', 'a.', 'Offensive; displeasing to the feelings; disagreeable;\n   as, a distasteful truth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distasteful', 'a.', 'Manifesting distaste or dislike; repulsive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distasteive', 'a.', 'Tending to excite distaste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distasteive', 'n.', 'That which excites distaste or aversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distasture', 'n.', 'Something which excites distaste or disgust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distempered', 'imp. & p. p.', 'of Distemper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distempering', 'p. pr. & vb. n.', 'of Distemper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'To temper or mix unduly; to make disproportionate; to\n   change the due proportions of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'To derange the functions of, whether bodily, mental,\n   or spiritual; to disorder; to disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'To deprive of temper or moderation; to disturb; to\n   ruffle; to make disaffected, ill-humored, or malignant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'To intoxicate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'To mix (colors) in the way of distemper; as, to\n   distemper colors with size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'An undue or unnatural temper, or disproportionate\n   mixture of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'Severity of climate; extreme weather, whether hot or\n   cold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'A morbid state of the animal system; indisposition;\n   malady; disorder; -- at present chiefly applied to diseases of brutes;\n   as, a distemper in dogs; the horse distemper; the horn distemper in\n   cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'Morbid temper of the mind; undue predominance of a\n   passion or appetite; mental derangement; bad temper; ill humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'Political disorder; tumult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'A preparation of opaque or body colors, in which the\n   pigments are tempered or diluted with weak glue or size (cf. Tempera)\n   instead of oil, usually for scene painting, or for walls and ceilings\n   of rooms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemper', 'v. t.', 'A painting done with this preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemperance', 'n.', 'Distemperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemperate', 'a.', 'Immoderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemperate', 'a.', 'Diseased; disordered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemperately', 'adv.', 'Unduly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemperature', 'n.', 'Bad temperature; intemperateness; excess of heat or\n   cold, or of other qualities; as, the distemperature of the air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemperature', 'n.', 'Disorder; confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemperature', 'n.', 'Disorder of body; slight illness; distemper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemperature', 'n.', 'Perturbation of mind; mental uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distemperment', 'n.', 'Distempered state; distemperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distended', 'imp. & p. p.', 'of Distend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distending', 'p. pr. & vb. n.', 'of Distend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distend', 'v. t.', 'To extend in some one direction; to lengthen out; to\n   stretch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distend', 'v. t.', 'To stretch out or extend in all directions; to dilate;\n   to enlarge, as by elasticity of parts; to inflate so as to produce\n   tension; to cause to swell; as, to distend a bladder, the stomach, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distend', 'v. i.', 'To become expanded or inflated; to swell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distensibility', 'n.', 'The quality or capacity of being distensible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distensible', 'a.', 'Capable of being distended or dilated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distension', 'n.', 'Same as Distention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distensive', 'a.', 'Distending, or capable of being distended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distent', 'a.', 'Distended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distent', 'n.', 'Breadth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distention', 'n.', 'The act of distending; the act of stretching in breadth\n   or in all directions; the state of being Distended; as, the distention\n   of the lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distention', 'n.', 'Breadth; extent or space occupied by the thing\n   distended.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dister', 'v. t.', 'To banish or drive from a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disterminate', 'a.', 'Separated by bounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distermination', 'n.', 'Separation by bounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disthene', 'n.', 'Cyanite or kyanite; -- so called in allusion to its\n   unequal hardness in two different directions. See Cyanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disthrone', 'v. t.', 'To dethrone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disthronize', 'v. t.', 'To dethrone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distich', 'n.', 'A couple of verses or poetic lines making complete sense;\n   an epigram of two verses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distich', 'n.', 'Alt. of Distichous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distichous', 'n.', 'Disposed in two vertical rows; two-ranked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distichously', 'adv.', 'In a distichous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distil', 'v. t. & i.', 'See Distill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distilled', 'imp. & p. p.', 'of Distill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distilling', 'p. pr. & vb. n.', 'of Distill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distill', 'n. & v', 'To drop; to fall in drops; to trickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distill', 'n. & v', 'To flow gently, or in a small stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distill', 'n. & v', 'To practice the art of distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distill', 'v. t.', 'To let fall or send down in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distill', 'v. t.', 'To obtain by distillation; to extract by distillation,\n   as spirits, essential oil, etc.; to rectify; as, to distill brandy from\n   wine; to distill alcoholic spirits from grain; to distill essential\n   oils from flowers, etc.; to distill fresh water from sea water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distill', 'v. t.', 'To subject to distillation; as, to distill molasses in\n   making rum; to distill barley, rye, corn, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distill', 'v. t.', 'To dissolve or melt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillable', 'a.', 'Capable of being distilled; especially, capable of\n   being distilled without chemical change or decomposition; as, alcohol\n   is distillable; olive oil is not distillable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillate', 'n.', 'The product of distillation; as, the distillate from\n   molasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillation', 'n.', 'The act of falling in drops, or the act of pouring\n   out in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillation', 'n.', 'That which falls in drops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillation', 'n.', 'The separation of the volatile parts of a substance\n   from the more fixed; specifically, the operation of driving off gas or\n   vapor from volatile liquids or solids, by heat in a retort or still,\n   and the condensation of the products as far as possible by a cool\n   receiver, alembic, or condenser; rectification; vaporization;\n   condensation; as, the distillation of illuminating gas and coal, of\n   alcohol from sour mash, or of boric acid in steam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillation', 'n.', 'The substance extracted by distilling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillatory', 'a.', 'Belonging to, or used in, distilling; as,\n   distillatory vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillatory', 'n.', 'A distillatory apparatus; a still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distiller', 'n.', 'One who distills; esp., one who extracts alcoholic\n   liquors by distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distiller', 'n.', 'The condenser of a distilling apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distilleries', 'pl. ', 'of Distillery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillery', 'n.', 'The building and works where distilling, esp. of\n   alcoholic liquors, is carried on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillery', 'n.', 'The act of distilling spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distillment', 'n.', 'Distillation; the substance obtained by distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinct', 'a.', 'Distinguished; having the difference marked; separated by\n   a visible sign; marked out; specified.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinct', 'a.', 'Marked; variegated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinct', 'a.', 'Separate in place; not conjunct; not united by growth or\n   otherwise; -- with from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinct', 'a.', 'Not identical; different; individual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinct', 'a.', 'So separated as not to be confounded with any other\n   thing; not liable to be misunderstood; not confused; well-defined;\n   clear; as, we have a distinct or indistinct view of a prospect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinct', 'v. t.', 'To distinguish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinction', 'n.', 'A marking off by visible signs; separation into parts;\n   division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinction', 'n.', 'The act of distinguishing or denoting the differences\n   between objects, or the qualities by which one is known from others;\n   exercise of discernment; discrimination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinction', 'n.', 'That which distinguishes one thing from another;\n   distinguishing quality; sharply defined difference; as, the distinction\n   between real and apparent good.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinction', 'n.', 'Estimation of difference; regard to differences or\n   distinguishing circumstance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinction', 'n.', 'Conspicuous station; eminence; superiority; honorable\n   estimation; as, a man of distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinctive', 'a.', 'Marking or expressing distinction or difference;\n   distinguishing; characteristic; peculiar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinctive', 'a.', 'Having the power to distinguish and discern;\n   discriminating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinctively', 'adv.', 'With distinction; plainly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinctiveness', 'n.', 'State of being distinctive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinctly', 'adv.', 'With distinctness; not confusedly; without the\n   blending of one part or thing another; clearly; plainly; as, to see\n   distinctly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinctly', 'adv.', 'With meaning; significantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinctness', 'n.', 'The quality or state of being distinct; a separation\n   or difference that prevents confusion of parts or things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinctness', 'n.', 'Nice discrimination; hence, clearness; precision; as,\n   he stated his arguments with great distinctness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distincture', 'n.', 'Distinctness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguished', 'imp. & p. p.', 'of Distinguish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguishing', 'p. pr. & vb. n.', 'of Distinguish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguish', 'v. t.', 'Not set apart from others by visible marks; to make\n   distinctive or discernible by exhibiting differences; to mark off by\n   some characteristic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguish', 'v. t.', 'To separate by definition of terms or logical\n   division of a subject with regard to difference; as, to distinguish\n   sounds into high and low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguish', 'v. t.', 'To recognize or discern by marks, signs, or\n   characteristic quality or qualities; to know and discriminate\n   (anything) from other things with which it might be confounded; as, to\n   distinguish the sound of a drum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguish', 'v. t.', 'To constitute a difference; to make to differ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguish', 'v. t.', 'To separate from others by a mark of honor; to make\n   eminent or known; to confer distinction upon; -- with by or for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguish', 'v. i.', 'To make distinctions; to perceive the difference;\n   to exercise discrimination; -- with between; as, a judge distinguishes\n   between cases apparently similar, but differing in principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguish', 'v. i.', 'To become distinguished or distinctive; to make\n   ones self or itself discernible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguishable', 'a.', 'Capable of being distinguished; separable;\n   divisible; discernible; capable of recognition; as, a tree at a\n   distance is distinguishable from a shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguishable', 'a.', 'Worthy of note or special regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguishableness', 'n.', 'The quality of being distinguishable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguishably', 'adv.', 'So as to be distinguished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguished', 'a.', 'Marked; special.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguished', 'a.', 'Separated from others by distinct difference;\n   having, or indicating, superiority; eminent or known; illustrious; --\n   applied to persons and deeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguishedly', 'adv.', 'In a distinguished manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguisher', 'n.', 'One who, or that which, distinguishes or separates\n   one thing from another by marks of diversity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguisher', 'n.', 'One who discerns accurately the difference of\n   things; a nice or judicious observer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguishing', 'a.', 'Constituting difference, or distinction from\n   everything else; distinctive; peculiar; characteristic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguishingly', 'adv.', 'With distinction; with some mark of preference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distinguishment', 'n.', 'Observation of difference; distinction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distitle', 'v. t.', 'To deprive of title or right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distoma', 'n.', 'A genus of parasitic, trematode worms, having two suckers\n   for attaching themselves to the part they infest. See 1st Fluke, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distort', 'a.', 'Distorted; misshapen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distorted', 'imp. & p. p.', 'of Distort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distorting', 'p. pr. & vb. n.', 'of Distort');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distort', 'v. t.', 'To twist of natural or regular shape; to twist aside\n   physically; as, to distort the limbs, or the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distort', 'v. t.', 'To force or put out of the true posture or direction;\n   to twist aside mentally or morally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distort', 'v. t.', 'To wrest from the true meaning; to pervert; as, to\n   distort passages of Scripture, or their meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distorter', 'n.', 'One who, or that which, distorts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distortion', 'n.', 'The act of distorting, or twisting out of natural or\n   regular shape; a twisting or writhing motion; as, the distortions of\n   the face or body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distortion', 'n.', 'A wresting from the true meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distortion', 'n.', 'The state of being distorted, or twisted out of shape\n   or out of true position; crookedness; perversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distortion', 'n.', 'An unnatural deviation of shape or position of any part\n   of the body producing visible deformity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distortive', 'a.', 'Causing distortion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distract', 'a.', 'Separated; drawn asunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distract', 'a.', 'Insane; mad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distracted', 'imp. & p. p.', 'of Distract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraught', 'p. p.', 'of Distract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distracting', 'p. pr. & vb. n.', 'of Distract');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distract', 'v. t.', 'To draw apart or away; to divide; to disjoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distract', 'v. t.', 'To draw (the sight, mind, or attention) in different\n   directions; to perplex; to confuse; as, to distract the eye; to\n   distract the attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distract', 'v. t.', 'To agitate by conflicting passions, or by a variety of\n   motives or of cares; to confound; to harass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distract', 'v. t.', 'To unsettle the reason of; to render insane; to craze;\n   to madden; -- most frequently used in the participle, distracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distracted', 'a.', 'Mentally disordered; unsettled; mad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distractedly', 'adv.', 'Disjointedly; madly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distractedness', 'n.', 'A state of being distracted; distraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distracter', 'n.', 'One who, or that which, distracts away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distractful', 'a.', 'Distracting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distractible', 'a.', 'Capable of being drawn aside or distracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distractile', 'a.', 'Tending or serving to draw apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distracting', 'a.', 'Tending or serving to distract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraction', 'n.', 'The act of distracting; a drawing apart; separation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraction', 'n.', 'That which diverts attention; a diversion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraction', 'n.', 'A diversity of direction; detachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraction', 'n.', 'State in which the attention is called in different\n   ways; confusion; perplexity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraction', 'n.', 'Confusion of affairs; tumult; disorder; as, political\n   distractions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraction', 'n.', 'Agitation from violent emotions; perturbation of mind;\n   despair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraction', 'n.', 'Derangement of the mind; madness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distractious', 'a.', 'Distractive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distractive', 'a.', 'Causing perplexity; distracting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrained', 'imp. & p. p.', 'of Distrain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraining', 'p. pr. & vb. n.', 'of Distrain');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrain', 'v. t.', 'To press heavily upon; to bear down upon with\n   violence; hence, to constrain or compel; to bind; to distress, torment,\n   or afflict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrain', 'v. t.', 'To rend; to tear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrain', 'v. t.', 'To seize, as a pledge or indemnification; to take\n   possession of as security for nonpayment of rent, the reparation of an\n   injury done, etc.; to take by distress; as, to distrain goods for rent,\n   or of an amercement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrain', 'v. t.', 'To subject to distress; to coerce; as, to distrain a\n   person by his goods and chattels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrain', 'v. i.', 'To levy a distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrainable', 'a.', 'Capable of being, or liable to be, distrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrainer', 'n.', 'Same as Distrainor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrainor', 'n.', 'One who distrains; the party distraining goods or\n   chattels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraint', 'n.', 'The act or proceeding of seizing personal property by\n   distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrait', 'a.', 'Absent-minded; lost in thought; abstracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraught', 'a.', 'Torn asunder; separated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraught', 'a.', 'Distracted; perplexed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distraughted', 'a.', 'Distracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distream', 'v. i.', 'To flow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distress', 'n.', 'Extreme pain or suffering; anguish of body or mind; as,\n   to suffer distress from the gout, or from the loss of friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distress', 'n.', 'That which occasions suffering; painful situation;\n   misfortune; affliction; misery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distress', 'n.', 'A state of danger or necessity; as, a ship in distress,\n   from leaking, loss of spars, want of provisions or water, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distress', 'n.', 'The act of distraining; the taking of a personal chattel\n   out of the possession of a wrongdoer, by way of pledge for redress of\n   an injury, or for the performance of a duty, as for nonpayment of rent\n   or taxes, or for injury done by cattle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distress', 'n.', 'The thing taken by distraining; that which is seized to\n   procure satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distressed', 'imp. & p. p.', 'of Distress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distressing', 'p. pr. & vb. n.', 'of Distress');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distress', 'n.', 'To cause pain or anguish to; to pain; to oppress with\n   calamity; to afflict; to harass; to make miserable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distress', 'n.', 'To compel by pain or suffering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distress', 'n.', 'To seize for debt; to distrain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distressedness', 'n.', 'A state of being distressed or greatly pained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distressful', 'a.', 'Full of distress; causing, indicating, or attended\n   with, distress; as, a distressful situation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distressing', 'a.', 'Causing distress; painful; unpleasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distressing', 'adv.', 'In a distressing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributable', 'a.', 'Capable of being distributed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributary', 'a.', 'Tending to distribute or be distributed; that\n   distributes; distributive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributed', 'imp. & p. p.', 'of Distribute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributing', 'p. pr. & vb. n.', 'of Distribute');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribute', 'v. t.', 'To divide among several or many; to deal out; to\n   apportion; to allot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribute', 'v. t.', 'To dispense; to administer; as, to distribute\n   justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribute', 'v. t.', 'To divide or separate, as into classes, orders,\n   kinds, or species; to classify; to assort, as specimens, letters, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribute', 'v. t.', 'To separate (type which has been used) and return it\n   to the proper boxes in the cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribute', 'v. t.', 'To spread (ink) evenly, as upon a roller or a table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribute', 'v. t.', 'To employ (a term) in its whole extent; to take as\n   universal in one premise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribute', 'v. i.', 'To make distribution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributer', 'n.', 'One who, or that which, distributes or deals out\n   anything; a dispenser.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributing', 'a.', 'That distributes; dealing out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribution', 'n.', 'The act of distributing or dispensing; the act of\n   dividing or apportioning among several or many; apportionment; as, the\n   distribution of an estate among heirs or children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribution', 'n.', 'Separation into parts or classes; arrangement of\n   anything into parts; disposition; classification.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribution', 'n.', 'That which is distributed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribution', 'n.', 'A resolving a whole into its parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribution', 'n.', 'The sorting of types and placing them in their proper\n   boxes in the cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distribution', 'n.', 'The steps or operations by which steam is supplied to\n   and withdrawn from the cylinder at each stroke of the piston; viz.,\n   admission, suppression or cutting off, release or exhaust, and\n   compression of exhaust steam prior to the next admission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributional', 'a.', 'Of or pertaining to distribution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributionist', 'n.', 'A distributer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributive', 'a.', 'Tending to distribute; serving to divide and assign\n   in portions; dealing to each his proper share.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributive', 'a.', 'Assigning the species of a general term.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributive', 'a.', 'Expressing separation; denoting a taking singly, not\n   collectively; as, a distributive adjective or pronoun, such as each,\n   either, every; a distributive numeral, as (Latin) bini (two by two).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributive', 'n.', 'A distributive adjective or pronoun; also, a\n   distributive numeral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributively', 'adv.', 'By distribution; singly; not collectively; in a\n   distributive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distributiveness', 'n.', 'Quality of being distributive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'District', 'a.', 'Rigorous; stringent; harsh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'District', 'n.', 'The territory within which the lord has the power of\n   coercing and punishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'District', 'n.', 'A division of territory; a defined portion of a state,\n   town, or city, etc., made for administrative, electoral, or other\n   purposes; as, a congressional district, judicial district, land\n   district, school district, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'District', 'n.', 'Any portion of territory of undefined extent; a region; a\n   country; a tract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Districted', 'imp. & p. p.', 'of District');
  INSERT INTO words (word, word_type, description) VALUES  (    'Districting', 'p. pr. & vb. n.', 'of District');
  INSERT INTO words (word, word_type, description) VALUES  (    'District', 'v. t.', 'To divide into districts or limited portions of\n   territory; as, legislatures district States for the choice of\n   representatives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distriction', 'n.', 'Sudden display; flash; glitter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Districtly', 'adv.', 'Strictly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distringas', 'n.', 'A writ commanding the sheriff to distrain a person by\n   his goods or chattels, to compel a compliance with something required\n   of him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrouble', 'v. t.', 'To trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrusted', 'imp. & p. p.', 'of Distrust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrusting', 'p. pr. & vb. n.', 'of Distrust');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrust', 'v. t.', 'To feel absence of trust in; not to confide in or rely\n   upon; to deem of questionable sufficiency or reality; to doubt; to be\n   suspicious of; to mistrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrust', 'n.', 'Doubt of sufficiency, reality, or sincerity; want of\n   confidence, faith, or reliance; as, distrust of ones power, authority,\n   will, purposes, schemes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrust', 'n.', 'Suspicion of evil designs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrust', 'n.', 'State of being suspected; loss of trust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distruster', 'n.', 'One who distrusts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrustful', 'a.', 'Not confident; diffident; wanting confidence or\n   thrust; modest; as, distrustful of ourselves, of ones powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrustful', 'a.', 'Apt to distrust; suspicious; mistrustful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrusting', 'a.', 'That distrusts; suspicious; lacking confidence in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distrustless', 'a.', 'Free from distrust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distune', 'v. t.', 'To put out of tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturbed', 'imp. & p. p.', 'of Disturb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturbing', 'p. pr. & vb. n.', 'of Disturb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturb', 'v. t.', 'To throw into disorder or confusion; to derange; to\n   interrupt the settled state of; to excite from a state of rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturb', 'v. t.', 'To agitate the mind of; to deprive of tranquillity; to\n   disquiet; to render uneasy; as, a person is disturbed by receiving an\n   insult, or his mind is disturbed by envy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturb', 'v. t.', 'To turn from a regular or designed course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturb', 'n.', 'Disturbance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturbance', 'n.', 'An interruption of a state of peace or quiet;\n   derangement of the regular course of things; disquiet; disorder; as, a\n   disturbance of religious exercises; a disturbance of the galvanic\n   current.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturbance', 'n.', 'Confusion of the mind; agitation of the feelings;\n   perplexity; uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturbance', 'n.', 'Violent agitation in the body politic; public\n   commotion; tumult.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturbance', 'n.', 'The hindering or disquieting of a person in the lawful\n   and peaceable enjoyment of his right; the interruption of a right; as,\n   the disturbance of a franchise, of common, of ways, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturbation', 'n.', 'Act of disturbing; disturbance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturber', 'n.', 'One who, or that which, disturbs of disquiets; a\n   violator of peace; a troubler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturber', 'n.', 'One who interrupts or incommodes another in the\n   peaceable enjoyment of his right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disturn', 'v. t.', 'To turn aside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Distyle', 'a.', 'Having two columns in front; -- said of a temple, portico,\n   or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disulphate', 'n.', 'A salt of disulphuric or pyrosulphuric acid; a\n   pyrosulphate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disulphate', 'n.', 'An acid salt of sulphuric acid, having only one\n   equivalent of base to two of the acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disulphide', 'n.', 'A binary compound of sulphur containing two atoms of\n   sulphur in each molecule; -- formerly called disulphuret. Cf.\n   Bisulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disulphuret', 'n.', 'See Disulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disulphuric', 'a.', 'Applied to an acid having in each molecule two atoms\n   of sulphur in the higher state of oxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disuniform', 'a.', 'Not uniform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disunion', 'n.', 'The termination of union; separation; disjunction; as,\n   the disunion of the body and the soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disunion', 'n.', 'A breach of concord and its effect; alienation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disunion', 'n.', 'The termination or disruption of the union of the States\n   forming the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disunionist', 'n.', 'An advocate of disunion, specifically, of disunion of\n   the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disunited', 'imp. & p. p.', 'of Disunite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disuniting', 'p. pr. & vb. n.', 'of Disunite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disunite', 'v. t.', 'To destroy the union of; to divide; to part; to sever;\n   to disjoin; to sunder; to separate; as, to disunite particles of\n   matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disunite', 'v. t.', 'To alienate in spirit; to break the concord of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disunite', 'v. i.', 'To part; to fall asunder; to become separated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disuniter', 'n.', 'One who, or that which, disjoins or causes disunion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disunity', 'n.', 'A state of separation or disunion; want of unity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disusage', 'n.', 'Gradual cessation of use or custom; neglect of use;\n   disuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disused', 'imp. & p. p.', 'of Disuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disusing', 'p. pr. & vb. n.', 'of Disuse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disuse', 'v. t.', 'To cease to use; to discontinue the practice of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disuse', 'v. t.', 'To disaccustom; -- with to or from; as, disused to toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disuse', 'n.', 'Cessation of use, practice, or exercise; inusitation;\n   desuetude; as, the limbs lose their strength by disuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disutilize', 'v. t.', 'To deprive of utility; to render useless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disvaluation', 'n.', 'Disesteem; depreciation; disrepute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disvalue', 'v. t.', 'To undervalue; to depreciate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disvalue', 'n.', 'Disesteem; disregard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disvantageous', 'a.', 'Disadvantageous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disvelop', 'v. t.', 'To develop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disventure', 'n.', 'A disadventure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disvouch', 'v. t.', 'To discredit; to contradict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diswarn', 'v. t.', 'To dissuade from by previous warning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diswitted', 'a.', 'Deprived of wits or understanding; distracted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diswont', 'v. t.', 'To deprive of wonted usage; to disaccustom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disworkmanship', 'n.', 'Bad workmanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disworship', 'v. t.', 'To refuse to worship; to treat as unworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disworship', 'n.', 'A deprivation of honor; a cause of disgrace; a\n   discredit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disworth', 'v. t.', 'To deprive of worth; to degrade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Disyoke', 'v. t.', 'To unyoke; to free from a yoke; to disjoin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dit', 'n.', 'A word; a decree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dit', 'n.', 'A ditty; a song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dit', 'v. t.', 'To close up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditation', 'n.', 'The act of making rich; enrichment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditches', 'pl. ', 'of Ditch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditch', 'n.', 'A trench made in the earth by digging, particularly a trench\n   for draining wet land, for guarding or fencing inclosures, or for\n   preventing an approach to a town or fortress. In the latter sense, it\n   is called also a moat or a fosse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditch', 'n.', 'Any long, narrow receptacle for water on the surface of the\n   earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditched', 'imp. & p. p.', 'of Ditch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditching', 'p. pr. & vb. n.', 'of Ditch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditch', 'v. t.', 'To dig a ditch or ditches in; to drain by a ditch or\n   ditches; as, to ditch moist land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditch', 'v. t.', 'To surround with a ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditch', 'v. t.', 'To throw into a ditch; as, the engine was ditched and\n   turned on its side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditch', 'v. i.', 'To dig a ditch or ditches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditcher', 'n.', 'One who digs ditches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dite', 'v. t.', 'To prepare for action or use; to make ready; to dight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diterebene', 'n.', 'See Colophene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dithecal', 'a.', 'Alt. of Dithecous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dithecous', 'a.', 'Having two thecae, cells, or compartments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditheism', 'n.', 'The doctrine of those who maintain the existence of two\n   gods or of two original principles (as in Manicheism), one good and one\n   evil; dualism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditheist', 'n.', 'One who holds the doctrine of ditheism; a dualist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditheistic', 'a.', 'Alt. of Ditheistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditheistical', 'a.', 'Pertaining to ditheism; dualistic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dithionic', 'a.', 'Containing two equivalents of sulphur; as, dithionic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dithyramb', 'n.', 'A kind of lyric poetry in honor of Bacchus, usually sung\n   by a band of revelers to a flute accompaniment; hence, in general, a\n   poem written in a wild irregular strain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dithyrambic', 'a.', 'Pertaining to, or resembling, a dithyramb; wild and\n   boisterous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dithyrambic', 'n.', 'A dithyrambic poem; a dithyramb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dithyrambus', 'n.', 'See Dithyramb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dition', 'n.', 'Dominion; rule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditionary', 'a.', 'Under rule; subject; tributary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditionary', 'n.', 'A subject; a tributary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditokous', 'a.', 'Having two kinds of young, as certain annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditokous', 'a.', 'Producing only two eggs for a clutch, as certain birds\n   do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditolyl', 'n.', 'A white, crystalline, aromatic hydrocarbon, C14H14,\n   consisting of two radicals or residues of toluene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditone', 'n.', 'The Greek major third, which comprehend two major tones\n   (the modern major third contains one major and one minor whole tone).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditrichotomous', 'a.', 'Divided into twos or threes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditrichotomous', 'a.', 'Dividing into double or treble ramifications; --\n   said of a leaf or stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditrochean', 'a.', 'Containing two trochees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditrochee', 'n.', 'A double trochee; a foot made up of two trochees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditroite', 'n.', 'An igneous rock composed of orthoclase, elaeolite, and\n   sodalite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditt', 'n.', 'See Dit, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dittander', 'n.', 'A kind of peppergrass (Lepidium latifolium).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dittany', 'n.', 'A plant of the Mint family (Origanum Dictamnus), a native\n   of Crete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dittany', 'n.', 'The Dictamnus Fraxinella. See Dictamnus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dittany', 'n.', 'In America, the Cunila Mariana, a fragrant herb of the\n   Mint family.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dittied', 'a.', 'Set, sung, or composed as a ditty; -- usually in\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dittos', 'pl. ', 'of Ditto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditto', 'n.', 'The aforesaid thing; the same (as before). Often contracted\n   to do., or to two \"turned commas\" (\"), or small marks. Used in bills,\n   books of account, tables of names, etc., to save repetition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditto', 'adv.', 'As before, or aforesaid; in the same manner; also.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dittology', 'n.', 'A double reading, or twofold interpretation, as of a\n   Scripture text.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditties', 'pl. ', 'of Ditty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditty', 'v. t.', 'A saying or utterance; especially, one that is short and\n   frequently repeated; a theme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditty', 'v. t.', 'A song; a lay; a little poem intended to be sung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditty', 'v. i.', 'To sing; to warble a little tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditty-bag', 'n.', 'A sailors small bag to hold thread, needles, tape,\n   etc.; -- also called sailors housewife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ditty-box', 'n.', 'A small box to hold a sailors thread, needless, comb,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diureide', 'n.', 'One of a series of complex nitrogenous substances\n   regarded as containing two molecules of urea or their radicals, as uric\n   acid or allantoin. Cf. Ureide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diuresis', 'n.', 'Free excretion of urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diuretic', 'a.', 'Tending to increase the secretion and discharge of urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diuretic', 'n.', 'A medicine with diuretic properties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diuretical', 'a.', 'Diuretic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diureticalness', 'n.', 'The quality of being diuretical; diuretic property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurna', 'n. pl.', 'A division of Lepidoptera, including the butterflies;\n   -- so called because they fly only in the daytime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnal', 'a.', 'Relating to the daytime; belonging to the period of\n   daylight, distinguished from the night; -- opposed to nocturnal; as,\n   diurnal heat; diurnal hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnal', 'a.', 'Daily; recurring every day; performed in a day; going\n   through its changes in a day; constituting the measure of a day; as, a\n   diurnal fever; a diurnal task; diurnal aberration, or diurnal parallax;\n   the diurnal revolution of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnal', 'a.', 'Opening during the day, and closing at night; -- said of\n   flowers or leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnal', 'a.', 'Active by day; -- applied especially to the eagles and\n   hawks among raptorial birds, and to butterflies (Diurna) among insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnal', 'a.', 'A daybook; a journal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnal', 'a.', 'A small volume containing the daily service for the\n   \"little hours,\" viz., prime, tierce, sext, nones, vespers, and\n   compline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnal', 'a.', 'A diurnal bird or insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnalist', 'n.', 'A journalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnally', 'adv.', 'Daily; every day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnalness', 'n.', 'The quality of being diurnal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnation', 'n.', 'Continuance during the day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diurnation', 'n.', 'The condition of sleeping or becoming dormant by day,\n   as is the case of the bats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diuturnal', 'a.', 'Of long continuance; lasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diuturnity', 'n.', 'Long duration; lastingness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divagation', 'n.', 'A wandering about or going astray; digression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divalent', 'a.', 'Having two units of combining power; bivalent. Cf.\n   Valence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divan', 'n.', 'A book; esp., a collection of poems written by one author;\n   as, the divan of Hafiz.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divan', 'n.', 'In Turkey and other Oriental countries: A council of state;\n   a royal court. Also used by the poets for a grand deliberative council\n   or assembly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divan', 'n.', 'A chief officer of state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divan', 'n.', 'A saloon or hall where a council is held, in Oriental\n   countries, the state reception room in places, and in the houses of the\n   richer citizens. Cushions on the floor or on benches are ranged round\n   the room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divan', 'n.', 'A cushioned seat, or a large, low sofa or couch; especially,\n   one fixed to its place, and not movable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divan', 'n.', 'A coffee and smoking saloon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divaricated', 'imp. & p. p.', 'of Divaricate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divaricating', 'p. pr. & vb. n.', 'of Divaricate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divaricate', 'v. i.', 'To part into two branches; to become bifid; to fork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divaricate', 'v. i.', 'To diverge; to be divaricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divaricate', 'v. t.', 'To divide into two branches; to cause to branch\n   apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divaricate', 'a.', 'Diverging; spreading asunder; widely diverging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divaricate', 'a.', 'Forking and diverging; widely diverging; as the\n   branches of a tree, or as lines of sculpture, or color markings on\n   animals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divaricately', 'adv.', 'With divarication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divarication', 'n.', 'A separation into two parts or branches; a forking; a\n   divergence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divarication', 'n.', 'An ambiguity of meaning; a disagreement of difference\n   in opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divarication', 'n.', 'A divergence of lines of color sculpture, or of\n   fibers at different angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divaricator', 'n.', 'One of the muscles which open the shell of\n   brachiopods; a cardinal muscle. See Illust. of Brachiopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divast', 'a.', 'Devastated; laid waste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dived', 'imp. & p. p.', 'of Dive');
