  INSERT INTO words (word, word_type, description) VALUES  (    'Dive', 'v. i.', 'Fig.: To plunge or to go deeply into any subject,\n   question, business, etc.; to penetrate; to explore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dive', 'v. t.', 'To plunge (a person or thing) into water; to dip; to duck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dive', 'v. t.', 'To explore by diving; to plunge into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dive', 'n.', 'A plunge headforemost into water, the act of one who dives,\n   literally or figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Dive', 'n.', 'A place of low resort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divedapper', 'n.', 'A water fowl; the didapper. See Dabchick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divel', 'v. t.', 'To rend apart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divellent', 'a.', 'Drawing asunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divellicate', 'v. t.', 'To pull in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diver', 'n.', 'One who, or that which, dives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diver', 'n.', 'Fig.: One who goes deeply into a subject, study, or\n   business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diver', 'n.', 'Any bird of certain genera, as Urinator (formerly Colymbus),\n   or the allied genus Colymbus, or Podiceps, remarkable for their agility\n   in diving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverb', 'n.', 'A saying in which two members of the sentence are\n   contrasted; an antithetical proverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverberate', 'v. t.', 'To strike or sound through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverberation', 'n.', 'A sounding through.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverged', 'imp. & p. p.', 'of Diverge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverging', 'p. pr. & vb. n.', 'of Diverge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverge', 'v. i.', 'To extend from a common point in different directions;\n   to tend from one point and recede from each other; to tend to spread\n   apart; to turn aside or deviate (as from a given direction); -- opposed\n   to converge; as, rays of light diverge as they proceed from the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverge', 'v. i.', 'To differ from a typical form; to vary from a normal\n   condition; to dissent from a creed or position generally held or taken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divergement', 'n.', 'Divergence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divergence', 'n.', 'Alt. of Divergency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divergency', 'n.', 'A receding from each other in moving from a common\n   center; the state of being divergent; as, an angle is made by the\n   divergence of straight lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divergency', 'n.', 'Disagreement; difference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divergent', 'a.', 'Receding farther and farther from each other, as lines\n   radiating from one point; deviating gradually from a given direction;\n   -- opposed to convergent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divergent', 'a.', 'Causing divergence of rays; as, a divergent lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divergent', 'a.', 'Fig.: Disagreeing from something given; differing; as, a\n   divergent statement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverging', 'a.', 'Tending in different directions from a common center;\n   spreading apart; divergent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divergingly', 'adv.', 'In a diverging manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divers', 'a.', 'Different in kind or species; diverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divers', 'a.', 'Several; sundry; various; more than one, but not a great\n   number; as, divers philosophers. Also used substantively or\n   pronominally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverse', 'a.', 'Different; unlike; dissimilar; distinct; separate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverse', 'a.', 'Capable of various forms; multiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverse', 'adv.', 'In different directions; diversely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverse', 'v. i.', 'To turn aside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversely', 'adv.', 'In different ways; differently; variously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversely', 'adv.', 'In different directions; to different points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverseness', 'n.', 'The quality of being diverse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversifiability', 'n.', 'The quality or capacity of being diversifiable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversifiable', 'a.', 'Capable of being diversified or varied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversification', 'n.', 'The act of making various, or of changing form or\n   quality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversification', 'n.', 'State of diversity or variation; variegation;\n   modification; change; alternation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversified', 'a.', 'Distinguished by various forms, or by a variety of\n   aspects or objects; variegated; as, diversified scenery or landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversifier', 'n.', 'One who, or that which, diversifies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversiform', 'a.', 'Of a different form; of varied forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversified', 'imp. & p. p.', 'of Diversify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversifying', 'p. pr. & vb. n.', 'of Diversify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversify', 'v. t.', 'To make diverse or various in form or quality; to\n   give variety to; to variegate; to distinguish by numerous differences\n   or aspects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversiloquent', 'a.', 'Speaking in different ways.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversion', 'n.', 'The act of turning aside from any course, occupation, or\n   object; as, the diversion of a stream from its channel; diversion of\n   the mind from business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversion', 'n.', 'That which diverts; that which turns or draws the mind\n   from care or study, and thus relaxes and amuses; sport; play; pastime;\n   as, the diversions of youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversion', 'n.', 'The act of drawing the attention and force of an enemy\n   from the point where the principal attack is to be made; the attack,\n   alarm, or feint which diverts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversities', 'pl. ', 'of Diversity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversity', 'n.', 'A state of difference; dissimilitude; unlikeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversity', 'n.', 'Multiplicity of difference; multiformity; variety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversity', 'n.', 'Variegation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversivolent', 'a.', 'Desiring different things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diversory', 'a.', 'Serving or tending to divert; also, distinguishing.');
