  INSERT INTO words (word, word_type, description) VALUES  (    'Divert', 'v. t.', 'To turn away from any occupation, business, or study; to\n   cause to have lively and agreeable sensations; to amuse; to entertain;\n   as, children are diverted with sports; men are diverted with works of\n   wit and humor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divert', 'v. i.', 'To turn aside; to digress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverter', 'n.', 'One who, or that which, diverts, turns off, or pleases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Divertible', 'a.', 'Capable of being diverted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverticle', 'n.', 'A turning; a byway; a bypath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverticle', 'n.', 'A diverticulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverticular', 'a.', 'Pertaining to a diverticulum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Diverticula', 'pl. ', 'of Diverticulum');
